INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('446', '446 - A TUA PALAVRA', 'A TUA PALAVRA É TESOURO ESCONDIDO QUE ALGUÉM ENCONTROU; MISTÉRIOS SELADOS QUE O ESPÍRITO SANTO A NÓS REVELOU. A TUA PALAVRA É TRIGO GUARDADO, MALHADO NO LAGAR DE DEUS, NOSSO PÃO DO CÉU!\n\nCORO BENDITA PALAVRA QUE CURA E SALVA A QUEM NELA CRÊ, PALAVRA DE DEUS!\n\nA TUA PALAVRA É ABRIGO SEGURO QUE VIDA NOS TRAZ, A ROCHA DOS SÉCULOS, BEM FIRME ALICERCE, ETERNA E VERAZ; FELIZ QUEM SOBRE ELA EDIFICA SUA CASA, TORMENTAS ELA VENCERÁ, NÃO VACILARÁ!\n\nCORO BENDITA PALAVRA QUE CURA E SALVA A QUEM NELA CRÊ, PALAVRA DE DEUS! FINAL: PALAVRA DE DEUS!', 'A TUA PALAVRA É TESOURO ESCONDIDO QUE ALGUÉM ENCONTROU; MISTÉRIOS SELADOS QUE O ESPÍRITO SANTO A NÓS REVELOU. A TUA PALAVRA É TRIGO GUARDADO, MALHADO NO LAGAR DE DEUS, NOSSO PÃO DO CÉU! CORO BENDITA PALAVRA QUE CURA E SALVA A QUEM NELA CRÊ, PALAVRA DE DEUS! A TUA PALAVRA É ABRIGO SEGURO QUE VIDA NOS TRAZ, A ROCHA DOS SÉCULOS, BEM FIRME ALICERCE, ETERNA E VERAZ; FELIZ QUEM SOBRE ELA EDIFICA SUA CASA, TORMENTAS ELA VENCERÁ, NÃO VACILARÁ! CORO BENDITA PALAVRA QUE CURA E SALVA A QUEM NELA CRÊ, PALAVRA DE DEUS!  PALAVRA DE DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('447', '447 - SENHOR, GUIA-NOS', 'SENHOR, GUIA-NOS À TUA PRESENÇA ONDE O TEU SANGUE PURIFICA; FAZE-NOS UM, UM SÓ CORPO, UMA SÓ MENTE, UM CORAÇÃO.\n\nCORO SOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO NOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, TEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER; GUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA MAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, GUIA-NOS.\n\nJÁ SOMOS UM, UM SÓ CORPO, UMA SÓ MENTE, UM CORAÇÃO, PRA TE OFERTAR TÃO SOMENTE NOSSO AMOR E GRATIDÃO.\n\nCORO SOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO NOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, TEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER; GUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA MAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, GUIA-NOS. FINAL: GUIA-NOS.', 'SENHOR, GUIA-NOS À TUA PRESENÇA ONDE O TEU SANGUE PURIFICA; FAZE-NOS UM, UM SÓ CORPO, UMA SÓ MENTE, UM CORAÇÃO. CORO SOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO NOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, TEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER; GUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA MAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, GUIA-NOS. JÁ SOMOS UM, UM SÓ CORPO, UMA SÓ MENTE, UM CORAÇÃO, PRA TE OFERTAR TÃO SOMENTE NOSSO AMOR E GRATIDÃO. CORO SOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO NOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, TEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER; GUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA MAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, GUIA-NOS.  GUIA-NOS.',  1);
