INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('453', '453 - QUANDO DOBRO OS MEUS JOELHOS EM ORAÇÃO', 'QUANDO DOBRO OS MEUS JOELHOS EM ORAÇÃO, VEJO QUÃO PEQUENO SOU DIANTE DO TEU PODER, SEI QUE POSSO DERRAMAR MEU CORAÇÃO EM TEU ALTAR POIS DE TI VEM A RESPOSTA, VEM O PERDÃO.\n\nCORO EU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR. EU QUERO TE SERVIR, SENHOR, COM TODO O MEU VIGOR POIS SE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR PRA TRÁS EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM.\n\nQUANDO VEJO O QUE FIZESTE EM MEU VIVER, TANTAS BÊNÇÃOS RECEBIDAS SEM MERECER, O MEU CORAÇÃO TRANSBORDA DE GRATIDÃO; QUE DAREI A TI, SENHOR, SENÃO O MEU LOUVOR?\n\nCORO EU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR. EU QUERO TE SERVIR, SENHOR, COM TODO O MEU VIGOR POIS SE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR PRA TRÁS EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM. FINAL: EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM.\nBIS', 'QUANDO DOBRO OS MEUS JOELHOS EM ORAÇÃO, VEJO QUÃO PEQUENO SOU DIANTE DO TEU PODER, SEI QUE POSSO DERRAMAR MEU CORAÇÃO EM TEU ALTAR POIS DE TI VEM A RESPOSTA, VEM O PERDÃO. CORO EU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR. EU QUERO TE SERVIR, SENHOR, COM TODO O MEU VIGOR POIS SE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR PRA TRÁS EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM. QUANDO VEJO O QUE FIZESTE EM MEU VIVER, TANTAS BÊNÇÃOS RECEBIDAS SEM MERECER, O MEU CORAÇÃO TRANSBORDA DE GRATIDÃO; QUE DAREI A TI, SENHOR, SENÃO O MEU LOUVOR? CORO EU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR. EU QUERO TE SERVIR, SENHOR, COM TODO O MEU VIGOR POIS SE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR PRA TRÁS EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM.  EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('454', '454 - SENHOR, MEU DEUS, O QUE DIREI DE TI? (SAÚDE ESPIRITUAL)', 'SENHOR, MEU DEUS, O QUE DIREI DE TI? TU ÉS AQUELE QUE ME AMOU PRIMEIRO! MAS HOJE FRACO ESTOU, SINTO SAUDADES DE TI, SENHOR, E VIM CHORAR DIANTE AOS TEUS PÉS, EM ORAÇÃO.\n\nRENOVA EM MIM VALORES TEUS, ORDENA OS PENSAMENTOS MEUS E DÁ-ME, Ó PAI, SAÚDE ESPIRITUAL; PRA COM PRIMEIRO AMOR TE AMAR, O AMOR QUE UM DIA CONHECI QUANDO TOCASTE EM MIM PELA PRIMEIRA VEZ. INSTRUMENTOS\n\nSENHOR, MEU DEUS, O QUE DIREI DE TI? TU ÉS AQUELE QUE ME AMOU PRIMEIRO! MAS HOJE FRACO ESTOU, SINTO SAUDADES DE TI, SENHOR, E VIM CHORAR DIANTE AOS TEUS PÉS, EM ORAÇÃO.\n\nRENOVA EM MIM VALORES TEUS, ORDENA OS PENSAMENTOS MEUS E DÁ-ME, Ó PAI, SAÚDE ESPIRITUAL; PRA COM PRIMEIRO AMOR TE AMAR, O AMOR QUE UM DIA CONHECI QUANDO TOCASTE EM MIM PELA PRIMEIRA VEZ.\nBIS', 'SENHOR, MEU DEUS, O QUE DIREI DE TI? TU ÉS AQUELE QUE ME AMOU PRIMEIRO! MAS HOJE FRACO ESTOU, SINTO SAUDADES DE TI, SENHOR, E VIM CHORAR DIANTE AOS TEUS PÉS, EM ORAÇÃO. RENOVA EM MIM VALORES TEUS, ORDENA OS PENSAMENTOS MEUS E DÁ-ME, Ó PAI, SAÚDE ESPIRITUAL; PRA COM PRIMEIRO AMOR TE AMAR, O AMOR QUE UM DIA CONHECI QUANDO TOCASTE EM MIM PELA PRIMEIRA VEZ. INSTRUMENTOS RENOVA EM MIM VALORES TEUS, ORDENA OS PENSAMENTOS MEUS E DÁ-ME, Ó PAI, SAÚDE ESPIRITUAL; PRA COM PRIMEIRO AMOR TE AMAR, O AMOR QUE UM DIA CONHECI QUANDO TOCASTE EM MIM PELA PRIMEIRA VEZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('455', '455 - QUANTO MAIS EU TE CONHEÇO (CAMINHO FELIZ)', 'QUANTO MAIS EU TE CONHEÇO, MAIS EU QUERO TE SERVIR; QUANDO FALAS, OBEDEÇO, A BÊNÇÃO ESTÁ EM TE OUVIR. TU ÉS FOGO QUE CONSOME, BRISA SUAVE DE AMOR, TU ÉS DEUS QUE MUDA O HOMEM, ÉS O MEU AJUDADOR!\nBIS\n\nCORO QUE MARAVILHA, SENHOR, É ESTAR NESTE CAMINHO QUE LEVA AO CÉU! CAMINHO FELIZ, INDO PRA TE ENCONTRAR E VER O TEU ROSTO SERÁ COMO UM SONHO, PELA FÉ EU JÁ SEI, VAI SE REALIZAR.\n\nO QUE GUARDA A TUA LEI NÃO SE ARREPENDERÁ; SE A GLÓRIA DE DEUS EU BUSCO, ELA EU IREI ALCANÇAR; SE ESPINHOS NO CAMINHO EU TIVER DE ENFRENTAR, EU NÃO LIGO, MAS PROSSIGO, PARA CRISTO VOU OLHAR!\n\nCORO (2X) QUE MARAVILHA, SENHOR, É ESTAR NESTE CAMINHO QUE LEVA AO CÉU! CAMINHO FELIZ, INDO PRA TE ENCONTRAR E VER O TEU ROSTO SERÁ COMO UM SONHO, PELA FÉ EU JÁ SEI, VAI SE REALIZAR. FINAL: VAI SE REALIZAR.', 'QUANTO MAIS EU TE CONHEÇO, MAIS EU QUERO TE SERVIR; QUANDO FALAS, OBEDEÇO, A BÊNÇÃO ESTÁ EM TE OUVIR. TU ÉS FOGO QUE CONSOME, BRISA SUAVE DE AMOR, TU ÉS DEUS QUE MUDA O HOMEM, ÉS O MEU AJUDADOR! CORO QUE MARAVILHA, SENHOR, É ESTAR NESTE CAMINHO QUE LEVA AO CÉU! CAMINHO FELIZ, INDO PRA TE ENCONTRAR E VER O TEU ROSTO SERÁ COMO UM SONHO, PELA FÉ EU JÁ SEI, VAI SE REALIZAR. O QUE GUARDA A TUA LEI NÃO SE ARREPENDERÁ; SE A GLÓRIA DE DEUS EU BUSCO, ELA EU IREI ALCANÇAR; SE ESPINHOS NO CAMINHO EU TIVER DE ENFRENTAR, EU NÃO LIGO, MAS PROSSIGO, PARA CRISTO VOU OLHAR! CORO  QUE MARAVILHA, SENHOR, É ESTAR NESTE CAMINHO QUE LEVA AO CÉU! CAMINHO FELIZ, INDO PRA TE ENCONTRAR E VER O TEU ROSTO SERÁ COMO UM SONHO, PELA FÉ EU JÁ SEI, VAI SE REALIZAR.  VAI SE REALIZAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('456', '456 - CHUVAS DE BÊNÇÃOS TEREMOS', 'CHUVAS DE BÊNÇÃOS TEREMOS; É A PROMESSA DE DEUS. TEMPOS BENDITOS TRAZENDO, CHUVAS DE BÊNÇÃOS DOS CÉUS.\n\nCORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS. CHUVAS DE BÊNÇÃOS TEREMOS, DE VIDA PAZ E PERDÃO. OS PECADORES INDIGNOS GRAÇA DOS CÉUS OBTERÃO.\n\nSERVAS\n\nCORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS. CHUVAS DE BÊNÇÃOS TEREMOS, MANDA-NOS JÁ, Ó SENHOR! DÁ-NOS O GOZO DOS FRUTOS DOS TEUS ENSINOS DE AMOR.\n\nVARÕES\n\nCORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS. FINAL: AMÉM!', 'CHUVAS DE BÊNÇÃOS TEREMOS; É A PROMESSA DE DEUS. TEMPOS BENDITOS TRAZENDO, CHUVAS DE BÊNÇÃOS DOS CÉUS. CORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS. CHUVAS DE BÊNÇÃOS TEREMOS, DE VIDA PAZ E PERDÃO. OS PECADORES INDIGNOS GRAÇA DOS CÉUS OBTERÃO. CORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS. CHUVAS DE BÊNÇÃOS TEREMOS, MANDA-NOS JÁ, Ó SENHOR! DÁ-NOS O GOZO DOS FRUTOS DOS TEUS ENSINOS DE AMOR. CORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS.  AMÉM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('457', '457 - INSTRUMENTO EM TUAS MÃOS', 'INSTRUMENTO EM TUAS MÃOS QUERO SER, PARA TEU LOUVOR, UM VASO DE HONRA E BÊNÇÃOS PARA TU USARES, SENHOR. BREVE OU LONGA, A MINHA VIDASÓ TERÁ SENTIDO ASSIM, POR ISSO PEÇO: FAZE DE MIM INSTRUMENTO EM TUAS MÃOS.\n\nCORO UM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS, PARA TU MOLDARES E REVESTIRES DO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, SEPARADO PARA TI, PRA TE SERVIR CONFORME TE APROUVER EM TEU LOUVOR.\n\nCOM A ÁGUA DA PALAVRA MINHA MENTE VEM LIMPAR E PURIFICA A MINHA VIDA COM O SANGUE DE JESUS. COM O ÓLEO DO ESPÍRITO, ME REVESTE DE PODER, PRA SER UM VASO DE HONRA E GLÓRIA EM TUAS MÃOS, Ó MEU JESUS.\n\nCORO UM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS, PARA TU MOLDARES E REVESTIRES DO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, SEPARADO PARA TI, PRA TE SERVIR CONFORME TE APROUVER EM TEU LOUVOR. FINAL: TEU LOUVOR', 'INSTRUMENTO EM TUAS MÃOS QUERO SER, PARA TEU LOUVOR, UM VASO DE HONRA E BÊNÇÃOS PARA TU USARES, SENHOR. BREVE OU LONGA, A MINHA VIDASÓ TERÁ SENTIDO ASSIM, POR ISSO PEÇO: FAZE DE MIM INSTRUMENTO EM TUAS MÃOS. CORO UM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS, PARA TU MOLDARES E REVESTIRES DO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, SEPARADO PARA TI, PRA TE SERVIR CONFORME TE APROUVER EM TEU LOUVOR. COM A ÁGUA DA PALAVRA MINHA MENTE VEM LIMPAR E PURIFICA A MINHA VIDA COM O SANGUE DE JESUS. COM O ÓLEO DO ESPÍRITO, ME REVESTE DE PODER, PRA SER UM VASO DE HONRA E GLÓRIA EM TUAS MÃOS, Ó MEU JESUS. CORO UM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS, PARA TU MOLDARES E REVESTIRES DO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, SEPARADO PARA TI, PRA TE SERVIR CONFORME TE APROUVER EM TEU LOUVOR.  TEU LOUVOR',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('458', '458 - Ó CRISTO! PÃO DA VIDA', 'Ó CRISTO! PÃO DA VIDA, DESCIDO LÁ DO CÉU. O PÃO DE NOSSAS ALMAS, QUE O PAI DE AMOR NOS DEU! EM TI NOS ALEGRAMOS, PROVANDO MESMO AQUI DO ALENTO E DA DOÇURA, QUE ACHAMOS SEMPRE EM TI.\n\nDA ETERNA E SANTA VIDA, DA QUAL TU ÉS O AUTOR, SUSTENTO E FORTALEZA ÉS TU TAMBÉM, SENHOR. SEM TI NÃO NOS ASSISTEM NEM FORÇAS, NEM PODER; DE TI, NOSSO ALIMENTO, QUEREMOS NÓS VIVER.\n\nÓ CRISTO! PÃO DA VIDA! A TI LOUVAMOS NÓS, E AO PAI TAMBÉM ERGUEMOS A NOSSA ALEGRE VOZ. AGRADECEMOS SEMPRE O AMOR QUE AQUI NOS DEU, SUSTENTO VERDADEIRO NO SANTO PÃO DO CÉU. AMÉM.', 'Ó CRISTO! PÃO DA VIDA, DESCIDO LÁ DO CÉU. O PÃO DE NOSSAS ALMAS, QUE O PAI DE AMOR NOS DEU! EM TI NOS ALEGRAMOS, PROVANDO MESMO AQUI DO ALENTO E DA DOÇURA, QUE ACHAMOS SEMPRE EM TI. DA ETERNA E SANTA VIDA, DA QUAL TU ÉS O AUTOR, SUSTENTO E FORTALEZA ÉS TU TAMBÉM, SENHOR. SEM TI NÃO NOS ASSISTEM NEM FORÇAS, NEM PODER; DE TI, NOSSO ALIMENTO, QUEREMOS NÓS VIVER. Ó CRISTO! PÃO DA VIDA! A TI LOUVAMOS NÓS, E AO PAI TAMBÉM ERGUEMOS A NOSSA ALEGRE VOZ. AGRADECEMOS SEMPRE O AMOR QUE AQUI NOS DEU, SUSTENTO VERDADEIRO NO SANTO PÃO DO CÉU. AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('459', '459 - É TEMPO, É TEMPO', 'É TEMPO, É TEMPO, O MESTRE ESTÁ CHAMANDO JÁ! MARCHAR, MARCHAR, CONFIANDO EM SEU AMOR! PARTIR, PARTIR, A SALVAÇÃO A PROCLAMAR, COM A PALAVRA SANTA DO BOM SALVADOR.\n\nCORO MARCHAR, (MARCHAR, MARCHAR) SIM, AVANTE! (SIM, AVANTE!) MARCHAR, ERGUENDO O PENDÃO REAL! (MARCHAR, ERGUENDO O PENDÃO REAL!) AVANTE! (AVANTE, SIM) SIM, AVANTE, (SEMPRE, SIM) UNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! CONSAGRANDO TODO O VOSSO CORAÇÃO, PRA A JESUS OBEDECER, SEU QUERER EXECUTAR, ENTOAI, LOUVORES ALTOS! AVANÇAR!\n\n“QUEREMOS LUZ”, É O GRITO DAS NAÇÕES PAGÃS, QUE VEM ATRAVESSANDO O IMENSO MAR. IR JÁ, SIM, JÁ, LEVANDO NOVAS DE AMOR, SEM ESQUECER TAMBÉM AQUI DE SEMEAR.\n\nCORO MARCHAR, (MARCHAR, MARCHAR) SIM, AVANTE! (SIM, AVANTE!) MARCHAR, ERGUENDO O PENDÃO REAL! (MARCHAR, ERGUENDO O PENDÃO REAL!) AVANTE! (AVANTE, SIM) SIM, AVANTE, (SEMPRE, SIM) UNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! CONSAGRANDO TODO O VOSSO CORAÇÃO, PRA A JESUS OBEDECER, SEU QUERER EXECUTAR, ENTOAI, LOUVORES ALTOS! AVANÇAR!\n\nDESPERTA, IGREJA! O TEU PODER VEM EXERCER, A TODOS FAZE CRISTO CONHECER; A TUA MÃO ESTENDE COM PACIENTE AMOR; ESFORÇA-TE DA MORTE ETERNA A OS DETER.\n\nCORO MARCHAR, (MARCHAR, MARCHAR) SIM, AVANTE! (SIM, AVANTE!) MARCHAR, ERGUENDO O PENDÃO REAL! (MARCHAR, ERGUENDO O PENDÃO REAL!) AVANTE! (AVANTE, SIM) SIM, AVANTE, (SEMPRE, SIM) UNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! CONSAGRANDO TODO O VOSSO CORAÇÃO, PRA A JESUS OBEDECER, SEU QUERER EXECUTAR, ENTOAI, LOUVORES ALTOS! AVANÇAR!\n\nIGREJA, ALERTA! O DIA PROMETIDO VEM, QUANDO ACLAMADO O SALVADOR SERÁ; POR TODA A PARTE O BEM AMADO REDENTOR ETERNA GLÓRIA, HONRA E LOUVOR TERÁ.\n\nCORO MARCHAR, (MARCHAR, MARCHAR) SIM, AVANTE! (SIM, AVANTE!) MARCHAR, ERGUENDO O PENDÃO REAL! (MARCHAR, ERGUENDO O PENDÃO REAL!) AVANTE! (AVANTE, SIM) SIM, AVANTE, (SEMPRE, SIM) UNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! CONSAGRANDO TODO O VOSSO CORAÇÃO, PRA A JESUS OBEDECER, SEU QUERER EXECUTAR, ENTOAI, LOUVORES ALTOS! AVANÇAR!\n\nCORO MARCHAR, (MARCHAR, MARCHAR) SIM, AVANTE! (SIM, AVANTE!) MARCHAR, ERGUENDO O PENDÃO REAL! (MARCHAR, ERGUENDO O PENDÃO REAL!) AVANTE! (AVANTE, SIM) SIM, AVANTE, (SEMPRE, SIM) UNIDOS, FIRMES SEMPRE AO AVANÇAR.', 'É TEMPO, É TEMPO, O MESTRE ESTÁ CHAMANDO JÁ! MARCHAR, MARCHAR, CONFIANDO EM SEU AMOR! PARTIR, PARTIR, A SALVAÇÃO A PROCLAMAR, COM A PALAVRA SANTA DO BOM SALVADOR. CORO MARCHAR, (MARCHAR, MARCHAR) SIM, AVANTE! (SIM, AVANTE!) MARCHAR, ERGUENDO O PENDÃO REAL! (MARCHAR, ERGUENDO O PENDÃO REAL!) AVANTE! (AVANTE, SIM) SIM, AVANTE, (SEMPRE, SIM) UNIDOS, FIRMES SEMPRE AO AVANÇAR. GLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! CONSAGRANDO TODO O VOSSO CORAÇÃO, PRA A JESUS OBEDECER, SEU QUERER EXECUTAR, ENTOAI, LOUVORES ALTOS! AVANÇAR! QUEREMOS LUZ, É O GRITO DAS NAÇÕES PAGÃS, QUE VEM ATRAVESSANDO O IMENSO MAR. IR JÁ, SIM, JÁ, LEVANDO NOVAS DE AMOR, SEM ESQUECER TAMBÉM AQUI DE SEMEAR. DESPERTA, IGREJA! O TEU PODER VEM EXERCER, A TODOS FAZE CRISTO CONHECER; A TUA MÃO ESTENDE COM PACIENTE AMOR; ESFORÇA-TE DA MORTE ETERNA A OS DETER. IGREJA, ALERTA! O DIA PROMETIDO VEM, QUANDO ACLAMADO O SALVADOR SERÁ; POR TODA A PARTE O BEM AMADO REDENTOR ETERNA GLÓRIA, HONRA E LOUVOR TERÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('460', '460 - Ó JESUS! Ó VERA PÁSCOA', 'Ó JESUS! Ó VERA PÁSCOA! SUSPIRADA DOS ANTIGOS! Ó CORDEIRO ETERNO E MEIGO, DIGNA-TE ASSISTIR AQUI!\n\nBOM JESUS, Ó PÃO DIVINO! PELA FÉ TE RECEBEMOS; ÉS NAS ALMAS O ALIMENTO QUE SUSTENTA O NOSSO AMOR.\n\nBOM JESUS, Ó VINHO PURO! DE PERENE GOZO A FONTE! QUE NOSSA ALMA VIVA SEMPRE PARA TI, DE TI, EM TI. AMÉM.', 'Ó JESUS! Ó VERA PÁSCOA! SUSPIRADA DOS ANTIGOS! Ó CORDEIRO ETERNO E MEIGO, DIGNA-TE ASSISTIR AQUI! BOM JESUS, Ó PÃO DIVINO! PELA FÉ TE RECEBEMOS; ÉS NAS ALMAS O ALIMENTO QUE SUSTENTA O NOSSO AMOR. BOM JESUS, Ó VINHO PURO! DE PERENE GOZO A FONTE! QUE NOSSA ALMA VIVA SEMPRE PARA TI, DE TI, EM TI. AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('461', '461 - TU, Ó SENHOR, TU ÉS TÃO PURO (AVIVA A TUA OBRA)', 'TU, Ó SENHOR, TU ÉS TÃO PURO DE OLHOS E NÃO PODES VER O MAL, NEM INJUSTIÇA PODES CONTEMPLAR. ESCUTA, SENHOR, A NOSSA ORAÇÃO VEM OUVIR E AVIVA, SENHOR, A TUA OBRA!\n\nA TUA VOZ ESCUTAMOS COM TEMOR, POIS SABEMOS QUE O TEU JUÍZO BREVE VEM A TODA TERRA. NA IRA, Ó DEUS, VEM TER MISERICÓRDIA DE NÓS E AVIVA, SENHOR, A TUA OBRA!\n\nSENHOR, NA TUA IRA, Ó DEUS, VEM TER MISERICÓRDIA DE NÓS E AVIVA, SENHOR, A TUA OBRA! E AVIVA, SENHOR, A TUA OBRA!', 'TU, Ó SENHOR, TU ÉS TÃO PURO DE OLHOS E NÃO PODES VER O MAL, NEM INJUSTIÇA PODES CONTEMPLAR. ESCUTA, SENHOR, A NOSSA ORAÇÃO VEM OUVIR E AVIVA, SENHOR, A TUA OBRA! A TUA VOZ ESCUTAMOS COM TEMOR, POIS SABEMOS QUE O TEU JUÍZO BREVE VEM A TODA TERRA. NA IRA, Ó DEUS, VEM TER MISERICÓRDIA DE NÓS E AVIVA, SENHOR, A TUA OBRA! SENHOR, NA TUA IRA, Ó DEUS, VEM TER MISERICÓRDIA DE NÓS E AVIVA, SENHOR, A TUA OBRA! E AVIVA, SENHOR, A TUA OBRA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('462', '462 - QUANTOS FIÉIS, SENHOR', 'QUANTOS FIÉIS, SENHOR, MÁRTIRES E HERÓIS DA FÉ, DERAM SUA VIDA, POR TUA VINHA, TOMBARAM, MAS DE PÉ. OUVIU-SE NAS ARENAS SEU CANTO DE LOUVOR; MAIS QUE AS FOGUEIRAS, NELES ARDIA O AMOR DO SALVADOR.\n\nCORO GRAÇAS DOU POR TANTOS FIÉIS, SENHOR QUE FORAM MAIS FORTES DO QUE A MORTE E NOS LEGARAM DA FÉ A HERANÇA, FAZ DE NÓS VALENTES EM TUAS MÃOS E AVIVA TUA OBRA, Ó DEUS, REVELA O TEU PODER NESTA GERAÇÃO.\n\nRAMOS DE MIRRA FOSTE PRA TEUS FIÉIS, JESUS; FAMÍLIA, AMIGOS, BENS, MESMO A VIDA, TROCARAM PELA CRUZ; NUVENS DE TESTEMUNHAS QUE O CÉU VAI RECEBER COM PALMAS DE OURO, COROA E GLÓRIA A TODO O QUE VENCER.\n\nCORO GRAÇAS DOU POR TANTOS FIÉIS, SENHOR QUE FORAM MAIS FORTES DO QUE A MORTE E NOS LEGARAM DA FÉ A HERANÇA, FAZ DE NÓS VALENTES EM TUAS MÃOS E AVIVA TUA OBRA, Ó DEUS, REVELA O TEU PODER NESTA GERAÇÃO.', 'QUANTOS FIÉIS, SENHOR, MÁRTIRES E HERÓIS DA FÉ, DERAM SUA VIDA, POR TUA VINHA, TOMBARAM, MAS DE PÉ. OUVIU-SE NAS ARENAS SEU CANTO DE LOUVOR; MAIS QUE AS FOGUEIRAS, NELES ARDIA O AMOR DO SALVADOR. CORO GRAÇAS DOU POR TANTOS FIÉIS, SENHOR QUE FORAM MAIS FORTES DO QUE A MORTE E NOS LEGARAM DA FÉ A HERANÇA, FAZ DE NÓS VALENTES EM TUAS MÃOS E AVIVA TUA OBRA, Ó DEUS, REVELA O TEU PODER NESTA GERAÇÃO. RAMOS DE MIRRA FOSTE PRA TEUS FIÉIS, JESUS; FAMÍLIA, AMIGOS, BENS, MESMO A VIDA, TROCARAM PELA CRUZ; NUVENS DE TESTEMUNHAS QUE O CÉU VAI RECEBER COM PALMAS DE OURO, COROA E GLÓRIA A TODO O QUE VENCER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('463', '463 - VEM, SENHOR, VISITA TUA VINHA', 'VEM, SENHOR, VISITA TUA VINHA, QUE PLANTASTE EM NOSSAS VIDAS COM TUA DESTRA. VEM, SENHOR, VISITA TUA VINHA, QUE COMPRASTE COM TEU PRECIOSO SANGUE.\n\nCORO VEM, VISITA-ME; Ó VEM, PURIFICA-ME, SONDA O MEU CORAÇÃO E DÁ-ME O TEU SANTO PERDÃO E FAZE-ME REPOUSAR EM TI, SENHOR.\n\nQUERO SER, SENHOR, TEU SERVO AMADO POIS CONFIO EM TEU PODER Ó MEU JESUS. PODER QUE PARA MIM É INFINITO E É DADO A TODO AQUELE QUE EM TI CRER.\n\nCORO VEM, VISITA-ME; Ó VEM, PURIFICA-ME, SONDA O MEU CORAÇÃO E DÁ-ME O TEU SANTO PERDÃO E FAZE-ME REPOUSAR EM TI, SENHOR. FINAL: E FAZE-ME REPOUSAR EM TI, SENHOR.', 'VEM, SENHOR, VISITA TUA VINHA, QUE PLANTASTE EM NOSSAS VIDAS COM TUA DESTRA. VEM, SENHOR, VISITA TUA VINHA, QUE COMPRASTE COM TEU PRECIOSO SANGUE. CORO VEM, VISITA-ME; Ó VEM, PURIFICA-ME, SONDA O MEU CORAÇÃO E DÁ-ME O TEU SANTO PERDÃO E FAZE-ME REPOUSAR EM TI, SENHOR. QUERO SER, SENHOR, TEU SERVO AMADO POIS CONFIO EM TEU PODER Ó MEU JESUS. PODER QUE PARA MIM É INFINITO E É DADO A TODO AQUELE QUE EM TI CRER. CORO VEM, VISITA-ME; Ó VEM, PURIFICA-ME, SONDA O MEU CORAÇÃO E DÁ-ME O TEU SANTO PERDÃO E FAZE-ME REPOUSAR EM TI, SENHOR.  E FAZE-ME REPOUSAR EM TI, SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('464', '464 - TU QUE SOBRE A AMARGA CRUZ', 'TU, QUE SOBRE A AMARGA CRUZ REVELASTE TEU AMOR; TU, QUE VIVES, Ó JESUS, VIVIFICA-NOS, SENHOR!\n\nCORO VEM, Ó VEM, JESUS, SENHOR, NOSSAS ALMAS DESPERTAR! COM TEU SANTO E PURO AMOR VEM, SENHOR, NOS INFLAMAR, OH! VEM, OH! VEM, NOSSAS ALMAS INFLAMAR!\n\nEIS O MUNDO TENTADOR A QUERER NOS ATRAIR; SEM TEU FOGO ABRASADOR NÃO PODEMOS RESISTIR.\n\nCORO VEM, Ó VEM, JESUS, SENHOR, NOSSAS ALMAS DESPERTAR! COM TEU SANTO E PURO AMOR VEM, SENHOR, NOS INFLAMAR, OH! VEM, OH! VEM, NOSSAS ALMAS INFLAMAR!\n\nQUANTOS QUE CORRIAM BEM DE TI LONGE AGORA VÃO! OUTROS SEGUEM, MAS TAMBÉM SEM FERVOR VIVENDO ESTÃO.\n\nCORO VEM, Ó VEM, JESUS, SENHOR, NOSSAS ALMAS DESPERTAR! COM TEU SANTO E PURO AMOR VEM, SENHOR, NOS INFLAMAR, OH! VEM, OH! VEM, NOSSAS ALMAS INFLAMAR!\n\nVEM AGORA CONSUMIR TUDO QUANTO, Ó SALVADOR, QUER, ALTIVO, RESISTIR AO TEU BRANDO E SANTO AMOR.\n\nCORO VEM, Ó VEM, JESUS, SENHOR, NOSSAS ALMAS DESPERTAR! COM TEU SANTO E PURO AMOR VEM, SENHOR, NOS INFLAMAR, OH! VEM, OH! VEM, NOSSAS ALMAS INFLAMAR!', 'TU, QUE SOBRE A AMARGA CRUZ REVELASTE TEU AMOR; TU, QUE VIVES, Ó JESUS, VIVIFICA-NOS, SENHOR! CORO VEM, Ó VEM, JESUS, SENHOR, NOSSAS ALMAS DESPERTAR! COM TEU SANTO E PURO AMOR VEM, SENHOR, NOS INFLAMAR, OH! VEM, OH! VEM, NOSSAS ALMAS INFLAMAR! EIS O MUNDO TENTADOR A QUERER NOS ATRAIR; SEM TEU FOGO ABRASADOR NÃO PODEMOS RESISTIR. QUANTOS QUE CORRIAM BEM DE TI LONGE AGORA VÃO! OUTROS SEGUEM, MAS TAMBÉM SEM FERVOR VIVENDO ESTÃO. VEM AGORA CONSUMIR TUDO QUANTO, Ó SALVADOR, QUER, ALTIVO, RESISTIR AO TEU BRANDO E SANTO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('465', '465 - O ESTANDARTE DESTA IGREJA', 'O ESTANDARTE DESTA IGREJA LEVANTEMOS SEM TEMOR! ELA É A MUITO AMADA ESPOSA DO BENDITO SALVADOR; É JESUS O COMANDANTE VERDADEIRO QUE A CONDUZ, SOMOS, NÓS, OS SEUS SOLDADOS NESTA IGREJA DE JESUS.\n\nCORO RESOLUTOS, AVANÇAI, TRABALHANDO POR JESUS! O ESTANDARTE LEVANTAI, ESPALHANDO A SUA LUZ.\n\nÓ IGREJA DEDIQUEMOS NOSSOS CORPOS AO SENHOR! NÃO DEVEMOS SER ESCRAVOS DO SAGAZ ENGANADOR. AS RIQUEZAS NOS SÃO DADAS PELA TERNA MÃO REAL, E O SENHOR DO CÉU OBSERVA SE FAZEMOS BEM OU MAL.\n\nCORO RESOLUTOS, AVANÇAI, TRABALHANDO POR JESUS! O ESTANDARTE LEVANTAI, ESPALHANDO A SUA LUZ.\n\nGRAÇA E GLÓRIA A TI PERTENCEM, Ó ESPOSA DO SENHOR! SÊ, ENTÃO, UM INSTRUMENTO DE SALVAR O PECADOR; POIS ATÉ OS FINS DO MUNDO CRISTO MESMO REINARÁ E O DOMÍNIO DO EVANGELHO TODA A TERRA ABRANGERÁ.\n\nCORO RESOLUTOS, AVANÇAI, TRABALHANDO POR JESUS! O ESTANDARTE LEVANTAI, ESPALHANDO A SUA LUZ.', 'O ESTANDARTE DESTA IGREJA LEVANTEMOS SEM TEMOR! ELA É A MUITO AMADA ESPOSA DO BENDITO SALVADOR; É JESUS O COMANDANTE VERDADEIRO QUE A CONDUZ, SOMOS, NÓS, OS SEUS SOLDADOS NESTA IGREJA DE JESUS. CORO RESOLUTOS, AVANÇAI, TRABALHANDO POR JESUS! O ESTANDARTE LEVANTAI, ESPALHANDO A SUA LUZ. Ó IGREJA DEDIQUEMOS NOSSOS CORPOS AO SENHOR! NÃO DEVEMOS SER ESCRAVOS DO SAGAZ ENGANADOR. AS RIQUEZAS NOS SÃO DADAS PELA TERNA MÃO REAL, E O SENHOR DO CÉU OBSERVA SE FAZEMOS BEM OU MAL. GRAÇA E GLÓRIA A TI PERTENCEM, Ó ESPOSA DO SENHOR! SÊ, ENTÃO, UM INSTRUMENTO DE SALVAR O PECADOR; POIS ATÉ OS FINS DO MUNDO CRISTO MESMO REINARÁ E O DOMÍNIO DO EVANGELHO TODA A TERRA ABRANGERÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('466', '466 - EIS O ESTANDARTE', 'EIS O ESTANDARTE TREMULANDO À LUZ, EIS A SUA DIVISA: C’ROA SOBRE CRUZ! PARA A SANTA GUERRA ELE VOS CONDUZ; QUEM QUER ALISTAR-SE SOB O REI JESUS?\n\nCORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! GUERRA CONTRA AS TREVAS! GUERRA CONTRA O MAL! E CONTRA O PECADO! GUERRA DIVINAL! GUERRA CONTRA O MUNDO! NELA QUEM ENTRAR HÁ DE, SEM RESERVAS, TUDO ABANDONAR.\n\nCORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! TUDO! SOA DURO? RECEAIS A CRUZ? NÃO VOS ENVERGONHE A GRAÇA DE JESUS! Ó IRMÃOS, LEMBRAI-VOS, QUEM POR ELE SOFRER A COROA DA SUA MÃO HÁ DE RECEBER!\n\nCORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! NESTA SANTA GUERRA DESEJAIS LUTAR E A C’ROA DE GLÓRIA LÁ NO CÉU GANHAR? A QUEM QUER SEGUI-LO, EIS QUE DIZ JESUS: “NEGUE-SE A SI MESMO E TOME A SUA CRUZ”.\n\nCORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! SALVADOR, EU HOJE VENHO ME RENDER, SÓ POR TI VENCIDO PODEREI VENCER; SÓ CONTIGO MORTO SEMPRE VIVEREI, TOMO AGORA MINHA CRUZ, MEU BONDOSO REI!\n\nCORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! SOB TEU ESTANDARTE MARCHAREI, JESUS, TUA DIVISA É MINHA: C’ROA SOBRE CRUZ!', 'EIS O ESTANDARTE TREMULANDO À LUZ, EIS A SUA DIVISA: C’ROA SOBRE CRUZ! PARA A SANTA GUERRA ELE VOS CONDUZ; QUEM QUER ALISTAR-SE SOB O REI JESUS? CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! GUERRA CONTRA AS TREVAS! GUERRA CONTRA O MAL! E CONTRA O PECADO! GUERRA DIVINAL! GUERRA CONTRA O MUNDO! NELA QUEM ENTRAR HÁ DE, SEM RESERVAS, TUDO ABANDONAR. CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! TUDO! SOA DURO? RECEAIS A CRUZ? NÃO VOS ENVERGONHE A GRAÇA DE JESUS! Ó IRMÃOS, LEMBRAI-VOS, QUEM POR ELE SOFRER A COROA DA SUA MÃO HÁ DE RECEBER! CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! NESTA SANTA GUERRA DESEJAIS LUTAR E A C’ROA DE GLÓRIA LÁ NO CÉU GANHAR? A QUEM QUER SEGUI-LO, EIS QUE DIZ JESUS: NEGUE-SE A SI MESMO E TOME A SUA CRUZ. CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! SALVADOR, EU HOJE VENHO ME RENDER, SÓ POR TI VENCIDO PODEREI VENCER; SÓ CONTIGO MORTO SEMPRE VIVEREI, TOMO AGORA MINHA CRUZ, MEU BONDOSO REI! CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! SOB TEU ESTANDARTE MARCHAREI, JESUS, TUA DIVISA É MINHA: C’ROA SOBRE CRUZ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('467', '467 - OS GUERREIROS SE PREPARAM', 'OS GUERREIROS SE PREPARAM PARA A GRANDE LUTA, É JESUS O CAPITÃO QUE AVANTE OS LEVARÁ; A MILÍCIA DOS REMIDOS MARCHA IMPOLUTA CERTA QUE A VITÓRIA ALCANÇARÁ.\n\nCORO EU QUERO ESTAR COM CRISTO ONDE A LUTA SE TRAVAR, NO LANCE IMPREVISTO NA FRENTE ME ENCONTRAR; ATÉ QUE O POSSA VER NA GLÓRIA, ME ALEGRANDO DA VITÓRIA ONDE DEUS VAI ME COROAR.\n\nEIS OS BATALHÕES DE CRISTO PROSSEGUINDO AVANTE! NÃO OS VÊS COM QUE VALOR COMBATEM CONTRA O MAL? PODES TU FICAR DORMINDO, MESMO VACILANTE, QUANDO ATACAM OUTROS A BELIAL?\n\nCORO EU QUERO ESTAR COM CRISTO ONDE A LUTA SE TRAVAR, NO LANCE IMPREVISTO NA FRENTE ME ENCONTRAR; ATÉ QUE O POSSA VER NA GLÓRIA, ME ALEGRANDO DA VITÓRIA ONDE DEUS VAI ME COROAR.\n\nDÁ-TE PRESSA, NÃO VACILES, HOJE DEUS TE CHAMA PARA VIRES PELEJAR AO LADO DO SENHOR; ENTRA NA BATALHA ONDE MAIS O FOGO INFLAMA E PELEJA CONTRA O VIL TENTADOR.\n\nCORO EU QUERO ESTAR COM CRISTO ONDE A LUTA SE TRAVAR, NO LANCE IMPREVISTO NA FRENTE ME ENCONTRAR; ATÉ QUE O POSSA VER NA GLÓRIA, ME ALEGRANDO DA VITÓRIA ONDE DEUS VAI ME COROAR.\n\nA PELEJA É TREMENDA, TORNA-SE RENHIDA, MAS SÃO POUCOS OS SOLDADOS PARA BATALHAR; Ó VEM LIBERTAR AS POBRES ALMAS OPRIMIDAS DE QUEM, FURIOSO, AS QUER TRAGAR.\n\nCORO EU QUERO ESTAR COM CRISTO ONDE A LUTA SE TRAVAR, NO LANCE IMPREVISTO NA FRENTE ME ENCONTRAR; ATÉ QUE O POSSA VER NA GLÓRIA, ME ALEGRANDO DA VITÓRIA ONDE DEUS VAI ME COROAR.', 'OS GUERREIROS SE PREPARAM PARA A GRANDE LUTA, É JESUS O CAPITÃO QUE AVANTE OS LEVARÁ; A MILÍCIA DOS REMIDOS MARCHA IMPOLUTA CERTA QUE A VITÓRIA ALCANÇARÁ. CORO EU QUERO ESTAR COM CRISTO ONDE A LUTA SE TRAVAR, NO LANCE IMPREVISTO NA FRENTE ME ENCONTRAR; ATÉ QUE O POSSA VER NA GLÓRIA, ME ALEGRANDO DA VITÓRIA ONDE DEUS VAI ME COROAR. EIS OS BATALHÕES DE CRISTO PROSSEGUINDO AVANTE! NÃO OS VÊS COM QUE VALOR COMBATEM CONTRA O MAL? PODES TU FICAR DORMINDO, MESMO VACILANTE, QUANDO ATACAM OUTROS A BELIAL? DÁ-TE PRESSA, NÃO VACILES, HOJE DEUS TE CHAMA PARA VIRES PELEJAR AO LADO DO SENHOR; ENTRA NA BATALHA ONDE MAIS O FOGO INFLAMA E PELEJA CONTRA O VIL TENTADOR. A PELEJA É TREMENDA, TORNA-SE RENHIDA, MAS SÃO POUCOS OS SOLDADOS PARA BATALHAR; Ó VEM LIBERTAR AS POBRES ALMAS OPRIMIDAS DE QUEM, FURIOSO, AS QUER TRAGAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('468', '468 - HÁ TANTA TERRA', 'HÁ TANTA TERRA PRA POSSUIR, MAS NA BATALHA NÃO ESTAMOS SÓS; MUITAS PROMESSAS SE VÃO CUMPRIR SE O SENHOR SE AGRADAR DE NÓS.\n\nCORO (2X) ASSIM NÓS VAMOS COM FÉ NO TEU PODER, SE FORES CONOSCO, SENHOR, IREMOS VENCER! INSTRUMENTOS\n\nCERCA A CIDADE COM ORAÇÃO, LEVANTA, IGREJA, A TUA VOZ; DIANTE DE TI MUROS RUIRÃO SE O SENHOR SE AGRADAR DE NÓS.\n\nCORO (2X) ASSIM NÓS VAMOS COM FÉ NO TEU PODER, SE FORES CONOSCO, SENHOR, IREMOS VENCER!', 'HÁ TANTA TERRA PRA POSSUIR, MAS NA BATALHA NÃO ESTAMOS SÓS; MUITAS PROMESSAS SE VÃO CUMPRIR SE O SENHOR SE AGRADAR DE NÓS. CORO  ASSIM NÓS VAMOS COM FÉ NO TEU PODER, SE FORES CONOSCO, SENHOR, IREMOS VENCER! INSTRUMENTOS CERCA A CIDADE COM ORAÇÃO, LEVANTA, IGREJA, A TUA VOZ; DIANTE DE TI MUROS RUIRÃO SE O SENHOR SE AGRADAR DE NÓS. CORO  ASSIM NÓS VAMOS COM FÉ NO TEU PODER, SE FORES CONOSCO, SENHOR, IREMOS VENCER!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('469', '469 - CASTELO FORTE', 'CASTELO FORTE É O NOSSO DEUS, ESPADA E BOM ESCUDO; COM SEU PODER DEFENDE OS SEUS, SOCORRO TRAZ E PROTEÇÃO. SABEIS QUEM É JESUS? O QUE VENCEU NA CRUZ, SENHOR DOS ALTOS CÉUS E SENDO O PRÓPRIO DEUS, TRIUNFA NA BATALHA.\n\nA NOSSA FORÇA NADA FAZ, ESTAMOS CONVENCIDOS; MAS NOSSO DEUS SOCORRO TRAZ E SOMOS PROTEGIDOS. SE TEMOS DE PERDER RIQUEZAS, BENS QUE HOUVER, EMBORA A VIDA VÁ, POR NÓS JESUS ESTÁ E DAR-NOS-Á SEU REINO.\n\nSIM, QUE A PALAVRA FICARÁ SABEMOS COM CERTEZA; E NADA NOS ASSUSTARÁ COM CRISTO POR DEFESA. SABEIS QUEM É JESUS? O QUE VENCEU NA CRUZ, SENHOR DOS ALTOS CÉUS E SENDO O PRÓPRIO DEUS, TRIUNFA NA BATALHA.', 'CASTELO FORTE É O NOSSO DEUS, ESPADA E BOM ESCUDO; COM SEU PODER DEFENDE OS SEUS, SOCORRO TRAZ E PROTEÇÃO. SABEIS QUEM É JESUS? O QUE VENCEU NA CRUZ, SENHOR DOS ALTOS CÉUS E SENDO O PRÓPRIO DEUS, TRIUNFA NA BATALHA. A NOSSA FORÇA NADA FAZ, ESTAMOS CONVENCIDOS; MAS NOSSO DEUS SOCORRO TRAZ E SOMOS PROTEGIDOS. SE TEMOS DE PERDER RIQUEZAS, BENS QUE HOUVER, EMBORA A VIDA VÁ, POR NÓS JESUS ESTÁ E DAR-NOS-Á SEU REINO. SIM, QUE A PALAVRA FICARÁ SABEMOS COM CERTEZA; E NADA NOS ASSUSTARÁ COM CRISTO POR DEFESA. SABEIS QUEM É JESUS? O QUE VENCEU NA CRUZ, SENHOR DOS ALTOS CÉUS E SENDO O PRÓPRIO DEUS, TRIUNFA NA BATALHA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('470', '470 - NO GETSÊMANI', 'NO GETSÊMANI JESUS OROU: “PASSA DE MIM ESTE CÁLICE”, MAS O ALTAR ESTAVA PRONTO PARA O RECEBER; O CUTELO, A LENHA E O FOGO AGUARDAVAM O CORDEIRO QUE DARIA A SUA VIDA PELO MUNDO.\n\nCORO AO LAGAR DESCEU O FRUTO DA VIDEIRA E CORREU DA COR DO SANGUE O SEU MOSTO PRA QUE EU PUDESSE ENCHER MEU CÁLICE DE SALVAÇÃO. INSTRUMENTOS\nBIS\n\nHOJE AQUI NÓS CELEBRAMOS ESTA PÁSCOA VERDADEIRA POIS DO JUGO LIBERTADOS TEMOS PAZ COM DEUS; COMO MEMBROS DE UM SÓ CORPO PARTILHAMOS DE UM MESMO PÃO E BEBEMOS DESTE CÁLICE DO SEU SANGUE.\n\nCORO AO LAGAR DESCEU O FRUTO DA VIDEIRA E CORREU DA COR DO SANGUE O SEU MOSTO PRA QUE EU PUDESSE ENCHER MEU CÁLICE DE SALVAÇÃO.\nBIS', 'NO GETSÊMANI JESUS OROU: PASSA DE MIM ESTE CÁLICE, MAS O ALTAR ESTAVA PRONTO PARA O RECEBER; O CUTELO, A LENHA E O FOGO AGUARDAVAM O CORDEIRO QUE DARIA A SUA VIDA PELO MUNDO. CORO AO LAGAR DESCEU O FRUTO DA VIDEIRA E CORREU DA COR DO SANGUE O SEU MOSTO PRA QUE EU PUDESSE ENCHER MEU CÁLICE DE SALVAÇÃO. INSTRUMENTOS HOJE AQUI NÓS CELEBRAMOS ESTA PÁSCOA VERDADEIRA POIS DO JUGO LIBERTADOS TEMOS PAZ COM DEUS; COMO MEMBROS DE UM SÓ CORPO PARTILHAMOS DE UM MESMO PÃO E BEBEMOS DESTE CÁLICE DO SEU SANGUE. CORO AO LAGAR DESCEU O FRUTO DA VIDEIRA E CORREU DA COR DO SANGUE O SEU MOSTO PRA QUE EU PUDESSE ENCHER MEU CÁLICE DE SALVAÇÃO.',  1);
