INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('472', '472 - VINDE, CANTAI E ENTOAI LOUVORES A JESUS', 'VINDE, CANTAI E ENTOAI LOUVORES A JESUS, QUE PARA A NOSSA SALVAÇÃO MORREU NA AMARGA CRUZ; SEU SANGUE DERRAMOU, DE TUDO ME LAVOU, MAIS ALVO DO QUE A NEVE ME TORNOU.\n\nCORO O SANGUE DE JESUS ME LAVOU, ME LAVOU, O SANGUE DE JESUS ME LAVOU, ME LAVOU, ALEGRE CANTAREI LOUVORES AO MEU REI, A MEU SENHOR JESUS QUE ME SALVOU.\n\nVINDE CONOSCO VOS UNIR NA GUERRA CONTRA O MAL E COM O NOSSO SALVADOR EM MARCHA TRIUNFAL, A TODOS PROCLAMAR SUA GRAÇA E SEU PODER, SEU SANGUE DERRAMOU PRA NOS SALVAR.\n\nCORO O SANGUE DE JESUS ME LAVOU, ME LAVOU, O SANGUE DE JESUS ME LAVOU, ME LAVOU, ALEGRE CANTAREI LOUVORES AO MEU REI, A MEU SENHOR JESUS QUE ME SALVOU.\n\nO CAPITÃO DA SALVAÇÃO É CRISTO, O SALVADOR; O REI DOS REIS, O REDENTOR, JESUS, NOSSO SENHOR; ELE TUDO VENCERÁ, VITÓRIA NOS DARÁ, À GLÓRIA, SALVOS, NOS CONDUZIRÁ.\n\nCORO O SANGUE DE JESUS ME LAVOU, ME LAVOU, O SANGUE DE JESUS ME LAVOU, ME LAVOU, ALEGRE CANTAREI LOUVORES AO MEU REI, A MEU SENHOR JESUS QUE ME SALVOU.', 'VINDE, CANTAI E ENTOAI LOUVORES A JESUS, QUE PARA A NOSSA SALVAÇÃO MORREU NA AMARGA CRUZ; SEU SANGUE DERRAMOU, DE TUDO ME LAVOU, MAIS ALVO DO QUE A NEVE ME TORNOU. CORO O SANGUE DE JESUS ME LAVOU, ME LAVOU, O SANGUE DE JESUS ME LAVOU, ME LAVOU, ALEGRE CANTAREI LOUVORES AO MEU REI, A MEU SENHOR JESUS QUE ME SALVOU. VINDE CONOSCO VOS UNIR NA GUERRA CONTRA O MAL E COM O NOSSO SALVADOR EM MARCHA TRIUNFAL, A TODOS PROCLAMAR SUA GRAÇA E SEU PODER, SEU SANGUE DERRAMOU PRA NOS SALVAR. O CAPITÃO DA SALVAÇÃO É CRISTO, O SALVADOR; O REI DOS REIS, O REDENTOR, JESUS, NOSSO SENHOR; ELE TUDO VENCERÁ, VITÓRIA NOS DARÁ, À GLÓRIA, SALVOS, NOS CONDUZIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('473', '473 - MINHA PORÇÃO', 'MINHA PORÇÃO ÉS TU, SENHOR, E TODO MEU SER TE DAREI COM AMOR. FOI NO TEU FALAR QUE DESCOBRI, QUE ÉS TUDO, TUDO PRA MIM!\n\nCORO ÉS MEU AUXÍLIO, MEU GRANDE AMOR, E O MEU TESOURO, ÉS TU MEU SENHOR. NA TUA OBRA VOU ME DEDICAR, E SEMPRE TE AMAR.\n\nQUERO OUVIR TUA VOZ EM MEU VIVER, POIS QUANDO TU FALAS SINTO GOZO EM MEU SER. TUA DESTRA É A LUZ QUE ME CONDUZ, AOS TEUS BRAÇOS, MEU PAI E SENHOR.\n\nCORO (BIS) ÉS MEU AUXÍLIO, MEU GRANDE AMOR, E O MEU TESOURO, ÉS TU MEU SENHOR. NA TUA OBRA VOU ME DEDICAR, E SEMPRE TE AMAR. FINAL: E SEMPRE TE AMAR.', 'MINHA PORÇÃO ÉS TU, SENHOR, E TODO MEU SER TE DAREI COM AMOR. FOI NO TEU FALAR QUE DESCOBRI, QUE ÉS TUDO, TUDO PRA MIM! CORO ÉS MEU AUXÍLIO, MEU GRANDE AMOR, E O MEU TESOURO, ÉS TU MEU SENHOR. NA TUA OBRA VOU ME DEDICAR, E SEMPRE TE AMAR. QUERO OUVIR TUA VOZ EM MEU VIVER, POIS QUANDO TU FALAS SINTO GOZO EM MEU SER. TUA DESTRA É A LUZ QUE ME CONDUZ, AOS TEUS BRAÇOS, MEU PAI E SENHOR. CORO  ÉS MEU AUXÍLIO, MEU GRANDE AMOR, E O MEU TESOURO, ÉS TU MEU SENHOR. NA TUA OBRA VOU ME DEDICAR, E SEMPRE TE AMAR.  E SEMPRE TE AMAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('474', '474 - QUEM SOU EU, SENHOR?', 'QUEM SOU EU, SENHOR? PRA TE LEMBRARES DE MIM, PRA ME TRAZERES ATÉ AQUI E ME DIZER COISAS TÃO LINDAS.\n\nTU SABES QUEM EU SOU, O MENOR DE MINHA CASA, E NO TEU AMOR, SENHOR, PROFETIZASTE A MIM UM CAMINHO NOVO A TRILHAR.\n\nCORO (2X) ME TOMASTE, SENHOR, DA TUA MALHADA, DE TRÁS DAS TUAS OVELHAS ME TIRASTE Ó DEUS, POR ONDE QUER QUE EU FOR, PARA O TEU LOUVOR, A TUA PRESENÇA COMIGO IRÁ. FINAL: SE MINHA VIDA ABENÇOARES, PARA SEMPRE ABENÇOADA SERÁ.', 'QUEM SOU EU, SENHOR? PRA TE LEMBRARES DE MIM, PRA ME TRAZERES ATÉ AQUI E ME DIZER COISAS TÃO LINDAS. TU SABES QUEM EU SOU, O MENOR DE MINHA CASA, E NO TEU AMOR, SENHOR, PROFETIZASTE A MIM UM CAMINHO NOVO A TRILHAR. CORO  ME TOMASTE, SENHOR, DA TUA MALHADA, DE TRÁS DAS TUAS OVELHAS ME TIRASTE Ó DEUS, POR ONDE QUER QUE EU FOR, PARA O TEU LOUVOR, A TUA PRESENÇA COMIGO IRÁ.  SE MINHA VIDA ABENÇOARES, PARA SEMPRE ABENÇOADA SERÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('475', '475 - AINDA UMA VEZ (AGEU)', '“AINDA UMA VEZ, DAQUI A POUCO, E FAREI TREMER OS CÉUS, E TODA A TERRA, TODO MAR E A TERRA SECA, FAREI TREMER NAÇÕES, E ENFIM VIRÁ O DESEJADO DAS NAÇÕES.”\n\nCORO “E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR),” DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR.\n\n“MINHA É A PRATA E MEU O OURO”, ASSIM DIZ O SENHOR, “E A GLÓRIA DESTA CASA ENFIM EXCEDERÁ, A GLÓRIA DA PRIMEIRA, E NESTE LUGAR, DAREI A MINHA PAZ.”\n\nCORO “E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR),” DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR DOS EXÉRCITOS!\n\nFINAL: “SIM ESTE LUGAR (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR)” DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR JESUS.', 'AINDA UMA VEZ, DAQUI A POUCO, E FAREI TREMER OS CÉUS, E TODA A TERRA, TODO MAR E A TERRA SECA, FAREI TREMER NAÇÕES, E ENFIM VIRÁ O DESEJADO DAS NAÇÕES. CORO E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR), DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR. MINHA É A PRATA E MEU O OURO, ASSIM DIZ O SENHOR, E A GLÓRIA DESTA CASA ENFIM EXCEDERÁ, A GLÓRIA DA PRIMEIRA, E NESTE LUGAR, DAREI A MINHA PAZ. CORO E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR), DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR DOS EXÉRCITOS! SIM ESTE LUGAR (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR) DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('476', '476 - ESTA OBRA É DE DEUS', 'ESTA OBRA É DE DEUS, NINGUÉM A PODE IMPEDIR, FOI JESUS QUEM CONVOCOU ESTÁ NA HORA DE AGIR.\nBIS\n\nVAMOS TODOS! VAMOS TODOS! TRABALHAR PARA A GLÓRIA DE JESUS. ALELUIA! ALELUIA! LOUVANDO AQUELE QUE POR NÓS MORREU NA CRUZ, GLÓRIA A JESUS! ALELUIA! ALELUIA! GLORIFICADO SEJA O NOME DO SENHOR.\n\nVIVO CONTENTE, VIVO ALEGRE, PORQUE JESUS JÁ ME LIBERTOU. VIVO CONTENTE, VIVO ALEGRE, PORQUE JESUS JÁ ME LIBERTOU.', 'ESTA OBRA É DE DEUS, NINGUÉM A PODE IMPEDIR, FOI JESUS QUEM CONVOCOU ESTÁ NA HORA DE AGIR. VAMOS TODOS! VAMOS TODOS! TRABALHAR PARA A GLÓRIA DE JESUS. ALELUIA! ALELUIA! LOUVANDO AQUELE QUE POR NÓS MORREU NA CRUZ, GLÓRIA A JESUS! ALELUIA! ALELUIA! GLORIFICADO SEJA O NOME DO SENHOR. VIVO CONTENTE, VIVO ALEGRE, PORQUE JESUS JÁ ME LIBERTOU. VIVO CONTENTE, VIVO ALEGRE, PORQUE JESUS JÁ ME LIBERTOU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('477', '477 - SEMPRE VOU LEMBRAR', 'SEMPRE VOU LEMBRAR DA CORAGEM E DO AMOR, DA FIRMEZA E VONTADE, DA FÉ E DEVOÇÃO. DA IGREJA DO TEMPO DE PAULO, DE PEDRO E JOÃO. ENFRENTARAM A FÚRIA DE ROMA, MAS NUNCA NEGARAM SUA FÉ DE CRISTÃO É UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO.\n\nSEMPRE VOU LEMBRAR DA BRAVURA DOS SANTOS, EM PLENA ARENA ENFRENTANDO OS LEÕES. QUANTO MAIS A FOGUEIRA QUEIMAVA, MAIS SE OUVIA O LOUVOR DOS NOSSOS IRMÃOS. SOBRE O SANGUE TOMBAVAM NAS RUAS, CHEGAVAM NOS CÉUS COM A VITÓRIA NAS MÃOS. É UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO.\n\nCORO Ó MEU DEUS, REAVIVA A TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMO FORAM OS PRIMEIROS CRISTÃOS. Ó MEU DEUS, REAVIVA TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMEÇANDO EM MEU CORAÇÃO. INSTRUMENTOS\n\nSEMPRE VOU LEMBRAR DA BRAVURA DOS SANTOS, EM PLENA ARENA ENFRENTANDO OS LEÕES. QUANTO MAIS A FOGUEIRA QUEIMAVA, MAIS SE OUVIA O LOUVOR DOS NOSSOS IRMÃOS. SOBRE O SANGUE TOMBAVAM NAS RUAS, CHEGAVAM NOS CÉUS COM A VITÓRIA NAS MÃOS. É UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO.\n\nCORO Ó MEU DEUS, REAVIVA A TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMO FORAM OS PRIMEIROS CRISTÃOS. Ó MEU DEUS, REAVIVA TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMEÇANDO EM MEU CORAÇÃO.\n\nFINAL: Ó MEU DEUS, REAVIVA TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMEÇANDO EM MEU CORAÇÃO.', 'SEMPRE VOU LEMBRAR DA CORAGEM E DO AMOR, DA FIRMEZA E VONTADE, DA FÉ E DEVOÇÃO. DA IGREJA DO TEMPO DE PAULO, DE PEDRO E JOÃO. ENFRENTARAM A FÚRIA DE ROMA, MAS NUNCA NEGARAM SUA FÉ DE CRISTÃO É UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO. SEMPRE VOU LEMBRAR DA BRAVURA DOS SANTOS, EM PLENA ARENA ENFRENTANDO OS LEÕES. QUANTO MAIS A FOGUEIRA QUEIMAVA, MAIS SE OUVIA O LOUVOR DOS NOSSOS IRMÃOS. SOBRE O SANGUE TOMBAVAM NAS RUAS, CHEGAVAM NOS CÉUS COM A VITÓRIA NAS MÃOS. É UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO. CORO Ó MEU DEUS, REAVIVA A TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE