INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('478', '478 - VI UM NOVO CÉU', 'VI UM NOVO CÉU, UMA NOVA TERRA, EU VI A NOVA JERUSALÉM. VI UM TRONO BRANCO E SOBRE ELE UM SER, CUJO ROSTO EU NÃO PUDE VER MAS EU SEI QUE ERA O MEU JESUS.\nBIS\n\nVI UM CORO CELESTIAL, VI OS ANJOS DE DEUS QUE CANTAVAM EM UMA SÓ VOZ: VIVA O REI JESUS! INSTRUMENTOS EM BREVE ESTAREMOS NESSE NOVO LAR, CANTANDO: VIVA O REI JESUS! EM RUAS DE OURO NÓS VAMOS ANDAR, DA ÁRVORE DA VIDA DESFRUTAR.\nBIS\n\nE COM JESUS PARA SEMPRE MORAR, (VOU MORAR) (VARÕES) E COM JESUS PARA SEMPRE MORAR, (VOU LOUVAR) (VARÕES) E COM JESUS PARA SEMPRE MORAR.', 'VI UM NOVO CÉU, UMA NOVA TERRA, EU VI A NOVA JERUSALÉM. VI UM TRONO BRANCO E SOBRE ELE UM SER, CUJO ROSTO EU NÃO PUDE VER MAS EU SEI QUE ERA O MEU JESUS. VI UM CORO CELESTIAL, VI OS ANJOS DE DEUS QUE CANTAVAM EM UMA SÓ VOZ: VIVA O REI JESUS! INSTRUMENTOS EM BREVE ESTAREMOS NESSE NOVO LAR, CANTANDO: VIVA O REI JESUS! EM RUAS DE OURO NÓS VAMOS ANDAR, DA ÁRVORE DA VIDA DESFRUTAR. E COM JESUS PARA SEMPRE MORAR, (VOU MORAR)  E COM JESUS PARA SEMPRE MORAR, (VOU LOUVAR)  E COM JESUS PARA SEMPRE MORAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('479', '479 - QUEM ME DERA', 'QUEM ME DERA FOSSEM AGORA ESCRITAS AS MINHAS PALAVRAS, COM PENA DE FERRO GRAVADAS, PRA SEMPRE ESCULPIDAS NA ROCHA.\n\nCORO EU SEI QUE VIVE MEU REDENTOR E POR FIM SOBRE A TERRA VIRÁ; O MEU CORPO SE LEVANTARÁ REVESTIDO DE GLÓRIA E LOUVOR. INSTRUMENTOS\n\nFACE A FACE VEREI MEU SENHOR, OS MEUS OLHOS O CONTEMPLARÃO; DE SAUDADE PULSA O CORAÇÃO, MINH’ALMA CLAMA: VEM, REDENTOR!\n\nCORO (2X) EU SEI QUE VIVE MEU REDENTOR E POR FIM SOBRE A TERRA VIRÁ; O MEU CORPO SE LEVANTARÁ REVESTIDO DE GLÓRIA E LOUVOR. REVESTIDO DE GLÓRIA E LOUVOR. REVESTIDO DE GLÓRIA E LOUVOR.', 'QUEM ME DERA FOSSEM AGORA ESCRITAS AS MINHAS PALAVRAS, COM PENA DE FERRO GRAVADAS, PRA SEMPRE ESCULPIDAS NA ROCHA. CORO EU SEI QUE VIVE MEU REDENTOR E POR FIM SOBRE A TERRA VIRÁ; O MEU CORPO SE LEVANTARÁ REVESTIDO DE GLÓRIA E LOUVOR. INSTRUMENTOS FACE A FACE VEREI MEU SENHOR, OS MEUS OLHOS O CONTEMPLARÃO; DE SAUDADE PULSA O CORAÇÃO, MINH’ALMA CLAMA: VEM, REDENTOR! CORO  EU SEI QUE VIVE MEU REDENTOR E POR FIM SOBRE A TERRA VIRÁ; O MEU CORPO SE LEVANTARÁ REVESTIDO DE GLÓRIA E LOUVOR. REVESTIDO DE GLÓRIA E LOUVOR. REVESTIDO DE GLÓRIA E LOUVOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('480', '480 - EU ESPERO, ANSIOSO, POR MEU DEUS', 'EU ESPERO, ANSIOSO, POR MEU DEUS EU ESPERO, ANSIOSO, POR MEU DEUS DE, COM ELE, ESTAR PARA SEMPRE LÁ NO CÉU, COM MEU DEUS QUERO ESTAR PARA SEMPRE.\n\nMUITAS LUTAS EU TEREI DE ENFRENTAR, MUITAS LUTAS EU TEREI DE SUPORTAR, MAS SEI QUE O MEU SENHOR NUNCA MAIS VAI ME DEIXAR, COM MEU DEUS QUERO ESTAR PARA SEMPRE.\n\nCOM MEU DEUS EU SOU MUITO FELIZ (2X) POIS SEI QUE O MEU SENHOR NUNCA MAIS VAI ME DEIXAR, COM MEU DEUS QUERO ESTAR PARA SEMPRE, PARA SEMPRE VOU ESTAR.', 'EU ESPERO, ANSIOSO, POR MEU DEUS EU ESPERO, ANSIOSO, POR MEU DEUS DE, COM ELE, ESTAR PARA SEMPRE LÁ NO CÉU, COM MEU DEUS QUERO ESTAR PARA SEMPRE. MUITAS LUTAS EU TEREI DE ENFRENTAR, MUITAS LUTAS EU TEREI DE SUPORTAR, MAS SEI QUE O MEU SENHOR NUNCA MAIS VAI ME DEIXAR, COM MEU DEUS QUERO ESTAR PARA SEMPRE. COM MEU DEUS EU SOU MUITO FELIZ  POIS SEI QUE O MEU SENHOR NUNCA MAIS VAI ME DEIXAR, COM MEU DEUS QUERO ESTAR PARA SEMPRE, PARA SEMPRE VOU ESTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('481', '481 - ASSIM COMO A NOITE ESPERA PELO DIA', 'ASSIM COMO A NOITE ESPERA PELO DIA, ASSIM COMO A PRAIA ESPERA PELO MAR ASSIM COMO AS NUVENS ESPERAM PELO VENTO, COM MAIS ANSIEDADE EU ESTOU A TE ESPERAR.\n\nCORO TE ESPERO, CHORANDO, TE ESPERO, MESTRE QUERIDO, ESTOU ME ENCURVANDO ANTE O PESO DA CRUZ; ME AJUDA A CHEGAR AO MEU LAR PROMETIDO, SEM TI NÃO CONSIGO VIVER, MEU JESUS.\n\nBEM MAIS QUE AS FLORES ESPERAM PELO ORVALHO, BEM MAIS QUE O PERDIDO ESPERA SE ENCONTRAR, BEM MAIS QUE O FILHO ESPERA A MÃE QUERIDA, COM MAIS ANSIEDADE EU ESTOU A TE ESPERAR.\n\nCORO TE ESPERO, CHORANDO, TE ESPERO, MESTRE QUERIDO, ESTOU ME ENCURVANDO ANTE O PESO DA CRUZ; ME AJUDA A CHEGAR AO MEU LAR PROMETIDO, SEM TI NÃO CONSIGO VIVER, MEU JESUS. FINAL: Ó, MEU JESUS.', 'ASSIM COMO A NOITE ESPERA PELO DIA, ASSIM COMO A PRAIA ESPERA PELO MAR ASSIM COMO AS NUVENS ESPERAM PELO VENTO, COM MAIS ANSIEDADE EU ESTOU A TE ESPERAR. CORO TE ESPERO, CHORANDO, TE ESPERO, MESTRE QUERIDO, ESTOU ME ENCURVANDO ANTE O PESO DA CRUZ; ME AJUDA A CHEGAR AO MEU LAR PROMETIDO, SEM TI NÃO CONSIGO VIVER, MEU JESUS. BEM MAIS QUE AS FLORES ESPERAM PELO ORVALHO, BEM MAIS QUE O PERDIDO ESPERA SE ENCONTRAR, BEM MAIS QUE O FILHO ESPERA A MÃE QUERIDA, COM MAIS ANSIEDADE EU ESTOU A TE ESPERAR. CORO TE ESPERO, CHORANDO, TE ESPERO, MESTRE QUERIDO, ESTOU ME ENCURVANDO ANTE O PESO DA CRUZ; ME AJUDA A CHEGAR AO MEU LAR PROMETIDO, SEM TI NÃO CONSIGO VIVER, MEU JESUS.  Ó, MEU JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('482', '482 - OS SINAIS DO FIM SE CUMPREM', 'OS SINAIS DO FIM SE CUMPREM EM NOSSA GERAÇÃO, TODA A TERRA SE ANGUSTIA, GEME A CRIAÇÃO; TREVAS CAEM SOBRE O MUNDO, SÓ UM POVO TEM A LUZ, QUEM TEM SEDE VENHA E BEBA, ÁGUA VIVA É JESUS.\n\nCORO DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS? QUEM PODERÁ SEPARAR-NOS DO AMOR QUE HÁ EM JESUS?\n\nNÓS TAMBÉM JUNTOS GEMEMOS ANSIANDO A PARTIDA, NÃO GEMEMOS PARA A MORTE MAS PELA ETERNA VIDA, POIS JÁ A LUZ A VISTA ALCANÇA, NOVO DIA VAI RAIAR, CUMPRE-SE NOSSA ESPERANÇA, JESUS CRISTO VAI VOLTAR!\n\nCORO DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS? QUEM PODERÁ SEPARAR-NOS DO AMOR QUE HÁ EM JESUS?', 'OS SINAIS DO FIM SE CUMPREM EM NOSSA GERAÇÃO, TODA A TERRA SE ANGUSTIA, GEME A CRIAÇÃO; TREVAS CAEM SOBRE O MUNDO, SÓ UM POVO TEM A LUZ, QUEM TEM SEDE VENHA E BEBA, ÁGUA VIVA É JESUS. CORO DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS? QUEM PODERÁ SEPARAR-NOS DO AMOR QUE HÁ EM JESUS? NÓS TAMBÉM JUNTOS GEMEMOS ANSIANDO A PARTIDA, NÃO GEMEMOS PARA A MORTE MAS PELA ETERNA VIDA, POIS JÁ A LUZ A VISTA ALCANÇA, NOVO DIA VAI RAIAR, CUMPRE-SE NOSSA ESPERANÇA, JESUS CRISTO VAI VOLTAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('483', '483 - CHEGOU O TEMPO DE PENSAR', 'CHEGOU O TEMPO DE PENSAR NAS COISAS QUE SÃO LÁ DO CÉU, DEIXANDO ESTE MUNDO MAU, COM TODOS OS PECADOS SEUS; PENSAI NAS COISAS LÁ DE CIMA, QUE SE APROXIMA O REINO DE DEUS.\nBIS\n\nCORO Ó BUSCAI AS BÊNÇÃOS DO SENHOR, CONFIAI NO SEU IMENSO AMOR; OS SINAIS ESTÃO NOS DECLARANDO QUE VAI SE PASSANDO O QUE JESUS FALOU; VEM A CRISTO QUE ELE TE REVESTE COM O DOM CELESTE DO CONSOLADOR.\nBIS\n\nCOMO FOI NOS DIAS DE NOÉ, COM O DILÚVIO TUDO SE ACABOU, TAMBÉM NOS DIAS DE GOMORRA O FOGO TUDO DEVOROU; PORÉM NÃO HOUVE AGONIA COMO NESSE DIA, DIA DO SENHOR.\nBIS\n\nCORO Ó BUSCAI AS BÊNÇÃOS DO SENHOR, CONFIAI NO SEU IMENSO AMOR; OS SINAIS ESTÃO NOS DECLARANDO QUE VAI SE PASSANDO O QUE JESUS FALOU; VEM A CRISTO QUE ELE TE REVESTE COM O DOM CELESTE DO CONSOLADOR.\nBIS', 'CHEGOU O TEMPO DE PENSAR NAS COISAS QUE SÃO LÁ DO CÉU, DEIXANDO ESTE MUNDO MAU, COM TODOS OS PECADOS SEUS; PENSAI NAS COISAS LÁ DE CIMA, QUE SE APROXIMA O REINO DE DEUS. CORO Ó BUSCAI AS BÊNÇÃOS DO SENHOR, CONFIAI NO SEU IMENSO AMOR; OS SINAIS ESTÃO NOS DECLARANDO QUE VAI SE PASSANDO O QUE JESUS FALOU; VEM A CRISTO QUE ELE TE REVESTE COM O DOM CELESTE DO CONSOLADOR. COMO FOI NOS DIAS DE NOÉ, COM O DILÚVIO TUDO SE ACABOU, TAMBÉM NOS DIAS DE GOMORRA O FOGO TUDO DEVOROU; PORÉM NÃO HOUVE AGONIA COMO NESSE DIA, DIA DO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('484', '484 - TEMEI A DEUS', 'TEMEI A DEUS E DAI-LHE GLÓRIA POIS É CHEGADA A HORA DO SEU JUÍZO; E ADORAI AQUELE QUE FEZ O CÉU E A TERRA E O MAR E AS FONTES DAS ÁGUAS.\n\nTUA IGREJA TE ESPERA, TODOS OS SINAIS SÃO CUMPRIDOS, DO FIRMAMENTO TU NOS OUVES VEM JÁ, JESUS! FINAL: VEM JÁ, JESUS!\nBIS', 'TEMEI A DEUS E DAI-LHE GLÓRIA POIS É CHEGADA A HORA DO SEU JUÍZO; E ADORAI AQUELE QUE FEZ O CÉU E A TERRA E O MAR E AS FONTES DAS ÁGUAS. TUA IGREJA TE ESPERA, TODOS OS SINAIS SÃO CUMPRIDOS, DO FIRMAMENTO TU NOS OUVES VEM JÁ, JESUS!  VEM JÁ, JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('485', '485 - SETE CASTIÇAIS', 'SETE CASTIÇAIS O ESPÍRITO ACENDEU E DO MEIO DA IGREJA NOS FALOU; TODO O MUNDO OUVIU A VOZ DO CORDEIRO QUE VENCEU, VAI CUMPRIR-SE AGORA O QUE ELE PROMETEU: CRISTO VAI VOLTAR!\n\nO QUE É DIGNO JÁ OS SELOS DESATOU, A TROMBETA DA VITÓRIA VAI SOAR; A VIDEIRA FLORESCEU, A FIGUEIRA JÁ BROTOU, VAI CUMPRIR-SE O QUE ELE UM DIA NOS FALOU: CRISTO VAI VOLTAR!\n\nCORO MARANATA! CRISTO VAI VOLTAR! O ESPÍRITO E A ESPOSA DIZEM: VEM! ORA VEM, JESUS!\n\nHÁ SINAIS NO CÉU, NA TERRA E NO MAR, O JUÍZO DO SENHOR NÃO VAI TARDAR; ESTÁS PRONTO MEU IRMÃO, SE DEUS HOJE TE CHAMAR? SÓ NO SANGUE DO CORDEIRO HÁ SALVAÇÃO, CRISTO VAI VOLTAR!\n\nCORO (2X) MARANATA! CRISTO VAI VOLTAR! O ESPÍRITO E A ESPOSA DIZEM: VEM! ORA VEM, JESUS!', 'SETE CASTIÇAIS O ESPÍRITO ACENDEU E DO MEIO DA IGREJA NOS FALOU; TODO O MUNDO OUVIU A VOZ DO CORDEIRO QUE VENCEU, VAI CUMPRIR-SE AGORA O QUE ELE PROMETEU: CRISTO VAI VOLTAR! O QUE É DIGNO JÁ OS SELOS DESATOU, A TROMBETA DA VITÓRIA VAI SOAR; A VIDEIRA FLORESCEU, A FIGUEIRA JÁ BROTOU, VAI CUMPRIR-SE O QUE ELE UM DIA NOS FALOU: CRISTO VAI VOLTAR! CORO MARANATA! CRISTO VAI VOLTAR! O ESPÍRITO E A ESPOSA DIZEM: VEM! ORA VEM, JESUS! HÁ SINAIS NO CÉU, NA TERRA E NO MAR, O JUÍZO DO SENHOR NÃO VAI TARDAR; ESTÁS PRONTO MEU IRMÃO, SE DEUS HOJE TE CHAMAR? SÓ NO SANGUE DO CORDEIRO HÁ SALVAÇÃO, CRISTO VAI VOLTAR! CORO  MARANATA! CRISTO VAI VOLTAR! O ESPÍRITO E A ESPOSA DIZEM: VEM! ORA VEM, JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('486', '486 - DO NORTE VEM O ÁUREO ESPLENDOR', 'DO NORTE VEM O ÁUREO ESPLENDOR, O AZUL DO CÉU TAMBÉM ME FAZ SONHAR COM UMA NOVA TERRA, UM NOVO CÉU, ONDE OS JUSTOS VIVERÃO EM PLENA PAZ E PARA SEMPRE, ONDE UNIDOS VIVERÃO E PARA SEMPRE ESTARÃO NA PRESENÇA DE JESUS. INSTRUMENTOS\n\nSEI QUE UM DIA OS PRÉDIOS CAIRÃO E OS MOINHOS NÃO MAIS ROLARÃO; APENAS FICARÁ DE PÉ O QUE NA ROCHA SE FIRMOU, O QUE ANDOU NA CLARA LUZ, O QUE OUVINDO UM DIA CREU E QUE OUVINDO OBEDECEU A PALAVRA DE JESUS. INSTRUMENTOS\n\nBREVE A VIDA FINDA O SEU LABOR E SABEREI QUE NADA FOI EM VÃO, SE EU SORRI, LUTEI, CHOREI, SE CONSEGUI, PERDI, ACHEI, O QUE SENTI, TUDO PASSOU; IMPORTA AGORA, SIM, ESTAR, JUNTO A QUEM POR QUEM VIVI JUNTO A TI, Ó MEU SENHOR.\nBIS', 'DO NORTE VEM O ÁUREO ESPLENDOR, O AZUL DO CÉU TAMBÉM ME FAZ SONHAR COM UMA NOVA TERRA, UM NOVO CÉU, ONDE OS JUSTOS VIVERÃO EM PLENA PAZ E PARA SEMPRE, ONDE UNIDOS VIVERÃO E PARA SEMPRE ESTARÃO NA PRESENÇA DE JESUS. INSTRUMENTOS SEI QUE UM DIA OS PRÉDIOS CAIRÃO E OS MOINHOS NÃO MAIS ROLARÃO; APENAS FICARÁ DE PÉ O QUE NA ROCHA SE FIRMOU, O QUE ANDOU NA CLARA LUZ, O QUE OUVINDO UM DIA CREU E QUE OUVINDO OBEDECEU A PALAVRA DE JESUS. INSTRUMENTOS BREVE A VIDA FINDA O SEU LABOR E SABEREI QUE NADA FOI EM VÃO, SE EU SORRI, LUTEI, CHOREI, SE CONSEGUI, PERDI, ACHEI, O QUE SENTI, TUDO PASSOU; IMPORTA AGORA, SIM, ESTAR, JUNTO A QUEM POR QUEM VIVI JUNTO A TI, Ó MEU SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('487', '487 - VIGIAI E SEMPRE ORAI, Ó CRENTES', 'VIGIAI E SEMPRE ORAI, Ó CRENTES, BREVE CRISTO VOLTARÁ; PARA O CÉU, O SEU DESCANSO ETERNO, OS REMIDOS LEVARÁ.\n\nCORO VIGIAI! É ORDEM SANTA, VIGIAI EM ORAÇÃO! BREVE CRISTO VOLTARÁ, PARA O CÉU NOS LEVARÁ! VIGIAI, GUARDANDO SEMPRE LIMPO, DO PECADO, O CORAÇÃO; CONFIANDO NO DIVINO MESTRE, VENCEREIS A TENTAÇÃO.\n\nCORO VIGIAI! É ORDEM SANTA, VIGIAI EM ORAÇÃO! BREVE CRISTO VOLTARÁ, PARA O CÉU NOS LEVARÁ! VIGIANDO E SEMPRE TRABALHANDO, NESTE MUNDO VIL LUTAI, AGUARDANDO A VOZ DE JESUS CRISTO: “NO MEU DOCE LAR ENTRAI!”', 'VIGIAI E SEMPRE ORAI, Ó CRENTES, BREVE CRISTO VOLTARÁ; PARA O CÉU, O SEU DESCANSO ETERNO, OS REMIDOS LEVARÁ. CORO VIGIAI! É ORDEM SANTA, VIGIAI EM ORAÇÃO! BREVE CRISTO VOLTARÁ, PARA O CÉU NOS LEVARÁ! VIGIAI, GUARDANDO SEMPRE LIMPO, DO PECADO, O CORAÇÃO; CONFIANDO NO DIVINO MESTRE, VENCEREIS A TENTAÇÃO. CORO VIGIAI! É ORDEM SANTA, VIGIAI EM ORAÇÃO! BREVE CRISTO VOLTARÁ, PARA O CÉU NOS LEVARÁ! VIGIANDO E SEMPRE TRABALHANDO, NESTE MUNDO VIL LUTAI, AGUARDANDO A VOZ DE JESUS CRISTO: NO MEU DOCE LAR ENTRAI!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('488', '488 - CERTA VEZ, AO ORAR', 'CERTA VEZ, AO ORAR, PENSEI QUANTO FALTARÁ PARA REGRESSAR O NOSSO REI? SUA VOZ OUÇO, ENTÃO, REVELANDO AO CORAÇÃO: “MINHA IGREJA, BREVE VOLTAREI!”\n\nCORO “BREVE VENHO VOS BUSCAR PARA TER-VOS JUNTO A MIM SEDE FORTES E FIÉIS ATÉ O FIM! QUE ALEGRIA SINTO, ENTÃO, EM MEU CORAÇÃO EU SEI, BREVE VIRÁ JESUS, MEU REI!\n\nQUE RIQUEZAS SEM PAR ELE FOI NOS PREPARAR, LÁ NO CÉU ONDE NÃO HÁ MAIS DOR; MEU DESEJO É PARTIR, PARA SUA VOZ OUVIR: “ENTRA NO GOZO DO TEU SENHOR”\n\nCORO “BREVE VENHO VOS BUSCAR PARA TER-VOS JUNTO A MIM SEDE FORTES E FIÉIS ATÉ O FIM! QUE ALEGRIA SINTO, ENTÃO, EM MEU CORAÇÃO EU SEI, BREVE VIRÁ JESUS, MEU REI!', 'CERTA VEZ, AO ORAR, PENSEI QUANTO FALTARÁ PARA REGRESSAR O NOSSO REI? SUA VOZ OUÇO, ENTÃO, REVELANDO AO CORAÇÃO: MINHA IGREJA, BREVE VOLTAREI! CORO BREVE VENHO VOS BUSCAR PARA TER-VOS JUNTO A MIM SEDE FORTES E FIÉIS ATÉ O FIM! QUE ALEGRIA SINTO, ENTÃO, EM MEU CORAÇÃO EU SEI, BREVE VIRÁ JESUS, MEU REI! QUE RIQUEZAS SEM PAR ELE FOI NOS PREPARAR, LÁ NO CÉU ONDE NÃO HÁ MAIS DOR; MEU DESEJO É PARTIR, PARA SUA VOZ OUVIR: ENTRA NO GOZO DO TEU SENHOR',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('489', '489 - EM BREVE ESCUTAREI', 'EM BREVE ESCUTAREI O TOQUE TRIUNFAL DA ÚLTIMA TROMBETA CELESTIAL, E TODOS OS FIÉIS DO SONO ACORDARÃO E AO CÉU CONOSCO JUNTOS SUBIRÃO.\n\nCORO ALI JESUS VEREI, DE JOELHOS CAIREI AOS PÉS DO AMADO SALVADOR! E ENTRE A MULTIDÃO CANTAREI A CANÇÃO DO MAIS SUBLIME E BELO AMOR!\n\nQUE IMPORTA A DOR AQUI, A BREVE PROVAÇÃO, SE VÊ-LO ALI SERÁ MEU GALARDÃO? SUA VOZ EU OUVIREI: “BENDITOS DE MEU PAI, NO LAR QUE PREPAREI AGORA ENTRAI!”\n\nCORO ALI JESUS VEREI, DE JOELHOS CAIREI AOS PÉS DO AMADO SALVADOR! E ENTRE A MULTIDÃO CANTAREI A CANÇÃO DO MAIS SUBLIME E BELO AMOR!', 'EM BREVE ESCUTAREI O TOQUE TRIUNFAL DA ÚLTIMA TROMBETA CELESTIAL, E TODOS OS FIÉIS DO SONO ACORDARÃO E AO CÉU CONOSCO JUNTOS SUBIRÃO. CORO ALI JESUS VEREI, DE JOELHOS CAIREI AOS PÉS DO AMADO SALVADOR! E ENTRE A MULTIDÃO CANTAREI A CANÇÃO DO MAIS SUBLIME E BELO AMOR! QUE IMPORTA A DOR AQUI, A BREVE PROVAÇÃO, SE VÊ-LO ALI SERÁ MEU GALARDÃO? SUA VOZ EU OUVIREI: BENDITOS DE MEU PAI, NO LAR QUE PREPAREI AGORA ENTRAI!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('490', '490 - ELE VOLTARÁ PRA BUSCAR A SUA IGREJA', 'ELE VOLTARÁ PRA BUSCAR A SUA IGREJA, QUE AQUI NO MUNDO FOI FIEL; ELE VOLTARÁ E MUITOS NÃO ACREDITAM, ESSES TAIS FICARÃO, SOFRENDO, SEM TER SALVAÇÃO.\n\nCORO ELE PODE VOLTAR AGORA, NINGUÉM SABE O DIA NEM A HORA QUE ELE VOLTARÁ; ELE PODE VOLTAR HOJE MESMO, PREPARA-TE, IRMÃO, PRA NÃO FICAR.\n\nELE VOLTARÁ, POIS ASSIM NOS AFIRMA A BÍBLIA, MAIS PERTO DE DEUS DEVEMOS FICAR; ELE VOLTARÁ, ACERTA A TUA VIDA, DIANTE DE DEUS, DE SEU SANTO ALTAR.\n\nCORO ELE PODE VOLTAR AGORA, NINGUÉM SABE O DIA NEM A HORA QUE ELE VOLTARÁ; ELE PODE VOLTAR HOJE MESMO, PREPARA-TE, IRMÃO, PRA NÃO FICAR. FINAL: PREPARA-TE IRMÃO PRA NÃO FICAR.', 'ELE VOLTARÁ PRA BUSCAR A SUA IGREJA, QUE AQUI NO MUNDO FOI FIEL; ELE VOLTARÁ E MUITOS NÃO ACREDITAM, ESSES TAIS FICARÃO, SOFRENDO, SEM TER SALVAÇÃO. CORO ELE PODE VOLTAR AGORA, NINGUÉM SABE O DIA NEM A HORA QUE ELE VOLTARÁ; ELE PODE VOLTAR HOJE MESMO, PREPARA-TE, IRMÃO, PRA NÃO FICAR. ELE VOLTARÁ, POIS ASSIM NOS AFIRMA A BÍBLIA, MAIS PERTO DE DEUS DEVEMOS FICAR; ELE VOLTARÁ, ACERTA A TUA VIDA, DIANTE DE DEUS, DE SEU SANTO ALTAR. CORO ELE PODE VOLTAR AGORA, NINGUÉM SABE O DIA NEM A HORA QUE ELE VOLTARÁ; ELE PODE VOLTAR HOJE MESMO, PREPARA-TE, IRMÃO, PRA NÃO FICAR.  PREPARA-TE IRMÃO PRA NÃO FICAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('491', '491 - ELE UM DIA VOLTARÁ', 'ELE UM DIA VOLTARÁ, VEM BUSCAR A SUA IGREJA, MEU JESUS, MEU JESUS VOLTARÁ; TODA A TERRA ESTREMECEU NO DIA DA SUA PARTIDA, MEU JESUS, MEU JESUS, MINHA VIDA.\n\nNOSSO FARDO ELE CARREGOU, TODO O SEU AMOR NOS DEU, NOSSAS DORES CONSIGO LEVOU, VIDA NOVA NOS OFERECEU; COMO É BOM SERVIR AO MEU JESUS, SEU NOME GLORIFICAR; SUA GLÓRIA NOS CONDUZ A ANDAR NA SUA LUZ, A JESUS QUERO LOUVAR! FINAL: LOUVAR, LOUVAR.\nBIS', 'ELE UM DIA VOLTARÁ, VEM BUSCAR A SUA IGREJA, MEU JESUS, MEU JESUS VOLTARÁ; TODA A TERRA ESTREMECEU NO DIA DA SUA PARTIDA, MEU JESUS, MEU JESUS, MINHA VIDA. NOSSO FARDO ELE CARREGOU, TODO O SEU AMOR NOS DEU, NOSSAS DORES CONSIGO LEVOU, VIDA NOVA NOS OFERECEU; COMO É BOM SERVIR AO MEU JESUS, SEU NOME GLORIFICAR; SUA GLÓRIA NOS CONDUZ A ANDAR NA SUA LUZ, A JESUS QUERO LOUVAR!  LOUVAR, LOUVAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('492', '492 - ELE VOLTARÁ, SIM, ELE VOLTARÁ', 'ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS PARA BUSCAR UMA IGREJA A QUAL ESTÁ PREPARADA.\n\nELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS POVO ESCOLHIDO LUTANDO COM CRISTO NESTA BATALHA.\n\nMAS ESTA LUTA AGORA JÁ É O FIM, SOMOS FELIZES PORQUE NÃO ESTAREMOS AQUI; AS TROMBETAS IRÃO SOAR E CRISTO EM BREVE VEM NOS BUSCAR PARA A GLÓRIA ETERNA HERDAR. INSTRUMENTOS\n\nELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS PARA BUSCAR UMA IGREJA A QUAL ESTÁ PREPARADA.\n\nELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS POVO ESCOLHIDO LUTANDO COM CRISTO NESTA BATALHA.\n\nMAS ESTA LUTA AGORA JÁ É O FIM, SOMOS FELIZES PORQUE NÃO ESTAREMOS AQUI; AS TROMBETAS IRÃO SOAR E CRISTO EM BREVE VEM NOS BUSCAR PARA A GLÓRIA ETERNA HERDAR.\nBIS', 'ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS PARA BUSCAR UMA IGREJA A QUAL ESTÁ PREPARADA. ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS POVO ESCOLHIDO LUTANDO COM CRISTO NESTA BATALHA. MAS ESTA LUTA AGORA JÁ É O FIM, SOMOS FELIZES PORQUE NÃO ESTAREMOS AQUI; AS TROMBETAS IRÃO SOAR E CRISTO EM BREVE VEM NOS BUSCAR PARA A GLÓRIA ETERNA HERDAR. INSTRUMENTOS MAS ESTA LUTA AGORA JÁ É O FIM, SOMOS FELIZES PORQUE NÃO ESTAREMOS AQUI; AS TROMBETAS IRÃO SOAR E CRISTO EM BREVE VEM NOS BUSCAR PARA A GLÓRIA ETERNA HERDAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('493', '493 - COMO FOI PARA O CÉU, JESUS CRISTO HÁ DE VIR', 'COMO FOI PARA O CÉU, JESUS CRISTO HÁ DE VIR, QUANDO O SOM DA TROMBETA ECOAR; QUANDO A VOZ DE UM ARCANJO NO CÉU ESTRUGIR EU IREI COM JESUS ME ENCONTRAR.\n\nCORO OH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER! QUANDO O SOM DA TROMBETA ECOAR; QUANDO CRISTO, NAS NUVENS, TIVER DE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nNESSE DIA DE GLÓRIA O MEU CORPO MORTAL, SEMELHANTE AO DE CRISTO HÁ DE SER; E JÁ LIVRE DA MORTE E JÁ LIVRE DO MAL, O MILÊNIO DE CRISTO HEI DE VER.\n\nCORO OH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER! QUANDO O SOM DA TROMBETA ECOAR; QUANDO CRISTO, NAS NUVENS, TIVER DE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nEU AQUI, PELA CRUZ, PARA O MUNDO MORRI, MUITA DOR INDA AQUI SOFREREI; MINHA VIDA COM CRISTO, EM MEU DEUS ESCONDI E COM CRISTO EU AQUI REINAREI.\n\nCORO OH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER! QUANDO O SOM DA TROMBETA ECOAR; QUANDO CRISTO, NAS NUVENS, TIVER DE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nVEM, JESUS, Ó SENHOR, VEM DEPRESSA REINAR, VEM A PAZ E A JUSTIÇA TRAZER; CRIAÇÃO, POVO TEU, TUDO ALMEJA O RAIAR DESSE DIA DE GLÓRIA E PODER.\n\nCORO OH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER! QUANDO O SOM DA TROMBETA ECOAR; QUANDO CRISTO, NAS NUVENS, TIVER DE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nESTE IMPÉRIO DO MAL, VEM, SENHOR, DESTRUIR, VEM ESPOSO CELESTE, REINAR! VEM Ó SOL DA JUSTIÇA NO MUNDO LUZIR Ó MEU REI, VEM MEU PRANTO ESTANCAR!\n\nCORO OH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER! QUANDO O SOM DA TROMBETA ECOAR; QUANDO CRISTO, NAS NUVENS, TIVER DE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!', 'COMO FOI PARA O CÉU, JESUS CRISTO HÁ DE VIR, QUANDO O SOM DA TROMBETA ECOAR; QUANDO A VOZ DE UM ARCANJO NO CÉU ESTRUGIR EU IREI COM JESUS ME ENCONTRAR. CORO OH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER! QUANDO O SOM DA TROMBETA ECOAR; QUANDO CRISTO, NAS NUVENS, TIVER DE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR! NESSE DIA DE GLÓRIA O MEU CORPO MORTAL, SEMELHANTE AO DE CRISTO HÁ DE SER; E JÁ LIVRE DA MORTE E JÁ LIVRE DO MAL, O MILÊNIO DE CRISTO HEI DE VER. EU AQUI, PELA CRUZ, PARA O MUNDO MORRI, MUITA DOR INDA AQUI SOFREREI; MINHA VIDA COM CRISTO, EM MEU DEUS ESCONDI E COM CRISTO EU AQUI REINAREI. VEM, JESUS, Ó SENHOR, VEM DEPRESSA REINAR, VEM A PAZ E A JUSTIÇA TRAZER; CRIAÇÃO, POVO TEU, TUDO ALMEJA O RAIAR DESSE DIA DE GLÓRIA E PODER. ESTE IMPÉRIO DO MAL, VEM, SENHOR, DESTRUIR, VEM ESPOSO CELESTE, REINAR! VEM Ó SOL DA JUSTIÇA NO MUNDO LUZIR Ó MEU REI, VEM MEU PRANTO ESTANCAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('494', '494 - NÃO SEI QUANDO CRISTO JESUS HÁ DE VIR', 'NÃO SEI QUANDO CRISTO JESUS HÁ DE VIR E NEM QUAL O DIA EM QUE EU HEI DE PARTIR, MAS EU SEI QUANDO A SUA PRESENÇA LUZIR SERÁ GRANDE GLÓRIA PRA MIM (3X) MAS EU SEI QUANDO A SUA PRESENÇA LUZIR, SERÁ GRANDE GLÓRIA PRA MIM.\n\nNÃO SEI QUE MORADA JESUS VAI ME DAR, E NEM QUAL O NOME QUE EU HEI DE GANHAR, MAS EU SEI QUE O BEM VINDO DELE HEI DE ESCUTAR, SÓ ISSO SERÁ CÉU PRA MIM (3X) MAS EU SEI QUE O BEM-VINDO DELE HEI DE ESCUTAR, SÓ ISSO SERÁ CÉU PRA MIM.\n\nO CANTO DOS ANJOS AINDA NÃO SEI NEM QUE SINFONIAS EU LÁ OUVIREI, MAS EU SEI QUE O FALAR DE JESUS, O MEU REI SERÁ SINFONIA PRA MIM (3X) MAS EU SEI QUE O FALAR DE JESUS, O MEU REI, SERÁ SINFONIA PRA MIM.', 'NÃO SEI QUANDO CRISTO JESUS HÁ DE VIR E NEM QUAL O DIA EM QUE EU HEI DE PARTIR, MAS EU SEI QUANDO A SUA PRESENÇA LUZIR SERÁ GRANDE GLÓRIA PRA MIM  MAS EU SEI QUANDO A SUA PRESENÇA LUZIR, SERÁ GRANDE GLÓRIA PRA MIM. NÃO SEI QUE MORADA JESUS VAI ME DAR, E NEM QUAL O NOME QUE EU HEI DE GANHAR, MAS EU SEI QUE O BEM VINDO DELE HEI DE ESCUTAR, SÓ ISSO SERÁ CÉU PRA MIM  MAS EU SEI QUE O BEM-VINDO DELE HEI DE ESCUTAR, SÓ ISSO SERÁ CÉU PRA MIM. O CANTO DOS ANJOS AINDA NÃO SEI NEM QUE SINFONIAS EU LÁ OUVIREI, MAS EU SEI QUE O FALAR DE JESUS, O MEU REI SERÁ SINFONIA PRA MIM  MAS EU SEI QUE O FALAR DE JESUS, O MEU REI, SERÁ SINFONIA PRA MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('495', '495 - O QUE HÁ DE VIR, VIRÁ', 'O QUE HÁ DE VIR, VIRÁ, (BIS) NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS SÓ OS FIÉIS SUBIRÃO (BIS) NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS\n\nO QUE HÁ DE VIR, VIRÁ, (2X) NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS FINAL: O QUE HÁ DE VIR, VIRÁ (3X)', 'O QUE HÁ DE VIR, VIRÁ,  NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS SÓ OS FIÉIS SUBIRÃO  NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS O QUE HÁ DE VIR, VIRÁ,  NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS  O QUE HÁ DE VIR, VIRÁ',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('496', '496 - NÃO VAI TARDAR', 'NÃO VAI TARDAR E CRISTO HÁ DE VOLTAR; NÃO VAI TARDAR E VEREMOS JESUS. EU QUERO ME PREPARAR, POIS QUERO LOGO SUBIR; E QUERO JUNTO A VOCÊ, LÁ NO CÉU, CONVERSAR COM JESUS.\nBIS', 'NÃO VAI TARDAR E CRISTO HÁ DE VOLTAR; NÃO VAI TARDAR E VEREMOS JESUS. EU QUERO ME PREPARAR, POIS QUERO LOGO SUBIR; E QUERO JUNTO A VOCÊ, LÁ NO CÉU, CONVERSAR COM JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('497', '497 - JESUS, SIM, VEM, DO CÉU EM GLÓRIA ELE VEM', 'JESUS, SIM, VEM, DO CÉU EM GLÓRIA ELE VEM, ECOA A NOVA PELO MUNDO ALÉM! OH! ESPERANÇA QUE A SUA IGREJA TEM! DAI GLÓRIA A DEUS JESUS EM BREVE VEM\n\nCORO (2X) NOSSA ESPERANÇA É SUA VINDA, O REI DOS REIS VEM NOS BUSCAR; NÓS AGUARDAMOS JESUS AINDA, TÉ A LUZ DA MANHÃ RAIAR.\n\nJESUS SIM, VEM, OS MORTOS ESPERANDO ESTÃO O GRAN MOMENTO DA RESSURREIÇÃO E DO SEPULCRO EM BREVE SE LEVANTARÃO DAI GLÓRIA A DEUS, JESUS EM BREVE VEM!\n\nCORO (2X) NOSSA ESPERANÇA É SUA VINDA, O REI DOS REIS VEM NOS BUSCAR; NÓS AGUARDAMOS JESUS AINDA, TÉ A LUZ DA MANHÃ RAIAR.\n\nJESUS, SIM, VEM, DO CÉU CERCADO DE ESPLENDOR, ANIQUILANDO A CORRUPÇÃO E A DOR, QUEBRANDO OS LAÇOS DO ASTUTO USURPADOR! DAI GLÓRIA A DEUS, JESUS EM BREVE VEM!\n\nCORO (2X) NOSSA ESPERANÇA É SUA VINDA, O REI DOS REIS VEM NOS BUSCAR; NÓS AGUARDAMOS JESUS AINDA, TÉ A LUZ DA MANHÃ RAIAR.\n\nJESUS, SIM, VEM, COMPLETAMENTE RESTAURAR O MUNDO QUE SE ARRUÍNA SEM PARAR SIM, TODAS AS COISAS VEM DEPRESSA TRANSFORMAR! DAI GLÓRIA A DEUS JESUS EM BREVE VEM!\n\nCORO (2X) NOSSA ESPERANÇA É SUA VINDA, O REI DOS REIS VEM NOS BUSCAR; NÓS AGUARDAMOS JESUS AINDA, TÉ A LUZ DA MANHÃ RAIAR.', 'JESUS, SIM, VEM, DO CÉU EM GLÓRIA ELE VEM, ECOA A NOVA PELO MUNDO ALÉM! OH! ESPERANÇA QUE A SUA IGREJA TEM! DAI GLÓRIA A DEUS JESUS EM BREVE VEM CORO  NOSSA ESPERANÇA É SUA VINDA, O REI DOS REIS VEM NOS BUSCAR; NÓS AGUARDAMOS JESUS AINDA, TÉ A LUZ DA MANHÃ RAIAR. JESUS SIM, VEM, OS MORTOS ESPERANDO ESTÃO O GRAN MOMENTO DA RESSURREIÇÃO E DO SEPULCRO EM BREVE SE LEVANTARÃO DAI GLÓRIA A DEUS, JESUS EM BREVE VEM! JESUS, SIM, VEM, DO CÉU CERCADO DE ESPLENDOR, ANIQUILANDO A CORRUPÇÃO E A DOR, QUEBRANDO OS LAÇOS DO ASTUTO USURPADOR! DAI GLÓRIA A DEUS, JESUS EM BREVE VEM! JESUS, SIM, VEM, COMPLETAMENTE RESTAURAR O MUNDO QUE SE ARRUÍNA SEM PARAR SIM, TODAS AS COISAS VEM DEPRESSA TRANSFORMAR! DAI GLÓRIA A DEUS JESUS EM BREVE VEM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('498', '498 - CRISTO VEM ME BUSCAR', 'CRISTO VEM ME BUSCAR, PARA O CÉU ME LEVARÁ, O CORDEIRO PROMETIDO VOLTARÁ.\nBIS\n\nCORO OH! GLÓRIA, ALELUIA! MARANATA, VEM JESUS! ESTOU COBERTO PELO SANGUE DESSA CRUZ TENHO O CONSOLADOR SUA GLÓRIA E ESPLENDOR SOU LIBERTO PELO PODER DO SENHOR.\n\nELE MANDA ATENTO ESTAR, VIGIAR E SEMPRE ORAR PARA O TOQUE DA TROMBETA ESCUTAR.\nBIS\n\nCORO OH! GLÓRIA, ALELUIA! MARANATA, VEM JESUS! ESTOU COBERTO PELO SANGUE DESSA CRUZ TENHO O CONSOLADOR SUA GLÓRIA E ESPLENDOR SOU LIBERTO PELO PODER DO SENHOR.\n\nOH! QUE GOZO VOU SENTIR COM OS ANJOS A CANTAR, POIS COM CRISTO PARA SEMPRE VOU MORAR.\nBIS\n\nCORO OH! GLÓRIA, ALELUIA! MARANATA, VEM JESUS! ESTOU COBERTO PELO SANGUE DESSA CRUZ TENHO O CONSOLADOR SUA GLÓRIA E ESPLENDOR SOU LIBERTO PELO PODER DO SENHOR.', 'CRISTO VEM ME BUSCAR, PARA O CÉU ME LEVARÁ, O CORDEIRO PROMETIDO VOLTARÁ. CORO OH! GLÓRIA, ALELUIA! MARANATA, VEM JESUS! ESTOU COBERTO PELO SANGUE DESSA CRUZ TENHO O CONSOLADOR SUA GLÓRIA E ESPLENDOR SOU LIBERTO PELO PODER DO SENHOR. ELE MANDA ATENTO ESTAR, VIGIAR E SEMPRE ORAR PARA O TOQUE DA TROMBETA ESCUTAR. OH! QUE GOZO VOU SENTIR COM OS ANJOS A CANTAR, POIS COM CRISTO PARA SEMPRE VOU MORAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('499', '499 - OUÇO UMA VOZ TÃO SUAVE', 'OUÇO UMA VOZ TÃO SUAVE, UMA VOZ COMO DAS ÁGUAS QUE SAI DO TRONO DO SENHOR DIZENDO ASSIM COM AMOR:\n\nCORO “IGREJA, BREVEMENTE VOLTAREI, TUAS LÁGRIMAS EU ENXUGAREI, TENHO OUVIDO O TEU CLAMOR, TENHO ACEITO O TEU LOUVOR”; COMO EU TE AMO, TU ÉS O MEU AMOR. INSTRUMENTOS\n\n“ESTOU À DESTRA DE MEU PAI INTERCEDENDO POR TODOS VÓS”; E QUERO OUVI-LO DIZER: “EIS O MOMENTO DA IGREJA SUBIR.”\n\nCORO “IGREJA, BREVEMENTE VOLTAREI, TUAS LÁGRIMAS EU ENXUGAREI, TENHO OUVIDO O TEU CLAMOR, TENHO ACEITO O TEU LOUVOR”; COMO EU TE AMO, TU ÉS O MEU AMOR. FINAL: COMO EU TE AMO, TU ÉS O MEU AMOR.', 'OUÇO UMA VOZ TÃO SUAVE, UMA VOZ COMO DAS ÁGUAS QUE SAI DO TRONO DO SENHOR DIZENDO ASSIM COM AMOR: CORO IGREJA, BREVEMENTE VOLTAREI, TUAS LÁGRIMAS EU ENXUGAREI, TENHO OUVIDO O TEU CLAMOR, TENHO ACEITO O TEU LOUVOR; COMO EU TE AMO, TU ÉS O MEU AMOR. INSTRUMENTOS ESTOU À DESTRA DE MEU PAI INTERCEDENDO POR TODOS VÓS; E QUERO OUVI-LO DIZER: EIS O MOMENTO DA IGREJA SUBIR. CORO IGREJA, BREVEMENTE VOLTAREI, TUAS LÁGRIMAS EU ENXUGAREI, TENHO OUVIDO O TEU CLAMOR, TENHO ACEITO O TEU LOUVOR; COMO EU TE AMO, TU ÉS O MEU AMOR.  COMO EU TE AMO, TU ÉS O MEU AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('500', '500 - SEI QUE O SENHOR', 'SEI QUE O SENHOR AO MEU LADO ESTÁ, SEI QUE UM DIA ENCONTRÁ-LO-EI. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR.\n\nSEI QUE ESTA VIDA LOGO PASSARÁ, SEI QUE O SENHOR TAMBÉM ME LEVARÁ, SEI QUE AMANHÃ HÁ UM NOVO ALVORECER QUE APAGARÁ AS DORES NO AMANHECER. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR.\n\nSEI QUE A ALEGRIA LOGO VOLTARÁ, O AMOR DO SENHOR EM MIM FLORESCERÁ. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR. INSTRUMENTOS\n\nALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR.', 'SEI QUE O SENHOR AO MEU LADO ESTÁ, SEI QUE UM DIA ENCONTRÁ-LO-EI. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR. SEI QUE ESTA VIDA LOGO PASSARÁ, SEI QUE O SENHOR TAMBÉM ME LEVARÁ, SEI QUE AMANHÃ HÁ UM NOVO ALVORECER QUE APAGARÁ AS DORES NO AMANHECER. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR. SEI QUE A ALEGRIA LOGO VOLTARÁ, O AMOR DO SENHOR EM MIM FLORESCERÁ. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR. INSTRUMENTOS ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('501', '501 - O ESPÍRITO DE DEUS FALA À SUA IGREJA', 'O ESPÍRITO DE DEUS FALA À SUA IGREJA: “VIREI BUSCAR OS REMIDOS QUE FORAM LAVADOS NO SANGUE DO CORDEIRO, NO SANGUE DO FILHO JESUS; INSTRUMENTOS\n\nCEDO VENHO MUITO EM BREVE, EIS QUE ESTOU À PORTA E BATO, SE ABRIRES ENTRAREI, CUMPRIREI MINHA PROMESSA E UM REMIDO TU SERÁS”.\n\nCORO EIS O CANTO DE VITÓRIA (DE VITÓRIA) VEREMOS O REI DA GLÓRIA (O REI DA GLÓRIA) VEREMOS O REI JESUS (O REI JESUS) QUE FOI MORTO ALI NA CRUZ; RESSUSCITOU DANDO VIDA AO PECADOR.\n\n“BREVE CRISTO VOLTARÁ, QUEM VENCER TUDO HERDARÁ E EU SEREI SEU DEUS E ELE SERÁ MEU FILHO, ESSAS PALAVRAS SÃO FIÉIS”.\n\nCORO EIS O CANTO DE VITÓRIA (DE VITÓRIA) VEREMOS O REI DA GLÓRIA (O REI DA GLÓRIA) VEREMOS O REI JESUS (O REI JESUS) QUE FOI MORTO ALI NA CRUZ; RESSUSCITOU DANDO VIDA AO PECADOR.\n\nFINAL: O ESPÍRITO DE DEUS FALA À SUA IGREJA: “JESUS VIRÁ, O CORDEIRO VIVO, JESUS EM BREVE VIRÁ”.', 'O ESPÍRITO DE DEUS FALA À SUA IGREJA: VIREI BUSCAR OS REMIDOS QUE FORAM LAVADOS NO SANGUE DO CORDEIRO, NO SANGUE DO FILHO JESUS; INSTRUMENTOS CEDO VENHO MUITO EM BREVE, EIS QUE ESTOU À PORTA E BATO, SE ABRIRES ENTRAREI, CUMPRIREI MINHA PROMESSA E UM REMIDO TU SERÁS. CORO EIS O CANTO DE VITÓRIA (DE VITÓRIA) VEREMOS O REI DA GLÓRIA (O REI DA GLÓRIA) VEREMOS O REI JESUS (O REI JESUS) QUE FOI MORTO ALI NA CRUZ; RESSUSCITOU DANDO VIDA AO PECADOR. BREVE CRISTO VOLTARÁ, QUEM VENCER TUDO HERDARÁ E EU SEREI SEU DEUS E ELE SERÁ MEU FILHO, ESSAS PALAVRAS SÃO FIÉIS. O ESPÍRITO DE DEUS FALA À SUA IGREJA: JESUS VIRÁ, O CORDEIRO VIVO, JESUS EM BREVE VIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('502', '502 - QUANDO LÁ DO CÉU DESCENDO', 'QUANDO LÁ DO CÉU DESCENDO, PARA OS SEUS, JESUS VOLTAR, E O CLARIM DE DEUS A TODOS PROCLAMAR QUE CHEGOU O GRANDE DIA DA VITÓRIA DO MEU REI, EU, POR SUA IMENSA GRAÇA, LÁ ESTAREI.\n\nCORO QUANDO, ENFIM, CHEGAR O DIA DA VITÓRIA DO MEU REI, QUANDO, ENFIM, CHEGAR O DIA, PELA GRAÇA DE JESUS, EU LÁ ESTAREI.\n\nNESSE DIA, QUANDO OS MORTOS HÃO DE A VOZ DE CRISTO OUVIR, E DE SEUS SEPULCROS HÃO DE RESSURGIR, OS REMIDOS, TRANSFORMADOS, LOGO ACLAMARÃO SEU REI E, POR SUA IMENSA GRAÇA, LÁ ESTAREI.\n\nCORO QUANDO, ENFIM, CHEGAR O DIA DA VITÓRIA DO MEU REI, QUANDO, ENFIM, CHEGAR O DIA, PELA GRAÇA DE JESUS, EU LÁ ESTAREI.\n\nPELO MUNDO, REJEITADO, FOI JESUS, MEU SALVADOR; DESPREZARAM, INSULTARAM MEU SENHOR, MAS, FAUSTOSO VEM O DIA DA VITÓRIA DO MEU REI E, POR SUA IMENSA GRAÇA, LÁ ESTAREI.\n\nCORO QUANDO, ENFIM, CHEGAR O DIA DA VITÓRIA DO MEU REI, QUANDO, ENFIM, CHEGAR O DIA, PELA GRAÇA DE JESUS, EU LÁ ESTAREI.\n\nEM MIM MESMO, NADA TENHO EM QUE POSSA CONFIAR, MAS JESUS MORREU NA CRUZ PRA ME SALVAR; TÃO-SOMENTE NELE ESPERO, SIM, E SEMPRE ESPERAREI POIS, POR SUA IMENSA GRAÇA, LÁ ESTAREI.\n\nCORO QUANDO, ENFIM, CHEGAR O DIA DA VITÓRIA DO MEU REI, QUANDO, ENFIM, CHEGAR O DIA, PELA GRAÇA DE JESUS, EU LÁ ESTAREI.', 'QUANDO LÁ DO CÉU DESCENDO, PARA OS SEUS, JESUS VOLTAR, E O CLARIM DE DEUS A TODOS PROCLAMAR QUE CHEGOU O GRANDE DIA DA VITÓRIA DO MEU REI, EU, POR SUA IMENSA GRAÇA, LÁ ESTAREI. CORO QUANDO, ENFIM, CHEGAR O DIA DA VITÓRIA DO MEU REI, QUANDO, ENFIM, CHEGAR O DIA, PELA GRAÇA DE JESUS, EU LÁ ESTAREI. NESSE DIA, QUANDO OS MORTOS HÃO DE A VOZ DE CRISTO OUVIR, E DE SEUS SEPULCROS HÃO DE RESSURGIR, OS REMIDOS, TRANSFORMADOS, LOGO ACLAMARÃO SEU REI E, POR SUA IMENSA GRAÇA, LÁ ESTAREI. PELO MUNDO, REJEITADO, FOI JESUS, MEU SALVADOR; DESPREZARAM, INSULTARAM MEU SENHOR, MAS, FAUSTOSO VEM O DIA DA VITÓRIA DO MEU REI E, POR SUA IMENSA GRAÇA, LÁ ESTAREI. EM MIM MESMO, NADA TENHO EM QUE POSSA CONFIAR, MAS JESUS MORREU NA CRUZ PRA ME SALVAR; TÃO-SOMENTE NELE ESPERO, SIM, E SEMPRE ESPERAREI POIS, POR SUA IMENSA GRAÇA, LÁ ESTAREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('503', '503 - TENHO SAUDADES DE JESUS, MEU MESTRE', 'TENHO SAUDADES DE JESUS, MEU MESTRE, TENHO SAUDADES DE JESUS, MEU REI, MAS AO FINDAR O MEU LABOR TERRESTRE ME ENCONTRAREI COM ELE, SIM, EU SEI.\n\nCORO SAUDADES, TENHO SAUDADES DE TI, JESUS, MEU SALVADOR E REI; QUERO SENTIR A TUA PRESENÇA, POIS SEI QUE SÓ ASSIM FELIZ SEREI.\n\nTENHO VONTADE DE VIVER PRA CRISTO, DE NOS SEUS BRAÇOS SEMPRE ME ABRIGAR; SIM, QUERO A ELE SEMPRE SER SUBMISSO E CONFIAR NO SEU AMOR SEM PAR.\n\nCORO SAUDADES, TENHO SAUDADES DE TI, JESUS, MEU SALVADOR E REI; QUERO SENTIR A TUA PRESENÇA, POIS SEI QUE SÓ ASSIM FELIZ SEREI.\n\nNÃO QUERES TU TAMBÉM VIVER COM CRISTO E NELE SEMPRE CONFIAR? SUA PRESENÇA TE DARÁ CONFORTO, E SEU AMOR TE LEVARÁ AO LAR.\n\nCORO SAUDADES, TENHO SAUDADES DE TI, JESUS, MEU SALVADOR E REI; QUERO SENTIR A TUA PRESENÇA, POIS SEI QUE SÓ ASSIM FELIZ SEREI.', 'TENHO SAUDADES DE JESUS, MEU MESTRE, TENHO SAUDADES DE JESUS, MEU REI, MAS AO FINDAR O MEU LABOR TERRESTRE ME ENCONTRAREI COM ELE, SIM, EU SEI. CORO SAUDADES, TENHO SAUDADES DE TI, JESUS, MEU SALVADOR E REI; QUERO SENTIR A TUA PRESENÇA, POIS SEI QUE SÓ ASSIM FELIZ SEREI. TENHO VONTADE DE VIVER PRA CRISTO, DE NOS SEUS BRAÇOS SEMPRE ME ABRIGAR; SIM, QUERO A ELE SEMPRE SER SUBMISSO E CONFIAR NO SEU AMOR SEM PAR. NÃO QUERES TU TAMBÉM VIVER COM CRISTO E NELE SEMPRE CONFIAR? SUA PRESENÇA TE DARÁ CONFORTO, E SEU AMOR TE LEVARÁ AO LAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('504', '504 - EU SEI QUE JESUS', 'EU SEI QUE JESUS NÃO TARDA EM VIR, O MEU CORAÇÃO PREPARADO ESTÁ. INSTRUMENTOS EM CRISTO JESUS, A GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO.\n\nCORO JESUS, TU ÉS O MEU REI, JESUS É A ESPERANÇA DA GLÓRIA POR VIR, POR VIR, DA GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO. INSTRUMENTOS\n\nEM BREVE HÁ DE VIR, JESUS, O SENHOR, ETERNA ESPERANÇA DO MEU CORAÇÃO, A GLÓRIA POR VIR, REVELADA, ENTÃO, IREI COM JESUS PARA SEMPRE MORAR.\n\nCORO JESUS, TU ÉS O MEU REI, JESUS É A ESPERANÇA DA GLÓRIA POR VIR, POR VIR, DA GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO. FINAL: JESUS É A ESPERANÇA DO MEU CORAÇÃO', 'EU SEI QUE JESUS NÃO TARDA EM VIR, O MEU CORAÇÃO PREPARADO ESTÁ. INSTRUMENTOS EM CRISTO JESUS, A GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO. CORO JESUS, TU ÉS O MEU REI, JESUS É A ESPERANÇA DA GLÓRIA POR VIR, POR VIR, DA GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO. INSTRUMENTOS EM BREVE HÁ DE VIR, JESUS, O SENHOR, ETERNA ESPERANÇA DO MEU CORAÇÃO, A GLÓRIA POR VIR, REVELADA, ENTÃO, IREI COM JESUS PARA SEMPRE MORAR. CORO JESUS, TU ÉS O MEU REI, JESUS É A ESPERANÇA DA GLÓRIA POR VIR, POR VIR, DA GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO.  JESUS É A ESPERANÇA DO MEU CORAÇÃO',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('505', '505 - OS SEUS CABELOS', 'OS SEUS CABELOS BRANCOS SÃO COMO A NEVE E A BRANCA LÃ, E OS SEUS OLHOS COMO UMA CHAMA FORTE NO ROSTO A BRILHAR; RUMOR DE MUITAS ÁGUAS É SUA VOZ TAMBÉM, COMO UMA ESPADA AGUDA É O SEU FALAR.\n\nCORO JESUS! JESUS! SENHOR DOS SENHORES! PRINCÍPIO E FIM, O QUE ERA E É, E O QUE HÁ DE VIR! SEU NOME É JESUS! SEU NOME É JESUS!\n\nELE É O CORDEIRO DIGNO DE ABRIR O LIVRO POIS NA CRUZ MORREU, E COM SEU SANGUE HOMENS DE TODA A TRIBO PARA O PAI COMPROU; DIGNO É DE RECEBER TODA A ETERNIDADE, AÇÕES DE GRAÇAS, GLÓRIA E TODO O PODER.\n\nCORO JESUS! JESUS! SENHOR DOS SENHORES! PRINCÍPIO E FIM, O QUE ERA E É, E O QUE HÁ DE VIR! SEU NOME É JESUS! SEU NOME É JESUS!\n\nELE É O QUE VEM NAS NUVENS BUSCAR A ESPOSA, PREPARADA JÁ, COM SUAS VESTES BRANCAS, CANDEIA ACESA E CHEIA DE AMOR; ELE É A RESPLANDECENTE ESTRELA DA MANHÃ. “CEDO VIREI!” VEM JÁ, Ó SENHOR JESUS!\n\nCORO JESUS! JESUS! SENHOR DOS SENHORES! PRINCÍPIO E FIM, O QUE ERA E É, E O QUE HÁ DE VIR! SEU NOME É JESUS! SEU NOME É JESUS!', 'OS SEUS CABELOS BRANCOS SÃO COMO A NEVE E A BRANCA LÃ, E OS SEUS OLHOS COMO UMA CHAMA FORTE NO ROSTO A BRILHAR; RUMOR DE MUITAS ÁGUAS É SUA VOZ TAMBÉM, COMO UMA ESPADA AGUDA É O SEU FALAR. CORO JESUS! JESUS! SENHOR DOS SENHORES! PRINCÍPIO E FIM, O QUE ERA E É, E O QUE HÁ DE VIR! SEU NOME É JESUS! SEU NOME É JESUS! ELE É O CORDEIRO DIGNO DE ABRIR O LIVRO POIS NA CRUZ MORREU, E COM SEU SANGUE HOMENS DE TODA A TRIBO PARA O PAI COMPROU; DIGNO É DE RECEBER TODA A ETERNIDADE, AÇÕES DE GRAÇAS, GLÓRIA E TODO O PODER. ELE É O QUE VEM NAS NUVENS BUSCAR A ESPOSA, PREPARADA JÁ, COM SUAS VESTES BRANCAS, CANDEIA ACESA E CHEIA DE AMOR; ELE É A RESPLANDECENTE ESTRELA DA MANHÃ. CEDO VIREI! VEM JÁ, Ó SENHOR JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('506', '506 - TU QUE ESTÁS ASSENTADO', 'TU QUE ESTÁS ASSENTADO NO TRONO, SEMPRE REINANDO, SOBERANO; ANJOS CANTANDO, HOMENS LOUVANDO, DEUS REUNIDO COM SEU POVO. Ó, ALFA, ÔMEGA, CRISTO, FILHO, Ó VEM, Ó VEM, Ó VEM SENHOR JESUS!\n\nANSIOSO ESPERO A TUA VOLTA, NO GRANDE DIA EM QUE TU VIRÁS; ENTÃO SUBIREMOS, COM CRISTO ESTAREMOS PARA SEMPRE, ALELUIA! MARANATA, CRISTO, FILHO, MESTRE,Ó VEM, Ó VEM, Ó VEM, SENHOR JESUS!\nBIS', 'TU QUE ESTÁS ASSENTADO NO TRONO, SEMPRE REINANDO, SOBERANO; ANJOS CANTANDO, HOMENS LOUVANDO, DEUS REUNIDO COM SEU POVO. Ó, ALFA, ÔMEGA, CRISTO, FILHO, Ó VEM, Ó VEM, Ó VEM SENHOR JESUS! ANSIOSO ESPERO A TUA VOLTA, NO GRANDE DIA EM QUE TU VIRÁS; ENTÃO SUBIREMOS, COM CRISTO ESTAREMOS PARA SEMPRE, ALELUIA! MARANATA, CRISTO, FILHO, MESTRE,Ó VEM, Ó VEM, Ó VEM, SENHOR JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('507', '507 - O SOL ESCURECERÁ', 'O SOL ESCURECERÁ, A LUA NÃO MAIS BRILHARÁ E APARECERÁ NO CÉU COM PODER E GLÓRIA O FILHO DO HOMEM. DIA DE DORES, DIA DE TREVAS, O JUÍZO DO SENHOR VIRÁ SOBRE A TERRA; DIA GLORIOSO, MARAVILHOSO, A IGREJA REINARÁ COM CRISTO NA GLÓRIA.\n\nCANTAREMOS AO SENHOR LOUVORES DE VITÓRIA POIS A IGREJA VENCERÁ, JESUS VOLTARÁ. FINAL: JESUS, JESUS VOLTARÁ.\nBIS', 'O SOL ESCURECERÁ, A LUA NÃO MAIS BRILHARÁ E APARECERÁ NO CÉU COM PODER E GLÓRIA O FILHO DO HOMEM. DIA DE DORES, DIA DE TREVAS, O JUÍZO DO SENHOR VIRÁ SOBRE A TERRA; DIA GLORIOSO, MARAVILHOSO, A IGREJA REINARÁ COM CRISTO NA GLÓRIA. CANTAREMOS AO SENHOR LOUVORES DE VITÓRIA POIS A IGREJA VENCERÁ, JESUS VOLTARÁ.  JESUS, JESUS VOLTARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('508', '508 - BREVE NO CÉU JESUS HÁ DE APARECER', 'BREVE NO CÉU JESUS HÁ DE APARECER, EM GLORIOSA LUZ TODOS O HÃO DE VER; NAQUELE DIA, ENTÃO, EU HEI DE RECEBER DE CRISTO O GALARDÃO OH QUE PRAZER!\n\nCORO BREVE VEREI O BOM JESUS E VIVEREI EM PLENA LUZ; NO LINDO CÉU, EU GOZAREI, DE TODA A DOR, POR DEUS, LIVRE SEREI.\n\nNA VINDA DO SENHOR DESFRUTAREI PRAZER, QUANDO MEU SALVADOR EM GLÓRIA APARECER; EIS QUE ELE BREVE VEM, OS SANTOS LEVARÁ PARA A MANSÃO DE ALÉM, DONDE VIRÁ.\n\nCORO BREVE VEREI O BOM JESUS E VIVEREI EM PLENA LUZ; NO LINDO CÉU, EU GOZAREI, DE TODA A DOR, POR DEUS, LIVRE SEREI.', 'BREVE NO CÉU JESUS HÁ DE APARECER, EM GLORIOSA LUZ TODOS O HÃO DE VER; NAQUELE DIA, ENTÃO, EU HEI DE RECEBER DE CRISTO O GALARDÃO OH QUE PRAZER! CORO BREVE VEREI O BOM JESUS E VIVEREI EM PLENA LUZ; NO LINDO CÉU, EU GOZAREI, DE TODA A DOR, POR DEUS, LIVRE SEREI. NA VINDA DO SENHOR DESFRUTAREI PRAZER, QUANDO MEU SALVADOR EM GLÓRIA APARECER; EIS QUE ELE BREVE VEM, OS SANTOS LEVARÁ PARA A MANSÃO DE ALÉM, DONDE VIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('509', '509 - EM BREVE, MUI BREVE', 'EM BREVE, MUI BREVE IREMOS VER AS PORTAS DO CÉU SE ABRIREM, O CORDEIRO DESCENDO EM GLÓRIA NO MONTE DAS OLIVEIRAS. CORO (2X) HOSANA, HOSANA, HOSANA AO FILHO DE DAVI\n\nJUNTOS COM OS ANJOS LOUVAREMOS O SEU SANTO NOME, TODOS OS SEUS REMIDOS EXALTARÃO O REI NA SUA GLÓRIA. CORO (2X) HOSANA, HOSANA, HOSANA AO FILHO DE DAVI. FINAL: HOSANA AO FILHO DE DAVI.', 'EM BREVE, MUI BREVE IREMOS VER AS PORTAS DO CÉU SE ABRIREM, O CORDEIRO DESCENDO EM GLÓRIA NO MONTE DAS OLIVEIRAS. CORO  HOSANA, HOSANA, HOSANA AO FILHO DE DAVI JUNTOS COM OS ANJOS LOUVAREMOS O SEU SANTO NOME, TODOS OS SEUS REMIDOS EXALTARÃO O REI NA SUA GLÓRIA. CORO  HOSANA, HOSANA, HOSANA AO FILHO DE DAVI.  HOSANA AO FILHO DE DAVI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('510', '510 - ALERTA POVO, ESTÁ CHEGANDO A NOSSA HORA', 'ALERTA POVO, ESTÁ CHEGANDO A NOSSA HORA DA VITÓRIA QUE JESUS TEM PRA NOS DAR; ELE VEM COM PODER E GRANDE GLÓRIA, DESCENDO AO MUNDO PARA NOS ARREBATAR.\n\nCORO E NUM ABRIR E FECHAR DE OLHOS NÓS ESTAREMOS NAS MANSÕES CELESTIAIS, E SÓ SE OUVE: GLÓRIA A DEUS E ALELUIA! DOS REMIDOS QUE NO CÉU TÊM SEU LUGAR.\n\nALI NÃO HÁ MAIS PRANTO NEM DORES, AS NOSSAS LÁGRIMAS JESUS VAI ENXUGAR, E BEM NO MEIO ESTÁ A ÁRVORE DA VIDA QUE TEM SEUS FRUTOS PARA NOS ALIMENTAR.\n\nCORO E NUM ABRIR E FECHAR DE OLHOS NÓS ESTAREMOS NAS MANSÕES CELESTIAIS, E SÓ SE OUVE: GLÓRIA A DEUS E ALELUIA! DOS REMIDOS QUE NO CÉU TÊM SEU LUGAR.', 'ALERTA POVO, ESTÁ CHEGANDO A NOSSA HORA DA VITÓRIA QUE JESUS TEM PRA NOS DAR; ELE VEM COM PODER E GRANDE GLÓRIA, DESCENDO AO MUNDO PARA NOS ARREBATAR. CORO E NUM ABRIR E FECHAR DE OLHOS NÓS ESTAREMOS NAS MANSÕES CELESTIAIS, E SÓ SE OUVE: GLÓRIA A DEUS E ALELUIA! DOS REMIDOS QUE NO CÉU TÊM SEU LUGAR. ALI NÃO HÁ MAIS PRANTO NEM DORES, AS NOSSAS LÁGRIMAS JESUS VAI ENXUGAR, E BEM NO MEIO ESTÁ A ÁRVORE DA VIDA QUE TEM SEUS FRUTOS PARA NOS ALIMENTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('511', '511 - JESUS CRISTO VOLTARÁ COM SEU GRANDE ESPLENDOR', 'JESUS CRISTO VOLTARÁ COM SEU GRANDE ESPLENDOR, A IGREJA SUBIRÁ AO ENCONTRO DO SENHOR. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS!\n\nESSE DIA CHEGARÁ, ESTAREMOS NAS MANSÕES, TUDO AQUI HÁ DE FINDAR, JESUS CRISTO VOLTARÁ. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS!\n\nCANTAREMOS LOUVORES JUNTO COM OS ANJOS A JESUS, O REI. TODOS OS SEUS REMIDOS NO CÉU ESTARÃO LOUVANDO A CRISTO, O REI. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS! FINAL: MARANATA, MARANATA, VEM, JESUS!', 'JESUS CRISTO VOLTARÁ COM SEU GRANDE ESPLENDOR, A IGREJA SUBIRÁ AO ENCONTRO DO SENHOR. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS! ESSE DIA CHEGARÁ, ESTAREMOS NAS MANSÕES, TUDO AQUI HÁ DE FINDAR, JESUS CRISTO VOLTARÁ. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS! CANTAREMOS LOUVORES JUNTO COM OS ANJOS A JESUS, O REI. TODOS OS SEUS REMIDOS NO CÉU ESTARÃO LOUVANDO A CRISTO, O REI. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS!  MARANATA, MARANATA, VEM, JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('512', '512 - O MEU AMADO FALA E ME DIZ', 'O MEU AMADO FALA E ME DIZ: “LEVANTA-TE QUERIDA MINHA FORMOSA MINHA E VEM, E VEM, VEM, VEM”; PORQUE EIS QUE PASSOU O INVERNO, CESSOU A CHUVA E SE FOI, APARECEM AS FLORES NA TERRA, CHEGOU O TEMPO DE CANTAREM AS AVES.\n\nA VOZ DA ROLA OUVE-SE EM NOSSA TERRA A FIGUEIRA COMEÇOU A DAR SEUS FIGOS E AS VIDES EM FLOR EXALAM SEU AROMA; LEVANTA-TE, QUERIDA MINHA, FORMOSA MINHA E VEM.\n\nPOMBA MINHA QUE ANDAS PELAS FENDAS DOS PENHASCOS, NO ESCONDERIJO DAS ROCHAS ESCARPADAS, MOSTRA-ME O TEU ROSTO, FAZE-ME OUVIR A TUA VOZ, A TUA VOZ É DOCE E TEU ROSTO AMÁVEL.\n\nA VOZ DA ROLA OUVE-SE EM NOSSA TERRA A FIGUEIRA COMEÇOU A DAR SEUS FIGOS E AS VIDES EM FLOR EXALAM SEU AROMA; LEVANTA-TE, QUERIDA MINHA, FORMOSA MINHA E VEM.\n\nPOMBA MINHA QUE ANDAS PELAS FENDAS DOS PENHASCOS, NO ESCONDERIJO DAS ROCHAS ESCARPADAS, MOSTRA-ME O TEU ROSTO, FAZE-ME OUVIR A TUA VOZ, A TUA VOZ É DOCE E TEU ROSTO AMÁVEL. (3X)', 'O MEU AMADO FALA E ME DIZ: LEVANTA-TE QUERIDA MINHA FORMOSA MINHA E VEM, E VEM, VEM, VEM; PORQUE EIS QUE PASSOU O INVERNO, CESSOU A CHUVA E SE FOI, APARECEM AS FLORES NA TERRA, CHEGOU O TEMPO DE CANTAREM AS AVES. A VOZ DA ROLA OUVE-SE EM NOSSA TERRA A FIGUEIRA COMEÇOU A DAR SEUS FIGOS E AS VIDES EM FLOR EXALAM SEU AROMA; LEVANTA-TE, QUERIDA MINHA, FORMOSA MINHA E VEM. POMBA MINHA QUE ANDAS PELAS FENDAS DOS PENHASCOS, NO ESCONDERIJO DAS ROCHAS ESCARPADAS, MOSTRA-ME O TEU ROSTO, FAZE-ME OUVIR A TUA VOZ, A TUA VOZ É DOCE E TEU ROSTO AMÁVEL.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('513', '513 - QUEM É ESTA QUE APARECE', 'QUEM É ESTA QUE APARECE COMO A ALVA DO DIA, FORMOSA COMO A LUA, PURA COMO O SOL, FORMIDÁVEL COMO UM EXÉRCITO COM BANDEIRAS!\n\nDESCI AO JARDIM DAS NOGUEIRAS, PARA MIRAR OS RENOVOS DO VALE, PARA VER SE BROTAVAM AS VIDES, SE FLORESCIAM AS ROMEIRAS. NÃO SEI COMO, IMAGINEI-ME NO CARRO DO MEU NOBRE POVO!\n\nVOLTA, VOLTA, Ó, SULAMITA, VOLTA, VOLTA PRA QUE NÓS TE CONTEMPLEMOS; POR QUE QUEREIS CONTEMPLAR A SULAMITA, NA DANÇA DE MAANAIM? (BIS)\nBIS', 'QUEM É ESTA QUE APARECE COMO A ALVA DO DIA, FORMOSA COMO A LUA, PURA COMO O SOL, FORMIDÁVEL COMO UM EXÉRCITO COM BANDEIRAS! DESCI AO JARDIM DAS NOGUEIRAS, PARA MIRAR OS RENOVOS DO VALE, PARA VER SE BROTAVAM AS VIDES, SE FLORESCIAM AS ROMEIRAS. NÃO SEI COMO, IMAGINEI-ME NO CARRO DO MEU NOBRE POVO! VOLTA, VOLTA, Ó, SULAMITA, VOLTA, VOLTA PRA QUE NÓS TE CONTEMPLEMOS; POR QUE QUEREIS CONTEMPLAR A SULAMITA, NA DANÇA DE MAANAIM?',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('514', '514 - ESTA É A VOZ, A VOZ DO MEU AMADO', 'ESTA É A VOZ, A VOZ DO MEU AMADO, ÉS PARA MIM RAMALHETE DE MIRRA; TEU PERFUME, UNGUENTO DERRAMADO, TU ÉS GENTIL, AGRADÁVEL É O TEU AROMA.\n\nCORO VEM COMIGO, (VEM COMIGO) SERVAS VEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO!\n\nTU ÉS TODA FORMOSA, AMIGA MINHA, EM TI NÃO HÁ MANCHAS, ESPOSA MINHA; FAVOS DE MEL MANAM DOS TEUS LÁBIOS, OS TEUS VESTIDOS TÊM CHEIRO AROMÁTICO.\n\nCORO VEM COMIGO, (VEM COMIGO) SERVAS VEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO!\n\nTU ÉS FORMOSA, FORMOSA ENTRE AS MULHERES, OS TEUS OLHOS SÃO COMO OS DAS POMBAS; QUE BELOS SÃO OS TEUS AMORES, BEM DE MANHÃ IREMOS ÀS VINHAS.\n\nCORO (2X) VEM COMIGO, (VEM COMIGO) SERVAS VEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO! FINAL: Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO!', 'ESTA É A VOZ, A VOZ DO MEU AMADO, ÉS PARA MIM RAMALHETE DE MIRRA; TEU PERFUME, UNGUENTO DERRAMADO, TU ÉS GENTIL, AGRADÁVEL É O TEU AROMA. CORO VEM COMIGO, (VEM COMIGO) SERVAS VEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO! TU ÉS TODA FORMOSA, AMIGA MINHA, EM TI NÃO HÁ MANCHAS, ESPOSA MINHA; FAVOS DE MEL MANAM DOS TEUS LÁBIOS, OS TEUS VESTIDOS TÊM CHEIRO AROMÁTICO. TU ÉS FORMOSA, FORMOSA ENTRE AS MULHERES, OS TEUS OLHOS SÃO COMO OS DAS POMBAS; QUE BELOS SÃO OS TEUS AMORES, BEM DE MANHÃ IREMOS ÀS VINHAS. CORO  VEM COMIGO, (VEM COMIGO) SERVAS VEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO!  Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('515', '515 - QUANDO A ÚLTIMA TROMBETA ECOAR', 'QUANDO A ÚLTIMA TROMBETA ECOAR O MEU NOME OUVIREI JESUS CHAMAR, REVESTIDO EM NOVO CORPO ELE ME TRANSFORMARÁ, PODEREI ENTÃO SEU ROSTO CONTEMPLAR.\n\nCORO ALELUIA, ALELUIA, ALELUIA, ELE ME TRANSFORMARÁ; ALELUIA, ALELUIA, PODEREI ENTÃO SEU ROSTO CONTEMPLAR. INSTRUMENTOS\n\nQUANDO A ÚLTIMA TROMBETA ECOAR O MEU NOME OUVIREI JESUS CHAMAR, REVESTIDO EM NOVO CORPO ELE ME TRANSFORMARÁ, PODEREI ENTÃO SEU ROSTO CONTEMPLAR.\n\nCORO ALELUIA, ALELUIA, ALELUIA, ELE ME TRANSFORMARÁ; ALELUIA, ALELUIA, PODEREI ENTÃO SEU ROSTO CONTEMPLAR. FINAL: PODEREI ENTÃO SEU ROSTO CONTEMPLAR.', 'QUANDO A ÚLTIMA TROMBETA ECOAR O MEU NOME OUVIREI JESUS CHAMAR, REVESTIDO EM NOVO CORPO ELE ME TRANSFORMARÁ, PODEREI ENTÃO SEU ROSTO CONTEMPLAR. CORO ALELUIA, ALELUIA, ALELUIA, ELE ME TRANSFORMARÁ; ALELUIA, ALELUIA, PODEREI ENTÃO SEU ROSTO CONTEMPLAR. INSTRUMENTOS CORO ALELUIA, ALELUIA, ALELUIA, ELE ME TRANSFORMARÁ; ALELUIA, ALELUIA, PODEREI ENTÃO SEU ROSTO CONTEMPLAR.  PODEREI ENTÃO SEU ROSTO CONTEMPLAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('516', '516 - QUANDO A ANGELICAL TROMBETA', 'QUANDO A ANGELICAL TROMBETA NESTE MUNDO ESTRUGIR, O MEU NOME OUVIREI JESUS CHAMAR; POIS EU CREIO NA PROMESSA E QUE DEUS A VAI CUMPRIR, QUANDO OUVIR JESUS MEU NOME PROCLAMAR.\n\nCORO GLÓRIA, GLÓRIA, ALELUIA! O MEU NOME OUVIREI JESUS CHAMAR; GLÓRIA, GLÓRIA, ALELUIA! EU ESPERO OUVIR JESUS A ME CHAMAR.\n\nQUANDO O CÉU FOR ENROLADO E O SOL NÃO DER MAIS LUZ, O MEU NOME OUVIREI JESUS CHAMAR; PASSARÃO A TERRA, O MAR, MAS PERMANECERÁ JESUS, QUE MEU NOME VAI, NA GLÓRIA, PRONUNCIAR.\n\nCORO GLÓRIA, GLÓRIA, ALELUIA! O MEU NOME OUVIREI JESUS CHAMAR; GLÓRIA, GLÓRIA, ALELUIA! EU ESPERO OUVIR JESUS A ME CHAMAR.\n\nOH! QUE MÚSICA SUAVE LÁ NO CÉU IREI OUVIR; O MEU NOME JESUS CRISTO ANUNCIAR; OH! QUE GOZO VAI MINH’ALMA LÁ NOS ALTOS CÉUS FLUIR, QUANDO CRISTO O MEU NOME PROCLAMAR.\n\nCORO GLÓRIA, GLÓRIA, ALELUIA! O MEU NOME OUVIREI JESUS CHAMAR; GLÓRIA, GLÓRIA, ALELUIA! EU ESPERO OUVIR JESUS A ME CHAMAR.', 'QUANDO A ANGELICAL TROMBETA NESTE MUNDO ESTRUGIR, O MEU NOME OUVIREI JESUS CHAMAR; POIS EU CREIO NA PROMESSA E QUE DEUS A VAI CUMPRIR, QUANDO OUVIR JESUS MEU NOME PROCLAMAR. CORO GLÓRIA, GLÓRIA, ALELUIA! O MEU NOME OUVIREI JESUS CHAMAR; GLÓRIA, GLÓRIA, ALELUIA! EU ESPERO OUVIR JESUS A ME CHAMAR. QUANDO O CÉU FOR ENROLADO E O SOL NÃO DER MAIS LUZ, O MEU NOME OUVIREI JESUS CHAMAR; PASSARÃO A TERRA, O MAR, MAS PERMANECERÁ JESUS, QUE MEU NOME VAI, NA GLÓRIA, PRONUNCIAR. OH! QUE MÚSICA SUAVE LÁ NO CÉU IREI OUVIR; O MEU NOME JESUS CRISTO ANUNCIAR; OH! QUE GOZO VAI MINH’ALMA LÁ NOS ALTOS CÉUS FLUIR, QUANDO CRISTO O MEU NOME PROCLAMAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('517', '517 - JÁ NÃO TARDA O AMANHECER', 'JÁ NÃO TARDA O AMANHECER, A ALVORADA DE UM NOVO SER; PELOS MONTES DE LUZ JÁ VEM O MEU JESUS! ELE MANDA ME LEVANTAR, HÁ UMA FESTA A CELEBRAR; COM AMOR EU IREI CANTAR LOUVORES AO REI! ALELUIA, ALELUIA, ALELUIA, NÃO TARDA O AMANHECER!\n\nA FIGUEIRA SEUS FRUTOS DÁ, A VIDEIRA EM FLOR ESTÁ, O INVERNO PASSOU E A CHUVA JÁ CESSOU; TODA A TERRA RENASCE EM FLOR, PRIMAVERA, CANTANDO AMOR; ESTÁ TÃO PERTO DE NÓS, JÁ OUÇO A SUA VOZ! ALELUIA, ALELUIA, ALELUIA, JÁ OUÇO A SUA VOZ.\n\nSOPRA O VENTO NO MEU JARDIM, QUERO A TEMPO TER FLOR EM MIM, SEU PERFUME SEM PAR DESEJO DERRAMAR; SE DESCESSES DO ALTO CÉU E VIESSES, AMADO MEU, E COMESSES DO FRUTO QUE TE QUERO DAR! ALELUIA, ALELUIA, ALELUIA, O MEU AMADO VEM!', 'JÁ NÃO TARDA O AMANHECER, A ALVORADA DE UM NOVO SER; PELOS MONTES DE LUZ JÁ VEM O MEU JESUS! ELE MANDA ME LEVANTAR, HÁ UMA FESTA A CELEBRAR; COM AMOR EU IREI CANTAR LOUVORES AO REI! ALELUIA, ALELUIA, ALELUIA, NÃO TARDA O AMANHECER! A FIGUEIRA SEUS FRUTOS DÁ, A VIDEIRA EM FLOR ESTÁ, O INVERNO PASSOU E A CHUVA JÁ CESSOU; TODA A TERRA RENASCE EM FLOR, PRIMAVERA, CANTANDO AMOR; ESTÁ TÃO PERTO DE NÓS, JÁ OUÇO A SUA VOZ! ALELUIA, ALELUIA, ALELUIA, JÁ OUÇO A SUA VOZ. SOPRA O VENTO NO MEU JARDIM, QUERO A TEMPO TER FLOR EM MIM, SEU PERFUME SEM PAR DESEJO DERRAMAR; SE DESCESSES DO ALTO CÉU E VIESSES, AMADO MEU, E COMESSES DO FRUTO QUE TE QUERO DAR! ALELUIA, ALELUIA, ALELUIA, O MEU AMADO VEM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('518', '518 - NÃO TEREI MAIS TRISTEZAS', 'NÃO TEREI MAIS TRISTEZAS NEM MAIS FARDOS EU LEVAREI, NEM MAIS MORTE NEM MAIS DOR, EU JAMAIS ME SEPARAREI DE MEU MESTRE, MEU SENHOR; PARA SEMPRE ALI ESTAREI E FELIZ, COM JESUS, EU SEREI.\n\nCORO EU VOU COM CRISTO PARA O CÉU EM GLÓRIA ANJOS CANTANDO AO MEU REDOR, QUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, COM CRISTO FELIZ EU SEREI.\n\nOH! QUE DIA TÃO LINDO DO ENCONTRO COM CRISTO E LOUVORES A ELE DAREI. OH! QUE ALEGRIA O DIA QUE TEREI A COROA, COM CRISTO FELIZ EU SEREI.\n\nCORO EU VOU COM CRISTO PARA O CÉU EM GLÓRIA ANJOS CANTANDO AO MEU REDOR, QUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, COM CRISTO FELIZ EU SEREI. FINAL: COM CRISTO FELIZ EU SEREI.', 'NÃO TEREI MAIS TRISTEZAS NEM MAIS FARDOS EU LEVAREI, NEM MAIS MORTE NEM MAIS DOR, EU JAMAIS ME SEPARAREI DE MEU MESTRE, MEU SENHOR; PARA SEMPRE ALI ESTAREI E FELIZ, COM JESUS, EU SEREI. CORO EU VOU COM CRISTO PARA O CÉU EM GLÓRIA ANJOS CANTANDO AO MEU REDOR, QUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, COM CRISTO FELIZ EU SEREI. OH! QUE DIA TÃO LINDO DO ENCONTRO COM CRISTO E LOUVORES A ELE DAREI. OH! QUE ALEGRIA O DIA QUE TEREI A COROA, COM CRISTO FELIZ EU SEREI. CORO EU VOU COM CRISTO PARA O CÉU EM GLÓRIA ANJOS CANTANDO AO MEU REDOR, QUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, COM CRISTO FELIZ EU SEREI.  COM CRISTO FELIZ EU SEREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('519', '519 - UM NOVO LAR ME ESPERA', 'UM NOVO LAR ME ESPERA, VOU COM JESUS MORAR; QUE ALEGRIA SEM PAR, VOU PRA GLÓRIA, É A VITÓRIA. ANDAR PELAS RUAS DE OURO COM OS ANJOS E O REI JESUS, A QUEM ENTREGUEI A MINHA VIDA POIS POR MIM MORREU NA CRUZ.\nBIS', 'UM NOVO LAR ME ESPERA, VOU COM JESUS MORAR; QUE ALEGRIA SEM PAR, VOU PRA GLÓRIA, É A VITÓRIA. ANDAR PELAS RUAS DE OURO COM OS ANJOS E O REI JESUS, A QUEM ENTREGUEI A MINHA VIDA POIS POR MIM MORREU NA CRUZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('520', '520 - NO TRONO DA CIDADE DE DEUS', 'NO TRONO DA CIDADE DE DEUS, JERUSALÉM, OS ANJOS CANTAM ALELUIA, LOUVANDO A JESUS, REI DOS REIS, O SALVADOR, O LEÃO DE JUDÁ.\n\nCORO GLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, GLÓRIA, MILHARES DE VOZES NO CÉU PROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO. INSTRUMENTOS\n\nEM BREVE O MEU JESUS VOLTARÁ PARA LEVAR A IGREJA FIEL E TODOS JUNTOS NA ETERNIDADE, COM O SALVADOR NA ETERNA SIÃO!\n\nCORO GLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, GLÓRIA, MILHARES DE VOZES NO CÉU PROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO. FINAL: (SANTO) SANTO, SANTO, (SANTO) SANTO, SANTO.', 'NO TRONO DA CIDADE DE DEUS, JERUSALÉM, OS ANJOS CANTAM ALELUIA, LOUVANDO A JESUS, REI DOS REIS, O SALVADOR, O LEÃO DE JUDÁ. CORO GLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, GLÓRIA, MILHARES DE VOZES NO CÉU PROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO. INSTRUMENTOS EM BREVE O MEU JESUS VOLTARÁ PARA LEVAR A IGREJA FIEL E TODOS JUNTOS NA ETERNIDADE, COM O SALVADOR NA ETERNA SIÃO! CORO GLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, GLÓRIA, MILHARES DE VOZES NO CÉU PROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO.  (SANTO) SANTO, SANTO, (SANTO) SANTO, SANTO.',  1);
