INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('522', '522 - EXISTE UM LAR', 'EXISTE UM LAR QUE COM GRANDE AMOR FOI PREPARADO,DELE DESFRUTARÁ TODO AQUELE QUE A JESUS SE ENTREGOU. CORO NOVO CÉU, NOVA TERRA, NOVO LAR; DE FILHO, O MEU DEUS ME CHAMARÁ.\n\nMORTE, NEM LUTO, PRANTO, NEM DOR, SÓ PORTAS ABERTAS, LUZ E AMOR. CORO NOVO CÉU, NOVA TERRA, NOVO LAR; DE FILHO, O MEU DEUS ME CHAMARÁ.\nBIS', 'EXISTE UM LAR QUE COM GRANDE AMOR FOI PREPARADO,DELE DESFRUTARÁ TODO AQUELE QUE A JESUS SE ENTREGOU. CORO NOVO CÉU, NOVA TERRA, NOVO LAR; DE FILHO, O MEU DEUS ME CHAMARÁ. MORTE, NEM LUTO, PRANTO, NEM DOR, SÓ PORTAS ABERTAS, LUZ E AMOR. CORO NOVO CÉU, NOVA TERRA, NOVO LAR; DE FILHO, O MEU DEUS ME CHAMARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('523', '523 - LÁ NO CÉU OS ANJOS CANTAM', 'LÁ NO CÉU OS ANJOS CANTAM: VIVA, VIVA O REI; LINDO CORO CELESTIAL NA BELA CIDADE ETERNAL.\n\nCORO EM BREVE TAMBÉM ESTAREI NA SANTA CIDADE DE LUZ, LOUVANDO SEMPRE O MEU REI, O MEU PRECIOSO JESUS. LINDO RIO PURO DE ÁGUA VIVA QUE VEM DE DEUS PAI; LÁ NÃO HÁ TRISTEZA E DOR, SÓ PAZ, ALEGRIA E AMOR.\n\nCORO (2X) EM BREVE TAMBÉM ESTAREI NA SANTA CIDADE DE LUZ, LOUVANDO SEMPRE O MEU REI, O MEU PRECIOSO JESUS. (2X NO FINAL)', 'LÁ NO CÉU OS ANJOS CANTAM: VIVA, VIVA O REI; LINDO CORO CELESTIAL NA BELA CIDADE ETERNAL. CORO EM BREVE TAMBÉM ESTAREI NA SANTA CIDADE DE LUZ, LOUVANDO SEMPRE O MEU REI, O MEU PRECIOSO JESUS. LINDO RIO PURO DE ÁGUA VIVA QUE VEM DE DEUS PAI; LÁ NÃO HÁ TRISTEZA E DOR, SÓ PAZ, ALEGRIA E AMOR. CORO  EM BREVE TAMBÉM ESTAREI NA SANTA CIDADE DE LUZ, LOUVANDO SEMPRE O MEU REI, O MEU PRECIOSO JESUS. ( NO FINAL)',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('524', '524 - SE EU TIVESSE NA ALVORADA', 'SE EU ESTIVESSE NA ALVORADA COM AS ASAS ALVAS PARA VOAR, EU, MUI CONTENTE, ME TRANSLADAVA PARA AS FRONTEIRAS DE CANAÃ.\n\nCORO ANJOS DE BRANCO ME LEVARIAM JUNTO À PRESENÇA DO MEU SENHOR, E EU, COM JÚBILO, CANTARIA COM OS JÁ SALVOS POR SEU AMOR. LÁ NÃO HÁ PRANTO, NEM AMARGURA, LÁ NÃO SE SABE O QUE É DOR, ALI É GOZO E ALEGRIA, ALI É TUDO PURO AMOR.\n\nVESTIDOS BRANCOS, PALMA E COROA CADA UM DELES RECEBERÁ; E SE AGORA A CRISTO EU SIGO, JUNTO A ELE HEI DE VENCER.\n\nCORO ANJOS DE BRANCO ME LEVARIAM JUNTO À PRESENÇA DO MEU SENHOR, E EU, COM JÚBILO, CANTARIA COM OS JÁ SALVOS POR SEU AMOR. LÁ NÃO HÁ PRANTO, NEM AMARGURA, LÁ NÃO SE SABE O QUE É DOR, ALI É GOZO E ALEGRIA, ALI É TUDO PURO AMOR.', 'SE EU ESTIVESSE NA ALVORADA COM AS ASAS ALVAS PARA VOAR, EU, MUI CONTENTE, ME TRANSLADAVA PARA AS FRONTEIRAS DE CANAÃ. CORO ANJOS DE BRANCO ME LEVARIAM JUNTO À PRESENÇA DO MEU SENHOR, E EU, COM JÚBILO, CANTARIA COM OS JÁ SALVOS POR SEU AMOR. LÁ NÃO HÁ PRANTO, NEM AMARGURA, LÁ NÃO SE SABE O QUE É DOR, ALI É GOZO E ALEGRIA, ALI É TUDO PURO AMOR. VESTIDOS BRANCOS, PALMA E COROA CADA UM DELES RECEBERÁ; E SE AGORA A CRISTO EU SIGO, JUNTO A ELE HEI DE VENCER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('525', '525 - HÁ UM LINDO PAÍS', 'HÁ UM LINDO PAÍS ONDE OS ANJOS NÃO CESSAM DE ENTOAR LOUVORES AO GRANDE REI. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ.\n\nNO MEU NOVO LAR AS LÁGRIMAS JÁ NÃO CORREM POIS O SENHOR COM SEU AMOR ENXUGOU. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ.\n\nNO MEU NOVO LAR AS LÁGRIMAS JÁ NÃO CORREM POIS O SENHOR COM SEU AMOR ENXUGOU. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ. FINAL: (2X) (UM CORO CELESTIAL) A JESUS, O LEÃO DA TRIBO DE JUDÁ', 'HÁ UM LINDO PAÍS ONDE OS ANJOS NÃO CESSAM DE ENTOAR LOUVORES AO GRANDE REI. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ. NO MEU NOVO LAR AS LÁGRIMAS JÁ NÃO CORREM POIS O SENHOR COM SEU AMOR ENXUGOU. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ. NO MEU NOVO LAR AS LÁGRIMAS JÁ NÃO CORREM POIS O SENHOR COM SEU AMOR ENXUGOU. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ.   (UM CORO CELESTIAL) A JESUS, O LEÃO DA TRIBO DE JUDÁ',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('526', '526 - LINDO! LINDO! LINDO!', 'CORO (2X) LINDO! LINDO! LINDO! É NOVA JERUSALÉM.\n\nCIDADE SANTA DO MEU REI E SENHOR, ANJOS E ARCANJOS HABITAM ALI; A MORTE E A DOR JAMAIS ENTRARÃO NESSA CIDADE ETERNA DE GOZO E DE PAZ. CORO (2X) LINDO! LINDO! LINDO! É NOVA JERUSALÉM. INSTRUMENTOS\n\nOH! QUANTO ALMEJO ESTAR ALI NESSA CIDADE DE BRILHO ETERNAL, PARA CONTEMPLAR O REI JESUS, AQUELE QUE REVELOU A MIM SUA LUZ. CORO (2X) LINDO! LINDO! LINDO! É NOVA JERUSALÉM.', 'CORO  LINDO! LINDO! LINDO! É NOVA JERUSALÉM. CIDADE SANTA DO MEU REI E SENHOR, ANJOS E ARCANJOS HABITAM ALI; A MORTE E A DOR JAMAIS ENTRARÃO NESSA CIDADE ETERNA DE GOZO E DE PAZ. CORO  LINDO! LINDO! LINDO! É NOVA JERUSALÉM. INSTRUMENTOS OH! QUANTO ALMEJO ESTAR ALI NESSA CIDADE DE BRILHO ETERNAL, PARA CONTEMPLAR O REI JESUS, AQUELE QUE REVELOU A MIM SUA LUZ. CORO  LINDO! LINDO! LINDO! É NOVA JERUSALÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('527', '527 - REGOZIJAI-VOS', '“REGOZIJAI-VOS, Ó MEUS AMADOS, ESPERAI UM POUCO MAIS, O MEU FILHO VIRÁ EM BREVE, SOBRE AS NUVENS ELE VIRÁ, JESUS VIRÁ!”\n\nCORO DESPERTAI (DESPERTAI) Ó FILHOS DE SIÃO, CONFIAI (CONFIAI) NO DEUS DE ABRAÃO, DEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI) NA VOSSA SALVAÇÃO POIS JESUS VIRÁ! INSTRUMENTOS\n\n“SANTIFICAI-VOS COMO EU SOU SANTO, RENOVAI VOSSAS CANDEIAS COM O ÓLEO DO MEU ESPÍRITO; PREPARAI-VOS, ELE VIRÁ, JESUS VIRÁ!”\n\nCORO (2X) DESPERTAI (DESPERTAI) Ó FILHOS DE SIÃO, CONFIAI (CONFIAI) NO DEUS DE ABRAÃO, DEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI) NA VOSSA SALVAÇÃO POIS JESUS VIRÁ! FINAL: ELE VIRÁ, JESUS VIRÁ!', 'REGOZIJAI-VOS, Ó MEUS AMADOS, ESPERAI UM POUCO MAIS, O MEU FILHO VIRÁ EM BREVE, SOBRE AS NUVENS ELE VIRÁ, JESUS VIRÁ! CORO DESPERTAI (DESPERTAI) Ó FILHOS DE SIÃO, CONFIAI (CONFIAI) NO DEUS DE ABRAÃO, DEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI) NA VOSSA SALVAÇÃO POIS JESUS VIRÁ! INSTRUMENTOS SANTIFICAI-VOS COMO EU SOU SANTO, RENOVAI VOSSAS CANDEIAS COM O ÓLEO DO MEU ESPÍRITO; PREPARAI-VOS, ELE VIRÁ, JESUS VIRÁ! CORO  DESPERTAI (DESPERTAI) Ó FILHOS DE SIÃO, CONFIAI (CONFIAI) NO DEUS DE ABRAÃO, DEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI) NA VOSSA SALVAÇÃO POIS JESUS VIRÁ!  ELE VIRÁ, JESUS VIRÁ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('528', '528 - EU AVISTO UMA TERRA FELIZ', 'EU AVISTO UMA TERRA FELIZ, ONDE IREI PARA SEMPRE MORAR; HÁ MANSÕES NESSE LINDO PAÍS QUE JESUS FOI AO CÉU PREPARAR. VOU MORAR, VOU MORAR NESSA TERRA CELESTE PORVIR.\nBIS\n\nCANTAREI NESSE LINDO PAÍS, BELOS HINOS AO MEU SALVADOR POIS ALI VIVEREI BEM FELIZ, SEM TRISTEZAS, VEXAMES NEM DOR. VOU CANTAR, VOU CANTAR NESSA TERRA CELESTE PORVIR.\nBIS\n\nDEIXAREI ESTE MUNDO AFINAL, PARA IR A JESUS ADORAR; NESSA BELA CIDADE FESTAL, MIL VENTURAS SEM FIM VOU GOZAR. VOU GOZAR, VOU GOZAR NESSA TERRA CELESTE PORVIR.\nBIS', 'EU AVISTO UMA TERRA FELIZ, ONDE IREI PARA SEMPRE MORAR; HÁ MANSÕES NESSE LINDO PAÍS QUE JESUS FOI AO CÉU PREPARAR. VOU MORAR, VOU MORAR NESSA TERRA CELESTE PORVIR. CANTAREI NESSE LINDO PAÍS, BELOS HINOS AO MEU SALVADOR POIS ALI VIVEREI BEM FELIZ, SEM TRISTEZAS, VEXAMES NEM DOR. VOU CANTAR, VOU CANTAR NESSA TERRA CELESTE PORVIR. DEIXAREI ESTE MUNDO AFINAL, PARA IR A JESUS ADORAR; NESSA BELA CIDADE FESTAL, MIL VENTURAS SEM FIM VOU GOZAR. VOU GOZAR, VOU GOZAR NESSA TERRA CELESTE PORVIR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('529', '529 - EU OUVI DA FORMOSA CIDADE', 'EU OUVI DA FORMOSA CIDADE, MUITO ACIMA DE TUDO NO MUNDO, EU SENTI UM DESEJO PROFUNDO DE UM DIA ESTAR COM JESUS. ALELUIA, EM VOZ TRIUNFANTE, JÁ ESCUTO O CORO CELESTE, TERMINADA A MÁGOA TERRESTRE ESTAREI COM JESUS LÁ NO CÉU.\n\nEU OUVI DE UM VESTIDO TÃO ALVO, DA COROA ORNADA DE ESTRELAS, DOS IRMÃOS, DA FELIZ PARENTELA, QUE UM DIA VEREI EU ALI. ALELUIA, ALI TUDO É GOZO CADA DÚVIDA FOI DISSIPADA, PARA SEMPRE A MALÍCIA É TIRADA, ESTOU NO ALVO, NO CÉU COM JESUS.\n\nEU OUVI DE UM PAÍS TÃO DISTANTE, ONDE NÃO HÁ DOENÇAS NEM DORES, NÃO HÁ MAIS ESTES VIS TENTADORES, QUE PERTURBAM A VIDA AQUI. ALELUIA, FELIZ ESPERANÇA, JÁ ESCUTO O CANTO CELESTE, TERMINADA A MÁGOA TERRESTRE ESTAREI COM JESUS LÁ NO CÉU.', 'EU OUVI DA FORMOSA CIDADE, MUITO ACIMA DE TUDO NO MUNDO, EU SENTI UM DESEJO PROFUNDO DE UM DIA ESTAR COM JESUS. ALELUIA, EM VOZ TRIUNFANTE, JÁ ESCUTO O CORO CELESTE, TERMINADA A MÁGOA TERRESTRE ESTAREI COM JESUS LÁ NO CÉU. EU OUVI DE UM VESTIDO TÃO ALVO, DA COROA ORNADA DE ESTRELAS, DOS IRMÃOS, DA FELIZ PARENTELA, QUE UM DIA VEREI EU ALI. ALELUIA, ALI TUDO É GOZO CADA DÚVIDA FOI DISSIPADA, PARA SEMPRE A MALÍCIA É TIRADA, ESTOU NO ALVO, NO CÉU COM JESUS. EU OUVI DE UM PAÍS TÃO DISTANTE, ONDE NÃO HÁ DOENÇAS NEM DORES, NÃO HÁ MAIS ESTES VIS TENTADORES, QUE PERTURBAM A VIDA AQUI. ALELUIA, FELIZ ESPERANÇA, JÁ ESCUTO O CANTO CELESTE, TERMINADA A MÁGOA TERRESTRE ESTAREI COM JESUS LÁ NO CÉU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('530', '530 - OH! QUE SAUDOSA LEMBRANÇA', 'OH! QUE SAUDOSA LEMBRANÇA TENHO DE TI, Ó SIÃO, TERRA QUE EU TANTO AMO POIS ÉS DO MEU CORAÇÃO; EU PARA TI VOAREI QUANDO O SENHOR MEU VOLTAR POIS ELE FOI PARA O CÉU E BREVE VEM ME BUSCAR.\n\nCORO SIM, EU PORFIAREI POR ESSA TERRA DE ALÉM E LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM; LÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER, ELE É TUDO PRA MIM, E SEM ELE EU NÃO POSSO VIVER.\n\nBELA, MUI BELA, É A ESPERANÇA DOS QUE VIGIAM POR TI, POIS ELES RECEBEM FORÇAS QUE SÓ SE ENCONTRAM ALI; OS QUE PROCURAM CHEGAR AO TEU REGAÇO, Ó SIÃO, LIVRES SERÃO DE PECAR E DE TODA A TENTAÇÃO.\n\nCORO SIM, EU PORFIAREI POR ESSA TERRA DE ALÉM E LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM; LÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER, ELE É TUDO PRA MIM, E SEM ELE EU NÃO POSSO VIVER.\n\nDIZ A SAGRADA ESCRITURA QUE SÃO FORMOSOS OS PÉS DAQUELES QUE BOAS NOVAS LEVAM PARA OS INFIÉIS; E SE TÃO BELO É FALAR DESSAS GRANDEZAS AQUI, QUE NÃO SERÁ O GOZAR, A GRAÇA QUE EXISTE ALI?\n\nCORO SIM, EU PORFIAREI POR ESSA TERRA DE ALÉM E LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM; LÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER, ELE É TUDO PRA MIM, E SEM ELE EU NÃO POSSO VIVER.', 'OH! QUE SAUDOSA LEMBRANÇA TENHO DE TI, Ó SIÃO, TERRA QUE EU TANTO AMO POIS ÉS DO MEU CORAÇÃO; EU PARA TI VOAREI QUANDO O SENHOR MEU VOLTAR POIS ELE FOI PARA O CÉU E BREVE VEM ME BUSCAR. CORO SIM, EU PORFIAREI POR ESSA TERRA DE ALÉM E LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM; LÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER, ELE É TUDO PRA MIM, E SEM ELE EU NÃO POSSO VIVER. BELA, MUI BELA, É A ESPERANÇA DOS QUE VIGIAM POR TI, POIS ELES RECEBEM FORÇAS QUE SÓ SE ENCONTRAM ALI; OS QUE PROCURAM CHEGAR AO TEU REGAÇO, Ó SIÃO, LIVRES SERÃO DE PECAR E DE TODA A TENTAÇÃO. DIZ A SAGRADA ESCRITURA QUE SÃO FORMOSOS OS PÉS DAQUELES QUE BOAS NOVAS LEVAM PARA OS INFIÉIS; E SE TÃO BELO É FALAR DESSAS GRANDEZAS AQUI, QUE NÃO SERÁ O GOZAR, A GRAÇA QUE EXISTE ALI?',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('531', '531 - JERUSALÉM, JERUSALÉM', 'JERUSALÉM, JERUSALÉM, DO POVO SANTO DO SENHOR, COM FÉ UM DIA LÁ ESTAREI RENDENDO GLÓRIA AO MEU SALVADOR.\n\nCORO ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, COM OS ANJOS CANTAREI TODO O LOUVOR; ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, JERUSALÉM DO POVO DO SENHOR.\n\nJERUSALÉM, CIDADE SANTA, ILUMINADA COM A GLÓRIA DE DEUS, EM SUAS RUAS EU ANDAREI COM O CORDEIRO E OS REMIDOS SEUS.\n\nCORO ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, COM OS ANJOS CANTAREI TODO O LOUVOR; ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, JERUSALÉM DO POVO DO SENHOR.', 'JERUSALÉM, JERUSALÉM, DO POVO SANTO DO SENHOR, COM FÉ UM DIA LÁ ESTAREI RENDENDO GLÓRIA AO MEU SALVADOR. CORO ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, COM OS ANJOS CANTAREI TODO O LOUVOR; ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, JERUSALÉM DO POVO DO SENHOR. JERUSALÉM, CIDADE SANTA, ILUMINADA COM A GLÓRIA DE DEUS, EM SUAS RUAS EU ANDAREI COM O CORDEIRO E OS REMIDOS SEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('532', '532 - QUANDO O MUNDO AQUI DEIXAR', 'QUANDO O MUNDO AQUI DEIXAR TRISTEZAS NÃO TEREI, POIS COMO OS ANJOS SEREI, NÃO HÁ DOR, ANGÚSTIAS NEM AFLIÇÕES, NOS BRAÇOS DO SALVADOR DESCANSAREI LÁ NO CÉU.\n\nCORO JERUSALÉM ANSEIO ENCONTRAR, (JERUSALÉM ANSEIO ENCONTRAR) RUAS DE OURO EM MEU NOVO LAR, (RUAS DE OURO EM MEU NOVO LAR) OH! QUE PRAZER, (OH! QUE PRAZER) VEREI MEU REI, (VEREI MEU REI) PRA SEMPRE ESTAR COM JESUS.\n\nRIO DE ÁGUA VIVA, CLARO COMO CRISTAL, NO LINDO CÉU GOZAREI, NÃO HÁ DOR, ANGÚSTIAS NEM AFLIÇÕES, NOS BRAÇOS DO SALVADOR DESCANSAREI LÁ NO CÉU.\n\nCORO (2X) JERUSALÉM ANSEIO ENCONTRAR, (JERUSALÉM ANSEIO ENCONTRAR) RUAS DE OURO EM MEU NOVO LAR, (RUAS DE OURO EM MEU NOVO LAR) OH! QUE PRAZER, (OH! QUE PRAZER) VEREI MEU REI, (VEREI MEU REI) PRA SEMPRE ESTAR COM JESUS PRA SEMPRE ESTAR COM JESUS (2X)', 'QUANDO O MUNDO AQUI DEIXAR TRISTEZAS NÃO TEREI, POIS COMO OS ANJOS SEREI, NÃO HÁ DOR, ANGÚSTIAS NEM AFLIÇÕES, NOS BRAÇOS DO SALVADOR DESCANSAREI LÁ NO CÉU. CORO JERUSALÉM ANSEIO ENCONTRAR, (JERUSALÉM ANSEIO ENCONTRAR) RUAS DE OURO EM MEU NOVO LAR, (RUAS DE OURO EM MEU NOVO LAR) OH! QUE PRAZER, (OH! QUE PRAZER) VEREI MEU REI, (VEREI MEU REI) PRA SEMPRE ESTAR COM JESUS. RIO DE ÁGUA VIVA, CLARO COMO CRISTAL, NO LINDO CÉU GOZAREI, NÃO HÁ DOR, ANGÚSTIAS NEM AFLIÇÕES, NOS BRAÇOS DO SALVADOR DESCANSAREI LÁ NO CÉU. CORO  JERUSALÉM ANSEIO ENCONTRAR, (JERUSALÉM ANSEIO ENCONTRAR) RUAS DE OURO EM MEU NOVO LAR, (RUAS DE OURO EM MEU NOVO LAR) OH! QUE PRAZER, (OH! QUE PRAZER) VEREI MEU REI, (VEREI MEU REI) PRA SEMPRE ESTAR COM JESUS PRA SEMPRE ESTAR COM JESUS',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('533', '533 - MINHA ALMA, POIS, ALEGRE CANTA', 'MINHA ALMA, POIS, ALEGRE CANTA: VOU PRA JERUSALÉM; É UMA CIDADE SANTA, NOVA JERUSALÉM.\n\nCORO SE VOCÊ QUISER A VIDA E CAMINHAR TAMBÉM, SEGURA NA MÃO DE CRISTO E VAI PRA NOVA JERUSALÉM. INSTRUMENTOS LÁ NÃO HÁ PRANTO, TRISTEZAS E NEM DOR, SÓ HÁ A PAZ DE CRISTO, ALEGRIA E MUITO AMOR.\nBIS', 'MINHA ALMA, POIS, ALEGRE CANTA: VOU PRA JERUSALÉM; É UMA CIDADE SANTA, NOVA JERUSALÉM. CORO SE VOCÊ QUISER A VIDA E CAMINHAR TAMBÉM, SEGURA NA MÃO DE CRISTO E VAI PRA NOVA JERUSALÉM. INSTRUMENTOS LÁ NÃO HÁ PRANTO, TRISTEZAS E NEM DOR, SÓ HÁ A PAZ DE CRISTO, ALEGRIA E MUITO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('534', '534 - BREVE VOLTARÁ O MESSIAS', 'BREVE VOLTARÁ O MESSIAS, O UNGIDO DE DEUS, O REI, O SENHOR, O QUE É DIGNO DE TODO O LOUVOR.\n\nO SOL PERDERÁ O SEU BRILHO E A LUA NÃO DARÁ MAIS A SUA LUZ QUANDO O FILHO DO HOMEM VOLTAR. TODOS OS POVOS DA TERRA O VERÃO VINDO SOBRE AS NUVENS DO CÉU, COM GLÓRIA, COM GLÓRIA, COM GLÓRIA JESUS VOLTARÁ!\nBIS', 'BREVE VOLTARÁ O MESSIAS, O UNGIDO DE DEUS, O REI, O SENHOR, O QUE É DIGNO DE TODO O LOUVOR. O SOL PERDERÁ O SEU BRILHO E A LUA NÃO DARÁ MAIS A SUA LUZ QUANDO O FILHO DO HOMEM VOLTAR. TODOS OS POVOS DA TERRA O VERÃO VINDO SOBRE AS NUVENS DO CÉU, COM GLÓRIA, COM GLÓRIA, COM GLÓRIA JESUS VOLTARÁ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('535', '535 - ALÉM DO CÉU AZUL', 'ALÉM DO CÉU AZUL FOI JESUS PREPARAR UM LAR PRA DAR A QUEM A VITÓRIA ALCANÇAR; ANELO CONSEGUIR A VIDA NO PORVIR, COM FÉ NO MEU SENHOR JESUS.\n\nBEM SEI QUE EU, DE MIM, NADA TENHO PRA DAR, MAS SEI QUE MEU JESUS JÁ ME VEIO SALVAR; AGORA QUERO EU TER FÉ NO CORAÇÃO, ATÉ SEU ROSTO VER ALÉM.', 'ALÉM DO CÉU AZUL FOI JESUS PREPARAR UM LAR PRA DAR A QUEM A VITÓRIA ALCANÇAR; ANELO CONSEGUIR A VIDA NO PORVIR, COM FÉ NO MEU SENHOR JESUS. BEM SEI QUE EU, DE MIM, NADA TENHO PRA DAR, MAS SEI QUE MEU JESUS JÁ ME VEIO SALVAR; AGORA QUERO EU TER FÉ NO CORAÇÃO, ATÉ SEU ROSTO VER ALÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('536', '536 - FINDA A LIDA TERREAL', 'FINDA A LIDA TERREAL, QUANDO JÁ DO RIO ALÉM, NESSA VIDA GLORIOSA ME ENCONTRAR; SEI QUE LÁ MEU REDENTOR SORRIDENTE HEI DE VER, ENTRE A TURBA, O PRIMEIRO A ME CHAMAR.\n\nCORO HEI DE VER MEU REDENTOR, REDIMIDO AO SEU LADO HEI DE ESTAR; HEI DE VER MEU SALVADOR, EM GLORIOSA E SANTA LUZ CELESTIAL.\n\nOH! DA ALMA MEU ENLEVO É SEU ROSTO CONTEMPLAR NESSA AURORA DO DIA ETERNAL; COMO ENTÃO MEU CORAÇÃO O NÃO HÁ DE ALI LOUVAR PELA GRAÇA E FAVOR CELESTIAL?\n\nCORO HEI DE VER MEU REDENTOR, REDIMIDO AO SEU LADO HEI DE ESTAR; HEI DE VER MEU SALVADOR, EM GLORIOSA E SANTA LUZ CELESTIAL.\n\nNESSA PÁTRIA RESPLENDENTE HEI DE AMIGOS ENCONTRAR, SIM, AMIGOS MAIS PREZADOS HEI DE TER; MAS PRIMEIRO QUE TUDO, QUANDO EU ALI CHEGAR, MEU JESUS É QUEM EU MAIS ANSEIO VER.\n\nCORO HEI DE VER MEU REDENTOR, REDIMIDO AO SEU LADO HEI DE ESTAR; HEI DE VER MEU SALVADOR, EM GLORIOSA E SANTA LUZ CELESTIAL.\n\nPELAS PORTAS DA CIDADE, EM VESTES A ALVEJAR, ALI, ONDE NOITE E PRANTO NÃO ESTARÃO; ENTRE CANTO ANGELICAL HÁ MEUS PASSOS DE GUIAR, PERTO, SIM, MUI PERTO O HEI DE VER, ENTÃO.\n\nCORO HEI DE VER MEU REDENTOR, REDIMIDO AO SEU LADO HEI DE ESTAR; HEI DE VER MEU SALVADOR, EM GLORIOSA E SANTA LUZ CELESTIAL.', 'FINDA A LIDA TERREAL, QUANDO JÁ DO RIO ALÉM, NESSA VIDA GLORIOSA ME ENCONTRAR; SEI QUE LÁ MEU REDENTOR SORRIDENTE HEI DE VER, ENTRE A TURBA, O PRIMEIRO A ME CHAMAR. CORO HEI DE VER MEU REDENTOR, REDIMIDO AO SEU LADO HEI DE ESTAR; HEI DE VER MEU SALVADOR, EM GLORIOSA E SANTA LUZ CELESTIAL. OH! DA ALMA MEU ENLEVO É SEU ROSTO CONTEMPLAR NESSA AURORA DO DIA ETERNAL; COMO ENTÃO MEU CORAÇÃO O NÃO HÁ DE ALI LOUVAR PELA GRAÇA E FAVOR CELESTIAL? NESSA PÁTRIA RESPLENDENTE HEI DE AMIGOS ENCONTRAR, SIM, AMIGOS MAIS PREZADOS HEI DE TER; MAS PRIMEIRO QUE TUDO, QUANDO EU ALI CHEGAR, MEU JESUS É QUEM EU MAIS ANSEIO VER. PELAS PORTAS DA CIDADE, EM VESTES A ALVEJAR, ALI, ONDE NOITE E PRANTO NÃO ESTARÃO; ENTRE CANTO ANGELICAL HÁ MEUS PASSOS DE GUIAR, PERTO, SIM, MUI PERTO O HEI DE VER, ENTÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('537', '537 - QUANDO O JORDÃO PASSARMOS UNIDOS', 'QUANDO O JORDÃO PASSARMOS UNIDOS E ENTRARMOS NO CÉU, VEREMOS LÁ, COMO A AREIA DA PRAIA, OS REMIDOS, OH! QUE GLORIOSA VISTA SERÁ!\n\nCORO TANTOS COMO A AREIA DA PRAIA! TANTOS COMO A AREIA DO MAR! QUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ, SIM,TANTOS COMO A AREIA DA PRAIA!\n\nQUANDO VIRMOS OS SALVOS DO MUNDO QUE A MORTE JAMAIS ALCANÇARÁ, SE SAUDAREM COM GOZO PROFUNDO, OH! QUE GLORIOSA VISTA SERÁ!\n\nCORO TANTOS COMO A AREIA DA PRAIA! TANTOS COMO A AREIA DO MAR! QUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ, SIM,TANTOS COMO A AREIA DA PRAIA!\n\nLÁ NA MARGEM DO RIO DA VIDA ONDE PAZ E JUSTIÇA HAVERÁ, NÓS VEREMOS A TERRA PROMETIDA, OH! QUE GLORIOSA VISTA SERÁ!\n\nCORO TANTOS COMO A AREIA DA PRAIA! TANTOS COMO A AREIA DO MAR! QUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ, SIM,TANTOS COMO A AREIA DA PRAIA!\n\nQUANDO CRISTO JESUS CONTEMPLARMOS COROADO NO CÉU, COMO ESTÁ, E PROSTRADOS AOS SEUS PÉS ADORARMOS OH! QUE GLORIOSA VISTA SERÁ!\n\nCORO TANTOS COMO A AREIA DA PRAIA! TANTOS COMO A AREIA DO MAR! QUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ, SIM,TANTOS COMO A AREIA DA PRAIA!', 'QUANDO O JORDÃO PASSARMOS UNIDOS E ENTRARMOS NO CÉU, VEREMOS LÁ, COMO A AREIA DA PRAIA, OS REMIDOS, OH! QUE GLORIOSA VISTA SERÁ! CORO TANTOS COMO A AREIA DA PRAIA! TANTOS COMO A AREIA DO MAR! QUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ, SIM,TANTOS COMO A AREIA DA PRAIA! QUANDO VIRMOS OS SALVOS DO MUNDO QUE A MORTE JAMAIS ALCANÇARÁ, SE SAUDAREM COM GOZO PROFUNDO, OH! QUE GLORIOSA VISTA SERÁ! LÁ NA MARGEM DO RIO DA VIDA ONDE PAZ E JUSTIÇA HAVERÁ, NÓS VEREMOS A TERRA PROMETIDA, OH! QUE GLORIOSA VISTA SERÁ! QUANDO CRISTO JESUS CONTEMPLARMOS COROADO NO CÉU, COMO ESTÁ, E PROSTRADOS AOS SEUS PÉS ADORARMOS OH! QUE GLORIOSA VISTA SERÁ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('538', '538 - HÁ UM LUGAR PRA MIM', 'HÁ UM LUGAR PRA MIM CHEIO DE AMOR, COM O MEU JESUS MORAR, COM OS ANJOS HABITAR.\n\nCORO LÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) SÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA. NESSE LUGAR PRA MIM, DE ETERNA REDENÇÃO, ENCONTRO PAZ SEM FIM EM MEU CORAÇÃO.\n\nCORO (2X) LÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) SÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA. FINAL: ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA.', 'HÁ UM LUGAR PRA MIM CHEIO DE AMOR, COM O MEU JESUS MORAR, COM OS ANJOS HABITAR. CORO LÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) SÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA. NESSE LUGAR PRA MIM, DE ETERNA REDENÇÃO, ENCONTRO PAZ SEM FIM EM MEU CORAÇÃO. CORO  LÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) SÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA.  ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('539', '539 - JUNTO AO TRONO DE DEUS, PREPARADO', 'JUNTO AO TRONO DE DEUS PREPARADO HÁ, CRISTÃO, UM LUGAR PARA TI; HÁ ALEGRIAS, HÁ GOZO EXALTADO, HÁ DELÍCIAS PROFUSAS ALI; SIM, ALI, SIM, ALI, DE SEUS ANJOS FIÉIS RODEADO, NUMA ESFERA DE GLÓRIA E DE LUZ, JUNTO A DEUS NOS ESPERA JESUS.\n\nOS ENCANTOS DA TERRA NÃO PODEM DAR IDÉIA DO GOZO DALI, SE NA TERRA OS PRAZERES ACODEM, SÃO PRAZERES QUE FINDAM AQUI; MAS DALI, MAS DALI, AS VENTURAS ETERNAS CONCORREM COM O BRILHO PERPÉTUO DA LUZ, A TORNAR-TE FELIZ COM JESUS.\n\nCONSERVEMOS EM NOSSA LEMBRANÇA AS RIQUEZAS DO LINDO PAÍS, E GUARDEMOS CONOSCO A ESPERANÇA DUMA VIDA MELHOR, MAIS FELIZ; POIS DALI, POIS DALI, UMA VOZ VERDADEIRA NÃO CANSA DE FALAR-NOS DO REINO DA LUZ E DO AMOR PROTETOR DE JESUS.\n\nSE QUISERMOS GOZAR DA VENTURA QUE NO BELO PAÍS HAVERÁ, É SOMENTE PEDIR DE ALMA PURA E ESSA GRAÇA JESUS NOS DARÁ; POIS DALI, POIS DALI, TODO CHEIO DE AMOR, DE TERNURA, DESSE AMOR QUE MOSTROU LÁ NA CRUZ, NOS ATENDE, NOS OUVE, JESUS.', 'JUNTO AO TRONO DE DEUS PREPARADO HÁ, CRISTÃO, UM LUGAR PARA TI; HÁ ALEGRIAS, HÁ GOZO EXALTADO, HÁ DELÍCIAS PROFUSAS ALI; SIM, ALI, SIM, ALI, DE SEUS ANJOS FIÉIS RODEADO, NUMA ESFERA DE GLÓRIA E DE LUZ, JUNTO A DEUS NOS ESPERA JESUS. OS ENCANTOS DA TERRA NÃO PODEM DAR IDÉIA DO GOZO DALI, SE NA TERRA OS PRAZERES ACODEM, SÃO PRAZERES QUE FINDAM AQUI; MAS DALI, MAS DALI, AS VENTURAS ETERNAS CONCORREM COM O BRILHO PERPÉTUO DA LUZ, A TORNAR-TE FELIZ COM JESUS. CONSERVEMOS EM NOSSA LEMBRANÇA AS RIQUEZAS DO LINDO PAÍS, E GUARDEMOS CONOSCO A ESPERANÇA DUMA VIDA MELHOR, MAIS FELIZ; POIS DALI, POIS DALI, UMA VOZ VERDADEIRA NÃO CANSA DE FALAR-NOS DO REINO DA LUZ E DO AMOR PROTETOR DE JESUS. SE QUISERMOS GOZAR DA VENTURA QUE NO BELO PAÍS HAVERÁ, É SOMENTE PEDIR DE ALMA PURA E ESSA GRAÇA JESUS NOS DARÁ; POIS DALI, POIS DALI, TODO CHEIO DE AMOR, DE TERNURA, DESSE AMOR QUE MOSTROU LÁ NA CRUZ, NOS ATENDE, NOS OUVE, JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('540', '540 - OH! PENSAI NESSE LAR LÁ DO CÉU', 'OH! PENSAI NESSE LAR LÁ DO CÉU, BEM AO LADO DO RIO DE LUZ, ONDE OS SANTOS PRA SEMPRE JÁ GOZAM DA PRESENÇA DO NOSSO JESUS.\n\nCORO (2X) LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! OH! PENSAI NOS AMIGOS DO CÉU QUE A JORNADA JÁ TÊM ACABADO E NOS CANTOS QUE SOAM NOS ARES, NO PALÁCIO POR DEUS PREPARADO.\n\nCORO (2X) LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! HEI DE VER LÁ NO CÉU MEU JESUS, FACE A FACE SEU ROSTO MIRAR; LONGE, LONGE, CUIDADOS TRISTEZAS! COM JESUS VOU PRA SEMPRE MORAR.\n\nCORO (2X) LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! CEDO, CEDO NO CÉU LÁ ESTAREI, VEJO O FIM DA JORNADA CHEGAR; MEU JESUS ALI ESTÁ ME ESPERANDO, É MELHOR ESTAR ALI QUE AQUI ESTAR.\n\nCORO (2X) LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU!', 'OH! PENSAI NESSE LAR LÁ DO CÉU, BEM AO LADO DO RIO DE LUZ, ONDE OS SANTOS PRA SEMPRE JÁ GOZAM DA PRESENÇA DO NOSSO JESUS. CORO  LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! OH! PENSAI NOS AMIGOS DO CÉU QUE A JORNADA JÁ TÊM ACABADO E NOS CANTOS QUE SOAM NOS ARES, NO PALÁCIO POR DEUS PREPARADO. CORO  LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! HEI DE VER LÁ NO CÉU MEU JESUS, FACE A FACE SEU ROSTO MIRAR; LONGE, LONGE, CUIDADOS TRISTEZAS! COM JESUS VOU PRA SEMPRE MORAR. CORO  LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! CEDO, CEDO NO CÉU LÁ ESTAREI, VEJO O FIM DA JORNADA CHEGAR; MEU JESUS ALI ESTÁ ME ESPERANDO, É MELHOR ESTAR ALI QUE AQUI ESTAR. CORO  LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('541', '541 - ARREBATADO FUI NO DIA DO SENHOR', 'ARREBATADO FUI NO DIA DO SENHOR, OUVI UMA GRANDE VOZ, A VOZ DO MEU JESUS; E AOS SEUS PÉS CAÍ, AOS PÉS DO SALVADOR, AQUELE QUE POR MIM MORREU NA CRUZ.\n\nCORO E VI UM NOVO CÉU, UMA NOVA TERRA VI, UMA GRANDE VOZ NO CÉU SE FEZ ENTÃO OUVIR; NÃO HAVERÁ MAIS DOR, NEM PRANTO E NEM CLAMOR, SÓ HAVERÁ DE DEUS UM GRANDE AMOR.\n\nONIPOTENTE DEUS, A QUEM VENCER, DARÁ TUDO O QUE É SEU, NO CÉU, TUDO HERDARÁ DE DEUS; “SEREI TEU ETERNO PAI, SERÁS MEU FILHO, ENTÃO, POIS QUEM VENCER ALCANÇARÁ PERDÃO”.\n\nCORO E VI UM NOVO CÉU, UMA NOVA TERRA VI, UMA GRANDE VOZ NO CÉU SE FEZ ENTÃO OUVIR; NÃO HAVERÁ MAIS DOR, NEM PRANTO E NEM CLAMOR, SÓ HAVERÁ DE DEUS UM GRANDE AMOR.', 'ARREBATADO FUI NO DIA DO SENHOR, OUVI UMA GRANDE VOZ, A VOZ DO MEU JESUS; E AOS SEUS PÉS CAÍ, AOS PÉS DO SALVADOR, AQUELE QUE POR MIM MORREU NA CRUZ. CORO E VI UM NOVO CÉU, UMA NOVA TERRA VI, UMA GRANDE VOZ NO CÉU SE FEZ ENTÃO OUVIR; NÃO HAVERÁ MAIS DOR, NEM PRANTO E NEM CLAMOR, SÓ HAVERÁ DE DEUS UM GRANDE AMOR. ONIPOTENTE DEUS, A QUEM VENCER, DARÁ TUDO O QUE É SEU, NO CÉU, TUDO HERDARÁ DE DEUS; SEREI TEU ETERNO PAI, SERÁS MEU FILHO, ENTÃO, POIS QUEM VENCER ALCANÇARÁ PERDÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('542', '542 - AO LADO DE JESUS QUERO ESTAR', 'AO LADO DE JESUS EU QUERO ESTAR E O SEU AMOR AO MUNDO ANUNCIAR; EM GRAÇA, ATINGIR A GLÓRIA DO PORVIR; AO LADO DE JESUS EU QUERO ESTAR.\n\nCOROA MUI BRILHANTE EU HEI DE TER E A GLÓRIA DA CIDADE EU HEI DE VER, E LÁ HEI DE CANTAR UM HINO TRIUNFAL; AO LADO DE JESUS EU QUERO ESTAR.\n\nCANSADO, MUITAS VEZES, DE LUTAR E DE UMA CRUZ PESADA CARREGAR, UM DIA A DEIXAREI, COROA GANHAREI; AO LADO DE JESUS EU QUERO ESTAR.\n\nO ROSTO DE JESUS VOU CONTEMPLAR, PRA SEMPRE LÁ NO CÉU IREI MORAR, COM ANJOS, QUERUBINS, TAMBÉM OS SERAFINS; AO LADO DE JESUS EU QUERO ESTAR.\n\nCOM CRISTO EU ESTAREI LÁ NA MANSÃO, A ELE EU ENTREGUEI MEU CORAÇÃO, ELE É MEU SALVADOR, O ETERNO REDENTOR; AO LADO DE JESUS EU QUERO ESTAR.', 'AO LADO DE JESUS EU QUERO ESTAR E O SEU AMOR AO MUNDO ANUNCIAR; EM GRAÇA, ATINGIR A GLÓRIA DO PORVIR; AO LADO DE JESUS EU QUERO ESTAR. COROA MUI BRILHANTE EU HEI DE TER E A GLÓRIA DA CIDADE EU HEI DE VER, E LÁ HEI DE CANTAR UM HINO TRIUNFAL; AO LADO DE JESUS EU QUERO ESTAR. CANSADO, MUITAS VEZES, DE LUTAR E DE UMA CRUZ PESADA CARREGAR, UM DIA A DEIXAREI, COROA GANHAREI; AO LADO DE JESUS EU QUERO ESTAR. O ROSTO DE JESUS VOU CONTEMPLAR, PRA SEMPRE LÁ NO CÉU IREI MORAR, COM ANJOS, QUERUBINS, TAMBÉM OS SERAFINS; AO LADO DE JESUS EU QUERO ESTAR. COM CRISTO EU ESTAREI LÁ NA MANSÃO, A ELE EU ENTREGUEI MEU CORAÇÃO, ELE É MEU SALVADOR, O ETERNO REDENTOR; AO LADO DE JESUS EU QUERO ESTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('543', '543 - AO LADO DE DEUS', 'AO LADO DE DEUS QUERO ESTAR, NAS MANSÕES QUE ELE MESMO PREPAROU, VER, FACE A FACE, O MEU SENHOR JESUS, REI DOS REIS; NAS RUAS DE OURO SEI QUE ANDAREI, NO TOQUE DA TROMBETA SUBIREI.\n\nCORO (2X) OUVIREI (OUVIREI) JESUS CHAMAR (PELO MEU NOME) E COM OS ANJOS (CANTAREI) VIVA O REI (VIVA O REI) JESUS! INSTRUMENTOS\n\nNA ETERNIDADE EU VOU MORAR, PARA SEMPRE COM OS ANJOS VOU CANTAR; COM QUERUBINS E SERAFINS A JESUS ADORAREI; NAS RUAS DE OURO SEI QUE ANDAREI, NO TOQUE DA TROMBETA SUBIREI.\n\nCORO (2X) OUVIREI (OUVIREI) JESUS CHAMAR (PELO MEU NOME) E COM OS ANJOS (CANTAREI) VIVA O REI (VIVA O REI) JESUS! INSTRUMENTOS\n\nFINAL: VIVA O REI JESUS! INSTRUMENTOS JESUS!', 'AO LADO DE DEUS QUERO ESTAR, NAS MANSÕES QUE ELE MESMO PREPAROU, VER, FACE A FACE, O MEU SENHOR JESUS, REI DOS REIS; NAS RUAS DE OURO SEI QUE ANDAREI, NO TOQUE DA TROMBETA SUBIREI. CORO  OUVIREI (OUVIREI) JESUS CHAMAR (PELO MEU NOME) E COM OS ANJOS (CANTAREI) VIVA O REI (VIVA O REI) JESUS! INSTRUMENTOS NA ETERNIDADE EU VOU MORAR, PARA SEMPRE COM OS ANJOS VOU CANTAR; COM QUERUBINS E SERAFINS A JESUS ADORAREI; NAS RUAS DE OURO SEI QUE ANDAREI, NO TOQUE DA TROMBETA SUBIREI. VIVA O REI JESUS! INSTRUMENTOS JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('544', '544 - EM BREVE O DIA CHEGARÁ', 'EM BREVE O DIA CHEGARÁ, JESUS NAS NUVENS VIRÁ E NOS LEVARÁ PARA UM NOVO LAR, A NOVA JERUSALÉM.\n\nCORO E NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDE DO TRONO DO SENHOR E PARA SEMPRE LOUVAREMOS O CORDEIRO QUE VENCEU, O ALFA E O ÔMEGA, O NOSSO REI. INSTRUMENTOS\n\nTODA DOR CESSARÁ, O DIA E NOITE NÃO HAVERÁ PORQUE O SENHOR SERÁ A NOSSA LUZ E PARA SEMPRE REINAREMOS COM JESUS.\n\nCORO (2X) E NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDE DO TRONO DO SENHOR E PARA SEMPRE LOUVAREMOS O CORDEIRO QUE VENCEU, O ALFA E O ÔMEGA, O NOSSO REI.', 'EM BREVE O DIA CHEGARÁ, JESUS NAS NUVENS VIRÁ E NOS LEVARÁ PARA UM NOVO LAR, A NOVA JERUSALÉM. CORO E NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDE DO TRONO DO SENHOR E PARA SEMPRE LOUVAREMOS O CORDEIRO QUE VENCEU, O ALFA E O ÔMEGA, O NOSSO REI. INSTRUMENTOS TODA DOR CESSARÁ, O DIA E NOITE NÃO HAVERÁ PORQUE O SENHOR SERÁ A NOSSA LUZ E PARA SEMPRE REINAREMOS COM JESUS. CORO  E NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDE DO TRONO DO SENHOR E PARA SEMPRE LOUVAREMOS O CORDEIRO QUE VENCEU, O ALFA E O ÔMEGA, O NOSSO REI.',  1);
