INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('548', '548 - NAS LINDAS MORADAS', 'NAS LINDAS MORADAS EM BREVE ESTAREI LOUVANDO PRA SEMPRE A JESUS, MEU GRANDE REI; NAS RUAS DE OURO SEI QUE VOU ANDAR E JUNTO COM OS ANJOS LOUVAR.\n\nCORO A JESUS, O REI, O LEÃO DA TRIBO DE JUDÁ, QUE VENCEU E O LIVRO ABRIRÁ.\n\nO SEU ROSTO LINDO NO CÉU HEI DE VER, NO SEU MEIGO OLHAR SENTIREI QUANTO ME AMOU; SUAS MARAVILHAS IREI CONTEMPLAR E JUNTO COM OS ANJOS LOUVAR.\n\nCORO (2X) A JESUS, O REI, O LEÃO DA TRIBO DE JUDÁ, QUE VENCEU E O LIVRO ABRIRÁ. FINAL: E O LIVRO ABRIRÁ, E O LIVRO ABRIRÁ.', 'NAS LINDAS MORADAS EM BREVE ESTAREI LOUVANDO PRA SEMPRE A JESUS, MEU GRANDE REI; NAS RUAS DE OURO SEI QUE VOU ANDAR E JUNTO COM OS ANJOS LOUVAR. CORO A JESUS, O REI, O LEÃO DA TRIBO DE JUDÁ, QUE VENCEU E O LIVRO ABRIRÁ. O SEU ROSTO LINDO NO CÉU HEI DE VER, NO SEU MEIGO OLHAR SENTIREI QUANTO ME AMOU; SUAS MARAVILHAS IREI CONTEMPLAR E JUNTO COM OS ANJOS LOUVAR. CORO  A JESUS, O REI, O LEÃO DA TRIBO DE JUDÁ, QUE VENCEU E O LIVRO ABRIRÁ.  E O LIVRO ABRIRÁ, E O LIVRO ABRIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('549', '549 - MARAVILHOSO SERÁ', 'MARAVILHOSO SERÁ QUANDO JESUS VOLTAR, MARAVILHOSO SERÁ. INSTRUMENTOS\n\nOH! QUE ALEGRIA SERÁ QUANDO JESUS VOLTAR, ANJOS LOUVANDO SEU NOME, OS CÉUS SE ABRINDO EM ESPLENDOR.\n\nCORO (2X) MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ. TODA LUTA CESSARÁ, TODO PRANTO, TODA DOR, A IGREJA SUBIRÁ LOUVANDO AO SENHOR.\n\nCORO (2X) MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ. ORA VEM, ORA VEM, SENHOR JESUS, TUA IGREJA TE ESPERA, TUA IGREJA PROCLAMA: MARANATA! ORA VEM, SENHOR JESUS.\n\nCORO (2X) MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ. FINAL: MARANATA! ORA VEM, (MARANATA! ORA VEM) MARANATA! ORA VEM, (MARANATA! ORA VEM) VEM, JESUS!', 'MARAVILHOSO SERÁ QUANDO JESUS VOLTAR, MARAVILHOSO SERÁ. INSTRUMENTOS OH! QUE ALEGRIA SERÁ QUANDO JESUS VOLTAR, ANJOS LOUVANDO SEU NOME, OS CÉUS SE ABRINDO EM ESPLENDOR. CORO  MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ. TODA LUTA CESSARÁ, TODO PRANTO, TODA DOR, A IGREJA SUBIRÁ LOUVANDO AO SENHOR. CORO  MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ. ORA VEM, ORA VEM, SENHOR JESUS, TUA IGREJA TE ESPERA, TUA IGREJA PROCLAMA: MARANATA! ORA VEM, SENHOR JESUS. CORO  MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ.  MARANATA! ORA VEM, (MARANATA! ORA VEM) MARANATA! ORA VEM, (MARANATA! ORA VEM) VEM, JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('550', '550 - CANTAI, Ó IGREJA', 'CANTAI, Ó IGREJA (CANTAI, Ó IGREJA), O DIA CHEGARÁ, O NOIVO ENTRE AS NUVENS A TI RECEBERÁ.\n\nCORO (2X) ALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS), ALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA CANTARÁ EM SIÃO. MESMO EM LUTAS (MESMO EM LUTAS) QUE PROVAM NOSSO AMOR, A IGREJA, TRIUNFANTE, ESPERA NO SENHOR.\n\nCORO (2X) ALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS), ALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA CANTARÁ EM SIÃO. FINAL: ALEGRAI-VOS.', 'CANTAI, Ó IGREJA (CANTAI, Ó IGREJA), O DIA CHEGARÁ, O NOIVO ENTRE AS NUVENS A TI RECEBERÁ. CORO  ALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS), ALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA CANTARÁ EM SIÃO. MESMO EM LUTAS (MESMO EM LUTAS) QUE PROVAM NOSSO AMOR, A IGREJA, TRIUNFANTE, ESPERA NO SENHOR. CORO  ALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS), ALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA CANTARÁ EM SIÃO.  ALEGRAI-VOS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('551', '551 - TENHO LIDO DA BELA CIDADE (A BELA CIDADE)', 'TENHO LIDO DA BELA CIDADE, CONSTRUÍDA POR CRISTO NOS CÉUS; É MURADA DE JASPE LUZENTE E JUNCADA COM ÁUREOS TROFÉUS. E NO MEIO DA PRAÇA EIS O RIO DO VIGOR E DA VIDA ETERNAL; MAS METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL.\n\nCORO JAMAIS SE CONTOU AO MORTAL (BIS) METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL.\n\nTENHO LIDO DAS BELAS MORADAS QUE JESUS FOI NO CÉU PREPARAR, ONDE OS CRENTES FIÉIS PARA SEMPRE, MUI FELIZES, IRÃO HABITAR; NEM TRISTEZA, NEM DOR, NEM GEMIDOS ENTRARÃO NA MANSÃO PATERNAL; MAS METADE DO GOZO CELESTE JAMAIS SE CONTOU AO MORTAL.\n\nCORO JAMAIS SE CONTOU AO MORTAL (BIS) METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL.\n\nTENHO LIDO DAS VESTES BRILHANTES, DAS COROAS QUE OS SANTOS TERÃO QUANDO O PAI OS CHAMAR E DISSER-LHES: “RECEBEI O ETERNAL GALARDÃO”. TENHO LIDO QUE OS SANTOS NA GLÓRIA PISARÃO RUAS DE OURO E CRISTAL; MAS METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL.\n\nCORO JAMAIS SE CONTOU AO MORTAL (BIS) METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL.', 'TENHO LIDO DA BELA CIDADE, CONSTRUÍDA POR CRISTO NOS CÉUS; É MURADA DE JASPE LUZENTE E JUNCADA COM ÁUREOS TROFÉUS. E NO MEIO DA PRAÇA EIS O RIO DO VIGOR E DA VIDA ETERNAL; MAS METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL. CORO JAMAIS SE CONTOU AO MORTAL  METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL. TENHO LIDO DAS BELAS MORADAS QUE JESUS FOI NO CÉU PREPARAR, ONDE OS CRENTES FIÉIS PARA SEMPRE, MUI FELIZES, IRÃO HABITAR; NEM TRISTEZA, NEM DOR, NEM GEMIDOS ENTRARÃO NA MANSÃO PATERNAL; MAS METADE DO GOZO CELESTE JAMAIS SE CONTOU AO MORTAL. TENHO LIDO DAS VESTES BRILHANTES, DAS COROAS QUE OS SANTOS TERÃO QUANDO O PAI OS CHAMAR E DISSER-LHES: RECEBEI O ETERNAL GALARDÃO. TENHO LIDO QUE OS SANTOS NA GLÓRIA PISARÃO RUAS DE OURO E CRISTAL; MAS METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('552', '552 - PÁTRIA MINHA', 'PÁTRIA MINHA, POR TI SUSPIRO QUANDO NO TEU BOM DESCANSO CHEGAREI? OS PATRIARCAS, DE DEUS AMIGOS, E OS BONS PROFETAS, FIÉIS, ANTIGOS, JÁ ENTRARAM NA TUA GLÓRIA, CONTEMPLANDO, EM ESPLENDOR, O GRANDE REI.\n\nOS REMIDOS, TÃO PERSEGUIDOS, PELO SANGUE JÁ VENCERAM O DRAGÃO; POR JESUS CRISTO SÃO VENCEDORES, E AGORA CANTAM OS SEUS LOUVORES; PÁTRIA SANTA, DESEJO VER-TE, VER COM CRISTO A REDIMIDA MULTIDÃO.\n\nLÁ O RIO DAS ÁGUAS VIVAS SAI DO TRONO DO CORDEIRO E DO SENHOR; É LUMINOSO DESDE A NASCENTE, COMO CRISTAL É RESPLANDECENTE; PELA MARGEM DAQUELE RIO ANDAM OS REMIDOS COM O SALVADOR. INSTRUMENTOS\n\nNÃO HÁ PRANTO NA MINHA PÁTRIA, NELA NUNCA SE DARÁ SEPARAÇÃO. ALI O TRONO DE DEUS DESCANSA, ALI TEREMOS REAL BONANÇA; OS REMIDOS DA MINHA PÁTRIA COM JESUS ETERNAMENTE REINARÃO.', 'PÁTRIA MINHA, POR TI SUSPIRO QUANDO NO TEU BOM DESCANSO CHEGAREI? OS PATRIARCAS, DE DEUS AMIGOS, E OS BONS PROFETAS, FIÉIS, ANTIGOS, JÁ ENTRARAM NA TUA GLÓRIA, CONTEMPLANDO, EM ESPLENDOR, O GRANDE REI. OS REMIDOS, TÃO PERSEGUIDOS, PELO SANGUE JÁ VENCERAM O DRAGÃO; POR JESUS CRISTO SÃO VENCEDORES, E AGORA CANTAM OS SEUS LOUVORES; PÁTRIA SANTA, DESEJO VER-TE, VER COM CRISTO A REDIMIDA MULTIDÃO. LÁ O RIO DAS ÁGUAS VIVAS SAI DO TRONO DO CORDEIRO E DO SENHOR; É LUMINOSO DESDE A NASCENTE, COMO CRISTAL É RESPLANDECENTE; PELA MARGEM DAQUELE RIO ANDAM OS REMIDOS COM O SALVADOR. INSTRUMENTOS NÃO HÁ PRANTO NA MINHA PÁTRIA, NELA NUNCA SE DARÁ SEPARAÇÃO. ALI O TRONO DE DEUS DESCANSA, ALI TEREMOS REAL BONANÇA; OS REMIDOS DA MINHA PÁTRIA COM JESUS ETERNAMENTE REINARÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('553', '553 - VEM! MARANATA! JESUS VEM!', 'VEM! MARANATA! JESUS VEM! ESTE DIA CHEGARÁ, SUA IGREJA SUBIRÁ. VAMOS MORAR NA CIDADE ETERNAL, NA NOVA JERUSALÉM, ONDE AS RUAS SÃO DE OURO.\n\nLÁ VAMOS CANTAR, VAMOS LOUVAR A JESUS CRISTO. LÁ VAMOS CANTAR, VAMOS LOUVAR (SÓ ELE É DIGNO) JUNTOS COM OS ANJOS NO CÉU.\n\nVEM! MARANATA! JESUS VEM! ESTE DIA CHEGARÁ, SUA IGREJA SUBIRÁ. VAMOS MORAR NA CIDADE ETERNAL, NA NOVA JERUSALÉM, ONDE AS RUAS SÃO DE OURO.\n\nLÁ VAMOS CANTAR, VAMOS LOUVAR A JESUS CRISTO. LÁ VAMOS CANTAR, VAMOS LOUVAR (SÓ ELE É DIGNO) JUNTOS COM OS ANJOS NO CÉU. FINAL: TODOS LOUVANDO A JESUS, NO CÉU, NO CÉU.', 'VEM! MARANATA! JESUS VEM! ESTE DIA CHEGARÁ, SUA IGREJA SUBIRÁ. VAMOS MORAR NA CIDADE ETERNAL, NA NOVA JERUSALÉM, ONDE AS RUAS SÃO DE OURO. LÁ VAMOS CANTAR, VAMOS LOUVAR A JESUS CRISTO. LÁ VAMOS CANTAR, VAMOS LOUVAR (SÓ ELE É DIGNO) JUNTOS COM OS ANJOS NO CÉU. LÁ VAMOS CANTAR, VAMOS LOUVAR A JESUS CRISTO. LÁ VAMOS CANTAR, VAMOS LOUVAR (SÓ ELE É DIGNO) JUNTOS COM OS ANJOS NO CÉU.  TODOS LOUVANDO A JESUS, NO CÉU, NO CÉU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('554', '554 - JERUSALÉM (SUAS RUAS DE OURO)', 'JERUSALÉM, JERUSALÉM, SUAS RUAS DE OURO, MEU GRANDE BEM. JERUSALÉM, JERUSALÉM, CIDADE TÃO LINDA, IGUAL NÃO TEM. INSTRUMENTOS\n\nDAS LINDAS MORADAS, Ó PÁTRIA QUERIDA, DO PERFUME DAS FLORES, ESSÊNCIA DA VIDA, A MINH’ALMA ESPERA, ESPERA POR TI, Ó JERUSALÉM!\n\nJERUSALÉM, JERUSALÉM, SUAS RUAS DE OURO, MEU GRANDE BEM; JERUSALÉM, JERUSALÉM, CIDADE TÃO LINDA, IGUAL NÃO TEM. INSTRUMENTOS\n\nDAS LINDAS MORADAS, Ó PÁTRIA QUERIDA, DO PERFUME DAS FLORES, ESSÊNCIA DA VIDA, A MINH’ALMA ESPERA, ESPERA POR TI, Ó JERUSALÉM!\n\nJERUSALÉM (JERUSALÉM) JERUSALÉM (JERUSALÉM) SUAS RUAS DE OURO, MEU GRANDE BEM; JERUSALÉM (JERUSALÉM) JERUSALÉM (JERUSALÉM) CIDADE TÃO LINDA, IGUAL NÃO TEM.', 'JERUSALÉM, JERUSALÉM, SUAS RUAS DE OURO, MEU GRANDE BEM. JERUSALÉM, JERUSALÉM, CIDADE TÃO LINDA, IGUAL NÃO TEM. INSTRUMENTOS DAS LINDAS MORADAS, Ó PÁTRIA QUERIDA, DO PERFUME DAS FLORES, ESSÊNCIA DA VIDA, A MINH’ALMA ESPERA, ESPERA POR TI, Ó JERUSALÉM! JERUSALÉM, JERUSALÉM, SUAS RUAS DE OURO, MEU GRANDE BEM; JERUSALÉM, JERUSALÉM, CIDADE TÃO LINDA, IGUAL NÃO TEM. INSTRUMENTOS JERUSALÉM (JERUSALÉM) JERUSALÉM (JERUSALÉM) SUAS RUAS DE OURO, MEU GRANDE BEM; JERUSALÉM (JERUSALÉM) JERUSALÉM (JERUSALÉM) CIDADE TÃO LINDA, IGUAL NÃO TEM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('555', '555 - DA LINDA PÁTRIA', 'DA LINDA PÁTRIA ESTOU MUI LONGE, TRISTE EU ESTOU; EU TENHO DE JESUS SAUDADE; QUANDO SERÁ QUE VOU? PASSARINHOS, BELAS FLORES, QUEREM ME ENCANTAR, OH! VÃOS TERRESTRES ESPLENDORES, NÃO QUERO AQUI FICAR!\n\nJESUS ME DEU FIEL PROMESSA, VEM ME BUSCAR; MEU CORAÇÃO ESTÁ COM PRESSA, EU QUERO AO CÉU VOAR. MEUS PECADOS SÃO MUI GRANDES E CULPADO SOU; MAS O SEU SANGUE PÕE-ME LIMPO E PARA A PÁTRIA VOU.\n\nQUAL FILHO DO SEU LAR SAUDOSO, EU QUERO IR; QUAL PASSARINHO PARA O NINHO, EU QUERO AO CÉU SUBIR. SUA VINDA AO MUNDO É CERTA, QUANDO NÃO O SEI, MAS ELE ME ACHARÁ ALERTA E PARA O CÉU IREI.', 'DA LINDA PÁTRIA ESTOU MUI LONGE, TRISTE EU ESTOU; EU TENHO DE JESUS SAUDADE; QUANDO SERÁ QUE VOU? PASSARINHOS, BELAS FLORES, QUEREM ME ENCANTAR, OH! VÃOS TERRESTRES ESPLENDORES, NÃO QUERO AQUI FICAR! JESUS ME DEU FIEL PROMESSA, VEM ME BUSCAR; MEU CORAÇÃO ESTÁ COM PRESSA, EU QUERO AO CÉU VOAR. MEUS PECADOS SÃO MUI GRANDES E CULPADO SOU; MAS O SEU SANGUE PÕE-ME LIMPO E PARA A PÁTRIA VOU. QUAL FILHO DO SEU LAR SAUDOSO, EU QUERO IR; QUAL PASSARINHO PARA O NINHO, EU QUERO AO CÉU SUBIR. SUA VINDA AO MUNDO É CERTA, QUANDO NÃO O SEI, MAS ELE ME ACHARÁ ALERTA E PARA O CÉU IREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('556', '556 - NA NOVA JERUSALÉM IREI MORAR', 'NA NOVA JERUSALÉM IREI MORAR, ONDE MORA O MEU JESUS, LÁ ESTAREI, NA CIDADE ETERNAL IREI GOZAR, NAS RUAS DE OURO SEI QUE ANDAREI.\n\nCORO LÁ CANTAREI LOUVORES A JESUS, LÁ LOUVAREI COM OS ANJOS AO MEU REI NA CIDADE DE LUZ EM BREVE ESTAREI. INSTRUMENTOS\n\nAQUI NÃO FICAREI, NÃO É MEU LUGAR, PRA SEMPRE COM JESUS IREI MORAR, COM OS ANJOS, QUERUBINS, TAMBÉM SERAFINS A FACE DE JESUS VOU CONTEMPLAR.\n\nCORO (2X) LÁ CANTAREI LOUVORES A JESUS, LÁ LOUVAREI COM OS ANJOS AO MEU REI, NA CIDADE DE LUZ EM BREVE ESTAREI. FINAL: NA CIDADE DE LUZ EM BREVE ESTAREI.', 'NA NOVA JERUSALÉM IREI MORAR, ONDE MORA O MEU JESUS, LÁ ESTAREI, NA CIDADE ETERNAL IREI GOZAR, NAS RUAS DE OURO SEI QUE ANDAREI. CORO LÁ CANTAREI LOUVORES A JESUS, LÁ LOUVAREI COM OS ANJOS AO MEU REI NA CIDADE DE LUZ EM BREVE ESTAREI. INSTRUMENTOS AQUI NÃO FICAREI, NÃO É MEU LUGAR, PRA SEMPRE COM JESUS IREI MORAR, COM OS ANJOS, QUERUBINS, TAMBÉM SERAFINS A FACE DE JESUS VOU CONTEMPLAR. CORO  LÁ CANTAREI LOUVORES A JESUS, LÁ LOUVAREI COM OS ANJOS AO MEU REI, NA CIDADE DE LUZ EM BREVE ESTAREI.  NA CIDADE DE LUZ EM BREVE ESTAREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('557', '557 - ESTE MUNDO JAMAIS PODE ME SEPARAR', 'ESTE MUNDO JAMAIS PODE ME SEPARAR DOS VALORES CELESTIAIS QUE EU VOU RECEBER; MEU TESOURO E ESPERANÇA ESTÃO NO MEU NOVO LAR, SOU HERDEIRO COM CRISTO, VOU COM ELE MORAR.\n\nCORO CÉU, LINDO CÉU! CÉU, LINDO CÉU! HÁ MANSÕES CELESTIAIS, TODAS FEITAS POR DEUS! CÉU, LINDO CÉU! CÉU, LINDO CÉU! EU VOU PRO CÉU, LINDO CÉU, COM CRISTO VOU MORAR NO LINDO CÉU!\n\nJÁ ESTANDO ALÉM RIO, MINHA LUTA FINDOU, EU NÃO TEMO MAIS PECADO POIS JESUS ME LAVOU; OH! QUE GLÓRIA NA SUBIDA PRAS ALTURAS DOS CÉUS, CANTAREI A LINDA HISTÓRIA DO CORDEIRO DE DEUS!\n\nCORO CÉU, LINDO CÉU! CÉU, LINDO CÉU! HÁ MANSÕES CELESTIAIS, TODAS FEITAS POR DEUS! CÉU, LINDO CÉU! CÉU, LINDO CÉU! EU VOU PRO CÉU, LINDO CÉU, COM CRISTO VOU MORAR NO LINDO CÉU!', 'ESTE MUNDO JAMAIS PODE ME SEPARAR DOS VALORES CELESTIAIS QUE EU VOU RECEBER; MEU TESOURO E ESPERANÇA ESTÃO NO MEU NOVO LAR, SOU HERDEIRO COM CRISTO, VOU COM ELE MORAR. CORO CÉU, LINDO CÉU! CÉU, LINDO CÉU! HÁ MANSÕES CELESTIAIS, TODAS FEITAS POR DEUS! CÉU, LINDO CÉU! CÉU, LINDO CÉU! EU VOU PRO CÉU, LINDO CÉU, COM CRISTO VOU MORAR NO LINDO CÉU! JÁ ESTANDO ALÉM RIO, MINHA LUTA FINDOU, EU NÃO TEMO MAIS PECADO POIS JESUS ME LAVOU; OH! QUE GLÓRIA NA SUBIDA PRAS ALTURAS DOS CÉUS, CANTAREI A LINDA HISTÓRIA DO CORDEIRO DE DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('558', '558 - VEM, JESUS CRISTO JÁ VEM', 'VEM, JESUS CRISTO JÁ VEM, EM BREVE ELE VEM, VEM NOS BUSCAR, QUE DIA GLORIOSO SERÁ QUANDO OS SALVOS SUBIREM PRA COM ELE ESTAR.\n\nEM BREVE JESUS CRISTO VEM, IREMOS COM ELE SUBIR, VEREMOS NOSSO SENHOR.\n\nVARÕES\n\nEM BREVE JESUS CRISTO VEM, (DE GLÓRIA) IREMOS COM ELE SUBIR, (EM GLÓRIA) VEREMOS NOSSO REDENTOR. INSTRUMENTOS\n\nVEM, JESUS CRISTO JÁ VEM, EM BREVE ELE VEM, VEM NOS BUSCAR, QUE DIA GLORIOSO SERÁ QUANDO OS SALVOS SUBIREM PRA COM ELE ESTAR.\n\nEM BREVE JESUS CRISTO VEM, IREMOS COM ELE SUBIR, VEREMOS NOSSO SENHOR.\n\nVARÕES\n\nEM BREVE JESUS CRISTO VEM, (DE GLÓRIA) IREMOS COM ELE SUBIR, (EM GLÓRIA) VEREMOS NOSSO REDENTOR. INSTRUMENTOS\n\nVEM, (SIM, JESUS VEM) VEM, (SIM, JESUS VEM) VEM, SIM, JESUS VEM.', 'VEM, JESUS CRISTO JÁ VEM, EM BREVE ELE VEM, VEM NOS BUSCAR, QUE DIA GLORIOSO SERÁ QUANDO OS SALVOS SUBIREM PRA COM ELE ESTAR. EM BREVE JESUS CRISTO VEM, IREMOS COM ELE SUBIR, VEREMOS NOSSO SENHOR. EM BREVE JESUS CRISTO VEM, (DE GLÓRIA) IREMOS COM ELE SUBIR, (EM GLÓRIA) VEREMOS NOSSO REDENTOR. INSTRUMENTOS VEM, (SIM, JESUS VEM) VEM, (SIM, JESUS VEM) VEM, SIM, JESUS VEM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('559', '559 - A ÚLTIMA TROMBETA SOARÁ', 'A ÚLTIMA TROMBETA SOARÁ, JESUS ENTÃO VIRÁ; QUÃO TERRÍVEL DIA SERÁ, MAS ASSIM APROUVE A DEUS REALIZAR.\n\nAMADOS, PREPARAI VOSSOS CAMINHOS, CONVERTEI-VOS A DEUS, O ÚNICO SALVADOR E DOMINADOR, PARA QUE NÃO SOFRAIS NO GRANDE DIA DO SENHOR\n\nCORO OUVI, Ó CÉUS, OUVI, Ó MORADORES DA TERRA O QUE O ESPÍRITO SANTO DIZ: “JESUS, O DESEJADO DAS NAÇÕES VEM BUSCAR SUA IGREJA FIEL”. INSTRUMENTOS\n\n“EU, JESUS, ENVIEI O MEU ANJO PARA VOS TESTIFICAR ESTAS COISAS NAS IGREJAS, EU SOU A RAIZ E A GERAÇÃO DE DAVI, A RESPLANDECENTE ESTRELA DA MANHÃ”. O ESPÍRITO E A ESPOSA DIZEM: VEM! E QUEM OUVE DIGA: VEM! QUEM TEM SEDE VENHA E QUEM QUISER BEBA DE GRAÇA DA ÁGUA DA VIDA.\n\nCORO (2X) OUVI, Ó CÉUS, OUVI, Ó MORADORES DA TERRA O QUE O ESPÍRITO SANTO DIZ: “JESUS, O DESEJADO DAS NAÇÕES VEM BUSCAR SUA IGREJA FIEL”. FINAL: VEM BUSCAR SUA IGREJA FIEL.', 'A ÚLTIMA TROMBETA SOARÁ, JESUS ENTÃO VIRÁ; QUÃO TERRÍVEL DIA SERÁ, MAS ASSIM APROUVE A DEUS REALIZAR. AMADOS, PREPARAI VOSSOS CAMINHOS, CONVERTEI-VOS A DEUS, O ÚNICO SALVADOR E DOMINADOR, PARA QUE NÃO SOFRAIS NO GRANDE DIA DO SENHOR CORO OUVI, Ó CÉUS, OUVI, Ó MORADORES DA TERRA O QUE O ESPÍRITO SANTO DIZ: JESUS, O DESEJADO DAS NAÇÕES VEM BUSCAR SUA IGREJA FIEL. INSTRUMENTOS EU, JESUS, ENVIEI O MEU ANJO PARA VOS TESTIFICAR ESTAS COISAS NAS IGREJAS, EU SOU A RAIZ E A GERAÇÃO DE DAVI, A RESPLANDECENTE ESTRELA DA MANHÃ. O ESPÍRITO E A ESPOSA DIZEM: VEM! E QUEM OUVE DIGA: VEM! QUEM TEM SEDE VENHA E QUEM QUISER BEBA DE GRAÇA DA ÁGUA DA VIDA. CORO  OUVI, Ó CÉUS, OUVI, Ó MORADORES DA TERRA O QUE O ESPÍRITO SANTO DIZ: JESUS, O DESEJADO DAS NAÇÕES VEM BUSCAR SUA IGREJA FIEL.  VEM BUSCAR SUA IGREJA FIEL.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('560', '560 - EIS QUE ESTOU A PORTA E BATO', '“EIS QUE ESTOU À PORTA E BATO, SE ABRIRES, ENTRAREI E CONTIGO VIVEREI, ABRE A PORTA DO TEU CORAÇÃO. O MEU NOME É JESUS, O AUTOR DA PRÓPRIA VIDA, SOU O TEU LIBERTADOR, O CORDEIRO QUE RESSUSCITOU.\n\nEIS QUE VENHO SOBRE AS NUVENS, EIS QUE VENHO VENCEDOR, NADA MAIS ME DETERÁ, O MEU POVO VOU ARREBATAR, A TROMBETA BREVE SOARÁ; TUDO NOVO SE FARÁ NO MEU REINO DE JUSTIÇA E PAZ”. MARANATA, JESUS VEM! INSTRUMENTOS\n\n“ESTE DIA ESTÁ CHEGANDO, A FIGUEIRA JÁ BROTOU E A IGREJA FIEL ORA E VIGIA SEM CESSAR. O MEU NOME É JESUS, SOU A FONTE DE ÁGUA VIVA, QUEM TEM SEDE VEM A MIM, VEM BEBER DE GRAÇA DESTA ÁGUA.\n\nEIS QUE VENHO SOBRE AS NUVENS, EIS QUE VENHO VENCEDOR, NADA MAIS ME DETERÁ, O MEU POVO VOU ARREBATAR, A TROMBETA BREVE SOARÁ; TUDO NOVO SE FARÁ NO MEU REINO DE JUSTIÇA E PAZ”. MARANATA, JESUS VEM!\nBIS\n\nFINAL: (MARANATA, JESUS) MARANATA, JESUS VEM!', 'EIS QUE ESTOU À PORTA E BATO, SE ABRIRES, ENTRAREI E CONTIGO VIVEREI, ABRE A PORTA DO TEU CORAÇÃO. O MEU NOME É JESUS, O AUTOR DA PRÓPRIA VIDA, SOU O TEU LIBERTADOR, O CORDEIRO QUE RESSUSCITOU. EIS QUE VENHO SOBRE AS NUVENS, EIS QUE VENHO VENCEDOR, NADA MAIS ME DETERÁ, O MEU POVO VOU ARREBATAR, A TROMBETA BREVE SOARÁ; TUDO NOVO SE FARÁ NO MEU REINO DE JUSTIÇA E PAZ. MARANATA, JESUS VEM! INSTRUMENTOS ESTE DIA ESTÁ CHEGANDO, A FIGUEIRA JÁ BROTOU E A IGREJA FIEL ORA E VIGIA SEM CESSAR. O MEU NOME É JESUS, SOU A FONTE DE ÁGUA VIVA, QUEM TEM SEDE VEM A MIM, VEM BEBER DE GRAÇA DESTA ÁGUA. EIS QUE VENHO SOBRE AS NUVENS, EIS QUE VENHO VENCEDOR, NADA MAIS ME DETERÁ, O MEU POVO VOU ARREBATAR, A TROMBETA BREVE SOARÁ; TUDO NOVO SE FARÁ NO MEU REINO DE JUSTIÇA E PAZ. MARANATA, JESUS VEM! (MARANATA, JESUS) MARANATA, JESUS VEM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('561', '561 - OUVI COMO QUE A VOZ DE UMA GRANDE MULTIDÃO', 'OUVI COMO QUE A VOZ DE UMA GRANDE MULTIDÃO, COMO VOZ DE MUITAS ÁGUAS, COMO VOZ DE GRANDES TROVÕES; QUE DIZIA: ALELUIA! O SENHOR TODO PODEROSO REINA, REINA, REINA!\n\nREGOZIJEMO-NOS, ALEGREMO-NOS, DEMOS-LHE GLÓRIA! PORQUE VINDAS SÃO AS BODAS DO CORDEIRO E SUA ESPOSA SE PREPAROU. INSTRUMENTOS\n\nOUVI COMO QUE A VOZ DE UMA GRANDE MULTIDÃO, COMO VOZ DE MUITAS ÁGUAS, COMO VOZ DE GRANDES TROVÕES; QUE DIZIA: ALELUIA! O SENHOR TODO PODEROSO REINA, REINA, REINA! FINAL: AMÉM.', 'OUVI COMO QUE A VOZ DE UMA GRANDE MULTIDÃO, COMO VOZ DE MUITAS ÁGUAS, COMO VOZ DE GRANDES TROVÕES; QUE DIZIA: ALELUIA! O SENHOR TODO PODEROSO REINA, REINA, REINA! REGOZIJEMO-NOS, ALEGREMO-NOS, DEMOS-LHE GLÓRIA! PORQUE VINDAS SÃO AS BODAS DO CORDEIRO E SUA ESPOSA SE PREPAROU. INSTRUMENTOS OUVI COMO QUE A VOZ DE UMA GRANDE MULTIDÃO, COMO VOZ DE MUITAS ÁGUAS, COMO VOZ DE GRANDES TROVÕES; QUE DIZIA: ALELUIA! O SENHOR TODO PODEROSO REINA, REINA, REINA!  AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('562', '562 - QUANDO MEU TEMPO DE LUTA PASSAR', 'QUANDO MEU TEMPO DE LUTA PASSAR, QUANDO MEU DEUS PARA SI ME CHAMAR, GRATO, PERANTE JESUS HEI DE ESTAR; GLÓRIA PERENE SERÁ PARA MIM!\n\nCORO SIM, HÁ DE SER GLÓRIA PRA MIM, GLÓRIA PRA MIM, GLÓRIA PRA MIM; QUANDO PUDER O SEU ROSTO MIRAR, OH! HÁ DE SER GRANDE GLÓRIA PRA MIM!\n\nQUANDO POR GRAÇA DO SEU GRANDE AMOR EU ALCANÇAR O INFINITO FAVOR, DE IR PARA PERTO DO MEU SALVADOR, GLÓRIA PERENE SERÁ PARA MIM!\n\nCORO SIM, HÁ DE SER GLÓRIA PRA MIM, GLÓRIA PRA MIM, GLÓRIA PRA MIM; QUANDO PUDER O SEU ROSTO MIRAR, OH! HÁ DE SER GRANDE GLÓRIA PRA MIM!\n\nMUITOS AMIGOS ALI HEI DE ACHAR, PAZ, A ALEGRIA ETERNAL, BEM ESTAR; MAS QUANDO MEU SALVADOR ME SAUDAR, GLÓRIA PERENE SERÁ PARA MIM!\n\nCORO SIM, HÁ DE SER GLÓRIA PRA MIM, GLÓRIA PRA MIM, GLÓRIA PRA MIM; QUANDO PUDER O SEU ROSTO MIRAR, OH! HÁ DE SER GRANDE GLÓRIA PRA MIM!', 'QUANDO MEU TEMPO DE LUTA PASSAR, QUANDO MEU DEUS PARA SI ME CHAMAR, GRATO, PERANTE JESUS HEI DE ESTAR; GLÓRIA PERENE SERÁ PARA MIM! CORO SIM, HÁ DE SER GLÓRIA PRA MIM, GLÓRIA PRA MIM, GLÓRIA PRA MIM; QUANDO PUDER O SEU ROSTO MIRAR, OH! HÁ DE SER GRANDE GLÓRIA PRA MIM! QUANDO POR GRAÇA DO SEU GRANDE AMOR EU ALCANÇAR O INFINITO FAVOR, DE IR PARA PERTO DO MEU SALVADOR, GLÓRIA PERENE SERÁ PARA MIM! MUITOS AMIGOS ALI HEI DE ACHAR, PAZ, A ALEGRIA ETERNAL, BEM ESTAR; MAS QUANDO MEU SALVADOR ME SAUDAR, GLÓRIA PERENE SERÁ PARA MIM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('563', '563 - NÃO SE TURBEM OS VOSSOS CORAÇÕES', 'NÃO SE TURBEM OS VOSSOS CORAÇÕES, BREVE SE CUMPRIRÁ A PROMESSA DE JESUS; TODO PRANTO CESSARÁ E OS SANTOS SUBIRÃO LEVADOS PELOS ANJOS AO SENHOR.\n\nCORO ERGUEI VOSSAS CABEÇAS E CONTEMPLAI OS ANJOS LOUVANDO A DEUS, O PAI; O DIA DA VITÓRIA BREVE VAI CHEGAR, REGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR.\n\nANTES MESMO QUE NASCESSEIS VOS ESCOLHI E VOS CONSAGREI, PARA ANUNCIARDES AS MINHAS PALAVRAS PORQUE VOLTAREI E SOMENTE OS FIÉIS LEVAREI.\n\nCORO ERGUEI VOSSAS CABEÇAS E CONTEMPLAI OS ANJOS LOUVANDO A DEUS, O PAI; O DIA DA VITÓRIA BREVE VAI CHEGAR, REGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR. FINAL: REGOZIJAI-VOS, Ó ESCOLHIDOS DO SENHOR.', 'NÃO SE TURBEM OS VOSSOS CORAÇÕES, BREVE SE CUMPRIRÁ A PROMESSA DE JESUS; TODO PRANTO CESSARÁ E OS SANTOS SUBIRÃO LEVADOS PELOS ANJOS AO SENHOR. CORO ERGUEI VOSSAS CABEÇAS E CONTEMPLAI OS ANJOS LOUVANDO A DEUS, O PAI; O DIA DA VITÓRIA BREVE VAI CHEGAR, REGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR. ANTES MESMO QUE NASCESSEIS VOS ESCOLHI E VOS CONSAGREI, PARA ANUNCIARDES AS MINHAS PALAVRAS PORQUE VOLTAREI E SOMENTE OS FIÉIS LEVAREI. CORO ERGUEI VOSSAS CABEÇAS E CONTEMPLAI OS ANJOS LOUVANDO A DEUS, O PAI; O DIA DA VITÓRIA BREVE VAI CHEGAR, REGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR.  REGOZIJAI-VOS, Ó ESCOLHIDOS DO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('564', '564 - DISPÕE-TE, RESPLANDECE', 'DISPÕE-TE, RESPLANDECE, PORQUE VEM A TUA LUZ E A GLÓRIA DO SENHOR JESUS NASCE SOBRE TI.\n\nPORQUE EIS QUE AS TREVAS COBREM A TERRA E A ESCURIDÃO OS POVOS MAS SOBRE TI APARECE RESPLENDENTE O SENHOR JESUS E A SUA GLÓRIA SE VÊ SOBRE TI, NOVA JERUSALÉM.\n\nAS NAÇÕES SE ENCAMINHAM PARA A TUA LUZ, E OS REIS PARA O RESPLENDOR QUE TE NASCEU.\n\nLEVANTA EM REDOR OS TEUS OLHOS E VÊ, TODOS ESTES SE AJUNTAM E VÊM TER CONTIGO, TEUS FILHOS CHEGAM DE LONGE E TUAS FILHAS SÃO TRAZIDAS NOS BRAÇOS, NOVA JERUSALÉM.\n\nENTÃO O VERÁS E SERÁS RADIANTE DE ALEGRIA, O TEU CORAÇÃO ESTREMECERÁ E SE DILATARÁ DE JÚBILO. PORQUE A ABUNDÂNCIA DO MAR SE TORNARÁ A TI E AS RIQUEZAS DAS NAÇÕES VIRÃO A TER CONTIGO, NOVA JERUSALÉM, NOVA JERUSALÉM.', 'DISPÕE-TE, RESPLANDECE, PORQUE VEM A TUA LUZ E A GLÓRIA DO SENHOR JESUS NASCE SOBRE TI. PORQUE EIS QUE AS TREVAS COBREM A TERRA E A ESCURIDÃO OS POVOS MAS SOBRE TI APARECE RESPLENDENTE O SENHOR JESUS E A SUA GLÓRIA SE VÊ SOBRE TI, NOVA JERUSALÉM. AS NAÇÕES SE ENCAMINHAM PARA A TUA LUZ, E OS REIS PARA O RESPLENDOR QUE TE NASCEU. LEVANTA EM REDOR OS TEUS OLHOS E VÊ, TODOS ESTES SE AJUNTAM E VÊM TER CONTIGO, TEUS FILHOS CHEGAM DE LONGE E TUAS FILHAS SÃO TRAZIDAS NOS BRAÇOS, NOVA JERUSALÉM. ENTÃO O VERÁS E SERÁS RADIANTE DE ALEGRIA, O TEU CORAÇÃO ESTREMECERÁ E SE DILATARÁ DE JÚBILO. PORQUE A ABUNDÂNCIA DO MAR SE TORNARÁ A TI E AS RIQUEZAS DAS NAÇÕES VIRÃO A TER CONTIGO, NOVA JERUSALÉM, NOVA JERUSALÉM.',  1);
