INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('566', '566 - O SENHOR É O MEU PASTOR (MARANATA, JESUS VEM)', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, JESUS CRISTO É O MEU SENHOR, NA SUA GLÓRIA VOU ESTAR. LOUVORES AO SEU NOME, E ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ), NA SUA GLÓRIA VOU ESTAR.\n\nO SENHOR É O MEU PASTOR, NADA ME FALTARÁ, JESUS CRISTO É O MEU SENHOR, NA SUA GLÓRIA VOU ESTAR. LOUVORES AO SEU NOME, E ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ), TODA A IGREJA CANTARÁ.\n\nCORO MARANATA, JESUS VEM! MARANATA, VEM BUSCAR! MARANATA, TODOS SEUS, COM O SENHOR VÃO MORAR. INSTRUMENTOS DE LUZ (DE LUZ).\n\nO SENHOR É O MEU PASTOR, NADA ME FALTARÁ, JESUS CRISTO É O MEU SENHOR, NA SUA GLÓRIA VOU ESTAR. LOUVORES AO SEU NOME, E ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ), TODA A IGREJA CANTARÁ.\n\nCORO (2X) MARANATA, JESUS VEM! MARANATA, VEM BUSCAR! MARANATA, TODOS SEUS, COM O SENHOR VÃO MORAR.', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, JESUS CRISTO É O MEU SENHOR, NA SUA GLÓRIA VOU ESTAR. LOUVORES AO SEU NOME, E ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ), NA SUA GLÓRIA VOU ESTAR. O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, JESUS CRISTO É O MEU SENHOR, NA SUA GLÓRIA VOU ESTAR. LOUVORES AO SEU NOME, E ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ), TODA A IGREJA CANTARÁ. CORO MARANATA, JESUS VEM! MARANATA, VEM BUSCAR! MARANATA, TODOS SEUS, COM O SENHOR VÃO MORAR. INSTRUMENTOS DE LUZ (DE LUZ). CORO  MARANATA, JESUS VEM! MARANATA, VEM BUSCAR! MARANATA, TODOS SEUS, COM O SENHOR VÃO MORAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('567', '567 - QUANDO SERÁ O GRANDE DIA', 'QUANDO SERÁ O GRANDE DIA, EM QUE O SENHOR VIRÁ NOS BUSCAR? NINGUÉM SABE O DIA, NINGUÉM SABE A HORA, MAS A TROMBETA ESTÁ PRA SOAR.\n\nEIS QUE OS SINAIS JÁ SE CUMPRIRAM, EM TODA TERRA, RIOS E MARES. A SUA IGREJA OUVE A VOZ DO ESPÍRITO: MARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ!\n\nCORO MARANATA, MARANATA, A IGREJA CLAMA, MARANATA! O SENHOR VEM! O SENHOR VEM! INSTRUMENTOS\n\nDIAS DIFÍCEIS, DIAS DE DORES, DIAS DE LUTAS, ESTÃO SE PASSANDO. A SUA IGREJA OLHA PRO ALTO, MARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ!\n\nCORO MARANATA, MARANATA, A IGREJA CLAMA, MARANATA! O SENHOR VEM! O SENHOR VEM!', 'QUANDO SERÁ O GRANDE DIA, EM QUE O SENHOR VIRÁ NOS BUSCAR? NINGUÉM SABE O DIA, NINGUÉM SABE A HORA, MAS A TROMBETA ESTÁ PRA SOAR. EIS QUE OS SINAIS JÁ SE CUMPRIRAM, EM TODA TERRA, RIOS E MARES. A SUA IGREJA OUVE A VOZ DO ESPÍRITO: MARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ! CORO MARANATA, MARANATA, A IGREJA CLAMA, MARANATA! O SENHOR VEM! O SENHOR VEM! INSTRUMENTOS DIAS DIFÍCEIS, DIAS DE DORES, DIAS DE LUTAS, ESTÃO SE PASSANDO. A SUA IGREJA OLHA PRO ALTO, MARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ! CORO MARANATA, MARANATA, A IGREJA CLAMA, MARANATA! O SENHOR VEM! O SENHOR VEM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('568', '568 - SENHOR, QUEM SÃO AQUELES', 'SENHOR, QUEM SÃO AQUELES, QUE VEM SUBINDO PELO CAMINHO? CANTANDO ALEGREMENTE COMO AS AVES QUE VÃO PARA O NINHO.\n\nCORO SENHOR, QUEM SÃO AQUELES, QUE EM VESTES BRANCAS VÃO PROSSEGUINDO? SENHOR, EU QUERO SEGUI-LOS, TU NÃO ME DEIXES FICAR SOZINHO.\n\nO MUNDO JÁ NÃO ME SERVE, A MINHA ALMA ESTÁ SOLUÇANDO. SENHOR, TU NÃO ME DEIXES, FICAR NO MUNDO SEMPRE SOFRENDO.\n\nCORO SENHOR, QUEM SÃO AQUELES, QUE EM VESTES BRANCAS VÃO PROSSEGUINDO? SENHOR, EU QUERO SEGUI-LOS, TU NÃO ME DEIXES FICAR SOZINHO.\n\nEU QUERO TAMBÉM SER UM DELES, TER FELICIDADE NO CORAÇÃO. SEGUIR AQUELE CAMINHO, SEM SOFRIMENTO E DE SALVAÇÃO.\n\nCORO SENHOR, QUEM SÃO AQUELES, QUE EM VESTES BRANCAS VÃO PROSSEGUINDO? SENHOR, EU QUERO SEGUI-LOS, TU NÃO ME DEIXES FICAR SOZINHO. FINAL: (2X) TU NÃO ME DEIXES FICAR SOZINHO.', 'SENHOR, QUEM SÃO AQUELES, QUE VEM SUBINDO PELO CAMINHO? CANTANDO ALEGREMENTE COMO AS AVES QUE VÃO PARA O NINHO. CORO SENHOR, QUEM SÃO AQUELES, QUE EM VESTES BRANCAS VÃO PROSSEGUINDO? SENHOR, EU QUERO SEGUI-LOS, TU NÃO ME DEIXES FICAR SOZINHO. O MUNDO JÁ NÃO ME SERVE, A MINHA ALMA ESTÁ SOLUÇANDO. SENHOR, TU NÃO ME DEIXES, FICAR NO MUNDO SEMPRE SOFRENDO. EU QUERO TAMBÉM SER UM DELES, TER FELICIDADE NO CORAÇÃO. SEGUIR AQUELE CAMINHO, SEM SOFRIMENTO E DE SALVAÇÃO. CORO SENHOR, QUEM SÃO AQUELES, QUE EM VESTES BRANCAS VÃO PROSSEGUINDO? SENHOR, EU QUERO SEGUI-LOS, TU NÃO ME DEIXES FICAR SOZINHO.   TU NÃO ME DEIXES FICAR SOZINHO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('569', '569 - JESUS VEM, VEM NOS BUSCAR', 'CORO JESUS VEM! VEM NOS BUSCAR! LEVAR-NOS PARA A ETERNIDADE. JESUS VEM! VEM NOS BUSCAR! COM MAJESTADE E PODER.\n\nO SEU PODER É DIVINO, A SUA GRAÇA É SEM PAR; TODOS OS SEUS FIÉIS HÃO DE SUBIR, VAMOS PRA SEMPRE MORAR, NA ETERNIDADE, JUNTOS COM O SALVADOR.\n\nCORO JESUS VEM! VEM NOS BUSCAR! LEVAR-NOS PARA A ETERNIDADE. JESUS VEM! VEM NOS BUSCAR! COM MAJESTADE E PODER.\n\nOS SINAIS ESTÃO SE CUMPRINDO, A SUA PALAVRA É FIEL; PREPARA-TE IGREJA, CINJA OS TEUS LOMBOS! MANTENHA ACESA A CANDEIA! PARA O ENCONTRO COM O NOIVO, NAS BODAS DO CORDEIRO.\n\nCORO JESUS VEM! VEM NOS BUSCAR! LEVAR-NOS PARA A ETERNIDADE. JESUS VEM! VEM NOS BUSCAR! COM MAJESTADE E PODER.\n\nFINAL: JESUS VEM! DA SUA GLÓRIA! JESUS VEM! COM PODER E GLÓRIA! COM MAJESTADE E PODER; VEM, VEM, VEM, JESUS!', 'CORO JESUS VEM! VEM NOS BUSCAR! LEVAR-NOS PARA A ETERNIDADE. JESUS VEM! VEM NOS BUSCAR! COM MAJESTADE E PODER. O SEU PODER É DIVINO, A SUA GRAÇA É SEM PAR; TODOS OS SEUS FIÉIS HÃO DE SUBIR, VAMOS PRA SEMPRE MORAR, NA ETERNIDADE, JUNTOS COM O SALVADOR. OS SINAIS ESTÃO SE CUMPRINDO, A SUA PALAVRA É FIEL; PREPARA-TE IGREJA, CINJA OS TEUS LOMBOS! MANTENHA ACESA A CANDEIA! PARA O ENCONTRO COM O NOIVO, NAS BODAS DO CORDEIRO. JESUS VEM! DA SUA GLÓRIA! JESUS VEM! COM PODER E GLÓRIA! COM MAJESTADE E PODER; VEM, VEM, VEM, JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('570', '570 - JÁ REFULGE A GLÓRIA ETERNA (VENCENDO VEM JESUS)', 'JÁ REFULGE A GLÓRIA ETERNA DE JESUS, O REI DOS REIS. BREVE OS REINOS DESTE MUNDO SEGUIRÃO AS SUAS LEIS! OS SINAIS DA SUA VINDA MAIS SE MOSTRAM CADA VEZ. VENCENDO VEM JESUS!\n\nCORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCENDO VEM JESUS!\n\nO CLARIM QUE CHAMA OS CRENTES À BATALHA, JÁ SOOU. CRISTO, À FRENTE DO SEU POVO, MULTIDÕES JÁ CONQUISTOU. O INIMIGO EM RETIRADA SEU FUROR PATENTEOU. VENCENDO VEM JESUS!\n\nCORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCENDO VEM JESUS!\n\nEIS QUE EM GLÓRIA REFULGENTE SOBRE AS NUVENS DESCERÁ. E AS NAÇÕES E OS REIS DA TERRA COM PODER GOVERNARÁ. SIM, EM PAZ E SANTIDADE TODA A TERRA REGERÁ. VENCENDO VEM JESUS!\n\nCORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCENDO VEM JESUS!\n\nE, POR FIM ENTRONIZADO AS NAÇÕES HÁ DE JULGAR. TODOS, GRANDES E PEQUENOS, O JUIZ, HÃO DE ENCARAR. E OS REMIDOS TRIUNFANTES, EM FULGOR HÃO DE CANTAR. VENCIDO TEM JESUS!\n\nCORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCIDO TEM JESUS!', 'JÁ REFULGE A GLÓRIA ETERNA DE JESUS, O REI DOS REIS. BREVE OS REINOS DESTE MUNDO SEGUIRÃO AS SUAS LEIS! OS SINAIS DA SUA VINDA MAIS SE MOSTRAM CADA VEZ. VENCENDO VEM JESUS! CORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCENDO VEM JESUS! O CLARIM QUE CHAMA OS CRENTES À BATALHA, JÁ SOOU. CRISTO, À FRENTE DO SEU POVO, MULTIDÕES JÁ CONQUISTOU. O INIMIGO EM RETIRADA SEU FUROR PATENTEOU. VENCENDO VEM JESUS! EIS QUE EM GLÓRIA REFULGENTE SOBRE AS NUVENS DESCERÁ. E AS NAÇÕES E OS REIS DA TERRA COM PODER GOVERNARÁ. SIM, EM PAZ E SANTIDADE TODA A TERRA REGERÁ. VENCENDO VEM JESUS! E, POR FIM ENTRONIZADO AS NAÇÕES HÁ DE JULGAR. TODOS, GRANDES E PEQUENOS, O JUIZ, HÃO DE ENCARAR. E OS REMIDOS TRIUNFANTES, EM FULGOR HÃO DE CANTAR. VENCIDO TEM JESUS! CORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCIDO TEM JESUS!',  1);
