INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('572', '572 - CHEGUEMOS AO ALTAR DE DEUS', 'CHEGUEMOS AO ALTAR DE DEUS COM A NOSSA ADORAÇÃO; NA SUA MÃO HÁ FORÇA E PODER. CORO (2X) ADOREMOS, ADOREMOS E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO. INSTRUMENTOS\n\nCHEGUEMOS AO ALTAR DE DEUS COM A NOSSA ADORAÇÃO; TUA É, SENHOR, A HONRA E A MAJESTADE, PORQUE TEU É TUDO QUANTO HÁ NOS CÉUS E NA TERRA; TEU, SENHOR, É O REINO E TU TE EXALTASTE; NA TUA MÃO HÁ FORÇA E PODER. (BIS)\n\nCORO (2X) ADOREMOS, ADOREMOS E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO. INSTRUMENTOS FINAL: E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO.', 'CHEGUEMOS AO ALTAR DE DEUS COM A NOSSA ADORAÇÃO; NA SUA MÃO HÁ FORÇA E PODER. CORO  ADOREMOS, ADOREMOS E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO. INSTRUMENTOS CHEGUEMOS AO ALTAR DE DEUS COM A NOSSA ADORAÇÃO; TUA É, SENHOR, A HONRA E A MAJESTADE, PORQUE TEU É TUDO QUANTO HÁ NOS CÉUS E NA TERRA; TEU, SENHOR, É O REINO E TU TE EXALTASTE; NA TUA MÃO HÁ FORÇA E PODER. CORO  ADOREMOS, ADOREMOS E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO. INSTRUMENTOS  E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('573', '573 - COMO É BOM TE ADORAR', 'COMO É BOM TE ADORAR SENHOR JESUS COMO É BOM ESTAR NA TUA PRESENÇA, CANTAR LOUVORES E BENDIZER DE TODO O MEU CORAÇÃO.\n\nCORO OH! SENHOR, (OH! SENHOR) (VARÕES) GRANDIOSO É O TEU NOME; OH! SENHOR, (VARÕES) (OH! SENHOR) (SERVAS) TEU ESPÍRITO SANTO NOS TEM CONSOLADO.\n\nTENS ABENÇOADO O TEU POVO, TENS ABENÇOADO, Ó DEUS, TENS ALEGRADO OS CORAÇÕES, TENS NOS DADO A LIBERTAÇÃO.\n\nCORO OH! SENHOR, (OH! SENHOR) (VARÕES) GRANDIOSO É O TEU NOME; OH! SENHOR, (VARÕES) (OH! SENHOR) (SERVAS) TEU ESPÍRITO SANTO NOS TEM CONSOLADO.\n\nFINAL: (2X) LOUVAREI AO SENHOR, (LOUVAREI) COM GRANDE ALEGRIA, (LOUVAREI) LOUVAREI AO SENHOR, LOUVAREI AO SENHOR JESUS. LOUVAREI AO SENHOR JESUS.', 'COMO É BOM TE ADORAR SENHOR JESUS COMO É BOM ESTAR NA TUA PRESENÇA, CANTAR LOUVORES E BENDIZER DE TODO O MEU CORAÇÃO. CORO OH! SENHOR, (OH! SENHOR)  GRANDIOSO É O TEU NOME; OH! SENHOR,  (OH! SENHOR)  TEU ESPÍRITO SANTO NOS TEM CONSOLADO. TENS ABENÇOADO O TEU POVO, TENS ABENÇOADO, Ó DEUS, TENS ALEGRADO OS CORAÇÕES, TENS NOS DADO A LIBERTAÇÃO. LOUVAREI AO SENHOR, (LOUVAREI) COM GRANDE ALEGRIA, (LOUVAREI) LOUVAREI AO SENHOR, LOUVAREI AO SENHOR JESUS. LOUVAREI AO SENHOR JESUS.',  1);
