INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('577', '577 - GLÓRIA E ALELUIA', 'GLÓRIA E ALELUIA NÓS TE DAMOS SENHOR BENDITO SEJA O TEU SANTO NOME (VARÕES) GRAÇAS E LOUVORES TE RENDEMOS SENHOR EXALTADA SEJA A TUA SALVAÇÃO. (VARÕES)\n\nPOIS O SENHOR É MINHA ROCHA E MINHA FORÇA E O MEU ALTO REFÚGIO; A ELE CLAMAREI E ELE ME SALVARÁ, É ELE QUEM ME TEM DADO A VITÓRIA.\n\nGLÓRIA E ALELUIA NÓS TE DAMOS SENHOR BENDITO SEJA O TEU SANTO NOME (VARÕES) GRAÇAS E LOUVORES TE RENDEMOS SENHOR EXALTADA SEJA A TUA SALVAÇÃO. (VARÕES)\n\nPOIS O SENHOR É MINHA ROCHA E MINHA FORÇA E O MEU ALTO REFÚGIO; A ELE CLAMAREI E ELE ME SALVARÁ, É ELE QUEM ME TEM DADO A VITÓRIA.\n\nGLÓRIA E ALELUIA NÓS TE DAMOS SENHOR BENDITO SEJA O TEU SANTO NOME (VARÕES) GRAÇAS E LOUVORES TE RENDEMOS SENHOR EXALTADA SEJA A TUA SALVAÇÃO. (VARÕES) FINAL: EXALTADA SEJA A TUA SALVAÇÃO.', 'GLÓRIA E ALELUIA NÓS TE DAMOS SENHOR BENDITO SEJA O TEU SANTO NOME  GRAÇAS E LOUVORES TE RENDEMOS SENHOR EXALTADA SEJA A TUA SALVAÇÃO. POIS O SENHOR É MINHA ROCHA E MINHA FORÇA E O MEU ALTO REFÚGIO; A ELE CLAMAREI E ELE ME SALVARÁ, É ELE QUEM ME TEM DADO A VITÓRIA. GLÓRIA E ALELUIA NÓS TE DAMOS SENHOR BENDITO SEJA O TEU SANTO NOME  GRAÇAS E LOUVORES TE RENDEMOS SENHOR EXALTADA SEJA A TUA SALVAÇÃO.   EXALTADA SEJA A TUA SALVAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('578', '578 - HOJE O SENHOR DERRAMOU', 'HOJE O SENHOR DERRAMOU DO SEU PODER SOBRE NÓS, COMO A CADA MANHÃ E ENTARDECER SENTIMOS SEU AMOR; POR ISSO DAMOS GRAÇAS POR TUDO O QUE TEM FEITO A NÓS, LOUVORES E HONRAS AO GRANDE SALVADOR.\n\nCORO MEU SENHOR, A TI LOUVAMOS POR TUA MÃO QUE NOS SUSTÉM, E APESAR DAS LUTAS E PROVAS SENHOR SABEMOS QUE CONOSCO SEMPRE ESTARÁS.\n\nO QUE TE DAR, MEU SENHOR, POR TANTAS BÊNÇÃOS CELESTIAIS? EM SANTIDADE Ó SENHOR TE OFERECEMOS CORAÇÕES FIÉIS, PURIFICADOS EM JESUS, BUSCANDO A SALVAÇÃO POIS BEM SABEMOS QUE O SANGUE DE CRISTO TRAZ PERDÃO.\n\nCORO (2X) MEU SENHOR, A TI LOUVAMOS POR TUA MÃO QUE NOS SUSTÉM, E APESAR DAS LUTAS E PROVAS SENHOR SABEMOS QUE CONOSCO SEMPRE ESTARÁS. FINAL: CONOSCO SEMPRE ESTARÁS.', 'HOJE O SENHOR DERRAMOU DO SEU PODER SOBRE NÓS, COMO A CADA MANHÃ E ENTARDECER SENTIMOS SEU AMOR; POR ISSO DAMOS GRAÇAS POR TUDO O QUE TEM FEITO A NÓS, LOUVORES E HONRAS AO GRANDE SALVADOR. CORO MEU SENHOR, A TI LOUVAMOS POR TUA MÃO QUE NOS SUSTÉM, E APESAR DAS LUTAS E PROVAS SENHOR SABEMOS QUE CONOSCO SEMPRE ESTARÁS. O QUE TE DAR, MEU SENHOR, POR TANTAS BÊNÇÃOS CELESTIAIS? EM SANTIDADE Ó SENHOR TE OFERECEMOS CORAÇÕES FIÉIS, PURIFICADOS EM JESUS, BUSCANDO A SALVAÇÃO POIS BEM SABEMOS QUE O SANGUE DE CRISTO TRAZ PERDÃO. CORO  MEU SENHOR, A TI LOUVAMOS POR TUA MÃO QUE NOS SUSTÉM, E APESAR DAS LUTAS E PROVAS SENHOR SABEMOS QUE CONOSCO SEMPRE ESTARÁS.  CONOSCO SEMPRE ESTARÁS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('579', '579 - SENHOR, EU NEM SEI CONTAR', 'SENHOR, EU NEM SEI CONTAR AS GRANDES BÊNÇÃOS QUE TENS DADO AO MEU CORAÇÃO, POIS O TEU AMOR ME FAZ TÃO FELIZ E A TUA GRAÇA ME SATISFAZ EM SABER QUE A CADA MANHÃ EU POSSO SENTIR JESUS TÃO PERTO DE MIM.\nBIS\n\nCORO EU QUERO AGRADECER (VARÕES) AO MEU SENHOR, EU QUERO AGRADECER (VARÕES) POR SEU AMOR, QUE ME FAZ SER VENCEDOR E ME FAZ FELIZ QUE ME FAZ SER UM SERVO DO SENHOR. INSTRUMENTOS TÃO PERTO DE MIM...\n\nCORO EU QUERO AGRADECER (VARÕES) AO MEU SENHOR, EU QUERO AGRADECER (VARÕES) POR SEU AMOR, QUE ME FAZ SER VENCEDOR E ME FAZ FELIZ QUE ME FAZ SER UM SERVO DO SENHOR. FINAL: QUE ME FAZ SER UM SERVO DO SENHOR.', 'SENHOR, EU NEM SEI CONTAR AS GRANDES BÊNÇÃOS QUE TENS DADO AO MEU CORAÇÃO, POIS O TEU AMOR ME FAZ TÃO FELIZ E A TUA GRAÇA ME SATISFAZ EM SABER QUE A CADA MANHÃ EU POSSO SENTIR JESUS TÃO PERTO DE MIM. CORO EU QUERO AGRADECER  AO MEU SENHOR, EU QUERO AGRADECER  POR SEU AMOR, QUE ME FAZ SER VENCEDOR E ME FAZ FELIZ QUE ME FAZ SER UM SERVO DO SENHOR. INSTRUMENTOS TÃO PERTO DE MIM... CORO EU QUERO AGRADECER  AO MEU SENHOR, EU QUERO AGRADECER  POR SEU AMOR, QUE ME FAZ SER VENCEDOR E ME FAZ FELIZ QUE ME FAZ SER UM SERVO DO SENHOR.  QUE ME FAZ SER UM SERVO DO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('580', '580 - NOS PROSTEMOS AOS SEUS PÉS', 'NOS PROSTREMOS AOS SEUS PÉS ADORANDO A JESUS, EXALTANDO A SUA GLÓRIA E MAJESTADE.\nBIS\n\nCORO COROAI A JESUS MINISTRANDO O SEU LOUVOR EM SUA CASA, COROAI A JESUS BENDIZENDO E LOUVANDO O SEU NOME. INSTRUMENTOS\n\nENTRONIZADO ESTÁS ENTRE OS LOUVORES, ASSENTADO NO TEU TRONO, E TODOS OS ANJOS DE DEUS TE ADORANDO.\nBIS\n\nCORO (2X) COROAI A JESUS MINISTRANDO O SEU LOUVOR EM SUA CASA, COROAI A JESUS BENDIZENDO E LOUVANDO O SEU NOME. FINAL: (2X) O SEU NOME (VARÕES) BENDIZENDO E LOUVANDO O SEU NOME.', 'NOS PROSTREMOS AOS SEUS PÉS ADORANDO A JESUS, EXALTANDO A SUA GLÓRIA E MAJESTADE. CORO COROAI A JESUS MINISTRANDO O SEU LOUVOR EM SUA CASA, COROAI A JESUS BENDIZENDO E LOUVANDO O SEU NOME. INSTRUMENTOS ENTRONIZADO ESTÁS ENTRE OS LOUVORES, ASSENTADO NO TEU TRONO, E TODOS OS ANJOS DE DEUS TE ADORANDO. CORO  COROAI A JESUS MINISTRANDO O SEU LOUVOR EM SUA CASA, COROAI A JESUS BENDIZENDO E LOUVANDO O SEU NOME.   O SEU NOME  BENDIZENDO E LOUVANDO O SEU NOME.',  1);
