INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('582', '582 - CANTAREI AO AMOR DOS AMORES', 'CANTAREI AO AMOR DOS AMORES, O AMOR, DOCE AMOR, O AMOR DE DEUS. NÃO HÁ MAIS DÚVIDAS EM MIM, TODO O MEU MUNDO MUDOU, AO AMOR DOS AMORES CANTAREI.\nBIS\n\nO INVERNO PASSOU, SE FOI A CHUVA, E COM LÍRIOS A TERRA SE COBRIU. É TEMPO DE NOVA CANÇÃO, A FIGUEIRA REVERDECEU, AO AMOR DOS AMORES CANTAREI.\nBIS\n\nMINHA VIDA HOJE É DIFERENTE, DE ALEGRIA CANTA E BATE O CORAÇÃO. ILUMINOU TODO O MEU SER, QUERO DE NOVO VIVER, AO AMOR DOS AMORES CANTAREI.\nBIS', 'CANTAREI AO AMOR DOS AMORES, O AMOR, DOCE AMOR, O AMOR DE DEUS. NÃO HÁ MAIS DÚVIDAS EM MIM, TODO O MEU MUNDO MUDOU, AO AMOR DOS AMORES CANTAREI. O INVERNO PASSOU, SE FOI A CHUVA, E COM LÍRIOS A TERRA SE COBRIU. É TEMPO DE NOVA CANÇÃO, A FIGUEIRA REVERDECEU, AO AMOR DOS AMORES CANTAREI. MINHA VIDA HOJE É DIFERENTE, DE ALEGRIA CANTA E BATE O CORAÇÃO. ILUMINOU TODO O MEU SER, QUERO DE NOVO VIVER, AO AMOR DOS AMORES CANTAREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('583', '583 - Ó GRANDE DEUS', 'Ó GRANDE DEUS, A TUA GRAÇA ENCHE O MEU CORAÇÃO; TEU É O CÉU E NA TERRA TU ÉS SALVAÇÃO TEU É O MAR E AS FONTES DAS ÁGUAS TAMBÉM, SÓ TU, SENHOR, ÉS A LUZ, ÉS A VIDA TAMBÉM.\n\nCORO NÃO SE ENCONTRA ENTRE OS POVOS DA TERRA TÃO GRANDE AMOR, NEM TAMBÉM NAS ESCARPAS DAS SERRAS TÃO RICO AMOR, NEM NAS NUVENS QUE PASSAM NOS ARES HÁ TANTO AMOR, MAS NO TRONO DA GLÓRIA SE ENCONTRA ESTE SANTO AMOR.\n\nÓ GRANDE DEUS, ME AJUDA NA TRIBULAÇÃO, DÁ-ME PODER PRA PREGAR E FAZER ORAÇÃO, DÁ-ME SABER PARA AQUI NESTA VIDA TRILHAR E COMPREENDER TEU AMOR, TUA GRAÇA SEM PAR.\n\nCORO NÃO SE ENCONTRA ENTRE OS POVOS DA TERRA TÃO GRANDE AMOR, NEM TAMBÉM NAS ESCARPAS DAS SERRAS TÃO RICO AMOR, NEM NAS NUVENS QUE PASSAM NOS ARES HÁ TANTO AMOR, MAS NO TRONO DA GLÓRIA SE ENCONTRA ESTE SANTO AMOR.', 'Ó GRANDE DEUS, A TUA GRAÇA ENCHE O MEU CORAÇÃO; TEU É O CÉU E NA TERRA TU ÉS SALVAÇÃO TEU É O MAR E AS FONTES DAS ÁGUAS TAMBÉM, SÓ TU, SENHOR, ÉS A LUZ, ÉS A VIDA TAMBÉM. CORO NÃO SE ENCONTRA ENTRE OS POVOS DA TERRA TÃO GRANDE AMOR, NEM TAMBÉM NAS ESCARPAS DAS SERRAS TÃO RICO AMOR, NEM NAS NUVENS QUE PASSAM NOS ARES HÁ TANTO AMOR, MAS NO TRONO DA GLÓRIA SE ENCONTRA ESTE SANTO AMOR. Ó GRANDE DEUS, ME AJUDA NA TRIBULAÇÃO, DÁ-ME PODER PRA PREGAR E FAZER ORAÇÃO, DÁ-ME SABER PARA AQUI NESTA VIDA TRILHAR E COMPREENDER TEU AMOR, TUA GRAÇA SEM PAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('584', '584 - EU TE AMO, MEU JESUS', 'EU TE AMO, MEU JESUS, POIS TU ÉS O REMIDOR, RESGATASTE A MINHA ALMA DA TRISTEZA E DA DOR, EU TE AMO, MEU JESUS, COMO É FORTE O MEU AMOR, ALEGRASTE A MINHA ALMA COM TUA PAZ E O TEU AMOR.\n\nCORO EU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM A PORTA DO JARDIM DA VIDA QUE É SEM FIM; A MINH’ALMA CANTA AGORA O AMOR QUE VIVE EM MIM, POIS MUDASTE O MEU SER, NÃO MAIS EU, CRISTO EM MIM. INSTRUMENTOS\n\nEU TE AMO, MEU JESUS, POIS TU ÉS O MEU PASTOR, LEVA-ME COM ALEGRIA AO BANQUETE DO AMOR; A MINH’ALMA REJUBILA POIS ÉS TUDO PARA MIM, SOU HERDEIRO DE UM REINO, DE UM REINO QUE É SEM FIM.\n\nCORO EU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM A PORTA DO JARDIM DA VIDA QUE É SEM FIM; A MINH’ALMA CANTA AGORA O AMOR QUE VIVE EM MIM, POIS MUDASTE O MEU SER, NÃO MAIS EU, CRISTO EM MIM. (3X)', 'EU TE AMO, MEU JESUS, POIS TU ÉS O REMIDOR, RESGATASTE A MINHA ALMA DA TRISTEZA E DA DOR, EU TE AMO, MEU JESUS, COMO É FORTE O MEU AMOR, ALEGRASTE A MINHA ALMA COM TUA PAZ E O TEU AMOR. CORO EU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM A PORTA DO JARDIM DA VIDA QUE É SEM FIM; A MINH’ALMA CANTA AGORA O AMOR QUE VIVE EM MIM, POIS MUDASTE O MEU SER, NÃO MAIS EU, CRISTO EM MIM. INSTRUMENTOS EU TE AMO, MEU JESUS, POIS TU ÉS O MEU PASTOR, LEVA-ME COM ALEGRIA AO BANQUETE DO AMOR; A MINH’ALMA REJUBILA POIS ÉS TUDO PARA MIM, SOU HERDEIRO DE UM REINO, DE UM REINO QUE É SEM FIM. CORO EU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM A PORTA DO JARDIM DA VIDA QUE É SEM FIM; A MINH’ALMA CANTA AGORA O AMOR QUE VIVE EM MIM, POIS MUDASTE O MEU SER, NÃO MAIS EU, CRISTO EM MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('585', '585 - FONTE DA VIDA', 'FONTE DA VIDA, ESTRELA DA MANHÃ, JESUS, O REI DOS REIS, SENHOR, EMANUEL! CORDEIRO DE DEUS, O LEÃO DE JUDÁ, CRISTO JESUS.\n\nTE ADORAMOS POIS TU ESTÁS AQUI, OS NOSSOS CORAÇÕES ESTÃO CHEIOS DE LOUVOR; TU ÉS TUDO PRA NÓS, TU ÉS O SENHOR, CRISTO JESUS. CRISTO JESUS.\nBIS', 'FONTE DA VIDA, ESTRELA DA MANHÃ, JESUS, O REI DOS REIS, SENHOR, EMANUEL! CORDEIRO DE DEUS, O LEÃO DE JUDÁ, CRISTO JESUS. TE ADORAMOS POIS TU ESTÁS AQUI, OS NOSSOS CORAÇÕES ESTÃO CHEIOS DE LOUVOR; TU ÉS TUDO PRA NÓS, TU ÉS O SENHOR, CRISTO JESUS. CRISTO JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('586', '586 - Ó PROFUNDIDADE DAS RIQUEZAS', 'Ó PROFUNDIDADE DAS RIQUEZAS, TANTO DA SABEDORIA COMO DO CONHECIMENTO DE DEUS, QUÃO INSONDÁVEIS SÃO SEUS JUÍZOS, QUÃO INESCRUTÁVEIS OS SEUS CAMINHOS! INSTRUMENTOS\n\nAH! POR QUE QUEM COMPREENDEU OS INTENTOS DO SENHOR? OU QUEM FOI SEU CONSELHEIRO? OU QUEM LHE DEU PRIMEIRO A ELE? PRA QUE LHE SEJA RECOMPENSADO.\n\nPORQUE DELE E POR ELE E PARA ELE SÃO TODAS AS COISAS; GLÓRIA, GLÓRIA, GLÓRIA POIS A ELE ETERNAMENTE, AMÉM. FINAL: AMÉM!\nBIS', 'Ó PROFUNDIDADE DAS RIQUEZAS, TANTO DA SABEDORIA COMO DO CONHECIMENTO DE DEUS, QUÃO INSONDÁVEIS SÃO SEUS JUÍZOS, QUÃO INESCRUTÁVEIS OS SEUS CAMINHOS! INSTRUMENTOS AH! POR QUE QUEM COMPREENDEU OS INTENTOS DO SENHOR? OU QUEM FOI SEU CONSELHEIRO? OU QUEM LHE DEU PRIMEIRO A ELE? PRA QUE LHE SEJA RECOMPENSADO. PORQUE DELE E POR ELE E PARA ELE SÃO TODAS AS COISAS; GLÓRIA, GLÓRIA, GLÓRIA POIS A ELE ETERNAMENTE, AMÉM.  AMÉM!',  1);
