INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('588', '588 - HÁ UM NOME QUE RELUZ', 'HÁ UM NOME QUE RELUZ MAIS QUE MIL SÓIS EM SEU FULGOR, SERES CELESTIAIS SE INCLINAM DIANTE DELE COM TEMOR; NEM MIL ANOS BASTARIAM EM MIL CANTOS DE LOUVOR PARA CELEBRAR A GLÓRIA DE JESUS!\n\nFOI NA CRUZ QUE POR SEU SANGUE PRINCIPADOS DESPOJOU, FOI NA CRUZ QUE A MALDIÇÃO DA LEI POR NÓS ALI CRAVOU; AO TERCEIRO DIA A MORTE PARA SEMPRE CONQUISTOU, OH! QUE GLÓRIA TEM O NOME DE JESUS!\n\nCORO NOME SANTO DE JESUS, O MISTÉRIO REVELADO LÁ NA CRUZ, O QUE PRÍNCIPES E SÁBIOS NÃO PUDERAM ENTENDER TEMOS PELA FÉ NO NOME DE JESUS!\n\nAO SEU NOME OBEDECE TUDO O QUE HÁ NA CRIAÇÃO, A UMA SÓ PALAVRA SUA CESSA TODA A OPOSIÇÃO; AQUI HOJE PROCLAMAMOS A ETERNA SALVAÇÃO, HAJA PAZ E CURA EM NOME DE JESUS!\n\nCORO NOME SANTO DE JESUS, O MISTÉRIO REVELADO LÁ NA CRUZ, O QUE PRÍNCIPES E SÁBIOS NÃO PUDERAM ENTENDER TEMOS PELA FÉ NO NOME DE JESUS!', 'HÁ UM NOME QUE RELUZ MAIS QUE MIL SÓIS EM SEU FULGOR, SERES CELESTIAIS SE INCLINAM DIANTE DELE COM TEMOR; NEM MIL ANOS BASTARIAM EM MIL CANTOS DE LOUVOR PARA CELEBRAR A GLÓRIA DE JESUS! FOI NA CRUZ QUE POR SEU SANGUE PRINCIPADOS DESPOJOU, FOI NA CRUZ QUE A MALDIÇÃO DA LEI POR NÓS ALI CRAVOU; AO TERCEIRO DIA A MORTE PARA SEMPRE CONQUISTOU, OH! QUE GLÓRIA TEM O NOME DE JESUS! CORO NOME SANTO DE JESUS, O MISTÉRIO REVELADO LÁ NA CRUZ, O QUE PRÍNCIPES E SÁBIOS NÃO PUDERAM ENTENDER TEMOS PELA FÉ NO NOME DE JESUS! AO SEU NOME OBEDECE TUDO O QUE HÁ NA CRIAÇÃO, A UMA SÓ PALAVRA SUA CESSA TODA A OPOSIÇÃO; AQUI HOJE PROCLAMAMOS A ETERNA SALVAÇÃO, HAJA PAZ E CURA EM NOME DE JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('589', '589 - JESUS TÃO BELO', 'JESUS TÃO BELO, Ó REI DA TERRA, NASCESTE IGUAL A DEUS SENHOR, ÉS DE MINH’ALMA, SOL, ALEGRIA, COROA, GLÓRIA, LUZ, AMOR.\n\nDOS CÉUS A GLÓRIA, A MAJESTADE, EM TI, SENHOR, EU POSSO VER, DE MIM MAIS PERTO OU MAIS QUERIDO, Ó MEU JESUS, NINGUÉM VOU TER. JESUS AMADO, Ó REI GLORIOSO, AO PECADOR SÓ QUERES BEM, POIS SEJA SEMPRE, LOUVOR E GLÓRIA A TI, Ó SALVADOR, AMÉM.', 'JESUS TÃO BELO, Ó REI DA TERRA, NASCESTE IGUAL A DEUS SENHOR, ÉS DE MINH’ALMA, SOL, ALEGRIA, COROA, GLÓRIA, LUZ, AMOR. DOS CÉUS A GLÓRIA, A MAJESTADE, EM TI, SENHOR, EU POSSO VER, DE MIM MAIS PERTO OU MAIS QUERIDO, Ó MEU JESUS, NINGUÉM VOU TER. JESUS AMADO, Ó REI GLORIOSO, AO PECADOR SÓ QUERES BEM, POIS SEJA SEMPRE, LOUVOR E GLÓRIA A TI, Ó SALVADOR, AMÉM.',  1);
