INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('591', '591 - GLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS', 'GLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS, A JESUS, O SENHOR, AO LEÃO DE JUDÁ, À RAIZ DE DAVI QUE VENCEU E O LIVRO ABRIRÁ.\n\nO CÉU, A TERRA E O MAR, E TUDO O QUE NELES HÁ, O ADORARÃO E CONFESSARÃO: JESUS CRISTO É O SENHOR. ELE É O SENHOR, ELE É O SENHOR; RESSURRETO DENTRE OS MORTOS, ELE É O SENHOR; TODO JOELHO SE DOBRARÁ, TODA LÍNGUA CONFESSARÁ QUE JESUS CRISTO É O SENHOR.\n\nGLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS, A JESUS, O SENHOR, AO LEÃO DE JUDÁ, À RAIZ DE DAVI QUE VENCEU E O LIVRO ABRIRÁ. O CÉU, A TERRA E O MAR, E TUDO O QUE NELES HÁ, O ADORARÃO E CONFESSARÃO: JESUS CRISTO É O SENHOR.', 'GLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS, A JESUS, O SENHOR, AO LEÃO DE JUDÁ, À RAIZ DE DAVI QUE VENCEU E O LIVRO ABRIRÁ. O CÉU, A TERRA E O MAR, E TUDO O QUE NELES HÁ, O ADORARÃO E CONFESSARÃO: JESUS CRISTO É O SENHOR. ELE É O SENHOR, ELE É O SENHOR; RESSURRETO DENTRE OS MORTOS, ELE É O SENHOR; TODO JOELHO SE DOBRARÁ, TODA LÍNGUA CONFESSARÁ QUE JESUS CRISTO É O SENHOR. GLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS, A JESUS, O SENHOR, AO LEÃO DE JUDÁ, À RAIZ DE DAVI QUE VENCEU E O LIVRO ABRIRÁ. O CÉU, A TERRA E O MAR, E TUDO O QUE NELES HÁ, O ADORARÃO E CONFESSARÃO: JESUS CRISTO É O SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('592', '592 - ALELUIA! MUITAS VOZES DE ANJOS', 'ALELUIA! MUITAS VOZES DE ANJOS EXALTAM O NOME DO CORDEIRO VIVO, JESUS; AQUELE QUE UM DIA NA AMARGA CRUZ MORREU COMO PROVA DE AMOR.\n\nCORO E SÓ ELE É DIGNO DE TODO O LOUVOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO REDENTOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO SALVADOR! INSTRUMENTOS\n\nALELUIA! NÓS TAMBÉM ESTAREMOS NAS LINDAS MANSÕES LOUVANDO PRA SEMPRE AO SENHOR; E COM GRANDE ALEGRIA VEREMOS JESUS, QUE DIA TÃO LINDO SERÁ.\n\nCORO E SÓ ELE É DIGNO DE TODO O LOUVOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO REDENTOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO SALVADOR! (2X)', 'ALELUIA! MUITAS VOZES DE ANJOS EXALTAM O NOME DO CORDEIRO VIVO, JESUS; AQUELE QUE UM DIA NA AMARGA CRUZ MORREU COMO PROVA DE AMOR. CORO E SÓ ELE É DIGNO DE TODO O LOUVOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO REDENTOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO SALVADOR! INSTRUMENTOS ALELUIA! NÓS TAMBÉM ESTAREMOS NAS LINDAS MANSÕES LOUVANDO PRA SEMPRE AO SENHOR; E COM GRANDE ALEGRIA VEREMOS JESUS, QUE DIA TÃO LINDO SERÁ. CORO E SÓ ELE É DIGNO DE TODO O LOUVOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO REDENTOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO SALVADOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('593', '593 - A CRISTO COROAI', 'A CRISTO COROAI, CORDEIRO VENCEDOR! OUVI, DAS HOSTES CELESTIAIS DOS ANJOS, O LOUVOR! ELEVA A TUA VOZ E ENTOA, CORAÇÃO, LOUVANDO AQUELE QUE MORREU E DEU-TE A SALVAÇÃO.\n\nA CRISTO COROAI, SEU LADO E MÃOS OLHAI! DAS SUAS CHAGAS O ESPLENDOR E A GLÓRIA CONTEMPLAI; NEM ANJOS LÁ DO CÉU O PODEM SUPORTAR, PERANTE O EXCELSO ESPLENDOR ABAIXAM SEU OLHAR.\n\nA CRISTO COROAI, A VIDA NOS DOOU! E, A FIM DE DAR-NOS SALVAÇÃO, DA MORTE TRIUNFOU. CANTEMOS SEU PODER; MORREU, MAS RESSURGIU, A VIDA ETERNA ALI NOS DEU E A MORTE DESTRUIU.\n\nA CRISTO COROAI, DAS ERAS, O SENHOR! DOS MUNDOS E ASTROS DA AMPLIDÃO ETERNO CRIADOR! AO GRANDE REDENTOR, QUE NOS DEU SALVAÇÃO, ETERNAMENTE TRIBUTAI LOUVOR E ADORAÇÃO.', 'A CRISTO COROAI, CORDEIRO VENCEDOR! OUVI, DAS HOSTES CELESTIAIS DOS ANJOS, O LOUVOR! ELEVA A TUA VOZ E ENTOA, CORAÇÃO, LOUVANDO AQUELE QUE MORREU E DEU-TE A SALVAÇÃO. A CRISTO COROAI, SEU LADO E MÃOS OLHAI! DAS SUAS CHAGAS O ESPLENDOR E A GLÓRIA CONTEMPLAI; NEM ANJOS LÁ DO CÉU O PODEM SUPORTAR, PERANTE O EXCELSO ESPLENDOR ABAIXAM SEU OLHAR. A CRISTO COROAI, A VIDA NOS DOOU! E, A FIM DE DAR-NOS SALVAÇÃO, DA MORTE TRIUNFOU. CANTEMOS SEU PODER; MORREU, MAS RESSURGIU, A VIDA ETERNA ALI NOS DEU E A MORTE DESTRUIU. A CRISTO COROAI, DAS ERAS, O SENHOR! DOS MUNDOS E ASTROS DA AMPLIDÃO ETERNO CRIADOR! AO GRANDE REDENTOR, QUE NOS DEU SALVAÇÃO, ETERNAMENTE TRIBUTAI LOUVOR E ADORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('594', '594 - NA EXPRESSÃO DESTE LOUVOR', 'NA EXPRESSÃO DESTE LOUVOR QUERO DAR AO MEU SENHOR TODA A MINHA GRATIDÃO POR SER MUI FELIZ. DEVO MUITO AO SALVADOR, NELE TENHO O PERDÃO, NELE TENHO A SALVAÇÃO, EU SOU TÃO FELIZ.\n\nCORO NÓS TE ADORAMOS, MEU SENHOR, PORQUE VENCESTE NA CRUZ, NÓS TE ADORAMOS, MEU SENHOR, PORQUE TU HÁS DE VOLTAR; NÓS TE ADORAMOS, MEU SENHOR, POIS TU ÉS A RAZÃO E O MOTIVO DA NOSSA ADORAÇÃO.\n\nLÁ NA CRUZ MORREU JESUS, O SEU SANGUE DERRAMOU, O MEU FARDO ELE LEVOU E HOJE LIVRE SOU.\n\nCORO (2X) NÓS TE ADORAMOS, MEU SENHOR, PORQUE VENCESTE NA CRUZ, NÓS TE ADORAMOS, MEU SENHOR, PORQUE TU HÁS DE VOLTAR; NÓS TE ADORAMOS, MEU SENHOR, POIS TU ÉS A RAZÃO E O MOTIVO DA NOSSA ADORAÇÃO.', 'NA EXPRESSÃO DESTE LOUVOR QUERO DAR AO MEU SENHOR TODA A MINHA GRATIDÃO POR SER MUI FELIZ. DEVO MUITO AO SALVADOR, NELE TENHO O PERDÃO, NELE TENHO A SALVAÇÃO, EU SOU TÃO FELIZ. CORO NÓS TE ADORAMOS, MEU SENHOR, PORQUE VENCESTE NA CRUZ, NÓS TE ADORAMOS, MEU SENHOR, PORQUE TU HÁS DE VOLTAR; NÓS TE ADORAMOS, MEU SENHOR, POIS TU ÉS A RAZÃO E O MOTIVO DA NOSSA ADORAÇÃO. LÁ NA CRUZ MORREU JESUS, O SEU SANGUE DERRAMOU, O MEU FARDO ELE LEVOU E HOJE LIVRE SOU. CORO  NÓS TE ADORAMOS, MEU SENHOR, PORQUE VENCESTE NA CRUZ, NÓS TE ADORAMOS, MEU SENHOR, PORQUE TU HÁS DE VOLTAR; NÓS TE ADORAMOS, MEU SENHOR, POIS TU ÉS A RAZÃO E O MOTIVO DA NOSSA ADORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('595', '595 - MAJESTADE, GLÓRIA E MAJESTADE', 'MAJESTADE, GLÓRIA E MAJESTADE, JESUS CRISTO MERECE TODO LOUVOR; MAJESTADE, GLÓRIA E MAJESTADE, SEU É O LOUVOR, SEU É O PODER GLÓRIA A ELE!\n\nEXALTAI, ENGRANDECEI SEU SANTO NOME! ADORAI, MAGNIFICAI A CRISTO REI! MAJESTADE, GLÓRIA E MAJESTADE, CRISTO MORREU, RESSUSCITOU E HOJE É SENHOR.', 'MAJESTADE, GLÓRIA E MAJESTADE, JESUS CRISTO MERECE TODO LOUVOR; MAJESTADE, GLÓRIA E MAJESTADE, SEU É O LOUVOR, SEU É O PODER GLÓRIA A ELE! EXALTAI, ENGRANDECEI SEU SANTO NOME! ADORAI, MAGNIFICAI A CRISTO REI! MAJESTADE, GLÓRIA E MAJESTADE, CRISTO MORREU, RESSUSCITOU E HOJE É SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('596', '596 - ÉS MAJESTADE', 'ÉS MAJESTADE, Ó REI DA GLÓRIA, ÉS O SENHOR, O REI DOS REIS; TUA É SENHOR A MAGNIFICÊNCIA E O PODER E A HONRA E A VITÓRIA. CORO (2X) JESUS, JESUS, ÉS O SENHOR, O REI DOS REIS.\n\nDIGNO ÉS DE ABRIR O LIVRO DA VIDA, DIGNO É O CORDEIRO QUE FOI MORTO DE RECEBER PODER E RIQUEZAS, SABEDORIA, GLÓRIA E LOUVOR. CORO (2X) JESUS, JESUS, ÉS O SENHOR, O REI DOS REIS.', 'ÉS MAJESTADE, Ó REI DA GLÓRIA, ÉS O SENHOR, O REI DOS REIS; TUA É SENHOR A MAGNIFICÊNCIA E O PODER E A HONRA E A VITÓRIA. CORO  JESUS, JESUS, ÉS O SENHOR, O REI DOS REIS. DIGNO ÉS DE ABRIR O LIVRO DA VIDA, DIGNO É O CORDEIRO QUE FOI MORTO DE RECEBER PODER E RIQUEZAS, SABEDORIA, GLÓRIA E LOUVOR. CORO  JESUS, JESUS, ÉS O SENHOR, O REI DOS REIS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('597', '597 - CANTAREI, CANTAREI HOSANA (VIVA O REI)', 'CANTAREI, CANTAREI HOSANA, CANTAREI HOSANA AO SALVADOR; LOUVAREI SEU SANTO NOME E COM TODA ALEGRIA NO CÉU OUVIREI:\n\nCORO AS VOZES DE MILHÕES DE ANJOS CANTANDO, COM TODOS OS FIÉIS FALAREI: VIVA O REI, VIVA O REI, VIVA O REI JESUS!\n\nENTRAREI NO SANTO DOS SANTOS COM UM CÂNTICO NOVO DE GRATIDÃO; VIVEREI PRA SEMPRE NA GLÓRIA, LOUVANDO A JESUS E LÁ OUVIREI:\n\nCORO AS VOZES DE MILHÕES DE ANJOS CANTANDO, COM TODOS OS FIÉIS FALAREI: VIVA O REI, VIVA O REI, VIVA O REI JESUS!\n\nGOZO, PAZ, PERFEITO AMOR, PLENA ALEGRIA NO MEU SENHOR; ESTE MUNDO DEIXAREI, NA LINDA PÁTRIA BREVE ESTAREI E OUVIREI:\n\nCORO (2X) AS VOZES DE MILHÕES DE ANJOS CANTANDO, COM TODOS OS FIÉIS FALAREI: VIVA O REI, VIVA O REI, VIVA O REI JESUS!', 'CANTAREI, CANTAREI HOSANA, CANTAREI HOSANA AO SALVADOR; LOUVAREI SEU SANTO NOME E COM TODA ALEGRIA NO CÉU OUVIREI: CORO AS VOZES DE MILHÕES DE ANJOS CANTANDO, COM TODOS OS FIÉIS FALAREI: VIVA O REI, VIVA O REI, VIVA O REI JESUS! ENTRAREI NO SANTO DOS SANTOS COM UM CÂNTICO NOVO DE GRATIDÃO; VIVEREI PRA SEMPRE NA GLÓRIA, LOUVANDO A JESUS E LÁ OUVIREI: GOZO, PAZ, PERFEITO AMOR, PLENA ALEGRIA NO MEU SENHOR; ESTE MUNDO DEIXAREI, NA LINDA PÁTRIA BREVE ESTAREI E OUVIREI: CORO  AS VOZES DE MILHÕES DE ANJOS CANTANDO, COM TODOS OS FIÉIS FALAREI: VIVA O REI, VIVA O REI, VIVA O REI JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('598', '598 - HÁ UM SÓ QUE É DIGNO', 'HÁ UM SÓ QUE É DIGNO DE SOBRE NÓS REINAR E NO TRONO DE DAVI COM GLÓRIA SE SENTAR; O QUE ELE FALA PRA NÓS É SANTA LEI, A ELE ACLAMAMOS: VIVA O NOSSO REI!\n\nCORO VIVA O REI! VIVA O REI! O FILHO DE DAVI, O LEÃO DE JUDÁ, O REI DOS REIS QUE BREVE VOLTARÁ.\n\nMUITOS SE LEVANTAM QUERENDO SE IMPOR, COM SEUS VÃOS PROJETOS, AO POVO DO SENHOR; MAS DEUS TEM SUA OBRA E NELA SERVIREI; FRACO SOU, MAS CLAMO: VIVA O NOSSO REI!\n\nCORO VIVA O REI! VIVA O REI! O FILHO DE DAVI, O LEÃO DE JUDÁ, O REI DOS REIS QUE BREVE VOLTARÁ.\n\nEIS AQUI QUEM REINA, MAIOR QUE SALOMÃO, COMO ESPADA AGUDA SÃO OS CONSELHOS SEUS; E A NÓS, IGREJA, DEU GRAÇA E SALVAÇÃO FEZ-NOS SEUS VALENTES, EXÉRCITO DE DEUS.\n\nCORO VIVA O REI! VIVA O REI! O FILHO DE DAVI, O LEÃO DE JUDÁ, O REI DOS REIS QUE BREVE VOLTARÁ.', 'HÁ UM SÓ QUE É DIGNO DE SOBRE NÓS REINAR E NO TRONO DE DAVI COM GLÓRIA SE SENTAR; O QUE ELE FALA PRA NÓS É SANTA LEI, A ELE ACLAMAMOS: VIVA O NOSSO REI! CORO VIVA O REI! VIVA O REI! O FILHO DE DAVI, O LEÃO DE JUDÁ, O REI DOS REIS QUE BREVE VOLTARÁ. MUITOS SE LEVANTAM QUERENDO SE IMPOR, COM SEUS VÃOS PROJETOS, AO POVO DO SENHOR; MAS DEUS TEM SUA OBRA E NELA SERVIREI; FRACO SOU, MAS CLAMO: VIVA O NOSSO REI! EIS AQUI QUEM REINA, MAIOR QUE SALOMÃO, COMO ESPADA AGUDA SÃO OS CONSELHOS SEUS; E A NÓS, IGREJA, DEU GRAÇA E SALVAÇÃO FEZ-NOS SEUS VALENTES, EXÉRCITO DE DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('599', '599 - JESUS, TE ENTRONIZAMOS AQUI ENTRE NÓS', 'JESUS, TE ENTRONIZAMOS AQUI ENTRE NÓS PARA TRIBUTARMOS NOSSA ADORAÇÃO; A TI RENDEMOS GLÓRIA, HONRA E LOUVOR, EXPRESSÃO DE TODA A GRATIDÃO.\n\nCORO COROAMOS A CRISTO, (COROAMOS A CRISTO NOSSO REI) VARÕES ELE É O NOSSO REI, SIM, O NOSSO REI; ERGUEMOS AS VOZES EM CORO, (HOSANA A TI, JESUS) VARÕES CANTANDO HOSANA A JESUS, E OS ANJOS PROCLAMAM NO CÉU: VIVA O REI. INSTRUMENTOS\n\nTU ÉS DEUS FILHO, O VERBO CRIADOR, ESTÁS REVESTIDO DE GLÓRIA E ESPLENDOR; SANTO, SANTO, SANTO, ÉS TU, JESUS, TODA HONRA SEJA PARA TI.\n\nCORO (2X) COROAMOS A CRISTO, (COROAMOS A CRISTO NOSSO REI) VARÕES ELE É O NOSSO REI, SIM, O NOSSO REI; ERGUEMOS AS VOZES EM CORO, (HOSANA A TI, JESUS) VARÕES CANTANDO HOSANA A JESUS, E OS ANJOS PROCLAMAM NO CÉU: VIVA O REI. FINAL: VIVA O REI.', 'JESUS, TE ENTRONIZAMOS AQUI ENTRE NÓS PARA TRIBUTARMOS NOSSA ADORAÇÃO; A TI RENDEMOS GLÓRIA, HONRA E LOUVOR, EXPRESSÃO DE TODA A GRATIDÃO. CORO COROAMOS A CRISTO, (COROAMOS A CRISTO NOSSO REI) VARÕES ELE É O NOSSO REI, SIM, O NOSSO REI; ERGUEMOS AS VOZES EM CORO, (HOSANA A TI, JESUS) VARÕES CANTANDO HOSANA A JESUS, E OS ANJOS PROCLAMAM NO CÉU: VIVA O REI. INSTRUMENTOS TU ÉS DEUS FILHO, O VERBO CRIADOR, ESTÁS REVESTIDO DE GLÓRIA E ESPLENDOR; SANTO, SANTO, SANTO, ÉS TU, JESUS, TODA HONRA SEJA PARA TI. CORO  COROAMOS A CRISTO, (COROAMOS A CRISTO NOSSO REI) VARÕES ELE É O NOSSO REI, SIM, O NOSSO REI; ERGUEMOS AS VOZES EM CORO, (HOSANA A TI, JESUS) VARÕES CANTANDO HOSANA A JESUS, E OS ANJOS PROCLAMAM NO CÉU: VIVA O REI.  VIVA O REI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('600', '600 - JESUS, TE ENTRONIZAMOS', 'JESUS, TE ENTRONIZAMOS, DECLARAMOS QUE ÉS REI, TU ESTÁS NO MEIO DE NÓS, TE EXALTAMOS COM LOUVORES.\n\nA TI FAZEMOS UM TRONO DE LOUVORES (3X) Ó VEM, JESUS, E TOMA O TEU LUGAR, Ó VEM, JESUS, E TOMA O TEU LUGAR. INSTRUMENTOS\n\nJESUS, TE ENTRONIZAMOS, DECLARAMOS QUE ÉS REI, TU ESTÁS NO MEIO DE NÓS, TE EXALTAMOS COM LOUVORES.\n\nA TI FAZEMOS UM TRONO DE LOUVORES, (3X) Ó VEM, JESUS, E TOMA O TEU LUGAR, Ó VEM, JESUS, E TOMA O TEU LUGAR. FINAL: Ó VEM, JESUS, E TOMA O TEU LUGAR.', 'JESUS, TE ENTRONIZAMOS, DECLARAMOS QUE ÉS REI, TU ESTÁS NO MEIO DE NÓS, TE EXALTAMOS COM LOUVORES. A TI FAZEMOS UM TRONO DE LOUVORES  Ó VEM, JESUS, E TOMA O TEU LUGAR, Ó VEM, JESUS, E TOMA O TEU LUGAR. INSTRUMENTOS A TI FAZEMOS UM TRONO DE LOUVORES,  Ó VEM, JESUS, E TOMA O TEU LUGAR, Ó VEM, JESUS, E TOMA O TEU LUGAR.  Ó VEM, JESUS, E TOMA O TEU LUGAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('601', '601 - AO QUE ESTÁ NO TRONO', 'AO QUE ESTÁ NO TRONO ENTRE OS QUERUBINS, AO QUE RESPLANDECE E É FOGO CONSUMIDOR; DIANTE DELE VOAM ANJOS, SERAFINS QUE SEUS ROSTOS COBREM PERANTE A FACE DO SENHOR.\n\nCORO GLÓRIA, FORÇA, GRAÇA E PODER, SABEDORIA, HONRA E LOUVOR\n\nAO CORDEIRO MORTO QUE RESSUSCITOU E QUE AO CÉU SUBIU, AO REI QUE TÃO BREVE VEM; À PALAVRA VIVA QUE SE REVELOU, À LUZ QUE ILUMINA A NOVA JERUSALÉM.\n\nCORO (2X) GLÓRIA, FORÇA, GRAÇA E PODER, SABEDORIA, HONRA E LOUVOR FINAL: SANTO É O SENHOR! (3X)', 'AO QUE ESTÁ NO TRONO ENTRE OS QUERUBINS, AO QUE RESPLANDECE E É FOGO CONSUMIDOR; DIANTE DELE VOAM ANJOS, SERAFINS QUE SEUS ROSTOS COBREM PERANTE A FACE DO SENHOR. CORO GLÓRIA, FORÇA, GRAÇA E PODER, SABEDORIA, HONRA E LOUVOR AO CORDEIRO MORTO QUE RESSUSCITOU E QUE AO CÉU SUBIU, AO REI QUE TÃO BREVE VEM; À PALAVRA VIVA QUE SE REVELOU, À LUZ QUE ILUMINA A NOVA JERUSALÉM. CORO  GLÓRIA, FORÇA, GRAÇA E PODER, SABEDORIA, HONRA E LOUVOR  SANTO É O SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('602', '602 - SANTO É O QUE ESTÁ NO TRONO', 'SANTO, SANTO, SANTO É O QUE ESTÁ NO TRONO, O QUE É REI E SENHOR; DIGNO, SÓ ELE É DIGNO DE RECEBER A GLÓRIA E LOUVOR!\n\nQUEM É O QUE BRILHA MAIS QUE O SOL? É O SENHOR! (SERVAS) QUEM É O QUE BRAMA COMO O LEÃO? É O SENHOR! (SERVAS) QUEM É O QUE PASSEIA ENTRE OS SETE CASTIÇAIS? É O CORDEIRO, É O LEÃO DE JUDÁ!\n\nQUEM É O QUE A MORTE CONQUISTOU? É O SENHOR! (VARÕES) QUEM É O QUE O MUNDO VAI JULGAR? É O SENHOR! (VARÕES) QUEM É O QUE VIRÁ A ESPOSA ARREBATAR? É O CORDEIRO, É O LEÃO DE JUDÁ.\n\nSANTO, SANTO, SANTO É O QUE ESTÁ NO TRONO, O QUE É REI E SENHOR; DIGNO, SÓ ELE É DIGNO DE RECEBER A GLÓRIA E LOUVOR! (3X)', 'SANTO, SANTO, SANTO É O QUE ESTÁ NO TRONO, O QUE É REI E SENHOR; DIGNO, SÓ ELE É DIGNO DE RECEBER A GLÓRIA E LOUVOR! QUEM É O QUE BRILHA MAIS QUE O SOL? É O SENHOR!  QUEM É O QUE BRAMA COMO O LEÃO? É O SENHOR!  QUEM É O QUE PASSEIA ENTRE OS SETE CASTIÇAIS? É O CORDEIRO, É O LEÃO DE JUDÁ! QUEM É O QUE A MORTE CONQUISTOU? É O SENHOR!  QUEM É O QUE O MUNDO VAI JULGAR? É O SENHOR!  QUEM É O QUE VIRÁ A ESPOSA ARREBATAR? É O CORDEIRO, É O LEÃO DE JUDÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('603', '603 - LOUVAREI AO MEU SENHOR', 'LOUVAREI AO MEU SENHOR ENQUANTO EU VIVER, SEU LOUVOR PRA SEMPRE ESTARÁ COMIGO; SUA GRAÇA ME SUSTÉM, SEU AMOR É TÃO PROFUNDO, SEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR.\n\nCORO (2X) SIM, LOUVAREI AO MEU SENHOR, POIS É DIGNO DE LOUVOR, SUA GRAÇA ME SUSTÉM, LOUVAREI PRA SEMPRE, AMÉM.\n\nLOUVAREI AO MEU SENHOR, SUA GRAÇA ME SALVOU, (SIM, LOUVAREI) PÔS UM NOVO CÂNTICO EM MEUS LÁBIOS. (SIM, LOUVAREI) SUA GRAÇA ME SUSTÉM, SEU AMOR É TÃO PROFUNDO, (SIM, LOUVAREI) SEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR.\n\nCORO (2X) SIM, LOUVAREI AO MEU SENHOR, POIS É DIGNO DE LOUVOR, SUA GRAÇA ME SUSTÉM, LOUVAREI PRA SEMPRE, AMÉM. FINAL: SIM, LOUVAREI.', 'LOUVAREI AO MEU SENHOR ENQUANTO EU VIVER, SEU LOUVOR PRA SEMPRE ESTARÁ COMIGO; SUA GRAÇA ME SUSTÉM, SEU AMOR É TÃO PROFUNDO, SEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR. CORO  SIM, LOUVAREI AO MEU SENHOR, POIS É DIGNO DE LOUVOR, SUA GRAÇA ME SUSTÉM, LOUVAREI PRA SEMPRE, AMÉM. LOUVAREI AO MEU SENHOR, SUA GRAÇA ME SALVOU, (SIM, LOUVAREI) PÔS UM NOVO CÂNTICO EM MEUS LÁBIOS. (SIM, LOUVAREI) SUA GRAÇA ME SUSTÉM, SEU AMOR É TÃO PROFUNDO, (SIM, LOUVAREI) SEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR. CORO  SIM, LOUVAREI AO MEU SENHOR, POIS É DIGNO DE LOUVOR, SUA GRAÇA ME SUSTÉM, LOUVAREI PRA SEMPRE, AMÉM.  SIM, LOUVAREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('604', '604 - O MEU LOUVOR É FRUTO', 'CORO (2X) O MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME; É FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO EM TI E DO TEU ESPÍRITO QUE HABITA EM MIM, QUE HABITA EM MIM.\n\nAINDA QUE AS TREVAS VENHAM ME CERCAR AINDA QUE OS MONTES DESABEM SOBRE MIM, MEUS LÁBIOS NÃO SE FECHARÃO PRA SEMPRE HEI DE TE LOUVAR!\n\nCORO O MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME; É FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO EM TI E DO TEU ESPÍRITO QUE HABITA EM MIM, QUE HABITA EM MIM.\n\nAINDA QUE CADEIAS VENHAM ME PRENDER AINDA QUE OS HOMENS SE LEVANTEM CONTRA MIM, MEUS LÁBIOS NÃO SE FECHARÃO, PRA SEMPRE HEI DE TE LOUVAR!\n\nCORO O MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME; É FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO EM TI E DO TEU ESPÍRITO QUE HABITA EM MIM, QUE HABITA EM MIM.', 'CORO  O MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME; É FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO EM TI E DO TEU ESPÍRITO QUE HABITA EM MIM, QUE HABITA EM MIM. AINDA QUE AS TREVAS VENHAM ME CERCAR AINDA QUE OS MONTES DESABEM SOBRE MIM, MEUS LÁBIOS NÃO SE FECHARÃO PRA SEMPRE HEI DE TE LOUVAR! CORO O MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME; É FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO EM TI E DO TEU ESPÍRITO QUE HABITA EM MIM, QUE HABITA EM MIM. AINDA QUE CADEIAS VENHAM ME PRENDER AINDA QUE OS HOMENS SE LEVANTEM CONTRA MIM, MEUS LÁBIOS NÃO SE FECHARÃO, PRA SEMPRE HEI DE TE LOUVAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('605', '605 - NOSSO DEUS É SOBERANO', 'NOSSO DEUS É SOBERANO, ELE REINA ANTES DA FUNDAÇÃO DO MUNDO. NOSSO DEUS É SOBERANO, ELE REINA ANTES DA FUNDAÇÃO DO MUNDO.\n\nA TERRA ERA SEM FORMA E VAZIA E O ESPÍRITO DO NOSSO DEUS SE MOVIA SOBRE A FACE DAS ÁGUAS; FOI ELE QUEM CRIOU O CÉU DOS CÉUS E FEZ SEPARAÇÃO DAS ÁGUAS DA TERRA SECA; FOI ELE QUEM CRIOU OS LUMINARES, CRIOU A NATUREZA E FORMOU O HOMEM.\n\nGLÓRIA A DEUS POR SUAS MARAVILHAS, PELA SUA GRANDEZA, GLÓRIA A DEUS! GLÓRIA A DEUS POR SUAS MARAVILHAS, PELA SUA GRANDEZA, GLÓRIA A DEUS! GLÓRIA A DEUS!', 'NOSSO DEUS É SOBERANO, ELE REINA ANTES DA FUNDAÇÃO DO MUNDO. NOSSO DEUS É SOBERANO, ELE REINA ANTES DA FUNDAÇÃO DO MUNDO. A TERRA ERA SEM FORMA E VAZIA E O ESPÍRITO DO NOSSO DEUS SE MOVIA SOBRE A FACE DAS ÁGUAS; FOI ELE QUEM CRIOU O CÉU DOS CÉUS E FEZ SEPARAÇÃO DAS ÁGUAS DA TERRA SECA; FOI ELE QUEM CRIOU OS LUMINARES, CRIOU A NATUREZA E FORMOU O HOMEM. GLÓRIA A DEUS POR SUAS MARAVILHAS, PELA SUA GRANDEZA, GLÓRIA A DEUS! GLÓRIA A DEUS POR SUAS MARAVILHAS, PELA SUA GRANDEZA, GLÓRIA A DEUS! GLÓRIA A DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('606', '606 - O CRIADOR DO UNIVERSO', 'O CRIADOR DO UNIVERSO, QUE FEZ DO NADA TUDO NASCER, O QUE SUSTENTA TODAS AS COISAS PELA PALAVRA DO SEU PODER; O VERBO ETERNO QUE SE FEZ CARNE, O DEUS EM HOMEM REVELADO, O DEUS QUE É PAI, FILHO E ESPÍRITO, PARA SEMPRE SEJA ADORADO.\n\nCORO SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS!\n\nO PAI, A QUEM ABANDONAMOS, MAS QUE JAMAIS NOS ESQUECEU, ATÉ QUE UM DIA REGRESSAMOS E COM PERDÃO NOS RECEBEU; O FILHO QUE NA CRUZ FOI DADO, O QUE HOJE VIVE E ORA POR NÓS, O ESPÍRITO QUE NÃO NOS DEIXA SÓS; PARA SEMPRE SEJA ADORADO.\n\nCORO SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS! FINAL: SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS! SEJA EXALTADO O TEU NOME, Ó DEUS!', 'O CRIADOR DO UNIVERSO, QUE FEZ DO NADA TUDO NASCER, O QUE SUSTENTA TODAS AS COISAS PELA PALAVRA DO SEU PODER; O VERBO ETERNO QUE SE FEZ CARNE, O DEUS EM HOMEM REVELADO, O DEUS QUE É PAI, FILHO E ESPÍRITO, PARA SEMPRE SEJA ADORADO. CORO SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS! O PAI, A QUEM ABANDONAMOS, MAS QUE JAMAIS NOS ESQUECEU, ATÉ QUE UM DIA REGRESSAMOS E COM PERDÃO NOS RECEBEU; O FILHO QUE NA CRUZ FOI DADO, O QUE HOJE VIVE E ORA POR NÓS, O ESPÍRITO QUE NÃO NOS DEIXA SÓS; PARA SEMPRE SEJA ADORADO. CORO SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS!  SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS! SEJA EXALTADO O TEU NOME, Ó DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('607', '607 - A DEUS SEJA O LOUVOR', 'A DEUS SEJA O LOUVOR POR TUDO O QUE ELE É, PERFEIÇÃO ABSOLUTA EM SANTIDADE E AMOR! E DAI AO FILHO A GLÓRIA POIS ALCANÇOU VITÓRIA, ELE É VENCEDOR, DE TUDO É O SENHOR!\n\nPOR TODO O SEMPRE SE FALARÁ DAS MARAVILHAS DO SEU AMOR E TODO SER SE ENCURVARÁ PRA O ADORAR! O REI QUE SERVO SE TORNOU, O DEUS QUE ÀQUELA CRUZ BAIXOU PARA, POR SEU SANGUE, O PECADOR RESGATAR. ÀQUELA CRUZ BAIXOU PARA, POR SEU SANGUE, O PECADOR RESGATAR.', 'A DEUS SEJA O LOUVOR POR TUDO O QUE ELE É, PERFEIÇÃO ABSOLUTA EM SANTIDADE E AMOR! E DAI AO FILHO A GLÓRIA POIS ALCANÇOU VITÓRIA, ELE É VENCEDOR, DE TUDO É O SENHOR! POR TODO O SEMPRE SE FALARÁ DAS MARAVILHAS DO SEU AMOR E TODO SER SE ENCURVARÁ PRA O ADORAR! O REI QUE SERVO SE TORNOU, O DEUS QUE ÀQUELA CRUZ BAIXOU PARA, POR SEU SANGUE, O PECADOR RESGATAR. ÀQUELA CRUZ BAIXOU PARA, POR SEU SANGUE, O PECADOR RESGATAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('608', '608 - DEUS DOS EXÉRCITOS DE ISRAEL', 'DEUS DOS EXÉRCITOS DE ISRAEL, CRIADOR DA TERRA E DO CÉU, TU ÉS DEUS DOS PROFETAS, SOBERANO CRIADOR; TU ÉS DEUS E DA VIDA ÉS AUTOR. ÉS CRIADOR! ÉS CRIADOR!\n\nJESUS, ETERNO SALVADOR, NA CRUZ DEMONSTRASTE TEU AMOR, COM GRAÇA E VERDADE, COM TEU SANGUE ME ALCANÇASTE; JESUS, TU ÉS DEUS, TU ÉS SENHOR. TU ÉS SENHOR! TU ÉS SENHOR!\n\nÓ ESPÍRITO SANTO CONSOLADOR, EM TEUS SERVOS OPERAS COM FAVOR, TU ESTÁS SEMPRE CONOSCO, ÉS O NOSSO INTERCESSOR; TU ÉS DEUS, Ó ESPÍRITO DE AMOR. CONSOLADOR! CONSOLADOR!', 'DEUS DOS EXÉRCITOS DE ISRAEL, CRIADOR DA TERRA E DO CÉU, TU ÉS DEUS DOS PROFETAS, SOBERANO CRIADOR; TU ÉS DEUS E DA VIDA ÉS AUTOR. ÉS CRIADOR! ÉS CRIADOR! JESUS, ETERNO SALVADOR, NA CRUZ DEMONSTRASTE TEU AMOR, COM GRAÇA E VERDADE, COM TEU SANGUE ME ALCANÇASTE; JESUS, TU ÉS DEUS, TU ÉS SENHOR. TU ÉS SENHOR! TU ÉS SENHOR! Ó ESPÍRITO SANTO CONSOLADOR, EM TEUS SERVOS OPERAS COM FAVOR, TU ESTÁS SEMPRE CONOSCO, ÉS O NOSSO INTERCESSOR; TU ÉS DEUS, Ó ESPÍRITO DE AMOR. CONSOLADOR! CONSOLADOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('609', '609 - SENHOR, MEU DEUS, QUANDO EU MARAVILHADO', 'SENHOR, MEU DEUS, QUANDO EU MARAVILHADO FICO A PENSAR NAS OBRAS DE TUAS MÃOS, O CÉU AZUL DE ESTRELAS PONTILHADO, O TEU PODER MOSTRANDO A CRIAÇÃO.\n\nCORO (2X) ENTÃO MINH’ALMA CANTA A TI, SENHOR: QUÃO GRANDE ÉS TU, QUÃO GRANDE ÉS TU!\n\nQUANDO A VAGAR NAS MATAS E FLORESTAS, O PASSAREDO ALEGRE OUÇO A CANTAR; OLHANDO OS MONTES VALES E CAMPINAS EM TUDO VEJO O TEU PODER SEM PAR.\n\nCORO (2X) ENTÃO MINH’ALMA CANTA A TI, SENHOR: QUÃO GRANDE ÉS TU, QUÃO GRANDE ÉS TU!\n\nQUANDO EU MEDITO EM TEU AMOR TÃO GRANDE, TEU FILHO DANDO AO MUNDO PRA SALVAR; NA CRUZ VERTENDO O SEU PRECIOSO SANGUE, MINH’ALMA PÔDE ASSIM PURIFICAR.\n\nCORO (2X) ENTÃO MINH’ALMA CANTA A TI, SENHOR: QUÃO GRANDE ÉS TU, QUÃO GRANDE ÉS TU!\n\nE QUANDO ENFIM JESUS VIER EM GLÓRIA E AO LAR CELESTE ENTÃO ME TRANSPORTAR, ADORAREI, PROSTRADO E PARA SEMPRE, QUÃO GRANDE ÉS TU, MEU DEUS, HEI DE CANTAR.\n\nCORO (2X) ENTÃO MINH’ALMA CANTA A TI, SENHOR: QUÃO GRANDE ÉS TU, QUÃO GRANDE ÉS TU!', 'SENHOR, MEU DEUS, QUANDO EU MARAVILHADO FICO A PENSAR NAS OBRAS DE TUAS MÃOS, O CÉU AZUL DE ESTRELAS PONTILHADO, O TEU PODER MOSTRANDO A CRIAÇÃO. CORO  ENTÃO MINH’ALMA CANTA A TI, SENHOR: QUÃO GRANDE ÉS TU, QUÃO GRANDE ÉS TU! QUANDO A VAGAR NAS MATAS E FLORESTAS, O PASSAREDO ALEGRE OUÇO A CANTAR; OLHANDO OS MONTES VALES E CAMPINAS EM TUDO VEJO O TEU PODER SEM PAR. QUANDO EU MEDITO EM TEU AMOR TÃO GRANDE, TEU FILHO DANDO AO MUNDO PRA SALVAR; NA CRUZ VERTENDO O SEU PRECIOSO SANGUE, MINH’ALMA PÔDE ASSIM PURIFICAR. E QUANDO ENFIM JESUS VIER EM GLÓRIA E AO LAR CELESTE ENTÃO ME TRANSPORTAR, ADORAREI, PROSTRADO E PARA SEMPRE, QUÃO GRANDE ÉS TU, MEU DEUS, HEI DE CANTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('610', '610 - SANTO! SANTO! SANTO! DEUS ONIPOTENTE!', 'SANTO! SANTO! SANTO! DEUS ONIPOTENTE! CEDO DE MANHÃ CANTAREMOS TEU LOUVOR! SANTO! SANTO! SANTO! DEUS, JEOVÁ, TRIUNO, ÉS UM SÓ DEUS, EXCELSO CRIADOR!\n\nSANTO! SANTO! SANTO! TODOS OS REMIDOS, JUNTOS COM OS ANJOS, PROCLAMAM TEU LOUVOR; ANTES DE FORMAR-SE O FIRMAMENTO E A TERRA, ERAS E SEMPRE ÉS E HÁS DE SER, SENHOR!\n\nSANTO! SANTO! SANTO! NÓS OS PECADORES NÃO PODEMOS VER TUA GLÓRIA SEM TREMOR; TU SOMENTE ÉS SANTO, NÃO HÁ NENHUM OUTRO PURO E PERFEITO, EXCELSO BENFEITOR! INSTRUMENTOS\n\nSANTO! SANTO! SANTO! DEUS ONIPOTENTE! TUAS OBRAS LOUVAM TEU NOME COM FERVOR; SANTO! SANTO! SANTO! JUSTO E COMPASSIVO! ÉS UM SÓ DEUS, SUPREMO CRIADOR.', 'SANTO! SANTO! SANTO! DEUS ONIPOTENTE! CEDO DE MANHÃ CANTAREMOS TEU LOUVOR! SANTO! SANTO! SANTO! DEUS, JEOVÁ, TRIUNO, ÉS UM SÓ DEUS, EXCELSO CRIADOR! SANTO! SANTO! SANTO! TODOS OS REMIDOS, JUNTOS COM OS ANJOS, PROCLAMAM TEU LOUVOR; ANTES DE FORMAR-SE O FIRMAMENTO E A TERRA, ERAS E SEMPRE ÉS E HÁS DE SER, SENHOR! SANTO! SANTO! SANTO! NÓS OS PECADORES NÃO PODEMOS VER TUA GLÓRIA SEM TREMOR; TU SOMENTE ÉS SANTO, NÃO HÁ NENHUM OUTRO PURO E PERFEITO, EXCELSO BENFEITOR! INSTRUMENTOS SANTO! SANTO! SANTO! DEUS ONIPOTENTE! TUAS OBRAS LOUVAM TEU NOME COM FERVOR; SANTO! SANTO! SANTO! JUSTO E COMPASSIVO! ÉS UM SÓ DEUS, SUPREMO CRIADOR.',  1);
