INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('612', '612 - SANTO, SANTO, SANTO, Ó DEUS DE GLÓRIA E PODER', 'SANTO, SANTO, SANTO, Ó DEUS DE GLÓRIA E PODER; CÉUS E TERRA ESTÃO PLENOS DE TI, HOSANA, HOSANA, HOSANA AO NOSSO DEUS.\n\nBENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA, HOSANA AO NOSSO DEUS.', 'SANTO, SANTO, SANTO, Ó DEUS DE GLÓRIA E PODER; CÉUS E TERRA ESTÃO PLENOS DE TI, HOSANA, HOSANA, HOSANA AO NOSSO DEUS. BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA, HOSANA AO NOSSO DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('613', '613 - DAI GLÓRIA A DEUS', 'DAI GLÓRIA A DEUS! QUEM NÃO O TEMERÁ? GLÓRIA E PODER SEM FIM DIANTE DELE HÁ! EIS O LEÃO DA TRIBO DE JUDÁ, AQUELE QUE ERA E É, E QUE VOLTARÁ!\n\nANJOS E QUERUBINS, ARCANJOS E SERAFINS, DIA E NOITE ADORAM O QUE FOI MORTO MAS HOJE VIVE E É REI DOS REIS! DAI GLÓRIA A DEUS, DAI GLÓRIA A DEUS PAI, AO FILHO ADORAI, DAI GLÓRIA A DEUS! (3X)', 'DAI GLÓRIA A DEUS! QUEM NÃO O TEMERÁ? GLÓRIA E PODER SEM FIM DIANTE DELE HÁ! EIS O LEÃO DA TRIBO DE JUDÁ, AQUELE QUE ERA E É, E QUE VOLTARÁ! ANJOS E QUERUBINS, ARCANJOS E SERAFINS, DIA E NOITE ADORAM O QUE FOI MORTO MAS HOJE VIVE E É REI DOS REIS! DAI GLÓRIA A DEUS, DAI GLÓRIA A DEUS PAI, AO FILHO ADORAI, DAI GLÓRIA A DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('614', '614 - A DEUS DEMOS GLÓRIA', 'A DEUS DEMOS GLÓRIA COM GRANDE FERVOR, SEU FILHO BENDITO POR NÓS TODOS DEU; A GRAÇA CONCEDE A QUALQUER PECADOR ABRINDO-LHE A PORTA DE ENTRADA NO CÉU.\n\nCORO EXULTAI! EXULTAI! VINDE TODOS LOUVAR A JESUS SALVADOR, A JESUS REDENTOR! A DEUS DEMOS GLÓRIA PORQUANTO DO CÉU SEU FILHO BENDITO POR NÓS TODOS DEU.\n\nOH! GRAÇA REAL! FOI ASSIM QUE JESUS MORRENDO, SEU SANGUE POR NÓS DERRAMOU; HERANÇA NOS CÉUS, COM OS SANTOS EM LUZ, COMPROU-NOS, JESUS, POIS O PREÇO PAGOU.\n\nCORO EXULTAI! EXULTAI! VINDE TODOS LOUVAR A JESUS SALVADOR, A JESUS REDENTOR! A DEUS DEMOS GLÓRIA PORQUANTO DO CÉU SEU FILHO BENDITO POR NÓS TODOS DEU.\n\nA CRER NOS CONVIDA TAL PROVA DE AMOR, NOS MERECIMENTOS DO FILHO DE DEUS; E QUEM, POIS, CONFIA EM JESUS, SALVADOR, VAI VÊ-LO EXALTADO NA GLÓRIA DOS CÉUS.\n\nCORO EXULTAI! EXULTAI! VINDE TODOS LOUVAR A JESUS SALVADOR, A JESUS REDENTOR! A DEUS DEMOS GLÓRIA PORQUANTO DO CÉU SEU FILHO BENDITO POR NÓS TODOS DEU.', 'A DEUS DEMOS GLÓRIA COM GRANDE FERVOR, SEU FILHO BENDITO POR NÓS TODOS DEU; A GRAÇA CONCEDE A QUALQUER PECADOR ABRINDO-LHE A PORTA DE ENTRADA NO CÉU. CORO EXULTAI! EXULTAI! VINDE TODOS LOUVAR A JESUS SALVADOR, A JESUS REDENTOR! A DEUS DEMOS GLÓRIA PORQUANTO DO CÉU SEU FILHO BENDITO POR NÓS TODOS DEU. OH! GRAÇA REAL! FOI ASSIM QUE JESUS MORRENDO, SEU SANGUE POR NÓS DERRAMOU; HERANÇA NOS CÉUS, COM OS SANTOS EM LUZ, COMPROU-NOS, JESUS, POIS O PREÇO PAGOU. A CRER NOS CONVIDA TAL PROVA DE AMOR, NOS MERECIMENTOS DO FILHO DE DEUS; E QUEM, POIS, CONFIA EM JESUS, SALVADOR, VAI VÊ-LO EXALTADO NA GLÓRIA DOS CÉUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('615', '615 - LOUVOR E GLÓRIA', 'LOUVOR E GLÓRIA E SABEDORIA E AÇÕES DE GRAÇA E HONRA E PODER E FORÇA AO NOSSO DEUS, AO NOSSO DEUS, PRA TODO O SEMPRE, AMÉM. REPETE O LOUVOR', 'LOUVOR E GLÓRIA E SABEDORIA E AÇÕES DE GRAÇA E HONRA E PODER E FORÇA AO NOSSO DEUS, AO NOSSO DEUS, PRA TODO O SEMPRE, AMÉM. REPETE O LOUVOR',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('616', '616 - AO DEUS DE AMOR E DE IMENSA BONDADE', 'AO DEUS DE AMOR E DE IMENSA BONDADE, COM VOZ DE JÚBILO VINDE E ACLAMAI! COM O CORAÇÃO TRANSBORDANTE DE GRAÇAS, SEU GRANDE AMOR, TODOS VINDE E LOUVAI!\n\nCORO NO CÉU, NA TERRA, QUE MARAVILHAS VAI OPERANDO O PODER DO SENHOR! MAS SEU AMOR AOS HOMENS PERDIDOS, DAS MARAVILHAS É SEMPRE A MAIOR!\n\nJÁ NOSSOS PAIS NOS CONTARAM A GLÓRIA DE DEUS, FALANDO COM MUITO PRAZER, QUE NAS TRISTEZAS, NOS GRANDES PERIGOS, ELE OS SALVOU POR SEU GRANDE PODER!\n\nCORO NO CÉU, NA TERRA, QUE MARAVILHAS VAI OPERANDO O PODER DO SENHOR! MAS SEU AMOR AOS HOMENS PERDIDOS, DAS MARAVILHAS É SEMPRE A MAIOR!\n\nHOJE TAMBÉM NÓS BEM ALTO CANTAMOS QUE AS ORAÇÕES ELE NOS ATENDEU! SEU FORTE BRAÇO, QUE É TÃO COMPASSIVO, EM NOSSO AUXÍLIO ELE SEMPRE ESTENDEU!\n\nCORO NO CÉU, NA TERRA, QUE MARAVILHAS VAI OPERANDO O PODER DO SENHOR! MAS SEU AMOR AOS HOMENS PERDIDOS, DAS MARAVILHAS É SEMPRE A MAIOR!\n\nCOMO ATÉ HOJE E DAQUI PARA SEMPRE ELE SERÁ NOSSO ETERNO PODER! NOSSO CASTELO BEM FORTE E SEGURO E NOSSA FONTE DE EXCELSO PRAZER!\n\nCORO NO CÉU, NA TERRA, QUE MARAVILHAS VAI OPERANDO O PODER DO SENHOR! MAS SEU AMOR AOS HOMENS PERDIDOS, DAS MARAVILHAS É SEMPRE A MAIOR!', 'AO DEUS DE AMOR E DE IMENSA BONDADE, COM VOZ DE JÚBILO VINDE E ACLAMAI! COM O CORAÇÃO TRANSBORDANTE DE GRAÇAS, SEU GRANDE AMOR, TODOS VINDE E LOUVAI! CORO NO CÉU, NA TERRA, QUE MARAVILHAS VAI OPERANDO O PODER DO SENHOR! MAS SEU AMOR AOS HOMENS PERDIDOS, DAS MARAVILHAS É SEMPRE A MAIOR! JÁ NOSSOS PAIS NOS CONTARAM A GLÓRIA DE DEUS, FALANDO COM MUITO PRAZER, QUE NAS TRISTEZAS, NOS GRANDES PERIGOS, ELE OS SALVOU POR SEU GRANDE PODER! HOJE TAMBÉM NÓS BEM ALTO CANTAMOS QUE AS ORAÇÕES ELE NOS ATENDEU! SEU FORTE BRAÇO, QUE É TÃO COMPASSIVO, EM NOSSO AUXÍLIO ELE SEMPRE ESTENDEU! COMO ATÉ HOJE E DAQUI PARA SEMPRE ELE SERÁ NOSSO ETERNO PODER! NOSSO CASTELO BEM FORTE E SEGURO E NOSSA FONTE DE EXCELSO PRAZER!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('617', '617 - O MEU CORAÇÃO SE REGOZIJA NO SENHOR', 'O MEU CORAÇÃO SE REGOZIJA NO SENHOR, O MEU PODER ESTÁ EXALTADO NO SENHOR; A MINHA BOCA SE ABRIRÁ EM GLORIFICAÇÃO PORQUE ME ALEGRO NA SUA SALVAÇÃO.\n\nCORO NÃO HÁ SANTO COMO O SENHOR, NÃO HÁ OUTRO ALÉM DE TI; ROCHA INABALÁVEL É O NOSSO DEUS. INSTRUMENTOS\n\nO SENHOR É O QUE TIRA A VIDA E A DÁ E OS SEUS SANTOS ETERNAMENTE GUARDARÁ; ELE EXALTARÁ DO SEU UNGIDO O PODER PORQUE O SENHOR É O NOSSO DEUS.\n\nCORO (2X) NÃO HÁ SANTO COMO O SENHOR, NÃO HÁ OUTRO ALÉM DE TI; ROCHA INABALÁVEL É O NOSSO DEUS.', 'O MEU CORAÇÃO SE REGOZIJA NO SENHOR, O MEU PODER ESTÁ EXALTADO NO SENHOR; A MINHA BOCA SE ABRIRÁ EM GLORIFICAÇÃO PORQUE ME ALEGRO NA SUA SALVAÇÃO. CORO NÃO HÁ SANTO COMO O SENHOR, NÃO HÁ OUTRO ALÉM DE TI; ROCHA INABALÁVEL É O NOSSO DEUS. INSTRUMENTOS O SENHOR É O QUE TIRA A VIDA E A DÁ E OS SEUS SANTOS ETERNAMENTE GUARDARÁ; ELE EXALTARÁ DO SEU UNGIDO O PODER PORQUE O SENHOR É O NOSSO DEUS. CORO  NÃO HÁ SANTO COMO O SENHOR, NÃO HÁ OUTRO ALÉM DE TI; ROCHA INABALÁVEL É O NOSSO DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('618', '618 - CANTAREI AO MEU SENHOR ENQUANTO EU VIVER', 'CANTAREI AO MEU SENHOR ENQUANTO EU VIVER, CANTAREI LOUVORES ENQUANTO EXISTIR; ENTÃO ME ALEGRAREI NO MEU SENHOR, MUI SUAVE SERÁ A MINHA MEDITAÇÃO. LOUVA, MINH’ALMA, AO SENHOR, BENDIZE-O JÁ.\n\n4X', 'CANTAREI AO MEU SENHOR ENQUANTO EU VIVER, CANTAREI LOUVORES ENQUANTO EXISTIR; ENTÃO ME ALEGRAREI NO MEU SENHOR, MUI SUAVE SERÁ A MINHA MEDITAÇÃO. LOUVA, MINH’ALMA, AO SENHOR, BENDIZE-O JÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('619', '619 - VEM, DERRAMA A PAZ', 'VEM, DERRAMA A PAZ, VEM, DERRAMA AS BÊNÇÃOS SOBRE ESTE POVO QUE SE CHAMA POVO TEU; DÁ-NOS TEU AMOR, DÁ-NOS TUA FORÇA PRA QUE TENTAÇÕES NÃO VENHAM A NOS DESVIAR.\n\nE A GLÓRIA SEJA DADA A TI PELO QUE TENS FEITO, PELO QUE TENS SIDO, PELO QUE FARÁS EM NÓS. INSTRUMENTOS\n\nVEM, DERRAMA A PAZ, VEM, DERRAMA AS BÊNÇÃOS SOBRE ESTE POVO QUE SE CHAMA POVO TEU; DÁ-NOS TEU AMOR, DÁ-NOS TUA FORÇA PRA QUE TENTAÇÕES NÃO VENHAM A NOS DESVIAR.\n\nE A GLÓRIA SEJA DADA A TI PELO QUE TENS FEITO, PELO QUE TENS SIDO, PELO QUE FARÁS EM NÓS. FINAL: PELO QUE FARÁS EM NÓS.', 'VEM, DERRAMA A PAZ, VEM, DERRAMA AS BÊNÇÃOS SOBRE ESTE POVO QUE SE CHAMA POVO TEU; DÁ-NOS TEU AMOR, DÁ-NOS TUA FORÇA PRA QUE TENTAÇÕES NÃO VENHAM A NOS DESVIAR. E A GLÓRIA SEJA DADA A TI PELO QUE TENS FEITO, PELO QUE TENS SIDO, PELO QUE FARÁS EM NÓS. INSTRUMENTOS E A GLÓRIA SEJA DADA A TI PELO QUE TENS FEITO, PELO QUE TENS SIDO, PELO QUE FARÁS EM NÓS.  PELO QUE FARÁS EM NÓS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('620', '620 - ESTAMOS AQUI, Ó DEUS', 'ESTAMOS AQUI, Ó DEUS, PRA LOUVAR O TEU NOME, POIS TU NOS TORNASTE SERVOS TEUS. LOUVEMOS A DEUS! A CAMINHO ESTAMOS DA NOVA JERUSALÉM, CIDADE ETERNAL QUE O PAI NOS PREPAROU. MARANATA! ORA VEM, SENHOR JESUS, VEM NOS BUSCAR. INSTRUMENTOS\n\nESTAMOS AQUI, Ó DEUS, PRA LOUVAR O TEU NOME, POIS TU NOS TORNASTE SERVOS TEUS. LOUVEMOS A DEUS! A CAMINHO ESTAMOS DA NOVA JERUSALÉM, CIDADE ETERNAL QUE O PAI NOS PREPAROU. MARANATA! ORA VEM, SENHOR JESUS, VEM NOS BUSCAR.\n\nFINAL: (2X) MARANATA! LOUVEMOS A DEUS, LOUVEMOS A DEUS, LOUVEMOS A DEUS. MARANATA!', 'ESTAMOS AQUI, Ó DEUS, PRA LOUVAR O TEU NOME, POIS TU NOS TORNASTE SERVOS TEUS. LOUVEMOS A DEUS! A CAMINHO ESTAMOS DA NOVA JERUSALÉM, CIDADE ETERNAL QUE O PAI NOS PREPAROU. MARANATA! ORA VEM, SENHOR JESUS, VEM NOS BUSCAR. INSTRUMENTOS ESTAMOS AQUI, Ó DEUS, PRA LOUVAR O TEU NOME, POIS TU NOS TORNASTE SERVOS TEUS. LOUVEMOS A DEUS! A CAMINHO ESTAMOS DA NOVA JERUSALÉM, CIDADE ETERNAL QUE O PAI NOS PREPAROU. MARANATA! ORA VEM, SENHOR JESUS, VEM NOS BUSCAR. MARANATA! LOUVEMOS A DEUS, LOUVEMOS A DEUS, LOUVEMOS A DEUS. MARANATA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('621', '621 - ADORAR-TE', 'ADORAR-TE É O MEU MAIOR PRAZER, SEGUIR-TE É O MEU VIVER. INSTRUMENTOS MEU CORAÇÃO TRANSBORDA, SENHOR, DA TUA GRAÇA E DO TEU AMOR; ADORAR-TE É O MEU MAIOR PRAZER, SEGUIR-TE É O MEU VIVER.\n\nCORO NÃO HÁ DEUS COMO TU, Ó SENHOR, DEUS FORTE, PRÍNCIPE DA PAZ; TEU DOMÍNIO É ETERNO, SENHOR, TU REINAS SOBRE TERRA, CÉU E MAR, TU REINAS EM NÓS. INSTRUMENTOS\n\nNOS TEUS ÁTRIOS ENTRAMOS, SENHOR, COM OS LÁBIOS CHEIOS DE LOUVOR, GRAÇAS TE DAMOS PELA PAZ, (PELA PAZ) PELAS BÊNÇÃOS, (PELAS BÊNÇÃOS, SENHOR) POR TÃO GRANDE SALVAÇÃO, POR JESUS ENTRE NÓS.\n\nCORO (2X) NÃO HÁ DEUS COMO TU, Ó SENHOR, DEUS FORTE, PRÍNCIPE DA PAZ; TEU DOMÍNIO É ETERNO, SENHOR, TU REINAS SOBRE TERRA, CÉU E MAR, TU REINAS EM NÓS.', 'ADORAR-TE É O MEU MAIOR PRAZER, SEGUIR-TE É O MEU VIVER. INSTRUMENTOS MEU CORAÇÃO TRANSBORDA, SENHOR, DA TUA GRAÇA E DO TEU AMOR; ADORAR-TE É O MEU MAIOR PRAZER, SEGUIR-TE É O MEU VIVER. CORO NÃO HÁ DEUS COMO TU, Ó SENHOR, DEUS FORTE, PRÍNCIPE DA PAZ; TEU DOMÍNIO É ETERNO, SENHOR, TU REINAS SOBRE TERRA, CÉU E MAR, TU REINAS EM NÓS. INSTRUMENTOS NOS TEUS ÁTRIOS ENTRAMOS, SENHOR, COM OS LÁBIOS CHEIOS DE LOUVOR, GRAÇAS TE DAMOS PELA PAZ, (PELA PAZ) PELAS BÊNÇÃOS, (PELAS BÊNÇÃOS, SENHOR) POR TÃO GRANDE SALVAÇÃO, POR JESUS ENTRE NÓS. CORO  NÃO HÁ DEUS COMO TU, Ó SENHOR, DEUS FORTE, PRÍNCIPE DA PAZ; TEU DOMÍNIO É ETERNO, SENHOR, TU REINAS SOBRE TERRA, CÉU E MAR, TU REINAS EM NÓS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('622', '622 - A BELEZA DA TUA SANTIDADE', 'A BELEZA DA TUA SANTIDADE É MAIOR QUE AS MARAVILHAS DA TERRA, POIS AQUI CADA UMA SE ENCERRA; TUA BELEZA, PORÉM, POR TODA A ETERNIDADE É.\n\nCÉUS E TERRA PASSARÃO, TUA PALAVRA PERMANECERÁ, A MATÉRIA ENVELHECERÁ MAS TEU AMOR PRA SEMPRE EXISTIRÁ. A BELEZA DA TUA SANTIDADE É MAIOR QUE AS MARAVILHAS DA TERRA, POIS AQUI CADA UMA SE ENCERRA; TUA BELEZA, PORÉM, POR TODA A ETERNIDADE É.\n\nCÉUS E TERRA PASSARÃO, TUA PALAVRA PERMANECERÁ, A MATÉRIA ENVELHECERÁ MAS TEU AMOR PRA SEMPRE EXISTIRÁ. INSTRUMENTOS CÉUS E TERRA PASSARÃO... FINAL: TEU AMOR PRA SEMPRE EXISTIRÁ.', 'A BELEZA DA TUA SANTIDADE É MAIOR QUE AS MARAVILHAS DA TERRA, POIS AQUI CADA UMA SE ENCERRA; TUA BELEZA, PORÉM, POR TODA A ETERNIDADE É. CÉUS E TERRA PASSARÃO, TUA PALAVRA PERMANECERÁ, A MATÉRIA ENVELHECERÁ MAS TEU AMOR PRA SEMPRE EXISTIRÁ. A BELEZA DA TUA SANTIDADE É MAIOR QUE AS MARAVILHAS DA TERRA, POIS AQUI CADA UMA SE ENCERRA; TUA BELEZA, PORÉM, POR TODA A ETERNIDADE É. CÉUS E TERRA PASSARÃO, TUA PALAVRA PERMANECERÁ, A MATÉRIA ENVELHECERÁ MAS TEU AMOR PRA SEMPRE EXISTIRÁ. INSTRUMENTOS CÉUS E TERRA PASSARÃO...  TEU AMOR PRA SEMPRE EXISTIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('623', '623 - EU QUERO CANTAR DE GOZO E DE PAZ', 'EU QUERO CANTAR DE GOZO E DE PAZ, SENTIR A ALEGRIA QUE JESUS CRISTO TRAZ; EU QUERO SENTIR A PRESENÇA DO AMOR EM MEU CORAÇÃO, O SALVADOR.\n\nEU QUERO DIZER PARA O MUNDO QUE O SENHOR DEUS ENVIOU SEU FILHO A MORRER NUMA CRUZ, TUDO PORQUE ME AMOU; EU QUERO CHORAR DE ALEGRIA E VIVER A CANTAR, TUDO PORQUE MEU JESUS MORREU PARA ME SALVAR. INSTRUMENTOS\n\nEU QUERO CANTAR DE GOZO E DE PAZ, SENTIR A ALEGRIA QUE JESUS CRISTO TRAZ; EU QUERO SENTIR A PRESENÇA DO AMOR EM MEU CORAÇÃO, O SALVADOR.\n\nEU QUERO DIZER PARA O MUNDO QUE O SENHOR DEUS ENVIOU SEU FILHO A MORRER NUMA CRUZ, TUDO PORQUE ME AMOU; EU QUERO CHORAR DE ALEGRIA E VIVER A CANTAR, TUDO PORQUE MEU JESUS MORREU PARA ME SALVAR.\nBIS', 'EU QUERO CANTAR DE GOZO E DE PAZ, SENTIR A ALEGRIA QUE JESUS CRISTO TRAZ; EU QUERO SENTIR A PRESENÇA DO AMOR EM MEU CORAÇÃO, O SALVADOR. EU QUERO DIZER PARA O MUNDO QUE O SENHOR DEUS ENVIOU SEU FILHO A MORRER NUMA CRUZ, TUDO PORQUE ME AMOU; EU QUERO CHORAR DE ALEGRIA E VIVER A CANTAR, TUDO PORQUE MEU JESUS MORREU PARA ME SALVAR. INSTRUMENTOS EU QUERO DIZER PARA O MUNDO QUE O SENHOR DEUS ENVIOU SEU FILHO A MORRER NUMA CRUZ, TUDO PORQUE ME AMOU; EU QUERO CHORAR DE ALEGRIA E VIVER A CANTAR, TUDO PORQUE MEU JESUS MORREU PARA ME SALVAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('624', '624 - A MINH’ALMA ENGRANDECE', 'A MINH’ALMA ENGRANDECE AO MEU SENHOR E REI. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR?\nBIS\nBIS\n\nDEU-ME LÍNGUA NOVA PRA LOUVÁ-LO, ELE É O SENHOR. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR?\nBIS\nBIS\n\nSEI QUE ELE VOLTA PRA BUSCAR-ME, LEVAR-ME AO CÉU. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR?\nBIS\nBIS', 'A MINH’ALMA ENGRANDECE AO MEU SENHOR E REI. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR? DEU-ME LÍNGUA NOVA PRA LOUVÁ-LO, ELE É O SENHOR. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR? SEI QUE ELE VOLTA PRA BUSCAR-ME, LEVAR-ME AO CÉU. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR?',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('625', '625 - DIGNO É JESUS', 'DIGNO É JESUS DE TODA A GLÓRIA, DIGNO É DE TODO O LOUVOR; TODA A TERRA EXALTE O SEU NOME POIS ELE É REI DOS REIS E É SENHOR. PROSTREMO-NOS DIANTE DO SEU TRONO POIS TODO JOELHO HÁ DE SE DOBRAR E TODA LÍNGUA VAI CONFESSAR QUE ELE É SENHOR, O REI QUE EM TODA A TERRA VAI REINAR.\n\nA TI DAMOS O LOUVOR E A HONRA, SENHOR JESUS, SENHOR JESUS! NOS PROSTRAMOS DIANTE DA TUA GLÓRIA, SENHOR JESUS, SENHOR JESUS! SENHOR JESUS, REI DOS REIS, O SENHOR!', 'DIGNO É JESUS DE TODA A GLÓRIA, DIGNO É DE TODO O LOUVOR; TODA A TERRA EXALTE O SEU NOME POIS ELE É REI DOS REIS E É SENHOR. PROSTREMO-NOS DIANTE DO SEU TRONO POIS TODO JOELHO HÁ DE SE DOBRAR E TODA LÍNGUA VAI CONFESSAR QUE ELE É SENHOR, O REI QUE EM TODA A TERRA VAI REINAR. A TI DAMOS O LOUVOR E A HONRA, SENHOR JESUS, SENHOR JESUS! NOS PROSTRAMOS DIANTE DA TUA GLÓRIA, SENHOR JESUS, SENHOR JESUS! SENHOR JESUS, REI DOS REIS, O SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('626', '626 - OH! CONTEMPLAI', 'OH! CONTEMPLAI E VEDE ESTE AMOR, O AMOR DE JESUS CRISTO POR NÓS; SE DEU, SE ENTREGOU POR TI E POR MIM, NOS RESGATOU, COM SEU SANGUE NOS COMPROU.\n\nDA MORTE ELE É O VENCEDOR, DA VIDA ETERNA ELE É SENHOR, PALAVRA DE DEUS É O SEU NOME, REI DOS REIS, SENHOR DOS SENHORES.\n\nDIGNO É DE RECEBER A GLÓRIA E O LOUVOR E AÇÕES DE GRAÇAS; DIGNO É DE RECEBER A HONRA E O PODER PARA TODO O SEMPRE, JESUS.', 'OH! CONTEMPLAI E VEDE ESTE AMOR, O AMOR DE JESUS CRISTO POR NÓS; SE DEU, SE ENTREGOU POR TI E POR MIM, NOS RESGATOU, COM SEU SANGUE NOS COMPROU. DA MORTE ELE É O VENCEDOR, DA VIDA ETERNA ELE É SENHOR, PALAVRA DE DEUS É O SEU NOME, REI DOS REIS, SENHOR DOS SENHORES. DIGNO É DE RECEBER A GLÓRIA E O LOUVOR E AÇÕES DE GRAÇAS; DIGNO É DE RECEBER A HONRA E O PODER PARA TODO O SEMPRE, JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('627', '627 - NÓS TE ADORAMOS, SENHOR', 'NÓS TE ADORAMOS, SENHOR, POIS ÉS DIGNO DE LOUVOR, NOSSO REI E SALVADOR, NOSSO SENHOR. A TUA GLÓRIA ESTÁ NESTE LUGAR, A TUA PRESENÇA FAZ TRANSBORDAR O NOSSO CORAÇÃO DE LOUVOR. INSTRUMENTOS\n\nCORO A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA.\n\nCORO A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA. (SE FAZ OUVIR) INSTRUMENTOS\n\nEM SANTIDADE, Ó SENHOR, QUEREMOS TE ADORAR, EM UMA SÓ VOZ TUA GLÓRIA EXALTAR.\n\nCORO (2X) A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA.\n\nFINAL: A TUA GLÓRIA ESTÁ NESTE LUGAR, A TUA PRESENÇA FAZ TRANSBORDAR O NOSSO CORAÇÃO DE LOUVOR.', 'NÓS TE ADORAMOS, SENHOR, POIS ÉS DIGNO DE LOUVOR, NOSSO REI E SALVADOR, NOSSO SENHOR. A TUA GLÓRIA ESTÁ NESTE LUGAR, A TUA PRESENÇA FAZ TRANSBORDAR O NOSSO CORAÇÃO DE LOUVOR. INSTRUMENTOS CORO A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA. CORO A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA. (SE FAZ OUVIR) INSTRUMENTOS EM SANTIDADE, Ó SENHOR, QUEREMOS TE ADORAR, EM UMA SÓ VOZ TUA GLÓRIA EXALTAR. CORO  A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA. A TUA GLÓRIA ESTÁ NESTE LUGAR, A TUA PRESENÇA FAZ TRANSBORDAR O NOSSO CORAÇÃO DE LOUVOR.',  1);
