INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('629', '629 - QUE ALEGRIA', 'QUE ALEGRIA É OUVIR O TEU POVO TODO CANTANDO DE FELICIDADE, REGOZIJANDO DIANTE DO ALTAR; QUE ALEGRIA VER O TEU POVO TODO ADORANDO A TI, Ó GRANDE DEUS, E AO TEU FILHO JESUS QUE NOS VEIO SALVAR.\n\nQUE ALEGRIA É ESTAR NA TUA CASA TODOS OS DIAS, COM OS IRMÃOS REUNIDOS, É A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR; QUE ALEGRIA É DEUS OPERANDO NA SUA IGREJA SALVANDO E LIBERTANDO, ORIENTANDO E CURANDO TODO O QUE A ELE VEM.\n\nQUE ALEGRIA É ESTAR NA TUA CASA TODOS OS DIAS, COM OS IRMÃOS REUNIDOS, É A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR; QUE ALEGRIA É DEUS OPERANDO NA SUA IGREJA SALVANDO E LIBERTANDO ORIENTANDO E CURANDO TODO O QUE A ELE VEM.\nBIS', 'QUE ALEGRIA É OUVIR O TEU POVO TODO CANTANDO DE FELICIDADE, REGOZIJANDO DIANTE DO ALTAR; QUE ALEGRIA VER O TEU POVO TODO ADORANDO A TI, Ó GRANDE DEUS, E AO TEU FILHO JESUS QUE NOS VEIO SALVAR. QUE ALEGRIA É ESTAR NA TUA CASA TODOS OS DIAS, COM OS IRMÃOS REUNIDOS, É A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR; QUE ALEGRIA É DEUS OPERANDO NA SUA IGREJA SALVANDO E LIBERTANDO, ORIENTANDO E CURANDO TODO O QUE A ELE VEM. QUE ALEGRIA É ESTAR NA TUA CASA TODOS OS DIAS, COM OS IRMÃOS REUNIDOS, É A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR; QUE ALEGRIA É DEUS OPERANDO NA SUA IGREJA SALVANDO E LIBERTANDO ORIENTANDO E CURANDO TODO O QUE A ELE VEM.',  1);
