INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('638', '638 - JESUS VEIO E SALVOU-ME', 'JESUS VEIO E SALVOU-ME E ASSIM TRANSFORMOU O MEU VIVER, AGORA POSSO ADORÁ-LO E CONFIAR POIS É O MEU SALVADOR.\n\nCORO DIGNO, SÓ ELE É DIGNO DE RECEBER HONRA E LOUVOR, POIS COMIGO ELE ESTÁ, NUNCA VAI ME DEIXAR; DIGNO É O SENHOR. INSTRUMENTOS\n\nVITÓRIAS ELE CONCEDE-ME E COM BÊNÇÃOS ENVOLVE O MEU SER E EM MIM RENOVA A CADA DIA A CERTEZA DA ETERNA SALVAÇÃO.\n\nCORO (2X) DIGNO, SÓ ELE É DIGNO DE RECEBER HONRA E LOUVOR, POIS COMIGO ELE ESTÁ, NUNCA VAI ME DEIXAR; DIGNO É O SENHOR. FINAL: DIGNO É O SENHOR. (3X)', 'JESUS VEIO E SALVOU-ME E ASSIM TRANSFORMOU O MEU VIVER, AGORA POSSO ADORÁ-LO E CONFIAR POIS É O MEU SALVADOR. CORO DIGNO, SÓ ELE É DIGNO DE RECEBER HONRA E LOUVOR, POIS COMIGO ELE ESTÁ, NUNCA VAI ME DEIXAR; DIGNO É O SENHOR. INSTRUMENTOS VITÓRIAS ELE CONCEDE-ME E COM BÊNÇÃOS ENVOLVE O MEU SER E EM MIM RENOVA A CADA DIA A CERTEZA DA ETERNA SALVAÇÃO. CORO  DIGNO, SÓ ELE É DIGNO DE RECEBER HONRA E LOUVOR, POIS COMIGO ELE ESTÁ, NUNCA VAI ME DEIXAR; DIGNO É O SENHOR.  DIGNO É O SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('639', '639 - MOTIVOS TENHO PRA LOUVAR-TE', 'MOTIVOS TENHO PRA LOUVAR-TE, Ó DEUS, MOTIVOS TENHO PRA DIZER: GLÓRIA A DEUS! MOTIVOS TENHO PRA DIZER QUE TU NÃO MUDASTE, MOTIVOS TENHO PRA FALAR DO TEU AMOR.\n\nPELO QUE TU FIZESTE NA TUA OBRA, PELO TEU POVO QUE A TI SE DEDICOU, NA EXISTÊNCIA VIVA DO ESPÍRITO. POR TUA OBRA E TEU PODER, PELO TEU POVO SUSTENTADO AQUI. POR TUA OBRA E TEU PODER, PELO TEU POVO SUSTENTADO AQUI.', 'MOTIVOS TENHO PRA LOUVAR-TE, Ó DEUS, MOTIVOS TENHO PRA DIZER: GLÓRIA A DEUS! MOTIVOS TENHO PRA DIZER QUE TU NÃO MUDASTE, MOTIVOS TENHO PRA FALAR DO TEU AMOR. PELO QUE TU FIZESTE NA TUA OBRA, PELO TEU POVO QUE A TI SE DEDICOU, NA EXISTÊNCIA VIVA DO ESPÍRITO. POR TUA OBRA E TEU PODER, PELO TEU POVO SUSTENTADO AQUI. POR TUA OBRA E TEU PODER, PELO TEU POVO SUSTENTADO AQUI.',  1);
