INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('641', '641 - A LUZ DO MUNDO', 'A LUZ DO MUNDO, O LÍRIO DOS VALES, A FONTE DE ÁGUAS VIVAS, EMANUEL; O VERBO VIVO, RAIZ DE DAVI, ESTRELA DA MANHÃ, REI DOS REIS. COMO CANTAR O NOSSO AMOR E NOSSA GRATIDÃO POR TEU FAVOR?\n\nCORO GLÓRIA, HONRA E TODO LOUVOR SEJAM A CRISTO, NOSSO SENHOR; GLÓRIA, HONRA E TODO LOUVOR DAI A CRISTO, ETERNO SENHOR.\n\nVENCEU A MORTE (VENCEU A MORTE) VARÕES NOS RESGATOU, (NOS RESGATOU) VARÕES MOSTROU A TODA A TERRA QUE É SENHOR; NADA NO MUNDO (NADA NO MUNDO) VARÕES O IMPEDIRÁ. (O IMPEDIRÁ) VARÕES JESUS RESSUSCITOU E VAI VOLTAR.\n\nCORO (2X) GLÓRIA, HONRA E TODO LOUVOR SEJAM A CRISTO, NOSSO SENHOR; GLÓRIA, HONRA E TODO LOUVOR DAI A CRISTO, ETERNO SENHOR. FINAL: ETERNO SENHOR.', 'A LUZ DO MUNDO, O LÍRIO DOS VALES, A FONTE DE ÁGUAS VIVAS, EMANUEL; O VERBO VIVO, RAIZ DE DAVI, ESTRELA DA MANHÃ, REI DOS REIS. COMO CANTAR O NOSSO AMOR E NOSSA GRATIDÃO POR TEU FAVOR? CORO GLÓRIA, HONRA E TODO LOUVOR SEJAM A CRISTO, NOSSO SENHOR; GLÓRIA, HONRA E TODO LOUVOR DAI A CRISTO, ETERNO SENHOR. VENCEU A MORTE (VENCEU A MORTE) VARÕES NOS RESGATOU, (NOS RESGATOU) VARÕES MOSTROU A TODA A TERRA QUE É SENHOR; NADA NO MUNDO (NADA NO MUNDO) VARÕES O IMPEDIRÁ. (O IMPEDIRÁ) VARÕES JESUS RESSUSCITOU E VAI VOLTAR. CORO  GLÓRIA, HONRA E TODO LOUVOR SEJAM A CRISTO, NOSSO SENHOR; GLÓRIA, HONRA E TODO LOUVOR DAI A CRISTO, ETERNO SENHOR.  ETERNO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('642', '642 - EM NOSSOS LÁBIOS', 'EM NOSSOS LÁBIOS SEMPRE HAVERÁ UM CANTO A TI, JESUS, POR TUDO QUE TENS FEITO E POR TUDO QUE FARÁS EM NOSSAS VIDAS.\n\nQUEREMOS EXPRESSAR NOSSO AMOR E GRATIDÃO A TI, JESUS, PELA TUA SALVAÇÃO E PELO TEU PERDÃO, RESGATANDO-NOS PRA TUA ADORAÇÃO, O TEU AMOR NOS SUSTENTA, SENHOR, A TUA GRAÇA RENOVA OS CORAÇÕES E NOS BASTA.\n\nCORO TODO LOUVOR E ADORAÇÃO PERTENCEM A TI ETERNAMENTE, NOSSAS VIDAS, Ó SENHOR, ENTREGAMOS EM TEU ALTAR PRA TE ADORAR, JESUS. INSTRUMENTOS FAZ DE NÓS, SENHOR, UM VASO EM TUAS MÃOS PARA EXPRESSAR.\n\nCORO TODO LOUVOR E ADORAÇÃO PERTENCEM A TI ETERNAMENTE, NOSSAS VIDAS, Ó SENHOR, ENTREGAMOS EM TEU ALTAR PRA TE ADORAR, JESUS. INSTRUMENTOS\n\nEM NOSSOS LÁBIOS SEMPRE HAVERÁ UM CANTO A TI, JESUS, POR TUDO QUE TENS FEITO E POR TUDO QUE FARÁS EM NOSSAS VIDAS.', 'EM NOSSOS LÁBIOS SEMPRE HAVERÁ UM CANTO A TI, JESUS, POR TUDO QUE TENS FEITO E POR TUDO QUE FARÁS EM NOSSAS VIDAS. QUEREMOS EXPRESSAR NOSSO AMOR E GRATIDÃO A TI, JESUS, PELA TUA SALVAÇÃO E PELO TEU PERDÃO, RESGATANDO-NOS PRA TUA ADORAÇÃO, O TEU AMOR NOS SUSTENTA, SENHOR, A TUA GRAÇA RENOVA OS CORAÇÕES E NOS BASTA. CORO TODO LOUVOR E ADORAÇÃO PERTENCEM A TI ETERNAMENTE, NOSSAS VIDAS, Ó SENHOR, ENTREGAMOS EM TEU ALTAR PRA TE ADORAR, JESUS. INSTRUMENTOS FAZ DE NÓS, SENHOR, UM VASO EM TUAS MÃOS PARA EXPRESSAR. CORO TODO LOUVOR E ADORAÇÃO PERTENCEM A TI ETERNAMENTE, NOSSAS VIDAS, Ó SENHOR, ENTREGAMOS EM TEU ALTAR PRA TE ADORAR, JESUS. INSTRUMENTOS',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('643', '643 - TODO LOUVOR SEJA DADO A DEUS', 'TODO LOUVOR SEJA DADO A DEUS, POR TUDO QUE TEM FEITO EM NOSSAS VIDAS, NOSSA GRATIDÃO A JESUS O REI, FOI ELE QUEM NOS SUSTENTOU ATÉ AQUI.\n\nPOR ISSO RENDEMOS UM LOUVOR AO REI JESUS E ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR. CORO LOUVEMOS AO SENHOR QUE É JUSTO E PODEROSO, É QUEM NOS GUIA, A SUA GRAÇA ESTÁ EM NOSSO MEIO, A SUA PRESENÇA É VIVA A CADA INSTANTE. INSTRUMENTOS\n\nTODO LOUVOR SEJA DADO A DEUS, POR TUDO QUE TEM FEITO EM NOSSAS VIDAS, NOSSA GRATIDÃO A JESUS O REI, FOI ELE QUEM NOS SUSTENTOU ATÉ AQUI. POR ISSO RENDEMOS UM LOUVOR AO REI JESUS E ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR.\n\nCORO (2X) LOUVEMOS AO SENHOR QUE É JUSTO E PODEROSO, É QUEM NOS GUIA, A SUA GRAÇA ESTÁ EM NOSSO MEIO, A SUA PRESENÇA É VIVA A CADA INSTANTE. FINAL: DE NOSSAS VIDAS.', 'TODO LOUVOR SEJA DADO A DEUS, POR TUDO QUE TEM FEITO EM NOSSAS VIDAS, NOSSA GRATIDÃO A JESUS O REI, FOI ELE QUEM NOS SUSTENTOU ATÉ AQUI. POR ISSO RENDEMOS UM LOUVOR AO REI JESUS E ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR. CORO LOUVEMOS AO SENHOR QUE É JUSTO E PODEROSO, É QUEM NOS GUIA, A SUA GRAÇA ESTÁ EM NOSSO MEIO, A SUA PRESENÇA É VIVA A CADA INSTANTE. INSTRUMENTOS TODO LOUVOR SEJA DADO A DEUS, POR TUDO QUE TEM FEITO EM NOSSAS VIDAS, NOSSA GRATIDÃO A JESUS O REI, FOI ELE QUEM NOS SUSTENTOU ATÉ AQUI. POR ISSO RENDEMOS UM LOUVOR AO REI JESUS E ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR. CORO  LOUVEMOS AO SENHOR QUE É JUSTO E PODEROSO, É QUEM NOS GUIA, A SUA GRAÇA ESTÁ EM NOSSO MEIO, A SUA PRESENÇA É VIVA A CADA INSTANTE.  DE NOSSAS VIDAS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('644', '644 - LOUVAMOS-TE, Ó DEUS', 'LOUVAMOS-TE, Ó DEUS, PELO DOM DE JESUS, QUE POR NÓS, PECADORES, FOI MORTO NA CRUZ.\n\nCORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. LOUVAMOS-TE, Ó DEUS, PELO ESPÍRITO E SUA LUZ, QUE AS TREVAS DISSIPA, E A CRISTO CONDUZ\n\nCORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. LOUVAMOS-TE, SENHOR, Ó CORDEIRO DE DEUS, FOSTE MORTO, MAS VIVES, ETERNO, NOS CÉUS.\n\nCORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. VEM ENCHER-NOS, Ó DEUS, DE CELESTE ARDOR E FAZER-NOS SENTIR TEU IMENSO AMOR.', 'LOUVAMOS-TE, Ó DEUS, PELO DOM DE JESUS, QUE POR NÓS, PECADORES, FOI MORTO NA CRUZ. CORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. LOUVAMOS-TE, Ó DEUS, PELO ESPÍRITO E SUA LUZ, QUE AS TREVAS DISSIPA, E A CRISTO CONDUZ CORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. LOUVAMOS-TE, SENHOR, Ó CORDEIRO DE DEUS, FOSTE MORTO, MAS VIVES, ETERNO, NOS CÉUS. CORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. VEM ENCHER-NOS, Ó DEUS, DE CELESTE ARDOR E FAZER-NOS SENTIR TEU IMENSO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('645', '645 - COROAI', 'COROAI O QUE É DIGNO E BENDITO SOBRE A TERRA, EXALTAI O SEU NOME COM LOUVORES DE VITÓRIA; O SEU NOME ENTRE TODAS AS NAÇÕES SERÁ LOUVADO; (O CORDEIRO ENTRE TODAS AS NAÇÕES SERÁ LOUVADO) OH! JESUS, MEU SENHOR, REINARÁ.\n\nREINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA, TODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ; CONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU, (DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU) SOBRE AS NUVENS DESCERÁ E REINARÁ. INSTRUMENTOS\n\nREINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA, TODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ; CONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU, (DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU) SOBRE AS NUVENS DESCERÁ E REINARÁ. FINAL: COROAI.', 'COROAI O QUE É DIGNO E BENDITO SOBRE A TERRA, EXALTAI O SEU NOME COM LOUVORES DE VITÓRIA; O SEU NOME ENTRE TODAS AS NAÇÕES SERÁ LOUVADO; (O CORDEIRO ENTRE TODAS AS NAÇÕES SERÁ LOUVADO) OH! JESUS, MEU SENHOR, REINARÁ. REINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA, TODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ; CONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU, (DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU) SOBRE AS NUVENS DESCERÁ E REINARÁ. INSTRUMENTOS REINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA, TODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ; CONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU, (DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU) SOBRE AS NUVENS DESCERÁ E REINARÁ.  COROAI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('646', '646 - AGRADECIDOS SOMOS A TI', 'AGRADECIDOS SOMOS A TI, MEU REI JESUS, PELA GRAÇA E PELA VIDA QUE HOJE HÁ EM NÓS; GRATOS SOMOS POR TÃO GRANDE AMOR DEMONSTRADO NO MADEIRO, TU ÉS MEU SALVADOR.\n\nALELUIA TE DAMOS, Ó MEU SENHOR PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; POR NÓS JESUS MORREU, QUE DOR ELE SENTIU, MAS AO TERCEIRO DIA ELE RESSURGIU.\n\nALELUIA TE DAMOS, Ó MEU SENHOR, PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; E HOJE VIVO ESTÁ À DESTRA DE DEUS PAI AGUARDANDO A HORA CERTA PARA VIR NOS BUSCAR.\n\nALELUIA TE DAMOS, Ó MEU SENHOR, PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; POR NÓS JESUS MORREU, MAS AO TERCEIRO DIA ELE RESSURGIU.\n\nFINAL: AGRADECIDOS SOMOS A TI, MEU REI JESUS, PELA GRAÇA E PELA VIDA QUE HOJE HÁ EM NÓS, QUE HOJE HÁ EM NÓS.', 'AGRADECIDOS SOMOS A TI, MEU REI JESUS, PELA GRAÇA E PELA VIDA QUE HOJE HÁ EM NÓS; GRATOS SOMOS POR TÃO GRANDE AMOR DEMONSTRADO NO MADEIRO, TU ÉS MEU SALVADOR. ALELUIA TE DAMOS, Ó MEU SENHOR PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; POR NÓS JESUS MORREU, QUE DOR ELE SENTIU, MAS AO TERCEIRO DIA ELE RESSURGIU. ALELUIA TE DAMOS, Ó MEU SENHOR, PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; E HOJE VIVO ESTÁ À DESTRA DE DEUS PAI AGUARDANDO A HORA CERTA PARA VIR NOS BUSCAR. ALELUIA TE DAMOS, Ó MEU SENHOR, PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; POR NÓS JESUS MORREU, MAS AO TERCEIRO DIA ELE RESSURGIU. AGRADECIDOS SOMOS A TI, MEU REI JESUS, PELA GRAÇA E PELA VIDA QUE HOJE HÁ EM NÓS, QUE HOJE HÁ EM NÓS.',  1);
