INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('657', '657 - OS CÉUS PROCLAMAM', 'OS CÉUS PROCLAMAM A SUA GLÓRIA E O FIRMAMENTO AS OBRAS DAS SUAS MÃOS; UM DIA PASSA, O OUTRO VEM, É ELE QUEM DÁ VITÓRIAS AO SEU POVO; DO ALTO VEIO A SUA MÃO TOCOU-ME E DEU-ME ALEGRIA DE VIVER.\nBIS\n\nCORO (2X) GLÓRIA AO MEU JESUS, POIS DELE SOU E ELE É MEU.\n\nSE FAZ OUVIR A SUA VOZ POR TODA A TERRA E AOS CONFINS DO MUNDO; SINTO A PRESENÇA DE DEUS TÃO CALMA POIS QUEM É DEUS SENÃO O NOSSO DEUS? COMIGO ESTÁ, NÃO VOU DEIXAR QUE QUALQUER COISA QUEBRE ESSA COMUNHÃO.\nBIS\n\nCORO (2X) GLÓRIA AO MEU JESUS, POIS DELE SOU E ELE É MEU.', 'OS CÉUS PROCLAMAM A SUA GLÓRIA E O FIRMAMENTO AS OBRAS DAS SUAS MÃOS; UM DIA PASSA, O OUTRO VEM, É ELE QUEM DÁ VITÓRIAS AO SEU POVO; DO ALTO VEIO A SUA MÃO TOCOU-ME E DEU-ME ALEGRIA DE VIVER. CORO  GLÓRIA AO MEU JESUS, POIS DELE SOU E ELE É MEU. SE FAZ OUVIR A SUA VOZ POR TODA A TERRA E AOS CONFINS DO MUNDO; SINTO A PRESENÇA DE DEUS TÃO CALMA POIS QUEM É DEUS SENÃO O NOSSO DEUS? COMIGO ESTÁ, NÃO VOU DEIXAR QUE QUALQUER COISA QUEBRE ESSA COMUNHÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('658', '658 - MEDITAREI NAS MARAVILHAS DO SENHOR', 'MEDITAREI NAS MARAVILHAS DO SENHOR E FALAREI DO SEU PODER ENTRE AS NAÇÕES; CELEBRAREI A MAJESTADE DO SENHOR, PROCLAMAREI SUA JUSTIÇA SOBRE A TERRA. PORQUE GRANDES SÃO AS TUAS OBRAS, TU ÉS FIEL ETERNAMENTE.\n\nEXALTAR-TE-EI, Ó DEUS, POIS TU ÉS MEU REI, GLORIFICAREI TEU NOME ENQUANTO EU VIVER. PERTO ESTÁ O SENHOR DOS QUE O INVOCAM.\n\n4X\nBIS', 'MEDITAREI NAS MARAVILHAS DO SENHOR E FALAREI DO SEU PODER ENTRE AS NAÇÕES; CELEBRAREI A MAJESTADE DO SENHOR, PROCLAMAREI SUA JUSTIÇA SOBRE A TERRA. PORQUE GRANDES SÃO AS TUAS OBRAS, TU ÉS FIEL ETERNAMENTE. EXALTAR-TE-EI, Ó DEUS, POIS TU ÉS MEU REI, GLORIFICAREI TEU NOME ENQUANTO EU VIVER. PERTO ESTÁ O SENHOR DOS QUE O INVOCAM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('659', '659 - CELEBRAI COM JÚBILO AO SENHOR', 'CELEBRAI COM JÚBILO AO SENHOR TODAS AS TERRAS, SERVI AO SENHOR COM ALEGRIA; APRESENTAI-VOS DIANTE DELE COM CÂNTICOS, SABEI QUE O SENHOR É DEUS.\n\nFOI ELE QUEM NOS FEZ E DELE SOMOS, SOMOS O SEU POVO E REBANHO DO SEU PASTO. ENTRAI EM SUAS PORTAS COM AÇÕES DE GRAÇAS E NOS SEUS ÁTRIOS COM HINOS DE LOUVOR; RENDEI-LHE GRAÇAS E BENDIZEI-LHE O NOME PORQUE O SENHOR É BOM.\nBIS\n\nSUA MISERICÓRDIA DURA PARA SEMPRE, DE GERAÇÃO EM GERAÇÃO,SUA FIDELIDADE. SUA MISERICÓRDIA DURA PARA SEMPRE, DE GERAÇÃO EM GERAÇÃO, SUA FIDELIDADE.', 'CELEBRAI COM JÚBILO AO SENHOR TODAS AS TERRAS, SERVI AO SENHOR COM ALEGRIA; APRESENTAI-VOS DIANTE DELE COM CÂNTICOS, SABEI QUE O SENHOR É DEUS. FOI ELE QUEM NOS FEZ E DELE SOMOS, SOMOS O SEU POVO E REBANHO DO SEU PASTO. ENTRAI EM SUAS PORTAS COM AÇÕES DE GRAÇAS E NOS SEUS ÁTRIOS COM HINOS DE LOUVOR; RENDEI-LHE GRAÇAS E BENDIZEI-LHE O NOME PORQUE O SENHOR É BOM. SUA MISERICÓRDIA DURA PARA SEMPRE, DE GERAÇÃO EM GERAÇÃO,SUA FIDELIDADE. SUA MISERICÓRDIA DURA PARA SEMPRE, DE GERAÇÃO EM GERAÇÃO, SUA FIDELIDADE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('660', '660 - O MEU CORAÇÃO', 'O MEU CORAÇÃO FERVE COM PALAVRAS BOAS, FALO DO QUE TENHO FEITO NO TOCANTE AO REI; A MINHA LÍNGUA É A PENA DE UM DESTRO ESCRITOR, TU ÉS MAIS FORMOSO QUE OS FILHOS DOS HOMENS;\n\nA GRAÇA SE DERRAMOU EM TEUS LÁBIOS, POR ISSO DEUS TE ABENÇOOU PARA SEMPRE; À COXA CINGE A TUA ESPADA, Ó VALENTE, COM A TUA GLÓRIA E TUA MAJESTADE. INSTRUMENTOS\n\nE NESTE ESPLENDOR TEU CAVALGA PROSPERAMENTE, PELA VERDADE E JUSTIÇA E A MANSIDÃO; AS TUAS FLECHAS SÃO AGUDAS NO CORAÇÃO DOS INIMIGOS DO REI,\n\nE POR ELAS OS POVOS SE QUEDAM DIANTE DE TI; O TEU TRONO, Ó DEUS, É ETERNO E PERPÉTUO, O CETRO DO TEU REINO É DE EQUIDADE, TU AMAS JUSTIÇA E ODEIAS MALDADE, POR ISSO O TEU DEUS TE UNGIU COM ALEGRIA.', 'O MEU CORAÇÃO FERVE COM PALAVRAS BOAS, FALO DO QUE TENHO FEITO NO TOCANTE AO REI; A MINHA LÍNGUA É A PENA DE UM DESTRO ESCRITOR, TU ÉS MAIS FORMOSO QUE OS FILHOS DOS HOMENS; A GRAÇA SE DERRAMOU EM TEUS LÁBIOS, POR ISSO DEUS TE ABENÇOOU PARA SEMPRE; À COXA CINGE A TUA ESPADA, Ó VALENTE, COM A TUA GLÓRIA E TUA MAJESTADE. INSTRUMENTOS E NESTE ESPLENDOR TEU CAVALGA PROSPERAMENTE, PELA VERDADE E JUSTIÇA E A MANSIDÃO; AS TUAS FLECHAS SÃO AGUDAS NO CORAÇÃO DOS INIMIGOS DO REI, E POR ELAS OS POVOS SE QUEDAM DIANTE DE TI; O TEU TRONO, Ó DEUS, É ETERNO E PERPÉTUO, O CETRO DO TEU REINO É DE EQUIDADE, TU AMAS JUSTIÇA E ODEIAS MALDADE, POR ISSO O TEU DEUS TE UNGIU COM ALEGRIA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('661', '661 - ÉS O MAIS FORMOSO, JESUS', 'ÉS O MAIS FORMOSO, JESUS, GRAÇA HÁ EM TEU FALAR, CONSAGRO-TE O QUE COMPUS, NESTE CANTO TE ADORAR; CINGE A TUA ESPADA, HERÓI, DE GLÓRIA E MAJESTADE, COM PODER HÁS DE ENSINAR JUSTIÇA E VERDADE.\n\nTEUS PERFUMES SE EXALAM: CÁSSIA, MIRRA E ALOÉS; DE AMOR TEUS OLHOS BRILHAM, ME CONDUZEM PELA FÉ. O TEU TRONO, Ó REI, É PODER POR TODA A ETERNIDADE, AMAS A JUSTIÇA, PORÉM, ODEIAS A IMPIEDADE.\n\n“OUVE, FILHA, VÊ, DÁ ATENÇÃO, FALA O TEU SALVADOR, PROSTRA-TE ANTE MIM, ENTÃO, ADORA O DEUS DE AMOR; NOIVA MINHA, ALMEJAS O CÉU, VESTES DE OURO TE DAREI, TÃO FORMOSA E FIEL, À MINHA GLÓRIA A LEVAREI”.\n\n“TEUS VESTIDOS SÃO BORDADOS DE OURO PURO DE OFIR, TEU NOME FAREI LEMBRADO, EU TE QUERO JUNTO A MIM; COM ALEGRIA E TODA GLÓRIA, FIÉIS TE SEGUIRÃO E COM CANTO DE VITÓRIA, PRA SEMPRE TE LOUVARÃO”.', 'ÉS O MAIS FORMOSO, JESUS, GRAÇA HÁ EM TEU FALAR, CONSAGRO-TE O QUE COMPUS, NESTE CANTO TE ADORAR; CINGE A TUA ESPADA, HERÓI, DE GLÓRIA E MAJESTADE, COM PODER HÁS DE ENSINAR JUSTIÇA E VERDADE. TEUS PERFUMES SE EXALAM: CÁSSIA, MIRRA E ALOÉS; DE AMOR TEUS OLHOS BRILHAM, ME CONDUZEM PELA FÉ. O TEU TRONO, Ó REI, É PODER POR TODA A ETERNIDADE, AMAS A JUSTIÇA, PORÉM, ODEIAS A IMPIEDADE. OUVE, FILHA, VÊ, DÁ ATENÇÃO, FALA O TEU SALVADOR, PROSTRA-TE ANTE MIM, ENTÃO, ADORA O DEUS DE AMOR; NOIVA MINHA, ALMEJAS O CÉU, VESTES DE OURO TE DAREI, TÃO FORMOSA E FIEL, À MINHA GLÓRIA A LEVAREI. TEUS VESTIDOS SÃO BORDADOS DE OURO PURO DE OFIR, TEU NOME FAREI LEMBRADO, EU TE QUERO JUNTO A MIM; COM ALEGRIA E TODA GLÓRIA, FIÉIS TE SEGUIRÃO E COM CANTO DE VITÓRIA, PRA SEMPRE TE LOUVARÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('662', '662 - ONIPOTENTE DEUS', 'ONIPOTENTE DEUS, TUAS OBRAS TE EXALTAM, SENHOR; ONIPOTENTE DEUS, CÉUS E TERRA TE RENDEM LOUVOR. QUE É O HOMEM QUE DELE TE LEMBRES E O FILHO DO HOMEM PARA O VISITAR? PORÉM, HONRA DESTE PRA GOVERNAR. INSTRUMENTOS\n\nSENHOR JESUS CRISTO, MINHA VIDA VIESTE SALVAR; SENHOR JESUS CRISTO, COM TEU SANGUE ME PURIFICAR. COMO SERVO VIESTE, NA CRUZ TE HUMILHASTE, MAS VIVO NA GLÓRIA HOJE ESTÁS, ÉS MEU REI, SENHOR E, SEI, VOLTARÁS. INSTRUMENTOS\n\nESPÍRITO SANTO, Ó VEM E HABITA EM MEU SER, ESPÍRITO SANTO, VIVIFICA-ME COM TEU PODER; TAL COMO ÁGUA QUE JORRA, E O VENTO QUE SOPRA, E O FOGO ARDENTE, ENFIM, SANTO ESPÍRITO DE DEUS VIVE EM MIM.\nBIS', 'ONIPOTENTE DEUS, TUAS OBRAS TE EXALTAM, SENHOR; ONIPOTENTE DEUS, CÉUS E TERRA TE RENDEM LOUVOR. QUE É O HOMEM QUE DELE TE LEMBRES E O FILHO DO HOMEM PARA O VISITAR? PORÉM, HONRA DESTE PRA GOVERNAR. INSTRUMENTOS SENHOR JESUS CRISTO, MINHA VIDA VIESTE SALVAR; SENHOR JESUS CRISTO, COM TEU SANGUE ME PURIFICAR. COMO SERVO VIESTE, NA CRUZ TE HUMILHASTE, MAS VIVO NA GLÓRIA HOJE ESTÁS, ÉS MEU REI, SENHOR E, SEI, VOLTARÁS. INSTRUMENTOS ESPÍRITO SANTO, Ó VEM E HABITA EM MEU SER, ESPÍRITO SANTO, VIVIFICA-ME COM TEU PODER; TAL COMO ÁGUA QUE JORRA, E O VENTO QUE SOPRA, E O FOGO ARDENTE, ENFIM, SANTO ESPÍRITO DE DEUS VIVE EM MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('663', '663 - LOUVAI AO SENHOR TODAS AS NAÇÕES', 'LOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O TODOS OS POVOS. LOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O TODOS OS POVOS. LOUVAI AO SENHOR, LOUVAI AO SENHOR.\n\nPORQUE A SUA BENIGNIDADE É GRANDE PARA CONOSCO, E A VERDADE DO SENHOR DURA PARA SEMPRE. LOUVAI AO SENHOR, LOUVAI AO SENHOR.', 'LOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O TODOS OS POVOS. LOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O TODOS OS POVOS. LOUVAI AO SENHOR, LOUVAI AO SENHOR. PORQUE A SUA BENIGNIDADE É GRANDE PARA CONOSCO, E A VERDADE DO SENHOR DURA PARA SEMPRE. LOUVAI AO SENHOR, LOUVAI AO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('664', '664 - QUÃO AMÁVEIS SÃO', 'QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS, SENHOR DOS EXÉRCITOS! A MINH’ALMA ESTÁ ANELANTE E DESFALECE PELOS ÁTRIOS DO SENHOR.\n\nCORO O MEU CORAÇÃO E A MINHA CARNE CLAMAM PELO DEUS VIVO! QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS SENHOR DOS EXÉRCITOS\n\nATÉ O PARDAL ENCONTROU CASA E A ANDORINHA ENCONTROU NINHO PARA SI JUNTO AO TEU ALTAR, SENHOR DOS EXÉRCITOS, REI MEU E DEUS MEU.\n\nCORO O MEU CORAÇÃO E A MINHA CARNE CLAMAM PELO DEUS VIVO! QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS SENHOR DOS EXÉRCITOS', 'QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS, SENHOR DOS EXÉRCITOS! A MINH’ALMA ESTÁ ANELANTE E DESFALECE PELOS ÁTRIOS DO SENHOR. CORO O MEU CORAÇÃO E A MINHA CARNE CLAMAM PELO DEUS VIVO! QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS SENHOR DOS EXÉRCITOS ATÉ O PARDAL ENCONTROU CASA E A ANDORINHA ENCONTROU NINHO PARA SI JUNTO AO TEU ALTAR, SENHOR DOS EXÉRCITOS, REI MEU E DEUS MEU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('665', '665 - BENDIZEI AO SENHOR', 'BENDIZEI AO SENHOR VÓS TODOS, SERVOS DO SENHOR, QUE ASSISTIS NA CASA DO SENHOR NAS HORAS DA NOITE. ERGUEI AS MÃOS NO SANTUÁRIO E BENDIZEI AO SENHOR; DE SIÃO TE ABENÇOE O SENHOR, CRIADOR DO CÉU E DA TERRA.\nBIS', 'BENDIZEI AO SENHOR VÓS TODOS, SERVOS DO SENHOR, QUE ASSISTIS NA CASA DO SENHOR NAS HORAS DA NOITE. ERGUEI AS MÃOS NO SANTUÁRIO E BENDIZEI AO SENHOR; DE SIÃO TE ABENÇOE O SENHOR, CRIADOR DO CÉU E DA TERRA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('666', '666 - TAL QUAL ESTOU', 'TAL QUAL ESTOU, EIS-ME, SENHOR, POIS O TEU SANGUE REMIDOR VERTESTE PELO PECADOR; Ó SALVADOR, ME ACHEGO A TI!\n\nTAL QUAL ESTOU, SEM ESPERAR QUE POSSA A VIDA MELHORAR, EM TI SÓ QUERO CONFIAR; Ó SALVADOR, ME ACHEGO A TI!\n\nTAL QUAL ESTOU E SEM PODER, AS FALTAS PODES PREENCHER E TUDO QUANTO ME É MISTER; Ó SALVADOR, ME ACHEGO A TI!\n\nTAL QUAL ESTOU ME ACEITARÁS E TU, MINH’ALMA LIMPARÁS, COM TEU AMOR ME COBRIRÁS; Ó SALVADOR, ME ACHEGO A TI!', 'TAL QUAL ESTOU, EIS-ME, SENHOR, POIS O TEU SANGUE REMIDOR VERTESTE PELO PECADOR; Ó SALVADOR, ME ACHEGO A TI! TAL QUAL ESTOU, SEM ESPERAR QUE POSSA A VIDA MELHORAR, EM TI SÓ QUERO CONFIAR; Ó SALVADOR, ME ACHEGO A TI! TAL QUAL ESTOU E SEM PODER, AS FALTAS PODES PREENCHER E TUDO QUANTO ME É MISTER; Ó SALVADOR, ME ACHEGO A TI! TAL QUAL ESTOU ME ACEITARÁS E TU, MINH’ALMA LIMPARÁS, COM TEU AMOR ME COBRIRÁS; Ó SALVADOR, ME ACHEGO A TI!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('667', '667 - JUSTO ÉS, SENHOR', 'JUSTO ÉS, SENHOR, NOS TEUS SANTOS CAMINHOS, ÉS DIGNO EM TODAS AS TUAS OBRAS. JUSTO ÉS, SENHOR, NOS TEUS SANTOS CAMINHOS, ÉS DIGNO EM TODAS AS TUAS OBRAS.\n\nBEM PERTO ESTÁS, SENHOR BEM PERTO ESTÁS DOS QUE TE INVOCAM, DE TODOS QUE TE INVOCAM EM VERDADE. FINAL: ALELUIA! ALELUIA!\nBIS', 'JUSTO ÉS, SENHOR, NOS TEUS SANTOS CAMINHOS, ÉS DIGNO EM TODAS AS TUAS OBRAS. JUSTO ÉS, SENHOR, NOS TEUS SANTOS CAMINHOS, ÉS DIGNO EM TODAS AS TUAS OBRAS. BEM PERTO ESTÁS, SENHOR BEM PERTO ESTÁS DOS QUE TE INVOCAM, DE TODOS QUE TE INVOCAM EM VERDADE.  ALELUIA! ALELUIA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('668', '668 - UM POBRE CEGO', 'UM POBRE CEGO À BEIRA DE UMA ESTRADA ENVOLTO EM TRAPOS, TREVAS E DESDÉM; PEDIA ESMOLAS DE ALMA ANGUSTIADA, VEIO JESUS E TROUXE A LUZ E O BEM.\n\nCORO AO VIR JESUS A TEMPESTADE CESSA, AO VIR JESUS AS LÁGRIMAS SE VÃO; ELE TRANSFORMA TODA NOSSA VIDA ILUMINANDO A NEGRA ESCURIDÃO.\n\nHOJE, TAMBÉM, O PECADOR DEPARA, EM CRISTO, LIVRAMENTO DE AFLIÇÃO, EM MEIO À TENTAÇÃO QUE O DESTROÇARA, VEIO JESUS E TROUXE A SALVAÇÃO.\n\nCORO AO VIR JESUS A TEMPESTADE CESSA, AO VIR JESUS AS LÁGRIMAS SE VÃO; ELE TRANSFORMA TODA NOSSA VIDA ILUMINANDO A NEGRA ESCURIDÃO.', 'UM POBRE CEGO À BEIRA DE UMA ESTRADA ENVOLTO EM TRAPOS, TREVAS E DESDÉM; PEDIA ESMOLAS DE ALMA ANGUSTIADA, VEIO JESUS E TROUXE A LUZ E O BEM. CORO AO VIR JESUS A TEMPESTADE CESSA, AO VIR JESUS AS LÁGRIMAS SE VÃO; ELE TRANSFORMA TODA NOSSA VIDA ILUMINANDO A NEGRA ESCURIDÃO. HOJE, TAMBÉM, O PECADOR DEPARA, EM CRISTO, LIVRAMENTO DE AFLIÇÃO, EM MEIO À TENTAÇÃO QUE O DESTROÇARA, VEIO JESUS E TROUXE A SALVAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('669', '669 - DEUS DOS ANTIGOS', 'DEUS DOS ANTIGOS, CUJA FORTE MÃO REGE E SUSTÉM OS ASTROS DA AMPLIDÃO; DO CINTILANTE CÉU INSPIRADOR, COM GRATIDÃO CANTAMOS TEU LOUVOR.\n\nJÁ NO PASSADO FOSTE NOSSA LUZ, HOJE ÉS FAROL QUE À VIDA NOS CONDUZ; SÊ NOSSO ESTEIO, GUIA E PROTEÇÃO, TUA PALAVRA É LEI E DIREÇÃO.\n\nTEU POVO, Ó DEUS, RESTAURA EM SEU LABOR, TRANSFORMA A NOITE EM DIA DE ESPLENDOR; AS NOSSAS VIDAS VEM FORTALECER PARA TEU NOME SEMPRE ENGRANDECER. AMÉM.', 'DEUS DOS ANTIGOS, CUJA FORTE MÃO REGE E SUSTÉM OS ASTROS DA AMPLIDÃO; DO CINTILANTE CÉU INSPIRADOR, COM GRATIDÃO CANTAMOS TEU LOUVOR. JÁ NO PASSADO FOSTE NOSSA LUZ, HOJE ÉS FAROL QUE À VIDA NOS CONDUZ; SÊ NOSSO ESTEIO, GUIA E PROTEÇÃO, TUA PALAVRA É LEI E DIREÇÃO. TEU POVO, Ó DEUS, RESTAURA EM SEU LABOR, TRANSFORMA A NOITE EM DIA DE ESPLENDOR; AS NOSSAS VIDAS VEM FORTALECER PARA TEU NOME SEMPRE ENGRANDECER. AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('670', '670 - SE PAZ, A MAIS DOCE', 'SE PAZ, A MAIS DOCE ME DERES GOZAR, SE DOR A MAIS FORTE SOFRER, OH! SEJA O QUE FOR, TU ME FAZES SABER QUE FELIZ COM JESUS SEMPRE SOU!\n\nCORO SOU FELIZ, (SOU FELIZ) COM JESUS, (COM JESUS) SOU FELIZ, COM JESUS, MEU SENHOR!\n\nEMBORA ME ASSALTE O CRUEL TENTADOR E ATAQUE COM VIS TENTAÇÕES; OH! CERTO ESTOU, APESAR DE AFLIÇÕES, QUE FELIZ EU SEREI COM JESUS!\n\nCORO SOU FELIZ, (SOU FELIZ) COM JESUS, (COM JESUS) SOU FELIZ, COM JESUS, MEU SENHOR!\n\nMEU TRISTE PECADO, POR MEU SALVADOR, FOI PAGO DE UM MODO CABAL; VALEU-ME O SENHOR, OH! MERCÊ SEM IGUAL! SOU FELIZ! GRAÇAS DOU A JESUS!\n\nCORO SOU FELIZ, (SOU FELIZ) COM JESUS, (COM JESUS) SOU FELIZ, COM JESUS, MEU SENHOR!\n\nA VINDA EU ANSEIO DO MEU SALVADOR; EM BREVE VIRÁ ME LEVAR AO CÉU, ONDE VOU PARA SEMPRE MORAR COM REMIDOS NA LUZ DO SENHOR!\n\nCORO SOU FELIZ, (SOU FELIZ) COM JESUS, (COM JESUS) SOU FELIZ, COM JESUS, MEU SENHOR!', 'SE PAZ, A MAIS DOCE ME DERES GOZAR, SE DOR A MAIS FORTE SOFRER, OH! SEJA O QUE FOR, TU ME FAZES SABER QUE FELIZ COM JESUS SEMPRE SOU! CORO SOU FELIZ, (SOU FELIZ) COM JESUS, (COM JESUS) SOU FELIZ, COM JESUS, MEU SENHOR! EMBORA ME ASSALTE O CRUEL TENTADOR E ATAQUE COM VIS TENTAÇÕES; OH! CERTO ESTOU, APESAR DE AFLIÇÕES, QUE FELIZ EU SEREI COM JESUS! MEU TRISTE PECADO, POR MEU SALVADOR, FOI PAGO DE UM MODO CABAL; VALEU-ME O SENHOR, OH! MERCÊ SEM IGUAL! SOU FELIZ! GRAÇAS DOU A JESUS! A VINDA EU ANSEIO DO MEU SALVADOR; EM BREVE VIRÁ ME LEVAR AO CÉU, ONDE VOU PARA SEMPRE MORAR COM REMIDOS NA LUZ DO SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('671', '671 - OUVE, POIS ISRAEL', 'OUVE, POIS, ISRAEL! O SENHOR É O TEU DEUS, ELE É O ÚNICO SENHOR, “AMARÁS O SENHOR TEU DEUS DE TODA A TUA ALMA,” ESTAS PALAVRAS TE ORDENO.\n\nATA-AS EM TEU CORAÇÃO, AOS TEUS FILHOS FALARÁS E SERÁ POR SINAL NAS TUAS MÃOS E NOS UMBRAIS DE TUA CASA. E ASSIM VOSSAS CASAS ESTARÃO CHEIAS DE TODO BEM, DOS POÇOS TIRAREIS ÁGUAS DE SALVAÇÃO.\n\nCOMEREIS E VOS FARTAREIS DAS VINHAS, COMEREIS E VOS FARTAREIS INSTRUMENTOS GUARDA AS MINHAS PALAVRAS NÃO ESQUEÇAS DO SENHOR TEU DEUS QUE TE TIROU DA SERVIDÃO E HOJE LIVRE ESTÁS, AO TEU DEUS TEMERÁS E SÓ A ELE SERVIRÁS. POIS EM BREVE VIRÁ. (4X)\nBIS', 'OUVE, POIS, ISRAEL! O SENHOR É O TEU DEUS, ELE É O ÚNICO SENHOR, AMARÁS O SENHOR TEU DEUS DE TODA A TUA ALMA, ESTAS PALAVRAS TE ORDENO. ATA-AS EM TEU CORAÇÃO, AOS TEUS FILHOS FALARÁS E SERÁ POR SINAL NAS TUAS MÃOS E NOS UMBRAIS DE TUA CASA. E ASSIM VOSSAS CASAS ESTARÃO CHEIAS DE TODO BEM, DOS POÇOS TIRAREIS ÁGUAS DE SALVAÇÃO. COMEREIS E VOS FARTAREIS DAS VINHAS, COMEREIS E VOS FARTAREIS INSTRUMENTOS GUARDA AS MINHAS PALAVRAS NÃO ESQUEÇAS DO SENHOR TEU DEUS QUE TE TIROU DA SERVIDÃO E HOJE LIVRE ESTÁS, AO TEU DEUS TEMERÁS E SÓ A ELE SERVIRÁS. POIS EM BREVE VIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('672', '672 - MEU SERVO, NÃO TEMAS', 'MEU SERVO, NÃO TEMAS, NÃO TEMAS POIS TE ESCOLHI PRA FAZER A MINHA OBRA, SERVO, TU ÉS MEU.\nBIS\n\nQUANDO EU TE ENVIAR, TU FALARÁS, QUANDO EU MANDAR, TU CALARÁS, E ASSIM FARÁS MINHA OBRA E TE ALEGRARÁS. QUANDO A OBRA FIZERES E TUDO SE COMPLETAR, TE TRAREI À MINHA PRESENÇA E TE ALEGRARÁS.\n\n3X', 'MEU SERVO, NÃO TEMAS, NÃO TEMAS POIS TE ESCOLHI PRA FAZER A MINHA OBRA, SERVO, TU ÉS MEU. QUANDO EU TE ENVIAR, TU FALARÁS, QUANDO EU MANDAR, TU CALARÁS, E ASSIM FARÁS MINHA OBRA E TE ALEGRARÁS. QUANDO A OBRA FIZERES E TUDO SE COMPLETAR, TE TRAREI À MINHA PRESENÇA E TE ALEGRARÁS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('673', '673 - Ó PAI, EU QUERIA TANTO VER (AUTOR DA MINHA FÉ)', 'Ó PAI, EU QUERIA TANTO VER O MEU SENHOR DESCER VINDO ME ENCONTRAR, EU POSSO ATÉ IMAGINAR A REFULGENTE GLÓRIA DO SENHOR JESUS, TRANSPONDO AS BRANCAS NUVENS NO MAIS PURO AZUL ONDE NEM SUL, NEM NORTE EXISTIRÁ.\n\nE EM MEIO A LÁGRIMAS, SORRISOS DE ALEGRIA E DE PRAZER, EU QUE ERA CEGO AGORA POSSO VER, CONTEMPLAR, CONTEMPLAR, ENFIM, POR ISSO EU CANTO GLÓRIA. CORO (2X) GLÓRIA, GLÓRIA AO AUTOR DA MINHA FÉ.\n\nÓ PAI, EU QUERIA TANTO OUVIR O SOM QUE VAI ABRIR O ENCONTRO TRIUNFAL, REVER AMIGOS QUE UM DIA EM CRISTO FORAM FEITOS MEUS IRMÃOS, E AGORA, SIM, PODEMOS DAR AS MÃOS POIS TEMOS TODOS UM, SOMENTE UM, UM SÓ SENHOR;\n\nE EIS O CONSOLO QUE ENVOLVE A MINHA VIDA, O MEU SENHOR JESUS QUE FOI MORTO, SIM, NAQUELA CRUZ, VOLTARÁ, VOLTARÁ, ENFIM, POR ISSO EU CANTO GLÓRIA. CORO (2X) GLÓRIA, GLÓRIA AO AUTOR DA MINHA FÉ.\n\nFINAL: (ALELUIA) GLÓRIA AO SENHOR, (3X) (ALELUIA) O AUTOR DA MINHA FÉ; (ALELUIA) GLÓRIA AO SENHOR, (2X) (ALELUIA) O AUTOR DA MINHA FÉ.\nBIS', 'Ó PAI, EU QUERIA TANTO VER O MEU SENHOR DESCER VINDO ME ENCONTRAR, EU POSSO ATÉ IMAGINAR A REFULGENTE GLÓRIA DO SENHOR JESUS, TRANSPONDO AS BRANCAS NUVENS NO MAIS PURO AZUL ONDE NEM SUL, NEM NORTE EXISTIRÁ. E EM MEIO A LÁGRIMAS, SORRISOS DE ALEGRIA E DE PRAZER, EU QUE ERA CEGO AGORA POSSO VER, CONTEMPLAR, CONTEMPLAR, ENFIM, POR ISSO EU CANTO GLÓRIA. CORO  GLÓRIA, GLÓRIA AO AUTOR DA MINHA FÉ. Ó PAI, EU QUERIA TANTO OUVIR O SOM QUE VAI ABRIR O ENCONTRO TRIUNFAL, REVER AMIGOS QUE UM DIA EM CRISTO FORAM FEITOS MEUS IRMÃOS, E AGORA, SIM, PODEMOS DAR AS MÃOS POIS TEMOS TODOS UM, SOMENTE UM, UM SÓ SENHOR; E EIS O CONSOLO QUE ENVOLVE A MINHA VIDA, O MEU SENHOR JESUS QUE FOI MORTO, SIM, NAQUELA CRUZ, VOLTARÁ, VOLTARÁ, ENFIM, POR ISSO EU CANTO GLÓRIA. CORO  GLÓRIA, GLÓRIA AO AUTOR DA MINHA FÉ. (ALELUIA) GLÓRIA AO SENHOR,  (ALELUIA) O AUTOR DA MINHA FÉ; (ALELUIA) GLÓRIA AO SENHOR,  (ALELUIA) O AUTOR DA MINHA FÉ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('674', '674 - EU NÃO SEI QUAL SERÁ O LOUVOR', 'EU NÃO SEI QUAL SERÁ MEU LOUVOR AO OUVIR A VOZ DO SENHOR: “VINDE, BENDITOS DE MEU PAI, POSSUÍ O REINO DE AMOR E PAZ; REDIMIDOS DO SENHOR!”\n\nO QUE DEUS TEM PREPARADO PARA NÓS, UM LINDO CÉU DE AMOR E PAZ! VIVEREMOS PARA SEMPRE COM O SENHOR! OLHO HUMANO NUNCA VIU, SOM ALGUM JAMAIS SE OUVIU DAS MORADAS DO SENHOR. INSTRUMENTOS\n\nO QUE DEUS TEM PREPARADO PARA NÓS, UM LINDO CÉU DE AMOR E PAZ! VIVEREMOS PARA SEMPRE COM O SENHOR! OLHO HUMANO NUNCA VIU, SOM ALGUM JAMAIS SE OUVIU DAS MORADAS DO SENHOR. FINAL: “VINDE, BENDITOS DE MEU PAI, POSSUÍ O REINO DE AMOR E PAZ”.', 'EU NÃO SEI QUAL SERÁ MEU LOUVOR AO OUVIR A VOZ DO SENHOR: VINDE, BENDITOS DE MEU PAI, POSSUÍ O REINO DE AMOR E PAZ; REDIMIDOS DO SENHOR! O QUE DEUS TEM PREPARADO PARA NÓS, UM LINDO CÉU DE AMOR E PAZ! VIVEREMOS PARA SEMPRE COM O SENHOR! OLHO HUMANO NUNCA VIU, SOM ALGUM JAMAIS SE OUVIU DAS MORADAS DO SENHOR. INSTRUMENTOS O QUE DEUS TEM PREPARADO PARA NÓS, UM LINDO CÉU DE AMOR E PAZ! VIVEREMOS PARA SEMPRE COM O SENHOR! OLHO HUMANO NUNCA VIU, SOM ALGUM JAMAIS SE OUVIU DAS MORADAS DO SENHOR.  VINDE, BENDITOS DE MEU PAI, POSSUÍ O REINO DE AMOR E PAZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('675', '675 - NA ETERNIDADE', 'NA ETERNIDADE, AO LADO DE JESUS, ANSEIO LOGO ESTAR E ESTE MUNDO DEIXAR; QUERO COM OS ANJOS FORMAR UM GRANDE CORO E CANTAR LOUVORES DE ADORAÇÃO AO SENHOR. O CORO DOS ANJOS E AS VOZES DOS ARCANJOS JÁ LOUVAM A JESUS.\n\nCORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS. INSTRUMENTOS\n\nO MEU CORAÇÃO TRANSBORDA DE ALEGRIA AO IMAGINAR QUE GRANDE DIA SERÁ; O CORDEIRO SANTO VIRÁ SOBRE AS NUVENS E A SUA IGREJA AO SEU ENCONTRO SUBIRÁ. SAUDAI A JESUS! ELE É O REI DA GLÓRIA QUE VIRÁ NOS BUSCAR.\n\nCORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS.\n\nSAUDAI A JESUS! ELE É O REI DA GLÓRIA QUE VIRÁ NOS BUSCAR. CORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS. FINAL: LOUVAR A JESUS!', 'NA ETERNIDADE, AO LADO DE JESUS, ANSEIO LOGO ESTAR E ESTE MUNDO DEIXAR; QUERO COM OS ANJOS FORMAR UM GRANDE CORO E CANTAR LOUVORES DE ADORAÇÃO AO SENHOR. O CORO DOS ANJOS E AS VOZES DOS ARCANJOS JÁ LOUVAM A JESUS. CORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS. INSTRUMENTOS O MEU CORAÇÃO TRANSBORDA DE ALEGRIA AO IMAGINAR QUE GRANDE DIA SERÁ; O CORDEIRO SANTO VIRÁ SOBRE AS NUVENS E A SUA IGREJA AO SEU ENCONTRO SUBIRÁ. SAUDAI A JESUS! ELE É O REI DA GLÓRIA QUE VIRÁ NOS BUSCAR. CORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS. SAUDAI A JESUS! ELE É O REI DA GLÓRIA QUE VIRÁ NOS BUSCAR. CORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS.  LOUVAR A JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('676', '676 - Ó VINDE ADORAR', 'Ó VINDE ADORAR AO DEUS CRIADOR, DA TERRA E DOS CÉUS, ETERNO SENHOR; AO DEUS QUE, HABITANDO NO REINO DA LUZ, A NÓS SE REVELA POR CRISTO JESUS.\n\nSEU GRANDE PODER DEVEIS CONTEMPLAR, NO CÉU ESTRELADO, NO AMPLO MAR; A GOTA DE ORVALHO, A MÍNIMA FLOR PROCLAMAM CONSTANTES SER DEUS SEU AUTOR. Ó VINDE ADORAR AO DEUS SALVADOR, QUE EM CRISTO JESUS NOS MOSTRA FAVOR; AO DEUS QUE, BENIGNO, COM GRAÇA E PERDÃO, PECADOS COBRINDO, NOS DÁ REDENÇÃO.', 'Ó VINDE ADORAR AO DEUS CRIADOR, DA TERRA E DOS CÉUS, ETERNO SENHOR; AO DEUS QUE, HABITANDO NO REINO DA LUZ, A NÓS SE REVELA POR CRISTO JESUS. SEU GRANDE PODER DEVEIS CONTEMPLAR, NO CÉU ESTRELADO, NO AMPLO MAR; A GOTA DE ORVALHO, A MÍNIMA FLOR PROCLAMAM CONSTANTES SER DEUS SEU AUTOR. Ó VINDE ADORAR AO DEUS SALVADOR, QUE EM CRISTO JESUS NOS MOSTRA FAVOR; AO DEUS QUE, BENIGNO, COM GRAÇA E PERDÃO, PECADOS COBRINDO, NOS DÁ REDENÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('677', '677 - A TI, QUE HABITAS ENTRE OS QUERUBINS', 'A TI, QUE HABITAS ENTRE OS QUERUBINS, E ÉS DEUS DA ETERNIDADE, A TI, COMPASSIVO, PAI DE AMOR, PROSTRADO EU ADORO. A TI, QUE ÉS TÃO PURO E PERFEITO, TEU ROSTO NINGUÉM PODE CONTEMPLAR; LOUVORES E HONRA, GLÓRIA E PODER, PRA SEMPRE, SEMPRE, AMÉM.\n\nA TI, QUE PASSEIAS ENTRE OS CASTIÇAIS, VESTIDO COM VESTES BRANCAS, A TI, CUJO ROSTO BRILHA COMO O SOL, PROSTRADO EU ADORO. A TI, O PRIMEIRO E O ÚLTIMO, A TI, QUE MORRESTE MAS VIVO ESTÁS, LOUVORES E HONRA, GLÓRIA E PODER, PRA SEMPRE, SEMPRE, AMÉM.', 'A TI, QUE HABITAS ENTRE OS QUERUBINS, E ÉS DEUS DA ETERNIDADE, A TI, COMPASSIVO, PAI DE AMOR, PROSTRADO EU ADORO. A TI, QUE ÉS TÃO PURO E PERFEITO, TEU ROSTO NINGUÉM PODE CONTEMPLAR; LOUVORES E HONRA, GLÓRIA E PODER, PRA SEMPRE, SEMPRE, AMÉM. A TI, QUE PASSEIAS ENTRE OS CASTIÇAIS, VESTIDO COM VESTES BRANCAS, A TI, CUJO ROSTO BRILHA COMO O SOL, PROSTRADO EU ADORO. A TI, O PRIMEIRO E O ÚLTIMO, A TI, QUE MORRESTE MAS VIVO ESTÁS, LOUVORES E HONRA, GLÓRIA E PODER, PRA SEMPRE, SEMPRE, AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('678', '678 - TUA GLÓRIA ENCHE TODA A TERRA', 'TUA GLÓRIA ENCHE TODA A TERRA, TEU NOME É SOBRE TODO NOME, TU ÉS O DONO DA MINHA VIDA, É SÓ EM TI QUE TENHO SALVAÇÃO! TU ÉS O DONO DO MEU QUERER E OS MEUS LÁBIOS PRA SEMPRE TE ADORARÃO.\nBIS\n\nE COM OS ANJOS TE ADORAREI E AOS TEUS PÉS ME PROSTRAREI; TU ÉS MINHA VIDA E O MEU LOUVOR E AO TEU NOME EXALTAREI.\n\nTUA GLÓRIA ENCHE TODA A TERRA, TEU NOME É SOBRE TODO NOME, TU ÉS O DONO DA MINHA VIDA, É SÓ EM TI QUE TENHO SALVAÇÃO! TU ÉS O DONO DO MEU QUERER E OS MEUS LÁBIOS PRA SEMPRE TE ADORARÃO. (3X)\n\nAQUELE QUE MORREU MAS A MORTE VENCEU (BIS) AQUELE QUE RESSUSCITOU, SOBRE A MORTE TRIUNFOU. (BIS)\n\nE AGORA ASSENTADO ESTÁ À DIREITA DE DEUS PAI, TEM O PODER SOBRE TERRA E CÉU, TEM O NOME SOBRE TODO NOME, ELE É O CORDEIRO, JESUS CRISTO, SENHOR.\nBIS\n\nAQUELE QUE NOS AMA E COM SEU SANGUE NOS LIBERTOU DO PECADO E NOS CONSTITUIU REINO E SACERDOTES PARA NOSSO DEUS E PAI, A ELE A GLÓRIA E O DOMÍNIO PELOS SÉCULOS DOS SÉCULOS, AMÉM. A ELE A GLÓRIA E O DOMÍNIO, PELOS SÉCULOS DOS SÉCULOS, AMÉM, AMÉM, AMÉM.', 'TUA GLÓRIA ENCHE TODA A TERRA, TEU NOME É SOBRE TODO NOME, TU ÉS O DONO DA MINHA VIDA, É SÓ EM TI QUE TENHO SALVAÇÃO! TU ÉS O DONO DO MEU QUERER E OS MEUS LÁBIOS PRA SEMPRE TE ADORARÃO. E COM OS ANJOS TE ADORAREI E AOS TEUS PÉS ME PROSTRAREI; TU ÉS MINHA VIDA E O MEU LOUVOR E AO TEU NOME EXALTAREI. AQUELE QUE MORREU MAS A MORTE VENCEU  AQUELE QUE RESSUSCITOU, SOBRE A MORTE TRIUNFOU. E AGORA ASSENTADO ESTÁ À DIREITA DE DEUS PAI, TEM O PODER SOBRE TERRA E CÉU, TEM O NOME SOBRE TODO NOME, ELE É O CORDEIRO, JESUS CRISTO, SENHOR. AQUELE QUE NOS AMA E COM SEU SANGUE NOS LIBERTOU DO PECADO E NOS CONSTITUIU REINO E SACERDOTES PARA NOSSO DEUS E PAI, A ELE A GLÓRIA E O DOMÍNIO PELOS SÉCULOS DOS SÉCULOS, AMÉM. A ELE A GLÓRIA E O DOMÍNIO, PELOS SÉCULOS DOS SÉCULOS, AMÉM, AMÉM, AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('679', '679 - EIS A ESCRAVA RESGATADA', 'EIS A ESCRAVA RESGATADA! GRANDE PREÇO CRISTO DEU; NÃO FOI OURO, NEM FOI PRATA, PRÓPRIO SANGUE ELE VERTEU.\n\nCORO TANTO FOI O TEU AMOR QUE POR MIM ASSIM MOSTRASTE; PRA REMIR MEU CATIVEIRO TUA VIDA NÃO POUPASTE!\n\nJÁ AGORA QUE SOU TUA, SEM JAMAIS A TI PERDER, QUERO, MEU SENHOR, SERVIR-TE GRATA, E SÓ PARA TI VIVER.\n\nCORO TANTO FOI O TEU AMOR QUE POR MIM ASSIM MOSTRASTE; PRA REMIR MEU CATIVEIRO TUA VIDA NÃO POUPASTE!\n\nQUERO RECEBER TEU JUGO, E EM TEUS PASSOS CAMINHAR; SE POR TI EU SOFRO TUDO, VOU CONTIGO EM PAZ REINAR.\n\nCORO TANTO FOI O TEU AMOR QUE POR MIM ASSIM MOSTRASTE; PRA REMIR MEU CATIVEIRO TUA VIDA NÃO POUPASTE!\n\nESTÁS NO CÉU! VIVO EU NA TERRA, ESPERANDO O TEU VOLTAR; LEVARÁS ENTÃO A ESCRAVA PRA CONTIGO ALI FICAR.\n\nCORO TANTO FOI O TEU AMOR QUE POR MIM ASSIM MOSTRASTE; PRA REMIR MEU CATIVEIRO TUA VIDA NÃO POUPASTE!\n\nTODO O AMOR POR MIM SENTIAS, PADECENDO A DOR DA CRUZ, VESTE-ME DA TUA GLÓRIA! VEM, Ó VEM, SENHOR JESUS.\n\nCORO TANTO FOI O TEU AMOR QUE POR MIM ASSIM MOSTRASTE; PRA REMIR MEU CATIVEIRO TUA VIDA NÃO POUPASTE!', 'EIS A ESCRAVA RESGATADA! GRANDE PREÇO CRISTO DEU; NÃO FOI OURO, NEM FOI PRATA, PRÓPRIO SANGUE ELE VERTEU. CORO TANTO FOI O TEU AMOR QUE POR MIM ASSIM MOSTRASTE; PRA REMIR MEU CATIVEIRO TUA VIDA NÃO POUPASTE! JÁ AGORA QUE SOU TUA, SEM JAMAIS A TI PERDER, QUERO, MEU SENHOR, SERVIR-TE GRATA, E SÓ PARA TI VIVER. QUERO RECEBER TEU JUGO, E EM TEUS PASSOS CAMINHAR; SE POR TI EU SOFRO TUDO, VOU CONTIGO EM PAZ REINAR. ESTÁS NO CÉU! VIVO EU NA TERRA, ESPERANDO O TEU VOLTAR; LEVARÁS ENTÃO A ESCRAVA PRA CONTIGO ALI FICAR. TODO O AMOR POR MIM SENTIAS, PADECENDO A DOR DA CRUZ, VESTE-ME DA TUA GLÓRIA! VEM, Ó VEM, SENHOR JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('680', '680 - TEU NOME É PRA MIM, SENHOR', 'TEU NOME É PRA MIM, SENHOR MARAVILHOSO, DEUS FORTE; TEU NOME É PRA MIM, SENHOR MARAVILHOSO, PRÍNCIPE DA PAZ.\n\nCORO (2X) EMANUEL É DEUS CONOSCO, CONOSCO ESTÁ, PRA SEMPRE VIVERÁ. TEU NOME É PRA MIM, SENHOR CONSELHEIRO, PAI DA ETERNIDADE; TEU NOME É PRA MIM, SENHOR CONSELHEIRO, PAI DA ETERNIDADE.\n\nCORO (3X) EMANUEL É DEUS CONOSCO, CONOSCO ESTÁ, PRA SEMPRE VIVERÁ. FINAL: TEU NOME É PRA MIM, SENHOR, JESUS.', 'TEU NOME É PRA MIM, SENHOR MARAVILHOSO, DEUS FORTE; TEU NOME É PRA MIM, SENHOR MARAVILHOSO, PRÍNCIPE DA PAZ. CORO  EMANUEL É DEUS CONOSCO, CONOSCO ESTÁ, PRA SEMPRE VIVERÁ. TEU NOME É PRA MIM, SENHOR CONSELHEIRO, PAI DA ETERNIDADE; TEU NOME É PRA MIM, SENHOR CONSELHEIRO, PAI DA ETERNIDADE. CORO  EMANUEL É DEUS CONOSCO, CONOSCO ESTÁ, PRA SEMPRE VIVERÁ.  TEU NOME É PRA MIM, SENHOR, JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('681', '681 - NOSSO LOUVOR', 'NOSSO LOUVOR VAMOS TE OFERTAR PELO QUE ÉS, PELO QUE FARÁS; NÃO HÁ PALAVRAS QUE POSSAM EXPRESSAR NOSSA ADORAÇÃO A TI, SENHOR. INSTRUMENTOS\n\nCOM GRATIDÃO (COM GRATIDÃO) VAMOS TE ADORAR (ADORAR) PELO QUE ÉS, PELO QUE FARÁS, (EXPRESSAR) NÃO HÁ PALAVRAS QUE POSSAM EXPRESSAR NOSSO AMOR A TI, SENHOR.\n\nCORO MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS POR TUA MISERICÓRDIA, A TI PERTENCE TODO O LOUVOR. INSTRUMENTOS\n\nNOSSO REFÚGIO E DIREÇÃO, (ÉS TU, SENHOR) NOSSO AMPARO E PROTEÇÃO, NOSSO ABRIGO NO TEMPORAL ÉS TU, SENHOR. COM GRATIDÃO VAMOS TE ADORAR, EM TEU ALTAR VAMOS OFERTAR NOSSO LOUVOR, QUE É FRUTO DO TEU AMOR.\n\nCORO MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS POR TUA MISERICÓRDIA, A TI PERTENCE TODO O LOUVOR.\n\nFINAL: MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS A TI PERTENCE TODO O LOUVOR. (2X)', 'NOSSO LOUVOR VAMOS TE OFERTAR PELO QUE ÉS, PELO QUE FARÁS; NÃO HÁ PALAVRAS QUE POSSAM EXPRESSAR NOSSA ADORAÇÃO A TI, SENHOR. INSTRUMENTOS COM GRATIDÃO (COM GRATIDÃO) VAMOS TE ADORAR (ADORAR) PELO QUE ÉS, PELO QUE FARÁS, (EXPRESSAR) NÃO HÁ PALAVRAS QUE POSSAM EXPRESSAR NOSSO AMOR A TI, SENHOR. CORO MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS POR TUA MISERICÓRDIA, A TI PERTENCE TODO O LOUVOR. INSTRUMENTOS NOSSO REFÚGIO E DIREÇÃO, (ÉS TU, SENHOR) NOSSO AMPARO E PROTEÇÃO, NOSSO ABRIGO NO TEMPORAL ÉS TU, SENHOR. COM GRATIDÃO VAMOS TE ADORAR, EM TEU ALTAR VAMOS OFERTAR NOSSO LOUVOR, QUE É FRUTO DO TEU AMOR. CORO MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS POR TUA MISERICÓRDIA, A TI PERTENCE TODO O LOUVOR. MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS A TI PERTENCE TODO O LOUVOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('682', '682 - A JESUS SEJA DADA TODA GLÓRIA', 'CORO A JESUS SEJA DADA TODA GLÓRIA E LOUVOR, A JESUS SEJA DADA TODA GLÓRIA, HONRA E LOUVOR.\n\nESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI.\n\nCORO A JESUS SEJA DADA TODA GLÓRIA E LOUVOR, A JESUS SEJA DADA TODA GLÓRIA, HONRA E LOUVOR.\n\nQUEREMOS TE ADORAR EM ESPÍRITO E EM VERDADE, A VERDADEIRA ADORAÇÃO É A QUE PROCEDE DO CORAÇÃO, DO CORAÇÃO DOS QUE LOUVAM AO SENHOR.\n\nCORO (2X) A JESUS SEJA DADA TODA GLÓRIA E LOUVOR, A JESUS SEJA DADA TODA GLÓRIA, HONRA E LOUVOR.', 'CORO A JESUS SEJA DADA TODA GLÓRIA E LOUVOR, A JESUS SEJA DADA TODA GLÓRIA, HONRA E LOUVOR. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. QUEREMOS TE ADORAR EM ESPÍRITO E EM VERDADE, A VERDADEIRA ADORAÇÃO É A QUE PROCEDE DO CORAÇÃO, DO CORAÇÃO DOS QUE LOUVAM AO SENHOR. CORO  A JESUS SEJA DADA TODA GLÓRIA E LOUVOR, A JESUS SEJA DADA TODA GLÓRIA, HONRA E LOUVOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('683', '683 - JESUS CRISTO VOLTARÁ', 'JESUS CRISTO VOLTARÁ PRA NOS LEVAR, SUA IGREJA FIEL SUBIRÁ PRA GLÓRIA; DEIXAREMOS ESTE MUNDO DE TRISTEZA, PARA ESTAR COM NOSSO AMADO SALVADOR.\n\nCORO LOUVAREMOS A JESUS, O REI, CANTAREMOS UM HINO DE VITÓRIA, COM OS ANJOS ESTAREMOS NAS MAIS BELAS MANSÕES CELESTIAIS.\n\nALELUIA, VAMOS TODOS ADORAR A JESUS, O LEÃO DA TRIBO DE JUDÁ, QUE FOI MORTO MAS RESSUSCITOU, VAMOS CANTAR (VAMOS CANTAR) ESTE GRANDE AMOR DO NOSSO SALVADOR.\n\nCORO LOUVAREMOS A JESUS, O REI, CANTAREMOS UM HINO DE VITÓRIA, COM OS ANJOS ESTAREMOS NAS MAIS BELAS MANSÕES CELESTIAIS.\n\nALELUIA, VAMOS TODOS ADORAR A JESUS, O LEÃO DA TRIBO DE JUDÁ, QUE FOI MORTO MAS RESSUSCITOU, VAMOS CANTAR (VAMOS CANTAR) ESTE GRANDE AMOR DO NOSSO SALVADOR.\n\nCORO LOUVAREMOS A JESUS, O REI, CANTAREMOS UM HINO DE VITÓRIA, COM OS ANJOS ESTAREMOS NAS MAIS BELAS MANSÕES CELESTIAIS.', 'JESUS CRISTO VOLTARÁ PRA NOS LEVAR, SUA IGREJA FIEL SUBIRÁ PRA GLÓRIA; DEIXAREMOS ESTE MUNDO DE TRISTEZA, PARA ESTAR COM NOSSO AMADO SALVADOR. CORO LOUVAREMOS A JESUS, O REI, CANTAREMOS UM HINO DE VITÓRIA, COM OS ANJOS ESTAREMOS NAS MAIS BELAS MANSÕES CELESTIAIS. ALELUIA, VAMOS TODOS ADORAR A JESUS, O LEÃO DA TRIBO DE JUDÁ, QUE FOI MORTO MAS RESSUSCITOU, VAMOS CANTAR (VAMOS CANTAR) ESTE GRANDE AMOR DO NOSSO SALVADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('684', '684 - ELE É O REI DOS REIS', 'ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS. INSTRUMENTOS\n\nAO QUE ESTÁ NO TRONO SEJA O LOUVOR, AO CORDEIRO MORTO QUE RESSUSCITOU, À RESPLANDECENTE ESTRELA DA MANHÃ, SEJA DADA A GLÓRIA E TODO LOUVOR, SEJA DADA A GLÓRIA E TODO LOUVOR.\n\nCORO O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O CORDEIRO QUE VENCEU; O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS. INSTRUMENTOS\n\nLOUVEMOS A GLÓRIA (DO REI DOS REIS) DAQUELE QUE É VENCEDOR, (JESUS VOLTARÁ) PORQUE SÃO CHEGADAS (JESUS VOLTARÁ) AS BODAS DO CORDEIRO.\n\nCORO O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O CORDEIRO QUE VENCEU; O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS. FINAL: ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS.', 'ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS. INSTRUMENTOS AO QUE ESTÁ NO TRONO SEJA O LOUVOR, AO CORDEIRO MORTO QUE RESSUSCITOU, À RESPLANDECENTE ESTRELA DA MANHÃ, SEJA DADA A GLÓRIA E TODO LOUVOR, SEJA DADA A GLÓRIA E TODO LOUVOR. CORO O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O CORDEIRO QUE VENCEU; O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS. INSTRUMENTOS LOUVEMOS A GLÓRIA (DO REI DOS REIS) DAQUELE QUE É VENCEDOR, (JESUS VOLTARÁ) PORQUE SÃO CHEGADAS (JESUS VOLTARÁ) AS BODAS DO CORDEIRO. CORO O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O CORDEIRO QUE VENCEU; O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS.  ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('685', '685 - PERFEITO AMOR EU CONHECI', 'PERFEITO AMOR EU CONHECI, NÃO IMAGINEI QUE ELE PODERIA EXISTIR; AMOR FIEL E VERDADEIRO AMOR, TUDO ELE SUPORTOU, ME AMOU ATÉ O FIM.\n\nFOI, ENTÃO, QUE EU SENTI COMO É FORTE O AMOR QUE RECEBI; AMOR FIEL E VERDADEIRO AMOR É JESUS, O MEU SENHOR. TENHO EM MIM O SEU AMOR.\n\nDIFERENTE DO AMOR QUE NO MUNDO ESTÁ, AMOR PERFEITO É DOM DE DEUS E SÓ ELE PODE DAR; COM ESTE AMOR EU POSSO CRER, TUDO SUPERAR, SEI QUE EM TODA A MINHA VIDA ELE NÃO FALHARÁ.\n\nDIFERENTE DO AMOR (DIFERENTE DO AMOR) QUE NO MUNDO ESTÁ, AMOR PERFEITO É DOM DE DEUS E SÓ ELE PODE DAR; COM ESTE AMOR EU POSSO CRER, (COM ESTE AMOR EU POSSO CRER) TUDO SUPERAR, SEI QUE EM TODA A MINHA VIDA ELE NÃO FALHARÁ. INSTRUMENTOS\n\nFINAL: AMOR FIEL E VERDADEIRO AMOR, RECEBI DO MEU SENHOR, QUE ME AMOU COM ETERNO AMOR.', 'PERFEITO AMOR EU CONHECI, NÃO IMAGINEI QUE ELE PODERIA EXISTIR; AMOR FIEL E VERDADEIRO AMOR, TUDO ELE SUPORTOU, ME AMOU ATÉ O FIM. FOI, ENTÃO, QUE EU SENTI COMO É FORTE O AMOR QUE RECEBI; AMOR FIEL E VERDADEIRO AMOR É JESUS, O MEU SENHOR. TENHO EM MIM O SEU AMOR. DIFERENTE DO AMOR QUE NO MUNDO ESTÁ, AMOR PERFEITO É DOM DE DEUS E SÓ ELE PODE DAR; COM ESTE AMOR EU POSSO CRER, TUDO SUPERAR, SEI QUE EM TODA A MINHA VIDA ELE NÃO FALHARÁ. DIFERENTE DO AMOR (DIFERENTE DO AMOR) QUE NO MUNDO ESTÁ, AMOR PERFEITO É DOM DE DEUS E SÓ ELE PODE DAR; COM ESTE AMOR EU POSSO CRER, (COM ESTE AMOR EU POSSO CRER) TUDO SUPERAR, SEI QUE EM TODA A MINHA VIDA ELE NÃO FALHARÁ. INSTRUMENTOS AMOR FIEL E VERDADEIRO AMOR, RECEBI DO MEU SENHOR, QUE ME AMOU COM ETERNO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('686', '686 - QUANDO JESUS PROVOU O SEU AMOR POR MIM', 'QUANDO JESUS PROVOU O SEU AMOR POR MIM, SOFRENDO NA CRUZ, DERRAMANDO SEU SANGUE PURO, ESTAVA COM ELE O AMOR DO PAI. QUÃO IMENSURÁVEL ESTE AMOR!\n\nCORO (2X) EMBORA EU NÃO MEREÇA, FOI A SUA COMPAIXÃO QUE ME DEU ETERNA SALVAÇÃO. INSTRUMENTOS\n\nTODO O MEU LOUVOR, MINHA ADORAÇÃO, ENTREGO A CRISTO, DONO DO MEU VIVER; GLÓRIA E HONRA AO CORDEIRO ETERNO, QUE DEU SUA VIDA POR ME AMAR.\n\nCORO (2X) EMBORA EU NÃO MEREÇA, FOI A SUA COMPAIXÃO QUE ME DEU ETERNA SALVAÇÃO.', 'QUANDO JESUS PROVOU O SEU AMOR POR MIM, SOFRENDO NA CRUZ, DERRAMANDO SEU SANGUE PURO, ESTAVA COM ELE O AMOR DO PAI. QUÃO IMENSURÁVEL ESTE AMOR! CORO  EMBORA EU NÃO MEREÇA, FOI A SUA COMPAIXÃO QUE ME DEU ETERNA SALVAÇÃO. INSTRUMENTOS TODO O MEU LOUVOR, MINHA ADORAÇÃO, ENTREGO A CRISTO, DONO DO MEU VIVER; GLÓRIA E HONRA AO CORDEIRO ETERNO, QUE DEU SUA VIDA POR ME AMAR. CORO  EMBORA EU NÃO MEREÇA, FOI A SUA COMPAIXÃO QUE ME DEU ETERNA SALVAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('687', '687 - QUEM É COMO TU?', 'QUEM É COMO TU? FORTE COMO TU, SENHOR, COM TUA FIDELIDADE AO REDOR DE TI? QUEM É COMO TU? QUE DOMINAS O ÍMPETO DO MAR; QUANDO SUAS ONDAS SE LEVANTAM, TU FAZES ACALMAR.\n\nCORO TEU É O CÉU, TUA A TERRA, O MUNDO E A SUA PLENITUDE; NORTE E SUL CRIASTE; MISERICÓRDIA E VERDADE VÃO PERANTE TUA FACE, OH! SENHOR!\n\nCOM TUA PRESENÇA OS MEUS PASSOS SE FIRMARÃO, MEU PEQUENO BRAÇO SE FORTALECERÁ E OS MEUS PENSAMENTOS DESCANSARÃO EM TEU ALTAR; NENHUMA TEMPESTADE ME ABALARÁ!\n\nCORO TEU É O CÉU, TUA A TERRA, O MUNDO E A SUA PLENITUDE; NORTE E SUL CRIASTE; MISERICÓRDIA E VERDADE VÃO PERANTE TUA FACE, OH! SENHOR! FINAL: QUEM É COMO TU? (Ó SENHOR)', 'QUEM É COMO TU? FORTE COMO TU, SENHOR, COM TUA FIDELIDADE AO REDOR DE TI? QUEM É COMO TU? QUE DOMINAS O ÍMPETO DO MAR; QUANDO SUAS ONDAS SE LEVANTAM, TU FAZES ACALMAR. CORO TEU É O CÉU, TUA A TERRA, O MUNDO E A SUA PLENITUDE; NORTE E SUL CRIASTE; MISERICÓRDIA E VERDADE VÃO PERANTE TUA FACE, OH! SENHOR! COM TUA PRESENÇA OS MEUS PASSOS SE FIRMARÃO, MEU PEQUENO BRAÇO SE FORTALECERÁ E OS MEUS PENSAMENTOS DESCANSARÃO EM TEU ALTAR; NENHUMA TEMPESTADE ME ABALARÁ! CORO TEU É O CÉU, TUA A TERRA, O MUNDO E A SUA PLENITUDE; NORTE E SUL CRIASTE; MISERICÓRDIA E VERDADE VÃO PERANTE TUA FACE, OH! SENHOR!  QUEM É COMO TU? (Ó SENHOR)',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('688', '688 - CRISTO JÁ RESSUSCITOU', 'CRISTO JÁ RESSUSCITOU: ALELUIA! SOBRE A MORTE TRIUNFOU: ALELUIA! TUDO CONSUMADO ESTÁ! ALELUIA! SALVAÇÃO DE GRAÇA DÁ! ALELUIA!\n\nUMA VEZ NA CRUZ SOFREU: ALELUIA! UMA VEZ POR NÓS MORREU: ALELUIA! MAS AGORA VIVO ESTÁ, ALELUIA! E PRA SEMPRE REINARÁ, ALELUIA! INSTRUMENTOS GRATOS HINOS ENTOAI, ALELUIA! A JESUS O GRANDE REI, ALELUIA! POIS À MORTE QUIS BAIXAR, ALELUIA! PECADORES PRA SALVAR! ALELUIA!', 'CRISTO JÁ RESSUSCITOU: ALELUIA! SOBRE A MORTE TRIUNFOU: ALELUIA! TUDO CONSUMADO ESTÁ! ALELUIA! SALVAÇÃO DE GRAÇA DÁ! ALELUIA! UMA VEZ NA CRUZ SOFREU: ALELUIA! UMA VEZ POR NÓS MORREU: ALELUIA! MAS AGORA VIVO ESTÁ, ALELUIA! E PRA SEMPRE REINARÁ, ALELUIA! INSTRUMENTOS GRATOS HINOS ENTOAI, ALELUIA! A JESUS O GRANDE REI, ALELUIA! POIS À MORTE QUIS BAIXAR, ALELUIA! PECADORES PRA SALVAR! ALELUIA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('689', '689 - AMOR, QUE POR AMOR DESCESTE!', 'AMOR, QUE POR AMOR DESCESTE! AMOR, QUE POR AMOR MORRESTE! AH! QUANTA DOR NÃO PADECESTE, MEU CORAÇÃO PRA CONQUISTAR E MEU AMOR GANHAR!\n\nAMOR, QUE COM AMOR SEGUIAS A MIM, QUE SEM AMOR TU VIAS! OH! QUANTO AMOR POR MIM SENTIAS, MEU SALVADOR, MEU BOM JESUS, SOFRENDO SOBRE A CRUZ! INSTRUMENTOS\n\nAMOR, QUE TUDO ME PERDOAS! AMOR, QUE ATÉ MESMO ABENÇOAS UM RÉU A QUEM TU TE AFEIÇOAS! POR TI VENCIDO, Ó SALVADOR, EIS-ME AOS TEUS PÉS, SENHOR.\n\nAMOR QUE NUNCA, NUNCA MUDA, QUE EM TUA GRAÇA ME SEGURA, CERCANDO-ME DE MIL VENTURAS; ACEITA AGORA, Ó SALVADOR, O MEU HUMILDE AMOR!', 'AMOR, QUE POR AMOR DESCESTE! AMOR, QUE POR AMOR MORRESTE! AH! QUANTA DOR NÃO PADECESTE, MEU CORAÇÃO PRA CONQUISTAR E MEU AMOR GANHAR! AMOR, QUE COM AMOR SEGUIAS A MIM, QUE SEM AMOR TU VIAS! OH! QUANTO AMOR POR MIM SENTIAS, MEU SALVADOR, MEU BOM JESUS, SOFRENDO SOBRE A CRUZ! INSTRUMENTOS AMOR, QUE TUDO ME PERDOAS! AMOR, QUE ATÉ MESMO ABENÇOAS UM RÉU A QUEM TU TE AFEIÇOAS! POR TI VENCIDO, Ó SALVADOR, EIS-ME AOS TEUS PÉS, SENHOR. AMOR QUE NUNCA, NUNCA MUDA, QUE EM TUA GRAÇA ME SEGURA, CERCANDO-ME DE MIL VENTURAS; ACEITA AGORA, Ó SALVADOR, O MEU HUMILDE AMOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('690', '690 - DA IGREJA O FUNDAMENTO (A PEDRA FUNDAMENTAL)', 'DA IGREJA O FUNDAMENTO É CRISTO, O SALVADOR; EM SEU PODER DESCANSA E É FORTE EM SEU AMOR. EM CRISTO BEM FIRMADA, SEGURA SEMPRE ESTÁ, E, SOBRE A ROCHA ETERNA JAMAIS SE ABALARÁ.\n\nA PEDRA PRECIOSA QUE DEUS PREDESTINOU SUSTENTA PEDRAS VIVAS, QUE A GRAÇA TRABALHOU. E, QUANDO O MONUMENTO SURGIR EM PLENA LUZ, A GLÓRIA DO EDIFÍCIO SERÁ DO REI JESUS!\n\nSENHOR, NÓS TE ROGAMOS QUE, ERGUIDO POR AMOR, O TEMPLO CONSAGRADO REDUNDE EM TEU LOUVOR; E QUE ALMAS REDIMIDAS AQUI, EM COMUNHÃO, SE TORNEM TEMPLO SANTO DA TUA HABITAÇÃO. FINAL: AMÉM!', 'DA IGREJA O FUNDAMENTO É CRISTO, O SALVADOR; EM SEU PODER DESCANSA E É FORTE EM SEU AMOR. EM CRISTO BEM FIRMADA, SEGURA SEMPRE ESTÁ, E, SOBRE A ROCHA ETERNA JAMAIS SE ABALARÁ. A PEDRA PRECIOSA QUE DEUS PREDESTINOU SUSTENTA PEDRAS VIVAS, QUE A GRAÇA TRABALHOU. E, QUANDO O MONUMENTO SURGIR EM PLENA LUZ, A GLÓRIA DO EDIFÍCIO SERÁ DO REI JESUS! SENHOR, NÓS TE ROGAMOS QUE, ERGUIDO POR AMOR, O TEMPLO CONSAGRADO REDUNDE EM TEU LOUVOR; E QUE ALMAS REDIMIDAS AQUI, EM COMUNHÃO, SE TORNEM TEMPLO SANTO DA TUA HABITAÇÃO.  AMÉM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('691', '691 - AO DEUS DE ABRAÃO', 'AO DEUS DE ABRAÃO LOUVAI; DO VASTO CÉU SENHOR; ETERNO E PODEROSO PAIE DEUS DE AMOR. AUGUSTO DEUS JEOVÁ, QUE TERRA E CÉU CRIOU; MINH'' ALMA O NOME ABENÇOARÁ DO GRANDE EU SOU.\n\nAO DEUS DE ABRAÃO LOUVAI; EIS, POR MANDADO SEU, MINH'' ALMA DEIXA A TERRA, E VAI GOZAR NO CÉU. O MUNDO DESPREZEI, SEU LUCRO E SEU LOUVOR, E DEUS POR MEU SENHOR TOMEI E PROTETOR.\n\nMEU GUIA DEUS SERÁ SEU INFINITO AMOR FELIZ EM TUDO ME FARÁ POR ONDE EU FOR TOMOU-ME PELA MÃO, EM TREVAS DEU-ME LUZ, E DÁ-ME ETERNA SALVAÇÃO, POR MEU JESUS.\n\nMEU DEUS POR SI JUROU, E NELE CONFIEI, E PARA O CÉU QUE PREPAROU EU SUBIREI. SUA FACE EU HEI DE VER, CONFIADO EM SEU AMOR, E EU HEI DE SEMPRE ENGRANDECER MEU REDENTOR.', 'AO DEUS DE ABRAÃO LOUVAI; DO VASTO CÉU SENHOR; ETERNO E PODEROSO PAIE DEUS DE AMOR. AUGUSTO DEUS JEOVÁ, QUE TERRA E CÉU CRIOU; MINH'' ALMA O NOME ABENÇOARÁ DO GRANDE EU SOU. AO DEUS DE ABRAÃO LOUVAI; EIS, POR MANDADO SEU, MINH'' ALMA DEIXA A TERRA, E VAI GOZAR NO CÉU. O MUNDO DESPREZEI, SEU LUCRO E SEU LOUVOR, E DEUS POR MEU SENHOR TOMEI E PROTETOR. MEU GUIA DEUS SERÁ SEU INFINITO AMOR FELIZ EM TUDO ME FARÁ POR ONDE EU FOR TOMOU-ME PELA MÃO, EM TREVAS DEU-ME LUZ, E DÁ-ME ETERNA SALVAÇÃO, POR MEU JESUS. MEU DEUS POR SI JUROU, E NELE CONFIEI, E PARA O CÉU QUE PREPAROU EU SUBIREI. SUA FACE EU HEI DE VER, CONFIADO EM SEU AMOR, E EU HEI DE SEMPRE ENGRANDECER MEU REDENTOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('692', '692 - COMIGO HABITA, Ó DEUS!', 'COMIGO HABITA, Ó DEUS! A NOITE VEM, AS TREVAS CRESCEM; EIS, SENHOR, CONVÉM QUE ME SOCORRA A TUA PROTEÇÃO; OH! VEM FAZER COMIGO HABITAÇÃO!\n\nVEM REVELAR-TE A MIM, JESUS, SENHOR! MESTRE DIVINO! REI! CONSOLADOR! É QUEM ME GUIA! AMPARO EM PROVAÇÃO! VEM, VEM FAZER COMIGO HABITAÇÃO.\n\nPRESENTE ESTÁS NAS TREVAS OU NA LUZ! NÃO HÁ PERIGO, ANDANDO COM JESUS! PARTIR COM CRISTO, ETERNA REDENÇÃO, ONDE MEU DEUS FIZER HABITAÇÃO.\n\nQUE ALEGRIA, QUE SATISFAÇÃO VER O SENHOR, A GRANDE SALVAÇÃO! EXULTAREMOS NA RESSURREIÇÃO; TENHO COM DEUS ETERNA HABITAÇÃO.', 'COMIGO HABITA, Ó DEUS! A NOITE VEM, AS TREVAS CRESCEM; EIS, SENHOR, CONVÉM QUE ME SOCORRA A TUA PROTEÇÃO; OH! VEM FAZER COMIGO HABITAÇÃO! VEM REVELAR-TE A MIM, JESUS, SENHOR! MESTRE DIVINO! REI! CONSOLADOR! É QUEM ME GUIA! AMPARO EM PROVAÇÃO! VEM, VEM FAZER COMIGO HABITAÇÃO. PRESENTE ESTÁS NAS TREVAS OU NA LUZ! NÃO HÁ PERIGO, ANDANDO COM JESUS! PARTIR COM CRISTO, ETERNA REDENÇÃO, ONDE MEU DEUS FIZER HABITAÇÃO. QUE ALEGRIA, QUE SATISFAÇÃO VER O SENHOR, A GRANDE SALVAÇÃO! EXULTAREMOS NA RESSURREIÇÃO; TENHO COM DEUS ETERNA HABITAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('693', '693 - DORMINDO NO MEU LEITO (JERUSALÉM, CIDADE SANTA)', 'DORMINDO NO MEU LEITO, EM SONHO ENCANTADOR, UM DIA EU VI JERUSALÉM E O TEMPLO DO SENHOR. OUVI CANTAR CRIANÇAS E EM MEIO AO SEU CANTAR.\n\nROMPEU A VOZ DOS ANJOS, DO CÉU A PROCLAMAR: “JERUSALÉM! JERUSALÉM! CANTAI, Ó SANTA GREI! HOSANA! HOSANA! HOSANA AO VOSSO REI!” INSTRUMENTOS\nBIS\n\nENTÃO O SONHO SE ALTEROU, NÃO MAIS O SOM FELIZ OUVIA DAS HOSANAS DOS COROS INFANTIS. O AR EM TORNO SE ESFRIOU, DO SOL FALTAVA A LUZ;\n\nE NUM ALTO E TOSCO MONTE VI O VULTO DE UMA CRUZ. “JERUSALÉM! JERUSALÉM! (AOS ANJOS ESCUTEI) HOSANA! HOSANA! HOSANA AO VOSSO REI!” INSTRUMENTOS\nBIS\n\nAINDA A CENA SE MUDOU; SURGIA EM RESPLENDOR A DIVINAL CIDADE, MORADA DO SENHOR. DA LUA NÃO BRILHAVA A LUZ, NEM SOL NASCIA LÁ, MAS SÓ FULGIA A LUZ DE DEUS, MUI PURA EM SEU BRILHAR,\n\nE TODOS QUE QUERIAM, SIM, PODIAM LOGO ENTRAR NA MUI FELIZ JERUSALÉM, QUE NUNCA PASSARÁ. “JERUSALÉM! JERUSALÉM! TEU DIA VAI RAIAR! HOSANA! HOSANA! HOSANA SEM CESSAR! HOSANA! HOSANA! HOSANA SEM CESSAR!”\nBIS', 'DORMINDO NO MEU LEITO, EM SONHO ENCANTADOR, UM DIA EU VI JERUSALÉM E O TEMPLO DO SENHOR. OUVI CANTAR CRIANÇAS E EM MEIO AO SEU CANTAR. ROMPEU A VOZ DOS ANJOS, DO CÉU A PROCLAMAR: JERUSALÉM! JERUSALÉM! CANTAI, Ó SANTA GREI! HOSANA! HOSANA! HOSANA AO VOSSO REI! INSTRUMENTOS ENTÃO O SONHO SE ALTEROU, NÃO MAIS O SOM FELIZ OUVIA DAS HOSANAS DOS COROS INFANTIS. O AR EM TORNO SE ESFRIOU, DO SOL FALTAVA A LUZ; E NUM ALTO E TOSCO MONTE VI O VULTO DE UMA CRUZ. JERUSALÉM! JERUSALÉM! (AOS ANJOS ESCUTEI) HOSANA! HOSANA! HOSANA AO VOSSO REI! INSTRUMENTOS AINDA A CENA SE MUDOU; SURGIA EM RESPLENDOR A DIVINAL CIDADE, MORADA DO SENHOR. DA LUA NÃO BRILHAVA A LUZ, NEM SOL NASCIA LÁ, MAS SÓ FULGIA A LUZ DE DEUS, MUI PURA EM SEU BRILHAR, E TODOS QUE QUERIAM, SIM, PODIAM LOGO ENTRAR NA MUI FELIZ JERUSALÉM, QUE NUNCA PASSARÁ. JERUSALÉM! JERUSALÉM! TEU DIA VAI RAIAR! HOSANA! HOSANA! HOSANA SEM CESSAR! HOSANA! HOSANA! HOSANA SEM CESSAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('694', '694 - JERUSALÉM ETERNA', 'JERUSALÉM ETERNA, EM BREVE TU VIRÁS, DESCENDO COMO NOIVA ATAVIADA PARA O SEU MARIDO.\n\nJERUSALÉM, MINHA CIDADE, COMO ANSEIO ENTRAR E ANDAR POR TUAS RUAS, SAUDAR OS TEUS REMIDOS E ADORAR TEU REI. INSTRUMENTOS\nBIS\n\nJERUSALÉM ETERNA, ESPOSA DO CORDEIRO, EM TI HABITARÁ DEUS COM OS HOMENS PARA TODO SEMPRE. NAÇÕES VIRÃO PARA ADORAR E REIS TRARÃO SUA GLÓRIA AO FILHO DE DAVI, AO PRÍNCIPE DA PAZ, TEU REI, JERUSALÉM. JERUSALÉM.\nBIS', 'JERUSALÉM ETERNA, EM BREVE TU VIRÁS, DESCENDO COMO NOIVA ATAVIADA PARA O SEU MARIDO. JERUSALÉM, MINHA CIDADE, COMO ANSEIO ENTRAR E ANDAR POR TUAS RUAS, SAUDAR OS TEUS REMIDOS E ADORAR TEU REI. INSTRUMENTOS JERUSALÉM ETERNA, ESPOSA DO CORDEIRO, EM TI HABITARÁ DEUS COM OS HOMENS PARA TODO SEMPRE. NAÇÕES VIRÃO PARA ADORAR E REIS TRARÃO SUA GLÓRIA AO FILHO DE DAVI, AO PRÍNCIPE DA PAZ, TEU REI, JERUSALÉM. JERUSALÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('695', '695 - JERUSALÉM EXCELSA', 'JERUSALÉM EXCELSA, GLORIAMO-NOS EM TI; CONSOLADORA ESPERANÇA DO TEU REBANHO AQUI; RADIANTE, BELOS MUROS, ELE JÁ DE LONGE VÊ E A GLÓRIA DA CIDADE CONTEMPLA PELA FÉ.\n\nÉ A CRUZ QUE TE ALUMIA; E AO GRANDE REDENTOR, CORDEIRO, TEU ESPOSO, TRIBUTAS-LHE LOUVOR. QUE GOZO QUE ME INSPIRA, ETERNA HABITAÇÃO, SABER QUE EM TI TERMINO A PEREGRINAÇÃO!\n\nÓ DOCE PÁTRIA AMADA, TEU GOZO SERÁ MEU! Ó PÁTRIA DESEJADA, QUE ENCANTO É ESSE TEU; EXULTA, TU QUE GEMES NA DOR QUE SE DESFAZ; COM DEUS, QUE TE REDIME, FELIZ SEMPRE SERÁS.', 'JERUSALÉM EXCELSA, GLORIAMO-NOS EM TI; CONSOLADORA ESPERANÇA DO TEU REBANHO AQUI; RADIANTE, BELOS MUROS, ELE JÁ DE LONGE VÊ E A GLÓRIA DA CIDADE CONTEMPLA PELA FÉ. É A CRUZ QUE TE ALUMIA; E AO GRANDE REDENTOR, CORDEIRO, TEU ESPOSO, TRIBUTAS-LHE LOUVOR. QUE GOZO QUE ME INSPIRA, ETERNA HABITAÇÃO, SABER QUE EM TI TERMINO A PEREGRINAÇÃO! Ó DOCE PÁTRIA AMADA, TEU GOZO SERÁ MEU! Ó PÁTRIA DESEJADA, QUE ENCANTO É ESSE TEU; EXULTA, TU QUE GEMES NA DOR QUE SE DESFAZ; COM DEUS, QUE TE REDIME, FELIZ SEMPRE SERÁS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('696', '696 - NA TERRA ABENÇOADA', 'NA TERRA ABENÇOADA ESTOU; E NELA PEREGRINO SOU; DELÍCIAS ABUNDANTES SÃO; E SÓ DOS CÉUS SAUDADES DÃO.\n\nCORO OH! BELA TERRA DE AMOR! DO ALTO MONTE ENCANTADOR OLHANDO, VEJO ALÉM DO MAR, (QUE BREVE HEI DE ATRAVESSAR) A PRAIA ÁUREA, ETERNAL, QUERIDO LAR CELESTIAL.\n\nCOMIGO ANDA O SALVADOR; CONVERSA EM TONS DE SANTO AMOR, ME GUIA SEMPRE PELA MÃO, E RUMO AO CÉU MEUS PASSOS VÃO.\n\nCORO OH! BELA TERRA DE AMOR! DO ALTO MONTE ENCANTADOR OLHANDO, VEJO ALÉM DO MAR, (QUE BREVE HEI DE ATRAVESSAR) A PRAIA ÁUREA, ETERNAL, QUERIDO LAR CELESTIAL.\n\nSENTIR A BRISA, OH! QUE PRAZER, DOCE PERFUME VEM TRAZER DAS FLORES QUE NÃO MURCHARÃO, DOS FRUTOS QUE SUPERNOS SÃO.\n\nCORO OH! BELA TERRA DE AMOR! DO ALTO MONTE ENCANTADOR OLHANDO, VEJO ALÉM DO MAR, (QUE BREVE HEI DE ATRAVESSAR) A PRAIA ÁUREA, ETERNAL, QUERIDO LAR CELESTIAL.\n\nNA DOCE ARAGEM CELESTIAL FLUTUA O CANTO ANGELICAL DA TRIUNFANTE MULTIDÃO QUE ENTOA A GRANDE REDENÇÃO.\n\nCORO OH! BELA TERRA DE AMOR! DO ALTO MONTE ENCANTADOR OLHANDO, VEJO ALÉM DO MAR, (QUE BREVE HEI DE ATRAVESSAR) A PRAIA ÁUREA, ETERNAL, QUERIDO LAR CELESTIAL.', 'NA TERRA ABENÇOADA ESTOU; E NELA PEREGRINO SOU; DELÍCIAS ABUNDANTES SÃO; E SÓ DOS CÉUS SAUDADES DÃO. CORO OH! BELA TERRA DE AMOR! DO ALTO MONTE ENCANTADOR OLHANDO, VEJO ALÉM DO MAR, (QUE BREVE HEI DE ATRAVESSAR) A PRAIA ÁUREA, ETERNAL, QUERIDO LAR CELESTIAL. COMIGO ANDA O SALVADOR; CONVERSA EM TONS DE SANTO AMOR, ME GUIA SEMPRE PELA MÃO, E RUMO AO CÉU MEUS PASSOS VÃO. SENTIR A BRISA, OH! QUE PRAZER, DOCE PERFUME VEM TRAZER DAS FLORES QUE NÃO MURCHARÃO, DOS FRUTOS QUE SUPERNOS SÃO. NA DOCE ARAGEM CELESTIAL FLUTUA O CANTO ANGELICAL DA TRIUNFANTE MULTIDÃO QUE ENTOA A GRANDE REDENÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('697', '697 - OUVI O SALVADOR DIZER', 'OUVI O SALVADOR DIZER: “VEM DESCANSAR EM MIM E CONFIANTE RECEBER CONFORTO E PAZ SEM FIM”. FUI A JESUS E LHE ENTREGUEI MEU TRISTE CORAÇÃO; ABRIGO E PAZ E GOZO ACHEI, ACHEI CONSOLAÇÃO.\n\nOUVI O SALVADOR DIZER: “DE GRAÇA É O MEU AMOR. AS ÁGUAS VIVAS VEM BEBER; DA VIDA A FONTE SOU”. FUI A JESUS E ME PROSTREI, DA FONTE ENFIM BEBI; JAMAIS A SEDE SENTIREI, ESTANDO SEMPRE ALI.\n\nOUVI O SALVADOR DIZER: “DO MUNDO EU SOU A LUZ; OH! VEM A MIM, POIS SENTIRÁS QUE TE AMO DESDE A CRUZ”. FUI A JESUS E NELE ACHEI O SOL QUE BRILHA EM MIM; E NESSA LUZ EU ANDAREI ATÉ DA VIDA O FIM. FINAL: AMÉM.', 'OUVI O SALVADOR DIZER: VEM DESCANSAR EM MIM E CONFIANTE RECEBER CONFORTO E PAZ SEM FIM. FUI A JESUS E LHE ENTREGUEI MEU TRISTE CORAÇÃO; ABRIGO E PAZ E GOZO ACHEI, ACHEI CONSOLAÇÃO. OUVI O SALVADOR DIZER: DE GRAÇA É O MEU AMOR. AS ÁGUAS VIVAS VEM BEBER; DA VIDA A FONTE SOU. FUI A JESUS E ME PROSTREI, DA FONTE ENFIM BEBI; JAMAIS A SEDE SENTIREI, ESTANDO SEMPRE ALI. OUVI O SALVADOR DIZER: DO MUNDO EU SOU A LUZ; OH! VEM A MIM, POIS SENTIRÁS QUE TE AMO DESDE A CRUZ. FUI A JESUS E NELE ACHEI O SOL QUE BRILHA EM MIM; E NESSA LUZ EU ANDAREI ATÉ DA VIDA O FIM.  AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('698', '698 - TUA, Ó DEUS, É TODA A GRANDEZA', 'TUA, Ó DEUS, É TODA A GRANDEZA, TUA, Ó DEUS, É TODA A GRANDEZA, TUA, Ó DEUS, É TODA A GRANDEZA, E O PODER, E A GLÓRIA, E A VITÓRIA, E OS LOUVORES. VITÓRIA E LOUVORES.\n\nTUA, Ó DEUS; TUA, Ó DEUS, É A GRANDEZA, E O PODER, (A GRANDEZA E O PODER), E A GLÓRIA, E A VITÓRIA, E OS LOUVORES ETERNOS;\n\nPOIS TUDO O QUE ESTÁ NO MAR, E ESTÁ NO CÉU E NA TERRA É TEU! TEU É O IMPÉRIO. TEU É O IMPÉRIO, Ó SENHOR! E TU ÉS ACIMA DE TODOS OS REIS, DE TODOS OS REIS, AMÉM ALELUIA!\n\nTUA, Ó DEUS; TUA, Ó DEUS, É A GRANDEZA, E O PODER, (A GRANDEZA E O PODER), E A GLÓRIA, E A VITÓRIA, E OS LOUVORES ETERNOS;', 'TUA, Ó DEUS, É TODA A GRANDEZA, TUA, Ó DEUS, É TODA A GRANDEZA, TUA, Ó DEUS, É TODA A GRANDEZA, E O PODER, E A GLÓRIA, E A VITÓRIA, E OS LOUVORES. VITÓRIA E LOUVORES. TUA, Ó DEUS; TUA, Ó DEUS, É A GRANDEZA, E O PODER, (A GRANDEZA E O PODER), E A GLÓRIA, E A VITÓRIA, E OS LOUVORES ETERNOS; POIS TUDO O QUE ESTÁ NO MAR, E ESTÁ NO CÉU E NA TERRA É TEU! TEU É O IMPÉRIO. TEU É O IMPÉRIO, Ó SENHOR! E TU ÉS ACIMA DE TODOS OS REIS, DE TODOS OS REIS, AMÉM ALELUIA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('699', '699 - QUE GRANDE REUNIÃO (JESUS DE NAZARÉ)', 'QUE GRANDE REUNIÃO CONVOCADA ALI, DA COLINA SE ACHAVA AO PÉ. OLHANDO MELHOR ENTRE ELES VI A JESUS DE NAZARÉ. VI CEGO EM MOMENTOS TORNAR A VER, ENTREVADO FICAR DE PÉ, E TODOS GOZAVAM REAL PRAZER EM JESUS DE NAZARÉ.\n\nCORO JESUS CRISTO, MEU MESTRE DIVINO, PRA SEMPRE POR TI VIVEREI. A VIDA SERÁ QUAL UM HINO EM TI, MEU AMADO REI.\n\nINDAGAM OS PRÍNCIPES ENTRE SI, TODOS QUEREM SABER QUEM É O HOMEM HUMILDE QUE ESTÁ ALI. É JESUS DE NAZARÉ. O MAR SE AQUIETA AO MANDO SEU, PODEROSO E GRANDIOSO ELE É, É O NOSSO PÃO QUE DESCEU DO CÉU, JESUS DE NAZARÉ.\n\nCORO JESUS CRISTO, MEU MESTRE DIVINO, PRA SEMPRE POR TI VIVEREI. A VIDA SERÁ QUAL UM HINO EM TI, MEU AMADO REI.\n\nSE GRANDES, QUAIS ONDAS, AS LUTAS SÃO, SE JÁ QUASE VACILA A FÉ, ACALMA A TORMENTA NO CORAÇÃO, JESUS DE NAZARÉ. A ÁGUA DA VIDA A TODOS DÁ AO CONTRITO ELE SALVARÁ! MEU DÚBIO CAMINHO EM SUA MÃO ESTÁ, JESUS DE NAZARÉ.\n\nCORO JESUS CRISTO, MEU MESTRE DIVINO, PRA SEMPRE POR TI VIVEREI. A VIDA SERÁ QUAL UM HINO EM TI, MEU AMADO REI.', 'QUE GRANDE REUNIÃO CONVOCADA ALI, DA COLINA SE ACHAVA AO PÉ. OLHANDO MELHOR ENTRE ELES VI A JESUS DE NAZARÉ. VI CEGO EM MOMENTOS TORNAR A VER, ENTREVADO FICAR DE PÉ, E TODOS GOZAVAM REAL PRAZER EM JESUS DE NAZARÉ. CORO JESUS CRISTO, MEU MESTRE DIVINO, PRA SEMPRE POR TI VIVEREI. A VIDA SERÁ QUAL UM HINO EM TI, MEU AMADO REI. INDAGAM OS PRÍNCIPES ENTRE SI, TODOS QUEREM SABER QUEM É O HOMEM HUMILDE QUE ESTÁ ALI. É JESUS DE NAZARÉ. O MAR SE AQUIETA AO MANDO SEU, PODEROSO E GRANDIOSO ELE É, É O NOSSO PÃO QUE DESCEU DO CÉU, JESUS DE NAZARÉ. SE GRANDES, QUAIS ONDAS, AS LUTAS SÃO, SE JÁ QUASE VACILA A FÉ, ACALMA A TORMENTA NO CORAÇÃO, JESUS DE NAZARÉ. A ÁGUA DA VIDA A TODOS DÁ AO CONTRITO ELE SALVARÁ! MEU DÚBIO CAMINHO EM SUA MÃO ESTÁ, JESUS DE NAZARÉ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('700', '700 - ESTEVÃO AVISTOU (EU TAMBÉM QUERO VER)', 'ESTEVÃO AVISTOU OS CÉUS ABERTOS: O FILHO DO DEUS PAI; VIU BEM DE PERTO, CORAL ANGELICAL CANTANDO AO REI DOS REIS, SALMODIANDO AO GRANDE DEUS!\n\nCORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR!\n\nJOÃO TAMBÉM OLHOU OS CÉUS ABERTOS, A GLÓRIA DO SENHOR, SIM, MANIFESTOS: UMA CIDADE REAL, UM RIO DE CRISTAL! NUNCA, JAMAIS SE VIU BELEZAS MIL!\n\nCORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR!\n\nELIAS CONTEMPLOU, NO FIRMAMENTO: CARRO DE FOGO QUE BRILHAVA AO VENTO; E PARA O CÉU HOMEM DE DEUS SUBIU ROMPENDO AS NUVENS CELESTIAIS!\n\nCORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR! INSTRUMENTOS\n\nCORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR! FINAL: EU TAMBÉM QUERO VER!', 'ESTEVÃO AVISTOU OS CÉUS ABERTOS: O FILHO DO DEUS PAI; VIU BEM DE PERTO, CORAL ANGELICAL CANTANDO AO REI DOS REIS, SALMODIANDO AO GRANDE DEUS! CORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR! JOÃO TAMBÉM OLHOU OS CÉUS ABERTOS, A GLÓRIA DO SENHOR, SIM, MANIFESTOS: UMA CIDADE REAL, UM RIO DE CRISTAL! NUNCA, JAMAIS SE VIU BELEZAS MIL! ELIAS CONTEMPLOU, NO FIRMAMENTO: CARRO DE FOGO QUE BRILHAVA AO VENTO; E PARA O CÉU HOMEM DE DEUS SUBIU ROMPENDO AS NUVENS CELESTIAIS! CORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR! INSTRUMENTOS CORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHO