INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('701', '701 - AS TUAS MÃOS', 'AS TUAS MÃOS DIRIGEM MEU DESTINO OH! DEUS DE AMOR! FOLGO QUE SEJA ASSIM! TEUS SÃO OS MEUS PODERES, MINHA VIDA; EM TUDO, ETERNO PAI, DISPÕE DE MIM!\n\nMEUS DIAS SEJAM CURTOS OU COMPRIDOS, PASSADOS EM TRISTEZAS OU PRAZER, EM SOMBRA OU LUZ DE ACORDO COM TEU PLANO! E É TUDO BOM SE VEM DO TEU QUERER.\n\nAS TUAS MÃOS DIRIGEM MEU DESTINO, DANTES CRAVADAS NA SANGRENTA CRUZ. POR MEUS PECADOS FORAM TRASPASSADAS BEM POSSO NELAS DESCANSAR, JESUS!\n\nNOS CÉUS ERGUIDAS SEMPRE INTERCEDENDO AS SANTAS MÃOS NÃO PEDIRÃO EM VÃO! AO SEU CUIDADO, EM PLENA CONFIANÇA, ENTREGO A MINHA ETERNA SALVAÇÃO.\n\nAS TUAS MÃOS DIRIGEM MEU DESTINO; O ACASO, PARA MIM, NÃO HAVERÁ! O GRANDE PAI VIGIA O MEU CAMINHO, E SEM MOTIVO NÃO ME AFLIGIRÁ.\n\nTENHO NO SEU PODER CONSTANTE APOIO, FORTE É SEU BRAÇO, INSONE O SEU AMOR; E EM BREVE, ENTRANDO NA CIDADE ETERNA EU LOUVAREI MEU REI E SALVADOR!', 'AS TUAS MÃOS DIRIGEM MEU DESTINO OH! DEUS DE AMOR! FOLGO QUE SEJA ASSIM! TEUS SÃO OS MEUS PODERES, MINHA VIDA; EM TUDO, ETERNO PAI, DISPÕE DE MIM! MEUS DIAS SEJAM CURTOS OU COMPRIDOS, PASSADOS EM TRISTEZAS OU PRAZER, EM SOMBRA OU LUZ DE ACORDO COM TEU PLANO! E É TUDO BOM SE VEM DO TEU QUERER. AS TUAS MÃOS DIRIGEM MEU DESTINO, DANTES CRAVADAS NA SANGRENTA CRUZ. POR MEUS PECADOS FORAM TRASPASSADAS BEM POSSO NELAS DESCANSAR, JESUS! NOS CÉUS ERGUIDAS SEMPRE INTERCEDENDO AS SANTAS MÃOS NÃO PEDIRÃO EM VÃO! AO SEU CUIDADO, EM PLENA CONFIANÇA, ENTREGO A MINHA ETERNA SALVAÇÃO. AS TUAS MÃOS DIRIGEM MEU DESTINO; O ACASO, PARA MIM, NÃO HAVERÁ! O GRANDE PAI VIGIA O MEU CAMINHO, E SEM MOTIVO NÃO ME AFLIGIRÁ. TENHO NO SEU PODER CONSTANTE APOIO, FORTE É SEU BRAÇO, INSONE O SEU AMOR; E EM BREVE, ENTRANDO NA CIDADE ETERNA EU LOUVAREI MEU REI E SALVADOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('702', '702 - OS CÉUS EM BREVE SE ABRIRÃO', 'OS CÉUS EM BREVE SE ABRIRÃO E TODOS TE VERÃO, Ó FILHO DE DAVI; OS REIS VIRÃO A TI, DE JOELHOS ADORAR AQUELE QUE PRA SEMPRE IRÁ REINAR.\n\nENTÃO TODOS VERÃO QUEM ÉS, PROSTRADOS A TEUS PÉS ENTENDERÃO O AMOR, O VERDADEIRO AMOR QUE TE LEVOU, JESUS, A DAR-ME A TUA VIDA NAQUELA CRUZ.\n\nCORO NÓS TE SAUDAMOS; HOSANA AO FILHO DE DAVI! BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA!\n\nOS CÉUS, DE GOZO, TREMERÃO, E TODA CRIAÇÃO SE ALEGRARÁ EM TI, Ó FILHO DE DAVI, POIS NA VIDA E PAZ AO NOSSO MUNDO ENFIM TU NOS TRARÁS.\n\nCORO NÓS TE SAUDAMOS; HOSANA AO FILHO DE DAVI! BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA! FINAL: HOSANA!', 'OS CÉUS EM BREVE SE ABRIRÃO E TODOS TE VERÃO, Ó FILHO DE DAVI; OS REIS VIRÃO A TI, DE JOELHOS ADORAR AQUELE QUE PRA SEMPRE IRÁ REINAR. ENTÃO TODOS VERÃO QUEM ÉS, PROSTRADOS A TEUS PÉS ENTENDERÃO O AMOR, O VERDADEIRO AMOR QUE TE LEVOU, JESUS, A DAR-ME A TUA VIDA NAQUELA CRUZ. CORO NÓS TE SAUDAMOS; HOSANA AO FILHO DE DAVI! BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA! OS CÉUS, DE GOZO, TREMERÃO, E TODA CRIAÇÃO SE ALEGRARÁ EM TI, Ó FILHO DE DAVI, POIS NA VIDA E PAZ AO NOSSO MUNDO ENFIM TU NOS TRARÁS. CORO NÓS TE SAUDAMOS; HOSANA AO FILHO DE DAVI! BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA!  HOSANA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('703', '703 - SERÁ DE MANHÃ', 'SERÁ DE MANHÃ, NO COMEÇO DO DIA? SERÁ QUANDO A LUZ PELAS TREVAS PENETRA, QUE CRISTO HÁ DE VIR COM OS ANJOS DA GLÓRIA, RECEBER DESTE MUNDO OS SEUS?\n\nCORO OH! JESUS, SALVADOR, SENHOR! QUANDO VAMOS CANTAR: CRISTO VOLTA, ALELUIA, ALELUIA, AMÉM! ALELUIA, AMÉM!\n\nSERÁ NA AURORA, SERÁ PELA TARDE? SIM, PODERÁ SER QUE AS TREVAS DA NOITE SE TORNEM NA LUZ DESSE BRILHO DE GLÓRIA, QUANDO CRISTO OS SEUS RECEBER.\n\nCORO OH! JESUS, SALVADOR, SENHOR! QUANDO VAMOS CANTAR: CRISTO VOLTA, ALELUIA, ALELUIA, AMÉM! ALELUIA, AMÉM!\n\nOH! GOZO SEM FIM, QUANDO A MORTE VENCIDA, JESUS REVESTIR-NOS DE PERPÉTUA VIDA, E TODOS NÓS FORMOS MORAR LÁ NA GLÓRIA, NESSE LAR QUE JESUS PREPAROU!\n\nCORO OH! JESUS, SALVADOR, SENHOR! QUANDO VAMOS CANTAR: CRISTO VOLTA, ALELUIA, ALELUIA, AMÉM! ALELUIA, AMÉM!', 'SERÁ DE MANHÃ, NO COMEÇO DO DIA? SERÁ QUANDO A LUZ PELAS TREVAS PENETRA, QUE CRISTO HÁ DE VIR COM OS ANJOS DA GLÓRIA, RECEBER DESTE MUNDO OS SEUS? CORO OH! JESUS, SALVADOR, SENHOR! QUANDO VAMOS CANTAR: CRISTO VOLTA, ALELUIA, ALELUIA, AMÉM! ALELUIA, AMÉM! SERÁ NA AURORA, SERÁ PELA TARDE? SIM, PODERÁ SER QUE AS TREVAS DA NOITE SE TORNEM NA LUZ DESSE BRILHO DE GLÓRIA, QUANDO CRISTO OS SEUS RECEBER. OH! GOZO SEM FIM, QUANDO A MORTE VENCIDA, JESUS REVESTIR-NOS DE PERPÉTUA VIDA, E TODOS NÓS FORMOS MORAR LÁ NA GLÓRIA, NESSE LAR QUE JESUS PREPAROU!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('704', '704 - SENHOR, SE ESTIVESSES AQUI (LÁZARO)', '704 - SENHOR, SE ESTIVESSES AQUI (LÁZARO)\n\nSENHOR, SE ESTIVESSES AQUI O MEU IRMÃO NÃO TERIA MORRIDO, FOI O QUE DISSE AQUELA MULHER, LANÇANDO-SE AOS PÉS DE JESUS QUE AO OUVIR O CHORO QUE HAVIA NAQUELE LUGAR, MOVEU-SE EM ESPÍRITO, JESUS CHOROU.\n\nCORO TIRAI A PEDRA DISSE JESUS E NÃO PODIAM COMPREENDER O QUE IRIA FAZER, ENTÃO A LÁZARO ELE CHAMOU E O RESSUSCITOU, TODOS VIRAM A GLÓRIA DE DEUS. ELE TROUXE A ESPERANÇA, ELE TROUXE A VIDA, POIS ELE É O AUTOR DA VIDA. INSTRUMENTOS\nBIS\n\nSE ESTÁS COMO AQUELA MULHER, SE PARA O TEU PROBLEMA NÃO TEM SOLUÇÃO, CHORA AOS PÉS DE JESUS, E ELE ENXUGARÁ AS TUAS LÁGRIMAS. VERÁS A GLÓRIA DE DEUS.\n\nCORO (2X) TIRAI A PEDRA DISSE JESUS E NÃO PODIAM COMPREENDER O QUE IRIA FAZER, ENTÃO A LÁZARO ELE CHAMOU E O RESSUSCITOU, TODOS VIRAM A GLÓRIA DE DEUS. ELE TROUXE A ESPERANÇA, ELE TROUXE A VIDA, POIS ELE É O AUTOR DA VIDA.\nBIS', '704 - SENHOR, SE ESTIVESSES AQUI (LÁZARO) SENHOR, SE ESTIVESSES AQUI O MEU IRMÃO NÃO TERIA MORRIDO, FOI O QUE DISSE AQUELA MULHER, LANÇANDO-SE AOS PÉS DE JESUS QUE AO OUVIR O CHORO QUE HAVIA NAQUELE LUGAR, MOVEU-SE EM ESPÍRITO, JESUS CHOROU. CORO TIRAI A PEDRA DISSE JESUS E NÃO PODIAM COMPREENDER O QUE IRIA FAZER, ENTÃO A LÁZARO ELE CHAMOU E O RESSUSCITOU, TODOS VIRAM A GLÓRIA DE DEUS. ELE TROUXE A ESPERANÇA, ELE TROUXE A VIDA, POIS ELE É O AUTOR DA VIDA. INSTRUMENTOS SE ESTÁS COMO AQUELA MULHER, SE PARA O TEU PROBLEMA NÃO TEM SOLUÇÃO, CHORA AOS PÉS DE JESUS, E ELE ENXUGARÁ AS TUAS LÁGRIMAS. VERÁS A GLÓRIA DE DEUS. CORO  TIRAI A PEDRA DISSE JESUS E NÃO PODIAM COMPREENDER O QUE IRIA FAZER, ENTÃO A LÁZARO ELE CHAMOU E O RESSUSCITOU, TODOS VIRAM A GLÓRIA DE DEUS. ELE TROUXE A ESPERANÇA, ELE TROUXE A VIDA, POIS ELE É O AUTOR DA VIDA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('705', '705 - GRANDES SÃO, SENHOR, OS TEUS FEITOS (GRATIDÃO)', 'GRANDES SÃO, SENHOR, OS TEUS FEITOS! A TI DEVO TODO O MEU LOUVOR. O TEU NOME É PODEROSO, E A TUA GRAÇA É TUDO PARA MIM! (PARA MIM)\n\nTU, Ó DEUS, ÉS ONIPOTENTE! TUA MORADA É ETERNA, SENHOR MINHA VIDA SE ENCHE DE ALEGRIA, A MINH’ALMA TE ENGRANDECE POR TE SERVIR.\n\nA TI EU DEVO TODA HONRA E TODA GLÓRIA! (TODA GLÓRIA) NO TEU NOME ENCONTRO PAZ E VITÓRIA, MINHA VIDA É FRUTO DO TEU GRANDE AMOR, POR ISSO DIGO QUE ÉS MEU REI E MEU SENHOR! INSTRUMENTOS\nBIS\n\nFINAL: GRANDES SÃO SENHOR OS TEUS FEITOS! (GRANDES SÃO).', 'GRANDES SÃO, SENHOR, OS TEUS FEITOS! A TI DEVO TODO O MEU LOUVOR. O TEU NOME É PODEROSO, E A TUA GRAÇA É TUDO PARA MIM! (PARA MIM) TU, Ó DEUS, ÉS ONIPOTENTE! TUA MORADA É ETERNA, SENHOR MINHA VIDA SE ENCHE DE ALEGRIA, A MINH’ALMA TE ENGRANDECE POR TE SERVIR. A TI EU DEVO TODA HONRA E TODA GLÓRIA! (TODA GLÓRIA) NO TEU NOME ENCONTRO PAZ E VITÓRIA, MINHA VIDA É FRUTO DO TEU GRANDE AMOR, POR ISSO DIGO QUE ÉS MEU REI E MEU SENHOR! INSTRUMENTOS GRANDES SÃO SENHOR OS TEUS FEITOS! (GRANDES SÃO).',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('706', '706 - DESTE MUNDO ESCRAVO FUI (GRANDES COISAS FEZ O SENHOR POR NÓS)', 'DESTE MUNDO ESCRAVO FUI, PRESO EM ILUSÕES SEM FIM. E ME VIA A PERGUNTAR: QUANDO VIRÁ O SENHOR POR MIM? COMO QUANDO EM CATIVEIRO O SENHOR TROUXE A SIÃO, O SEU POVO AMADO.\n\nFOI COMO UM SONHO, NOSSA BOCA SE ENCHEU DE RISO E CANTO. E DIZIA ENTRE AS NAÇÕES: GRANDES COISAS FEZ O SENHOR! GRANDES COISAS FEZ O SENHOR POR NÓS GRANDES COISAS FEZ O SENHOR POR NÓS JÁ PLANTEI AS MINHAS LÁGRIMAS, COLHEREI OS FRUTOS DE JUSTIÇA.\nBIS\n\nCOMO QUANDO EM CATIVEIRO, ME TROUXE O SENHOR. FOI COMO UM SONHO, MINHA BOCA SE ENCHEU DE RISO E CANTO E DIREI ENTRE AS NAÇÕES: QUE GRANDES COISAS FEZ O SENHOR! GRANDES COISAS FEZ O SENHOR POR NÓS GRANDES COISAS FEZ O SENHOR POR NÓS VIVO AGORA A ESPERANÇA DE COM ELE UM DIA ME ENCONTRAR NO CÉU.\n\nFINAL: VIVO AGORA A ESPERANÇA DE COM ELE UM DIA ME ENCONTRAR NO CÉU, NO CÉU.', 'DESTE MUNDO ESCRAVO FUI, PRESO EM ILUSÕES SEM FIM. E ME VIA A PERGUNTAR: QUANDO VIRÁ O SENHOR POR MIM? COMO QUANDO EM CATIVEIRO O SENHOR TROUXE A SIÃO, O SEU POVO AMADO. FOI COMO UM SONHO, NOSSA BOCA SE ENCHEU DE RISO E CANTO. E DIZIA ENTRE AS NAÇÕES: GRANDES COISAS FEZ O SENHOR! GRANDES COISAS FEZ O SENHOR POR NÓS GRANDES COISAS FEZ O SENHOR POR NÓS JÁ PLANTEI AS MINHAS LÁGRIMAS, COLHEREI OS FRUTOS DE JUSTIÇA. COMO QUANDO EM CATIVEIRO, ME TROUXE O SENHOR. FOI COMO UM SONHO, MINHA BOCA SE ENCHEU DE RISO E CANTO E DIREI ENTRE AS NAÇÕES: QUE GRANDES COISAS FEZ O SENHOR! GRANDES COISAS FEZ O SENHOR POR NÓS GRANDES COISAS FEZ O SENHOR POR NÓS VIVO AGORA A ESPERANÇA DE COM ELE UM DIA ME ENCONTRAR NO CÉU. VIVO AGORA A ESPERANÇA DE COM ELE UM DIA ME ENCONTRAR NO CÉU, NO CÉU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('707', '707 - EU CONFIO EM TI, SENHOR', 'EU CONFIO EM TI, SENHOR! MESMO QUANDO TUDO NÃO VAI BEM, E A ESPERANÇA FUGIR, EU OLHAREI SÓ PRA TI. ACALMA O MEU CORAÇÃO, SEGURA MINHA MÃO E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR.\n\nPORQUE NUNCA SE OUVIU, E NUNCA SE VIU UM DEUS ALÉM DE TI. E EU APRENDI A CONFIAR NO DEUS PROVEDOR QUE NÃO FALHARÁ. ROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. SOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. EU CONFIO EM TI, SENHOR! JESUS! (BIS) INSTRUMENTOS\n\nEU CONFIO EM TI, SENHOR! MEU CAMINHO PONHO EM TEU ALTAR. TUA VOZ EU QUERO OUVIR, TUA PRESENÇA, SIM, QUERO SENTIR. ENSINA-ME A CAMINHAR E A TE HONRAR, SENHOR, E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR.\n\nPORQUE NUNCA SE OUVIU, E NUNCA SE VIU UM DEUS ALÉM DE TI. E EU APRENDI A CONFIAR NO DEUS PROVEDOR QUE NÃO FALHARÁ. ROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. SOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. EU CONFIO EM TI, SENHOR! (BIS)\n\nACALMA O MEU CORAÇÃO, SEGURA MINHA MÃO E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR. ROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. SOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. EU CONFIO EM TI, SENHOR! (2X) JESUS! FINAL: ABRIGO SEGURO, JESUS!', 'EU CONFIO EM TI, SENHOR! MESMO QUANDO TUDO NÃO VAI BEM, E A ESPERANÇA FUGIR, EU OLHAREI SÓ PRA TI. ACALMA O MEU CORAÇÃO, SEGURA MINHA MÃO E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR. PORQUE NUNCA SE OUVIU, E NUNCA SE VIU UM DEUS ALÉM DE TI. E EU APRENDI A CONFIAR NO DEUS PROVEDOR QUE NÃO FALHARÁ. ROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. SOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. EU CONFIO EM TI, SENHOR! JESUS!  INSTRUMENTOS EU CONFIO EM TI, SENHOR! MEU CAMINHO PONHO EM TEU ALTAR. TUA VOZ EU QUERO OUVIR, TUA PRESENÇA, SIM, QUERO SENTIR. ENSINA-ME A CAMINHAR E A TE HONRAR, SENHOR, E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR. PORQUE NUNCA SE OUVIU, E NUNCA SE VIU UM DEUS ALÉM DE TI. E EU APRENDI A CONFIAR NO DEUS PROVEDOR QUE NÃO