INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('708', '708 - OUVI NO CÉU (BODAS DO CORDEIRO)', '708 - OUVI NO CÉU (BODAS DO CORDEIRO)\n\nOUVI NO CÉU A VOZ DE UMA MULTIDÃO E VI OS ANCIÃOS E OS SERES QUE SE PROSTRARAM EM ADORAÇÃO AO NOSSO DEUS. QUE NO TRONO ASSENTADO ESTÁ DIZENDO AMÉM, ALELUIA!\n\nALEGREMOS E DEMOS-LHE GLÓRIA, GLÓRIA PORQUE SÃO CHEGADAS AS BODAS DO CORDEIRO E SUA ESPOSA PRONTA ESTÁ REVESTIDA DE UM LINHO FINO QUE RESPLANDECE PORQUE O LINHO SÃO AS JUSTIÇAS DOS SANTOS. INSTRUMENTOS\n\nBEM AVENTURADOS SÃO AQUELES CONVIDADOS PORQUE DERAM TUDO O QUE TINHAM A JESUS O CORDEIRO, FILHO DE DEUS! QUE VIGIARAM E ORARAM E VENCERAM ESTÃO CHEIOS DO ESPÍRITO LOUVANDO AO SENHOR.\nBIS\n\nSÃO PALAVRAS DE DEUS. INSTRUMENTOS FINAL: SÃO PALAVRAS DE DEUS.', '708 - OUVI NO CÉU (BODAS DO CORDEIRO) OUVI NO CÉU A VOZ DE UMA MULTIDÃO E VI OS ANCIÃOS E OS SERES QUE SE PROSTRARAM EM ADORAÇÃO AO NOSSO DEUS. QUE NO TRONO ASSENTADO ESTÁ DIZENDO AMÉM, ALELUIA! ALEGREMOS E DEMOS-LHE GLÓRIA, GLÓRIA PORQUE SÃO CHEGADAS AS BODAS DO CORDEIRO E SUA ESPOSA PRONTA ESTÁ REVESTIDA DE UM LINHO FINO QUE RESPLANDECE PORQUE O LINHO SÃO AS JUSTIÇAS DOS SANTOS. INSTRUMENTOS BEM AVENTURADOS SÃO AQUELES CONVIDADOS PORQUE DERAM TUDO O QUE TINHAM A JESUS O CORDEIRO, FILHO DE DEUS! QUE VIGIARAM E ORARAM E VENCERAM ESTÃO CHEIOS DO ESPÍRITO LOUVANDO AO SENHOR. SÃO PALAVRAS DE DEUS. INSTRUMENTOS  SÃO PALAVRAS DE DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('709', '709 - RESPLANDECE, Ó JERUSALÉM', 'RESPLANDECE Ó JERUSALÉM! PORQUE NASCE SOBRE TI A TUA LUZ. JERUSALÉM, ÉS ILUMINADA PELA GLÓRIA DO SENHOR, AS TUAS RUAS SÃO DE OURO, Ó QUANTO ALMEJO NELAS ANDAR.\n\nJERUSALÉM, Ó QUANTO ALMEJO ESTAR AO LADO DO MEU SENHOR PRA SEMPRE O ADORAR. JERUSALÉM, POR SUAS PORTAS VOU ENTRAR E EM CORO CELESTIAL, A JESUS ADORAR. INSTRUMENTOS\n\nDENTRO DOS TEUS MUROS, (Ó JERUSALÉM) ASSENTA-SE NO TRONO À DESTRA DE DEUS PAI, O NOSSO REI, O CORDEIRO QUE VENCEU.\n\nJERUSALÉM, QUERO CONTEMPLAR, A TUA GLÓRIA COM A GLÓRIA DO SENHOR, QUERO HABITAR DENTRO DOS TEUS MUROS. FINAL: Ó JERUSALÉM. (JERUSALÉM)\n\n3X', 'RESPLANDECE Ó JERUSALÉM! PORQUE NASCE SOBRE TI A TUA LUZ. JERUSALÉM, ÉS ILUMINADA PELA GLÓRIA DO SENHOR, AS TUAS RUAS SÃO DE OURO, Ó QUANTO ALMEJO NELAS ANDAR. JERUSALÉM, Ó QUANTO ALMEJO ESTAR AO LADO DO MEU SENHOR PRA SEMPRE O ADORAR. JERUSALÉM, POR SUAS PORTAS VOU ENTRAR E EM CORO CELESTIAL, A JESUS ADORAR. INSTRUMENTOS DENTRO DOS TEUS MUROS, (Ó JERUSALÉM) ASSENTA-SE NO TRONO À DESTRA DE DEUS PAI, O NOSSO REI, O CORDEIRO QUE VENCEU. JERUSALÉM, QUERO CONTEMPLAR, A TUA GLÓRIA COM A GLÓRIA DO SENHOR, QUERO HABITAR DENTRO DOS TEUS MUROS.  Ó JERUSALÉM. (JERUSALÉM)',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('710', '710 - O MEU VIVER É CRISTO', 'O MEU VIVER É CRISTO, MEU SALVADOR, POIS SE ENTREGOU PARA A VIDA ETERNA ME DAR. HOJE VENHO A TI PARA EXPRESSAR.\n\nCORO TODA GRATIDÃO A TI JESUS, PORQUE TU ÉS MARAVILHOSO. PRA SEMPRE CANTAREI A TI, MEU REI, POIS TU ME FIZESTE BEM.\n\nMEU VERDADEIRO AMIGO SEMPRE FIEL. ESCONDERIJO MEU, PARA SEMPRE EMANUEL. ÉS O MEU SENHOR, A RAZÃO DO MEU VIVER! HOJE VENHO A TI PARA EXPRESSAR.\n\nCORO (2X) TODA GRATIDÃO A TI JESUS, PORQUE TU ÉS MARAVILHOSO. PRA SEMPRE CANTAREI A TI, MEU REI, POIS TU ME FIZESTE BEM. FINAL: POIS TU ME FIZESTE BEM.', 'O MEU VIVER É CRISTO, MEU SALVADOR, POIS SE ENTREGOU PARA A VIDA ETERNA ME DAR. HOJE VENHO A TI PARA EXPRESSAR. CORO TODA GRATIDÃO A TI JESUS, PORQUE TU ÉS MARAVILHOSO. PRA SEMPRE CANTAREI A TI, MEU REI, POIS TU ME FIZESTE BEM. MEU VERDADEIRO AMIGO SEMPRE FIEL. ESCONDERIJO MEU, PARA SEMPRE EMANUEL. ÉS O MEU SENHOR, A RAZÃO DO MEU VIVER! HOJE VENHO A TI PARA EXPRESSAR. CORO  TODA GRATIDÃO A TI JESUS, PORQUE TU ÉS MARAVILHOSO. PRA SEMPRE CANTAREI A TI, MEU REI, POIS TU ME FIZESTE BEM.  POIS TU ME FIZESTE BEM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('711', '711 - TUA GRAÇA ME BASTA, Ó SENHOR', 'TUA GRAÇA ME BASTA, Ó SENHOR, ESTA GRAÇA QUE UM DIA ME ALCANÇOU. TEU ESPÍRITO ALEGRA O MEU CORAÇÃO, ME FAZENDO VIVER A REVELAÇÃO, RENOVANDO A SALVAÇÃO EM MINHA VIDA.\n\nCORO DERRAMA SOBRE NÓS A TUA GRAÇA (DERRAMA TUA GRAÇA), DERRAMA SOBRE NÓS O TEU PODER, PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS.\n\nTUA GRAÇA NOS LIVRA DO MUNDO, (TUA GRAÇA NOS LIVRA DO MUNDO) TRAZENDO VIDA NOVA A CADA DIA. TEU ESPÍRITO HABITA ENTRE NÓS, E NÓS TEMOS A CERTEZA DA SALVAÇÃO, ANSIANDO EM BREVE ESTAR NA TUA GLÓRIA.\n\nCORO (2X) DERRAMA SOBRE NÓS A TUA GRAÇA (DERRAMA TUA GRAÇA), DERRAMA SOBRE NÓS O TEU PODER, PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS.\n\nFINAL: PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS! (MARANATA! ORA VEM) VEM, JESUS!', 'TUA GRAÇA ME BASTA, Ó SENHOR, ESTA GRAÇA QUE UM DIA ME ALCANÇOU. TEU ESPÍRITO ALEGRA O MEU CORAÇÃO, ME FAZENDO VIVER A REVELAÇÃO, RENOVANDO A SALVAÇÃO EM MINHA VIDA. CORO DERRAMA SOBRE NÓS A TUA GRAÇA (DERRAMA TUA GRAÇA), DERRAMA SOBRE NÓS O TEU PODER, PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS. TUA GRAÇA NOS LIVRA DO MUNDO, (TUA GRAÇA NOS LIVRA DO MUNDO) TRAZENDO VIDA NOVA A CADA DIA. TEU ESPÍRITO HABITA ENTRE NÓS, E NÓS TEMOS A CERTEZA DA SALVAÇÃO, ANSIANDO EM BREVE ESTAR NA TUA GLÓRIA. CORO  DERRAMA SOBRE NÓS A TUA GRAÇA (DERRAMA TUA GRAÇA), DERRAMA SOBRE NÓS O TEU PODER, PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS. PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS! (MARANATA! ORA VEM) VEM, JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('712', '712 - AO FINDAR O LABOR DESTA VIDA (A ÚLTIMA HORA)', '712 - AO FINDAR O LABOR DESTA VIDA (A ÚLTIMA HORA)\n\nAO FINDAR O LABOR DESTA VIDA, QUANDO A MORTE A TEU LADO CHEGAR, QUE DESTINO HÁ DE TER A TUA ALMA? QUAL SERÁ NO FUTURO TEU LAR?\n\nCORO MEU AMIGO, HOJE TU TENS A ESCOLHA: VIDA OU MORTE QUAL VAIS ACEITAR? AMANHÃ PODE SER MUITO TARDE; HOJE CRISTO TE QUER LIBERTAR.\n\nTU PROCURAS A PAZ NESTE MUNDO, EM PRAZERES QUE PASSAM EM VÃO, MAS NA ÚLTIMA HORA DA VIDA, ELES NUNCA TE SATISFARÃO.\n\nCORO MEU AMIGO, HOJE TU TENS A ESCOLHA: VIDA OU MORTE QUAL VAIS ACEITAR? AMANHÃ PODE SER MUITO TARDE; HOJE CRISTO TE QUER LIBERTAR.\n\nCOM TUA ALMA MANCHADA NÃO PODES NUNCA VER O SEMBLANTE DE DEUS; SÓ OS CRENTES COM CORAÇÃO LIMPO PODERÃO TER O GOZO DOS CÉUS.\n\nCORO MEU AMIGO, HOJE TU TENS A ESCOLHA: VIDA OU MORTE QUAL VAIS ACEITAR? AMANHÃ PODE SER MUITO TARDE; HOJE CRISTO TE QUER LIBERTAR.\n\nSE TU QUERES DEIXAR TEUS PECADOS, ENTREGAR TUA VIDA A JESUS, TU TERÁS, SIM, NA ÚLTIMA HORA UM CAMINHO BRILHANTE DE LUZ.\n\nCORO MEU AMIGO, HOJE TU TENS A ESCOLHA: VIDA OU MORTE QUAL VAIS ACEITAR? AMANHÃ PODE SER MUITO TARDE; HOJE CRISTO TE QUER LIBERTAR.', '712 - AO FINDAR O LABOR DESTA VIDA (A ÚLTIMA HORA) AO FINDAR O LABOR DESTA VIDA, QUANDO A MORTE A TEU LADO CHEGAR, QUE DESTINO HÁ DE TER A TUA ALMA? QUAL SERÁ NO FUTURO TEU LAR? CORO MEU AMIGO, HOJE TU TENS A ESCOLHA: VIDA OU MORTE QUAL VAIS ACEITAR? AMANHÃ PODE SER MUITO TARDE; HOJE CRISTO TE QUER LIBERTAR. TU PROCURAS A PAZ NESTE MUNDO, EM PRAZERES QUE PASSAM EM VÃO, MAS NA ÚLTIMA HORA DA VIDA, ELES NUNCA TE SATISFARÃO. COM TUA ALMA MANCHADA NÃO PODES NUNCA VER O SEMBLANTE DE DEUS; SÓ OS CRENTES COM CORAÇÃO LIMPO PODERÃO TER O GOZO DOS CÉUS. SE TU QUERES DEIXAR TEUS PECADOS, ENTREGAR TUA VIDA A JESUS, TU TERÁS, SIM, NA ÚLTIMA HORA UM CAMINHO BRILHANTE DE LUZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('713', '713 - CAREÇO DE JESUS!', 'CAREÇO DE JESUS! SEMPRE DE TI, SENHOR! SOMENTE A TUA VOZ TEM PARA MIM VALOR!\n\nCORO DE TI, SENHOR, CAREÇO, SEMPRE DE TI CAREÇO! OH! DÁ-ME A TUA BÊNÇÃO, ASPIRO A TI!\n\nCAREÇO DE JESUS! UNIDO A TI, SENHOR, PECADO E TENTAÇÃO PERDEM O SEU VIGOR.\n\nCORO DE TI, SENHOR, CAREÇO, SEMPRE DE TI CAREÇO! OH! DÁ-ME A TUA BÊNÇÃO, ASPIRO A TI!\n\nCAREÇO DE JESUS! REGE MEU CORAÇÃO! ENSINA-ME A VIVER EM SANTA RETIDÃO!\n\nCORO DE TI, SENHOR, CAREÇO, SEMPRE DE TI CAREÇO! OH! DÁ-ME A TUA BÊNÇÃO, ASPIRO A TI!\n\nCAREÇO DE JESUS! NAS TREVAS E NA LUZ! SEM TI A VIDA É VÃ; SOU POBRE SEM JESUS.\n\nCORO DE TI, SENHOR, CAREÇO, SEMPRE DE TI CAREÇO! OH! DÁ-ME A TUA BÊNÇÃO, ASPIRO A TI!', 'CAREÇO DE JESUS! SEMPRE DE TI, SENHOR! SOMENTE A TUA VOZ TEM PARA MIM VALOR! CORO DE TI, SENHOR, CAREÇO, SEMPRE DE TI CAREÇO! OH! DÁ-ME A TUA BÊNÇÃO, ASPIRO A TI! CAREÇO DE JESUS! UNIDO A TI, SENHOR, PECADO E TENTAÇÃO PERDEM O SEU VIGOR. CAREÇO DE JESUS! REGE MEU CORAÇÃO! ENSINA-ME A VIVER EM SANTA RETIDÃO! CAREÇO DE JESUS! NAS TREVAS E NA LUZ! SEM TI A VIDA É VÃ; SOU POBRE SEM JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('714', '714 - COM TUA MÃO SEGURA BEM A MINHA (AMPARO DIVINO)', 'COM TUA MÃO SEGURA BEM A MINHA, POIS EU TÃO FRÁGIL SOU, Ó SALVADOR, QUE NÃO ME ATREVO A DAR JAMAIS UM PASSO SEM TEU AMPARO, CRISTO, MEU SENHOR! INSTRUMENTOS\n\nCOM TUA MÃO SEGURA BEM A MINHA, E PELO MUNDO, ALEGRE, SEGUIREI. MESMO ONDE AS SOMBRAS CAEM MAIS ESCURAS, TEU ROSTO VENDO, NADA TEMEREI. INSTRUMENTOS\n\nQUANDO EU CHEGAR À BEIRA DESSE RIO QUE TU, POR MIM, QUISESTE ATRAVESSAR, COM TUA MÃO SEGURA BEM A MINHA, E SOBRE A MORTE EU HEI DE TRIUNFAR. INSTRUMENTOS\n\nAO REGRESSARES, LÁ DOS CÉUS DESCENDO, A MINHA MÃO SEGURA BEM, SENHOR, POIS, MEU JESUS, EU QUERO ENTRAR CONTIGO LÁ ONDE EU GOZE O TEU ETERNO AMOR.', 'COM TUA MÃO SEGURA BEM A MINHA, POIS EU TÃO FRÁGIL SOU, Ó SALVADOR, QUE NÃO ME ATREVO A DAR JAMAIS UM PASSO SEM TEU AMPARO, CRISTO, MEU SENHOR! INSTRUMENTOS COM TUA MÃO SEGURA BEM A MINHA, E PELO MUNDO, ALEGRE, SEGUIREI. MESMO ONDE AS SOMBRAS CAEM MAIS ESCURAS, TEU ROSTO VENDO, NADA TEMEREI. INSTRUMENTOS QUANDO EU CHEGAR À BEIRA DESSE RIO QUE TU, POR MIM, QUISESTE ATRAVESSAR, COM TUA MÃO SEGURA BEM A MINHA, E SOBRE A MORTE EU HEI DE TRIUNFAR. INSTRUMENTOS AO REGRESSARES, LÁ DOS CÉUS DESCENDO, A MINHA MÃO SEGURA BEM, SENHOR, POIS, MEU JESUS, EU QUERO ENTRAR CONTIGO LÁ ONDE EU GOZE O TEU ETERNO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('715', '715 - DEUS VOS GUARDE PELO SEU PODER', 'DEUS VOS GUARDE PELO SEU PODER, SEMPRE ESTEJA AO VOSSO LADO; VOS DISPENSE O SEU CUIDADO, DEUS VOS GUARDE PELO SEU PODER.\n\nCORO PELO SEU PODER E NO SEU AMOR, TÉ NOS ENCONTRARMOS COM JESUS; PELO SEU PODER E NO SEU AMOR, OH! QUE DEUS VOS GUARDE EM SUA LUZ.\n\nDEUS VOS GUARDE BEM NO SEU AMOR, CONSOLADOS E CONTENTES; ACHEGADOS PARA OS CRENTES, DEUS VOS GUARDE BEM NO SEU AMOR.\n\nCORO PELO SEU PODER E NO SEU AMOR, TÉ NOS ENCONTRARMOS COM JESUS; PELO SEU PODER E NO SEU AMOR, OH! QUE DEUS VOS GUARDE EM SUA LUZ.\n\nDEUS VOS GUARDE DO PODER DO MAL, DA RUÍNA, DO PECADO; DOS MOTINS DE QUALQUER LADO, DEUS VOS GUARDE DO PODER DO MAL,\n\nCORO PELO SEU PODER E NO SEU AMOR, TÉ NOS ENCONTRARMOS COM JESUS; PELO SEU PODER E NO SEU AMOR, OH! QUE DEUS VOS GUARDE EM SUA LUZ.\n\nDEUS VOS GUARDE PARA SEU LOUVOR, PARA O SEU PRESENTE GOZO; SEU SERVIÇO GLORIOSO, DEUS VOS GUARDE PARA SEU LOUVOR.\n\nCORO PELO SEU PODER E NO SEU AMOR, TÉ NOS ENCONTRARMOS COM JESUS; PELO SEU PODER E NO SEU AMOR, OH! QUE DEUS VOS GUARDE EM SUA LUZ.', 'DEUS VOS GUARDE PELO SEU PODER, SEMPRE ESTEJA AO VOSSO LADO; VOS DISPENSE O SEU CUIDADO, DEUS VOS GUARDE PELO SEU PODER. CORO PELO SEU PODER E NO SEU AMOR, TÉ NOS ENCONTRARMOS COM JESUS; PELO SEU PODER E NO SEU AMOR, OH! QUE DEUS VOS GUARDE EM SUA LUZ. DEUS VOS GUARDE BEM NO SEU AMOR, CONSOLADOS E CONTENTES; ACHEGADOS PARA OS CRENTES, DEUS VOS GUARDE BEM NO SEU AMOR. DEUS VOS GUARDE DO PODER DO MAL, DA RUÍNA, DO PECADO; DOS MOTINS DE QUALQUER LADO, DEUS VOS GUARDE DO PODER DO MAL, DEUS VOS GUARDE PARA SEU LOUVOR, PARA O SEU PRESENTE GOZO; SEU SERVIÇO GLORIOSO, DEUS VOS GUARDE PARA SEU LOUVOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('716', '716 - ENQUANTO, Ó SALVADOR, TEU LIVRO LER', 'ENQUANTO, Ó SALVADOR, TEU LIVRO LER, MEUS OLHOS VÊM ABRIR, TE QUERO VER; DA MERA LETRA ALÉM, A TI, SENHOR, EU BUSCO A TI, JESUS, MEU REDENTOR. À BEIRA MAR, JESUS, PARTISTE O PÃO, SATISFAZENDO ALI A MULTIDÃO; DA VIDA O PÃO ÉS TU; VEM, POIS, ASSIM, SATISFAZER, SENHOR, A MIM, A MIM!', 'ENQUANTO, Ó SALVADOR, TEU LIVRO LER, MEUS OLHOS VÊM ABRIR, TE QUERO VER; DA MERA LETRA ALÉM, A TI, SENHOR, EU BUSCO A TI, JESUS, MEU REDENTOR. À BEIRA MAR, JESUS, PARTISTE O PÃO, SATISFAZENDO ALI A MULTIDÃO; DA VIDA O PÃO ÉS TU; VEM, POIS, ASSIM, SATISFAZER, SENHOR, A MIM, A MIM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('717', '717 - A TERRA SEMEAMOS', 'A TERRA SEMEAMOS A FIM DE NOS DAR PÃO, MAS DEUS É QUEM A NUTRE COM BENFAZEJA MÃO. É DEUS QUEM MANDA O FRIO, A CHUVA E O BOM CALOR E FAZ SURGIR DA TERRA O FRUTO APÓS A FLOR.\n\nCORO DE TODO BEM A FONTE É NOSSO BOM SENHOR! LOUVAI A DEUS, LOUVAI A DEUS POR TODO O SEU AMOR!\n\nO CRIADOR DE TUDO, QUE PERTO OU LONGE ESTÁ COLORE A FLOR SILVESTRE E À ESTRELA O BRILHO DÁ. OS VENTOS LHE OBEDECEM, O BRAVO MAR TAMBÉM; AS FRÁGEIS AVEZINHAS O SEU CUIDADO TÊM!\n\nCORO DE TODO BEM A FONTE É NOSSO BOM SENHOR! LOUVAI A DEUS, LOUVAI A DEUS POR TODO O SEU AMOR! INSTRTUMENTOS\n\nA NÓS, PORÉM, SEUS FILHOS, REVELA MAIS AMOR; O FILHO ETERNO DEU-NOS, JESUS, O SALVADOR, A NÓS FAZENDO HERDEIROS DE TUDO QUANTO TEM, NA REDENÇÃO PERFEITA QUE DELE NOS PROVÉM.\n\nCORO DE TODO BEM A FONTE É NOSSO BOM SENHOR! LOUVAI A DEUS, LOUVAI A DEUS POR TODO O SEU AMOR!', 'A TERRA SEMEAMOS A FIM DE NOS DAR PÃO, MAS DEUS É QUEM A NUTRE COM BENFAZEJA MÃO. É DEUS QUEM MANDA O FRIO, A CHUVA E O BOM CALOR E FAZ SURGIR DA TERRA O FRUTO APÓS A FLOR. CORO DE TODO BEM A FONTE É NOSSO BOM SENHOR! LOUVAI A DEUS, LOUVAI A DEUS POR TODO O SEU AMOR! O CRIADOR DE TUDO, QUE PERTO OU LONGE ESTÁ COLORE A FLOR SILVESTRE E À ESTRELA O BRILHO DÁ. OS VENTOS LHE OBEDECEM, O BRAVO MAR TAMBÉM; AS FRÁGEIS AVEZINHAS O SEU CUIDADO TÊM! CORO DE TODO BEM A FONTE É NOSSO BOM SENHOR! LOUVAI A DEUS, LOUVAI A DEUS POR TODO O SEU AMOR! INSTRTUMENTOS A NÓS, PORÉM, SEUS FILHOS, REVELA MAIS AMOR; O FILHO ETERNO DEU-NOS, JESUS, O SALVADOR, A NÓS FAZENDO HERDEIROS DE TUDO QUANTO TEM, NA REDENÇÃO PERFEITA QUE DELE NOS PROVÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('718', '718 - GUIA, Ó CRISTO, A MINHA NAU', 'GUIA, Ó CRISTO, A MINHA NAU SOBRE O REVOLTOSO MAR. TÃO ENFURECIDO E MAU; QUER FAZÊ-LA NAUFRAGAR VEM JESUS, Ó VEM GUIAR MINHA NAU VEM PILOTAR.\n\nSE, NO PORTO QUANDO ENTRAR, MAIS O MAR SE ENFURECER, QUE ME POSSA DELEITAR EM OUVIR JESUS DIZER: “ENTRA POBRE VIAJOR NO DESCANSO DO SENHOR!”', 'GUIA, Ó CRISTO, A MINHA NAU SOBRE O REVOLTOSO MAR. TÃO ENFURECIDO E MAU; QUER FAZÊ-LA NAUFRAGAR VEM JESUS, Ó VEM GUIAR MINHA NAU VEM PILOTAR. SE, NO PORTO QUANDO ENTRAR, MAIS O MAR SE ENFURECER, QUE ME POSSA DELEITAR EM OUVIR JESUS DIZER: ENTRA POBRE VIAJOR NO DESCANSO DO SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('719', '719 - AVANTE, AVANTE, Ó CRENTES', 'AVANTE, AVANTE, Ó CRENTES, SOLDADOS DE JESUS! ERGUEI SEU ESTANDARTE, LUTAI POR SUA CRUZ! E CONTRA OS INIMIGOS, EM GRANDES MULTIDÕES O EXCELSO COMANDANTE DIRIGE OS BATALHÕES.\n\nAVANTE, AVANTE, Ó CRENTES, POR CRISTO PELEJAI! VESTI SUA ARMADURA, EM SEU PODER MARCHAI! NO POSTO SEMPRE ACHADOS, VELANDO EM ORAÇÃO, EM MEIO AOS PERIGOS SEGUINDO O CAPITÃO! INSTRTUMENTOS\n\nAVANTE, AVANTE, Ó CRENTES, A PASSO TRIUNFAL! APÓS COMBATE HORRENDO; VIRÁ A PAZ FINAL! ENTÃO, ETERNAMENTE, SERÁ O VENCEDOR, NO CÉU GLORIFICADO COM CRISTO, O SALVADOR!', 'AVANTE, AVANTE, Ó CRENTES, SOLDADOS DE JESUS! ERGUEI SEU ESTANDARTE, LUTAI POR SUA CRUZ! E CONTRA OS INIMIGOS, EM GRANDES MULTIDÕES O EXCELSO COMANDANTE DIRIGE OS BATALHÕES. AVANTE, AVANTE, Ó CRENTES, POR CRISTO PELEJAI! VESTI SUA ARMADURA, EM SEU PODER MARCHAI! NO POSTO SEMPRE ACHADOS, VELANDO EM ORAÇÃO, EM MEIO AOS PERIGOS SEGUINDO O CAPITÃO! INSTRTUMENTOS AVANTE, AVANTE, Ó CRENTES, A PASSO TRIUNFAL! APÓS COMBATE HORRENDO; VIRÁ A PAZ FINAL! ENTÃO, ETERNAMENTE, SERÁ O VENCEDOR, NO CÉU GLORIFICADO COM CRISTO, O SALVADOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('720', '720 - AS GRUTAS, AS ROCHAS IMENSAS (UM HINO AO SENHOR)', 'AS GRUTAS, AS ROCHAS IMENSAS, DOS MUNDOS O GRANDE ESPLENDOR, PROCLAMAM, BEM ALTO, CONSTANTES, UM HINO AO TEU NOME, SENHOR!\n\nCORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR.\n\nNOS CÉU, AS ESTRELAS BRILHANTES, DOS MARES O GRANDE FRAGOR, E AS BRISAS ENTOAM, RIDENTES, UM HINO AO TEU NOME, SENHOR!\n\nCORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR.\n\nAS AVES ALEGRES NA MATA, POR ENTRE AS RAMAGENS EM FLOR, EXULTAM EM CORO, CANTANDO UM HINO AO TEU NOME, SENHOR!\n\nCORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR. INSTRUMENTOS\n\nE TU, PECADOR, QUE VAGUEIAS, QUE FAZES AO TEU CRIADOR? NÃO ACHAS MOMENTO EM QUE CANTES UM HINO DE GLÓRIA AO SENHOR?\n\nCORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR. FINAL: AMÉM!', 'AS GRUTAS, AS ROCHAS IMENSAS, DOS MUNDOS O GRANDE ESPLENDOR, PROCLAMAM, BEM ALTO, CONSTANTES, UM HINO AO TEU NOME, SENHOR! CORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR. NOS CÉU, AS ESTRELAS BRILHANTES, DOS MARES O GRANDE FRAGOR, E AS BRISAS ENTOAM, RIDENTES, UM HINO AO TEU NOME, SENHOR! AS AVES ALEGRES NA MATA, POR ENTRE AS RAMAGENS EM FLOR, EXULTAM EM CORO, CANTANDO UM HINO AO TEU NOME, SENHOR! CORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR. INSTRUMENTOS E TU, PECADOR, QUE VAGUEIAS, QUE FAZES AO TEU CRIADOR? NÃO ACHAS MOMENTO EM QUE CANTES UM HINO DE GLÓRIA AO SENHOR? CORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR.  AMÉM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('721', '721 - PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI', 'CORO PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI! PROCLAMAI! PROCLAMAI! PROCLAMAI QUE SUAVE E DOCE É SUA SANTA LEI! PROCLAMAI! PROCLAMAI!\n\nPROCLAMAI COMO ELE SE CHAMA O SALVADOR JESUS, COMO ELE POR NÓS MORREU NA ENSANGUENTADA CRUZ; COMO ELE, O CORDEIRO, SOBRE O TRONO ESTÁ, O DEUS DE TODA A GRAÇA QUE MORREU EM MEU LUGAR.\n\nCORO PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI! PROCLAMAI! PROCLAMAI! PROCLAMAI QUE SUAVE E DOCE É SUA SANTA LEI! PROCLAMAI! PROCLAMAI!\n\nPROCLAMAI QUE REINA EM GRAÇA NOSSO SALVADOR! QUE POR CETRO DE SEU REINO ELE TEM O AMOR! ANUNCIAI AOS TRISTES QUE ELE VIVO ESTÁ, E A TODOS OS CANSADOS QUE DESCANSO ELE DÁ.\n\nCORO PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI! PROCLAMAI! PROCLAMAI! PROCLAMAI QUE SUAVE E DOCE É SUA SANTA LEI! PROCLAMAI! PROCLAMAI!\n\nPROCLAMAI QUE ELE DO CÉU EM BREVE DESCERÁ! E COM TODOS OS SEUS SANTOS APARECERÁ! QUE SEM DEMORA VENHAM TODOS SE RENDER, E COM AMOR, EM TUDO A CRISTO OBEDECER.\n\nCORO PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI! PROCLAMAI! PROCLAMAI! PROCLAMAI QUE SUAVE E DOCE É SUA SANTA LEI! PROCLAMAI! PROCLAMAI!', 'CORO PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI! PROCLAMAI! PROCLAMAI! PROCLAMAI QUE SUAVE E DOCE É SUA SANTA LEI! PROCLAMAI! PROCLAMAI! PROCLAMAI COMO ELE SE CHAMA O SALVADOR JESUS, COMO ELE POR NÓS MORREU NA ENSANGUENTADA CRUZ; COMO ELE, O CORDEIRO, SOBRE O TRONO ESTÁ, O DEUS DE TODA A GRAÇA QUE MORREU EM MEU LUGAR. PROCLAMAI QUE REINA EM GRAÇA NOSSO SALVADOR! QUE POR CETRO DE SEU REINO ELE TEM O AMOR! ANUNCIAI AOS TRISTES QUE ELE VIVO ESTÁ, E A TODOS OS CANSADOS QUE DESCANSO ELE DÁ. PROCLAMAI QUE ELE DO CÉU EM BREVE DESCERÁ! E COM TODOS OS SEUS SANTOS APARECERÁ! QUE SEM DEMORA VENHAM TODOS SE RENDER, E COM AMOR, EM TUDO A CRISTO OBEDECER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('722', '722 - A DEUS, SUPREMO BENFEITOR', 'A DEUS, SUPREMO BENFEITOR; VÓS ANJOS E HOMENS DAI LOUVOR. A DEUS, O FILHO, A DEUS, O PAI A DEUS ESPÍRITO, GLÓRIA DAI.', 'A DEUS, SUPREMO BENFEITOR; VÓS ANJOS E HOMENS DAI LOUVOR. A DEUS, O FILHO, A DEUS, O PAI A DEUS ESPÍRITO, GLÓRIA DAI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('723', '723 - CRISTO PODE HOJE VIR (OH! DIA ALEGRE!)', 'CRISTO PODE HOJE VIR, OH! DIA ALEGRE! IREI AMIGOS VER; O MAL TERÁ O SEU FIM. SE CRISTO DO CÉU DESCER!\n\nCORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS! INSTRUMENTOS\n\nSE HOJE FOR PRA MEU LAR, OH! DIA ALEGRE! PARECE-ME OUVIR OS SALVOS CANTANDO: “VEM!'' QUEM DERA PRA O CÉU PARTIR!\n\nCORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS! INSTRUMENTOS\n\nPOR QUE ANSIOSO ESTAR? OH! DIA ALEGRE! HÁ LUZ NA MARGEM ALÉM; TEMPORAL NUNCA HAVERÁ; DO CÉU BREVE CRISTO VEM!\n\nCORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS! INSTRUMENTOS\n\nFIEL A JESUS ESTOU, OH! DIA ALEGRE! LIVRE ESTOU PRA DIZER: “CRISTO É TUDO PRA MIM”; DO CÉU VIRÁ COM PODER!\n\nCORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS!', 'CRISTO PODE HOJE VIR, OH! DIA ALEGRE! IREI AMIGOS VER; O MAL TERÁ O SEU FIM. SE CRISTO DO CÉU DESCER! CORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS! INSTRUMENTOS SE HOJE FOR PRA MEU LAR, OH! DIA ALEGRE! PARECE-ME OUVIR OS SALVOS CANTANDO: VEM! QUEM DERA PRA O CÉU PARTIR! POR QUE ANSIOSO ESTAR? OH! DIA ALEGRE! HÁ LUZ NA MARGEM ALÉM; TEMPORAL NUNCA HAVERÁ; DO CÉU BREVE CRISTO VEM! FIEL A JESUS ESTOU, OH! DIA ALEGRE! LIVRE ESTOU PRA DIZER: CRISTO É TUDO PRA MIM; DO CÉU VIRÁ COM PODER! CORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('724', '724 - EIS OS MILHÕES', 'EIS OS MILHÕES QUE EM TREVAS TÃO MEDONHAS JAZEM PERDIDOS, SEM O SALVADOR! OH! QUEM IRÁ AS NOVAS PROCLAMANDO QUE DEUS EM CRISTO SALVA O PECADOR?\n\nCORO TODO PODER O PAI MEU DEU, NA TERRA COMO LÁ NO CÉU! IDE, POIS, ANUNCIAI O EVANGELHO, E EIS-ME CONVOSCO SEMPRE!\n\nPORTAS ABERTAS EIS POR TODO O MUNDO! CRENTES EM CRISTO, SEMPRE AVANTE ANDAI! E, NA PELEJA UNINDO AS VOSSAS FORÇAS, DA ESCRAVIDÃO OS POVOS LIBERTAI.\n\nCORO TODO PODER O PAI MEU DEU, NA TERRA COMO LÁ NO CÉU! IDE, POIS, ANUNCIAI O EVANGELHO, E EIS-ME CONVOSCO SEMPRE!\n\n“OH! VINDE A MIM”, A VOZ DIVINA CHAMA. “VINDE”, CLAMAI EM NOME DE JESUS. POR NOS SALVAR DA MALDIÇÃO ETERNA, SEU SANGUE DERRAMOU POR NÓS NA CRUZ.\n\nCORO TODO PODER O PAI MEU DEU, NA TERRA COMO LÁ NO CÉU! IDE, POIS, ANUNCIAI O EVANGELHO, E EIS-ME CONVOSCO SEMPRE!\n\nÓ DEUS! APRESSA O GLORIOSO DIA EM QUE OS REMIDOS TODOS SE UNIRÃO, E EM CORO SANTO, EXCELSO, JUBILOSO, POR TODO O SEMPRE GLÓRIA A TI DARÃO!\n\nCORO TODO PODER O PAI MEU DEU, NA TERRA COMO LÁ NO CÉU! IDE, POIS, ANUNCIAI O EVANGELHO, E EIS-ME CONVOSCO SEMPRE!', 'EIS OS MILHÕES QUE EM TREVAS TÃO MEDONHAS JAZEM PERDIDOS, SEM O SALVADOR! OH! QUEM IRÁ AS NOVAS PROCLAMANDO QUE DEUS EM CRISTO SALVA O PECADOR? CORO TODO PODER O PAI MEU DEU, NA TERRA COMO LÁ NO CÉU! IDE, POIS, ANUNCIAI O EVANGELHO, E EIS-ME CONVOSCO SEMPRE! PORTAS ABERTAS EIS POR TODO O MUNDO! CRENTES EM CRISTO, SEMPRE AVANTE ANDAI! E, NA PELEJA UNINDO AS VOSSAS FORÇAS, DA ESCRAVIDÃO OS POVOS LIBERTAI. OH! VINDE A MIM, A VOZ DIVINA CHAMA. VINDE, CLAMAI EM NOME DE JESUS. POR NOS SALVAR DA MALDIÇÃO ETERNA, SEU SANGUE DERRAMOU POR NÓS NA CRUZ. Ó DEUS! APRESSA O GLORIOSO DIA EM QUE OS REMIDOS TODOS SE UNIRÃO, E EM CORO SANTO, EXCELSO, JUBILOSO, POR TODO O SEMPRE GLÓRIA A TI DARÃO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('725', '725 - EU PERDIDO PECADOR', 'EU PERDIDO PECADOR, LONGE DO MEU JESUS, JÁ ME ACHAVA SEM VIGOR A PERECER SEM LUZ; MEU ESTADO CRISTO VI DANDO-ME SUA MÃO, E SALVAR-ME CONSEGUIU DA PERDIÇÃO.\n\nCORO CRISTO ME AMOU, E ME LIVROU; O SEU IMENSO AMOR ME TRANSFORMOU. FOI SEU PODER, O SEU QUERER. SIM, CRISTO O SALVADOR ME TRANSFORMOU. INSTRUMENTOS\n\nMINHA VIDA, TODO O SER, QUERO-LHE CONSAGRAR; A SEU LADO VOU VIVER, O SEU AMOR CANTAR; A MENSAGEM TRANSMITIR AOS QUE PERDIDOS SÃO. VENHAM TODOS JÁ FRUIR A SALVAÇÃO.\n\nCORO CRISTO ME AMOU, E ME LIVROU; O SEU IMENSO AMOR ME TRANSFORMOU. FOI SEU PODER, O SEU QUERER. SIM, CRISTO O SALVADOR ME TRANSFORMOU.', 'EU PERDIDO PECADOR, LONGE DO MEU JESUS, JÁ ME ACHAVA SEM VIGOR A PERECER SEM LUZ; MEU ESTADO CRISTO VI DANDO-ME SUA MÃO, E SALVAR-ME CONSEGUIU DA PERDIÇÃO. CORO CRISTO ME AMOU, E ME LIVROU; O SEU IMENSO AMOR ME TRANSFORMOU. FOI SEU PODER, O SEU QUERER. SIM, CRISTO O SALVADOR ME TRANSFORMOU. INSTRUMENTOS MINHA VIDA, TODO O SER, QUERO-LHE CONSAGRAR; A SEU LADO VOU VIVER, O SEU AMOR CANTAR; A MENSAGEM TRANSMITIR AOS QUE PERDIDOS SÃO. VENHAM TODOS JÁ FRUIR A SALVAÇÃO. CORO CRISTO ME AMOU, E ME LIVROU; O SEU IMENSO AMOR ME TRANSFORMOU. FOI SEU PODER, O SEU QUERER. SIM, CRISTO O SALVADOR ME TRANSFORMOU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('726', '726 - QUANDO A TEMPESTADE RUGE', 'QUANDO A TEMPESTADE RUGE COM O SEU FEROZ BRAMIR, QUANDO AS NUVENS SE ACUMULAM, RAIOS MIL A DESPEDIR, DO TROVÃO O SOM TREMENDO OUVE-SE ENTÃO COM PAVOR; MAS, NA VOZ DA TEMPESTADE, SOA A TUA VOZ, SENHOR!\n\nCORO TUA VOZ OUVIMOS NÓS A ANIMAR OS QUE ANDAM SÓS, MAS SEMPRE EM TI CONFIADOS E POR TI SEMPRE A LUTAR, NA ARIDEZ DE IMENSAS PLAGAS, NAS SOLIDÕES DO VASTO MAR!\n\nQUANDO O MAR VEM MANSAMENTE NA PRAIA SE ESPREGUIÇAR, QUANDO A BRISA SUSSURRANTE NOS SEGREDA AO PERPASSAR, SOA DOCE HARMONIA, OUVE-SE UM FELIZ RUMOR, SOBRE O CORO VEM DAS ONDAS TUA DOCE VOZ, SENHOR!\n\nCORO TUA VOZ OUVIMOS NÓS A ANIMAR OS QUE ANDAM SÓS, MAS SEMPRE EM TI CONFIADOS E POR TI SEMPRE A LUTAR, NA ARIDEZ DE IMENSAS PLAGAS, NAS SOLIDÕES DO VASTO MAR!\n\nQUANDO O CORAÇÃO AFLITO QUER À DOR, AO MAL FUGIR, E SE AGITA E LUTA E RUGE, SEM A DOCE PAZ SENTIR, ENTÃO, QUAL ECO AFASTADO NAS QUEBRADAS A ROLAR, AO AFLITO E CONTRISTADO TUA VOZ VEM CONSOLAR.\n\nCORO TUA VOZ OUVIMOS NÓS A ANIMAR OS QUE ANDAM SÓS, MAS SEMPRE EM TI CONFIADOS E POR TI SEMPRE A LUTAR, NA ARIDEZ DE IMENSAS PLAGAS, NAS SOLIDÕES DO VASTO MAR!', 'QUANDO A TEMPESTADE RUGE COM O SEU FEROZ BRAMIR, QUANDO AS NUVENS SE ACUMULAM, RAIOS MIL A DESPEDIR, DO TROVÃO O SOM TREMENDO OUVE-SE ENTÃO COM PAVOR; MAS, NA VOZ DA TEMPESTADE, SOA A TUA VOZ, SENHOR! CORO TUA VOZ OUVIMOS NÓS A ANIMAR OS QUE ANDAM SÓS, MAS SEMPRE EM TI CONFIADOS E POR TI SEMPRE A LUTAR, NA ARIDEZ DE IMENSAS PLAGAS, NAS SOLIDÕES DO VASTO MAR! QUANDO O MAR VEM MANSAMENTE NA PRAIA SE ESPREGUIÇAR, QUANDO A BRISA SUSSURRANTE NOS SEGREDA AO PERPASSAR, SOA DOCE HARMONIA, OUVE-SE UM FELIZ RUMOR, SOBRE O CORO VEM DAS ONDAS TUA DOCE VOZ, SENHOR! QUANDO O CORAÇÃO AFLITO QUER À DOR, AO MAL FUGIR, E SE AGITA E LUTA E RUGE, SEM A DOCE PAZ SENTIR, ENTÃO, QUAL ECO AFASTADO NAS QUEBRADAS A ROLAR, AO AFLITO E CONTRISTADO TUA VOZ VEM CONSOLAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('727', '727 - EIS QUE ABRO A PORTA', 'EIS QUE ABRO A PORTA PRA DIZER AO POVO MEU: ENTRA E VEM CEAR COM O VOSSO DEUS. ANTES DAREI-VOS NOVOS CORPOS, POVO QUE TANTO SOFREU. SERÃO GLORIFICADOS PARA VEREM O VOSSO DEUS.\n\nSERÁ UMA LINDA FESTA, ELA NUNCA FINDARÁ, NUNCA MAIS VAMOS SOFRER, NUNCA MAIS VAMOS CHORAR. POIS ALI CONOSCO ESTARÁ E O VEREMOS FACE A FACE, E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS. INSTRUMENTOS\n\nEIS QUE ABRO A PORTA PRA DIZER AO POVO MEU: ENTRA E VEM CEAR COM O VOSSO DEUS. ANTES DAREI-VOS NOVOS CORPOS, POVO QUE TANTO SOFREU. SERÃO GLORIFICADOS PARA VEREM O VOSSO DEUS.\n\nSERÁ UMA LINDA FESTA, ELA NUNCA FINDARÁ, NUNCA MAIS VAMOS SOFRER, NUNCA MAIS VAMOS CHORAR. POIS ALI CONOSCO ESTARÁ E O VEREMOS FACE A FACE, E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS. FINAL: E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS.', 'EIS QUE ABRO A PORTA PRA DIZER AO POVO MEU: ENTRA E VEM CEAR COM O VOSSO DEUS. ANTES DAREI-VOS NOVOS CORPOS, POVO QUE TANTO SOFREU. SERÃO GLORIFICADOS PARA VEREM O VOSSO DEUS. SERÁ UMA LINDA FESTA, ELA NUNCA FINDARÁ, NUNCA MAIS VAMOS SOFRER, NUNCA MAIS VAMOS CHORAR. POIS ALI CONOSCO ESTARÁ E O VEREMOS FACE A FACE, E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS. INSTRUMENTOS SERÁ UMA LINDA FESTA, ELA NUNCA FINDARÁ, NUNCA MAIS VAMOS SOFRER, NUNCA MAIS VAMOS CHORAR. POIS ALI CONOSCO ESTARÁ E O VEREMOS FACE A FACE, E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS.  E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('728', '728 - NOSSA GRATIDÃO', 'NOSSA GRATIDÃO E NOSSO LOUVOR IREMOS DEIXAR NO TEU ALTAR. QUE DAREMOS A TI, SENHOR, PELOS TEUS FEITOS E POR TUA OBRA AQUI NESTE LUGAR?\n\nGLÓRIA, SEMPRE GLÓRIA, COM TEUS ANJOS PARA SEMPRE ADORAR. GLÓRIA, SEMPRE GLÓRIA, A TEUS PÉS, SENHOR, IREMOS EXALTAR. O SENHOR É JUSTO, É FIEL, SUA MISERICÓRDIA NÃO TEM FIM. TODAS AS TUAS OBRAS, SENHOR, TE LOUVARÃO E OS TEUS SANTOS TE BENDIRÃO.\n\nGLÓRIA (OS CÉUS MANIFESTAM), SEMPRE GLÓRIA (SUA GLÓRIA E PODER). OS ANJOS TE LOUVAM SEM CESSAR (GLÓRIA). GLÓRIA (EM NOSSOS LÁBIOS), SEMPRE GLÓRIA (HÁ SEMPRE UM LOUVOR), DE EXALTAÇÃO POR TUA GLÓRIA, SENHOR.\nBIS\n\nFINAL: GLÓRIA (GLÓRIA) PARA SEMPRE AO SENHOR, GLÓRIA (GLÓRIA), O TEU NOME EXALTAR.', 'NOSSA GRATIDÃO E NOSSO LOUVOR IREMOS DEIXAR NO TEU ALTAR. QUE DAREMOS A TI, SENHOR, PELOS TEUS FEITOS E POR TUA OBRA AQUI NESTE LUGAR? GLÓRIA, SEMPRE GLÓRIA, COM TEUS ANJOS PARA SEMPRE ADORAR. GLÓRIA, SEMPRE GLÓRIA, A TEUS PÉS, SENHOR, IREMOS EXALTAR. O SENHOR É JUSTO, É FIEL, SUA MISERICÓRDIA NÃO TEM FIM. TODAS AS TUAS OBRAS, SENHOR, TE LOUVARÃO E OS TEUS SANTOS TE BENDIRÃO. GLÓRIA (OS CÉUS MANIFESTAM), SEMPRE GLÓRIA (SUA GLÓRIA E PODER). OS ANJOS TE LOUVAM SEM CESSAR (GLÓRIA). GLÓRIA (EM NOSSOS LÁBIOS), SEMPRE GLÓRIA (HÁ SEMPRE UM LOUVOR), DE EXALTAÇÃO POR TUA GLÓRIA, SENHOR. GLÓRIA (GLÓRIA) PARA SEMPRE AO SENHOR, GLÓRIA (GLÓRIA), O TEU NOME EXALTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('729', '729 - OS TEUS ANJOS TE EXALTAM', 'OS TEUS ANJOS TE EXALTAM, MEU SENHOR E REI, COMO ALMEJO ESTAR NA TUA GLÓRIA. DEIXAR ESTE MUNDO DE TREVAS, CHEIO DE TRIBULAÇÕES, E SOMENTE TE LOUVAR, MEU SENHOR.\n\nSENHOR, TU PASSASTE UM DIA POR AFLIÇÕES E SABES QUE NÃO É FÁCIL VIVER AQUI. MAS, COMO TU VENCESTE O MUNDO, EU TAMBÉM POSSO VENCER, POIS, NÃO HÁ NADA QUE SEJA IMPOSSÍVEL PARA TI.\n\nSINTO-ME FELIZ QUANDO OUÇO TUA VOZ A FALAR A MEU CORAÇÃO: “NÃO TARDAREI, EM BREVE VOLTAREI (VOLTAREI), PARA VOS LEVAR (SIM, VOLTAREI), EM BREVE VOLTAREI.” FINAL: E NÓS TE ESPERAMOS, SENHOR.', 'OS TEUS ANJOS TE EXALTAM, MEU SENHOR E REI, COMO ALMEJO ESTAR NA TUA GLÓRIA. DEIXAR ESTE MUNDO DE TREVAS, CHEIO DE TRIBULAÇÕES, E SOMENTE TE LOUVAR, MEU SENHOR. SENHOR, TU PASSASTE UM DIA POR AFLIÇÕES E SABES QUE NÃO É FÁCIL VIVER AQUI. MAS, COMO TU VENCESTE O MUNDO, EU TAMBÉM POSSO VENCER, POIS, NÃO HÁ NADA QUE SEJA IMPOSSÍVEL PARA TI. SINTO-ME FELIZ QUANDO OUÇO TUA VOZ A FALAR A MEU CORAÇÃO: NÃO TARDAREI, EM BREVE VOLTAREI (VOLTAREI), PARA VOS LEVAR (SIM, VOLTAREI), EM BREVE VOLTAREI.  E NÓS TE ESPERAMOS, SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('730', '730 - SÓ TU NOS FAZES VENCEDORES', 'SÓ TU NOS FAZES VENCEDORES, EM TI TEMOS FORÇAS PRA JORNADA; SACIA A FOME COM TEU SUBLIME AMOR, DOCE AMOR.\n\nPOIS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR.\n\nSÓ TU NOS FAZES VENCEDORES, EM TI TEMOS FORÇAS PRA JORNADA; SACIA A FOME COM TEU SUBLIME AMOR, DOCE AMOR. POIS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR. INSTRUMENTOS\n\nNÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR. FINAL: NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR.', 'SÓ TU NOS FAZES VENCEDORES, EM TI TEMOS FORÇAS PRA JORNADA; SACIA A FOME COM TEU SUBLIME AMOR, DOCE AMOR. POIS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR. SÓ TU NOS FAZES VENCEDORES, EM TI TEMOS FORÇAS PRA JORNADA; SACIA A FOME COM TEU SUBLIME AMOR, DOCE AMOR. POIS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR. INSTRUMENTOS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR.  NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR.',  1);
