INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('01', '01 - MEU DEUS, MEU PAI', 'MEU DEUS, MEU PAI,\nA TI CLAMO, MEU SENHOR,\nPELO SANGUE DE JESUS,\nDÁ-ME PAZ E COMUNHÃO.\n\nDÁ-ME PAZ E COMUNHÃO.\nBIS', 'MEU DEUS, MEU PAI, A TI CLAMO, MEU SENHOR, PELO SANGUE DE JESUS, DÁ-ME PAZ E COMUNHÃO. DÁ-ME PAZ E COMUNHÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('02', '02 - PAI, ESTOU A TE CLAMAR', 'PAI, ESTOU A TE CLAMAR,\nPEDINDO PERDÃO, SOCORRO,\nEU QUERO TE OUVIR.\n\nPAI, ENSINA-ME A ADORAR\nE A GLORIFICAR TEU NOME,\nEU QUERO TE AMAR.\n\nPAI, EU QUERO TE SERVIR,\nOH! DÁ-ME DA TUA GRAÇA\nE ME BASTARÁ, E ME BASTARÁ,\nE ME BASTARÁ.', 'PAI, ESTOU A TE CLAMAR, PEDINDO PERDÃO, SOCORRO, EU QUERO TE OUVIR. PAI, ENSINA-ME A ADORAR E A GLORIFICAR TEU NOME, EU QUERO TE AMAR. PAI, EU QUERO TE SERVIR, OH! DÁ-ME DA TUA GRAÇA E ME BASTARÁ, E ME BASTARÁ, E ME BASTARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('03', '03 - CLAMO, Ó SENHOR, POR TEU SANGUE', 'CLAMO, Ó SENHOR, POR TEU SANGUE, (2X)\nSANGUE QUE POR MIM FOI VERTIDO,\nSANGUE DE UMA NOVA ALIANÇA.\n\nSENHOR, DERRAMA TEU SANGUE \nSOBRE MIM, Ó SENHOR.\nBIS', 'CLAMO, Ó SENHOR, POR TEU SANGUE,  SANGUE QUE POR MIM FOI VERTIDO, SANGUE DE UMA NOVA ALIANÇA. SENHOR, DERRAMA TEU SANGUE SOBRE MIM, Ó SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('04', '04 - CLAMO, Ó SENHOR', 'CLAMO, Ó SENHOR, \nPELO SANGUE DE JESUS,\nNA TUA PRESENÇA LIMPO QUERO ESTAR,\nE SÓ LAVADO NESTE SANGUE\nNO SANTO DOS SANTOS POSSO ENTRAR.\n\nLAVA-ME, SENHOR,\nVISITA-ME, SENHOR, \nCOM O ESPÍRITO SANTO,\nO CONSOLADOR.\nTEREI PLENA COMUNHÃO\nE A TUA PROTEÇÃO.\n\nE A TUA PROTEÇÃO.\nE A TUA PROTEÇÃO.\nBIS', 'CLAMO, Ó SENHOR, PELO SANGUE DE JESUS, NA TUA PRESENÇA LIMPO QUERO ESTAR, E SÓ LAVADO NESTE SANGUE NO SANTO DOS SANTOS POSSO ENTRAR. LAVA-ME, SENHOR, VISITA-ME, SENHOR, COM O ESPÍRITO SANTO, O CONSOLADOR. TEREI PLENA COMUNHÃO E A TUA PROTEÇÃO. E A TUA PROTEÇÃO. E A TUA PROTEÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('05', '05 - ESTOU AQUI PRA CONFESSAR', 'ESTOU AQUI PRA CONFESSAR\nA TI OS MEUS PECADOS,\nPRECISO DO TEU PERDÃO,\nDA TUA COMUNHÃO.\n\nESTOU AQUI PRA TE PEDIR:\nLIMPA MEU CORAÇÃO,\nCOMO A BRANCA LÃ\nTORNA-O, SENHOR.\n\nSUSTENTA-ME EM TUAS MÃOS, JESUS,\nPROTEGIDO EM TEUS BRAÇOS QUERO ESTAR;\nESCONDE-ME DE TODA A MALDADE\nE SEMPRE CONTIGO VOU ANDAR. (BIS)', 'ESTOU AQUI PRA CONFESSAR A TI OS MEUS PECADOS, PRECISO DO TEU PERDÃO, DA TUA COMUNHÃO. ESTOU AQUI PRA TE PEDIR: LIMPA MEU CORAÇÃO, COMO A BRANCA LÃ TORNA-O, SENHOR. SUSTENTA-ME EM TUAS MÃOS, JESUS, PROTEGIDO EM TEUS BRAÇOS QUERO ESTAR; ESCONDE-ME DE TODA A MALDADE E SEMPRE CONTIGO VOU ANDAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('06', '06 - PERDOA-ME, SENHOR', 'PERDOA-ME, SENHOR\nCLAMO PELO SANGUE DE JESUS\nQUERO RENOVAR\nA MINHA COMUNHÃO.\nPERDOA-ME, SENHOR,\nQUERO DEPOSITAR O MEU CORAÇÃO\nSEMPRE EM TEU ALTAR.\n\nVISITA-ME COM O TEU ESPÍRITO\nE FAZE-ME HABITAR\nAO TEU LADO ETERNAMENTE!', 'PERDOA-ME, SENHOR CLAMO PELO SANGUE DE JESUS QUERO RENOVAR A MINHA COMUNHÃO. PERDOA-ME, SENHOR, QUERO DEPOSITAR O MEU CORAÇÃO SEMPRE EM TEU ALTAR. VISITA-ME COM O TEU ESPÍRITO E FAZE-ME HABITAR AO TEU LADO ETERNAMENTE!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('07', '07 - É TEU SANGUE, MEU SENHOR JESUS', 'É TEU SANGUE, MEU SENHOR JESUS,\nQUE GARANTE A NOSSA COMUNHÃO.\nÉ TEU SANGUE, MEU SENHOR JESUS,\nQUE NOS TRAZ LIBERTAÇÃO.\n\nPOR ISSO VOU CLAMARINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('08', '08 - DIANTE DO TEU ALTAR', 'DIANTE DO TEU ALTAR, SENHOR,\nOS MEUS JOELHOS VOU DOBRAR\nE POR TEU SANGUE VOU CLAMAR,\nPEDINDO: VEM PURIFICAR\nO MEU CORAÇÃO,QUE ESPERA \nEM TI COM GRATIDÃO!\n\nCORO\nEU CLAMO PELA VIDA,\nA VIDA QUE HÁ EM TI, JESUS!\nPELO PODER DO SANGUE \nVERTIDO NA CRUZ!\nEU CLAMO PELA VIDA\nA VIDA QUE HÁ EM TI, JESUS!\nPELO PODER DO SANGUE\nVERTIDO NA CRUZ!\n\nDIANTE DO TEU ALTAR, SENHOR,\nOS MEUS JOELHOS VOU DOBRAR\nE POR TEU SANGUE VOU CLAMAR,\nPEDINDO: VEM PURIFICAR\nO MEU CORAÇÃO,QUE ESPERA \nEM TI COM GRATIDÃO!\n\nCORO\nEU CLAMO PELA VIDA,\nA VIDA QUE HÁ EM TI, JESUS!\nPELO PODER DO SANGUE \nVERTIDO NA CRUZ!\nEU CLAMO PELA VIDA\nA VIDA QUE HÁ EM TI, JESUS!\nPELO PODER DO SANGUE\nVERTIDO NA CRUZ!', 'DIANTE DO TEU ALTAR, SENHOR, OS MEUS JOELHOS VOU DOBRAR E POR TEU SANGUE VOU CLAMAR, PEDINDO: VEM PURIFICAR O MEU CORAÇÃO,QUE ESPERA EM TI COM GRATIDÃO! CORO EU CLAMO PELA VIDA, A VIDA QUE HÁ EM TI, JESUS! PELO PODER DO SANGUE VERTIDO NA CRUZ! EU CLAMO PELA VIDA A VIDA QUE HÁ EM TI, JESUS! PELO PODER DO SANGUE VERTIDO NA CRUZ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('09', '09 - DOBRO OS MEUS JOELHOS', 'DOBRO OS MEUS JOELHOS PARA A TI CLAMAR, SENHOR,\nTOCA O MEU INTERIOR, TOCA-ME, SENHOR,\nDIANTE DE TI ESTOU PARA TE ADORAR;\nGUIA-ME, SENHOR, PELO TEU AMOR,\nPAZ E SEGURANÇA QUERO TER\nCOM JESUS, MEU SENHOR\n\nDOBRO OS MEUS JOELHOS PARA A TI CLAMAR, SENHOR,\nTOCA O MEU INTERIOR, TOCA-ME, SENHOR,\nDIANTE DE TI ESTOU PARA TE ADORAR;\nGUIA-ME, SENHOR, PELO TEU AMOR,\nPAZ E SEGURANÇA QUERO TER \nCOM JESUS.\n\nFINAL:\nGUIA-ME, SENHOR, PELO TEU AMOR,\nPAZ E SEGURANÇA QUERO TER\nCOM JESUS, MEU SENHOR.', 'DOBRO OS MEUS JOELHOS PARA A TI CLAMAR, SENHOR, TOCA O MEU INTERIOR, TOCA-ME, SENHOR, DIANTE DE TI ESTOU PARA TE ADORAR; GUIA-ME, SENHOR, PELO TEU AMOR, PAZ E SEGURANÇA QUERO TER COM JESUS, MEU SENHOR DOBRO OS MEUS JOELHOS PARA A TI CLAMAR, SENHOR, TOCA O MEU INTERIOR, TOCA-ME, SENHOR, DIANTE DE TI ESTOU PARA TE ADORAR; GUIA-ME, SENHOR, PELO TEU AMOR, PAZ E SEGURANÇA QUERO TER COM JESUS. GUIA-ME, SENHOR, PELO TEU AMOR, PAZ E SEGURANÇA QUERO TER COM JESUS, MEU SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('10', '10 - CLAMA, Ó IGREJA', 'CLAMA, Ó IGREJA,\nCLAMA PELO SANGUE DE JESUS.\nNESTE SANGUE HÁ PODER,\nVIDA, PAZ, LIBERTAÇÃO,\nNESTE SANGUE PODEROSO HÁ PERDÃO.\n\nFINAL:\nNESSE SANGUE PODEROSO HÁ PERDÃO\nBIS', 'CLAMA, Ó IGREJA, CLAMA PELO SANGUE DE JESUS. NESTE SANGUE HÁ PODER, VIDA, PAZ, LIBERTAÇÃO, NESTE SANGUE PODEROSO HÁ PERDÃO.  NESSE SANGUE PODEROSO HÁ PERDÃO', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('11', '11 - CLAMO A TI, MEU SENHOR E PAI  (UMA NOVA CANÇÃO)', 'CLAMO A TI, MEU SENHOR E PAI \nE PROSTRADO AOS TEUS PÉS PEÇO\nPERDÃO. CLAMO PELO SANGUE DE\nJESUS SOBRE MIM E COM LOUVORES\nE CÂNTICOS TE ADORAREI. A TUA\nGRAÇA SEJA SOBRE MIM, MEU SENHOR, \nA TUA LUZ RESPLANDEÇA EM MIM.\n\nCOLOCA NOS MEUS LÁBIOS UMA NOVA\nCANÇÃO, UM HINO DE LOUVOR A DEUS\nPAI, A DEUS PAI. \nE PROSTRADO AOS TEUS PÉS\nEM PLENA COMUNHÃO, MEU\nSENHOR, RENOVA TEU ESPÍRITO\nSOBRE MIM. SOBRE NÓS.\nBIS', 'CLAMO A TI, MEU SENHOR E PAI E PROSTRADO AOS TEUS PÉS PEÇO PERDÃO. CLAMO PELO SANGUE DE JESUS SOBRE MIM E COM LOUVORES E CÂNTICOS TE ADORAREI. A TUA GRAÇA SEJA SOBRE MIM, MEU SENHOR, A TUA LUZ RESPLANDEÇA EM MIM. COLOCA NOS MEUS LÁBIOS UMA NOVA CANÇÃO, UM HINO DE LOUVOR A DEUS PAI, A DEUS PAI. E PROSTRADO AOS TEUS PÉS EM PLENA COMUNHÃO, MEU SENHOR, RENOVA TEU ESPÍRITO SOBRE MIM. SOBRE NÓS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('10', '10 - PERDOA-ME, SENHOR SEGUNDO O SEU AMOR', 'PERDOA-ME SENHOR, SEGUNDO O\nSEU AMOR, LAVA-ME COM O SANGUE\nDE JESUS E ENVIA A TUA LUZ.\nPURIFICA-ME SENHOR, COM O SANGUE\nDE JESUS NOVA CRIATURA EM TI EU\nSOU PRONTO PARA O TEU LOUVOR.\n\nPOSSO AGORA TE ADORAR,\nPOSSO TE GLORIFICAR,\nMEU CORAÇÃO LIBERTO ESTÁ\nCHEIO DE ALEGRIA E PAZ.\nBIS', 'PERDOA-ME SENHOR, SEGUNDO O SEU AMOR, LAVA-ME COM O SANGUE DE JESUS E ENVIA A TUA LUZ. PURIFICA-ME SENHOR, COM O SANGUE DE JESUS NOVA CRIATURA EM TI EU SOU PRONTO PARA O TEU LOUVOR. POSSO AGORA TE ADORAR, POSSO TE GLORIFICAR, MEU CORAÇÃO LIBERTO ESTÁ CHEIO DE ALEGRIA E PAZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('13', '13 - PURIFICADO COM SANGUE ESTOU', 'PURIFICADO COM SANGUE ESTOU,\nQUAL NEVE ME TORNOU;\nÁGUA DA VIDA EM JESUS BEBI, SATISFEITO ME SENTI. PARA JESUS, LÁ NA CRUZ, EU OLHEI,\nE O MEU FARDO AO CHÃO DEIXEI;\nCANTANDO VOU: GLÓRIA AO SENHOR! \nGLÓRIA A CRISTO, NOSSO DEUS!\nBIS', 'PURIFICADO COM SANGUE ESTOU, QUAL NEVE ME TORNOU; ÁGUA DA VIDA EM JESUS BEBI, SATISFEITO ME SENTI. PARA JESUS, LÁ NA CRUZ, EU OLHEI, E O MEU FARDO AO CHÃO DEIXEI; CANTANDO VOU: GLÓRIA AO SENHOR! GLÓRIA A CRISTO, NOSSO DEUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('14', '14 - TE SUPLICO, Ó DEUS, EM ORAÇÃO', 'TE SUPLICO, Ó DEUS, EM ORAÇÃO.\nPOIS DESEJO DE TODO O CORAÇÃO\nENTENDER A TUA PALAVRA, \nTER SABEDORIA E TEMOR\nPARA PRESERVAR-ME \nNO TEU CAMINHO, SENHOR.\n\nCOMO PRATA E COMO O OURO BUSCAREI,\nCOMO UM TESOURO ESCONDIDO, PROCURAREI;\nA TUA PALAVRA ENTRARÁ EM MEU CORAÇÃO\nDANDO-ME A LEVEZA DO PERDÃO,\nOS TEUS MANDAMENTOS SUAVES \nSERÃO E UM LOUVOR DE GRATIDÃO \nENTOAREI A TI, SENHOR.\nBIS', 'TE SUPLICO, Ó DEUS, EM ORAÇÃO. POIS DESEJO DE TODO O CORAÇÃO ENTENDER A TUA PALAVRA, TER SABEDORIA E TEMOR PARA PRESERVAR-ME NO TEU CAMINHO, SENHOR. COMO PRATA E COMO O OURO BUSCAREI, COMO UM TESOURO ESCONDIDO, PROCURAREI; A TUA PALAVRA ENTRARÁ EM MEU CORAÇÃO DANDO-ME A LEVEZA DO PERDÃO, OS TEUS MANDAMENTOS SUAVES SERÃO E UM LOUVOR DE GRATIDÃO ENTOAREI A TI, SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('15', '15 - JESUS VEM ME ABENÇOAR', 'JESUS, VEM ME ABENÇOAR,\nSENTIR A TUA FACE.\nJESUS, VEM FICAR PERTINHO,\nDAS CRIANÇAS VEM CUIDAR.\n\nJESUS É MEU, (3X)\nÉ ELE QUEM CUIDA DE MIM.\nBIS', 'JESUS, VEM ME ABENÇOAR, SENTIR A TUA FACE. JESUS, VEM FICAR PERTINHO, DAS CRIANÇAS VEM CUIDAR. JESUS É MEU,  É ELE QUEM CUIDA DE MIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('16', '16 - PAI CELESTE', 'PAI CELESTE, PAI CELESTE,\nVEM FICAR COMIGO,\nPAI CELESTE.\n\nJESUS CRISTO, JESUS CRISTO,\nVEM FICAR COMIGO,\nJESUS CRISTO.\n\nSANTO ESPÍRITO, SANTO ESPÍRITO,\nVEM FICAR COMIGO, SANTO ESPÍRITO.', 'PAI CELESTE, PAI CELESTE, VEM FICAR COMIGO, PAI CELESTE. JESUS CRISTO, JESUS CRISTO, VEM FICAR COMIGO, JESUS CRISTO. SANTO ESPÍRITO, SANTO ESPÍRITO, VEM FICAR COMIGO, SANTO ESPÍRITO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('17', '17 - QUANDO FALO COM DEUS', 'QUANDO FALO COM DEUS AS LÁGRIMAS CORREM,\nA SUA PRESENÇA ME ENVOLVE TÃO FORTE,\nEM PAZ ME DEITO, PEGO NO SONO,\nPOIS EM SEUS BRAÇOS EU DESCANSO;\nBEM CEDO ACORDO E ME LEVANTO,\nPOIS SEI QUE JESUS ME SUSTENTOU.\n\nINSTRUMENTOS\n\nQUANDO FALO COM DEUS A LUZ ME ENVOLVE,\nTRANSPORTO PARA A ETERNIDADE,\nESTOU NO PLANO DE DEUS;\nPEÇO TODO DIA AO MEU SENHOR\nQUE SALVE A MINHA FAMÍLIA\nPRA JUNTOS LOUVARMOS\nAO BOM PASTOR\nQUE CUIDA DE NÓS COM AMOR. (BIS)', 'QUANDO FALO COM DEUS AS LÁGRIMAS CORREM, A SUA PRESENÇA ME ENVOLVE TÃO FORTE, EM PAZ ME DEITO, PEGO NO SONO, POIS EM SEUS BRAÇOS EU DESCANSO; BEM CEDO ACORDO E ME LEVANTO, POIS SEI QUE JESUS ME SUSTENTOU. INSTRUMENTOS QUANDO FALO COM DEUS A LUZ ME ENVOLVE, TRANSPORTO PARA A ETERNIDADE, ESTOU NO PLANO DE DEUS; PEÇO TODO DIA AO MEU SENHOR QUE SALVE A MINHA FAMÍLIA PRA JUNTOS LOUVARMOS AO BOM PASTOR QUE CUIDA DE NÓS COM AMOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('18', '18 - BUSCAI AO SENHOR', 'BUSCAI AO SENHOR \nENQUANTO SE PODE ACHAR,\nINVOCAI-O ENQUANTO ESTÁ PERTO.    \nBUSCAI AO SENHOR \nENQUANTO SE PODE ACHAR,\nINVOCAI-O ENQUANTO ESTÁ PERTO.\n\nDEIXE O ÍMPIO O SEU CAMINHO,\nE O HOMEM MALIGNO OS SEUS PENSAMENTOS\nE SE CONVERTA AO SENHOR,\nQUE DELE SE COMPADECERÁ;\nTORNE PARA O NOSSO DEUS,\nPORQUE GRANDIOSO É EM PERDOAR.\n\nBUSCAI AO SENHOR \nENQUANTO SE PODE ACHAR,\nINVOCAI-O ENQUANTO ESTÁ PERTO.    \nBUSCAI AO SENHOR \nENQUANTO SE PODE ACHAR,\nINVOCAI-O ENQUANTO ESTÁ PERTO.', 'BUSCAI AO SENHOR ENQUANTO SE PODE ACHAR, INVOCAI-O ENQUANTO ESTÁ PERTO. BUSCAI AO SENHOR ENQUANTO SE PODE ACHAR, INVOCAI-O ENQUANTO ESTÁ PERTO. DEIXE O ÍMPIO O SEU CAMINHO, E O HOMEM MALIGNO OS SEUS PENSAMENTOS E SE CONVERTA AO SENHOR, QUE DELE SE COMPADECERÁ; TORNE PARA O NOSSO DEUS, PORQUE GRANDIOSO É EM PERDOAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('19', '19 - QUANDO DAVI AO SENHOR ORAVA', 'QUANDO DAVI AO SENHOR ORAVA\nPRA JERUSALÉM ESTAVA O SEU OLHAR,\nPORQUE ELE SABIA QUE EM JERUSALÉM\nO NOME DO SENHOR FIRMADO ESTÁ.\n\nIGUAL DAVI, TAMBEM VOU ORAR\nOLHANDO PRA JERUSALÉM CELESTIAL,\nA CIDADE DE MEU REI E SENHOR,\nQUE DO SEU TRONO IRÁ RESPONDER\nÀ ORAÇÃO DE SEU SERVO FIEL.\n\nEU VOU ORAR, EU VOU ORAR,\nPORQUE EU SEI \nQUE MEU SENHOR VAI ME ESCUTAR\n\nEU VOU ORAR, EU VOU ORAR,\nPORQUE EU SEI \nQUE MEU SENHOR VAI ME ESCUTAR\n\nQUANDO DAVI AO SENHOR ORAVA\nPRA JERUSALÉM ESTAVA O SEU OLHAR,\nPORQUE ELE SABIA QUE EM JERUSALÉM\nO NOME DO SENHOR FIRMADO ESTÁ.\n\nIGUAL DAVI, TAMBEM VOU ORAR\nOLHANDO PRA JERUSALÉM CELESTIAL,\nA CIDADE DE MEU REI E SENHOR,\nQUE DO SEU TRONO IRÁ RESPONDER\nÀ ORAÇÃO DE SEU SERVO FIEL.\n\nEU VOU ORAR, EU VOU ORAR,\nPORQUE EU SEI \nQUE MEU SENHOR VAI ME ESCUTAR\n\nEU VOU ORAR, EU VOU ORAR,\nPORQUE EU SEI \nQUE MEU SENHOR VAI ME ESCUTAR', 'QUANDO DAVI AO SENHOR ORAVA PRA JERUSALÉM ESTAVA O SEU OLHAR, PORQUE ELE SABIA QUE EM JERUSALÉM O NOME DO SENHOR FIRMADO ESTÁ. IGUAL DAVI, TAMBEM VOU ORAR OLHANDO PRA JERUSALÉM CELESTIAL, A CIDADE DE MEU REI E SENHOR, QUE DO SEU TRONO IRÁ RESPONDER À ORAÇÃO DE SEU SERVO FIEL. EU VOU ORAR, EU VOU ORAR, PORQUE EU SEI QUE MEU SENHOR VAI ME ESCUTAR EU VOU ORAR, EU VOU ORAR, PORQUE EU SEI QUE MEU SENHOR VAI ME ESCUTAR', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('20', '20 - QUANDO ORO AO SENHOR', 'QUANDO ORO AO SENHOR\nSINTO PAZ EM MEU CORAÇÃO.\nSUA PRESENÇA É TÃO REAL,\nSUA GRAÇA ME SATISFAZ.\n\nPORQUE JESUS POR MIM MORREU\nE VIVO ESTÁ A INTERCEDER AO PAI POR MIM.\nPOSSO ENTÃO DESCANSAR,\nPORQUE EU SEI QUE O SENHOR\nTODO BEM FARÁ PRA MIM.\n\nFINAL: TODO BEM FARÁ PRA MIM.\nBIS', 'QUANDO ORO AO SENHOR SINTO PAZ EM MEU CORAÇÃO. SUA PRESENÇA É TÃO REAL, SUA GRAÇA ME SATISFAZ. PORQUE JESUS POR MIM MORREU E VIVO ESTÁ A INTERCEDER AO PAI POR MIM. POSSO ENTÃO DESCANSAR, PORQUE EU SEI QUE O SENHOR TODO BEM FARÁ PRA MIM.  TODO BEM FARÁ PRA MIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('21', '21 - SANTO, JUSTO, PRÍNCIPE DA VIDA', 'SANTO, JUSTO, PRÍNCIPE DA VIDA. \nFILHO DE DEUS, JESUS, EMANUEL. \nHOJE ESTÁ AQUI PARA LIBERTAR \nE ATRAVÉS DO SEU SANGUE\nENTRAMOS DIANTE DO PAI.\nBIS', 'SANTO, JUSTO, PRÍNCIPE DA VIDA. FILHO DE DEUS, JESUS, EMANUEL. HOJE ESTÁ AQUI PARA LIBERTAR E ATRAVÉS DO SEU SANGUE ENTRAMOS DIANTE DO PAI.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('22', '22 - FAZE ORAÇÃO', 'FAZE ORAÇÃO BEM CEDINHO,\nAO MEIO DIA, SIM, TAMBÉM;\nFAZE ORAÇÃO À NOITINHA, \nQUE É DEUS QUE TE SUSTÉM.\n\nJÁ DE MANHÃ DEUS ESCUTA,\nAO MEIO DIA, SIM, TAMBÉM;\nMESMO TE VÊ A NOITINHA, \nDE TUDO TE SUSTÉM.\n\nJÁ DE MANHÃ DEUS RESPONDE,\nAO MEIO DIA, SIM, TAMBÉM;\nFAZE ORAÇÃO À NOITINHA, \nPOIS ELE LOGO VEM.', 'FAZE ORAÇÃO BEM CEDINHO, AO MEIO DIA, SIM, TAMBÉM; FAZE ORAÇÃO À NOITINHA, QUE É DEUS QUE TE SUSTÉM. JÁ DE MANHÃ DEUS ESCUTA, AO MEIO DIA, SIM, TAMBÉM; MESMO TE VÊ A NOITINHA, DE TUDO TE SUSTÉM. JÁ DE MANHÃ DEUS RESPONDE, AO MEIO DIA, SIM, TAMBÉM; FAZE ORAÇÃO À NOITINHA, POIS ELE LOGO VEM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('23', '23 - QUANDO ISRAEL SAIU DO EGITO', 'QUANDO ISRAEL SAIU DO EGITO,\nISRAEL CANTOU, CANTOU:\nO SENHOR É A MINHA FORÇA.\n\nELE ME FOI POR SALVAÇÃO,\nESTE É O MEU DEUS,\nLHE FAREI UMA HABITAÇÃO.\n\nAO SENHOR TAMBÉM LOUVAREI\nPORQUE É A MINHA SALVAÇÃO,\nMEU REFÚGIO, MINHA FORTALEZA,\nLHE FAREI UMA HABITAÇÃO\nNO MEU CORAÇÃO.\nINSTRUMENTOS\n\nQUANDO ISRAEL SAIU DO EGITO,\nISRAEL CANTOU, CANTOU:\nO SENHOR É A MINHA FORÇA.\n\nELE ME FOI POR SALVAÇÃO,\nESTE É O MEU DEUS,\nLHE FAREI UMA HABITAÇÃO.\n\nAO SENHOR TAMBÉM LOUVAREI\nPORQUE É A MINHA SALVAÇÃO,\nMEU REFÚGIO, MINHA FORTALEZA,\nLHE FAREI UMA HABITAÇÃO\nNO MEU CORAÇÃO.\n\nQUANDO ISRAEL SAIU DO EGITO,\nISRAEL CANTOU, CANTOU:\nO SENHOR É A MINHA FORÇA.', 'QUANDO ISRAEL SAIU DO EGITO, ISRAEL CANTOU, CANTOU: O SENHOR É A MINHA FORÇA. ELE ME FOI POR SALVAÇÃO, ESTE É O MEU DEUS, LHE FAREI UMA HABITAÇÃO. AO SENHOR TAMBÉM LOUVAREI PORQUE É A MINHA SALVAÇÃO, MEU REFÚGIO, MINHA FORTALEZA, LHE FAREI UMA HABITAÇÃO NO MEU CORAÇÃO. INSTRUMENTOS AO SENHOR TAMBÉM LOUVAREI PORQUE É A MINHA SALVAÇÃO, MEU REFÚGIO, MINHA FORTALEZA, LHE FAREI UMA HABITAÇÃO NO MEU CORAÇÃO. QUANDO ISRAEL SAIU DO EGITO, ISRAEL CANTOU, CANTOU: O SENHOR É A MINHA FORÇA.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('24', '24 - UMA OVELHINHA DE JESUS EU SOU', 'UMA OVELHINHA DE JESUS EU SOU,\nSEMPRE ESTÁ COMIGO, É MEU BOM PASTOR;\nME MOSTRA O CAMINHO EM QUE DEVO ANDAR,\nÉ MEU AMADO SALVADOR.\n\nME LIVRA DO PERIGO, ESTENDE O SEU CAJADO,\nÉ MEU MELHOR AMIGO, DO MAL ESTOU GUARDADO;\nEM TODA A MINHA VIDA EU O AMAREI\nE PARA SEMPRE VIVEREI COM JESUS.\n\nE PARA SEMPRE VIVEREI COM JESUS.', 'UMA OVELHINHA DE JESUS EU SOU, SEMPRE ESTÁ COMIGO, É MEU BOM PASTOR; ME MOSTRA O CAMINHO EM QUE DEVO ANDAR, É MEU AMADO SALVADOR. ME LIVRA DO PERIGO, ESTENDE O SEU CAJADO, É MEU MELHOR AMIGO, DO MAL ESTOU GUARDADO; EM TODA A MINHA VIDA EU O AMAREI E PARA SEMPRE VIVEREI COM JESUS. E PARA SEMPRE VIVEREI COM JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('25', '25 - ONDE QUER QUE TU FORES IREI, JESUS', 'ONDE QUER QUE TU FORES IREI, JESUS,\nA TI MINHA ALMA JÁ DEI;\nSOU PARTE DO TEU CORPO,\nPERTENÇO AO TEU POVO,\nTU ÉS MEU REMIDOR.\n\nCORO\nA TUA PALAVRA ALIMENTA A ALMA DO PECADOR,\nCOLHEMOS EM TEU CAMPO, TRIGO, Ó SENHOR,\nDELÍCIAS EM TUA MESA E CÂNTICOS DE LOUVOR;\nALEGRO-ME CONTIGO, TU ÉS MEU REMIDOR.\n\nAJOELHO-ME A TEUS PÉS, AGRADEÇO A SALVAÇÃO\nQUE DE GRAÇA RECEBI, MEU SENHOR;\nTUA VOZ É PODEROSA,\nORDENAS BÊNÇÃOS PARA MIM\nE ME CONFORTAS NOS TEUS BRAÇOS DE AMOR.\n\nCORO\nA TUA PALAVRA ALIMENTA A ALMA DO PECADOR,\nCOLHEMOS EM TEU CAMPO, TRIGO, Ó SENHOR,\nDELÍCIAS EM TUA MESA E CÂNTICOS DE LOUVOR;\nALEGRO-ME CONTIGO, TU ÉS MEU REMIDOR.', 'ONDE QUER QUE TU FORES IREI, JESUS, A TI MINHA ALMA JÁ DEI; SOU PARTE DO TEU CORPO, PERTENÇO AO TEU POVO, TU ÉS MEU REMIDOR. CORO A TUA PALAVRA ALIMENTA A ALMA DO PECADOR, COLHEMOS EM TEU CAMPO, TRIGO, Ó SENHOR, DELÍCIAS EM TUA MESA E CÂNTICOS DE LOUVOR; ALEGRO-ME CONTIGO, TU ÉS MEU REMIDOR. AJOELHO-ME A TEUS PÉS, AGRADEÇO A SALVAÇÃO QUE DE GRAÇA RECEBI, MEU SENHOR; TUA VOZ É PODEROSA, ORDENAS BÊNÇÃOS PARA MIM E ME CONFORTAS NOS TEUS BRAÇOS DE AMOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('26', '26 - EU SOU UMA OVELHINHA', 'EU SOU UMA OVELHINHA DE JESUS,\nEU SOU UMA OVELHINHA DE JESUS.\nJESUS É O BOM PASTOR,\nJESUS CUIDA DE MIM.\nEU SOU UMA OVELHINHA\nDO SENHOR.\n\nBEM CEDO, AO LEVANTAR, EU VOU ORAR,\nAGRADECER A NOITE QUE ME DEU;\nEU LOUVO COM AMOR\nE DIGO SEMPRE ASSIM:\nEU SOU UM PEQUENINO DO SENHOR,\nEU SOU UM PEQUENINO DO SENHOR.', 'EU SOU UMA OVELHINHA DE JESUS, EU SOU UMA OVELHINHA DE JESUS. JESUS É O BOM PASTOR, JESUS CUIDA DE MIM. EU SOU UMA OVELHINHA DO SENHOR. BEM CEDO, AO LEVANTAR, EU VOU ORAR, AGRADECER A NOITE QUE ME DEU; EU LOUVO COM AMOR E DIGO SEMPRE ASSIM: EU SOU UM PEQUENINO DO SENHOR, EU SOU UM PEQUENINO DO SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('27', '27 - ELE É A RAIZ DE DAVI', 'ELE É A RAIZ, A RAIZ DE DAVI,\nELE É O LEÃO, O LEÃO DE JUDÁ,\nELE É A RAIZ, A RAIZ DE DAVI,\nMARAVILHOSO, CONSELHEIRO, \nPRÍNCIPE DA PAZ.\n\nE AGORA QUAL É A RAZÃO DA MINHA FÉ?\n(EU VOU DIZER)\nSIM, CRISTO, MEU AMADO SALVADOR.\n\nFINAL: SIM, CRISTO, MEU AMADO SALVADOR.\nBIS', 'ELE É A RAIZ, A RAIZ DE DAVI, ELE É O LEÃO, O LEÃO DE JUDÁ, ELE É A RAIZ, A RAIZ DE DAVI, MARAVILHOSO, CONSELHEIRO, PRÍNCIPE DA PAZ. E AGORA QUAL É A RAZÃO DA MINHA FÉ? (EU VOU DIZER) SIM, CRISTO, MEU AMADO SALVADOR.  SIM, CRISTO, MEU AMADO SALVADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('28', '28 - SOU UMA CRIANÇA FELIZ', 'SOU UMA CRIANÇA FELIZ\nPORQUE TENHO JESUS EM MEU CORAÇÃO,\nCOMO PROVA DESTE AMOR TÃO GRANDE\nEU CANTO AO MEU SENHOR\nUMA NOVA CANÇÃO.\n\nCORO \nUMA CANÇÃO DE AMOR\nQUE EXPRESSA A MINHA GRATIDÃO,\nOH! QUERIDO SALVADOR,\nPELA GRANDE SALVAÇÃO.\nBIS\n\nJESUS, TU ÉS MELHOR QUE TUDO\nO QUE EXISTE DE MAIS PRECIOSO,\nNADA PODE SE COMPARAR A ESTE AMOR\nQUE FLUI DE UM DEUS TÃO MARAVILHOSO.\n\nCORO \nUMA CANÇÃO DE AMOR\nQUE EXPRESSA A MINHA GRATIDÃO,\nOH! QUERIDO SALVADOR,\nPELA GRANDE SALVAÇÃO.\nBIS', 'SOU UMA CRIANÇA FELIZ PORQUE TENHO JESUS EM MEU CORAÇÃO, COMO PROVA DESTE AMOR TÃO GRANDE EU CANTO AO MEU SENHOR UMA NOVA CANÇÃO. CORO UMA CANÇÃO DE AMOR QUE EXPRESSA A MINHA GRATIDÃO, OH! QUERIDO SALVADOR, PELA GRANDE SALVAÇÃO. JESUS, TU ÉS MELHOR QUE TUDO O QUE EXISTE DE MAIS PRECIOSO, NADA PODE SE COMPARAR A ESTE AMOR QUE FLUI DE UM DEUS TÃO MARAVILHOSO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('29', '29 - JESUS, QUANDO ME LEMBRO', 'JESUS, QUANDO ME LEMBRO\nDO DIA EM QUE A TI ME ENTREGUEI,\nLEMBRO DO CALOR QUE SENTIA\nE DO AMOR QUE ME INVADIA,\nLEMBRO QUE A PALAVRA ABRIA\nE TUA VOZ OUVIA DIZER:\n\n“VINDE A MIM,\nOS CANSADOS E OPRIMIDOS,\nE EU VOS ALIVIAREI.\nVINDE A MIM,\nOS CANSADOS E OPRIMIDOS,\nE EU VOS ALIVIAREI.”', 'JESUS, QUANDO ME LEMBRO DO DIA EM QUE A TI ME ENTREGUEI, LEMBRO DO CALOR QUE SENTIA E DO AMOR QUE ME INVADIA, LEMBRO QUE A PALAVRA ABRIA E TUA VOZ OUVIA DIZER: VINDE A MIM, OS CANSADOS E OPRIMIDOS, E EU VOS ALIVIAREI. VINDE A MIM, OS CANSADOS E OPRIMIDOS, E EU VOS ALIVIAREI.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('30', '30 - ELE VIVE EM MIM', 'ELE VIVE EM MIM, (BIS)\nA FLOR DO JARDIM, JESUS,\nELE VIVE EM MIM.\n\nELE É O MEU REI, (BIS)\nSÓ A ELE SERVIREI, ELE É MEU REI.\n\nELE VAI VOLTAR, (BIS)\nVEM A CRISTO SEM TARDAR, \nELE VAI VOLTAR.', 'ELE VIVE EM MIM,  A FLOR DO JARDIM, JESUS, ELE VIVE EM MIM. ELE É O MEU REI,  SÓ A ELE SERVIREI, ELE É MEU REI. ELE VAI VOLTAR,  VEM A CRISTO SEM TARDAR, ELE VAI VOLTAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('31', '31 - EU QUERO SER O SAL DA TERRA', 'EU QUERO SER O SAL DA TERRA,\nEU QUERO SER A LUZ DO MUNDO,\nEU QUERO SER O SAL DA TERRA,\nEU QUERO SER A LUZ DO MUNDO.\n\nEU QUERO BRILHAR, \nEU QUERO BRILHAR, \nEU QUERO BRILHAR ATÉ JESUS\nVOLTAR.\nBIS', 'EU QUERO SER O SAL DA TERRA, EU QUERO SER A LUZ DO MUNDO, EU QUERO SER O SAL DA TERRA, EU QUERO SER A LUZ DO MUNDO. EU QUERO BRILHAR, EU QUERO BRILHAR, EU QUERO BRILHAR ATÉ JESUS VOLTAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('32', '32 - PRÍNCIPE DA PAZ', 'PRÍNCIPE DA PAZ, QUERO TE AMAR,\nTEUS MANDAMENTOS, SIM,\nTODOS GUARDAR;\nÓ DEUS, VEM AJUDAR-ME\nA FAZER TUA VONTADE\nE EM TUDO TEU NOME LOUVAR.\n\nJESUS REDENTOR,\nQUERO TE ADORAR\nEM ESPÍRITO E EM VERDADE;\nÓ DEUS, VEM ENSINAR-ME\nA LOUVAR TUA GRANDEZA\nE BENDIZER TEU NOME,\nÓ SENHOR!', 'PRÍNCIPE DA PAZ, QUERO TE AMAR, TEUS MANDAMENTOS, SIM, TODOS GUARDAR; Ó DEUS, VEM AJUDAR-ME A FAZER TUA VONTADE E EM TUDO TEU NOME LOUVAR. JESUS REDENTOR, QUERO TE ADORAR EM ESPÍRITO E EM VERDADE; Ó DEUS, VEM ENSINAR-ME A LOUVAR TUA GRANDEZA E BENDIZER TEU NOME, Ó SENHOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('33', '33 - CRISTO, MEU BOM MESTRE', 'CRISTO, MEU BOM MESTRE,\nEU TE AMO E TE QUERO;\nEU TE ADORO, MEU SENHOR E REI.\n\nSENHOR (SENHOR), MEU REI,\nSENHOR (SENHOR), MEU REI.	\n\nREPETIR O HINO\nBIS', 'CRISTO, MEU BOM MESTRE, EU TE AMO E TE QUERO; EU TE ADORO, MEU SENHOR E REI. SENHOR (SENHOR), MEU REI, SENHOR (SENHOR), MEU REI. REPETIR O HINO', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('34', '34 - TENHO UM SENHOR MUITO FORTE', 'TENHO UM SENHOR MUITO FORTE, \nVENCEU A MORTE\nPARA ME DAR UM LUGAR LÁ NO CÉU.\n\nESTE SENHOR É JESUS QUE ME CONDUZ\nPARA UM LAR DE GOZO E DE PAZ.\n\nQUERO SERVI-LO, QUERO LOUVÁ-LO,\nJUSTO SENHOR, DEUS FORTE, MARAVILHOSO,\nCONSELHEIRO, PAI DA ETERNIDADE,\nPRÍNCIPE DA PAZ, JESUS.', 'TENHO UM SENHOR MUITO FORTE, VENCEU A MORTE PARA ME DAR UM LUGAR LÁ NO CÉU. ESTE SENHOR É JESUS QUE ME CONDUZ PARA UM LAR DE GOZO E DE PAZ. QUERO SERVI-LO, QUERO LOUVÁ-LO, JUSTO SENHOR, DEUS FORTE, MARAVILHOSO, CONSELHEIRO, PAI DA ETERNIDADE, PRÍNCIPE DA PAZ, JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('35', '35 - QUERO SER COMO DAVI', 'QUERO SER COMO DAVI,\nTÃO PEQUENINO, \nNÃO CONFIAVA EM SI;\nQUERO SER TEMENTE A DEUS\nE ASSIM VENCER O FILISTEU.', 'QUERO SER COMO DAVI, TÃO PEQUENINO, NÃO CONFIAVA EM SI; QUERO SER TEMENTE A DEUS E ASSIM VENCER O FILISTEU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('36', '36 - EU QUERO ANDAR SEMPRE COM JESUS', 'EU QUERO ANDAR SEMPRE COM JESUS,\nÀ ETERNIDADE ELE ME CONDUZ;\nE QUANDO A LUTA TIVER QUE ENFRENTAR\nELE ESTARÁ A ME PROTEGER.\n\nSUSTENTA-ME, PEGA A MINHA MÃO,\nLEVANTA-ME, DÁ-ME A SALVAÇÃO;\nCRESCER COM ELE É O MAIOR TESOURO,\nTRABALHAR PRA ELE É A MINHA ADORAÇÃO. (2X)', 'EU QUERO ANDAR SEMPRE COM JESUS, À ETERNIDADE ELE ME CONDUZ; E QUANDO A LUTA TIVER QUE ENFRENTAR ELE ESTARÁ A ME PROTEGER. SUSTENTA-ME, PEGA A MINHA MÃO, LEVANTA-ME, DÁ-ME A SALVAÇÃO; CRESCER COM ELE É O MAIOR TESOURO, TRABALHAR PRA ELE É A MINHA ADORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('37', '37 - NO MEU CORAÇÃO HÁ UM TESOURO (PRÍNCIPE DA VIDA)', 'NO MEU CORAÇÃO HÁ UM TESOURO\nO MAIOR QUE POSSA EXISTIR\nA MELHOR COISA QUE FIZ FOI ENCONTRÁ-LO\nPOIS VIDA ETERNA ADQUIRI.\n\nPRÍNCIPE DA VIDA, PAI DA ETERNIDADE\nÉ O MEU TESOURO.\nPRÍNCIPE DA VIDA, PAI DA ETERNIDADE\nÉ O MEU TESOURO, É O MEU JESUS.', 'NO MEU CORAÇÃO HÁ UM TESOURO O MAIOR QUE POSSA EXISTIR A MELHOR COISA QUE FIZ FOI ENCONTRÁ-LO POIS VIDA ETERNA ADQUIRI. PRÍNCIPE DA VIDA, PAI DA ETERNIDADE É O MEU TESOURO. PRÍNCIPE DA VIDA, PAI DA ETERNIDADE É O MEU TESOURO, É O MEU JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('38', '38 - TENHO UM CORAÇÃO ALEGRE', 'TENHO UM CORAÇÃO ALEGRE\nPORQUE ESTOU NA CASA DO PAI;\nO MEU PAI CUIDA DE MIM,\nNELE TENHO PAZ.\n\nNÃO DEIXAREI SUA CASA\nPORQUE AQUI HÁ FARTURA DE PÃO;\nNÃO DEIXAREI JESUS\nPOIS NELE HÁ SALVAÇÃO.\n\nTENHO UM CORAÇÃO ALEGRE\nPORQUE ESTOU NA CASA DO PAI;\nO MEU PAI CUIDA DE MIM,\nNELE TENHO PAZ,\nNELE HÁ SALVAÇÃO.', 'TENHO UM CORAÇÃO ALEGRE PORQUE ESTOU NA CASA DO PAI; O MEU PAI CUIDA DE MIM, NELE TENHO PAZ. NÃO DEIXAREI SUA CASA PORQUE AQUI HÁ FARTURA DE PÃO; NÃO DEIXAREI JESUS POIS NELE HÁ SALVAÇÃO. TENHO UM CORAÇÃO ALEGRE PORQUE ESTOU NA CASA DO PAI; O MEU PAI CUIDA DE MIM, NELE TENHO PAZ, NELE HÁ SALVAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('39', '39 - NA AREIA NÃO QUERO CONSTRUIR (FIRME, SIM!)', 'NA AREIA NÃO QUERO CONSTRUIR,\nSEI MUITO BEM, A CASA VAI CAIR;\nMINHA BÍBLIA JÁ ME DIZ:\nÉ NA ROCHA QUE DEVO CONSTRUIR.\n\nCORO\nFIRME, SIM! FIRME, SIM!\nMINHA CASA FIRME ESTÁ,\nNÃO NA AREIA, SIM NA ROCHA,\nE JESUS VAI SUSTENTAR;\nFIRME, SIM! FIRME, SIM!\nMINHA CASA FIRME ESTÁ.\n\nEM JESUS, ROCHA ETERNA,\nMINHA CASA FIRME ESTÁ;\nNEM A CHUVA, VENTO OU MAR,\nMINHA CASA VAI DERRUBAR.\n\nCORO\nFIRME, SIM! FIRME, SIM!\nMINHA CASA FIRME ESTÁ,\nNÃO NA AREIA, SIM NA ROCHA,\nE JESUS VAI SUSTENTAR;\nFIRME, SIM! FIRME, SIM!\nMINHA CASA FIRME ESTÁ.', 'NA AREIA NÃO QUERO CONSTRUIR, SEI MUITO BEM, A CASA VAI CAIR; MINHA BÍBLIA JÁ ME DIZ: É NA ROCHA QUE DEVO CONSTRUIR. CORO FIRME, SIM! FIRME, SIM! MINHA CASA FIRME ESTÁ, NÃO NA AREIA, SIM NA ROCHA, E JESUS VAI SUSTENTAR; FIRME, SIM! FIRME, SIM! MINHA CASA FIRME ESTÁ. EM JESUS, ROCHA ETERNA, MINHA CASA FIRME ESTÁ; NEM A CHUVA, VENTO OU MAR, MINHA CASA VAI DERRUBAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('40', '40 - QUERO OUVIR (MEU QUERER)', 'QUERO OUVIR A TUA VOZ, Ó MEU SENHOR,\nA FALAR TÃO PURA E MEIGA AO CORAÇÃO\nE SENTIR O TOQUE DO TEU SANTO ESPÍRITO\nPARA QUE EU POSSA RENOVAR \nA COMUNHÃO.\n\nCORO (2X)\nQUERO LOUVAR, \nCOM ALEGRIA EM MEU VIVER\nRENOVAR A MINHA FÉ, \nENTREGAR TODO O MEU SER.\n\nQUERO FALAR DESTE SEU IMENSO AMOR.\nE DIZER A TODO O MUNDO QUE JESUS \nÉ O SALVADOR.\nQUE LÁ NA CRUZ ELE MORREU \nPARA NOS SALVAR\nDERRAMOU SEU SANGUE PURA \nE A VIDA ETERNA DÁ.\n\nCORO (2X)\nQUERO LOUVAR, \nCOM ALEGRIA EM MEU VIVER\nRENOVAR A MINHA FÉ, \nENTREGAR TODO O MEU SER.', 'QUERO OUVIR A TUA VOZ, Ó MEU SENHOR, A FALAR TÃO PURA E MEIGA AO CORAÇÃO E SENTIR O TOQUE DO TEU SANTO ESPÍRITO PARA QUE EU POSSA RENOVAR A COMUNHÃO. QUERO LOUVAR, COM ALEGRIA EM MEU VIVER RENOVAR A MINHA FÉ, ENTREGAR TODO O MEU SER. QUERO FALAR DESTE SEU IMENSO AMOR. E DIZER A TODO O MUNDO QUE JESUS É O SALVADOR. QUE LÁ NA CRUZ ELE MORREU PARA NOS SALVAR DERRAMOU SEU SANGUE PURA E A VIDA ETERNA DÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('41', '41 - A MINHA CASA FIRME ESTÁ', 'A MINHA CASA FIRME ESTÁ,\nFIRMADA NA ROCHA ELA ESTÁ.\nPODEM A CHUVA E O VENTO SOPRAR,\nQUE ELA NÃO SE ABALARÁ,\nPOIS ESTA CASA É O MEU CORAÇÃO.\nNELA JESUS FAZ HABITAÇÃO.\nQUERO FALAR NESSA MINHA CANÇÃO\nQUE JESUS CRISTO É A SALVAÇÃO.\n\nA MINHA CASA FIRME ESTÁ,\nFIRMADA NA ROCHA ELA ESTÁ.\nPODEM A CHUVA E O VENTO SOPRAR,\nQUE ELA NÃO SE ABALARÁ,\nPOIS ESTA CASA É O MEU CORAÇÃO.\nNELA JESUS FAZ HABITAÇÃO.\nQUERO FALAR NESSA MINHA CANÇÃO\nQUE JESUS CRISTO É A SALVAÇÃO.', 'A MINHA CASA FIRME ESTÁ, FIRMADA NA ROCHA ELA ESTÁ. PODEM A CHUVA E O VENTO SOPRAR, QUE ELA NÃO SE ABALARÁ, POIS ESTA CASA É O MEU CORAÇÃO. NELA JESUS FAZ HABITAÇÃO. QUERO FALAR NESSA MINHA CANÇÃO QUE JESUS CRISTO É A SALVAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('42', '42 - MINHA VIDA E MEU CORAÇÃO', 'MINHA VIDA E MEU CORAÇÃO\nPERTENCEM A JESUS, \nDIA A DIA, EM MEU VIVER, \nVOU ANDAR EM SUA LUZ.\nÀ PALAVRA VOU OBEDECER,\nCOM AMOR TESTEMUNHAR,\nE PRA SEMPRE, SEMPRE O LOUVAREI,      \nNA ETERNIDADE EU VOU MORAR.\nBIS', 'MINHA VIDA E MEU CORAÇÃO PERTENCEM A JESUS, DIA A DIA, EM MEU VIVER, VOU ANDAR EM SUA LUZ. À PALAVRA VOU OBEDECER, COM AMOR TESTEMUNHAR, E PRA SEMPRE, SEMPRE O LOUVAREI, NA ETERNIDADE EU VOU MORAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('43', '43 - SOU FELIZ', 'SOU FELIZ  \nTENHO JESUS EM MEU CORAÇÃO \n\nSOU FELIZ, ANDO EM SUA LUZ, \nNÃO ESTAREI NA ESCURIDÃO.\nBIS', 'SOU FELIZ TENHO JESUS EM MEU CORAÇÃO SOU FELIZ, ANDO EM SUA LUZ, NÃO ESTAREI NA ESCURIDÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('44', '44 - SOU SERVO DO SENHOR', 'SOU SERVO DO SENHOR, SERVI-LO É MEU PRAZER \nPOIS SALVO EM SUA GRAÇA POR SEU FILHO JESUS, \nTORNOU-ME LIVRE.\n\nEM JESUS POSSO IR DIRETO AO PAI, \nCOM OUSADIA ENTRAR NO SANTO DOS SANTOS COMO REI E SACERDOTE O SENHOR ME COLOCOU \n\nPOSSO GOVERNAR, POSSO MINISTRAR, \nAO SENHOR SEMPRE ADORAR, EM ESPÍRITO \nE VERDADE HOJE E ETERNAMENTE.\n\nSOU SERVO DO SENHOR, SERVI-LO É MEU PRAZER \nPOIS SALVO EM SUA GRAÇA POR SEU FILHO JESUS, \nTORNOU-ME LIVRE.\n\nEM JESUS POSSO IR DIRETO AO PAI, \nCOM OUSADIA ENTRAR NO SANTO DOS SANTOS COMO REI E SACERDOTE O SENHOR ME COLOCOU \n\nPOSSO GOVERNAR, POSSO MINISTRAR, AO SENHOR SEMPRE ADORAR, EM ESPÍRITO E VERDADE \nHOJE E ETERNAMENTE. (2X)', 'SOU SERVO DO SENHOR, SERVI-LO É MEU PRAZER POIS SALVO EM SUA GRAÇA POR SEU FILHO JESUS, TORNOU-ME LIVRE. EM JESUS POSSO IR DIRETO AO PAI, COM OUSADIA ENTRAR NO SANTO DOS SANTOS COMO REI E SACERDOTE O SENHOR ME COLOCOU POSSO GOVERNAR, POSSO MINISTRAR, AO SENHOR SEMPRE ADORAR, EM ESPÍRITO E VERDADE HOJE E ETERNAMENTE.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('45', '45 - JESUS RESSUSCITOU! (MEU AMIGO JESUS)', 'CORO (2X)\nJESUS RESSUSCITOU! \nCANTAREI EM MEU CORAÇÃO\nTODA GLÓRIA, HONRA E LOUVOR,\nA JESUS, POR SUA PAZ E PERDÃO.\n\nNO MEU CORAÇÃO NÃO HÁ TRISTEZAS, \nPOIS JESUS, COM SEU SANGUE, O LAVOU.\nHOJE EU CANTO COM MUITA ALEGRIA,\nADORANDO AO MEU REI E SENHOR.\n\nHOJE POSSO TESTEMUNHAR\nDE UM AMIGO QUE SÓ ME QUER BEM.\nDEU, NA CRUZ, SUA VIDA POR MIM, \nPRA QUE EU VÁ NO CÉU MORAR TAMBÉM.\n\nCORO\nJESUS RESSUSCITOU! \nCANTAREI EM MEU CORAÇÃO\nTODA GLÓRIA, HONRA E LOUVOR,\nA JESUS, POR SUA PAZ E PERDÃO.\n\nFINAL\nTODA GLÓRIA, HONRA E LOUVOR\nA JESUS, POR SUA PAZ E PERDÃO.', 'JESUS RESSUSCITOU! CANTAREI EM MEU CORAÇÃO TODA GLÓRIA, HONRA E LOUVOR, A JESUS, POR SUA PAZ E PERDÃO. NO MEU CORAÇÃO NÃO HÁ TRISTEZAS, POIS JESUS, COM SEU SANGUE, O LAVOU. HOJE EU CANTO COM MUITA ALEGRIA, ADORANDO AO MEU REI E SENHOR. HOJE POSSO TESTEMUNHAR DE UM AMIGO QUE SÓ ME QUER BEM. DEU, NA CRUZ, SUA VIDA POR MIM, PRA QUE EU VÁ NO CÉU MORAR TAMBÉM. CORO JESUS RESSUSCITOU! CANTAREI EM MEU CORAÇÃO TODA GLÓRIA, HONRA E LOUVOR, A JESUS, POR SUA PAZ E PERDÃO. FINAL TODA GLÓRIA, HONRA E LOUVOR A JESUS, POR SUA PAZ E PERDÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('46', '46 - COMO É BOM SERVIR A DEUS', 'COMO É BOM SERVIR A DEUS\nCONHECÊ-LO CADA VEZ MAIS\nE VIVENDO NO SEU AMOR\nSENTIR PROTEÇÃO.\n\nCORO (2X)\nQUE ME TRAZ SEGURANÇA, VIDA,\nAMOR, PAZ E ALEGRIA.\n\nCOM MEU DEUS EU SEI QUE SOU\nMAIS QUE VENCEDOR.\nVOU PASSANDO AS PROVAÇÕES\nFIRMADO NO SEU AMOR.', 'COMO É BOM SERVIR A DEUS CONHECÊ-LO CADA VEZ MAIS E VIVENDO NO SEU AMOR SENTIR PROTEÇÃO. QUE ME TRAZ SEGURANÇA, VIDA, AMOR, PAZ E ALEGRIA. COM MEU DEUS EU SEI QUE SOU MAIS QUE VENCEDOR. VOU PASSANDO AS PROVAÇÕES FIRMADO NO SEU AMOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('47', '47 - TENHO NO MEU CORAÇÃO', 'CORO\nTENHO NO MEU CORAÇÃO O DESEJO \nDE SERVIR AO SENHOR.\nTENHO NO MEU CORAÇÃO O DESEJO \nDE LOUVAR AO SENHOR.\n\nSOU UM SERVO ALEGRE\nPORQUE POSSO SENTIR O AMOR DO SENHOR,\nPORQUE DESDE PEQUENINO POSSO \nOUVIR A VOZ DO SENHOR.\n\nQUANDO TRISTE EU ESTOU\nO SENHOR ME ESTENDE A MÃO.\nQUANDO OLHO PARA OS CÉUS,\nO SENHOR OUVE A MINHA ORAÇÃO.\n\nCORO\nTENHO NO MEU CORAÇÃO O DESEJO \nDE SERVIR AO SENHOR.\nTENHO NO MEU CORAÇÃO O DESEJO \nDE LOUVAR AO SENHOR.\n\nPARA TE LOUVAR.     \nPARA TE LOUVAR.\n\nENTRÃO EU ABRO O MEU CORAÇÃO\nPARA TI, SENHOR, MEU JESUS.\nVEM EM MIM HABITAR,\nPOIS UM SERVO EU SOU.\nPOIS UM SERVO EU SOU.\nQUERO CRESCER NA TUA PRESENÇA Ó PAI,\nPARA TE LOUVAR.\nPARA TE LOUVAR.', 'CORO TENHO NO MEU CORAÇÃO O DESEJO DE SERVIR AO SENHOR. TENHO NO MEU CORAÇÃO O DESEJO DE LOUVAR AO SENHOR. SOU UM SERVO ALEGRE PORQUE POSSO SENTIR O AMOR DO SENHOR, PORQUE DESDE PEQUENINO POSSO OUVIR A VOZ DO SENHOR. QUANDO TRISTE EU ESTOU O SENHOR ME ESTENDE A MÃO. QUANDO OLHO PARA OS CÉUS, O SENHOR OUVE A MINHA ORAÇÃO. PARA TE LOUVAR. PARA TE LOUVAR. ENTRÃO EU ABRO O MEU CORAÇÃO PARA TI, SENHOR, MEU JESUS. VEM EM MIM HABITAR, POIS UM SERVO EU SOU. POIS UM SERVO EU SOU. QUERO CRESCER NA TUA PRESENÇA Ó PAI, PARA TE LOUVAR. PARA TE LOUVAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('48', '48 - JESUS CRISTO É O AMADO', 'JESUS CRISTO É O AMADO\nQUE MORREU PELO PECADOR\nO SEU SANGUE CLAMA AO PAI\nPARA REDENÇÃO,\nPARA OBTERMOS PERDÃO,\nSEGUNDO AS RIQUEZAS DE SUA GRAÇA.\n\nCORO \nPOR ESSE MOTIVO ESCOLHEMOS\nFICAR CONTIGO, JESUS,\nE HONRAR O TEU SACRIFÍCIO\nFEITO NO ALTAR DA CRUZ.\nESCOLHEMOS A VIDA E A BÊNÇÃO\nPARA NÓS E A NOSSA HERANÇA\nVIVERMOS SOB A TUA PROTEÇÃO.\n\nJESUS CRISTO É O AMADO\nQUE MORREU PELO PECADOR\nO SEU SANGUE CLAMA AO PAI\nPARA REDENÇÃO,\nPARA OBTERMOS PERDÃO,\nSEGUNDO AS RIQUEZAS DE SUA GRAÇA.\n\nCORO (2X)\nPOR ESSE MOTIVO ESCOLHEMOS\nFICAR CONTIGO, JESUS,\nE HONRAR O TEU SACRIFÍCIO\nFEITO NO ALTAR DA CRUZ.\nESCOLHEMOS A VIDA E A BÊNÇÃO\nPARA NÓS E A NOSSA HERANÇA\nVIVERMOS SOB A TUA PROTEÇÃO.', 'JESUS CRISTO É O AMADO QUE MORREU PELO PECADOR O SEU SANGUE CLAMA AO PAI PARA REDENÇÃO, PARA OBTERMOS PERDÃO, SEGUNDO AS RIQUEZAS DE SUA GRAÇA. CORO POR ESSE MOTIVO ESCOLHEMOS FICAR CONTIGO, JESUS, E HONRAR O TEU SACRIFÍCIO FEITO NO ALTAR DA CRUZ. ESCOLHEMOS A VIDA E A BÊNÇÃO PARA NÓS E A NOSSA HERANÇA VIVERMOS SOB A TUA PROTEÇÃO. POR ESSE MOTIVO ESCOLHEMOS FICAR CONTIGO, JESUS, E HONRAR O TEU SACRIFÍCIO FEITO NO ALTAR DA CRUZ. ESCOLHEMOS A VIDA E A BÊNÇÃO PARA NÓS E A NOSSA HERANÇA VIVERMOS SOB A TUA PROTEÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('49', '49 - A MINHA ALEGRIA É CRESCER COM O MEU SENHOR!', 'A MINHA ALEGRIA É CRESCER COM O MEU SENHOR!\nSENHOR DA MINHA VIDA, O MEU GRANDE AMOR!\nA TI EU RENDEREI TODA GLÓRIA E LOUVOR.\nAQUI TRABALHAREI NA TUA OBRA COM AMOR.\n\nUSA-ME SENHOR, PRA PREGAR AO PECADOR,\nUSA-ME SENHOR, NA TUA OBRA DE AMOR,\nUSA-ME SENHOR, QUERO TE GLORIFICAR!\nUSA-ME SENHOR, EM TODO E QUALQUER LUGAR,\nUSA-ME SENHOR, ATÉ MEU JESUS VOLTAR!', 'A MINHA ALEGRIA É CRESCER COM O MEU SENHOR! SENHOR DA MINHA VIDA, O MEU GRANDE AMOR! A TI EU RENDEREI TODA GLÓRIA E LOUVOR. AQUI TRABALHAREI NA TUA OBRA COM AMOR. USA-ME SENHOR, PRA PREGAR AO PECADOR, USA-ME SENHOR, NA TUA OBRA DE AMOR, USA-ME SENHOR, QUERO TE GLORIFICAR! USA-ME SENHOR, EM TODO E QUALQUER LUGAR, USA-ME SENHOR, ATÉ MEU JESUS VOLTAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('50', '50 - DO ALTO CÉU', 'DO ALTO CÉU ELE ME AMOU,\nDO MUNDO MAU ME RETIROU;\nFIZ A ESCOLHA DA SALVAÇÃO.\nEU ACEITEI O CORDEIRO DE DEUS.\n\nSEU SANGUE LAVOU O MEU CORAÇÃO,\nDE TODO PECADO ME PERDOOU;\nE HOJE SOU SALVO PELA GRAÇA DE DEUS.\n\nFINAL\nE HOJE SOU SALVO PELA GRAÇA DE DEUS.', 'DO ALTO CÉU ELE ME AMOU, DO MUNDO MAU ME RETIROU; FIZ A ESCOLHA DA SALVAÇÃO. EU ACEITEI O CORDEIRO DE DEUS. SEU SANGUE LAVOU O MEU CORAÇÃO, DE TODO PECADO ME PERDOOU; E HOJE SOU SALVO PELA GRAÇA DE DEUS. FINAL E HOJE SOU SALVO PELA GRAÇA DE DEUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('51', '51 - EIS-ME AQUI, SENHOR, EM TEU ALTAR', 'EIS-ME AQUI, SENHOR, \nEM TEU ALTAR, \nEIS-ME AQUI, SENHOR, \nPRONTO A TE ADORAR.\nBIS\n\nSEI QUE A PRESENÇA \nDO SENHOR JESUS\nNÃO FALHARÁ,\nSEI QUE TU ESTÁS AQUI,\nPRONTO A ABENÇOAR.\n\nINSTRUMENTOS\nBIS\n\nSANTO E GLORIOSO ÉS, CONOSCO ESTÁS,\nSANTO E GLORIOSO, VIMOS TE ADORAR,\nSANTO E GLORIOSO ÉS, Ó NOSSO AMADO,\nDO TEU ESPÍRITO TENS DERRAMADO,\nDANDO RENOVO E TRANSFORMANDO;\nSANTO E GLORIOSO ÉS,\nVEM EM NÓS TRANSBORDAR,\nÓ SANTO ESPÍRITO, TOMA O TEU LUGAR.\n\nSEI QUE A PRESENÇA \nDO SENHOR JESUS\nNÃO FALHARÁ,\nSEI QUE TU ESTÁS AQUI,\nPRONTO A ABENÇOAR.\nBIS', 'EIS-ME AQUI, SENHOR, EM TEU ALTAR, EIS-ME AQUI, SENHOR, PRONTO A TE ADORAR. SEI QUE A PRESENÇA DO SENHOR JESUS NÃO FALHARÁ, SEI QUE TU ESTÁS AQUI, PRONTO A ABENÇOAR. INSTRUMENTOS SANTO E GLORIOSO ÉS, CONOSCO ESTÁS, SANTO E GLORIOSO, VIMOS TE ADORAR, SANTO E GLORIOSO ÉS, Ó NOSSO AMADO, DO TEU ESPÍRITO TENS DERRAMADO, DANDO RENOVO E TRANSFORMANDO; SANTO E GLORIOSO ÉS, VEM EM NÓS TRANSBORDAR, Ó SANTO ESPÍRITO, TOMA O TEU LUGAR. SEI QUE A PRESENÇA DO SENHOR JESUS NÃO FALHARÁ, SEI QUE TU ESTÁS AQUI, PRONTO A ABENÇOAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('52', '52- SEU NOME É MARAVILHOSO', 'SEU NOME É MARAVILHOSO, (3x)\nPAI DA ETERNIDADE, PRÍNCIPE DA PAZ.\n\nSEU NOME É CONSELHEIRO, (3X)\nPAI DA ETERNIDADE, PRÍNCIPE DA PAZ\n\nSEU NOME É DEUS FORTE, (3X)\nPAI DA ETERNIDADE, PRÍNCIPE DA PAZ. \n\nIREI SEGUI-LO PARA SEMPRE\nPOIS ELE DÁ FELICIDADE\nSEU NOME É MARAVILHOSO,\nPAI DA ETERNIDADE, PRÍNCIPE DA PAZ.', 'SEU NOME É MARAVILHOSO,  PAI DA ETERNIDADE, PRÍNCIPE DA PAZ. SEU NOME É CONSELHEIRO,  PAI DA ETERNIDADE, PRÍNCIPE DA PAZ SEU NOME É DEUS FORTE,  PAI DA ETERNIDADE, PRÍNCIPE DA PAZ. IREI SEGUI-LO PARA SEMPRE POIS ELE DÁ FELICIDADE SEU NOME É MARAVILHOSO, PAI DA ETERNIDADE, PRÍNCIPE DA PAZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('53', '53 - É BOM, É BOM DEMAIS', 'É BOM, É BOM DEMAIS\nÉ MUITO BOM TER JESUS NO CORAÇÃO.\n\nVIVER COM ELE, PERTINHO DELE,\nÉ MUITO BOM TER JESUS NO CORAÇÃO.\nBIS\nBIS', 'É BOM, É BOM DEMAIS É MUITO BOM TER JESUS NO CORAÇÃO. VIVER COM ELE, PERTINHO DELE, É MUITO BOM TER JESUS NO CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('54', '54 - COMO É BOM SABER', 'COMO É BOM SABER QUE JESUS CRISTO\nFOI MENINO ASSIM COMO EU,\nQUE BRINCAVA E TRABALHAVA\nCOMO UM JOVEM GALILEU.\n\nCORO\nQUE PRAZER CONHECER CRISTO\nQUE TAMBÉM FOI MENINO AQUI\nE SABER QUE NO CÉU LINDO,\nQUER TER-ME JUNTO A SI!\n\nE É MELHOR SABER QUE JESUS CRISTO\nJÁ SUBIU LÁ PARA O CÉU\nE PREPARA UM LUGARZINHO\nDE UM MENINO, ASSIM, COMO EU.\n\nCORO\nQUE PRAZER CONHECER CRISTO\nQUE TAMBÉM FOI MENINO AQUI\nE SABER QUE NO CÉU LINDO,\nQUER TER-ME JUNTO A SI!', 'COMO É BOM SABER QUE JESUS CRISTO FOI MENINO ASSIM COMO EU, QUE BRINCAVA E TRABALHAVA COMO UM JOVEM GALILEU. CORO QUE PRAZER CONHECER CRISTO QUE TAMBÉM FOI MENINO AQUI E SABER QUE NO CÉU LINDO, QUER TER-ME JUNTO A SI! E É MELHOR SABER QUE JESUS CRISTO JÁ SUBIU LÁ PARA O CÉU E PREPARA UM LUGARZINHO DE UM MENINO, ASSIM, COMO EU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('55', '55 - É SOMENTE TEU MEU CORAÇÃO SENHOR', 'É SOMENTE TEU MEU CORAÇÃO, SENHOR,\nÉ SOMENTE TEU MEU CORPO E MEU LOUVOR;\nÉ SOMENTE TEU, MEU PAI, O MEU AMOR,\nSOU SOMENTE TEU, MEU REDENTOR.\n\nQUERO TE AMAR, MEU DEUS, Ó MEU SENHOR,\nQUERO TE ADORAR, PRA SEMPRE, COM ARDOR,\nQUERO A TI ORAR COM FÉ E COM FERVOR,\nSOU SOMENTE TEU, MEU REDENTOR.', 'É SOMENTE TEU MEU CORAÇÃO, SENHOR, É SOMENTE TEU MEU CORPO E MEU LOUVOR; É SOMENTE TEU, MEU PAI, O MEU AMOR, SOU SOMENTE TEU, MEU REDENTOR. QUERO TE AMAR, MEU DEUS, Ó MEU SENHOR, QUERO TE ADORAR, PRA SEMPRE, COM ARDOR, QUERO A TI ORAR COM FÉ E COM FERVOR, SOU SOMENTE TEU, MEU REDENTOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('56', '56 - CRISTO É O SENHOR', 'CRISTO É O SENHOR,DONO DO MEU VIVER,\nELE ME SALVOU, COM SEU SANGUE ME LAVOU;\n\nINSTRUMENTOS\n\nREPETIR O HINO\n\nFINAL: PRA SEMPRE COM ELE VOU ESTAR.', 'CRISTO É O SENHOR,DONO DO MEU VIVER, ELE ME SALVOU, COM SEU SANGUE ME LAVOU; INSTRUMENTOS REPETIR O HINO  PRA SEMPRE COM ELE VOU ESTAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('57', '57 - HÁ UMA LINDA CANÇÃO DE AMOR', 'HÁ UMA LINDA CANÇÃO DE AMOR\nDENTRO DO MEU CORAÇÃO,\nCRISTO ME DEU PLENA PAZ E PERDÃO,\nOH! EU SOU FELIZ!\nOH! EU SOU FELIZ, FELIZ (2x)\nCRISTO ME DEU PLENA PAZ E PERDÃO,\nOH! EU SOU FELIZ!', 'HÁ UMA LINDA CANÇÃO DE AMOR DENTRO DO MEU CORAÇÃO, CRISTO ME DEU PLENA PAZ E PERDÃO, OH! EU SOU FELIZ! OH! EU SOU FELIZ, FELIZ  CRISTO ME DEU PLENA PAZ E PERDÃO, OH! EU SOU FELIZ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('58', '58 - VIVER É TÃO LINDO', 'VIVER É TÃO LINDO QUANDO SE TEM JESUS,\nO MUNDO É TÃO PEQUENO\nPARA O QUE EU TENHO EM MIM;\nE O QUE TENHO\nÉ BEM MAIOR QUE TUDO NA VIDA\nEU TENHO É JESUS\n\nVIVO A CANTAR, VIVO A SORRIR\nTUDO O QUE EU TENHO\nJAMAIS SE ACABARÁ.', 'VIVER É TÃO LINDO QUANDO SE TEM JESUS, O MUNDO É TÃO PEQUENO PARA O QUE EU TENHO EM MIM; E O QUE TENHO É BEM MAIOR QUE TUDO NA VIDA EU TENHO É JESUS VIVO A CANTAR, VIVO A SORRIR TUDO O QUE EU TENHO JAMAIS SE ACABARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('59', '59 - O NOME DO MEU SENHOR', 'O NOME DO MEU SENHOR,\nO QUE VENCEU A MORTE E A CRUZ,\nO ÚNICO QUE É DIGNO DE LOUVOR,\nO SEU NOME É JESUS!\n\nCORO\nÉ FIEL, É A PAZ, É AMOR,                                 É O SALVADOR!\nÉ A ESPERANÇA ETERNA.\n\nO MEU SENHOR DERRAMOU\nO SEU PRECIOSO SANGUE NA CRUZ,\nPERDOOU MEUS PECADOS,\nLAVOU-ME, O SEU NOME É JESUS!\n\nCORO\nÉ FIEL, É A PAZ, É AMOR,                                 É O SALVADOR!\nÉ A ESPERANÇA ETERNA.\n\nAGORA VOU, FELIZ, TE ADORAR, \nPOIS O TEU SANGUE ME LIMPOU.\nCAMINHANDO VOU PARA A PÁTRIA,\nÓ JESUS, VEM ME BUSCAR!\n\nCORO\nÉ FIEL, É A PAZ, É AMOR, É O SALVADOR!\nÉ A ESPERANÇA ETERNA.\nÉ FIEL, É A PAZ, É AMOR, É O SALVADOR!\nÉ A ESPERANÇA ETERNA.\nMINHA ESPERANÇA É JESUS,', 'O NOME DO MEU SENHOR, O QUE VENCEU A MORTE E A CRUZ, O ÚNICO QUE É DIGNO DE LOUVOR, O SEU NOME É JESUS! CORO É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. O MEU SENHOR DERRAMOU O SEU PRECIOSO SANGUE NA CRUZ, PERDOOU MEUS PECADOS, LAVOU-ME, O SEU NOME É JESUS! AGORA VOU, FELIZ, TE ADORAR, POIS O TEU SANGUE ME LIMPOU. CAMINHANDO VOU PARA A PÁTRIA, Ó JESUS, VEM ME BUSCAR! CORO É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. MINHA ESPERANÇA É JESUS,', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('60', '60 - DEUS FALOU COMIGO', 'DEUS FALOU COMIGO,\nDEUS QUER FALAR COM VOCÊ;\nDEUS FALOU COMIGO\nQUE A PORTA DO CÉU É JESUS,\nA PORTA DO CÉU É JESUS,\nA PORTA DO CÉU É JESUS.\nBIS', 'DEUS FALOU COMIGO, DEUS QUER FALAR COM VOCÊ; DEUS FALOU COMIGO QUE A PORTA DO CÉU É JESUS, A PORTA DO CÉU É JESUS, A PORTA DO CÉU É JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('61', '61 - SEI QUE NA CRUZ SEU SANGUE VERTEU', 'SEI QUE NA CRUZ SEU SANGUE VERTEU,\nMORREU ALI MEU SENHOR,\nMAS NO TERCEIRO DIA RESSUCITOU\nPRA ME DAR O CÉU DE AMOR.\n\nEU QUERO JESUS QUE ME SALVA, (3X)\nCOM ELE QUERO IR AO CÉU.\nLÁ NO CÉU, (3X)\nQUERO MORAR COM MEU JESUS.\nLÁ NO CÉU, (3X)\nQUERO MORAR COM MEU JESUS.\n\nSEI QUE NA CRUZ SEU SANGUE VERTEU,\nMORREU ALI MEU SENHOR,\nMAS NO TERCEIRO DIA RESSUCITOU\nPRA ME DAR O CÉU DE AMOR.\n\nEU QUERO JESUS QUE ME SALVA, (3X)\nCOM ELE QUERO IR AO CÉU.\nLÁ NO CÉU, (3X)\nQUERO MORAR COM MEU JESUS.\nLÁ NO CÉU, (3X)\nQUERO MORAR COM MEU JESUS.', 'SEI QUE NA CRUZ SEU SANGUE VERTEU, MORREU ALI MEU SENHOR, MAS NO TERCEIRO DIA RESSUCITOU PRA ME DAR O CÉU DE AMOR. EU QUERO JESUS QUE ME SALVA,  COM ELE QUERO IR AO CÉU. LÁ NO CÉU,  QUERO MORAR COM MEU JESUS. LÁ NO CÉU,  QUERO MORAR COM MEU JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('62', '62 - É TÃO TRISTE NÃO VER A LUZ (O CEGO BARTIMEU)', 'É TÃO TRISTE NÃO VER A LUZ,\nA ESCURIDÃO DESTA VIDA VIVER,\nÀ BEIRA DO CAMINHO \nBARTIMEU VIVIA A SOFRER.\n\nMAS UMA VOZ DE ESPERANÇA\nAOS SEUS OUVIDOS UM DIA CHEGOU,\nA MULTIDÃO O IMPEDIA\nE O CEGO BEM ALTO GRITOU:\n\n- Ó JESUS, FILHO DE DAVI,\nTEM MISERICÓRDIA DE MIM;\nQUERIA VER O SOL NASCER,\nA LUZ DA VIDA RESPLANDECER.\n\nE JESUS TEVE COMPAIXÃO,\nDEU-LHE CURA E REDENÇÃO,\nE FELIZ PELO CAMINHO \nA JESUS SEGUIU.\n\nÉ TÃO TRISTE NÃO VER A LUZ,\nA ESCURIDÃO DESTA VIDA VIVER,\nÀ BEIRA DO CAMINHO \nBARTIMEU VIVIA A SOFRER.\n\nMAS UMA VOZ DE ESPERANÇA\nAOS SEUS OUVIDOS UM DIA CHEGOU,\nA MULTIDÃO O IMPEDIA\nE O CEGO BEM ALTO GRITOU:\n\nÓ JESUS, FILHO DE DAVI,\nTEM MISERICÓRDIA DE MIM;\nQUERIA VER O SOL NASCER,\nA LUZ DA VIDA RESPLANDECER.\n\nE JESUS TEVE COMPAIXÃO,\nDEU-LHE CURA E REDENÇÃO,\nE FELIZ PELO CAMINHO \nA JESUS SEGUIU.', 'É TÃO TRISTE NÃO VER A LUZ, A ESCURIDÃO DESTA VIDA VIVER, À BEIRA DO CAMINHO BARTIMEU VIVIA A SOFRER. MAS UMA VOZ DE ESPERANÇA AOS SEUS OUVIDOS UM DIA CHEGOU, A MULTIDÃO O IMPEDIA E O CEGO BEM ALTO GRITOU: - Ó JESUS, FILHO DE DAVI, TEM MISERICÓRDIA DE MIM; QUERIA VER O SOL NASCER, A LUZ DA VIDA RESPLANDECER. E JESUS TEVE COMPAIXÃO, DEU-LHE CURA E REDENÇÃO, E FELIZ PELO CAMINHO A JESUS SEGUIU. Ó JESUS, FILHO DE DAVI, TEM MISERICÓRDIA DE MIM; QUERIA VER O SOL NASCER, A LUZ DA VIDA RESPLANDECER.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('63', '63 - TEM BOM ÂNIMO, POBRE PECADOR', 'TEM BOM ÂNIMO, POBRE PECADOR,\nJESUS ESTÁ PRESENTE PARA TE SALVAR;\nSALVOU ZAQUEU, O RICO QUE ERA POBRE,\nBUSCOU UM CEGO À BEIRA DO CAMINHO.\n\n(FALANDO): “BARTIMEU,\nQUE QUERES QUE EU TE FAÇA?”\n\nQUE EU VEJA, FOI A RESPOSTA PRONTA;\nVENDO AGORA O ROSTO DE JESUS,\nLOGO O SEGUIU LOUVANDO ALEGREMENTE.\n\nPODES TER TUDO OU NADA POSSUIR,\nJESUS PERGUNTA AGORA:\n“QUE QUERES QUE EU TE FAÇA?”\nRESPONDE DEPRESSA: A TUA SALVAÇÃO;\nVIDA ETERNA JESUS TE DÁ AGORA.', 'TEM BOM ÂNIMO, POBRE PECADOR, JESUS ESTÁ PRESENTE PARA TE SALVAR; SALVOU ZAQUEU, O RICO QUE ERA POBRE, BUSCOU UM CEGO À BEIRA DO CAMINHO. (FALANDO): BARTIMEU, QUE QUERES QUE EU TE FAÇA? QUE EU VEJA, FOI A RESPOSTA PRONTA; VENDO AGORA O ROSTO DE JESUS, LOGO O SEGUIU LOUVANDO ALEGREMENTE. PODES TER TUDO OU NADA POSSUIR, JESUS PERGUNTA AGORA: QUE QUERES QUE EU TE FAÇA? RESPONDE DEPRESSA: A TUA SALVAÇÃO; VIDA ETERNA JESUS TE DÁ AGORA.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('64', '64 - EU VOU CONTAR UMA HISTÓRIA', 'EU VOU CONTAR UMA HISTÓRIA\nPRA VOCÊ NÃO ESQUECER,\nA HISTÓRIA DE UM HOMEM\nQUE A JESUS QUERIA VER.\n\nSUBIU EM UMA ÁRVORE PRA VER \nJESUS PASSAR POIS ERA TÃO PEQUENO, \nNÃO PODIA O AVISTAR.\n\nOLHANDO PARA CIMA JESUS O ENCONTROU,\nCOM SUA VOZ SUAVE, ENTÃO ELE FALOU:\n\n“- ZAQUEU, DESCE DEPRESSA\nPORQUE HOJE ME CONVÉM\nPOUSAR EM TUA CASA,\nVEM DEPRESSA, Ó SIM, VEM”.\n\nZAQUEU ENTÃO DESCEU\nQUANDO JESUS O CONVIDOU,\nJESUS MUDOU SUA VIDA,\nEM OUTRO HOMEM SE TORNOU.', 'EU VOU CONTAR UMA HISTÓRIA PRA VOCÊ NÃO ESQUECER, A HISTÓRIA DE UM HOMEM QUE A JESUS QUERIA VER. SUBIU EM UMA ÁRVORE PRA VER JESUS PASSAR POIS ERA TÃO PEQUENO, NÃO PODIA O AVISTAR. OLHANDO PARA CIMA JESUS O ENCONTROU, COM SUA VOZ SUAVE, ENTÃO ELE FALOU: - ZAQUEU, DESCE DEPRESSA PORQUE HOJE ME CONVÉM POUSAR EM TUA CASA, VEM DEPRESSA, Ó SIM, VEM. ZAQUEU ENTÃO DESCEU QUANDO JESUS O CONVIDOU, JESUS MUDOU SUA VIDA, EM OUTRO HOMEM SE TORNOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('65', '65 - ONDE NASCEU JESUS?', 'ONDE NASCEU JESUS?\nEU LOGO TE RESPONDO:\nEM BELÉM O MEU JESUS NASCEU.\n\nQUEM É SEU PAI?\nEU LOGO TE RESPONDO:\nDE DEUS É FILHO\nE ELE TAMBÉM É DEUS.\n\nEM QUE CIDADE CRESCEU?\nEU LOGO TE RESPONDO:\nEM NAZARÉ\nO MEU JESUS CRESCEU.\n\nQUEM O BATIZOU?\nEU LOGO TE RESPONDO:\nFOI JOÃO BATISTA\nNO RIO JORDÃO.\n\nE DIGO MAIS:\nELE ESTÁ VIVO,\nFALA CONOSCO,\nNOS TRAZENDO A PAZ.', 'ONDE NASCEU JESUS? EU LOGO TE RESPONDO: EM BELÉM O MEU JESUS NASCEU. QUEM É SEU PAI? EU LOGO TE RESPONDO: DE DEUS É FILHO E ELE TAMBÉM É DEUS. EM QUE CIDADE CRESCEU? EU LOGO TE RESPONDO: EM NAZARÉ O MEU JESUS CRESCEU. QUEM O BATIZOU? EU LOGO TE RESPONDO: FOI JOÃO BATISTA NO RIO JORDÃO. E DIGO MAIS: ELE ESTÁ VIVO, FALA CONOSCO, NOS TRAZENDO A PAZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('66', '66 - NASCEU UM NOVO DIA', 'NASCEU UM NOVO DIA;\nBRILHOU A LUZ DO SEU AMOR;\nA PAZ VEIO ENTÃO REINAR\nQUANDO JESUS EM MIM HABITOU.\nBIS', 'NASCEU UM NOVO DIA; BRILHOU A LUZ DO SEU AMOR; A PAZ VEIO ENTÃO REINAR QUANDO JESUS EM MIM HABITOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('67', '67 - MARAVILHA É O AMOR DE DEUS', 'MARAVILHA, MARAVILHA, \nMARAVILHA É O AMOR DE DEUS,                                       REVELADO NO CALVÁRIO, \nJESUS POR NÓS MORREU.\n\nMARAVILHA É ANUNCIAR ESSE  AMOR \nPRA VOCÊ QUE HOJE ESTÁ AQUI; \nVEM CONHECÊ-LO, VEM RECEBÊ-LO,    \nE A ALEGRIA INUNDARÁ TEU SER.\n\nMARAVILHA, MARAVILHA, MARAVILHA\nÉ A SALVAÇÃO. NÃO MORREREMOS! \nSIM, ESTAREMOS ETERNAMENTE \nCOM O NOSSO DEUS!\nBIS', 'MARAVILHA, MARAVILHA, MARAVILHA É O AMOR DE DEUS, REVELADO NO CALVÁRIO, JESUS POR NÓS MORREU. MARAVILHA É ANUNCIAR ESSE AMOR PRA VOCÊ QUE HOJE ESTÁ AQUI; VEM CONHECÊ-LO, VEM RECEBÊ-LO, E A ALEGRIA INUNDARÁ TEU SER. MARAVILHA, MARAVILHA, MARAVILHA É A SALVAÇÃO. NÃO MORREREMOS! SIM, ESTAREMOS ETERNAMENTE COM O NOSSO DEUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('68', '68 - VEM PARA O MEIO', 'VEM PARA O MEIO \nDE UM POVO QUE SABE  \nO CAMINHO DA ETERNIDADE,\nONDE A PROFECIA                                 APONTA  A DIREÇÃO;  \nESSE POVO SEGUE E \nALCANÇA  A SALVAÇÃO.\n\nVEM PARA O MEIO, \nJESUS ESTÁ AQUI,              \nMERGULHA NESSAS ÁGUAS \nDA SALVAÇÃO;\nESSAS ÁGUAS CURAM                            E NOVA CRIATURA SERÁS,            ENTÃO, PARA O LOUVOR DE DEUS.\n\nVEM PARA O MEIO \nDE UM POVO QUE SABE  \nO CAMINHO DA ETERNIDADE,\nONDE A PROFECIA                                 APONTA  A DIREÇÃO;  \nESSE POVO SEGUE E \nALCANÇA  A SALVAÇÃO.\n\nVEM PARA O MEIO, \nJESUS ESTÁ AQUI,              \nMERGULHA NESSAS ÁGUAS \nDA SALVAÇÃO;\nESSAS ÁGUAS CURAM                            E NOVA CRIATURA SERÁS,            ENTÃO, PARA O LOUVOR DE DEUS.', 'VEM PARA O MEIO DE UM POVO QUE SABE O CAMINHO DA ETERNIDADE, ONDE A PROFECIA APONTA A DIREÇÃO; ESSE POVO SEGUE E ALCANÇA A SALVAÇÃO. VEM PARA O MEIO, JESUS ESTÁ AQUI, MERGULHA NESSAS ÁGUAS DA SALVAÇÃO; ESSAS ÁGUAS CURAM E NOVA CRIATURA SERÁS, ENTÃO, PARA O LOUVOR DE DEUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('69', '69 - VOCÊ SABE O QUE É SALVAÇÃO?', 'VOCÊ SABE O QUE É SALVAÇÃO?\nÉ SENTIR UMA GRANDE ALEGRIA\nQUE O MUNDO NÃO CONHECE,\nNELE HÁ TREVAS E SOLIDÃO.\n\nJESUS NOS DEU SALVAÇÃO\nQUANDO LÁ NA CRUZ MORREU,\nLEVOU OS NOSSOS PECADOS,\nLIBERTOS AGORA ESTAMOS.\n\nDÊ A ELE O SEU LOUVOR,\nUMA VIDA DE GRATIDÃO;\nVENHA AGORA E ENTREGUE\nA JESUS SEU CORAÇÃO.\n\nVENHA AGORA E ENTREGUE\nA JESUS SEU CORAÇÃO.', 'VOCÊ SABE O QUE É SALVAÇÃO? É SENTIR UMA GRANDE ALEGRIA QUE O MUNDO NÃO CONHECE, NELE HÁ TREVAS E SOLIDÃO. JESUS NOS DEU SALVAÇÃO QUANDO LÁ NA CRUZ MORREU, LEVOU OS NOSSOS PECADOS, LIBERTOS AGORA ESTAMOS. DÊ A ELE O SEU LOUVOR, UMA VIDA DE GRATIDÃO; VENHA AGORA E ENTREGUE A JESUS SEU CORAÇÃO. VENHA AGORA E ENTREGUE A JESUS SEU CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('70', '70 - O DIA DO SENHOR VIRÁ', 'O DIA DO SENHOR VIRÁ  \nCOM JUÍZO SOBRE TODO MAL;\n\nVEM DEPRESSA,         \nVEM DEPRESSA,                         \nGRANDE BÊNÇÃO\nESTÁ A  TE  ESPERAR.\nBIS\n\nO SOL DA JUSTIÇA NASCERÁ,    \nSALVAÇÃO TRARÁ EM SUAS  ASAS;\n\nVEM DEPRESSA,         \nVEM DEPRESSA,                         \nGRANDE BÊNÇÃO\nESTÁ A  TE  ESPERAR.\n\nO SOL DA JUSTIÇA NASCERÁ,    SALVAÇÃO E LIBERTAÇÃO;\n\nSAIREIS E ANUNCIAREIS,                 \nEM SUAS ASAS \n‘INDA  HÁ LUGAR.\nBIS\n\nVEM DEPRESSA,         \nVEM DEPRESSA,                         \nEM SUAS ASAS \nTE ABRIGAR\n\nVEM DEPRESSA,         \nVEM DEPRESSA,                         \nEM SUAS ASAS \nTE ABRIGAR', 'O DIA DO SENHOR VIRÁ COM JUÍZO SOBRE TODO MAL; VEM DEPRESSA, VEM DEPRESSA, GRANDE BÊNÇÃO ESTÁ A TE ESPERAR. O SOL DA JUSTIÇA NASCERÁ, SALVAÇÃO TRARÁ EM SUAS ASAS; VEM DEPRESSA, VEM DEPRESSA, GRANDE BÊNÇÃO ESTÁ A TE ESPERAR. O SOL DA JUSTIÇA NASCERÁ, SALVAÇÃO E LIBERTAÇÃO; SAIREIS E ANUNCIAREIS, EM SUAS ASAS ‘INDA HÁ LUGAR. VEM DEPRESSA, VEM DEPRESSA, EM SUAS ASAS TE ABRIGAR VEM DEPRESSA, VEM DEPRESSA, EM SUAS ASAS TE ABRIGAR', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('71', '71 - JESUS VIU A MULTIDÃO', 'JESUS VIU A MULTIDÃO\nE NÃO OS DEIXOU IR,\nESTAVAM TÃO FAMINTOS,\nCOMO PODIAM PARTIR?\n\nENTÃO ELE ORDENOU:\n“DAI-LHES DE COMER”,\nOS DISCÍPULOS PERGUNTARAM: \nSENHOR, O QUE VAMOS FAZER?\n\nINSTRUMENTOS\n\nCINCO PÃES E DOIS PEIXINHOS  \nCINCO MIL ALIMENTOU,\nASSENTADOS SOBRE A RELVA,\nTODO O POVO SE FARTOU.\n\nJESUS É A SOLUÇÃO,\nELE É A PROVISÃO,\nO FILHO DO DEUS VIVO\nFAZ A MULTIPLICAÇÃO.', 'JESUS VIU A MULTIDÃO E NÃO OS DEIXOU IR, ESTAVAM TÃO FAMINTOS, COMO PODIAM PARTIR? ENTÃO ELE ORDENOU: DAI-LHES DE COMER, OS DISCÍPULOS PERGUNTARAM: SENHOR, O QUE VAMOS FAZER? INSTRUMENTOS CINCO PÃES E DOIS PEIXINHOS CINCO MIL ALIMENTOU, ASSENTADOS SOBRE A RELVA, TODO O POVO SE FARTOU. JESUS É A SOLUÇÃO, ELE É A PROVISÃO, O FILHO DO DEUS VIVO FAZ A MULTIPLICAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('72', '72 - TU ÉS O CRISTO, O FILHO DO DEUS VIVO', 'TU ÉS O CRISTO, \nO FILHO DO DEUS VIVO,\nNOSSO SALVADOR, \nJESUS NOME DOCE;\n\nMORRESTE NA CRUZ PRA NOS SALVAR,\nRESSUSCITASTE E NO CÉU ESTÁS,\nVOLTARÁS E NOS LEVARÁS\nPARA CONTIGO HABITAR.\n\nTU ÉS O CRISTO, \nO FILHO DO DEUS VIVO,\nNOSSO SALVADOR, \nJESUS NOME DOCE;\n\nMORRESTE NA CRUZ PRA NOS SALVAR,\nRESSUSCITASTE E NO CÉU ESTÁS,\nVOLTARÁS E NOS LEVARÁS\nPARA CONTIGO HABITAR.', 'TU ÉS O CRISTO, O FILHO DO DEUS VIVO, NOSSO SALVADOR, JESUS NOME DOCE; MORRESTE NA CRUZ PRA NOS SALVAR, RESSUSCITASTE E NO CÉU ESTÁS, VOLTARÁS E NOS LEVARÁS PARA CONTIGO HABITAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', '.', '73 - DE ETERNIDADE A ETERNIDADE\n\nDE ETERNIDADE A ETERNIDADEINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('74', '74 - AQUILO QUE MELHOR EXISTE', 'AQUILO QUE MELHOR EXISTE\nÉ FALAR DE JESUS.\nELE É O CAMINHO E A VIDA,\nA VERDADE E A LUZ.\nBIS\n\nJESUS, AO APROXIMAR-SE, \nJOÃO BATISTA APONTOU:\n“EIS O CORDEIRO DE DEUS, \n QUE VEIO AO MUNDO\n NOS DAR SALVAÇÃO”.\n\nOS DISCÍPULOS, DESANIMADOS\nPOR PESCAREM EM VÃO,\nJESUS OS MANDA \nDE VOLTA AO MAR\nE MUITOS PEIXES PESCAR.\n\nALEGRES DESCOBRIRAM:\n“O CORDEIRO DE DEUS\nESTÁ VIVO ENTRE NÓS!”\nREINA PRA SEMPRE \nO MEU SALVADOR.', 'AQUILO QUE MELHOR EXISTE É FALAR DE JESUS. ELE É O CAMINHO E A VIDA, A VERDADE E A LUZ. JESUS, AO APROXIMAR-SE, JOÃO BATISTA APONTOU: EIS O CORDEIRO DE DEUS, QUE VEIO AO MUNDO NOS DAR SALVAÇÃO. OS DISCÍPULOS, DESANIMADOS POR PESCAREM EM VÃO, JESUS OS MANDA DE VOLTA AO MAR E MUITOS PEIXES PESCAR. ALEGRES DESCOBRIRAM: O CORDEIRO DE DEUS ESTÁ VIVO ENTRE NÓS! REINA PRA SEMPRE O MEU SALVADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('75', '75 - VOCÊ SABE QUEM FOI JOÃO  BATISTA?', 'VOCÊ SABE QUEM FOI JOÃO BATISTA? \nFOI UM PROFETA DE DEUS.INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('76', '76 - JESUS FALOU QUE O CÉU É PRA MIM', 'JESUS FALOU QUE  \nO CÉU É PRA MIM (3X)\nJESUS FALOU QUE  \nO CÉU É PRA MIM, \nO CÉU É PRA MIM, \nEU VOU COM ELE SIM.\n\nCORO  \nE SE VOCÊ QUISER O CÉU TAMBÉM PRECISA ACEITAR \nJESUS EM SEU CORAÇÃO,\nPARA PODER LHE TRANSFORMAR\nE O CÉU LHE DAR \nE LÁ VOCÊ MORAR.\n\nEU SOU FIEL E  \nJESUS ME QUER BEM, (3X)\nPOR ISSO EU SEI QUE \nO CÉU É PRA MIM,\nO CÉU É PRA MIM. \nEU VOU PRA GLÓRIA, SIM.\n\nCORO  \nE SE VOCÊ QUISER O CÉU TAMBÉM PRECISA ACEITAR \nJESUS EM SEU CORAÇÃO,\nPARA PODER LHE TRANSFORMAR\nE O CÉU LHE DAR \nE LÁ VOCÊ MORAR.', 'JESUS FALOU QUE O CÉU É PRA MIM  JESUS FALOU QUE O CÉU É PRA MIM, O CÉU É PRA MIM, EU VOU COM ELE SIM. CORO E SE VOCÊ QUISER O CÉU TAMBÉM PRECISA ACEITAR JESUS EM SEU CORAÇÃO, PARA PODER LHE TRANSFORMAR E O CÉU LHE DAR E LÁ VOCÊ MORAR. EU SOU FIEL E JESUS ME QUER BEM,  POR ISSO EU SEI QUE O CÉU É PRA MIM, O CÉU É PRA MIM. EU VOU PRA GLÓRIA, SIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('77', '77 - VOU CONTAR-VOS DE JESUS', 'VOU CONTAR-VOS DE JESUS\nO QUE JOÃO NOS ENSINOU:\nELE, POR AMOR, SE FEZ \nNOSSO SALVADOR.\n\nDE ALUNOS QUE CHAMOU:\nPEDRO, ANDRÉ, TIAGO E JOÃO,\nFILIPE, BARTOLOMEU E\nMATEUS, TIAGO, SIMÃO, TOMÉ,\nJUDAS, O BOM AMIGO,\nOUTRO JUDAS, O TRAIDOR.\n\nJESUS A TODOS CHAMOU: \n“EU SOU CAMINHO, \nA VERDADE E A LUZ”,\nA ELES ENSINOU.\n\nVOU CONTAR-VOS DE JESUS\nO QUE JOÃO NOS ENSINOU:\nELE, POR AMOR, SE FEZ \nNOSSO SALVADOR.\n\nDE ALUNOS QUE CHAMOU:\nPEDRO, ANDRÉ, TIAGO E JOÃO,\nFILIPE, BARTOLOMEU E\nMATEUS, TIAGO, SIMÃO, TOMÉ,\nJUDAS, O BOM AMIGO,\nOUTRO JUDAS, O TRAIDOR.\n\nJESUS A TODOS CHAMOU: \n“EU SOU CAMINHO, \nA VERDADE E A LUZ”,\nA ELES ENSINOU.', 'VOU CONTAR-VOS DE JESUS O QUE JOÃO NOS ENSINOU: ELE, POR AMOR, SE FEZ NOSSO SALVADOR. DE ALUNOS QUE CHAMOU: PEDRO, ANDRÉ, TIAGO E JOÃO, FILIPE, BARTOLOMEU E MATEUS, TIAGO, SIMÃO, TOMÉ, JUDAS, O BOM AMIGO, OUTRO JUDAS, O TRAIDOR. JESUS A TODOS CHAMOU: EU SOU CAMINHO, A VERDADE E A LUZ, A ELES ENSINOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('78', '78 - O SENHOR É A MINHA LUZ', 'O SENHOR É A MINHA LUZ\nE A MINHA SALVAÇÃO.\nA QUEM EU TEMEREI?INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('79', '79 - DEUS AMA VOCÊ', 'DEUS AMA VOCÊ\nE ELE QUER TE SALVAR,\nELE VAI OPERAR \nEM SEU CORAÇÃO.\n\nA VIDA QUE UM DIAINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('80', '80 - EXISTE UM LUGAR', 'EXISTE UM LUGAR\nQUE MEU JESUS FOI PREPARAR.\nSE VOCÊ QUISER CHEGAR LÁ,\nÉ SÓ A ELE SE ENTREGAR.\n\nESSE LUGAR É O CÉU DE LUZ,\nONDE HABITA O MEU JESUS.\nSE QUERES PAZ, ALEGRIA E PERDÃO\nENTREGA A ELE O TEU CORAÇÃO.', 'EXISTE UM LUGAR QUE MEU JESUS FOI PREPARAR. SE VOCÊ QUISER CHEGAR LÁ, É SÓ A ELE SE ENTREGAR. ESSE LUGAR É O CÉU DE LUZ, ONDE HABITA O MEU JESUS. SE QUERES PAZ, ALEGRIA E PERDÃO ENTREGA A ELE O TEU CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('81', '81 - QUANDO OUVI JESUS ME CHAMAR', 'QUANDO OUVI JESUS ME CHAMARINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('82', '82 - JESUS VEM NOS BUSCAR', 'JESUS VEM NOS BUSCAR,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('83', '83 - FOI NO CAMINHO PARA DAMASCO', 'FOI NO CAMINHO PARA DAMASCO \nQUE O GRANDE SAULO, PERSEGUIDOR,\nVIU GLORIOSA LUZ CELESTE,  \nQUE POR TERRA O DERRUBOU.\n\nDESSA LUZ SURGIU A VOZ:\nSAULO, SAULO,  \nQUE ME PERSEGUES.\nELE, CAÍDO, RESPONDEU:\nQUEM ÉS, SENHOR? \nQUEM ÉS, SENHOR?\n\nINSTRUMENTOS\n\nEU SOU JESUS, QUE TU PERSEGUES.\nQUE QUERES TU, SENHOR, QUE EU FAÇA?\nLEVANTA-TE E OBEDECE.\nNOVO CAMINHO JESUS TE DÁ.\n\nQUERO TAMBÉM SER DE JESUS,\nTER ESTA LUZ, ME ILUMINAR.\nE A CADA PASSO OUVIR SUA VOZ,    \nATÉ O DIA QUE AO CÉU CHEGAR.', 'FOI NO CAMINHO PARA DAMASCO QUE O GRANDE SAULO, PERSEGUIDOR, VIU GLORIOSA LUZ CELESTE, QUE POR TERRA O DERRUBOU. DESSA LUZ SURGIU A VOZ: SAULO, SAULO, QUE ME PERSEGUES. ELE, CAÍDO, RESPONDEU: QUEM ÉS, SENHOR? QUEM ÉS, SENHOR? INSTRUMENTOS EU SOU JESUS, QUE TU PERSEGUES. QUE QUERES TU, SENHOR, QUE EU FAÇA? LEVANTA-TE E OBEDECE. NOVO CAMINHO JESUS TE DÁ. QUERO TAMBÉM SER DE JESUS, TER ESTA LUZ, ME ILUMINAR. E A CADA PASSO OUVIR SUA VOZ, ATÉ O DIA QUE AO CÉU CHEGAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('84', '84 - QUERES SER FELIZ?', 'QUERES SER FELIZ, CONHECER JESUS?\nELE VAI TE DAR ALEGRIA, VIDA E PAZ.\n\nEU JÁ SOU FELIZ,  \nDEI MEU CORAÇÃO \nE JESUS EM MIM \nVEIO HABITAR.\n\nQUERES SER FELIZ, CONHECER JESUS?\nELE VAI TE DAR ALEGRIA, VIDA E PAZ.\n\nEU JÁ SOU FELIZ, DEI MEU CORAÇÃO \nE JESUS EM MIM VEIO HABITAR.', 'QUERES SER FELIZ, CONHECER JESUS? ELE VAI TE DAR ALEGRIA, VIDA E PAZ. EU JÁ SOU FELIZ, DEI MEU CORAÇÃO E JESUS EM MIM VEIO HABITAR. QUERES SER FELIZ, CONHECER JESUS? ELE VAI TE DAR ALEGRIA, VIDA E PAZ. EU JÁ SOU FELIZ, DEI MEU CORAÇÃO E JESUS EM MIM VEIO HABITAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('85', '85 - NESTE DIA ESPECIAL', 'Neste dia especial, \nVou lhe apresentar Jesus,\nQue dissipa todo o mal e faz\nRaiar em nós sua luz.\n\nCORO\nJESUS É BOM AMIGO, \nABRIGO NO TEMPORAL,\nQUE NOS LIVRA DO PERIGO \nE POR NÓS VENCEU O MAL.\n\nABRA o seu coração                         e venha conhecer jesus,\nCom amor e segurança À\nVida eterna O conduz\n\nCORO\nJESUS É BOM AMIGO, \nABRIGO NO TEMPORAL,\nQUE NOS LIVRA DO PERIGO \nE POR NÓS VENCEU O MAL. \n\nFINAL: SIM, POR NÓS VENCEU O MAL.', 'NESTE DIA ESPECIAL, VOU LHE APRESENTAR JESUS, QUE DISSIPA TODO O MAL E FAZ RAIAR EM NÓS SUA LUZ. CORO JESUS É BOM AMIGO, ABRIGO NO TEMPORAL, QUE NOS LIVRA DO PERIGO E POR NÓS VENCEU O MAL. ABRA O SEU CORAÇÃO E VENHA CONHECER JESUS, COM AMOR E SEGURANÇA À VIDA ETERNA O CONDUZ CORO JESUS É BOM AMIGO, ABRIGO NO TEMPORAL, QUE NOS LIVRA DO PERIGO E POR NÓS VENCEU O MAL.  SIM, POR NÓS VENCEU O MAL.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('86', '86 - UM DIA MORREU O MEU JESUS', 'UM DIA MORREU O MEU JESUS\nNAQUELA CRUZ POR MIM,\nO SANGUE DERRAMADO \nLIMPOU OS MEUS PECADOS.\nEU VOU CANTAR, \nPORQUE JESUS ESTÁ AQUI.\n\nCORO\nAGORA MEU JESUS VOU                     ADORAR, EU VOU CANTAR. \nEU VOU LOUVAR COMO DAVI,\nAO DERROTAR \nOS QUE QUERIAM O ATINGIR.\n      \nINSTRUMENTOS\n\nJESUS, EU QUERO \nMINHA VIDA TE ENTREGAR \nE QUERO MAIS \nA TUA OBRA REALIZAR,\nSERVI-LO SEMPRE \nDE TODO O CORAÇÃO\nE SEMPRE EU CANTAREI \nESTA CANÇÃO.\n\nCORO (2X)\nAGORA MEU JESUS VOU                     ADORAR, EU VOU CANTAR. \nEU VOU LOUVAR COMO DAVI,\nAO DERROTAR OS QUE \nQUERIAM O ATINGIR.', 'UM DIA MORREU O MEU JESUS NAQUELA CRUZ POR MIM, O SANGUE DERRAMADO LIMPOU OS MEUS PECADOS. EU VOU CANTAR, PORQUE JESUS ESTÁ AQUI. CORO AGORA MEU JESUS VOU ADORAR, EU VOU CANTAR. EU VOU LOUVAR COMO DAVI, AO DERROTAR OS QUE QUERIAM O ATINGIR. INSTRUMENTOS JESUS, EU QUERO MINHA VIDA TE ENTREGAR E QUERO MAIS A TUA OBRA REALIZAR, SERVI-LO SEMPRE DE TODO O CORAÇÃO E SEMPRE EU CANTAREI ESTA CANÇÃO. AGORA MEU JESUS VOU ADORAR, EU VOU CANTAR. EU VOU LOUVAR COMO DAVI, AO DERROTAR OS QUE QUERIAM O ATINGIR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('87', '87 - O SENHOR É O BOM PASTOR', 'O SENHOR É O BOM PASTOR,\nELE É O SALVADOR,\nO DEUS DE PAZ E DE ALEGRIA,\nO DEUS DE AMOR.\n\nCORO\nELE VEM ME BUSCAR \nE VAI ME LEVAR\nPARA A CIDADE CELESTIAL.\nVOCÊ TAMBÉM PODE IR,\nÉ SÓ ANDAR NO CAMINHO \nDE CRISTO JESUS.\n\nELE CURA E SALVA,\nFALA AOS CORAÇÕES,\nCUIDA DOS SEUS FILHOS\nCOM MUITO AMOR.\n\nCORO\nELE VEM ME BUSCAR \nE VAI ME LEVAR\nPARA A CIDADE CELESTIAL.\nVOCÊ TAMBÉM PODE IR,\nÉ SÓ ANDAR NO CAMINHO \nDE CRISTO JESUS.\n\nCORO\nELE VEM ME BUSCAR \nE VAI ME LEVAR\nPARA A CIDADE CELESTIAL.\nVOCÊ TAMBÉM PODE IR,\nÉ SÓ ANDAR NO CAMINHO \nDE CRISTO JESUS.\nNO CAMINHO DE CRISTO JESUS.', 'O SENHOR É O BOM PASTOR, ELE É O SALVADOR, O DEUS DE PAZ E DE ALEGRIA, O DEUS DE AMOR. CORO ELE VEM ME BUSCAR E VAI ME LEVAR PARA A CIDADE CELESTIAL. VOCÊ TAMBÉM PODE IR, É SÓ ANDAR NO CAMINHO DE CRISTO JESUS. ELE CURA E SALVA, FALA AOS CORAÇÕES, CUIDA DOS SEUS FILHOS COM MUITO AMOR. CORO ELE VEM ME BUSCAR E VAI ME LEVAR PARA A CIDADE CELESTIAL. VOCÊ TAMBÉM PODE IR, É SÓ ANDAR NO CAMINHO DE CRISTO JESUS. NO CAMINHO DE CRISTO JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('88', '88 - VOU CONTAR PARA VOCÊ UMA HISTÓRIA ESPECIAL', 'VOU CONTAR PARA VOCÊ\nUMA HISTÓRIA ESPECIAL:\nÉ A HISTÓRIA DE JESUS,\nNOSSO AMIGO LEAL.\n\nELE VEIO COM A MISSÃO\nDE NOS DAR A SALVAÇÃO,\nPARA TANTO HOMEM SE FEZ,\nDEIXOU O CÉU DE ESPLENDOR,\nVEIO À TERRA DEMONSTRAR\nDE DEUS O INFINITO AMOR\n\nPADECEU EM UMA CRUZ\nE MORREU PARA NOS LIMPAR,\nNOSSO PECADO LAVAR\nCOM SEU SANGUE REMIDOR,\nE VIVO ELE ESTÁ!\n\nNO TERCEIRO DIA \nRESSUSCITOU,\nÀ DIREITA DE DEUS PAI\nROGA PELO PECADOR.\n\nBREVE, BREVE VOLTARÁ\nPARA A IGREJA ARREBATAR\nE JUNTOS VAMOS MORAR\nNO CELESTE LAR DE AMOR.\n\nSE VOCÊ TAMBÉM QUISER\nNESTE LAR DE AMOR MORAR\nBASTA ABRIR SEU CORAÇÃO\nE DEIXAR JESUS ENTRAR.\n\nFINAL:\nBASTA ABRIR SEU CORAÇÃO\nE DEIXAR JESUS ENTRAR.', 'VOU CONTAR PARA VOCÊ UMA HISTÓRIA ESPECIAL: É A HISTÓRIA DE JESUS, NOSSO AMIGO LEAL. ELE VEIO COM A MISSÃO DE NOS DAR A SALVAÇÃO, PARA TANTO HOMEM SE FEZ, DEIXOU O CÉU DE ESPLENDOR, VEIO À TERRA DEMONSTRAR DE DEUS O INFINITO AMOR PADECEU EM UMA CRUZ E MORREU PARA NOS LIMPAR, NOSSO PECADO LAVAR COM SEU SANGUE REMIDOR, E VIVO ELE ESTÁ! NO TERCEIRO DIA RESSUSCITOU, À DIREITA DE DEUS PAI ROGA PELO PECADOR. BREVE, BREVE VOLTARÁ PARA A IGREJA ARREBATAR E JUNTOS VAMOS MORAR NO CELESTE LAR DE AMOR. SE VOCÊ TAMBÉM QUISER NESTE LAR DE AMOR MORAR BASTA ABRIR SEU CORAÇÃO E DEIXAR JESUS ENTRAR.  BASTA ABRIR SEU CORAÇÃO E DEIXAR JESUS ENTRAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('89', '89 - CERTO HOMEM, PRÍNCIPE  DOS JUDEUS (NICODEMOS)', 'CERTO HOMEM, \nPRÍNCIPE DOS JUDEUS,\nFOI AO MESTRE \nPERGUNTAR-LHE ASSIM:\n\nBEM SABEMOS QUE \nÉS MESTRE VINDO DE DEUS. \nCOMO PODE ALGUÉM \nNASCER DE NOVO?\n\nAQUELE QUE NÃO NASCER\nDA ÁGUA E DO ESPÍRITO \nNÃO PODE VER O REINO DE DEUS. \nMAS AQUELE QUE PERMANECER \nFIEL ATÉ O FIM, O VERÁ.', 'CERTO HOMEM, PRÍNCIPE DOS JUDEUS, FOI AO MESTRE PERGUNTAR-LHE ASSIM: BEM SABEMOS QUE ÉS MESTRE VINDO DE DEUS. COMO PODE ALGUÉM NASCER DE NOVO? AQUELE QUE NÃO NASCER DA ÁGUA E DO ESPÍRITO NÃO PODE VER O REINO DE DEUS. MAS AQUELE QUE PERMANECER FIEL ATÉ O FIM, O VERÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('90', '90 - ELE MORREU MAS RESSURGIU', 'ELE MORREU, MAS RESSURGIU; \nPLENA PAZ ENCHEU O MEU VIVER; \nJESUS É TUDO O QUE SEMPRE QUIS, \nJESUS ME TROUXE O SEU PERDÃO.\n\nQUERO FALAR DESTA SALVAÇÃO, \nPOR ONDE QUER QUE EU POSSA ANDAR, \nJESUS É TUDO, ELE É A PAZ. \nQUE VOCÊ PODE ENCONTRAR (2X)\n\nINSTRUMENTOS\n\nCRISTO, O SENHOR, É QUEM ME CONDUZ, \nLIVRA-ME DO MAL E DA TENTAÇÃO. \nE O SEU AMOR SEMPRE TRADUZ \nVIDA REAL E O CÉU PRA MIM!\n\nCRISTO JESUS É O MEU SENHOR\nSEMPRE PRESENTE NO MEU CORAÇÃO.\nBREVE VIRÁ DO CÉU ME BUSCAR. \nQUE ALEGRIA SERÁ! (2X)', 'ELE MORREU, MAS RESSURGIU; PLENA PAZ ENCHEU O MEU VIVER; JESUS É TUDO O QUE SEMPRE QUIS, JESUS ME TROUXE O SEU PERDÃO. QUERO FALAR DESTA SALVAÇÃO, POR ONDE QUER QUE EU POSSA ANDAR, JESUS É TUDO, ELE É A PAZ. QUE VOCÊ PODE ENCONTRAR  INSTRUMENTOS CRISTO, O SENHOR, É QUEM ME CONDUZ, LIVRA-ME DO MAL E DA TENTAÇÃO. E O SEU AMOR SEMPRE TRADUZ VIDA REAL E O CÉU PRA MIM! CRISTO JESUS É O MEU SENHOR SEMPRE PRESENTE NO MEU CORAÇÃO. BREVE VIRÁ DO CÉU ME BUSCAR. QUE ALEGRIA SERÁ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('91', '91 - TENHO ALGO PRA VOCÊ', 'TENHO ALGO PRA VOCÊ NESTA CANÇÃO:\nTEM ALGUÉM QUE MORA EM MEU CORAÇÃO.\nESTE ALGUÉM DESEJA DAR A VOCÊ O PERDÃO.\n\nESTE ALGUÉM É MUITO \nIMPORTANTE!\nESTÁ BEM PERTO DE VOCÊ \nA TODO INSTANTE.\nELE SÓ POR NÓS MORREU \nNUMA CRUZ. ESTE ALGUÉM, \nLHE DIGO, É: JESUS', 'TENHO ALGO PRA VOCÊ NESTA CANÇÃO: TEM ALGUÉM QUE MORA EM MEU CORAÇÃO. ESTE ALGUÉM DESEJA DAR A VOCÊ O PERDÃO. ESTE ALGUÉM É MUITO IMPORTANTE! ESTÁ BEM PERTO DE VOCÊ A TODO INSTANTE. ELE SÓ POR NÓS MORREU NUMA CRUZ. ESTE ALGUÉM, LHE DIGO, É: JESUS', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('92', '92 - JESUS CURA E SALVA', 'JESUS CURA E SALVA,\nJESUS É O MEU SALVADOR;\nJESUS É MEU AMIGO,\nE ELE É O MEU REDENTOR.\n\nQUANDO ESTOU DOENTE,                  CURA ELE DÁ,                         \nQUANDO ESTOU TRISTE,                        A PAZ VEM REINAR!\n\nJESUS É FORÇA \nE ESPERANÇA! \nJESUS É O PRÍNCIPE\nDA PAZ!', 'JESUS CURA E SALVA, JESUS É O MEU SALVADOR; JESUS É MEU AMIGO, E ELE É O MEU REDENTOR. QUANDO ESTOU DOENTE, CURA ELE DÁ, QUANDO ESTOU TRISTE, A PAZ VEM REINAR! JESUS É FORÇA E ESPERANÇA! JESUS É O PRÍNCIPE DA PAZ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('93', '93 - JESUS TE AMA', 'JESUS TE AMA,\nELE QUER TE SALVAR,\nDEIXA A LUZ ENTRAR                   \nEM TEU CORAÇÃO.\n\nELE QUER TRANSFORMAR \nTEU CHORO EM ALEGRIA,\nELE QUER TE DAR PAZ, \nGOZO E ESPERANÇA.\n\nJESUS TE AMA \n(JESUS TE AMA),\nELE QUER TE SALVAR\n(ELE QUER TE SALVAR),\nDEIXA A LUZ \n(DEIXA A LUZ)\nENTRAR EM TEU CORAÇÃO.\n\nELE QUER TRANSFORMAR \nTEU CHORO EM ALEGRIA,\nELE QUER TE DAR PAZ, \nGOZO E ESPERANÇA.\n\nSE TU QUISERES\n(SE TU QUISERES)\nACEITAR A CRISTO\n(ACEITAR A CRISTO),\nABRE HOJE MESMO                \n(ABRE HOJE MESMO)\nO SEU CORAÇÃO!\n\nJESUS TE CHAMA!\nJESUS TE AMA!\nJESUS TE AMA!', 'JESUS TE AMA, ELE QUER TE SALVAR, DEIXA A LUZ ENTRAR EM TEU CORAÇÃO. ELE QUER TRANSFORMAR TEU CHORO EM ALEGRIA, ELE QUER TE DAR PAZ, GOZO E ESPERANÇA. JESUS TE AMA (JESUS TE AMA), ELE QUER TE SALVAR (ELE QUER TE SALVAR), DEIXA A LUZ (DEIXA A LUZ) ENTRAR EM TEU CORAÇÃO. SE TU QUISERES (SE TU QUISERES) ACEITAR A CRISTO (ACEITAR A CRISTO), ABRE HOJE MESMO (ABRE HOJE MESMO) O SEU CORAÇÃO! JESUS TE CHAMA! JESUS TE AMA! JESUS TE AMA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('94', '94 - AQUELE QUE TEM SEDE', 'AQUELE QUE TEM SEDE \nVÁ À FONTE E BEBA,\nSUAS ÁGUAS DESSEDENTAM\nE TRAZEM VIDA.\n\nRIOS DE ÁGUAS CORRERÃO\nNO SEU INTERIOR, INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('95', '95 - DO PÃO DO CÉU', 'DO PÃO DO CÉU ME NUTRIREI,\nDA FONTE ETERNA BEBEREI;\n“E QUEM BEBER”, \nNOS DIZ JESUS,\n“JAMAIS DE SEDE SOFRERÁ.”\n\nNÃO MAIS SEDE? \nQUE DIZEIS? \nNÃO MAIS SEDE \nASSIM SERÁS!\nBIS\n\n“E QUEM BEBER,” \nNOS DIZ JESUS\n“JAMAIS DE SEDE SOFRERÁ.”\nSEDE NÃO TERÁ!', 'DO PÃO DO CÉU ME NUTRIREI, DA FONTE ETERNA BEBEREI; E QUEM BEBER, NOS DIZ JESUS, JAMAIS DE SEDE SOFRERÁ. NÃO MAIS SEDE? QUE DIZEIS? NÃO MAIS SEDE ASSIM SERÁS! E QUEM BEBER, NOS DIZ JESUS JAMAIS DE SEDE SOFRERÁ. SEDE NÃO TERÁ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
