INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('98', '98 - EU ABRI MEU CORAÇÃO', 'EU ABRI MEU CORAÇÃO\nE JESUS VEIO HABITAR;\nDE MANSO BATEU\nLOGO A PORTA ABRI EU,\nE ELE VEIO EM MIM MORAR.\n\nEU ABRI MEU CORAÇÃO\nE JESUS VEIO HABITAR;\nDE MANSO BATEU\nLOGO A PORTA ABRI EU,\nE ELE VEIO EM MIM MORAR.', 'EU ABRI MEU CORAÇÃO E JESUS VEIO HABITAR; DE MANSO BATEU LOGO A PORTA ABRI EU, E ELE VEIO EM MIM MORAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('99', '99 - NO MEU CORAÇÃO', 'NO MEU CORAÇÃO\nNO MEU CORAÇÃO\nVEM HOJE ENTRAR, Ó CRISTO!\nVEM HOJE ENTRAR,                                SIM, VEM MORAR\nNO MEU CORAÇÃO, Ó CRISTO.', 'NO MEU CORAÇÃO NO MEU CORAÇÃO VEM HOJE ENTRAR, Ó CRISTO! VEM HOJE ENTRAR, SIM, VEM MORAR NO MEU CORAÇÃO, Ó CRISTO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('100', '100 - CRISTO SENTE AMOR POR MIM', 'CRISTO SENTE AMOR POR MIM,\nCOM CERTEZA EU CREIO ASSIM,\nPOR AMOR DE MIM MORREU,\nVIVO ESTÁ, POR MIM, NO CÉU.\n\nCORO\nCRISTO NOS AMA, \nCRISTO NOS CHAMA, \nCRISTO PROCLAMA\nTÃO GRANDE SALVAÇÃO.\n\nCRISTO TEM POR MIM AMOR\nE SE FEZ MEU SALVADOR,\nHOJE AINDA TEM PRAZER\nEM MENINOS RECEBER.\n\nCORO\nCRISTO NOS AMA, \nCRISTO NOS CHAMA, \nCRISTO PROCLAMA\nTÃO GRANDE SALVAÇÃO.\n\nPOR GOZAR SEU RICO AMOR\nHEI DE AMAR MEU SALVADOR,\nE DESEJO AQUI MOSTRAR\nSEU AMOR TÃO EXEMPLAR.\n\nCORO\nCRISTO NOS AMA, \nCRISTO NOS CHAMA, \nCRISTO PROCLAMA\nTÃO GRANDE SALVAÇÃO.', 'CRISTO SENTE AMOR POR MIM, COM CERTEZA EU CREIO ASSIM, POR AMOR DE MIM MORREU, VIVO ESTÁ, POR MIM, NO CÉU. CORO CRISTO NOS AMA, CRISTO NOS CHAMA, CRISTO PROCLAMA TÃO GRANDE SALVAÇÃO. CRISTO TEM POR MIM AMOR E SE FEZ MEU SALVADOR, HOJE AINDA TEM PRAZER EM MENINOS RECEBER. CORO CRISTO NOS AMA, CRISTO NOS CHAMA, CRISTO PROCLAMA TÃO GRANDE SALVAÇÃO. POR GOZAR SEU RICO AMOR HEI DE AMAR MEU SALVADOR, E DESEJO AQUI MOSTRAR SEU AMOR TÃO EXEMPLAR. CORO CRISTO NOS AMA, CRISTO NOS CHAMA, CRISTO PROCLAMA TÃO GRANDE SALVAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('101', '101 - JESUS ME AMA', 'JESUS ME AMA E QUER SALVAR-ME,\nJESUS ME AMA E QUER-ME BEM.\n\nCORO\nPOR ISSO EU CANTO,\nSEMPRE ELE AMA,\nJESUS ME AMA, \nMEU SALVADOR.\n\nESTIVE PRESO NO VIL PECADO,\nESTIVE PRESO, MAS ME SALVOU.\n\nCORO\nPOR ISSO EU CANTO,\nSEMPRE ELE AMA,\nJESUS ME AMA, \nMEU SALVADOR.\n\nCASTIGO DURO QUE EU MEREÇO,\nJESUS SOFREU JÁ EM MEU LUGAR.\n\nCORO\nPOR ISSO EU CANTO,\nSEMPRE ELE AMA,\nJESUS ME AMA, \nMEU SALVADOR.\n\nAGORA, QUERONA MINHA VIDA,\nAO MEU AMIGO SEMPRE AGRADAR.\n\nCORO\nPOR ISSO EU CANTO,\nSEMPRE ELE AMA,\nJESUS ME AMA, \nMEU SALVADOR.', 'JESUS ME AMA E QUER SALVAR-ME, JESUS ME AMA E QUER-ME BEM. CORO POR ISSO EU CANTO, SEMPRE ELE AMA, JESUS ME AMA, MEU SALVADOR. ESTIVE PRESO NO VIL PECADO, ESTIVE PRESO, MAS ME SALVOU. CORO POR ISSO EU CANTO, SEMPRE ELE AMA, JESUS ME AMA, MEU SALVADOR. CASTIGO DURO QUE EU MEREÇO, JESUS SOFREU JÁ EM MEU LUGAR. CORO POR ISSO EU CANTO, SEMPRE ELE AMA, JESUS ME AMA, MEU SALVADOR. AGORA, QUERONA MINHA VIDA, AO MEU AMIGO SEMPRE AGRADAR. CORO POR ISSO EU CANTO, SEMPRE ELE AMA, JESUS ME AMA, MEU SALVADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('102', '102 - DAS MARAVILHAS QUE A BÍBLIA CONTÉM', 'DAS MARAVILHAS \nQUE A BÍBLIA CONTÉM,\nEIS A MAIS LINDA:\nJESUS ME QUER BEM;\n\nCHEGO-ME A ELE, \nNINGUÉM ME DETÉM,\nQUANDO ME LEMBRO \nDE QUE ME QUER BEM.\n\nOH! QUE PRAZER! \nJESUS ME QUER BEM,\nSIM, ME QUER BEM, \nA TI TAMBÉM;\nOH! QUE PRAZER! \nJESUS ME QUER BEM,\nSIM, ME QUER BEM, JESUS.', 'DAS MARAVILHAS QUE A BÍBLIA CONTÉM, EIS A MAIS LINDA: JESUS ME QUER BEM; CHEGO-ME A ELE, NINGUÉM ME DETÉM, QUANDO ME LEMBRO DE QUE ME QUER BEM. OH! QUE PRAZER! JESUS ME QUER BEM, SIM, ME QUER BEM, A TI TAMBÉM; OH! QUE PRAZER! JESUS ME QUER BEM, SIM, ME QUER BEM, JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('103', '103 - VINDE, MENINOS', 'VINDE, MENINOS, \nVINDE A JESUS!\nELE GANHOU-VOS \nBÊNÇÃOS NA CRUZ,\nOS PEQUENINOS ELE CONDUZ,\nÓ VINDE AO SALVADOR!\n\nCORO\nQUE ALEGRIA! \nSEM PECADO OU MAL\nREUNIR-NOS TODOS, AFINAL,\nNA SANTA PÁTRIA CELESTIAL\nCOM NOSSO SALVADOR.\n\nJÁ SEM DEMORA,\nHOJE CONVÉM\nIR CAMINHANDO À  \nGLÓRIA ALÉM;\nJESUS VOS CHAMA, \nQUER VOSSO BEM,\nÓ VINDE AO SALVADOR!\n\nCORO\nQUE ALEGRIA! \nSEM PECADO OU MAL\nREUNIR-NOS TODOS, AFINAL,\nNA SANTA PÁTRIA CELESTIAL\nCOM NOSSO SALVADOR.\n\nCRISTO VOS AMA, \nBEM ELE O DIZ,\nQUER RECEBER-VOS\nNO BOM PAÍS;\nQUER CONCEDER-VOS\nVIDA FELIZ,\nÓ VINDE AO SALVADOR!\n\nCORO\nQUE ALEGRIA! \nSEM PECADO OU MAL\nREUNIR-NOS TODOS, AFINAL,\nNA SANTA PÁTRIA CELESTIAL\nCOM NOSSO SALVADOR.', 'VINDE, MENINOS, VINDE A JESUS! ELE GANHOU-VOS BÊNÇÃOS NA CRUZ, OS PEQUENINOS ELE CONDUZ, Ó VINDE AO SALVADOR! CORO QUE ALEGRIA! SEM PECADO OU MAL REUNIR-NOS TODOS, AFINAL, NA SANTA PÁTRIA CELESTIAL COM NOSSO SALVADOR. JÁ SEM DEMORA, HOJE CONVÉM IR CAMINHANDO À GLÓRIA ALÉM; JESUS VOS CHAMA, QUER VOSSO BEM, Ó VINDE AO SALVADOR! CRISTO VOS AMA, BEM ELE O DIZ, QUER RECEBER-VOS NO BOM PAÍS; QUER CONCEDER-VOS VIDA FELIZ, Ó VINDE AO SALVADOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('104', '104 - NO CAMINHO ELE ME GUIARÁ', 'NO CAMINHO ELE ME GUIARÁ,\nELE QUER BEM\nA TODO PECADOR,\nO SEU AMOR POR MIM, \nVOCÊ OUVIU?\nDEUS GOSTA DE VOCÊ.\n\nCORO\nDEUS GOSTA DE VOCÊ, (BIS)\nO SEU AMOR VOCÊ VERÁ\nPOR TODA A PARTE QUE OLHAR;\nDEUS GOSTA DE VOCÊ.\n\nO SOL DOURADO\nELE FAZ NASCER,\nAS LINDAS FLORES\nELE FAZ ABRIR,\nQUANDO O DESERTO\nCHORA A CHUVA CAI,\nDEUS GOSTA DE VOCÊ.\n\nCORO\nDEUS GOSTA DE VOCÊ, (BIS)\nO SEU AMOR VOCÊ VERÁ\nPOR TODA A PARTE QUE OLHAR;\nDEUS GOSTA DE VOCÊ.', 'NO CAMINHO ELE ME GUIARÁ, ELE QUER BEM A TODO PECADOR, O SEU AMOR POR MIM, VOCÊ OUVIU? DEUS GOSTA DE VOCÊ. CORO DEUS GOSTA DE VOCÊ,  O SEU AMOR VOCÊ VERÁ POR TODA A PARTE QUE OLHAR; DEUS GOSTA DE VOCÊ. O SOL DOURADO ELE FAZ NASCER, AS LINDAS FLORES ELE FAZ ABRIR, QUANDO O DESERTO CHORA A CHUVA CAI, DEUS GOSTA DE VOCÊ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('105', '105 - HAVEREMOS DE CHEGAR', 'HAVEREMOS DE CHEGAR\nPELO PODER DO SENHOR EM JERUSALÉM;\nONDE A FELICIDADE PARA SEMPRE REINARÁ, EM JERUSALÉM.\n\nCORO\nALELUIA, CANTAREMOS,\nLOUVANDO AO SALVADOR\nEM JERUSALÉM,\nPOIS ALI JAMAIS OS MALES\nPODERÃO NOS ALCANÇAR,\nNA FELIZ JERUSALÉM.\n\nINSTRUMENTOS\n\nQUERES TU TAMBÉM ENTRAR \nCOM OS SALVOS POR JESUS EM JERUSALÉM?\nÉ PRECISO, HOJE MESMO, ACEITAR ESSA LUZ,\nCAMINHANDO A JERUSALÉM.\n\nCORO\nALELUIA, CANTAREMOS,\nLOUVANDO AO SALVADOR\nEM JERUSALÉM,\nPOIS ALI JAMAIS OS MALES\nPODERÃO NOS ALCANÇAR,\nNA FELIZ JERUSALÉM. \nNA FELIZ JERUSALÉM. \nNA FELIZ JERUSALÉM.', 'HAVEREMOS DE CHEGAR PELO PODER DO SENHOR EM JERUSALÉM; ONDE A FELICIDADE PARA SEMPRE REINARÁ, EM JERUSALÉM. CORO ALELUIA, CANTAREMOS, LOUVANDO AO SALVADOR EM JERUSALÉM, POIS ALI JAMAIS OS MALES PODERÃO NOS ALCANÇAR, NA FELIZ JERUSALÉM. INSTRUMENTOS QUERES TU TAMBÉM ENTRAR COM OS SALVOS POR JESUS EM JERUSALÉM? É PRECISO, HOJE MESMO, ACEITAR ESSA LUZ, CAMINHANDO A JERUSALÉM. CORO ALELUIA, CANTAREMOS, LOUVANDO AO SALVADOR EM JERUSALÉM, POIS ALI JAMAIS OS MALES PODERÃO NOS ALCANÇAR, NA FELIZ JERUSALÉM. NA FELIZ JERUSALÉM. NA FELIZ JERUSALÉM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('106', '106 - JESUS CRISTO É O CAMINHO', 'JESUS CRISTO É O CAMINHO\nE O GUIA DE VALOR;\nELE SALVA TODO AQUELE\nQUE CONFIA EM SEU AMOR.\n\nJESUS CRISTO É O CAMINHO,\nJESUS CRISTO ME SALVOU,\nEU FUI SALVO PELA GRAÇA \nQUE EM MIM DEPOSITOU. \nEU FUI SALVO PELA GRAÇA \nQUE EM MIM DEPOSITOU.', 'JESUS CRISTO É O CAMINHO E O GUIA DE VALOR; ELE SALVA TODO AQUELE QUE CONFIA EM SEU AMOR. JESUS CRISTO É O CAMINHO, JESUS CRISTO ME SALVOU, EU FUI SALVO PELA GRAÇA QUE EM MIM DEPOSITOU. EU FUI SALVO PELA GRAÇA QUE EM MIM DEPOSITOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('107', '107 - EU QUERO CANTAR', 'EU QUERO CANTAR, \nEU QUERO FALAR\nQUE JESUS SALVA E CURA,\nQUE JESUS DEU-ME PAZ.\n\nREPETIR O HINO', 'EU QUERO CANTAR, EU QUERO FALAR QUE JESUS SALVA E CURA, QUE JESUS DEU-ME PAZ. REPETIR O HINO', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('108', '108 - HOJE É UM GRANDE DIA', 'HOJE É UM GRANDE DIA, É DIA DE LOUVAR;\nLOUVEMOS A JESUS QUE ESTÁ NESTE LUGAR;\nHOJE É DIA DE VITÓRIA E VITÓRIA \nELE NOS DÁ; RECEBA JESUS, \nELE QUER TE ABENÇOAR.\n\nCORO\nVENHAM TODOS JÁ,\nA JESUS VAMOS LOUVAR!\nACEITEM O SALVADOR \nQUE VIDA ETERNA \nQUER LHES DAR!\n\nHOJE AQUI ESTOU PARA TE LOUVAR\nPOR TANTAS MARAVILHAS, \nPOR SEMPRE ME AMAR.\nTUA OBRA EM MINHA VIDA, \nVOU ANUNCIAR, OBRA DE ALEGRIA \nE ALEGRIA QUE É SEM PAR!\n\nCORO\nVENHAM TODOS JÁ,\nA JESUS VAMOS LOUVAR!\nACEITEM O SALVADOR \nQUE VIDA ETERNA \nQUER LHES DAR!', 'HOJE É UM GRANDE DIA, É DIA DE LOUVAR; LOUVEMOS A JESUS QUE ESTÁ NESTE LUGAR; HOJE É DIA DE VITÓRIA E VITÓRIA ELE NOS DÁ; RECEBA JESUS, ELE QUER TE ABENÇOAR. CORO VENHAM TODOS JÁ, A JESUS VAMOS LOUVAR! ACEITEM O SALVADOR QUE VIDA ETERNA QUER LHES DAR! HOJE AQUI ESTOU PARA TE LOUVAR POR TANTAS MARAVILHAS, POR SEMPRE ME AMAR. TUA OBRA EM MINHA VIDA, VOU ANUNCIAR, OBRA DE ALEGRIA E ALEGRIA QUE É SEM PAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('109', '109 - CANTA ALEGREMENTE, Ó FILHA DE SIÃO', 'CANTA ALEGREMENTE, \nÓ FILHA DE SIÃO;\nREJUBILA, Ó ISRAEL:\nREGOZIJA-TE E EXULTA \nDE TODO O CORAÇÃO,\nÓ FILHA DE JERUSALÉM.\n\nO SENHOR AFASTOU OS TEUS JUÍZOS,\nEXTERMINOU O TEU INIMIGO;\nO SENHOR, O REI DE ISRAEL,\nESTÁ NO MEIO DE TI;\nTU NÃO VERÁS MAIS MAL ALGUM.\n\nNAQUELE DIA SE DIRÁ \n A JERUSALÉM: NÃO TEMAS, \n Ó SIÃO, NÃO ENFRAQUEÇAM \n AS TUAS MÃOS.\n\nO SENHOR TEU DEUS \nESTÁ NO MEIO DE TI, \nPODEROSO PARA TE SALVAR; \nSE DELEITARÁ EM TI \nCOM ALEGRIA;\n\nCALAR-SE-Á POR SEU AMOR,\nREGOZIJAR-SE-Á  EM TI COM JÚBILO,\nÓ FILHA DE JERUSALÉM (2X)', 'CANTA ALEGREMENTE, Ó FILHA DE SIÃO; REJUBILA, Ó ISRAEL: REGOZIJA-TE E EXULTA DE TODO O CORAÇÃO, Ó FILHA DE JERUSALÉM. O SENHOR AFASTOU OS TEUS JUÍZOS, EXTERMINOU O TEU INIMIGO; O SENHOR, O REI DE ISRAEL, ESTÁ NO MEIO DE TI; TU NÃO VERÁS MAIS MAL ALGUM. NAQUELE DIA SE DIRÁ A JERUSALÉM: NÃO TEMAS, Ó SIÃO, NÃO ENFRAQUEÇAM AS TUAS MÃOS. O SENHOR TEU DEUS ESTÁ NO MEIO DE TI, PODEROSO PARA TE SALVAR; SE DELEITARÁ EM TI COM ALEGRIA; CALAR-SE-Á POR SEU AMOR, REGOZIJAR-SE-Á EM TI COM JÚBILO, Ó FILHA DE JERUSALÉM', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
