INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('112', '112 - CONSOLA-ME, ESPÍRITO SANTO', 'CONSOLA-ME, \nESPÍRITO SANTO,\nPARA QUE EU POSSA \nAGUARDAR COM FÉ\nA VINDA DO FILHO \nDO DEUS DE ABRAÃO;\n\nCONSOLA-ME, \nCONSOLA-ME,\nCONSOLA-ME, SENHOR.\n\nCONSOLA-ME, \nESPÍRITO SANTO,\nPARA QUE EU POSSA \nAGUARDAR COM FÉ\nA VINDA DO FILHO \nDO DEUS DE ABRAÃO;\nCONSOLA-ME, CONSOLA-ME,\nCONSOLA-ME, SENHOR.', 'CONSOLA-ME, ESPÍRITO SANTO, PARA QUE EU POSSA AGUARDAR COM FÉ A VINDA DO FILHO DO DEUS DE ABRAÃO; CONSOLA-ME, CONSOLA-ME, CONSOLA-ME, SENHOR. CONSOLA-ME, ESPÍRITO SANTO, PARA QUE EU POSSA AGUARDAR COM FÉ A VINDA DO FILHO DO DEUS DE ABRAÃO; CONSOLA-ME, CONSOLA-ME, CONSOLA-ME, SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('113', '113 - TUA MÃO É PODEROSA', 'TUA MÃO É PODEROSA\nE O TEU NOME DOCE AMOR,\nNÃO TE AFASTARÁS DE MIM,\nNEM CONTENDES CONTRA MIM,\nANTES CUIDARÁS, \nSENHOR, DE MIM.\n\nTUA MÃO É PODEROSA\nE O TEU NOME DOCE AMOR,\nNÃO TE AFASTARÁS DE MIM,\nNEM CONTENDES CONTRA MIM,\nANTES CUIDARÁS,\nSENHOR, DE MIM.', 'TUA MÃO É PODEROSA E O TEU NOME DOCE AMOR, NÃO TE AFASTARÁS DE MIM, NEM CONTENDES CONTRA MIM, ANTES CUIDARÁS, SENHOR, DE MIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('114', '114 - O SENHOR É QUEM CUIDA DE MIM', 'O SENHOR É QUEM \nCUIDA DE MIM,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('115', '115 - JESUS, O BOM PASTOR', 'JESUS, O BOM PASTOR,\nCUIDA DE MIM \nCOM MUITO AMOR.\nA PASTOS VERDEJANTES\nSEMPRE LEVA-ME O SENHOR\n\nE A ÁGUAS TÃO TRANQUILAS MANSAMENTE ME CONDUZ.\nNAS VEREDAS DA JUSTIÇA,\nVIVEREI NA LUZ.\n\nSE MESMO O PERIGO \nTIVER QUE ENFRENTAR, \nSEI QUE MEU JESUS \nCOMIGO SEMPRE ESTARÁ.\nMEDO NÃO TEREI, \nPOIS SINTO SUA MÃO EM MIM.\nCOM O CAJADO DO SENHOR CONSOLO TENHO SIM!\n\nMESA ABUNDANTE HEI DE TER;\nÓLEO DO ESPÍRITO EM MEU VIVER;\nCÁLICE TRANSBORDANTE,\nVIDA ABUNDANTE,\nCOM A MISERICÓRDIA DO SENHOR.\n\nE POR LONGOS DIAS VIVEREI\nEM SUA CASA ETERNA HABITAREI.  \nEM SUA CASA ETERNA HABITAREI.', 'JESUS, O BOM PASTOR, CUIDA DE MIM COM MUITO AMOR. A PASTOS VERDEJANTES SEMPRE LEVA-ME O SENHOR E A ÁGUAS TÃO TRANQUILAS MANSAMENTE ME CONDUZ. NAS VEREDAS DA JUSTIÇA, VIVEREI NA LUZ. SE MESMO O PERIGO TIVER QUE ENFRENTAR, SEI QUE MEU JESUS COMIGO SEMPRE ESTARÁ. MEDO NÃO TEREI, POIS SINTO SUA MÃO EM MIM. COM O CAJADO DO SENHOR CONSOLO TENHO SIM! MESA ABUNDANTE HEI DE TER; ÓLEO DO ESPÍRITO EM MEU VIVER; CÁLICE TRANSBORDANTE, VIDA ABUNDANTE, COM A MISERICÓRDIA DO SENHOR. E POR LONGOS DIAS VIVEREI EM SUA CASA ETERNA HABITAREI. EM SUA CASA ETERNA HABITAREI.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('116', '116 - SOBRE AS ONDAS', 'SOBRE AS ONDAS \nPODEREI ANDAR.\nJESUS ESTÁ COMIGO, \nNENHUM MAL ME ATINGIRÁ.\nSOPRA O VENTO, DEIXA SOPRAR\nJESUS ACALMA AS ONDAS\nE A PAZ VOLTA A REINAR.\n\nAS ONDAS VÃO SUBINDO,\nFORMANDO UM MONTÃO,\nESTE MUNDO MAU QUER\nNAUFRAGAR MEU CORAÇÃO.\nMAS NÃO TEMO, \nOLHO PARA JESUS.\nAO SOM DA SUA VOZ\nATÉ AS TREVAS VIRAM LUZ.', 'SOBRE AS ONDAS PODEREI ANDAR. JESUS ESTÁ COMIGO, NENHUM MAL ME ATINGIRÁ. SOPRA O VENTO, DEIXA SOPRAR JESUS ACALMA AS ONDAS E A PAZ VOLTA A REINAR. AS ONDAS VÃO SUBINDO, FORMANDO UM MONTÃO, ESTE MUNDO MAU QUER NAUFRAGAR MEU CORAÇÃO. MAS NÃO TEMO, OLHO PARA JESUS. AO SOM DA SUA VOZ ATÉ AS TREVAS VIRAM LUZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('117', '117 - DEITADA NA RELVA', 'DEITADA NA RELVA \nUMA FELIZ OVELHA\nJUNTO AO PASTOR \nESTÁ A DESCANSAR,\n\nNAS ÁGUAS TRANQUILAS\nSACIA SUA SEDE,\nDA RELVA BEM VERDE\nPODE SE ALIMENTAR.\n\nCORO\nOVELHA, JAMAIS \nDEIXE ESTE APRISCO. \nTENS O BOM PASTOR \nPARA DE TI CUIDAR.\nNO MUNDO LÁ FORA \nHÁ TRISTEZA E GUERRA, \nJESUS É A ALEGRIA, \nVERDADE E PAZ.\n\nE QUANDO AS TREVAS \nCAEM SOBRE O MUNDO\nO TENTADOR VEM \nDISPOSTO A TRAGAR.\nJESUS EM SEUS BRAÇOS, \nOVELHA, TE ACOLHE,\nPARA QUE TU POSSAS \nA VIDA HERDAR.\n\nCORO\nOVELHA, JAMAIS \nDEIXE ESTE APRISCO. \nTENS O BOM PASTOR \nPARA DE TI CUIDAR.\nNO MUNDO LÁ FORA \nHÁ TRISTEZA E GUERRA, \nJESUS É A ALEGRIA, \nVERDADE E PAZ.', 'DEITADA NA RELVA UMA FELIZ OVELHA JUNTO AO PASTOR ESTÁ A DESCANSAR, NAS ÁGUAS TRANQUILAS SACIA SUA SEDE, DA RELVA BEM VERDE PODE SE ALIMENTAR. CORO OVELHA, JAMAIS DEIXE ESTE APRISCO. TENS O BOM PASTOR PARA DE TI CUIDAR. NO MUNDO LÁ FORA HÁ TRISTEZA E GUERRA, JESUS É A ALEGRIA, VERDADE E PAZ. E QUANDO AS TREVAS CAEM SOBRE O MUNDO O TENTADOR VEM DISPOSTO A TRAGAR. JESUS EM SEUS BRAÇOS, OVELHA, TE ACOLHE, PARA QUE TU POSSAS A VIDA HERDAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('118', '118 - ÂNCORA SEGURA', 'UMA ÂNCORA SEGURA \nTENHO NO SENHOR,\nQUEM NELE CONFIOU\nJAMAIS PERDIDO SE ACHOU.\n\nEU CONFIO NO SENHOR,\nSEGURO NELE ESTOU,\nSEI DE FATOS GRANDIOSOS,\nRESPOSTAS DO SENHOR:\n\nELIAS PEDIU A DEUS,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('119', '119 - JESUS, O MELHOR AMIGO', 'JESUS, O MELHOR AMIGO,\nQUE SEMPRE ESTÁ PRESENTE.\nNELE HÁ PODER \nPARA MUDAR O CRENTE.\n\nCORO (2X)                                           \nMESMO EM LUTAS E AFLIÇÕES                                                  ELE RESPONDE ÀS NOSSAS ORAÇÕES.\n\nJESUS TEM PODER.\nMESMO EM GUERRAS E BATALHAS\nVOCÊ VAI VENCER, \nPORQUE JESUS NÃO FALHA.\n\nSE VOCÊ QUISER UMA BÊNÇÃO,\nBASTA SOMENTE CRER,\nÉ SÓ PEDIR A ELE,\nQUE VOCÊ VAI RECEBER.\n\nCORO (2X)     \nMESMO EM LUTAS E AFLIÇÕES                                                  ELE RESPONDE ÀS NOSSAS ORAÇÕES.', 'JESUS, O MELHOR AMIGO, QUE SEMPRE ESTÁ PRESENTE. NELE HÁ PODER PARA MUDAR O CRENTE. MESMO EM LUTAS E AFLIÇÕES ELE RESPONDE ÀS NOSSAS ORAÇÕES. JESUS TEM PODER. MESMO EM GUERRAS E BATALHAS VOCÊ VAI VENCER, PORQUE JESUS NÃO FALHA. SE VOCÊ QUISER UMA BÊNÇÃO, BASTA SOMENTE CRER, É SÓ PEDIR A ELE, QUE VOCÊ VAI RECEBER.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('120', '120 - PODERÁ CANTAR', 'CORO\nPODERÁ CANTAR \nQUEM NA ROCHA ESTIVER,\nPODERÁ CANTAR, \nPOIS COM JESUS \nFIRMADO ESTARÁ.\n\nESPEREI PELO SENHOR COM PACIÊNCIA\nE ME FIRMOU OS PASSOS.\nDE ESCORREGAR LIVROU-ME,\nDEU-ME UM NOVO CANTO.\n\nCORO\nPODERÁ CANTAR \nQUEM NA ROCHA ESTIVER,\nPODERÁ CANTAR, \nPOIS COM JESUS \nFIRMADO ESTARÁ.\n\nO CORAÇÃO FIRME EM CRISTO\nPOR VENDAVAIS NÃO VACILARÁ.\nNÃO EM AREIA, MAS NA ROCHA,\nOUVI SUA VOZ, QUE SEGURANÇA!\n\nCORO\nPODERÁ CANTAR \nQUEM NA ROCHA ESTIVER,\nPODERÁ CANTAR, \nPOIS COM JESUS \nFIRMADO ESTARÁ.', 'CORO PODERÁ CANTAR QUEM NA ROCHA ESTIVER, PODERÁ CANTAR, POIS COM JESUS FIRMADO ESTARÁ. ESPEREI PELO SENHOR COM PACIÊNCIA E ME FIRMOU OS PASSOS. DE ESCORREGAR LIVROU-ME, DEU-ME UM NOVO CANTO. O CORAÇÃO FIRME EM CRISTO POR VENDAVAIS NÃO VACILARÁ. NÃO EM AREIA, MAS NA ROCHA, OUVI SUA VOZ, QUE SEGURANÇA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('121', '121 - MEU BARQUINHO', 'MEU BARQUINHO \nNAVEGA NO MAR,\nMUI FELIZ VIVO \nSEMPRE A CANTAR,\n\nGRANDES ONDAS\nVÊM PARA O NAUFRAGAR,\nNÃO TENHO MEDO, \nVOU A ÂNCORA LANÇAR.\n\nCORO\nVOU A JESUS, VOU A ÂNCORA LANÇAR.\nVOU A JESUS, POR SEU SANGUE VOU CLAMAR\nE FELIZ VOU AO PORTO CHEGAR.\nALELUIA! ALELUIA É O MEU CANTAR!\n\nMEU JESUS É AMIGO FIEL,\nPOR SUA MÃO ME CONDUZ ATÉ AO CÉU\nE SE UM DIA VIER A AFLIÇÃO,\nNÃO TENHO MEDO, \nPOIS É ELE O CAPITÃO.\n\nCORO\nVOU A JESUS, VOU A ÂNCORA LANÇAR.\nVOU A JESUS, POR SEU SANGUE VOU CLAMAR\nE FELIZ VOU AO PORTO CHEGAR.\nALELUIA! ALELUIA É O MEU CANTAR!', 'MEU BARQUINHO NAVEGA NO MAR, MUI FELIZ VIVO SEMPRE A CANTAR, GRANDES ONDAS VÊM PARA O NAUFRAGAR, NÃO TENHO MEDO, VOU A ÂNCORA LANÇAR. CORO VOU A JESUS, VOU A ÂNCORA LANÇAR. VOU A JESUS, POR SEU SANGUE VOU CLAMAR E FELIZ VOU AO PORTO CHEGAR. ALELUIA! ALELUIA É O MEU CANTAR! MEU JESUS É AMIGO FIEL, POR SUA MÃO ME CONDUZ ATÉ AO CÉU E SE UM DIA VIER A AFLIÇÃO, NÃO TENHO MEDO, POIS É ELE O CAPITÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('122', '122 - CRISTO É MEU AMIGO', 'CRISTO É MEU AMIGO,\nELE ESTÁ COMIGO.\nALELUIA, ALELUIA, \nALELUIA, ALELUIA,\nELE ESTÁ COMIGO.\n\nCRISTO É TEU AMIGO,\nELE ESTÁ CONTIGO.\nALELUIA, ALELUIA, \nALELUIA, ALELUIA,\nELE ESTÁ CONTIGO.', 'CRISTO É MEU AMIGO, ELE ESTÁ COMIGO. ALELUIA, ALELUIA, ALELUIA, ALELUIA, ELE ESTÁ COMIGO. CRISTO É TEU AMIGO, ELE ESTÁ CONTIGO. ALELUIA, ALELUIA, ALELUIA, ALELUIA, ELE ESTÁ CONTIGO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('123', '123 - NESTA BATALHA NÃO TEREIS QUE PELEJAR', 'NESTA BATALHA NÃO TEREIS QUE PELEJAR;\nAGUARDAI, FICAI PARADOS,\nE VEDE A SALVAÇÃO DO SENHOR \nPARA CONVOSCO, Ó JUDÁ E JERUSALÉM.\n\nNÃO TEMAIS, NEM VOS ASSUSTEIS;\nAMANHÃ SAÍ-LHES AO ENCONTRO,\nPORQUE O SENHOR SERÁ CONVOSCO.\nNESTA BATALHA NÃO TEREIS QUE PELEJAR.', 'NESTA BATALHA NÃO TEREIS QUE PELEJAR; AGUARDAI, FICAI PARADOS, E VEDE A SALVAÇÃO DO SENHOR PARA CONVOSCO, Ó JUDÁ E JERUSALÉM. NÃO TEMAIS, NEM VOS ASSUSTEIS; AMANHÃ SAÍ-LHES AO ENCONTRO, PORQUE O SENHOR SERÁ CONVOSCO. NESTA BATALHA NÃO TEREIS QUE PELEJAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('124', '124 - O CÉU E A TERRA PASSARÃO', 'O CÉU E A TERRA PASSARÃO\nMAS AS MINHAS PALAVRAS \nNÃO HÃO DE PASSAR.\n\nEU SOU DEUS, SOU FIEL,\nNÃO TEMAIS, NEM VOS ASSUSTEIS!\nPROCLAMAI A VITÓRIA,\nRENOVAI A VOSSA FÉ,\nE O VOSSO ÂNIMO PORQUE\nA TERRA PROMETIDA VOS DAREI. (BIS)\n\n125 - COM JESUS VENCEDOR SEREI\n\nCOM JESUS VENCEDOR SEREI! \nCOM JESUS VENCEDOR SEREI!  \nVENCEDOR, VENCEDOR,\nVENCEDOR SEREI!\nCOM JESUS VENCEDOR SEREI!\n\nNAS BATALHAS ELE ME ACOMPANHA. \nNAS BATALHAS ELE ME ACOMPANHA.\nMEU JESUS, MEU JESUS,\nSEMPRE ME ACOMPANHA.\nCOM JESUS VENCEDOR SEREI!\nCOM JESUS VENCEDOR SEREI!\n\nELE É O MEU SALVADOR.\nELE É O MEU SALVADOR. \nSALVADOR, SALVADOR                           \nÉ O MEU JESUS! \nCOM JESUS VENCEDOR SEREI!', 'O CÉU E A TERRA PASSARÃO MAS AS MINHAS PALAVRAS NÃO HÃO DE PASSAR. EU SOU DEUS, SOU FIEL, NÃO TEMAIS, NEM VOS ASSUSTEIS! PROCLAMAI A VITÓRIA, RENOVAI A VOSSA FÉ, E O VOSSO ÂNIMO PORQUE A TERRA PROMETIDA VOS DAREI. 125 - COM JESUS VENCEDOR SEREI COM JESUS VENCEDOR SEREI! COM JESUS VENCEDOR SEREI! VENCEDOR, VENCEDOR, VENCEDOR SEREI! COM JESUS VENCEDOR SEREI! NAS BATALHAS ELE ME ACOMPANHA. NAS BATALHAS ELE ME ACOMPANHA. MEU JESUS, MEU JESUS, SEMPRE ME ACOMPANHA. COM JESUS VENCEDOR SEREI! COM JESUS VENCEDOR SEREI! ELE É O MEU SALVADOR. ELE É O MEU SALVADOR. SALVADOR, SALVADOR É O MEU JESUS! COM JESUS VENCEDOR SEREI!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('126', '126 - DEUS FORTE COMO JEOVÁ', 'DEUS FORTE \nCOMO JEOVÁ\nNÃO HÁ OUTRO \nQUE EU CONHEÇA.\n\nDEUS DE VITÓRIA, \nVARÃO DE GUERRA,\nDEUS PODEROSO \nÉ O SENHOR. \n\nONDE, ONDE ESTÁ\nO TEU DEUS? \nONDE, ONDE ESTÁ\nO TEU DEUS?\n\nMEU DEUS ESTÁ NO CÉU,\nTAMBÉM NO MEU CORAÇÃO, \nO DO MUNDO ESTÁ MORTO\nMAS O MEU RESSUSCITOU.', 'DEUS FORTE COMO JEOVÁ NÃO HÁ OUTRO QUE EU CONHEÇA. DEUS DE VITÓRIA, VARÃO DE GUERRA, DEUS PODEROSO É O SENHOR. ONDE, ONDE ESTÁ O TEU DEUS? ONDE, ONDE ESTÁ O TEU DEUS? MEU DEUS ESTÁ NO CÉU, TAMBÉM NO MEU CORAÇÃO, O DO MUNDO ESTÁ MORTO MAS O MEU RESSUSCITOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('127', '127 - CRISTO É VITÓRIA', 'CRISTO É VITÓRIA!\nPROCLAMAI COM REAL PRAZER; \nCRISTO É VITÓRIA!\nE COM ELE VAMOS NÓS VENCER!\n\nSE FALTAR PRAZER E LUZ,\nTUDO NOS DÁ JESUS,\nPOIS TEMOS VITÓRIA\nPOR QUEM JÁ VENCEU NA CRUZ.', 'CRISTO É VITÓRIA! PROCLAMAI COM REAL PRAZER; CRISTO É VITÓRIA! E COM ELE VAMOS NÓS VENCER! SE FALTAR PRAZER E LUZ, TUDO NOS DÁ JESUS, POIS TEMOS VITÓRIA POR QUEM JÁ VENCEU NA CRUZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('128', '128 - CRISTO É MEU AMIGO, É MEU AMIGO', 'CRISTO É MEU AMIGO,\nÉ MEU AMIGO, \nSEGURA FIRME \nA MINHA MÃO!\n\nQUANDO EU ANDO BEM,\nQUANDO ESTOU DOENTE,\nQUANDO ESTOU CONTENTE,\nOU TRISTE ESTOU: ALELUIA!\n\nCRISTO É MEU AMIGO, \nÉ MEU AMIGO,\nSEGURA FIRME A MINHA MÃO!\n\nREPETIR O HINO', 'CRISTO É MEU AMIGO, É MEU AMIGO, SEGURA FIRME A MINHA MÃO! QUANDO EU ANDO BEM, QUANDO ESTOU DOENTE, QUANDO ESTOU CONTENTE, OU TRISTE ESTOU: ALELUIA! CRISTO É MEU AMIGO, É MEU AMIGO, SEGURA FIRME A MINHA MÃO! REPETIR O HINO', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('129', '129 - ONTEM, HOJE E PARA SEMPRE', 'ONTEM, HOJE  E PARA SEMPRE\nO SALVADOR É IGUAL, TODOS MUDAM, \nMENOS CRISTO, ROCHA ETERNAL.\n\nROCHA ETERNAL, \nROCHA ETERNAL,\nTODOS MUDAM,\nMENOS CRISTO,\nROCHA ETERNAL.', 'ONTEM, HOJE E PARA SEMPRE O SALVADOR É IGUAL, TODOS MUDAM, MENOS CRISTO, ROCHA ETERNAL. ROCHA ETERNAL, ROCHA ETERNAL, TODOS MUDAM, MENOS CRISTO, ROCHA ETERNAL.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
