INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('131', '131 - COM CRISTO NO BARCO', 'COM CRISTO NO BARCO\nTUDO VAI MUITO BEM,\nVAI MUITO BEM \nVAI MUITO BEM \nCOM CRISTO NO BARCO\nTUDO VAI MUITO BEM,\nE PASSA O TEMPORAL.\n\nPASSA O TEMPORAL, INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('132', '132 - MEU BARCO É PEQUENO', 'MEU BARCO É PEQUENO\nE GRANDE É O MAR,\nJESUS SEGURA MINHA MÃO;\nELE É MEU PILOTO\nE TUDO VAI BEM\nNA VIAGEM PRA JERUSALÉM.\n\nJESUS VÊ A SUA IGREJA \nCOMO UM FORTE EDIFÍCIO,\nFIRME E EDIFICADO \nPARA MORADA DE DEUS.\n\n133 - JESUS VÊ A SUA IGREJA\n\nCORO (2X)\nNÃO É FEITA DE TIJOLO \nE MADEIRA, NÃO!\nÉ JESUS BEM VIVO NO CORAÇÃO.\n\nEDIFÍCIO ESPECIAL\nDESENHADO POR JESUS,\nPARA SEMPRE ERGUIDO \nPELO SANTO ESPÍRITO.\n\nCORO (2X)\nNÃO É FEITA DE TIJOLO \nE MADEIRA, NÃO!\nÉ JESUS BEM VIVO NO CORAÇÃO.\n\nVENHA PARA A IGREJA,\nVAMOS JUNTOS TRABALHAR\nNESTA OBRA GLORIOSA             \nATÉ JESUS VOLTAR.\n\nCORO (2X)\nNÃO É FEITA DE TIJOLO \nE MADEIRA NÃO!\nÉ JESUS BEM VIVO NO CORAÇÃO.', 'MEU BARCO É PEQUENO E GRANDE É O MAR, JESUS SEGURA MINHA MÃO; ELE É MEU PILOTO E TUDO VAI BEM NA VIAGEM PRA JERUSALÉM. JESUS VÊ A SUA IGREJA COMO UM FORTE EDIFÍCIO, FIRME E EDIFICADO PARA MORADA DE DEUS. 133 - JESUS VÊ A SUA IGREJA NÃO É FEITA DE TIJOLO E MADEIRA, NÃO! É JESUS BEM VIVO NO CORAÇÃO. EDIFÍCIO ESPECIAL DESENHADO POR JESUS, PARA SEMPRE ERGUIDO PELO SANTO ESPÍRITO. VENHA PARA A IGREJA, VAMOS JUNTOS TRABALHAR NESTA OBRA GLORIOSA ATÉ JESUS VOLTAR. NÃO É FEITA DE TIJOLO E MADEIRA NÃO! É JESUS BEM VIVO NO CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('134', '134 - VOU CAMINHANDO PRA JERUSALÉM', 'VOU CAMINHANDO \nPRA JERUSALÉM,\nA CADA DIA, \nCHEGO MAIS PERTO,\n\nQUEM ME CONDUZ  \nÉ O MEU JESUS\nPOIS NO CLAMOR \nSEU SANGUE LIMPA \nE ANDO NA LUZ;\n\nE TENHO AS ARMAS ESPIRITUAIS: \nA ORAÇÃO, O LOUVOR, O JEJUM \nE  MADRUGADA E A PALAVRA REVELADA\nQUE ALIMENTA A ALMA E NOS ENSINA \nA VONTADE DO SENHOR.\n\nVOU CAMINHANDO\nPRA JERUSALÉM,\nSE VOCÊ QUISER,  \nVENHA COMIGO TAMBÉM\nSE VOCÊ QUISER,  \nVENHA COMIGO TAMBÉM', 'VOU CAMINHANDO PRA JERUSALÉM, A CADA DIA, CHEGO MAIS PERTO, QUEM ME CONDUZ É O MEU JESUS POIS NO CLAMOR SEU SANGUE LIMPA E ANDO NA LUZ; E TENHO AS ARMAS ESPIRITUAIS: A ORAÇÃO, O LOUVOR, O JEJUM E MADRUGADA E A PALAVRA REVELADA QUE ALIMENTA A ALMA E NOS ENSINA A VONTADE DO SENHOR. VOU CAMINHANDO PRA JERUSALÉM, SE VOCÊ QUISER, VENHA COMIGO TAMBÉM SE VOCÊ QUISER, VENHA COMIGO TAMBÉM', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('135', '135 - FLUA EM MIM, SENHOR', 'FLUA EM MIM, SENHOR,\nDO TEU ESPÍRITO,\nEU VOU TESTEMUNHAR \nDE TEU AMOR SEM PAR,\nAO MUNDO ANUNCIAR\nQUE CRISTO SALVA O PECADOR.\n\nFLUA EM MIM, SENHOR, \nDO TEU ESPÍRITO,\nFLUA EM MIM, SENHOR,\nDO TEU ESPÍRITO,\nEU VOU TESTEMUNHAR\nDE TEU AMOR SEM PAR,\nAO MUNDO ANUNCIAR\nQUE CRISTO SALVA O PECADOR.', 'FLUA EM MIM, SENHOR, DO TEU ESPÍRITO, EU VOU TESTEMUNHAR DE TEU AMOR SEM PAR, AO MUNDO ANUNCIAR QUE CRISTO SALVA O PECADOR. FLUA EM MIM, SENHOR, DO TEU ESPÍRITO, FLUA EM MIM, SENHOR, DO TEU ESPÍRITO, EU VOU TESTEMUNHAR DE TEU AMOR SEM PAR, AO MUNDO ANUNCIAR QUE CRISTO SALVA O PECADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('136', '136 - LÂMPADA PARA OS MEUS PÉS', 'LÂMPADA PARA OS MEUS PÉS,\nO CAMINHO ILUMINA,\nREVELADA POR DEUS,\nGUIARÁ O MEU CORAÇÃO.\n\nCORO\nA BÍBLIA É UM TESOURO \nDESCOBERTO PARA AQUELES \nQUE DE JESUS ESTÃO PERTO;\nA MINHA BÍBLIA VOU SEMPRE LER, PARA MAIS DE JESUS CONHECER.\n\nQUERO AMAR TUA PALAVRA,\nGUARDÁ-LA SEMPRE \nEM MEU CORAÇÃO,\nENSINAR ÀS PESSOAS\nQUE SÓ JESUS É A SALVAÇÃO.\n\nCORO\nA BÍBLIA É UM TESOURO \nDESCOBERTO PARA AQUELES \nQUE DE JESUS ESTÃO PERTO;\nA MINHA BÍBLIA \nVOU SEMPRE LER, PARA MAIS \nDE JESUS CONHECER.', 'LÂMPADA PARA OS MEUS PÉS, O CAMINHO ILUMINA, REVELADA POR DEUS, GUIARÁ O MEU CORAÇÃO. CORO A BÍBLIA É UM TESOURO DESCOBERTO PARA AQUELES QUE DE JESUS ESTÃO PERTO; A MINHA BÍBLIA VOU SEMPRE LER, PARA MAIS DE JESUS CONHECER. QUERO AMAR TUA PALAVRA, GUARDÁ-LA SEMPRE EM MEU CORAÇÃO, ENSINAR ÀS PESSOAS QUE SÓ JESUS É A SALVAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
