INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('138', '138 - SOU UM SOLDADO DO BOM SALVADOR', 'SOU UM SOLDADO \nDO BOM SALVADOR,\nPOR JESUS LUTANDO\nCOM FÉ E AMOR.\n\nESPADA SEGURANDO                    BEM FIRME EM MINHA MÃO,\nA JESUS SEGUINDO,                           O GRANDE CAPITÃO.\n\nAO CÉU EU VOU MARCHANDO, \nJESUS ME ESPERA LÁ,\nPRA FRENTE EU VOU ANDANDO, \nAO CÉU EU VOU CHEGAR.\nFALANDO: UM, DOIS, TRÊS, QUATRO.\n\nSOU UM SOLDADO \nDO BOM SALVADOR,\nPOR JESUS LUTANDO\nCOM FÉ E AMOR.', 'SOU UM SOLDADO DO BOM SALVADOR, POR JESUS LUTANDO COM FÉ E AMOR. ESPADA SEGURANDO BEM FIRME EM MINHA MÃO, A JESUS SEGUINDO, O GRANDE CAPITÃO. AO CÉU EU VOU MARCHANDO, JESUS ME ESPERA LÁ, PRA FRENTE EU VOU ANDANDO, AO CÉU EU VOU CHEGAR. FALANDO: UM, DOIS, TRÊS, QUATRO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('139', '139 - DONS DO ESPÍRITO SANTO', 'DONS DO ESPÍRITO SANTO,\nBÊNÇÃOS QUE ALMEJO TANTO,\nSÃO COMO PEDRAS PRECIOSAS,\nCOMO O PERFUME DAS ROSAS.\n\nSABEDORIA, CIÊNCIA, FÉ, \nDONS DE CURAR, E MARAVILHAS, PROFECIA, DISCERNIMENTO, \nLÍNGUAS ESTRANHAS, INTERPRETAÇÃO.\n\nDONS DO ESPÍRITO SANTO,\nBÊNÇÃOS QUE ALMEJO TANTO,\nSÃO COMO PEDRAS PRECIOSAS,\nCOMO O PERFUME DAS ROSAS.\n\nSABEDORIA, CIÊNCIA, FÉ, \nDONS DE CURAR, E MARAVILHAS, PROFECIA, DISCERNIMENTO, \nLÍNGUAS ESTRANHAS, INTERPRETAÇÃO.', 'DONS DO ESPÍRITO SANTO, BÊNÇÃOS QUE ALMEJO TANTO, SÃO COMO PEDRAS PRECIOSAS, COMO O PERFUME DAS ROSAS. SABEDORIA, CIÊNCIA, FÉ, DONS DE CURAR, E MARAVILHAS, PROFECIA, DISCERNIMENTO, LÍNGUAS ESTRANHAS, INTERPRETAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('140', '140 - UMA LUZ RESPLANDECENTE', 'UMA LUZ RESPLANDECENTE\nHÁ EM VOLTA DO TEU OLHAR,\nTUA FACE RELUZENTE\nVEM HOJE ME MOSTRAR.\n\nSEI QUE UM DIA TU VIRÁS\nE ENTÃO ME LEVARÁS\nANTES MESMO QUE EU PEÇA:\nSENHOR, VEM ME BUSCAR!\n\nSEGURA-ME PELA MÃO\nQUANDO A VIDA ATRAVESSAR,\nSEI QUE TU ESTARÁS COMIGO\nQUANDO O MAL ME ENCONTRAR.', 'UMA LUZ RESPLANDECENTE HÁ EM VOLTA DO TEU OLHAR, TUA FACE RELUZENTE VEM HOJE ME MOSTRAR. SEI QUE UM DIA TU VIRÁS E ENTÃO ME LEVARÁS ANTES MESMO QUE EU PEÇA: SENHOR, VEM ME BUSCAR! SEGURA-ME PELA MÃO QUANDO A VIDA ATRAVESSAR, SEI QUE TU ESTARÁS COMIGO QUANDO O MAL ME ENCONTRAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
