INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('142', '142 - COMO UMA FOLHA AO VENTO', 'COMO UMA FOLHA AO VENTO\nVOA PRA LÁ E PRA CÁ,\nEU TAMBÉM QUERO ESTAR\nAONDE O ESPÍRITO SANTO MANDAR.\n\nNÃO FAÇO MAIS O QUE EU QUERO,\nMAS O QUE DEUS REVELAR;\nCOMO UMA FOLHA AO VENTO,\nVOANDO, VAI SEMPRE ONDE \nO VENTO SOPRAR,\n\nEU TAMBÉM QUERO ESTAR\nAONDE O ESPÍRITO SANTO MANDAR.\nEU TAMBÉM QUERO ESTAR\nAONDE O ESPÍRITO SANTO MANDAR.', 'COMO UMA FOLHA AO VENTO VOA PRA LÁ E PRA CÁ, EU TAMBÉM QUERO ESTAR AONDE O ESPÍRITO SANTO MANDAR. NÃO FAÇO MAIS O QUE EU QUERO, MAS O QUE DEUS REVELAR; COMO UMA FOLHA AO VENTO, VOANDO, VAI SEMPRE ONDE O VENTO SOPRAR, EU TAMBÉM QUERO ESTAR AONDE O ESPÍRITO SANTO MANDAR. EU TAMBÉM QUERO ESTAR AONDE O ESPÍRITO SANTO MANDAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('143', '143 - TENHO UMA CANDEIA (CANDEIA ACESA)', 'CORO\nTENHO UMA CANDEIA,  \nDE ÓLEO ESTÁ CHEIA,                      \nELA ANUNCIA QUE \nJESUS VOLTARÁ;\n\nLUZ BRILHANTE, REFULGENTE,              \nÉ O GRITO DA IGREJA:\nJESUS VAI VOLTAR!\n\nACENDE TUA CANDEIA,    \nNÃO A DEIXES APAGAR,\nILUMINANDO O TEU  ROSTO \nJESUS TE VERÁ.\n\nCORO\nTENHO UMA CANDEIA,  \nDE ÓLEO ESTÁ CHEIA,                      \nELA ANUNCIA QUE JESUS VOLTARÁ;\nLUZ BRILHANTE, REFULGENTE,              \nÉ O GRITO DA IGREJA:\nJESUS VAI VOLTAR!\n\nCANDEIA VAZIA              \nEU NÃO QUERO, NÃO,\nCANDEIA CHEIA, \nEU QUERO, SIM,\nCANDEIA COM FOGO   \nPRONTA PARA SUBIR.\n\nCANDEIA  VAZIA              \nEU NÃO QUERO, NÃO,\nCANDEIA  CHEIA, \nEU QUERO, SIM,\nCANDEIA  COM  FOGO   \nPRONTA  PARA  SUBIR.\n\nCORO\nTENHO UMA CANDEIA,  \nDE ÓLEO ESTÁ CHEIA,                      \nELA ANUNCIA QUE JESUS VOLTARÁ;\nLUZ BRILHANTE, REFULGENTE,              \nÉ O GRITO DA IGREJA:\nJESUS VAI VOLTAR!', 'CORO TENHO UMA CANDEIA, DE ÓLEO ESTÁ CHEIA, ELA ANUNCIA QUE JESUS VOLTARÁ; LUZ BRILHANTE, REFULGENTE, É O GRITO DA IGREJA: JESUS VAI VOLTAR! ACENDE TUA CANDEIA, NÃO A DEIXES APAGAR, ILUMINANDO O TEU ROSTO JESUS TE VERÁ. CORO TENHO UMA CANDEIA, DE ÓLEO ESTÁ CHEIA, ELA ANUNCIA QUE JESUS VOLTARÁ; LUZ BRILHANTE, REFULGENTE, É O GRITO DA IGREJA: JESUS VAI VOLTAR! CANDEIA VAZIA EU NÃO QUERO, NÃO, CANDEIA CHEIA, EU QUERO, SIM, CANDEIA COM FOGO PRONTA PARA SUBIR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('144', '144 - NOVA ALIANÇA', 'NOVA ALIANÇA \nFEZ O MEU SENHOR\nCOM O SEU POVO \nNUM ATO DE AMOR; \n“EIS QUE FAÇO NOVAS \nTODAS AS COISAS”,\nASSIM FALOU O SENHOR.\n\nPREPARO O MEU CORAÇÃO\nPARA O ARREBATAMENTO,\nNÃO SERÁ FÁCIL, LUTAS VIRÃO,\nMAS NA BUSCA AO SENHOR\nELE DARÁ A SOLUÇÃO;\n\nCOM O ESPÍRITO SANTO\nQUER BATIZAR E EXPERIÊNCIAS \nCOM OS DONS QUER DAR,\n\nEU NÃO TEMEREI, \nCOM CRISTO ANDAREI \nNA DIREÇÃO DO ESPÍRITO SANTO.\n\nQUANDO EU CAMINHAR\nCOM MEU JESUS\nVAI RAIAR A LUZ\nE VIDAS SE ENTREGARÃO,\nRECEBENDO A SALVAÇÃO;\n\nE JUNTOS NUM CORPO UNIDO\nVAMOS RUMO À\nTÃO ESPERADA SIÃO.\nTÃO ESPERADA SIÃO.', 'NOVA ALIANÇA FEZ O MEU SENHOR COM O SEU POVO NUM ATO DE AMOR; EIS QUE FAÇO NOVAS TODAS AS COISAS, ASSIM FALOU O SENHOR. PREPARO O MEU CORAÇÃO PARA O ARREBATAMENTO, NÃO SERÁ FÁCIL, LUTAS VIRÃO, MAS NA BUSCA AO SENHOR ELE DARÁ A SOLUÇÃO; COM O ESPÍRITO SANTO QUER BATIZAR E EXPERIÊNCIAS COM OS DONS QUER DAR, EU NÃO TEMEREI, COM CRISTO ANDAREI NA DIREÇÃO DO ESPÍRITO SANTO. QUANDO EU CAMINHAR COM MEU JESUS VAI RAIAR A LUZ E VIDAS SE ENTREGARÃO, RECEBENDO A SALVAÇÃO; E JUNTOS NUM CORPO UNIDO VAMOS RUMO À TÃO ESPERADA SIÃO. TÃO ESPERADA SIÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
