INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('147', '147 - EM TUAS PISADAS', 'EM TUAS PISADAS \nCOLOCO OS MEUS PÉS,  \nASSIM, BEM FIRMADO, \nNÃO VACILAREI;\n\nOS  TEUS  PRECEITOS, \nSENHOR, OS  GUARDEI,\nAS TUAS  PALAVRAS                 \nSÃO MEU ALIMENTO,\nVIVER NA TUA OBRA  \nÉ O MEU SUSTENTO.\nBIS\n\nEM TUAS PISADAS \nCOLOCO OS MEUS PÉS,  \nASSIM, BEM FIRMADO, \nNÃO VACILAREI;\n\nOS  TEUS  PRECEITOS, \nSENHOR, OS  GUARDEI,\nAS TUAS  PALAVRAS                      \nSÃO MEU ALIMENTO,\nVIVER NA TUA OBRA  \nÉ O MEU SUSTENTO.\nBIS', 'EM TUAS PISADAS COLOCO OS MEUS PÉS, ASSIM, BEM FIRMADO, NÃO VACILAREI; OS TEUS PRECEITOS, SENHOR, OS GUARDEI, AS TUAS PALAVRAS SÃO MEU ALIMENTO, VIVER NA TUA OBRA É O MEU SUSTENTO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('148', '148 - ANDANDO EM ESPÍRITO', 'ANDANDO EM ESPÍRITO \nVENCEREI A TENTAÇÃO,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('149', '149 - NÓS SOMOS UMA VOZ', 'NÓS SOMOS UMA VOZ\nQUE CLAMA NO DESERTO,\nNESTE MUNDO DE ILUSÕES\nPROCLAMAMOS A MENSAGEM  \nDE SALVAÇÃO.\n\nDEUS AMA O QUE SE APRESSA,\nELE AMA O EVANGELISTA,\nAMA AQUELE QUE SE APRESSA\nPRA ANUNCIAR A SALVAÇÃOINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('150', '150 - CINCO SEIXOS', 'CINCO SEIXOS DO RIBEIRO \nDAVI COLOCOU\nEM SEU ALFORJE DE PASTOR\nQUANDO GOLIAS \nFOI A ENFRENTAR.\nSEM MEDO DAVI FOI LUTAR.\n\nDAVI OROU, \nCONFIOU EM DEUS,\nPARA ASSIM \nVENCER O FILISTEU.\n\nEM SEU CORAÇÃO HAVIA O LOUVOR,\nA PALAVRA, O JEJUM E A ORAÇÃO\nE NA MADRUGADA, \nSEMPRE A DEUS BUSCAVA\nDEIXANDO O SENHOR \nSUA VIDA DIRIGIR.\n\nE QUANDO GOLIAS FOI ENCONTRAR\nSÓ UMA PEDRA EM SUA FUNDA COLOCOU.\nE MIROU, GIROU, LANÇOU\nNA TESTA DO GIGANTE A PEDRA CRAVOU\n(TRUMMMM)\n\nO GIGANTE CAIU, \nDAVI FOI VENCEDOR.\nNO NOME DO SENHOR, \nNO NOME DO SENHOR.', 'CINCO SEIXOS DO RIBEIRO DAVI COLOCOU EM SEU ALFORJE DE PASTOR QUANDO GOLIAS FOI A ENFRENTAR. SEM MEDO DAVI FOI LUTAR. DAVI OROU, CONFIOU EM DEUS, PARA ASSIM VENCER O FILISTEU. EM SEU CORAÇÃO HAVIA O LOUVOR, A PALAVRA, O JEJUM E A ORAÇÃO E NA MADRUGADA, SEMPRE A DEUS BUSCAVA DEIXANDO O SENHOR SUA VIDA DIRIGIR. E QUANDO GOLIAS FOI ENCONTRAR SÓ UMA PEDRA EM SUA FUNDA COLOCOU. E MIROU, GIROU, LANÇOU NA TESTA DO GIGANTE A PEDRA CRAVOU (TRUMMMM) O GIGANTE CAIU, DAVI FOI VENCEDOR. NO NOME DO SENHOR, NO NOME DO SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('151', '151 - NO PRINCÍPIO ERA O VERBO', '“NO PRINCÍPIO ERA O VERBOINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('152', '152 - NAS BATALHAS SOU UM VENCEDOR', 'NAS BATALHAS SOU UM VENCEDOR, \nUSO AS ARMAS ESPIRITUAIS.\nREVESTIDO DE TODAINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('153', '153 - COMO NOÉ', 'COMO NOÉ, \nQUERO OBEDECER\nÀ DOCE VOZ DE DEUS,\nA ARCA VOU FAZER;\nCOMO NOÉ NA ARCA VOU ENTRAR\nE SALVAÇÃO VOU ALCANÇAR.\n\nPELA FÉ NOÉ \nEM TUDO OBEDECEU, \nSEGUINDO O PROJETO,\nNADA ELE TEMEU. \nHERDEIRO COM JESUS\nDEUS ASSIM O FEZ, \nPOIS A SALVAÇÃO\nÉ PRA TODO O QUE CRÊ.\n\nHERDEIRO COM JESUS\nDEUS ASSIM O FEZ, \nPOIS A SALVAÇÃO\nÉ PRA TODO O QUE CRÊ.\n\nCOMO NOÉ, \nQUERO OBEDECER\nÀ DOCE VOZ DE DEUS,\nA ARCA VOU FAZER.\nCOMO NOÉ NA ARCA \nVOU ENTRAR E SALVAÇÃO\nVOU ALCANÇAR.', 'COMO NOÉ, QUERO OBEDECER À DOCE VOZ DE DEUS, A ARCA VOU FAZER; COMO NOÉ NA ARCA VOU ENTRAR E SALVAÇÃO VOU ALCANÇAR. PELA FÉ NOÉ EM TUDO OBEDECEU, SEGUINDO O PROJETO, NADA ELE TEMEU. HERDEIRO COM JESUS DEUS ASSIM O FEZ, POIS A SALVAÇÃO É PRA TODO O QUE CRÊ. HERDEIRO COM JESUS DEUS ASSIM O FEZ, POIS A SALVAÇÃO É PRA TODO O QUE CRÊ. COMO NOÉ, QUERO OBEDECER À DOCE VOZ DE DEUS, A ARCA VOU FAZER. COMO NOÉ NA ARCA VOU ENTRAR E SALVAÇÃO VOU ALCANÇAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('154', '154 - COM O ESPÍRITO DE DEUS', 'CORO\nCOM O ESPÍRITO DE DEUS \nEM MINHA VIDA,\nSOU FELIZ, SOU FELIZ!\nTENHO PAZ, CONSOLO, \nGRAÇA E ALEGRIA.\nSOU FELIZ, SIM, SOU FELIZ!\n\nCRISTO PROMETEU \nDEIXAR-NOS O CONSOLADOR\nPARA ESTAR CONOSCO \nSEMPRE ONDE QUER QUE FOR,\n\nO ESPÍRITO DE VERDADE, \nQUE ACONSELHARÁ\nMINHA VIDA, ENSINANDO \nCOMO DEVO ANDAR.\n\nCORO\nCOM O ESPÍRITO DE DEUS \nEM MINHA VIDA,\nSOU FELIZ, SOU FELIZ!\nTENHO PAZ, CONSOLO, \nGRAÇA E ALEGRIA.\nSOU FELIZ, SIM, SOU FELIZ!\n\nQUERO SER OBEDIENTE \nAO ESPÍRITO DE DEUS,\nSEGUIR SUA PALAVRA \nE OS ENSINOS SEUS,\nTETEMUNHAR QUE TENHO \nDEUS EM MEU VIVER,\nMINHA VIDA É TRANSFORMADA,\nVOU DIZER PORQUÊ:\n\nCORO\nCOM O ESPÍRITO DE DEUS \nEM MINHA VIDA,\nSOU FELIZ, SOU FELIZ!\nTENHO PAZ, CONSOLO, \nGRAÇA E ALEGRIA.\nSOU FELIZ, SIM, SOU FELIZ!', 'CORO COM O ESPÍRITO DE DEUS EM MINHA VIDA, SOU FELIZ, SOU FELIZ! TENHO PAZ, CONSOLO, GRAÇA E ALEGRIA. SOU FELIZ, SIM, SOU FELIZ! CRISTO PROMETEU DEIXAR-NOS O CONSOLADOR PARA ESTAR CONOSCO SEMPRE ONDE QUER QUE FOR, O ESPÍRITO DE VERDADE, QUE ACONSELHARÁ MINHA VIDA, ENSINANDO COMO DEVO ANDAR. QUERO SER OBEDIENTE AO ESPÍRITO DE DEUS, SEGUIR SUA PALAVRA E OS ENSINOS SEUS, TETEMUNHAR QUE TENHO DEUS EM MEU VIVER, MINHA VIDA É TRANSFORMADA, VOU DIZER PORQUÊ:', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('155', '155 - QUERO ESTAR EM TUA PRESENÇA, Ó DEUS', 'QUERO ESTAR EM TUA \nPRESENÇA, Ó DEUS, \nEM TUA OBRA PROSSEGUIR ATÉ O FIM,\nSEM TEMER E SEMPRE                  CONFIAR QUE JESUS                                  AO MEU LADO ESTÁ.\n\nCORO\nFAZE DE MIM UM VENCEDOR, \nNÃO IMPORTA QUAL SEJA \nA MINHA DOR.\n\nFAZE DE MIM UM ENVIADO TEU,\nPARA A TUA OBRA REALIZAR\nE DO TEU PODER TESTEMUNHAR\nE POR FIM JERUSALÉM ALCANÇAR.\n\nQUERO SER UM SERVO DO SENHOR\nE ANDAR SOB A REVELAÇÃO,\nPARTILHAR DA SUA COMUNHÃO,\nDANDO A ELE SEMPRE \nO MEU CORAÇÃO.\n\nCORO\nFAZE DE MIM UM VENCEDOR, \nNÃO IMPORTA QUAL SEJA \nA MINHA DOR.\n\nFAZE DE MIM UM ENVIADO TEU, \nPARA A TUA OBRA REALIZAR\nE DO TEU PODER TESTEMUNHAR\nE POR FIM JERUSALÉM ALCANÇAR.', 'QUERO ESTAR EM TUA PRESENÇA, Ó DEUS, EM TUA OBRA PROSSEGUIR ATÉ O FIM, SEM TEMER E SEMPRE CONFIAR QUE JESUS AO MEU LADO ESTÁ. CORO FAZE DE MIM UM VENCEDOR, NÃO IMPORTA QUAL SEJA A MINHA DOR. FAZE DE MIM UM ENVIADO TEU, PARA A TUA OBRA REALIZAR E DO TEU PODER TESTEMUNHAR E POR FIM JERUSALÉM ALCANÇAR. QUERO SER UM SERVO DO SENHOR E ANDAR SOB A REVELAÇÃO, PARTILHAR DA SUA COMUNHÃO, DANDO A ELE SEMPRE O MEU CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('156', '156 - SOMOS CIDADÃOS DO CÉU', 'SOMOS CIDADÃOS, \nCIDADÃOS DO CÉU.\nSOMOS CIDADÃOS, \nTEMOS SALVAÇÃO.\n\nIDENTIDADE TEMOS,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('157', '157 - ESTE MUNDO NÃO É MEU LUGAR', 'ESTE MUNDO NÃO \nÉ MEU LUGAR, \nPEREGRINO, ESTOU\nA CAMINHAR.\n\nMAS PARA O CÉU IREIINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('158', '158 - NO CAMINHO EU QUERO ESTAR', 'NO CAMINHO EU QUERO ESTAR, \nAO SENHOR VOU OBEDECER.\nAONDE QUER QUE DEUS \nMANDAR ANUNCIAR O SEU AMOR. ESTOU PRONTO, SENHOR, EU VOU!\n\nTANTAS VIDAS NÃO \nCONHECEM O SENHOR, \nVIVEM TRISTES, TÃO VAZIAS, \nSEM AMOR, PRECISANDO RECEBER \nO SEU PERDÃO,  PARA ASSIM ALCANÇAREM A SALVAÇÃO,\n\nQUE JESUS LÁ NO CALVÁRIO REVELOU, \nQUANDO SEU PRECIOSO \nSANGUE DERRAMOU,\nCONCEDENDO VIDA ETERNA AO QUE CRER,\nTRANSFORMANDO SUAS VIDAS, \nQUE PRAZER!\n\nQUE ALEGRIA PODER SERVIR \nA JESUS, NOSSO SALVADOR!\nEM SUA OBRA TRABALHAR, \nATENDENDO AO SEU CHAMAR,\nESTOU PRONTO, \nSENHOR, EU VOU!', 'NO CAMINHO EU QUERO ESTAR, AO SENHOR VOU OBEDECER. AONDE QUER QUE DEUS MANDAR ANUNCIAR O SEU AMOR. ESTOU PRONTO, SENHOR, EU VOU! TANTAS VIDAS NÃO CONHECEM O SENHOR, VIVEM TRISTES, TÃO VAZIAS, SEM AMOR, PRECISANDO RECEBER O SEU PERDÃO, PARA ASSIM ALCANÇAREM A SALVAÇÃO, QUE JESUS LÁ NO CALVÁRIO REVELOU, QUANDO SEU PRECIOSO SANGUE DERRAMOU, CONCEDENDO VIDA ETERNA AO QUE CRER, TRANSFORMANDO SUAS VIDAS, QUE PRAZER! QUE ALEGRIA PODER SERVIR A JESUS, NOSSO SALVADOR! EM SUA OBRA TRABALHAR, ATENDENDO AO SEU CHAMAR, ESTOU PRONTO, SENHOR, EU VOU!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('159', '159 - SERVO DO SENHOR EU SOU', 'SERVO DO SENHOR EU SOU\nE POSSO OUVIR SUA VOZ.\nSERVO DO SENHOR EU SOU\nE QUERO SERVIR A JESUS.\n\nUMA SÓ VOZ FALA AO MEU CORAÇÃO,\nA VOZ DE JESUS, MEU SENHOR.\nOUÇO A VOZ DO PERFEITO AMOR, \nA VOZ DO MEU BOM SALVADOR.\n\nSE VOCÊ TAMBÉM QUISER,\nPODE OUVIR A JESUS.\nBASTA ABRIR SEU CORAÇÃO\nE DEIXAR ENTRAR SUA LUZ.\n\nE UMA VOZ FALARÁ AO SEU CORAÇÃO,\nA VOZ DE JESUS, O SENHOR.\nSÓ A DOCE VOZ ENCHERÁ SEU CORAÇÃO\nDE PAZ, ALEGRIA E AMOR. \nDE PAZ, ALEGRIA E AMOR.', 'SERVO DO SENHOR EU SOU E POSSO OUVIR SUA VOZ. SERVO DO SENHOR EU SOU E QUERO SERVIR A JESUS. UMA SÓ VOZ FALA AO MEU CORAÇÃO, A VOZ DE JESUS, MEU SENHOR. OUÇO A VOZ DO PERFEITO AMOR, A VOZ DO MEU BOM SALVADOR. SE VOCÊ TAMBÉM QUISER, PODE OUVIR A JESUS. BASTA ABRIR SEU CORAÇÃO E DEIXAR ENTRAR SUA LUZ. E UMA VOZ FALARÁ AO SEU CORAÇÃO, A VOZ DE JESUS, O SENHOR. SÓ A DOCE VOZ ENCHERÁ SEU CORAÇÃO DE PAZ, ALEGRIA E AMOR. DE PAZ, ALEGRIA E AMOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('160', '160 - EXISTEM SEGREDOS NA TUA PALAVRA', 'EXISTEM SEGREDOS \nNA TUA PALAVRA\nQUE SÓ TEU ESPÍRITO \nPODE REVELAR.\n\nENSINOS DE VIDA, \nDE FELICIDADE,\nCAMINHO DE LUZ \nQUE JESUS VEIO NOS DAR.\n\nÉ LIVRO SELADO, \nMISTÉRIO GUARDADO,\nQUE O HOMEM SEM DEUS  \nNÃO PODE ALCANÇAR.\n\nÉ LIVRO ABERTO, \nCLARO, DESCOBERTO,\nPARA OS SERVOS FIÉIS   \nQUE COM JESUS VÃO REINAR. \nPARA OS SERVOS FIÉIS   \nQUE COM JESUS VÃO REINAR.', 'EXISTEM SEGREDOS NA TUA PALAVRA QUE SÓ TEU ESPÍRITO PODE REVELAR. ENSINOS DE VIDA, DE FELICIDADE, CAMINHO DE LUZ QUE JESUS VEIO NOS DAR. É LIVRO SELADO, MISTÉRIO GUARDADO, QUE O HOMEM SEM DEUS NÃO PODE ALCANÇAR. É LIVRO ABERTO, CLARO, DESCOBERTO, PARA OS SERVOS FIÉIS QUE COM JESUS VÃO REINAR. PARA OS SERVOS FIÉIS QUE COM JESUS VÃO REINAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('161', '161 - TU ÉS PRA MIM UM VASO ESCOLHIDO', 'TU ÉS PRA MIM UM VASO ESCOLHIDO,\nTU ÉS PRA MIM UM VASO DE VALOR.\nEU TE CHAMEI PARA SALVAR TUA VIDA, EU SOU JESUS, O TEU SENHOR.\n\nTU ÉS PRA MIM UM VASO ESCOLHIDO,\nTU ÉS PRA MIM UM VASO DE VALOR.\nPARA LEVAR MEU NOME A TANTAS GENTES\nE TESTEMUNHAR DO MEU AMOR.\n\nCORO\nVAI, FALA E ANUNCIA\nJESUS QUER O HOMEM TRANSFORMAR. \nOS DIAS PASSAM, O TEMPO FINDA,\nJESUS TEM PRESSA E VAI VOLTAR!\n\nCORO\nVAI, FALA E ANUNCIA\nJESUS QUER O HOMEM TRANSFORMAR. \nOS DIAS PASSAM, O TEMPO FINDA,\nJESUS TEM PRESSA E VAI VOLTAR! (2X)', 'TU ÉS PRA MIM UM VASO ESCOLHIDO, TU ÉS PRA MIM UM VASO DE VALOR. EU TE CHAMEI PARA SALVAR TUA VIDA, EU SOU JESUS, O TEU SENHOR. TU ÉS PRA MIM UM VASO ESCOLHIDO, TU ÉS PRA MIM UM VASO DE VALOR. PARA LEVAR MEU NOME A TANTAS GENTES E TESTEMUNHAR DO MEU AMOR. CORO VAI, FALA E ANUNCIA JESUS QUER O HOMEM TRANSFORMAR. OS DIAS PASSAM, O TEMPO FINDA, JESUS TEM PRESSA E VAI VOLTAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('162', '162 - ANDAI NA MINHA PRESENÇA', 'CORO (2X)\nANDAI NA MINHA PRESENÇA, \nANDAI NA MINHA PRESENÇA\nE VOS GUIAREI.\n\nE VOS LEVAREI A UM LUGAR\nCHEIO DE GOZO E DE PAZ.\nFOME E SEDE JAMAIS TEREIS,\nPOIS COMEREIS DO PÃO DA VIDA.\n\nCORO (2X)\nANDAI NA MINHA PRESENÇA, \nANDAI NA MINHA PRESENÇA\nE VOS GUIAREI.\n\nE O MEU ESPÍRITO VOS CONDUZIRÁ \nÀ PÁTRIA CELESTIAL.\nLUTAS E DORES NUNCA MAIS TEREIS, \nPOIS VIVEREIS COM O VOSSO REI.\n\nCORO (2X)\nANDAI NA MINHA PRESENÇA, \nANDAI NA MINHA PRESENÇA\nE VOS GUIAREI.', 'ANDAI NA MINHA PRESENÇA, ANDAI NA MINHA PRESENÇA E VOS GUIAREI. E VOS LEVAREI A UM LUGAR CHEIO DE GOZO E DE PAZ. FOME E SEDE JAMAIS TEREIS, POIS COMEREIS DO PÃO DA VIDA. E O MEU ESPÍRITO VOS CONDUZIRÁ À PÁTRIA CELESTIAL. LUTAS E DORES NUNCA MAIS TEREIS, POIS VIVEREIS COM O VOSSO REI.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('163', '163 - DÁ-ME UM CORAÇÃO CHEIO DO TEU AMOR', 'DÁ-ME UM CORAÇÃO\nCHEIO DO TEU AMOR,\nQUE SEMPRE RECONHEÇA\nTUA BONDADE, Ó MEU SENHOR.\n\nUM CORAÇÃO QUE ESTREMEÇA\nAO OUVIR TUA VOZ ASSIM\nE QUE NUNCA SE ESQUEÇA\nO QUE TU FAZES POR MIM.\n\nUM CORAÇÃO OBEDIENTE\nÀ TUA REVELAÇÃO,                           \nPURO E RENOVADO\nPELA TUA SALVAÇÃO.\n\nÓ SENHOR, AMÉM!', 'DÁ-ME UM CORAÇÃO CHEIO DO TEU AMOR, QUE SEMPRE RECONHEÇA TUA BONDADE, Ó MEU SENHOR. UM CORAÇÃO QUE ESTREMEÇA AO OUVIR TUA VOZ ASSIM E QUE NUNCA SE ESQUEÇA O QUE TU FAZES POR MIM. UM CORAÇÃO OBEDIENTE À TUA REVELAÇÃO, PURO E RENOVADO PELA TUA SALVAÇÃO. Ó SENHOR, AMÉM!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('164', '164 - DÁ-ME UM CORAÇÃO HUMILDE E RETO', 'DÁ-ME UM CORAÇÃO HUMILDE E RETO,\nQUE ATENTE PARA TUA PALAVRA \nE SABEDORIA,PRA QUE EU POSSA \nANDAR EM TUA PRESENÇA.\n\nCORO \nQUERO SER OBEDIENTE À TUA VOZ,\nTRABALHAR NA TUA OBRA COM AMOR;\nQUERO IR MORAR PRA SEMPRE NO PALÁCIO \nDO REI DOS REIS, NAS MANSÕES CELESTIAIS.\n\nQUE MINHA CASA ESTEJA FIRMADA EM JESUS,\nROCHA ETERNA, ELE É MEU ABRIGO SEGURO,\nMINHA FORTALEZA, MEU AMIGO FIEL,                        \nO MEU SALVADOR.\n\nCORO \nQUERO SER OBEDIENTE À TUA VOZ,\nTRABALHAR NA TUA OBRA COM AMOR;\nQUERO IR MORAR PRA SEMPRE NO PALÁCIO \nDO REI DOS REIS, NAS MANSÕES CELESTIAIS.\n\nCORO \nQUERO SER OBEDIENTE À TUA VOZ,\nTRABALHAR NA TUA OBRA COM AMOR;\nQUERO IR MORAR PRA SEMPRE NO PALÁCIO \nDO REI DOS REIS, NAS MANSÕES CELESTIAIS. (BIS)', 'DÁ-ME UM CORAÇÃO HUMILDE E RETO, QUE ATENTE PARA TUA PALAVRA E SABEDORIA,PRA QUE EU POSSA ANDAR EM TUA PRESENÇA. CORO QUERO SER OBEDIENTE À TUA VOZ, TRABALHAR NA TUA OBRA COM AMOR; QUERO IR MORAR PRA SEMPRE NO PALÁCIO DO REI DOS REIS, NAS MANSÕES CELESTIAIS. QUE MINHA CASA ESTEJA FIRMADA EM JESUS, ROCHA ETERNA, ELE É MEU ABRIGO SEGURO, MINHA FORTALEZA, MEU AMIGO FIEL, O MEU SALVADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('165', '165 - QUERO SER SANTO PARA O SENHOR', 'QUERO SER SANTO PARA O SENHOR,\nQUERO SER PURO EM SEU AMOR,\nEM TODO O TEMPO TESTEMUNHAR DE SEU AMOR E GRAÇA SEM PAR.\n\nQUERO LEVAR A MENSAGEM DE SALVAÇÃO\nPARA ESTE MUNDO QUE ESTÁ NA ESCURIDÃO \nE QUANDO CRISTO VIER NOS BUSCAR\nPOSSAMOS TODOS NO CÉU IR MORAR.\n\nQUERO SER SANTO PARA O SENHOR,\nQUERO SER PURO EM SEU AMOR,\nEM TODO O TEMPO TESTEMUNHAR DE SEU AMOR E GRAÇA SEM PAR.\n\nQUERO LEVAR A MENSAGEM DE SALVAÇÃO\nPARA ESTE MUNDO QUE ESTÁ NA ESCURIDÃO \nE QUANDO CRISTO VIER NOS BUSCAR\nPOSSAMOS TODOS NO CÉU IR MORAR.\n\nQUERO SER SANTO,\nQUERO SER PURO\nPARA O SENHOR!', 'QUERO SER SANTO PARA O SENHOR, QUERO SER PURO EM SEU AMOR, EM TODO O TEMPO TESTEMUNHAR DE SEU AMOR E GRAÇA SEM PAR. QUERO LEVAR A MENSAGEM DE SALVAÇÃO PARA ESTE MUNDO QUE ESTÁ NA ESCURIDÃO E QUANDO CRISTO VIER NOS BUSCAR POSSAMOS TODOS NO CÉU IR MORAR. QUERO SER SANTO, QUERO SER PURO PARA O SENHOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('166', '166 - BRILHANDO, BRILHANDO', 'BRILHANDO, BRILHANDO,\nMINHA CANDEIA SEMPRE \nESTÁ BRILHANDO.\nBRILHANDO, BRILHANDO,\nBRILHANDO ATÉ JESUS VOLTAR.\n\nNÃO FALTARÁ O ÓLEO DO ESPÍRITO,\nMINHA CANDEIA SEMPRE CHEIA FICARÁ,\nPORQUE JESUS NOS DEU SUA PROMESSA\nQUE SEU ESPÍRITO CONOSCO ESTARÁ.\n\nE SE VOCÊ QUISER TAMBÉM BRILHAR,\nÉ SÓ ABRIR SEU CORAÇÃO\nE DEIXAR CRISTO NELE ENTRAR\nE ASSIM VOCÊ SEMPRE ESTARÁ.\n\nBRILHANDO, BRILHANDO,\nMINHA CANDEIA SEMPRE \nESTÁ BRILHANDO.\nBRILHANDO, BRILHANDO,\nBRILHANDO ATÉ JESUS VOLTAR.', 'BRILHANDO, BRILHANDO, MINHA CANDEIA SEMPRE ESTÁ BRILHANDO. BRILHANDO, BRILHANDO, BRILHANDO ATÉ JESUS VOLTAR. NÃO FALTARÁ O ÓLEO DO ESPÍRITO, MINHA CANDEIA SEMPRE CHEIA FICARÁ, PORQUE JESUS NOS DEU SUA PROMESSA QUE SEU ESPÍRITO CONOSCO ESTARÁ. E SE VOCÊ QUISER TAMBÉM BRILHAR, É SÓ ABRIR SEU CORAÇÃO E DEIXAR CRISTO NELE ENTRAR E ASSIM VOCÊ SEMPRE ESTARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('167', '167- DANIEL NA COVA DOS LEÕES', 'DANIEL TRÊS VEZES AO DIA\nBUSCAVA  AO SENHOR,\nDE JOELHOS ELE ORAVA\nE GLORIFICAVA A DEUS.\n\nCERTO DIA O ENCONTRARAM ORANDO PROSTRADO AO SEU DEUS, \nSEUS INIMIGOS CONTARAM \nAO REI E PEDIRAM PARA \nQUE CUMPRISSE A LEI.\n\nCORO\nDANIEL NA COVA DOS LEÕES,\nUM ANJO APARECEU.\nDANIEL NA COVA DOS LEÕES,\nO SEU DEUS O PROTEGEU.\n\nINSTRUMENTOS\n\nDANIEL FOI SALVO POR SEU \nDEUS E O REI COMPREENDEU.\nSEUS INIMIGOS FORAM PUNIDOS\nE O REI LOGO DECRETOU\nQUE O DEUS DE DANIEL\nÉ DEUS VIVO E SALVADOR.\n\nCORO\nDANIEL NA COVA DOS LEÕES,\nUM ANJO APARECEU.\nDANIEL NA COVA DOS LEÕES,\nO SEU DEUS O PROTEGEU.', 'DANIEL TRÊS VEZES AO DIA BUSCAVA AO SENHOR, DE JOELHOS ELE ORAVA E GLORIFICAVA A DEUS. CERTO DIA O ENCONTRARAM ORANDO PROSTRADO AO SEU DEUS, SEUS INIMIGOS CONTARAM AO REI E PEDIRAM PARA QUE CUMPRISSE A LEI. CORO DANIEL NA COVA DOS LEÕES, UM ANJO APARECEU. DANIEL NA COVA DOS LEÕES, O SEU DEUS O PROTEGEU. INSTRUMENTOS DANIEL FOI SALVO POR SEU DEUS E O REI COMPREENDEU. SEUS INIMIGOS FORAM PUNIDOS E O REI LOGO DECRETOU QUE O DEUS DE DANIEL É DEUS VIVO E SALVADOR. CORO DANIEL NA COVA DOS LEÕES, UM ANJO APARECEU. DANIEL NA COVA DOS LEÕES, O SEU DEUS O PROTEGEU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('168', '168 - JESUS CHAMA OS SEUS MINISTROS', 'JESUS CHAMA OS SEUS \nMINISTROS PARA UMA \nGRANDE MISSÃO:                             GUIAR O SEU POVO\nRUMO À BELA SIÃO.\n\nO APÓSTOLO É AQUELE,\nQUE LEVA A PALAVRA DO SENHOR.\nTESTEMUNHA PRA MUITOS POVOS,\nAPRESENTA A OBRA COM AMOR.\n\nO PROFETA APONTA O CAMINHO,\nÉ UM PROCLAMADOR.\nFALA DO QUE ESTÁ POR VIR,\nÉ O INTÉRPRETE DO SENHOR.\n\nPARA O EVANGELISTA\nNÃO HÁ TEMPO RUIM,\nPREGA A PALAVRA \nEM TODOS OS LUGARES\nCOM A AUTORIDADE DE JESUS.\n\nO PASTOR CUIDA DA IGREJA\nGUIANDO O REBANHO À SALVAÇÃO.\nAPASCENTA COM CUIDADO,\nLEVANDO O POVO À ADORAÇÃO!\n\nE O MESTRE É AQUELE,\nO MENOR DOS IRMÃOS,\nENSINA A SÃ DOUTRINA,\nEDUCA COM A REVELAÇÃO.\n\nESTES SÃO OS CINCO MINISTÉRIOS, \nFORMA SIMPLES E PATERNA \nDE JESUS ORIENTAR A SUA IGREJA,\nLEVANDO-A À VIDA ETERNA!\nLEVANDO-A À VIDA ETERNA!', 'JESUS CHAMA OS SEUS MINISTROS PARA UMA GRANDE MISSÃO: GUIAR O SEU POVO RUMO À BELA SIÃO. O APÓSTOLO É AQUELE, QUE LEVA A PALAVRA DO SENHOR. TESTEMUNHA PRA MUITOS POVOS, APRESENTA A OBRA COM AMOR. O PROFETA APONTA O CAMINHO, É UM PROCLAMADOR. FALA DO QUE ESTÁ POR VIR, É O INTÉRPRETE DO SENHOR. PARA O EVANGELISTA NÃO HÁ TEMPO RUIM, PREGA A PALAVRA EM TODOS OS LUGARES COM A AUTORIDADE DE JESUS. O PASTOR CUIDA DA IGREJA GUIANDO O REBANHO À SALVAÇÃO. APASCENTA COM CUIDADO, LEVANDO O POVO À ADORAÇÃO! E O MESTRE É AQUELE, O MENOR DOS IRMÃOS, ENSINA A SÃ DOUTRINA, EDUCA COM A REVELAÇÃO. ESTES SÃO OS CINCO MINISTÉRIOS, FORMA SIMPLES E PATERNA DE JESUS ORIENTAR A SUA IGREJA, LEVANDO-A À VIDA ETERNA! LEVANDO-A À VIDA ETERNA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('169', '169 - SETE CARTAS', 'SETE CARTAS JOÃO ESCREVEU, AO ANJO DAS IGREJAS DEUS MANDOU,\n“CONHEÇO AS TUAS OBRAS”, \nASSIM DIZ O SENHOR,\n“QUEM TEM OUVIDOS OUÇA \n A VOZ DO MEU ESPÍRITO”.\n\nÉFESO, ESMIRNA,\nPÉRGAMO, TIATIRA,\nSARDES, FILADÉLFIA \nE LAODICÉIA.\n\nSÃO SETE CARTAS, SETE IGREJAS,\nSETE ESPÍRITOS DE DEUS \nA FALAR NO MEIO DAS IGREJAS.\n\nSÃO SETE CARTAS, SETE IGREJAS,\nSETE ESPÍRITOS DE DEUS                   \nA FALAR NO MEIO DAS IGREJAS.\n\nINSTRUMENTOS\n\nSETE CARTAS JOÃO \nESCREVEU, AO ANJO DAS IGREJAS DEUS MANDOU,\n“CONHEÇO AS TUAS OBRAS”, \nASSIM DIZ O SENHOR,\n“QUEM TEM OUVIDOS OUÇA \n A VOZ DO MEU ESPÍRITO”.\n\nÉFESO, ESMIRNA,\nPÉRGAMO, TIATIRA,\nSARDES, FILADÉLFIA \nE LAODICÉIA.\n\nSÃO SETE CARTAS, SETE IGREJAS,\nSETE ESPÍRITOS DE DEUS                   \nA FALAR NO MEIO DAS IGREJAS.', 'SETE CARTAS JOÃO ESCREVEU, AO ANJO DAS IGREJAS DEUS MANDOU, CONHEÇO AS TUAS OBRAS, ASSIM DIZ O SENHOR, QUEM TEM OUVIDOS OUÇA A VOZ DO MEU ESPÍRITO. ÉFESO, ESMIRNA, PÉRGAMO, TIATIRA, SARDES, FILADÉLFIA E LAODICÉIA. SÃO SETE CARTAS, SETE IGREJAS, SETE ESPÍRITOS DE DEUS A FALAR NO MEIO DAS IGREJAS. SÃO SETE CARTAS, SETE IGREJAS, SETE ESPÍRITOS DE DEUS A FALAR NO MEIO DAS IGREJAS. INSTRUMENTOS', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('170', '170 - NÓS SOMOS A CARTA DE CRISTO', 'NÓS SOMOS A CARTA DE CRISTO,\nA CARTA VIVA DE DEUS,\nPARA SER LIDA POR TODOS,\nREVELAR OS PROPÓSITOS DE DEUS.\n\nO QUE COMUNICA ESSA CARTA?\nO AMOR, O PERDÃO DE JESUS,\nAS PROMESSAS DE VIDA ETERNA\nNO CAMINHO QUE ELE NOS CONDUZ.\n\nNÃO FOI ESCRITA NA PEDRA, INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('171', '171 - SOU O SANTO', 'SOU O SANTO O VERDADEIRO,                                  \nTENHO A CHAVE DE DAVI;                                        \nA PORTA ABERTA ESTÁ,      \nPRA QUE POSSAS ME ADORAR!\n\nGUARDASTE AS MINHAS PALAVRAS,\nNÃO NEGASTE O MEU NOME;\nTAMBÉM EU TE GUARDAREI,\nEIS QUE VENHO SEM DEMORA.\n\nENTÃO, GUARDA O QUE TENS,                  \nPARA QUE NINGUÉM\nTOME A TUA COROA.', 'SOU O SANTO O VERDADEIRO, TENHO A CHAVE DE DAVI; A PORTA ABERTA ESTÁ, PRA QUE POSSAS ME ADORAR! GUARDASTE AS MINHAS PALAVRAS, NÃO NEGASTE O MEU NOME; TAMBÉM EU TE GUARDAREI, EIS QUE VENHO SEM DEMORA. ENTÃO, GUARDA O QUE TENS, PARA QUE NINGUÉM TOME A TUA COROA.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('172', '172 - QUATRO COISAS TÃO PEQUENINAS', 'QUATRO COISAS TÃO PEQUENINAS \nUMA GRANDE LIÇÃO NOS DÃO: \nEMBORA TÃO FRÁGEIS,\nMUITO SÁBIAS SÃO.\n\nAS FORMIGAS SÃO TRABALHADORAS, \nCADA UMA EXERCE A SUA FUNÇÃO;\nÉ COMO A IGREJA DE CRISTO, \nTRABALHANDO PRA \nMANTER A SALVAÇÃO.\n\nEM SEGURANÇA O COELHO CONSTRÓI \nSUA CASA SOBRE A ROCHA;\nE, PARA ESTARMOS BEM SEGUROS,\nCONSTRUÍMOS NA ROCHA QUE É JESUS.\n\nOS GAFANHOTOS TODOS SAEM, \nE EM GRUPOS SE REPARTEM,\nSAÍMOS PROCLAMANDO A PALAVRA, \nSEMPRE UNIDOS, EM UM SÓ CORAÇÃO.\n\nE, NO FINAL DA CAMINHADA,\nHERDEIROS DAS PROMESSAS DE DEUS,\nTAL QUAL A ARANHA MORA NO PALÁCIO, \nMORAREMOS NAS MANSÕES COM JESUS!', 'QUATRO COISAS TÃO PEQUENINAS UMA GRANDE LIÇÃO NOS DÃO: EMBORA TÃO FRÁGEIS, MUITO SÁBIAS SÃO. AS FORMIGAS SÃO TRABALHADORAS, CADA UMA EXERCE A SUA FUNÇÃO; É COMO A IGREJA DE CRISTO, TRABALHANDO PRA MANTER A SALVAÇÃO. EM SEGURANÇA O COELHO CONSTRÓI SUA CASA SOBRE A ROCHA; E, PARA ESTARMOS BEM SEGUROS, CONSTRUÍMOS NA ROCHA QUE É JESUS. OS GAFANHOTOS TODOS SAEM, E EM GRUPOS SE REPARTEM, SAÍMOS PROCLAMANDO A PALAVRA, SEMPRE UNIDOS, EM UM SÓ CORAÇÃO. E, NO FINAL DA CAMINHADA, HERDEIROS DAS PROMESSAS DE DEUS, TAL QUAL A ARANHA MORA NO PALÁCIO, MORAREMOS NAS MANSÕES COM JESUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('173', '173 - OUÇO O CLAMOR DO BOM PASTOR', 'OUÇO O CLAMOR DO BOM PASTOR\nPELO DESERTO ASSOLADOR,\nSEUS CORDEIRINHOS A CHAMAR,\nMUI DESEJOSO DE OS SALVAR.\n\nCORO\nVAI BUSCAR, VAI BUSCAR!\nVAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR!\nTRAZE-OS PARA O MEU APRISCO.\n\nQUEM NÃO DESEJA TRABALHAR\nE OS CORDEIRINHOS SEUS BUSCAR\nE ENCAMINHÁ-LOS A JESUS,\nFONTE DE VIDA, AMOR E LUZ?\n\nCORO\nVAI BUSCAR, VAI BUSCAR!\nVAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR!\nTRAZE-OS PARA O MEU APRISCO.\n\nPELOS DESERTOS A SOFRER,\nOUVE OS SEUS GRITOS                         AO MORRER E O MESTRE CLAMA:\n“VAI BUSCAR!” PRA QUE OS POSSA,                     ENTÃO, SALVAR.\n\nCORO\nVAI BUSCAR, VAI BUSCAR!\nVAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR!\nTRAZE-OS PARA O MEU APRISCO.', 'OUÇO O CLAMOR DO BOM PASTOR PELO DESERTO ASSOLADOR, SEUS CORDEIRINHOS A CHAMAR, MUI DESEJOSO DE OS SALVAR. CORO VAI BUSCAR, VAI BUSCAR! VAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR! TRAZE-OS PARA O MEU APRISCO. QUEM NÃO DESEJA TRABALHAR E OS CORDEIRINHOS SEUS BUSCAR E ENCAMINHÁ-LOS A JESUS, FONTE DE VIDA, AMOR E LUZ? PELOS DESERTOS A SOFRER, OUVE OS SEUS GRITOS AO MORRER E O MESTRE CLAMA: VAI BUSCAR! PRA QUE OS POSSA, ENTÃO, SALVAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('174', '174 - A TUA PALAVRA É SEMENTE', 'A TUA PALAVRA É SEMENTE\nE TU ÉS O SEMEADOR,\nO MEU CORAÇÃO É A TERRA\nQUE TU SEMEASTE, SENHOR.\n\nCORO\nA TUA PALAVRA, A TUA PALAVRA,\nA TUA PALAVRA, SENHOR;\nA TUA PALAVRA, A TUA PALAVRA,\nA TUA PALAVRA É AMOR.\n\nMEDITANDO EU CERTO DIA\nNA TUA PALAVRA, SENHOR,\nSENTI QUE DO ALTO DESCIA\nA GRAÇA DO CONSOLADOR.\n\nCORO\nA TUA PALAVRA, A TUA PALAVRA,\nA TUA PALAVRA, SENHOR;\nA TUA PALAVRA, A TUA PALAVRA,\nA TUA PALAVRA É AMOR.', 'A TUA PALAVRA É SEMENTE E TU ÉS O SEMEADOR, O MEU CORAÇÃO É A TERRA QUE TU SEMEASTE, SENHOR. CORO A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA, SENHOR; A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA É AMOR. MEDITANDO EU CERTO DIA NA TUA PALAVRA, SENHOR, SENTI QUE DO ALTO DESCIA A GRAÇA DO CONSOLADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('175', '175 - UM CERTO PASTORZINHO', 'UM CERTO PASTORZINHO,\nQUE SE CHAMOU DAVI,\nERA MUI PEQUENO,                                NÃO CONFIAVA EM SI;\nELE, COM OUSADIA,                       ORANDO SEMPRE A DEUS,\nFOI ENFRENTAR, UM DIA,                           O HERÓI DOS FILISTEUS.\n\nNA FUNDA UMA PEDRA COLOCOU, \nE GIROU, GIROU, GIROU; \nNA FUNDA UMA PEDRA COLOCOU, \nE GIROU, GIROU, GIROU;\n\nMAIS UMA VEZ, MAIS OUTRA VEZ,\nMAIS UMA VEZ GIROU, \nE A PEDRA ATIROU, \nO ALVO ACERTOU, E O GIGANTE \nAO CHÃO TOMBOU!', 'UM CERTO PASTORZINHO, QUE SE CHAMOU DAVI, ERA MUI PEQUENO, NÃO CONFIAVA EM SI; ELE, COM OUSADIA, ORANDO SEMPRE A DEUS, FOI ENFRENTAR, UM DIA, O HERÓI DOS FILISTEUS. NA FUNDA UMA PEDRA COLOCOU, E GIROU, GIROU, GIROU; NA FUNDA UMA PEDRA COLOCOU, E GIROU, GIROU, GIROU; MAIS UMA VEZ, MAIS OUTRA VEZ, MAIS UMA VEZ GIROU, E A PEDRA ATIROU, O ALVO ACERTOU, E O GIGANTE AO CHÃO TOMBOU!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('176', '176 - CRIA EM MIM, Ó DEUS', 'CRIA EM MIM, Ó DEUS,\nUM CORAÇÃO PURO  \nE RENOVA DENTRO EM MIM \nUM ESPÍRITO RETO.\n\nE RENOVA DENTRO EM MIM\nUM ESPÍRITO RETO.', 'CRIA EM MIM, Ó DEUS, UM CORAÇÃO PURO E RENOVA DENTRO EM MIM UM ESPÍRITO RETO. E RENOVA DENTRO EM MIM UM ESPÍRITO RETO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('177', '177 - FALA AO MEU CORAÇÃO, JESUS', 'FALA AO MEU\nCORAÇÃO, JESUS! \nFALA AO MEU                            CORAÇÃO, JESUS!\n\nFALA, FALA, FALA,              \nFALA AO MEU CORAÇÃO.\n\nVIVE EM MEU \nCORAÇÃO, JESUS!\nVIVE EM MEU \nCORAÇÃO, JESUS! \n\nVIVE, VIVE, VIVE, \nVIVE EM MEU CORAÇÃO.\n\nREINA EM MEU                            CORAÇÃO, JESUS!\nREINA EM MEU                             CORAÇÃO, JESUS!\n\nFALA, VIVE, REINA, \nREINA EM MEU CORAÇÃO.', 'FALA AO MEU CORAÇÃO, JESUS! FALA AO MEU CORAÇÃO, JESUS! FALA, FALA, FALA, FALA AO MEU CORAÇÃO. VIVE EM MEU CORAÇÃO, JESUS! VIVE EM MEU CORAÇÃO, JESUS! VIVE, VIVE, VIVE, VIVE EM MEU CORAÇÃO. REINA EM MEU CORAÇÃO, JESUS! REINA EM MEU CORAÇÃO, JESUS! FALA, VIVE, REINA, REINA EM MEU CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('178', '178 - DISSE JESUS AOS JUDEUS  (EU TRABALHO TAMBÉM)', 'DISSE JESUS AOS JUDEUS,\nASSIM JESUS LHES RESPONDEU:\n“O MEU PAI TRABALHA ATÉ AGORA,\nEU TRABALHO TAMBÉM!\n\nEU TRABALHO TAMBÉM,\nEU TRABALHO TAMBÉM,\nO MEU PAI TRABALHA ATÉ AGORA,\nE EU TRABALHO TAMBÉM!”\n\nCONVÉM QUE EU FAÇA AS MESMAS OBRAS\nDAQUELE QUE ME ENVIOU,\nENQUANTO É DIA, A NOITE VEM QUANDO NÃO SE PODE TRABALHAR.\nO MEU PAI TRABALHA ATÉ AGORA, \nEU TRABALHO TAMBÉM!\n\nQUERO TRABALHAR TAMBÉM,\nQUERO TRABALHAR TAMBÉM,\nCOM O ESPÍRITO SANTO EM MINHA VIDA,\nQUERO TRABALHAR TAMBÉM!', 'DISSE JESUS AOS JUDEUS, ASSIM JESUS LHES RESPONDEU: O MEU PAI TRABALHA ATÉ AGORA, EU TRABALHO TAMBÉM! EU TRABALHO TAMBÉM, EU TRABALHO TAMBÉM, O MEU PAI TRABALHA ATÉ AGORA, E EU TRABALHO TAMBÉM! CONVÉM QUE EU FAÇA AS MESMAS OBRAS DAQUELE QUE ME ENVIOU, ENQUANTO É DIA, A NOITE VEM QUANDO NÃO SE PODE TRABALHAR. O MEU PAI TRABALHA ATÉ AGORA, EU TRABALHO TAMBÉM! QUERO TRABALHAR TAMBÉM, QUERO TRABALHAR TAMBÉM, COM O ESPÍRITO SANTO EM MINHA VIDA, QUERO TRABALHAR TAMBÉM!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('179', '179 - ORAR FAZ BEM', 'ORAR FAZ BEM! \nORAR FAZ BEM!\nFAZ BEM À ALMA,\nFAZ BEM AO CORAÇÃO,\nALEGRA A ALMA,\nALEGRA O CORAÇÃO!\n\nORAR FAZ BEM! \nORAR FAZ BEM! \nO NOSSO DEUS RESPONDE,\nRETIRA A AFLIÇÃO!\nO NOSSO DEUS RESPONDE,\nCONFORTA O CORAÇÃO!', 'ORAR FAZ BEM! ORAR FAZ BEM! FAZ BEM À ALMA, FAZ BEM AO CORAÇÃO, ALEGRA A ALMA, ALEGRA O CORAÇÃO! ORAR FAZ BEM! ORAR FAZ BEM! O NOSSO DEUS RESPONDE, RETIRA A AFLIÇÃO! O NOSSO DEUS RESPONDE, CONFORTA O CORAÇÃO!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('180', '180 - QUERO SER FIEL (JESUS ESTÁ VIVO)', 'QUERO SER FIEL A TI, JESUS.\nFAZER SEMPRE A TUA VONTADE.\nGUARDAR A TUA PALAVRA,\nQUE É LUZ E VIDA \nPARA O MEU CAMINHO.\n\nCORO\nSER FIEL, SER FIEL, \nSER FIEL ATÉ O FIM.\nSER FIEL, SER FIEL \nATÉ MEU JESUS VOLTAR!\n\nQUERO ANDAR \nNA TUA LUZ, SENHOR,\nSÓ ASSIM ESTOU SEGURO;\nQUERO PROCLAMAR EM ALTA VOZ: “JESUS RESSUSCITOU AO TERCEIRO DIA,\nESTÁ VIVO E REINA EM MEU VIVER”.\n\nCORO\nSER FIEL, SER FIEL, \nSER FIEL ATÉ O FIM.\nSER FIEL, SER FIEL \nATÉ MEU JESUS VOLTAR!\n\nJESUS ESTÁ VIVO, \nREINA EM MEU VIVER,\nE EM BREVE VOLTARÁ!', 'QUERO SER FIEL A TI, JESUS. FAZER SEMPRE A TUA VONTADE. GUARDAR A TUA PALAVRA, QUE É LUZ E VIDA PARA O MEU CAMINHO. CORO SER FIEL, SER FIEL, SER FIEL ATÉ O FIM. SER FIEL, SER FIEL ATÉ MEU JESUS VOLTAR! QUERO ANDAR NA TUA LUZ, SENHOR, SÓ ASSIM ESTOU SEGURO; QUERO PROCLAMAR EM ALTA VOZ: JESUS RESSUSCITOU AO TERCEIRO DIA, ESTÁ VIVO E REINA EM MEU VIVER. CORO SER FIEL, SER FIEL, SER FIEL ATÉ O FIM. SER FIEL, SER FIEL ATÉ MEU JESUS VOLTAR! JESUS ESTÁ VIVO, REINA EM MEU VIVER, E EM BREVE VOLTARÁ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('181', '181 - JERUSALÉM, CIDADE DO MEU REI', 'JERUSALÉM, CIDADE DO MEU REI,\nA CADA DIA ANSEIO ESTAR ALI; CONTEMPLAREI A FACE DO SENHOR,\nAQUELE QUE ENTREGOU\nSUA VIDA POR MIM.\n\nJAMAIS A MORTE VEREI\nPOIS VIDA ETERNA ALCANCEI\nQUANDO ENTREGUEI\nMEU CORAÇÃO PARA JESUS.', 'JERUSALÉM, CIDADE DO MEU REI, A CADA DIA ANSEIO ESTAR ALI; CONTEMPLAREI A FACE DO SENHOR, AQUELE QUE ENTREGOU SUA VIDA POR MIM. JAMAIS A MORTE VEREI POIS VIDA ETERNA ALCANCEI QUANDO ENTREGUEI MEU CORAÇÃO PARA JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('182', '182 - QUERO VIVER PARA SEMPRE COM JESUS', 'CORO\nQUERO VIVER                                           PARA SEMPRE COM JESUS,                                     QUERO BEBER DA ÁGUA DA VIDA,\n\nQUERO COMER                  \nDA ÁRVORE DA VIDA, \nCOM JESUS ETERNAMENTE  \nVOU VIVER!\n\nVOU VIVER, SIM, VOU VIVER,\nPASSEAR NO JARDIM DO MEU SENHOR;                               A ÁRVORE DA VIDA NO MEIO DO JARDIM\nÉ CERTEZA QUE JESUS ME PROMETEU.\n\nCORO\nQUERO VIVER    \nPARA SEMPRE COM JESUS,                                     QUERO BEBER DA ÁGUA DA VIDA, \nQUERO COMER DA ÁRVORE DA VIDA, \nCOM JESUS ETERNAMENTE  \nVOU VIVER!\n\nPARA TRÁS O PECADO FICARÁ,                 \nSEU DOMÍNIO NÃO ME ATINGIRÁ; \nESCOLHI MUITO BEM:                        VIVER ETERNAMENTE COM JESUS!\n\nCORO\nQUERO VIVER    \nPARA SEMPRE COM JESUS,                                     QUERO BEBER DA ÁGUA DA VIDA, \nQUERO COMER DA ÁRVORE DA VIDA, \nCOM JESUS ETERNAMENTE  \nVOU VIVER!', 'CORO QUERO VIVER PARA SEMPRE COM JESUS, QUERO BEBER DA ÁGUA DA VIDA, QUERO COMER DA ÁRVORE DA VIDA, COM JESUS ETERNAMENTE VOU VIVER! VOU VIVER, SIM, VOU VIVER, PASSEAR NO JARDIM DO MEU SENHOR; A ÁRVORE DA VIDA NO MEIO DO JARDIM É CERTEZA QUE JESUS ME PROMETEU. CORO QUERO VIVER PARA SEMPRE COM JESUS, QUERO BEBER DA ÁGUA DA VIDA, QUERO COMER DA ÁRVORE DA VIDA, COM JESUS ETERNAMENTE VOU VIVER! PARA TRÁS O PECADO FICARÁ, SEU DOMÍNIO NÃO ME ATINGIRÁ; ESCOLHI MUITO BEM: VIVER ETERNAMENTE COM JESUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('183', '183 - UM NOVO CÉU, UMA NOVA TERRA SE FARÁ', 'UM NOVO CÉU, \nUMA NOVA TERRA SE FARÁ,\nNOVO NOME NUMA \nPEDRA BRANCA RECEBEREI,\n\nNA CIDADE DE LUZ ETERNA IREI MORAR PARA SEMPRE COM O SALVADOR;\nRUAS DE OURO ANDAREI,\nRIO DA VIDA CONTEMPLAREI\n\nE MUITO LOUVOR ALI ENTOAREI,\nNÃO HAVERÁ DOR, \nPRANTO OU CLAMOR,\nTODA LÁGRIMA JESUS \nVAI ENXUGAR.\n\nNÓS SEREMOS O SEU \nPOVO ETERNAMENTE\nE DEUS NOSSO TABERNÁCULO \nPARA SEMPRE;\n\nSE VOCÊ QUISER VIR A CRISTO,\nABRA AGORA O SEU CORAÇÃO\nE DEIXA JESUS FAZER DA SUA VIDA\nMORADA ETERNA DE DEUS.', 'UM NOVO CÉU, UMA NOVA TERRA SE FARÁ, NOVO NOME NUMA PEDRA BRANCA RECEBEREI, NA CIDADE DE LUZ ETERNA IREI MORAR PARA SEMPRE COM O SALVADOR; RUAS DE OURO ANDAREI, RIO DA VIDA CONTEMPLAREI E MUITO LOUVOR ALI ENTOAREI, NÃO HAVERÁ DOR, PRANTO OU CLAMOR, TODA LÁGRIMA JESUS VAI ENXUGAR. NÓS SEREMOS O SEU POVO ETERNAMENTE E DEUS NOSSO TABERNÁCULO PARA SEMPRE; SE VOCÊ QUISER VIR A CRISTO, ABRA AGORA O SEU CORAÇÃO E DEIXA JESUS FAZER DA SUA VIDA MORADA ETERNA DE DEUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('184', '184 - NO EXPRESSO VIAJAMOS', 'NO EXPRESSO VIAJAMOS            PARA O BOM PAÍS DA LUZ,\nÉ O NOSSO PASSAPORTE       \nO NOME DE JESUS;\n\nÉ HORA DA PARTIDA   \nE ENSEJO AINDA TÊM\nAQUELES QUE QUISEREM               TER PASSAGEM NESSE TREM.\n\nBEM-VINDOS SEJAM TODOS\nPARA O TREM QUE PARTE JÁ,\nENTRADA PARA A PÁTRIA \nJESUS A TODOS DÁ;  \nEIS O SINAL DE MARCHA, \nSE QUERES IR TAMBÉM,\nRECEBE O TEU BILHETE \nDE PASSAGEM NESSE TREM.', 'NO EXPRESSO VIAJAMOS PARA O BOM PAÍS DA LUZ, É O NOSSO PASSAPORTE O NOME DE JESUS; É HORA DA PARTIDA E ENSEJO AINDA TÊM AQUELES QUE QUISEREM TER PASSAGEM NESSE TREM. BEM-VINDOS SEJAM TODOS PARA O TREM QUE PARTE JÁ, ENTRADA PARA A PÁTRIA JESUS A TODOS DÁ; EIS O SINAL DE MARCHA, SE QUERES IR TAMBÉM, RECEBE O TEU BILHETE DE PASSAGEM NESSE TREM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('185', '185 - HÁ UMA LINDA MORADA', 'HÁ UMA LINDA MORADA \nPRA MIM NO CÉU\nQUE JESUS FOI ALI PREPARAR;\nUM LUGAR DE DELÍCIAS \nE AMOR SEM FIM,\nCOM JESUS VOU \nPRA SEMPRE MORAR.\n\nNA CIDADE DE DEUS, \nNOVA JERUSALÉM,\nONDE O SOL \nNÃO PRECISA BRILHAR;\nO CORDEIRO É A LUZ \nQUE ILUMINA A CIDADE\nONDE VOU PARA \nSEMPRE MORAR.', 'HÁ UMA LINDA MORADA PRA MIM NO CÉU QUE JESUS FOI ALI PREPARAR; UM LUGAR DE DELÍCIAS E AMOR SEM FIM, COM JESUS VOU PRA SEMPRE MORAR. NA CIDADE DE DEUS, NOVA JERUSALÉM, ONDE O SOL NÃO PRECISA BRILHAR; O CORDEIRO É A LUZ QUE ILUMINA A CIDADE ONDE VOU PARA SEMPRE MORAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('186', '186 - NOSSAS VIDAS SÃO MUDADAS', 'NOSSAS VIDAS SÃO MUDADAS\nPARA ANDARMOS MUI FELIZES,\nQUANDO OUVIMOS A PALAVRA,\nDO NOSSO AMIGO E JUIZ.\n\nELE JULGA A NOSSA CAUSA\nE APAGA A TRANSGRESSÃO,\nDE TODO MAL SOMOS GUARDADOS, \nCAMINHAMOS EM SUA LUZ.\n\nLUZ QUE BRILHA A CADA INSTANTE \nPARA SERMOS IDENTIFICADOS,\nNAQUELE GRANDE DIA,\nE POR JESUS ARREBATADOS.\n\nMARANATA! É O NOSSO GRITO,\nMARANATA! VEM, JESUS!\nEM BREVE A TROMBETA SOARÁ, \nNO CÉU COM ELE QUERO ESTAR.\n\nMARANATA! É O NOSSO GRITO,\nMARANATA! VEM, JESUS!\nEM BREVE A TROMBETA SOARÁ, \nNO CÉU COM ELE QUERO ESTAR.\nNO CÉU COM ELE QUERO ESTAR.', 'NOSSAS VIDAS SÃO MUDADAS PARA ANDARMOS MUI FELIZES, QUANDO OUVIMOS A PALAVRA, DO NOSSO AMIGO E JUIZ. ELE JULGA A NOSSA CAUSA E APAGA A TRANSGRESSÃO, DE TODO MAL SOMOS GUARDADOS, CAMINHAMOS EM SUA LUZ. LUZ QUE BRILHA A CADA INSTANTE PARA SERMOS IDENTIFICADOS, NAQUELE GRANDE DIA, E POR JESUS ARREBATADOS. MARANATA! É O NOSSO GRITO, MARANATA! VEM, JESUS! EM BREVE A TROMBETA SOARÁ, NO CÉU COM ELE QUERO ESTAR. MARANATA! É O NOSSO GRITO, MARANATA! VEM, JESUS! EM BREVE A TROMBETA SOARÁ, NO CÉU COM ELE QUERO ESTAR. NO CÉU COM ELE QUERO ESTAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('187', '187 - VARÕES GALILEUS', 'VARÕES GALILEUS\nNÃO VOS MARAVILHEIS,\nESTE MESMO JESUS\nQUE HOJE VISTES SUBIR\nVOLTARÁ OUTRA VEZ.\n\nVARÕES GALILEUS\nNÃO VOS MARAVILHEIS,\nESTE MESMO JESUS\nQUE HOJE VISTES SUBIR\nVOLTARÁ OUTRA VEZ.\n\nE VOS LEVARÁ\n(E VOS LEVARÁ)\nPARA UM LAR DE PAZ \n(UM LAR DE PAZ),\nA CIDADE DE LUZ \nONDE A DOR CESSARÁ;\n\nPRANTO NÃO TEREIS \n(PRANTO NÃO TEREIS),\nQUAIS OS ANJOS SEREIS \n(ANJOS SEREIS).\nESTE MESMO JESUS \nQUE HOJE VISTES SUBIR\nVOLTARÁ OUTRA VEZ.', 'VARÕES GALILEUS NÃO VOS MARAVILHEIS, ESTE MESMO JESUS QUE HOJE VISTES SUBIR VOLTARÁ OUTRA VEZ. E VOS LEVARÁ (E VOS LEVARÁ) PARA UM LAR DE PAZ (UM LAR DE PAZ), A CIDADE DE LUZ ONDE A DOR CESSARÁ; PRANTO NÃO TEREIS (PRANTO NÃO TEREIS), QUAIS OS ANJOS SEREIS (ANJOS SEREIS). ESTE MESMO JESUS QUE HOJE VISTES SUBIR VOLTARÁ OUTRA VEZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('188', '188 - JERUSALÉM ANSEIO VER', 'JERUSALÉM ANSEIO VER,\nCIDADE SANTA CHEIA DE AMOR,\nOH! COMO É LINDO \nSEU ESPLENDOR;\nA LUZ QUE BRILHA,\nLUZ DO MEU SENHOR;\n\nRUAS DE OURO ANDAREI,\nJUNTO COM OS ANJOS TRILHAREI,\nVEREI A FACE DE JESUS \nE VIVEREI NA SUA LUZ.\n\nIREI MORAR, COM JESUS VOU HABITAR \nEM GRANDE GLÓRIA E ESPLENDOR \nCOM MEU SENHOR;\n\nLÁ NÃO HAVERÁ DOR, \nTRISTEZAS NEM DESILUSÕES,\nSEMPRE ESTAREI BEM PERTINHO \nDO MEU REI.\n\nINSTRUMENTOS\n\nJERUSALÉM ANSEIO VER,\nCIDADE SANTA CHEIA DE AMOR,\nOH! COMO É LINDO\nSEU ESPLENDOR; \nA LUZ QUE BRILHA,  \nLUZ DO MEU SENHOR;\n\nRUAS DE OURO ANDAREI,\nJUNTO COM OS ANJOS TRILHAREI,\nVEREI A FACE DE JESUS \nE VIVEREI NA SUA LUZ.\n\nIREI MORAR, COM JESUS VOU HABITAR \nEM GRANDE GLÓRIA E ESPLENDOR \nCOM MEU SENHOR;\n\nLÁ NÃO HAVERÁ DOR, \nTRISTEZAS NEM DESILUSÕES,\nSEMPRE ESTAREI BEM PERTINHO \nDO MEU REI.\n\nFINAL: SEMPRE ESTAREI BEM \n	PERTINHO DO MEU REI.', 'JERUSALÉM ANSEIO VER, CIDADE SANTA CHEIA DE AMOR, OH! COMO É LINDO SEU ESPLENDOR; A LUZ QUE BRILHA, LUZ DO MEU SENHOR; RUAS DE OURO ANDAREI, JUNTO COM OS ANJOS TRILHAREI, VEREI A FACE DE JESUS E VIVEREI NA SUA LUZ. IREI MORAR, COM JESUS VOU HABITAR EM GRANDE GLÓRIA E ESPLENDOR COM MEU SENHOR; LÁ NÃO HAVERÁ DOR, TRISTEZAS NEM DESILUSÕES, SEMPRE ESTAREI BEM PERTINHO DO MEU REI. INSTRUMENTOS LÁ NÃO HAVERÁ DOR, TRISTEZAS NEM DESILUSÕES, SEMPRE ESTAREI BEM PERTINHO DO MEU REI.  SEMPRE ESTAREI BEM PERTINHO DO MEU REI.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('189', '189 - UM DIA A IGREJA SUBIRÁ', 'UM DIA A IGREJA SUBIRÁ,\nBEM MAIS PERTINHO \nDE JESUS VAMOS FICAR;\nBEM MAIS PERTINHO\nBEM MAIS PERTINHO\nBEM MAIS PERTINHO\nVAMOS JUNTOS CELEBRAR,\n\nBEM MAIS PERTINHO\nBEM MAIS PERTINHO\nBEM MAIS PERTINHO\nDE JESUS VAMOS FICAR.\n\nUM DIA A IGREJA SUBIRÁ,\nBEM MAIS PERTINHO \nDE JESUS VAMOS FICAR;\nBEM MAIS PERTINHO (3X)\nVAMOS JUNTOS CELEBRAR,\nBEM MAIS PERTINHO (3X)\nDE JESUS VAMOS FICAR.', 'UM DIA A IGREJA SUBIRÁ, BEM MAIS PERTINHO DE JESUS VAMOS FICAR; BEM MAIS PERTINHO BEM MAIS PERTINHO BEM MAIS PERTINHO VAMOS JUNTOS CELEBRAR, BEM MAIS PERTINHO BEM MAIS PERTINHO BEM MAIS PERTINHO DE JESUS VAMOS FICAR. UM DIA A IGREJA SUBIRÁ, BEM MAIS PERTINHO DE JESUS VAMOS FICAR; BEM MAIS PERTINHO  VAMOS JUNTOS CELEBRAR, BEM MAIS PERTINHO  DE JESUS VAMOS FICAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('190', '190 - IGREJA, CORPO DE CRISTO', 'IGREJA, CORPO DE CRISTO, \nFOSTE COMPRADA\nCOM O SEU SANGUE,\nA TI EU PERTENÇO,\nJESUS JÁ ME SALVOU.\n\nCORO\nMINHA IGREJA É GLORIOSA,  \nÉ A NOIVA DO SENHOR,\nJESUS VEM, SIM, BUSCÁ-LA,   \nEU VOU COM O SENHOR.\n\nQUERES TU SUBIR TAMBÉM? VEM DEPRESSA PARA JESUS,   \nELE TE ESPERA COM AMOR,   \nVEM, VEM, DEPRESSA VEM.\n\nCORO\nMINHA IGREJA É GLORIOSA,  \nÉ A NOIVA DO SENHOR,\nJESUS VEM, SIM, BUSCÁ-LA,   \nEU VOU COM O SENHOR.\nJESUS VEM, SIM, BUSCÁ-LA, \nORA VEM, SENHOR JESUS.', 'IGREJA, CORPO DE CRISTO, FOSTE COMPRADA COM O SEU SANGUE, A TI EU PERTENÇO, JESUS JÁ ME SALVOU. CORO MINHA IGREJA É GLORIOSA, É A NOIVA DO SENHOR, JESUS VEM, SIM, BUSCÁ-LA, EU VOU COM O SENHOR. QUERES TU SUBIR TAMBÉM? VEM DEPRESSA PARA JESUS, ELE TE ESPERA COM AMOR, VEM, VEM, DEPRESSA VEM. CORO MINHA IGREJA É GLORIOSA, É A NOIVA DO SENHOR, JESUS VEM, SIM, BUSCÁ-LA, EU VOU COM O SENHOR. JESUS VEM, SIM, BUSCÁ-LA, ORA VEM, SENHOR JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('191', '191 - A NOIVA AMADA DO CORDEIRO', 'A NOIVA AMADA DO CORDEIRO\nESTÁ SENDO PREPARADA\nPARA O ENCONTRO COM SEU NOIVO\nNA LINDA FESTA NO CÉU.\n\nCORO\nDIANTE DO TRONO DO CORDEIRO\nEXPRESSARÁ TODO AMOR; \nALELUIA! HONRA E GLÓRIA\nAO NOSSO SALVADOR!\n\nSEGUE O CORDEIRO\nPARA ONDE ELE VAI,\nLÁ ESTÁ ELA, TÃO FORMOSA!\nPREPARADA JÁ ESTÁ.\n\nCORO\nDIANTE DO TRONO DO CORDEIRO\nEXPRESSARÁ TODO AMOR; \nALELUIA! HONRA E GLÓRIA\nAO NOSSO SALVADOR!\n\nCANTARÁ UM CANTO NOVO\nE SÓ ELA SABERÁ  \nPOIS SOMENTE OS REMIDOS\nESTE CANTO ENTOARÃO.\n\nCORO\nDIANTE DO TRONO DO CORDEIRO\nEXPRESSARÁ TODO AMOR; \nALELUIA! HONRA E GLÓRIA\nAO NOSSO SALVADOR!', 'A NOIVA AMADA DO CORDEIRO ESTÁ SENDO PREPARADA PARA O ENCONTRO COM SEU NOIVO NA LINDA FESTA NO CÉU. CORO DIANTE DO TRONO DO CORDEIRO EXPRESSARÁ TODO AMOR; ALELUIA! HONRA E GLÓRIA AO NOSSO SALVADOR! SEGUE O CORDEIRO PARA ONDE ELE VAI, LÁ ESTÁ ELA, TÃO FORMOSA! PREPARADA JÁ ESTÁ. CANTARÁ UM CANTO NOVO E SÓ ELA SABERÁ POIS SOMENTE OS REMIDOS ESTE CANTO ENTOARÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('192', '192 - VOU PRA JERUSALÉM', 'VOU PRA JERUSALÉM,      \nA CIDADE DO MEU REI.   \nALI VEREI JESUS,\nMEU SALVADOR,                \nO FILHO DE DEUS.\n\nVOU PRA JERUSALÉM.\nEM RUAS DE OURO SEI \nQUE ANDAREI,\nDA FONTE CRISTALINA,\nLÁ DESFRUTAREI\nE A GLÓRIA ETERNA \nDE DEUS ME ILUMINARÁ.\n\nPRA SEMPRE ESTAREI\nNA ETERNA MANSÃO,\nCOM OS ANJOS, \nADORANDO O SALVADOR\nQUE EM MEU LUGAR MORREU,\nMAS VIVO ELE É,\n\nSENTADO ESTÁ NO TRONO \nCOM PODER E GLÓRIA, \nHONRA E LOUVOR, POR ISSO \nVOU PRA JERUSALÉM.\nVOU PRA JERUSALÉM.\nVOU PRA JERUSALÉM.', 'VOU PRA JERUSALÉM, A CIDADE DO MEU REI. ALI VEREI JESUS, MEU SALVADOR, O FILHO DE DEUS. VOU PRA JERUSALÉM. EM RUAS DE OURO SEI QUE ANDAREI, DA FONTE CRISTALINA, LÁ DESFRUTAREI E A GLÓRIA ETERNA DE DEUS ME ILUMINARÁ. PRA SEMPRE ESTAREI NA ETERNA MANSÃO, COM OS ANJOS, ADORANDO O SALVADOR QUE EM MEU LUGAR MORREU, MAS VIVO ELE É, SENTADO ESTÁ NO TRONO COM PODER E GLÓRIA, HONRA E LOUVOR, POR ISSO VOU PRA JERUSALÉM. VOU PRA JERUSALÉM. VOU PRA JERUSALÉM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('193', '193 - HÁ UMA IGREJA', 'HÁ UMA IGREJA QUE AGUARDA O SENHOR,\nPOIS CONFIA NO SEU GRANDE AMOR \nQUE JESUS POR NÓS DEMONSTROU: \nPOR NOSSA VIDA UM PREÇO PAGOU!\n\nCORO\nVEM, SENHOR, TUA IGREJA TE ESPERA.INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('194', '194 - NA NOVA JERUSALÉM', 'NA NOVA JERUSALÉMINSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('195', '195 - EM BREVE ESTAREMOS NA GLÓRIA', 'EM BREVE ESTAREMOS NA GLÓRIA, \nA CANTAR ALELUIA AO CORDEIRO,\nAGUARDAMOS DIA E NOITE\nOUVIR O TOQUE \nDA TROMBETA DE DEUS ECOAR.\n\nVAMOS TER UMA GRANDE REUNIÃO\nNO CÉU COM O SALVADOR.\nO MOMENTO SE APROXIMA\nE A NOIVA SE APRONTA, \nAGUARDA O CHAMADO DO SENHOR.\n\nFELIZES SERVOS CHAMADOS\nPARA A GRANDE FESTA \nQUE ACONTECERÁ BREVEMENTE: \nSERÃO AS BODAS DO CORDEIRO.\n\nFELIZES SERVOS CHAMADOS\nPARA A GRANDE FESTA \nQUE ACONTECERÁ BREVEMENTE: \nSERÃO AS BODAS DO CORDEIRO. (2X)', 'EM BREVE ESTAREMOS NA GLÓRIA, A CANTAR ALELUIA AO CORDEIRO, AGUARDAMOS DIA E NOITE OUVIR O TOQUE DA TROMBETA DE DEUS ECOAR. VAMOS TER UMA GRANDE REUNIÃO NO CÉU COM O SALVADOR. O MOMENTO SE APROXIMA E A NOIVA SE APRONTA, AGUARDA O CHAMADO DO SENHOR. FELIZES SERVOS CHAMADOS PARA A GRANDE FESTA QUE ACONTECERÁ BREVEMENTE: SERÃO AS BODAS DO CORDEIRO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('196', '196 - JESUS NOS PROMETEU', 'JESUS NOS PROMETEU\nQUE VIRÁ NOS BUSCAR.\nESTE MUNDO PASSARÁ,\nTODO MAL FINDARÁ.\n\nCORO\nO TOQUE DA TROMBETA OUVIREMOS,\nO ANJO DO SENHOR A TOCARÁ.\nNAS NUVENS CELESTIAIS\nIREMOS ENCONTRAR JESUS.\n\nQUE GOZO E ALEGRIA HAVERÁ.\nA NOIVA DO SENHOR \nARREBATADA IRÁ. \nCOM JESUS PARA SEMPRE ESTARÁ.\n\nCORO\nO TOQUE DA TROMBETA OUVIREMOS,\nO ANJO DO SENHOR A TOCARÁ.\nNAS NUVENS CELESTIAIS\nIREMOS ENCONTRAR JESUS.', 'JESUS NOS PROMETEU QUE VIRÁ NOS BUSCAR. ESTE MUNDO PASSARÁ, TODO MAL FINDARÁ. CORO O TOQUE DA TROMBETA OUVIREMOS, O ANJO DO SENHOR A TOCARÁ. NAS NUVENS CELESTIAIS IREMOS ENCONTRAR JESUS. QUE GOZO E ALEGRIA HAVERÁ. A NOIVA DO SENHOR ARREBATADA IRÁ. COM JESUS PARA SEMPRE ESTARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('197', '197 - MARANATA! VEM SENHOR JESUS', 'MARANATA!                                          VEM, SENHOR JESUS,\nVEM BUSCAR TUA IGREJA.\nMARANATA! \nVEM, SENHOR JESUS,\nVEM BUSCAR TUA IGREJA FIEL.\n\nPOR TEU SANGUE ELA FOI COMPRADA,\nRESGATADA POR TEU GRANDE AMOR\nE PELO ESPÍRITO ESTÁ SENDO ATAVIADA,\nSE PREPARANDO PARA AS BODAS \nCOM O SENHOR.\n\nMARANATA!                                          VEM, SENHOR JESUS,\nVEM BUSCAR TUA IGREJA.\nMARANATA! \nVEM, SENHOR JESUS,\nVEM BUSCAR TUA IGREJA FIEL.\n\nPOR TEU SANGUE ELA FOI COMPRADA,\nRESGATADA POR TEU GRANDE AMOR\nE PELO ESPÍRITO ESTÁ SENDO ATAVIADA,\nSE PREPARANDO PARA AS BODAS \nCOM O SENHOR.\n\nMARANATA! \nVEM, SENHOR JESUS,\nVEM BUSCAR TUA IGREJA.\nMARANATA!\nVEM, SENHOR JESUS,\nVEM BUSCAR TUA IGREJA.\nVEM BUSCAR TUA IGREJA FIEL.', 'MARANATA! VEM, SENHOR JESUS, VEM BUSCAR TUA IGREJA. MARANATA! VEM, SENHOR JESUS, VEM BUSCAR TUA IGREJA FIEL. POR TEU SANGUE ELA FOI COMPRADA, RESGATADA POR TEU GRANDE AMOR E PELO ESPÍRITO ESTÁ SENDO ATAVIADA, SE PREPARANDO PARA AS BODAS COM O SENHOR. MARANATA! VEM, SENHOR JESUS, VEM BUSCAR TUA IGREJA. MARANATA! VEM, SENHOR JESUS, VEM BUSCAR TUA IGREJA. VEM BUSCAR TUA IGREJA FIEL.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('198', '198 - O DIA ESTÁ CHEGANDO', 'O DIA ESTÁ CHEGANDO, \nAS HORAS ESTÃO PASSANDO,\nO DIA JÁ VAI CHEGAR\nE JESUS VAI VOLTAR!\n\nO TEMPO SE APRESSA, \nO AMOR SE ESFRIANDO, \nPESSOAS NÃO SE FALANDO. \nMAS JESUS VAI VOLTAR!\n\nPOIS ASSIM SERÁ                      QUANDO JESUS VOLTAR.\nASSIM SERÁ QUANDO                    JESUS VOLTAR.\nVAI VOLTAR, VAI VOLTAR!\n\nINSTRUMENTOS\n\nMENSAGENS REVELADAS, \nESCRITAS POR JESUS,\nPARA NOS PREPARAR,\nPORQUE JESUS VAI VOLTAR.\nPREPARANDO OS CORAÇÕES \nPARA JESUS ENCONTRAR,\nJESUS, MEU BOM PASTOR, \nQUE ME FAZ VENCEDOR.\n\nESTAMOS PREPARANDO, \nESTAMOS PREPARANDO \nOS CORAÇÕES, \nESTAMOS PREPARANDO, \nPREPARANDO OS CORAÇÕES.\nPORQUE JESUS VAI VOLTAR, \nVAI VOLTAR, VAI VOLTAR!\nVAI VOLTAR!', 'O DIA ESTÁ CHEGANDO, AS HORAS ESTÃO PASSANDO, O DIA JÁ VAI CHEGAR E JESUS VAI VOLTAR! O TEMPO SE APRESSA, O AMOR SE ESFRIANDO, PESSOAS NÃO SE FALANDO. MAS JESUS VAI VOLTAR! POIS ASSIM SERÁ QUANDO JESUS VOLTAR. ASSIM SERÁ QUANDO JESUS VOLTAR. VAI VOLTAR, VAI VOLTAR! INSTRUMENTOS MENSAGENS REVELADAS, ESCRITAS POR JESUS, PARA NOS PREPARAR, PORQUE JESUS VAI VOLTAR. PREPARANDO OS CORAÇÕES PARA JESUS ENCONTRAR, JESUS, MEU BOM PASTOR, QUE ME FAZ VENCEDOR. ESTAMOS PREPARANDO, ESTAMOS PREPARANDO OS CORAÇÕES, ESTAMOS PREPARANDO, PREPARANDO OS CORAÇÕES. PORQUE JESUS VAI VOLTAR, VAI VOLTAR, VAI VOLTAR! VAI VOLTAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('199', '199 - AGUARDANDO A ORIENTAÇÃO DO SALVADOR', 'AGUARDANDO A ORIENTAÇÃO \nDO SALVADOR,\nCOM JESUS MARCHAMOS, \nSEGUINDO SEM PARAR.\n\nA BANDEIRA DA VITÓRIA \nCONOSCO ESTÁ.\nOH! GLÓRIA, HOSANA AO REI!\nJESUS VENCENDO VEM!\n\nPELA FÉ AVISTAMOS NOSSO LAR,\nANSIOSOS PARA O ENCONTRO \nCOM JESUS.\nTODA LÁGRIMA ELE ENXUGARÁ.\nOH! GLÓRIA, HOSANA AO REI!\nJESUS VENCENDO VEM!\n\nINSTRUMENTOS\n\nCOM OS ANJOS \nCANTAREMOS LÁ NO CÉU\nLOUVORES AO CORDEIRO \nQUE VENCEU.\nA COROA ELE NOS DARÁ.\n\nOH! GLÓRIA! \n(GLÓRIA, GLÓRIA, GLÓRIA)\nJESUS VENCENDO VEM! \nGLÓRIA, JESUS VENCENDO VEM!', 'AGUARDANDO A ORIENTAÇÃO DO SALVADOR, COM JESUS MARCHAMOS, SEGUINDO SEM PARAR. A BANDEIRA DA VITÓRIA CONOSCO ESTÁ. OH! GLÓRIA, HOSANA AO REI! JESUS VENCENDO VEM! PELA FÉ AVISTAMOS NOSSO LAR, ANSIOSOS PARA O ENCONTRO COM JESUS. TODA LÁGRIMA ELE ENXUGARÁ. OH! GLÓRIA, HOSANA AO REI! JESUS VENCENDO VEM! INSTRUMENTOS COM OS ANJOS CANTAREMOS LÁ NO CÉU LOUVORES AO CORDEIRO QUE VENCEU. A COROA ELE NOS DARÁ. OH! GLÓRIA! (GLÓRIA, GLÓRIA, GLÓRIA) JESUS VENCENDO VEM! GLÓRIA, JESUS VENCENDO VEM!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('200', '200 - VOU PARA O MEU LAR', 'CORO\nVOU PARA O MEU LAR, \nJESUS JÁ PREPAROU \nO MEU LUGAR.\nVOU PARA O MEU LAR, \nJESUS JÁ PREPAROU \nO MEU LUGAR.\n\nESTÁ CHEGANDO \nA HORA DA PARTIDA,\nJESUS JÁ VEM ME BUSCAR.\nA TROMBETA VAI ECOAR\nE VOU PARA O MEU LAR.\n\nCORO\nVOU PARA O MEU LAR, \nJESUS JÁ PREPAROU \nO MEU LUGAR.\nVOU PARA O MEU LAR, \nJESUS JÁ PREPAROU \nO MEU LUGAR.\n\nMINHA CANDEIA ESTÁ BEM ACESA\nE ÓLEO NA BOTIJA TAMBÉM HÁ.\nA TROMBETA VAI ECOAR\nE VOU PARA O MEU LAR.\n\nCORO\nVOU PARA O MEU LAR, \nJESUS JÁ PREPAROU \nO MEU LUGAR.\nVOU PARA O MEU LAR, \nJESUS JÁ PREPAROU \nO MEU LUGAR.', 'CORO VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR. VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR. ESTÁ CHEGANDO A HORA DA PARTIDA, JESUS JÁ VEM ME BUSCAR. A TROMBETA VAI ECOAR E VOU PARA O MEU LAR. MINHA CANDEIA ESTÁ BEM ACESA E ÓLEO NA BOTIJA TAMBÉM HÁ. A TROMBETA VAI ECOAR E VOU PARA O MEU LAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('201', '201 - SOBRE AS NUVENS JESUS VEM', 'SOBRE AS NUVENS JESUS VEM \nCOMO REI E VENCEDOR,\nSUA IGREJA VEM BUSCAR\nCUMPRINDO SUA PROMESSA \nAO SEU POVO.\n\nQUE ALEGRIA VER NOSSO REI.\nMARANATA, CANTAREMOS COM FERVOR,\nPOIS SEU BRADO É DE VITÓRIA,                    \nALELUIA, HONRA E GLÓRIA\nLHE DAREMOS COM AMOR.\n\nCOMO IGREJA FIEL,\nTODOS UNIDOS A UMA VOZ,\nCANTAREMOS JESUS \nVEM VENCEDOR!\nJESUS VEM VENCEDOR!\nJESUS VEM VENCEDOR!', 'SOBRE AS NUVENS JESUS VEM COMO REI E VENCEDOR, SUA IGREJA VEM BUSCAR CUMPRINDO SUA PROMESSA AO SEU POVO. QUE ALEGRIA VER NOSSO REI. MARANATA, CANTAREMOS COM FERVOR, POIS SEU BRADO É DE VITÓRIA, ALELUIA, HONRA E GLÓRIA LHE DAREMOS COM AMOR. COMO IGREJA FIEL, TODOS UNIDOS A UMA VOZ, CANTAREMOS JESUS VEM VENCEDOR! JESUS VEM VENCEDOR! JESUS VEM VENCEDOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('202', '202 - A RAABE VEIO A ORDEM', 'A RAABE VEIO A ORDEM:\nNA JANELA DE SUA CASA \nCOLOCAR, UM CORDÃO \nDE FIO DE ESCARLATA \nPRA DA MORTE SE LIVRAR.\n\nELA CREU E OBEDECEU.INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('203', '203 - O FIO DE ESCARLATA O MISTÉRIO', 'O FIO DE ESCARLATA É O MISTÉRIO, DA FÉ E DA GRAÇA DO SENHOR.\nFOI REVELADO A NÓS E NOS LIGA \nÀ PRESENÇA DE DEUS, O SENHOR.\n\nCORO\nSEGUINDO O FIO DE ESCARLATA       \nVEREMOS MARANATA.\nORA VEM, SENHOR JESUS!\n\nA IGREJA TRIUNFANTE, \nENTOANDO UM NOVO CANTO, \nSUBIRÁ PRA MORAR COM JESUS.\n\nESTE FIO DE ESCARLATA REPRESENTA\nO SANGUE DE JESUS: \nNOVA ALIANÇA, VÉU RASGADO, \nNOVO CAMINHO PARA O CÉU.\n\nCORO\nSEGUINDO O FIO DE ESCARLATA       \nVEREMOS MARANATA.\nORA VEM, SENHOR JESUS!\n\nFINAL:\nA IGREJA TRIUNFANTE, \nENTOANDO UM NOVO CANTO, \nSUBIRÁ PRA MORAR COM JESUS.', 'O FIO DE ESCARLATA É O MISTÉRIO, DA FÉ E DA GRAÇA DO SENHOR. FOI REVELADO A NÓS E NOS LIGA À PRESENÇA DE DEUS, O SENHOR. CORO SEGUINDO O FIO DE ESCARLATA VEREMOS MARANATA. ORA VEM, SENHOR JESUS! A IGREJA TRIUNFANTE, ENTOANDO UM NOVO CANTO, SUBIRÁ PRA MORAR COM JESUS. ESTE FIO DE ESCARLATA REPRESENTA O SANGUE DE JESUS: NOVA ALIANÇA, VÉU RASGADO, NOVO CAMINHO PARA O CÉU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('204', '204 - “MARCHAI! MARCHAI!”', 'CORO\n“MARCHAI!  MARCHAI!”\nFIRME, SEGURA, \nCONFIANTE VAI,\nA IGREJA ADORNADA, \nPREPARADA, AO ENCONTRO \nDE SEU NOIVO VAI.\n\nEIS O MOMENTO! ESTAMOS ATENTOS!\nVIGILANTES, EM CONSTANTE ADORAÇÃO.\nJESUS NAS NUVENS DESCERÁ, \nSUA IGREJA LEVARÁ\nPARA A NOVA JERUSALÉM, ETERNO LAR.\n\nCORO\n“MARCHAI!  MARCHAI!”\nFIRME, SEGURA, \nCONFIANTE VAI,\nA IGREJA ADORNADA, \nPREPARADA, AO ENCONTRO \nDE SEU NOIVO VAI.\n\nO FIO DE ESCARLATA NOS GARANTE\nAS MORADAS PREPARADAS POR JESUS.\nTUDO ESTÁ PRONTO! CANDEIA ACESA! \nSOMOS O POVO QUE O ESPÍRITO CONDUZ.\n\nINSTRUMENTOS\n\nCORO\n“MARCHAI!  MARCHAI!”\nFIRME, SEGURA, \nCONFIANTE VAI,\nA IGREJA ADORNADA, \nPREPARADA, AO ENCONTRO \nDE SEU NOIVO VAI.', 'CORO MARCHAI! MARCHAI! FIRME, SEGURA, CONFIANTE VAI, A IGREJA ADORNADA, PREPARADA, AO ENCONTRO DE SEU NOIVO VAI. EIS O MOMENTO! ESTAMOS ATENTOS! VIGILANTES, EM CONSTANTE ADORAÇÃO. JESUS NAS NUVENS DESCERÁ, SUA IGREJA LEVARÁ PARA A NOVA JERUSALÉM, ETERNO LAR. O FIO DE ESCARLATA NOS GARANTE AS MORADAS PREPARADAS POR JESUS. TUDO ESTÁ PRONTO! CANDEIA ACESA! SOMOS O POVO QUE O ESPÍRITO CONDUZ. INSTRUMENTOS', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('205', '205 - A MINHA ALMA ESPERA', 'A MINHA ALMA ESPERA,\nNO SENHOR ESPERA \nE JUNTOS VAMOS ADORAR,\nO GRANDE REI SENHOR,\nO NOSSO EMANUEL.\nE JUNTOS VAMOS ADORAR.\n\nVEM SENHOR! A TUA IGREJA \nESPERA E NÃO VAI TARDAR.\nA NOIVA PREPARADA ESTÁ \nE A IGREJA SUBIRÁ.\n\nA NOIVA PREPARADA ESTÁ\nE A IGREJA SUBIRÁ.\n\nINSTRUMENTOS\n\nA MINHA ALMA ESPERA,\nNO SENHOR ESPERA \nE JUNTOS VAMOS ADORAR,\nO GRANDE REI SENHOR,\nO NOSSO EMANUEL.\nE JUNTOS VAMOS ADORAR.\n\nVEM SENHOR! A TUA IGREJA \nESPERA E NÃO VAI TARDAR.\nA NOIVA PREPARADA ESTÁ \nE A IGREJA SUBIRÁ.\n\nA NOIVA PREPARADA ESTÁ\nE A IGREJA SUBIRÁ.', 'A MINHA ALMA ESPERA, NO SENHOR ESPERA E JUNTOS VAMOS ADORAR, O GRANDE REI SENHOR, O NOSSO EMANUEL. E JUNTOS VAMOS ADORAR. VEM SENHOR! A TUA IGREJA ESPERA E NÃO VAI TARDAR. A NOIVA PREPARADA ESTÁ E A IGREJA SUBIRÁ. A NOIVA PREPARADA ESTÁ E A IGREJA SUBIRÁ. INSTRUMENTOS VEM SENHOR! A TUA IGREJA ESPERA E NÃO VAI TARDAR. A NOIVA PREPARADA ESTÁ E A IGREJA SUBIRÁ. A NOIVA PREPARADA ESTÁ E A IGREJA SUBIRÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('206', '206 - NO CÉU UM DIA NÓS VAMOS MORAR', 'NO CÉU UM DIA NÓS VAMOS MORAR\nCOM JESUS, O BOM PASTOR, \nFELIZES SEREMOS.\n\nQUANTA ALEGRIA, \nMUITOS LOUVORES.\nCANTAREMOS AO SENHOR,\nCANTAREMOS AO SENHOR, \nCANTAREMOS AO SENHOR,  \nNOSSO SALVADOR!\n\nNO CÉU UM DIA NÓS VAMOS MORAR\nCOM JESUS, O BOM PASTOR, \nFELIZES SEREMOS.\n\nQUANTA ALEGRIA, \nMUITOS LOUVORES.\nCANTAREMOS AO SENHOR,\nCANTAREMOS AO SENHOR, \nCANTAREMOS AO SENHOR,  \nNOSSO SALVADOR!\nNOSSO SALVADOR!', 'NO CÉU UM DIA NÓS VAMOS MORAR COM JESUS, O BOM PASTOR, FELIZES SEREMOS. QUANTA ALEGRIA, MUITOS LOUVORES. CANTAREMOS AO SENHOR, CANTAREMOS AO SENHOR, CANTAREMOS AO SENHOR, NOSSO SALVADOR! QUANTA ALEGRIA, MUITOS LOUVORES. CANTAREMOS AO SENHOR, CANTAREMOS AO SENHOR, CANTAREMOS AO SENHOR, NOSSO SALVADOR! NOSSO SALVADOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('207', '207 - JESUS PROMETEU NOS BUSCAR', 'JESUS PROMETEU NOS BUSCAR\nARREBATAR SUA IGREJA FIEL\nCOM ELE IREMOS MORAR\nHABITAR LÁ NO CÉU\nNAS RUAS DE OURO \nIREMOS ANDAR.\n\nCORO\nOH! QUANTO ANSEIO ESTAR ALI\nVER FACE A FACE O MEU REI\nENFIM, VOU ADORAR JESUS.\n\nNÃO HÁ ALI TRISTEZA OU DOR, NEM MORTE, NEM PRANTO OU CLAMOR \nSÓ HAVERÁ SEU AMOR\nPOIS CONOSCO ESTARÁ \nCOM O CORDEIRO IREMOS AO LAR.\n\nCORO\nOH! QUANTO ANSEIO ESTAR ALI\nVER FACE A FACE O MEU REI\nENFIM, VOU ADORAR JESUS.\n\nESSE DIA ESTÁ PRA CHEGAR \nA TROMBETA ESTÁ PRA SOAR \nQUEM VENCER TUDO HERDARÁ \nCOM JESUS REINARÁ AO CORDEIRO\nIREMOS LOUVAR.\n\nCORO\nOH! QUANTO ANSEIO ESTAR ALI \nVER FACE A FACE  O MEU REI\nENFIM, VOU ADORAR JESUS.\nENFIM, VOU CONTEMPLAR JESUS. \nENFIM, VOU ABRAÇAR JESUS.', 'JESUS PROMETEU NOS BUSCAR ARREBATAR SUA IGREJA FIEL COM ELE IREMOS MORAR HABITAR LÁ NO CÉU NAS RUAS DE OURO IREMOS ANDAR. CORO OH! QUANTO ANSEIO ESTAR ALI VER FACE A FACE O MEU REI ENFIM, VOU ADORAR JESUS. NÃO HÁ ALI TRISTEZA OU DOR, NEM MORTE, NEM PRANTO OU CLAMOR SÓ HAVERÁ SEU AMOR POIS CONOSCO ESTARÁ COM O CORDEIRO IREMOS AO LAR. ESSE DIA ESTÁ PRA CHEGAR A TROMBETA ESTÁ PRA SOAR QUEM VENCER TUDO HERDARÁ COM JESUS REINARÁ AO CORDEIRO IREMOS LOUVAR. CORO OH! QUANTO ANSEIO ESTAR ALI VER FACE A FACE O MEU REI ENFIM, VOU ADORAR JESUS. ENFIM, VOU CONTEMPLAR JESUS. ENFIM, VOU ABRAÇAR JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('208', '208 - EU QUERO IR PARA O CÉU', 'EU QUERO IR PARA O CÉU\nVER O MEU SALVADOR.\nQUE ME RESGATOU,  \nSEU SANGUE ME COMPROU\n\nEU QUERO IR PARA O CÉU\nVIVER COM MEU JESUS \nANDAR NA SUA LUZ, \nPASSEAR NO SEU JARDIM.\n\nEU QUERO IR PARA O CÉU\nNAS RUAS DE OURO ANDAR\nVER AS BELAS MANSÕES,\nSUA GLÓRIA CONTEMPLAR\n\nINSTRUMENTOS\n\nEU QUERO IR PARA O CÉU\nMORAR COM MEU SENHOR \nDA ÁRVORE DA VIDA\nEU QUERO DESFRUTAR,\n\nADORAR O MEU JESUS, \nCOM OS ANJOS VOU CANTAR \nADORAR O MEU JESUS, \nCOM OS ANJOS VOU CANTAR', 'EU QUERO IR PARA O CÉU VER O MEU SALVADOR. QUE ME RESGATOU, SEU SANGUE ME COMPROU EU QUERO IR PARA O CÉU VIVER COM MEU JESUS ANDAR NA SUA LUZ, PASSEAR NO SEU JARDIM. EU QUERO IR PARA O CÉU NAS RUAS DE OURO ANDAR VER AS BELAS MANSÕES, SUA GLÓRIA CONTEMPLAR INSTRUMENTOS EU QUERO IR PARA O CÉU MORAR COM MEU SENHOR DA ÁRVORE DA VIDA EU QUERO DESFRUTAR, ADORAR O MEU JESUS, COM OS ANJOS VOU CANTAR ADORAR O MEU JESUS, COM OS ANJOS VOU CANTAR', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('209', '209 - SE EU FALO', 'SE EU FALO DO GRANDE\nAMOR DO PAI\nGRANDES BÊNÇÃOS \nPODEREI CONTAR.\nSE EU CANTO AS \nMARAVILHAS QUE ELE FAZ,\nO LOUVAREI ETERNAMENTE.\n\nGRANDE É O AMOR DO PAI, MARAVILHAS ELE FAZ\nQUANDO A IGREJA CANTA \nE ORA SEM CESSAR.\nSANTO, JUSTO E FIEL, \nSUA PROMESSA CUMPRIRÁ\nBREVE VEM NOS BUSCAR!\n\nMARANATA, JESUS VEM! \nMARANATA, JESUS VEM! \nBREVE VEM NOS BUSCAR! \nMARANATA, JESUS VEM! \nMARANATA, JESUS VEM! \nBREVE VEM NOS BUSCAR!\nBREVE VEM NOS BUSCAR!\nBREVE VEM NOS BUSCAR!', 'SE EU FALO DO GRANDE AMOR DO PAI GRANDES BÊNÇÃOS PODEREI CONTAR. SE EU CANTO AS MARAVILHAS QUE ELE FAZ, O LOUVAREI ETERNAMENTE. GRANDE É O AMOR DO PAI, MARAVILHAS ELE FAZ QUANDO A IGREJA CANTA E ORA SEM CESSAR. SANTO, JUSTO E FIEL, SUA PROMESSA CUMPRIRÁ BREVE VEM NOS BUSCAR! MARANATA, JESUS VEM! MARANATA, JESUS VEM! BREVE VEM NOS BUSCAR! MARANATA, JESUS VEM! MARANATA, JESUS VEM! BREVE VEM NOS BUSCAR! BREVE VEM NOS BUSCAR! BREVE VEM NOS BUSCAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('210', '210 - JESUS VOLTARÁ PRA ME BUSCAR! (MARANATA)', 'JESUS VOLTARÁ PRA ME BUSCAR!\nO MEU CORAÇÃO ANSEIA\nPOR ESSSE ENCONTRO TÃO ESPECIAL, \nONDE TODA LÁGRIMA\nELE VAI ENXUGAR.\n\nA MINHA CANDEIA ESTÁ ACESA\nE O ÓLEO NA BOTIJA TAMBÉM HÁ.\nPOR ISSO EU CLAMO.\nPOR ISSO EU CLAMO.\n\nMARANATA! VEM, SENHOR JESUS! \nMARANATA! VEM, SENHOR JESUS!     \nA TUA IGREJA TE ESPERA, \nA TUA IGREJA PROCLAMA:\nVEM, SIM, VEM JESUS.\n\nCORO \nMARANATA É O GRITO DA IGREJA,\nMARANATA É UMA FORMA DE VIDA,\nMARANATA É O DESPERTAR DO SONO,\nÉ A ESPERANÇA DA ALMA REMIDA.\n\nINSTRUMENTOS\n\nPOR ISSO EU CLAMO.\nPOR ISSO EU CLAMO.\nBIS\n\nCORO \nMARANATA É O GRITO DA IGREJA,\nMARANATA É UMA FORMA DE VIDA,\nMARANATA É O DESPERTAR DO SONO,\nÉ A ESPERANÇA DA ALMA REMIDA.\n\nELE VEM! MARANATA! \nJESUS VEM! MARANATA!\nELE VEM! MARANATA! \nJESUS VEM! MARANATA!', 'JESUS VOLTARÁ PRA ME BUSCAR! O MEU CORAÇÃO ANSEIA POR ESSSE ENCONTRO TÃO ESPECIAL, ONDE TODA LÁGRIMA ELE VAI ENXUGAR. A MINHA CANDEIA ESTÁ ACESA E O ÓLEO NA BOTIJA TAMBÉM HÁ. POR ISSO EU CLAMO. POR ISSO EU CLAMO. MARANATA! VEM, SENHOR JESUS! MARANATA! VEM, SENHOR JESUS! A TUA IGREJA TE ESPERA, A TUA IGREJA PROCLAMA: VEM, SIM, VEM JESUS. CORO MARANATA É O GRITO DA IGREJA, MARANATA É UMA FORMA DE VIDA, MARANATA É O DESPERTAR DO SONO, É A ESPERANÇA DA ALMA REMIDA. INSTRUMENTOS POR ISSO EU CLAMO. POR ISSO EU CLAMO. CORO MARANATA É O GRITO DA IGREJA, MARANATA É UMA FORMA DE VIDA, MARANATA É O DESPERTAR DO SONO, É A ESPERANÇA DA ALMA REMIDA. ELE VEM! MARANATA! JESUS VEM! MARANATA! ELE VEM! MARANATA! JESUS VEM! MARANATA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('211', '211 - OS DIAS ESTÃO PASSANDO', 'OS DIAS ESTÃO PASSANDO,\nO MOMENTO SE APROXIMA\nDO ENCONTRO TRIUNFAL.\nA TROMBETA VAI TOCAR.\n\nA PALAVRA ME DIRIGE,\nAPONTA O CAMINHO,\nOLHO PARA O ALTO                        \nE VEJO A REDENÇÃO.\n\nCORO \nEU IREI CONTEMPLAR \nOS CÉUS ABERTOS,\nOUVIREI JESUS CHAMAR \nPELO MEU NOME,\nEU TEREI O MEU CORPO TRANSFORMADO, \nPARA ENCONTRAR O SALVADOR.\n\nEU IREI ANDAR NAS RUAS DE OURO,\nEU IREI CANTAR NO LINDO CORAL,\nEU IREI VER A ÁRVORE DA VIDA,\nESTAREI EM MEU NOVO LAR \n(ESTAREI EM JERUSALÉM).\n\nINSTRUMENTOS\n\nQUERO ESTAR CONTIGO, MEU SENHOR,                                     (VEM, MEU SENHOR)\nVEM ME BUSCAR!\nTIRA TODA DOR E O MEU SOFRER,\nTRANSFORMA O MEU \nCHORO EM ALEGRIA.\n\nCORO \nEU IREI CONTEMPLAR \nOS CÉUS ABERTOS,\nOUVIREI JESUS CHAMAR \nPELO MEU NOME,\nEU TEREI O MEU CORPO TRANSFORMADO, \nPARA ENCONTRAR O SALVADOR.\n\nEU IREI ANDAR NAS RUAS DE OURO,\nEU IREI CANTAR NO LINDO CORAL,\nEU IREI VER A ÁRVORE DA VIDA,\nESTAREI EM MEU NOVO LAR \n(ESTAREI EM JERUSALÉM).\n\nCORO \nEU IREI CONTEMPLAR \nOS CÉUS ABERTOS,\nOUVIREI JESUS CHAMAR \nPELO MEU NOME,\nEU TEREI O MEU CORPO TRANSFORMADO, \nPARA ENCONTRAR O SALVADOR.\n\nEU IREI ANDAR NAS RUAS DE OURO,\nEU IREI CANTAR NO LINDO CORAL,\nEU IREI VER A ÁRVORE DA VIDA,\nESTAREI EM MEU NOVO LAR \n(ESTAREI EM JERUSALÉM).', 'OS DIAS ESTÃO PASSANDO, O MOMENTO SE APROXIMA DO ENCONTRO TRIUNFAL. A TROMBETA VAI TOCAR. A PALAVRA ME DIRIGE, APONTA O CAMINHO, OLHO PARA O ALTO E VEJO A REDENÇÃO. CORO EU IREI CONTEMPLAR OS CÉUS ABERTOS, OUVIREI JESUS CHAMAR PELO MEU NOME, EU TEREI O MEU CORPO TRANSFORMADO, PARA ENCONTRAR O SALVADOR. EU IREI ANDAR NAS RUAS DE OURO, EU IREI CANTAR NO LINDO CORAL, EU IREI VER A ÁRVORE DA VIDA, ESTAREI EM MEU NOVO LAR (ESTAREI EM JERUSALÉM). INSTRUMENTOS QUERO ESTAR CONTIGO, MEU SENHOR, (VEM, MEU SENHOR) VEM ME BUSCAR! TIRA TODA DOR E O MEU SOFRER, TRANSFORMA O MEU CHORO EM ALEGRIA. EU IREI ANDAR NAS RUAS DE OURO, EU IREI CANTAR NO LINDO CORAL, EU IREI VER A ÁRVORE DA VIDA, ESTAREI EM MEU NOVO LAR (ESTAREI EM JERUSALÉM).', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('212', '212 - SEI QUE CRISTO VOLTARÁ', 'SEI QUE CRISTO VOLTARÁ,\nPOIS NA BÍBLIA ESCRITO ESTÁ,\nJOÃO CATORZE, UM, DOIS, TRÊS,\nLEDE, POIS, E SABEREIS.\n\nSEI QUE ELE VOLTA, \nSEI QUE ELE VOLTA, \nSEI QUE ELE VOLTA, \nNA BÍBLIA ESCRITO ESTÁ.', 'SEI QUE CRISTO VOLTARÁ, POIS NA BÍBLIA ESCRITO ESTÁ, JOÃO CATORZE, UM, DOIS, TRÊS, LEDE, POIS, E SABEREIS. SEI QUE ELE VOLTA, SEI QUE ELE VOLTA, SEI QUE ELE VOLTA, NA BÍBLIA ESCRITO ESTÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('213', '213 - LÁ NO DOCE PORVIR', 'LÁ NO DOCE PORVIR, \nLÁ NO DOCE PORVIR, \nLÁ NO DOCE PORVIR, \nMANSÃO DE LUZ IREI TER COM JESUS,\nAH! QUE PRAZER ME ENCONTRAR ALI!\n\nSIM, NO DOCE PORVIR,\nSIM, NO DOCE PORVIR,  \nQUANDO A LUTA FINDAR\nE A VITÓRIA ALCANÇAR,\nSIM, NO DOCE PORVIR.', 'LÁ NO DOCE PORVIR, LÁ NO DOCE PORVIR, LÁ NO DOCE PORVIR, MANSÃO DE LUZ IREI TER COM JESUS, AH! QUE PRAZER ME ENCONTRAR ALI! SIM, NO DOCE PORVIR, SIM, NO DOCE PORVIR, QUANDO A LUTA FINDAR E A VITÓRIA ALCANÇAR, SIM, NO DOCE PORVIR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('214', '214 - NÓS RECEBEREMOS LÁ NO CÉU', 'NÓS RECEBEREMOS \nLÁ NO CÉU, LÁ NO CÉU,\nO LINDO, LINDO CÉU,\nOUTRO NOME NOVO\nALÉM DO VÉU, NESSE LINDO CÉU.\n\nUM NOME NOVO, \nUM NOME NOVO,\nNÓS TEREMOS JÁ;\nUM NOME NOVO,\nUM NOME NOVO,\nQUANDO ENTRARMOS LÁ.\n\nCORO\nSIM, RECEBEREMOS \nLÁ NO CÉU, LÁ NO CÉU, \nO LINDO, LINDO CÉU,\nOUTRO NOME NOVO\nALÉM DO VÉU, NESSE LINDO CÉU.\n\nNA PEDRINHA BRANCA, EU SÓ LEREI\nESSE NOME NOVO DE JESUS;\nBRANCA MAIS QUE A NEVE\nA GUARDAREI NO REINO DE LUZ.\n\nPEDRINHA BRANCA,\nPEDRINHA BRANCA\nNÓS TEREMOS JÁ;\nPEDRINHA BRANCA,\nPEDRINHA BRANCA\nQUANDO ENTRARMOS LÁ.\n\nCORO\nSIM, RECEBEREMOS \nLÁ NO CÉU, LÁ NO CÉU, \nO LINDO, LINDO CÉU,\nOUTRO NOME NOVO\nALÉM DO VÉU, NESSE LINDO CÉU.\n\nLARGA O MUNDO, \nCRÊ EM CRISTO E VEM!\nO MANÁ ESCONDIDO É PARA TI;\nSERVE, POIS, A DEUS,\nTENS TODO O BEM!\nCRISTO É TUDO ALI.\n\nMANÁ ESCONDIDO,\nMANÁ ESCONDIDO\nNÓS TEREMOS JÁ;\nMANÁ ESCONDIDO, \nMANÁ ESCONDIDO,\nQUANDO ENTRARMOS LÁ.\n\nCORO\nSIM, RECEBEREMOS \nLÁ NO CÉU, LÁ NO CÉU, \nO LINDO, LINDO CÉU,\nOUTRO NOME NOVO\nALÉM DO VÉU, NESSE LINDO CÉU.', 'NÓS RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU. UM NOME NOVO, UM NOME NOVO, NÓS TEREMOS JÁ; UM NOME NOVO, UM NOME NOVO, QUANDO ENTRARMOS LÁ. CORO SIM, RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU. NA PEDRINHA BRANCA, EU SÓ LEREI ESSE NOME NOVO DE JESUS; BRANCA MAIS QUE A NEVE A GUARDAREI NO REINO DE LUZ. PEDRINHA BRANCA, PEDRINHA BRANCA NÓS TEREMOS JÁ; PEDRINHA BRANCA, PEDRINHA BRANCA QUANDO ENTRARMOS LÁ. LARGA O MUNDO, CRÊ EM CRISTO E VEM! O MANÁ ESCONDIDO É PARA TI; SERVE, POIS, A DEUS, TENS TODO O BEM! CRISTO É TUDO ALI. MANÁ ESCONDIDO, MANÁ ESCONDIDO NÓS TEREMOS JÁ; MANÁ ESCONDIDO, MANÁ ESCONDIDO, QUANDO ENTRARMOS LÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('215', '215 - EIS QUE JESUS VEM NAS NUVENS', 'EIS QUE JESUS VEM NAS NUVENS\nBUSCAR SUAS JOIAS,\nSUAS JOIAS PRECIOSAS\nDE MUITO VALOR.\n\nCORO\nCOMO ESTRELAS DA AURORA\nBRILHANDO NA FRONTE\nDE JESUS, LÁ NA GLÓRIA,\nADORNO SERÃO.\n\nVEM BUSCÁ-LAS, VAI LEVÁ-LAS\nAO REINO CELESTE, \nSUAS JOIAS RESPLENDENTES \nDE MUITO VALOR.\n\nCORO\nCOMO ESTRELAS DA AURORA\nBRILHANDO NA FRONTE\nDE JESUS, LÁ NA GLÓRIA,\nADORNO SERÃO.\n\nOS MENINOS E AS MENINAS\nQUE SERVEM A JESUS,\nSÃO-LHES JOIAS, RICAS\nJOIAS DE MUITO VALOR.\n\nCORO\nCOMO ESTRELAS DA AURORA\nBRILHANDO NA FRONTE\nDE JESUS, LÁ NA GLÓRIA,\nADORNO SERÃO.', 'EIS QUE JESUS VEM NAS NUVENS BUSCAR SUAS JOIAS, SUAS JOIAS PRECIOSAS DE MUITO VALOR. CORO COMO ESTRELAS DA AURORA BRILHANDO NA FRONTE DE JESUS, LÁ NA GLÓRIA, ADORNO SERÃO. VEM BUSCÁ-LAS, VAI LEVÁ-LAS AO REINO CELESTE, SUAS JOIAS RESPLENDENTES DE MUITO VALOR. OS MENINOS E AS MENINAS QUE SERVEM A JESUS, SÃO-LHES JOIAS, RICAS JOIAS DE MUITO VALOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('216', '216 - SOBRE AS NUVENS DESCERÁ (ELE VIRÁ)', 'SOBRE AS NUVENS DESCERÁ\nNOSSO REI E SALVADOR;\nCOM PODER E GRANDE GLÓRIA\nAO SOAR DA TROMBETA ELE VIRÁ.\n\nCORO \nELE VIRÁ (ARREBATAR)\nELE VIRÁ (NÃO TARDARÁ)\nELE VIRÁ PARA BUSCAR \nA IGREJA FIEL.\n\nTUDO AQUI HÁ DE FINDAR,\nTUDO NOVO ELE FARÁ!\nQUEM VENCER TUDO HERDARÁ\nE NO CÉU COM JESUS \nVAMOS MORAR.\n\nCORO \nELE VIRÁ (ARREBATAR)\nELE VIRÁ (NÃO TARDARÁ)\nELE VIRÁ PARA BUSCAR \nA IGREJA FIEL.\n\nA NOVA JERUSALÉM\nO CORDEIRO ILUMINARÁ,\nRIO PURO HAVERÁ\nE A ÁRVORE DA VIDA\nALI ESTARÁ.\n\nCORO \nELE VIRÁ (ARREBATAR)\nELE VIRÁ (NÃO TARDARÁ)\nELE VIRÁ PARA BUSCAR \nA IGREJA FIEL.\n\nCORO \nELE VIRÁ (ARREBATAR)\nELE VIRÁ (NÃO TARDARÁ)\nELE VIRÁ PARA BUSCAR \nA IGREJA FIEL.\nELE VIRÁ PARA BUSCAR \nA IGREJA FIEL.', 'SOBRE AS NUVENS DESCERÁ NOSSO REI E SALVADOR; COM PODER E GRANDE GLÓRIA AO SOAR DA TROMBETA ELE VIRÁ. CORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL. TUDO AQUI HÁ DE FINDAR, TUDO NOVO ELE FARÁ! QUEM VENCER TUDO HERDARÁ E NO CÉU COM JESUS VAMOS MORAR. A NOVA JERUSALÉM O CORDEIRO ILUMINARÁ, RIO PURO HAVERÁ E A ÁRVORE DA VIDA ALI ESTARÁ. CORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL. ELE VIRÁ PARA BUSCAR A IGREJA FIEL.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('217', '217 - VEREI MEU GRANDE REI', 'VEREI MEU GRANDE REI,\nUM VENCEDOR SEREI,\nVEREI O MEU JESUS!\nNOVA JERUSALÉM,\nCIDADE DO MEU REI,\nESTAREI COM MEU JESUS!\n\nCORO\nOH! QUE GLÓRIA!\nSERÁ O ENCONTRO TRIUNFAL!\nALELUIA! VEREI OS CÉUS SE ABRIREM.\nE COM OS ANJOS A CANTAR,\nNA CIDADE VOU ENTRAR.\n“ORA VEM, VEM SENHOR JESUS!”\n\nELE MORREU NA CRUZ,\nPORÉM RESSUSCITOU \nE VIVO HOJE ESTÁ.\nQUANDO ENCONTRAR JESUS, \nVEREI A SUA LUZ,\nOH! QUE GLÓRIA ETERNAL!\n\nCORO\nOH! QUE GLÓRIA!\nSERÁ O ENCONTRO TRIUNFAL!\nALELUIA! VEREI OS CÉUS SE ABRIREM.\nE COM OS ANJOS A CANTAR,\nNA CIDADE VOU ENTRAR.\n“ORA VEM, VEM SENHOR JESUS!” VEM SENHOR JESUS.', 'VEREI MEU GRANDE REI, UM VENCEDOR SEREI, VEREI O MEU JESUS! NOVA JERUSALÉM, CIDADE DO MEU REI, ESTAREI COM MEU JESUS! CORO OH! QUE GLÓRIA! SERÁ O ENCONTRO TRIUNFAL! ALELUIA! VEREI OS CÉUS SE ABRIREM. E COM OS ANJOS A CANTAR, NA CIDADE VOU ENTRAR. ORA VEM, VEM SENHOR JESUS! ELE MORREU NA CRUZ, PORÉM RESSUSCITOU E VIVO HOJE ESTÁ. QUANDO ENCONTRAR JESUS, VEREI A SUA LUZ, OH! QUE GLÓRIA ETERNAL! CORO OH! QUE GLÓRIA! SERÁ O ENCONTRO TRIUNFAL! ALELUIA! VEREI OS CÉUS SE ABRIREM. E COM OS ANJOS A CANTAR, NA CIDADE VOU ENTRAR. ORA VEM, VEM SENHOR JESUS! VEM SENHOR JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('218', '218 - MARANATA', 'CORO\nMARANATA, \nMARANATA, \nJESUS BREVE VIRÁ!\n\nEU VOU SUBIR, POIS A TROMBETA ESCUTAREI;\nE LÁ NAS NUVENS COM \nJESUS ME ENCONTRAREI.\nDESPERTA IGREJA, VAMOS SUBIR! \nO TEU LUGAR NÃO É AQUI, \nPORQUE EM BREVE JESUS VIRÁ.\n\nCORO\nMARANATA, \nMARANATA, \nJESUS BREVE VIRÁ!\n\nEU VOU SUBIR, POIS A TROMBETA ESCUTAREI;\nE LÁ NAS NUVENS COM \nJESUS ME ENCONTRAREI.\nDESPERTA IGREJA, VAMOS SUBIR! \nO TEU LUGAR NÃO É AQUI, \nPORQUE EM BREVE JESUS VIRÁ.', 'CORO MARANATA, MARANATA, JESUS BREVE VIRÁ! EU VOU SUBIR, POIS A TROMBETA ESCUTAREI; E LÁ NAS NUVENS COM JESUS ME ENCONTRAREI. DESPERTA IGREJA, VAMOS SUBIR! O TEU LUGAR NÃO É AQUI, PORQUE EM BREVE JESUS VIRÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('219', '219 - ELE VIRÁ NO MEIO DA NOITE', 'ELE VIRÁ NO MEIO DA NOITE,\nO NOIVO VEM BUSCAR \nA NOIVA AMADA, ELE VEM!\nPRA IR À NOVA JERUSALÉM,\nRESGATARÁ O POVO \nQUE ANDA NA LUZ.\n\nOS DIAS SÃO DE TREVAS, \nEU SEI, MAS PARA A IGREJA \nBRILHA A GLÓRIA DE CRISTO JESUS!\nENCHE TUA CANDEIA, DEIXA BRILHAR, DEIXA O ESPÍRITO SANTO BRILHAR!\n\nCORO\nBRILHA EM NÓS \nO ESPÍRITO SANTO DE DEUS,\nNOS LEVANDO \nA ENCONTRAR COM JESUS!\n\nNAQUELE GRANDE DIA                SUBIRÁ QUEM ESTIVER \nNA LUZ, ENCHE A CANDEIA\nE BRILHA O FOGO DO SENHOR! \n\nINSTRUMENTOS\n\nELE VIRÁ NO MEIO DA NOITE,\nO NOIVO VEM BUSCAR \nA NOIVA AMADA, ELE VEM!\nPRA IR À NOVA JERUSALÉM,\nRESGATARÁ O POVO \nQUE ANDA NA LUZ.\n\nOS DIAS SÃO DE TREVAS, \nEU SEI, MAS PARA A IGREJA \nBRILHA A GLÓRIA DE CRISTO JESUS!\nENCHE TUA CANDEIA, DEIXA BRILHAR, DEIXA O ESPÍRITO SANTO BRILHAR!\n\nCORO\nBRILHA EM NÓS \nO ESPÍRITO SANTO DE DEUS,\nNOS LEVANDO \nA ENCONTRAR COM JESUS!\n\nNAQUELE GRANDE DIA\nSUBIRÁ QUEM ESTIVER \nNA LUZ, ENCHE A CANDEIA\nE BRILHA O FOGO DO SENHOR!\n\nCORO\nBRILHA EM NÓS \nO ESPÍRITO SANTO DE DEUS,\nNOS LEVANDO \nA ENCONTRAR COM JESUS!\n\nNAQUELE GRANDE DIA \nSUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA \nO FOGO DO SENHOR! \n\nENCHE A CANDEIA E BRILHA \nO FOGO DO SENHOR!', 'ELE VIRÁ NO MEIO DA NOITE, O NOIVO VEM BUSCAR A NOIVA AMADA, ELE VEM! PRA IR À NOVA JERUSALÉM, RESGATARÁ O POVO QUE ANDA NA LUZ. OS DIAS SÃO DE TREVAS, EU SEI, MAS PARA A IGREJA BRILHA A GLÓRIA DE CRISTO JESUS! ENCHE TUA CANDEIA, DEIXA BRILHAR, DEIXA O ESPÍRITO SANTO BRILHAR! CORO BRILHA EM NÓS O ESPÍRITO SANTO DE DEUS, NOS LEVANDO A ENCONTRAR COM JESUS! NAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! INSTRUMENTOS NAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! NAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('220', '220 - ONDE VIVE O REI DA GLÓRIA?', 'ONDE VIVE O REI DA GLÓRIA?\nVIVE EM MEU CORAÇÃO.\nONDE REINA O REI DA GLÓRIA?\nREINA EM MEU CORAÇÃO.\n\nELE FALA, ELE GUIA \nNOSSOS PASSOS A JERUSALÉM, \nPARA JUNTOS HABITARMOS\nCOM JESUS, NOSSO REI.\n\nO NOSSO REI DA GLÓRIA\nOPERA MARAVILHAS\nNO MEIO DA IGREJA,\nLIBERTA CORAÇÕES;\n\nE HOJE QUER TRANSFORMAR \nA SUA VIDA, VENHA AGORA E SINTA \nO PODER DO SALVADOR,\nALELUIA! OH, GLÓRIA!\nLOUVEMOS AO SENHOR.\n\nONDE VIVE E REINA O REI DA GLÓRIA?\nVIVE, REINA EM NOSSOS CORAÇÕES.', 'ONDE VIVE O REI DA GLÓRIA? VIVE EM MEU CORAÇÃO. ONDE REINA O REI DA GLÓRIA? REINA EM MEU CORAÇÃO. ELE FALA, ELE GUIA NOSSOS PASSOS A JERUSALÉM, PARA JUNTOS HABITARMOS COM JESUS, NOSSO REI. O NOSSO REI DA GLÓRIA OPERA MARAVILHAS NO MEIO DA IGREJA, LIBERTA CORAÇÕES; E HOJE QUER TRANSFORMAR A SUA VIDA, VENHA AGORA E SINTA O PODER DO SALVADOR, ALELUIA! OH, GLÓRIA! LOUVEMOS AO SENHOR. ONDE VIVE E REINA O REI DA GLÓRIA? VIVE, REINA EM NOSSOS CORAÇÕES.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('221', '221 - VAI RAIAR O DIA', 'CORO (2X)\nVAI RAIAR O DIA,                     \nDIA DA VITÓRIA,\nVAI RAIAR O DIA, DIA EM QUE \nA VITÓRIA ALCANÇAREMOS.\n\nGLÓRIA E MAJESTADE ESTÃO \nANTE A FACE DO SENHOR\nE FORMOSURA NO SEU SANTO LUGAR; \nANUNCIAI ENTRE AS NAÇÕES\nA SUA GLÓRIA, ENTRE OS POVOS AS SUAS MARAVILHAS.\n\nCORO\nVAI RAIAR O DIA,            \nDIA DA VITÓRIA,\nVAI RAIAR O DIA, DIA EM QUE \nA VITÓRIA ALCANÇAREMOS.\n\nDAI AO SENHOR DEVIDA GLÓRIA,\nTRAZEI OFERTAS E ENTRAI \nNOS SEUS ÁTRIOS; ADORAI AO SENHOR \nEM SANTIDADE, COM LOUVOR \nADORAI A SUA FACE.\n\nCORO\nVAI RAIAR O DIA,            \nDIA DA VITÓRIA,\nVAI RAIAR O DIA, DIA EM QUE \nA VITÓRIA ALCANÇAREMOS.\n\nDAI AO SENHOR GLÓRIA \nE FORÇA, BRAME O MAR                  \nE A SUA PLENITUDE;\nANUNCIAI A SALVAÇÃO                   \nDE DIA EM DIA PORQUE EM BREVE                           \nA VITÓRIA ALCANÇAREMOS.\n\nCORO (2X)\nVAI RAIAR O DIA,            \nDIA DA VITÓRIA,\nVAI RAIAR O DIA, DIA EM QUE \nA VITÓRIA ALCANÇAREMOS.', 'VAI RAIAR O DIA, DIA DA VITÓRIA, VAI RAIAR O DIA, DIA EM QUE A VITÓRIA ALCANÇAREMOS. GLÓRIA E MAJESTADE ESTÃO ANTE A FACE DO SENHOR E FORMOSURA NO SEU SANTO LUGAR; ANUNCIAI ENTRE AS NAÇÕES A SUA GLÓRIA, ENTRE OS POVOS AS SUAS MARAVILHAS. CORO VAI RAIAR O DIA, DIA DA VITÓRIA, VAI RAIAR O DIA, DIA EM QUE A VITÓRIA ALCANÇAREMOS. DAI AO SENHOR DEVIDA GLÓRIA, TRAZEI OFERTAS E ENTRAI NOS SEUS ÁTRIOS; ADORAI AO SENHOR EM SANTIDADE, COM LOUVOR ADORAI A SUA FACE. DAI AO SENHOR GLÓRIA E FORÇA, BRAME O MAR E A SUA PLENITUDE; ANUNCIAI A SALVAÇÃO DE DIA EM DIA PORQUE EM BREVE A VITÓRIA ALCANÇAREMOS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('222', '222 - DEUS HABITA NO MEIO DOS LOUVORES', 'DEUS HABITA \nNO MEIO DOS LOUVORES,  \nÓ CANTAI, IGREJA, CANTAI,\nÓ CANTAI, DEUS HABITA \nNO LOUVOR.\n\nÓ CANTAI, \nCANTAI, CANTAI, \nÓ CANTAI LOUVORES AO SENHOR;\nDEUS HABITA NO MEIO DOS LOUVORES,\nÓ CANTAI AO SENHOR.\n\nDEUS HABITA \nNO MEIO DOS LOUVORES,  \nÓ CANTAI, IGREJA, CANTAI,\nÓ CANTAI, DEUS HABITA \nNO LOUVOR.\n\nÓ CANTAI, \nCANTAI, CANTAI, \nÓ CANTAI LOUVORES AO SENHOR;\nDEUS HABITA NO MEIO DOS LOUVORES,\nÓ CANTAI AO SENHOR.', 'DEUS HABITA NO MEIO DOS LOUVORES, Ó CANTAI, IGREJA, CANTAI, Ó CANTAI, DEUS HABITA NO LOUVOR. Ó CANTAI, CANTAI, CANTAI, Ó CANTAI LOUVORES AO SENHOR; DEUS HABITA NO MEIO DOS LOUVORES, Ó CANTAI AO SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('223', '223 - PAI, TUA BELEZA ME ADMIRA', 'PAI, TUA BELEZA ME ADMIRA,\nNO UNIVERSO QUE CRIASTE\nEU POSSO VER COMO TU ÉS\nCOMO TU ÉS;\n\nPAI, ATÉ NAS MAIS \nPEQUENAS COISAS          \nEXISTE TANTA HARMONIA,\nPERFEITA É A OBRA \nDAS TUAS MÃOS.\n\nCORO\nCOM TEU PODER FUNDASTE A TERRA, \nLEVANTASTE MONTES, SEPARASTE MARES;\nNO CÉU PUSESTE O SOL E A LUA,\nSOMENTE AO TEU MANDAR \nA VIDA SE FORMOU.\n\nE DO BARRO TU FIZESTE O HOMEM\nÀ TUA IMAGEM E SEMELHANÇA\nPARA ESTAR PERANTE TI E TE LOUVAR;\n\nPAI, OUVE ESTA MINHA ADORAÇÃO,\nÉ A MAIS SINCERA EXPRESSÃO\nDO AMOR QUE HÁ DENTRO DE MIM.\n\nINSTRUMENTOS\n\nCORO\nCOM TEU PODER FUNDASTE A TERRA, \nLEVANTASTE MONTES, SEPARASTE MARES;\nNO CÉU PUSESTE O SOL E A LUA,\nSOMENTE AO TEU MANDAR \nA VIDA SE FORMOU.\n\nE DO BARRO TU FIZESTE O HOMEM\nÀ TUA IMAGEM E SEMELHANÇA\nPARA ESTAR PERANTE TI E TE LOUVAR;\n\nPAI, OUVE ESTA MINHA ADORAÇÃO,\nÉ A MAIS SINCERA EXPRESSÃO\nDO AMOR QUE HÁ DENTRO DE MIM.', 'PAI, TUA BELEZA ME ADMIRA, NO UNIVERSO QUE CRIASTE EU POSSO VER COMO TU ÉS COMO TU ÉS; PAI, ATÉ NAS MAIS PEQUENAS COISAS EXISTE TANTA HARMONIA, PERFEITA É A OBRA DAS TUAS MÃOS. CORO COM TEU PODER FUNDASTE A TERRA, LEVANTASTE MONTES, SEPARASTE MARES; NO CÉU PUSESTE O SOL E A LUA, SOMENTE AO TEU MANDAR A VIDA SE FORMOU. E DO BARRO TU FIZESTE O HOMEM À TUA IMAGEM E SEMELHANÇA PARA ESTAR PERANTE TI E TE LOUVAR; PAI, OUVE ESTA MINHA ADORAÇÃO, É A MAIS SINCERA EXPRESSÃO DO AMOR QUE HÁ DENTRO DE MIM. INSTRUMENTOS PAI, OUVE ESTA MINHA ADORAÇÃO, É A MAIS SINCERA EXPRESSÃO DO AMOR QUE HÁ DENTRO DE MIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('224', '224 - DEUS CRIOU OS CÉUS E A TERRA', 'DEUS CRIOU OS CÉUS E A TERRA, \nO UNIVERSO, TUDO ELE FEZ; A TERRA ERA VAZIA, COBERTA DE TREVAS, SEMELHANTE AO CORAÇÃO DO HOMEM  SEM DEUS.\n\nELE TROUXE A LUZ, SEPAROU AS ÁGUAS,   \nCRIOU A TERRA FIRME, AS ERVAS FEZ BROTAR,\nILUMINOU OS CÉUS COM O BRILHO DO SOL, \nLUA E ESTRELAS,\n\nENCHEU O MAR DE VIDA,  \nCRIOU OS ANIMAIS,\nPREPAROU TUDO TÃO BELO PARA DEPOIS O HOMEM CRIAR.\n\nCORO\nDO NADA ELE FEZ TUDO,\nCOM SUA PALAVRA OS CRIOU, \nELE É O DEUS DA VIDA\nE TUDO ESTÁ EM SUAS MÃOS;\n\nÉ ELE QUEM MOVE OS CORAÇÕES \nPARA CONHECERMOS O SEU AMOR, \nNOS REVELANDO JESUS, \nNOSSO SALVADOR.\n\nESTA TERRA UM DIA PASSARÁ,  \nNÃO MAIS EXISTIRÁ, O NOVO CÉU \nE A NOVA TERRA JESUS NOS DARÁ.\n\nCORO\nDO NADA ELE FEZ TUDO,            \nCOM SUA PALAVRA OS CRIOU,  \nELE É O DEUS DA VIDA\nE TUDO ESTÁ EM SUAS MÃOS;\n\nÉ ELE QUEM MOVE OS CORAÇÕES \nPARA CONHECERMOS O SEU AMOR, \nNOS REVELANDO JESUS,              NOSSO SALVADOR.', 'DEUS CRIOU OS CÉUS E A TERRA, O UNIVERSO, TUDO ELE FEZ; A TERRA ERA VAZIA, COBERTA DE TREVAS, SEMELHANTE AO CORAÇÃO DO HOMEM SEM DEUS. ELE TROUXE A LUZ, SEPAROU AS ÁGUAS, CRIOU A TERRA FIRME, AS ERVAS FEZ BROTAR, ILUMINOU OS CÉUS COM O BRILHO DO SOL, LUA E ESTRELAS, ENCHEU O MAR DE VIDA, CRIOU OS ANIMAIS, PREPAROU TUDO TÃO BELO PARA DEPOIS O HOMEM CRIAR. CORO DO NADA ELE FEZ TUDO, COM SUA PALAVRA OS CRIOU, ELE É O DEUS DA VIDA E TUDO ESTÁ EM SUAS MÃOS; É ELE QUEM MOVE OS CORAÇÕES PARA CONHECERMOS O SEU AMOR, NOS REVELANDO JESUS, NOSSO SALVADOR. ESTA TERRA UM DIA PASSARÁ, NÃO MAIS EXISTIRÁ, O NOVO CÉU E A NOVA TERRA JESUS NOS DARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('225', '225 - EU VIM AQUI SENHOR', 'EU VIM AQUI SENHOR, PARA AGRADECER \nPOR TUDO QUE TENS FEITO POR MIM; \nALEGRE ESTOU POR MAIS UM DIA\nNA TUA PRESENÇA, Ó DEUS.\n\nCORO\nGLÓRIA A JESUS, \nMARAVILHOSO ÉS PRA MIM,\nÉS FORTE E PODEROSO\nE CUMPRE SUAS PROMESSAS.\n\nO MEU PRAZER É TE LOUVAR\nPORQUE O SENHOR É FIEL;\nEU SEI QUE APENAS PEQUENO SOU,\nMAS SIRVO A TI POR AMOR.\n\nCORO\nGLÓRIA A JESUS, \nMARAVILHOSO ÉS PRA MIM,\nÉS FORTE E PODEROSO\nE CUMPRE SUAS PROMESSAS.', 'EU VIM AQUI SENHOR, PARA AGRADECER POR TUDO QUE TENS FEITO POR MIM; ALEGRE ESTOU POR MAIS UM DIA NA TUA PRESENÇA, Ó DEUS. CORO GLÓRIA A JESUS, MARAVILHOSO ÉS PRA MIM, ÉS FORTE E PODEROSO E CUMPRE SUAS PROMESSAS. O MEU PRAZER É TE LOUVAR PORQUE O SENHOR É FIEL; EU SEI QUE APENAS PEQUENO SOU, MAS SIRVO A TI POR AMOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('226', '226 - IREI CANTAR E ADORAR O REI JESUS', 'IREI CANTAR E ADORAR AO REI JESUS\nQUE POR AMOR MORREU \nNA CRUZ PRA ME SALVAR;\n\nALEGRE SOU POIS SALVO ESTOU\nE ETERNAMENTE COM O SENHOR\nNAS MANSÕES CELESTIAIS IREI MORAR.\n\nOH! ALELUIA, HONRA E GLÓRIA \nAO REI JESUS! LÁ NO CÉU \nETERNAMENTE IREI CANTAR!\n\nADORAREI AO MEU SENHOR,\nPROSTRADO AOS PÉS DO SALVADOR\nQUE ENTRONIZADO ESTÁ\nPOIS ELE É O REI DOS REIS.\n\nOH! ALELUIA, HONRA E GLÓRIA \nAO REI JESUS! LÁ NO CÉU \nETERNAMENTE IREI CANTAR!\n\nADORAREI AO MEU SENHOR,\nPROSTRADO AOS PÉS DO SALVADOR\nQUE ENTRONIZADO ESTÁ\nPOIS ELE É O REI DOS REIS.', 'IREI CANTAR E ADORAR AO REI JESUS QUE POR AMOR MORREU NA CRUZ PRA ME SALVAR; ALEGRE SOU POIS SALVO ESTOU E ETERNAMENTE COM O SENHOR NAS MANSÕES CELESTIAIS IREI MORAR. OH! ALELUIA, HONRA E GLÓRIA AO REI JESUS! LÁ NO CÉU ETERNAMENTE IREI CANTAR! ADORAREI AO MEU SENHOR, PROSTRADO AOS PÉS DO SALVADOR QUE ENTRONIZADO ESTÁ POIS ELE É O REI DOS REIS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('227', '227 - A JESUS IREI LOUVAR', 'A JESUS IREI LOUVAR,\nPOIS NA CRUZ SE DEU POR MIM.\nO SEU SANGUE DERRAMOU,\nMEU PECADO CARREGOU;\nTODA DOR ELE LEVOU,\nFOI QUEM PRIMEIRO ME AMOU.\n\nCOM QUERUBINS E SERAFINS\nA JESUS IREI LOUVAR,\nALELUIA! GLÓRIA A DEUS!\nLÁ NO CÉU EU VOU MORAR.\n\nCOM QUERUBINS E SERAFINS\nA JESUS IREI LOUVAR,\nALELUIA! GLÓRIA A DEUS!\nLÁ NO CÉU EU VOU MORAR.', 'A JESUS IREI LOUVAR, POIS NA CRUZ SE DEU POR MIM. O SEU SANGUE DERRAMOU, MEU PECADO CARREGOU; TODA DOR ELE LEVOU, FOI QUEM PRIMEIRO ME AMOU. COM QUERUBINS E SERAFINS A JESUS IREI LOUVAR, ALELUIA! GLÓRIA A DEUS! LÁ NO CÉU EU VOU MORAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('228', '228 - HOSANA AO FILHO DE DAVI', 'HOSANA AO FILHO DE DAVI,\nHOSANA AO REI DE ISRAEL;\nBENDITO O QUE VEM\nEM NOME DO SENHOR,\nHOSANA, HOSANA.\n\nHOSANA AO QUE VEM\nEM NOME DO SENHOR,\nHOSANA, HOSANA; \nHOSANA NAS MAIORES ALTURAS.', 'HOSANA AO FILHO DE DAVI, HOSANA AO REI DE ISRAEL; BENDITO O QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA. HOSANA AO QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA; HOSANA NAS MAIORES ALTURAS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
