INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('230', '230 - ESTA CANÇÃO', 'ESTA CANÇÃO É PARA TI, \nJESUS, DO MEU CORAÇÃO\nTIREI ESTE LOUVOR.\nMEU DEUS BENDITO, GLÓRIA, GLÓRIA;\nMEU DEUS BENDITO, GLÓRIA, GLÓRIA.\n\nO MEU LOUVOR PERTENCE SÓ A TI,\nTEUS ANJOS SEMPRE LOUVAM,\nA ELES VOU ME UNIR. \nPRA SEMPRE GLÓRIA, GLÓRIA, GLÓRIA.\nPRA SEMPRE GLÓRIA, GLÓRIA, GLÓRIA.', 'ESTA CANÇÃO É PARA TI, JESUS, DO MEU CORAÇÃO TIREI ESTE LOUVOR. MEU DEUS BENDITO, GLÓRIA, GLÓRIA; MEU DEUS BENDITO, GLÓRIA, GLÓRIA. O MEU LOUVOR PERTENCE SÓ A TI, TEUS ANJOS SEMPRE LOUVAM, A ELES VOU ME UNIR. PRA SEMPRE GLÓRIA, GLÓRIA, GLÓRIA. PRA SEMPRE GLÓRIA, GLÓRIA, GLÓRIA.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
