INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('232', '232 - HOJE EU NÃO QUERO PEDIR', 'HOJE EU NÃO QUERO PEDIR NADA A TI,\nEU SÓ QUERO LOUVAR, MEU SENHOR,\nPOIS GRANDES COISAS FIZESTES POR MIM, \nPOR ISSO ALEGRE ESTOU.\n\nA NOITE JÁ ACABOU, FOI-SE A DOR,\nA ALEGRIA VOLTOU EM LOUVOR;\nTUA PALAVRA É FIEL, CREIO ASSIM,\nPOR ISSO ALEGRE ESTOU.\n\nHOJE EU NÃO QUERO PEDIR NADA A TI,\nEU SÓ QUERO LOUVAR, MEU SENHOR,\nPOIS GRANDES COISAS FIZESTES POR MIM, \nPOR ISSO ALEGRE ESTOU.\n\nA NOITE JÁ ACABOU, FOI-SE A DOR,\nA ALEGRIA VOLTOU EM LOUVOR;\nTUA PALAVRA É FIEL, CREIO ASSIM,\nPOR ISSO ALEGRE ESTOU.', 'HOJE EU NÃO QUERO PEDIR NADA A TI, EU SÓ QUERO LOUVAR, MEU SENHOR, POIS GRANDES COISAS FIZESTES POR MIM, POR ISSO ALEGRE ESTOU. A NOITE JÁ ACABOU, FOI-SE A DOR, A ALEGRIA VOLTOU EM LOUVOR; TUA PALAVRA É FIEL, CREIO ASSIM, POR ISSO ALEGRE ESTOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('233', '233 - NA CIDADE DE DAVI NASCEU O SALVADOR', 'NA CIDADE DE DAVI \nNASCEU O SALVADOR,\nCRISTO, O SENHOR;\nMULTIDÕES DE ANJOS\nLOUVARAM A DEUS DIZENDO:\n\nCORO (2X)\nGLÓRIA A DEUS NAS ALTURAS, \nPAZ NA TERRA, BOA VONTADE \nPARA COM OS HOMENS.\n\nJESUS NASCEU\nNUM LUGAR TÃO SIMPLES,\nFOI NO MEU CORAÇÃO;\nACHOU LUGAR PRA NASCER EM MIM \nPOIS ABRI MEU CORAÇÃO \nE AGORA O SIRVO, SIRVO, SIM.\n\nCORO (2X)\nGLÓRIA A DEUS NAS ALTURAS, \nPAZ NA TERRA, BOA VONTADE \nPARA COM OS HOMENS.', 'NA CIDADE DE DAVI NASCEU O SALVADOR, CRISTO, O SENHOR; MULTIDÕES DE ANJOS LOUVARAM A DEUS DIZENDO: GLÓRIA A DEUS NAS ALTURAS, PAZ NA TERRA, BOA VONTADE PARA COM OS HOMENS. JESUS NASCEU NUM LUGAR TÃO SIMPLES, FOI NO MEU CORAÇÃO; ACHOU LUGAR PRA NASCER EM MIM POIS ABRI MEU CORAÇÃO E AGORA O SIRVO, SIRVO, SIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('234', '234 - SEI QUE SOU AMADO DO MEU SALVADOR', 'SEI QUE SOU AMADO \nDO MEU SALVADOR,\nSEI QUE ESTOU GUARDADO \nPELO SEU AMOR;\n\nDIA A DIA CAMINHANDO                     ESTOU INDO A JERUSALÉM,\nSEGUINDO MEU JESUS,\nEU SEI QUE EM BREVE CHEGAREI.\n\nCORO\nSOU FELIZ COM MEU SENHOR, (2X)    \nDIA A DIA CAMINHANDO                     ESTOU INDO A JERUSALÉM,\nSEGUINDO MEU JESUS,\nEU SEI QUE EM BREVE CHEGAREI.\n\nNAS MANSÕES CELESTIAIS \nIREI MORAR, COM OS ANJOS DO SENHOR \nIREI CANTAR; SEU AMOR JESUS MOSTRU \nPOR MIM, UM POBRE PECADOR,\nEM MEU LUGAR MORREU \nNA CRUZ, AGORA SALVO ESTOU.\n\nCORO\nSOU FELIZ COM MEU SENHOR, (2X)    \nDIA A DIA CAMINHANDO                     ESTOU INDO A JERUSALÉM,\nSEGUINDO MEU JESUS,\nEU SEI QUE EM BREVE CHEGAREI.', 'SEI QUE SOU AMADO DO MEU SALVADOR, SEI QUE ESTOU GUARDADO PELO SEU AMOR; DIA A DIA CAMINHANDO ESTOU INDO A JERUSALÉM, SEGUINDO MEU JESUS, EU SEI QUE EM BREVE CHEGAREI. CORO SOU FELIZ COM MEU SENHOR,  DIA A DIA CAMINHANDO ESTOU INDO A JERUSALÉM, SEGUINDO MEU JESUS, EU SEI QUE EM BREVE CHEGAREI. NAS MANSÕES CELESTIAIS IREI MORAR, COM OS ANJOS DO SENHOR IREI CANTAR; SEU AMOR JESUS MOSTRU POR MIM, UM POBRE PECADOR, EM MEU LUGAR MORREU NA CRUZ, AGORA SALVO ESTOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
