INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('40', '40 - QUERO OUVIR (MEU QUERER)', 'QUERO OUVIR A TUA VOZ, Ó MEU SENHOR, A FALAR TÃO PURA E MEIGA AO CORAÇÃO E SENTIR O TOQUE DO TEU SANTO ESPÍRITO PARA QUE EU POSSA RENOVAR A COMUNHÃO.\n\nCORO (2X) QUERO LOUVAR, COM ALEGRIA EM MEU VIVER RENOVAR A MINHA FÉ, ENTREGAR TODO O MEU SER.\n\nQUERO FALAR DESTE SEU IMENSO AMOR. E DIZER A TODO O MUNDO QUE JESUS É O SALVADOR. QUE LÁ NA CRUZ ELE MORREU PARA NOS SALVAR DERRAMOU SEU SANGUE PURA E A VIDA ETERNA DÁ.\n\nCORO (2X) QUERO LOUVAR, COM ALEGRIA EM MEU VIVER RENOVAR A MINHA FÉ, ENTREGAR TODO O MEU SER.', 'QUERO OUVIR A TUA VOZ, Ó MEU SENHOR, A FALAR TÃO PURA E MEIGA AO CORAÇÃO E SENTIR O TOQUE DO TEU SANTO ESPÍRITO PARA QUE EU POSSA RENOVAR A COMUNHÃO. QUERO LOUVAR, COM ALEGRIA EM MEU VIVER RENOVAR A MINHA FÉ, ENTREGAR TODO O MEU SER. QUERO FALAR DESTE SEU IMENSO AMOR. E DIZER A TODO O MUNDO QUE JESUS É O SALVADOR. QUE LÁ NA CRUZ ELE MORREU PARA NOS SALVAR DERRAMOU SEU SANGUE PURA E A VIDA ETERNA DÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
