INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('44', '44 - SOU SERVO DO SENHOR', 'SOU SERVO DO SENHOR, SERVI-LO É MEU PRAZER POIS SALVO EM SUA GRAÇA POR SEU FILHO JESUS, TORNOU-ME LIVRE.\n\nEM JESUS POSSO IR DIRETO AO PAI, COM OUSADIA ENTRAR NO SANTO DOS SANTOS COMO REI E SACERDOTE O SENHOR ME COLOCOU POSSO GOVERNAR, POSSO MINISTRAR, AO SENHOR SEMPRE ADORAR, EM ESPÍRITO E VERDADE HOJE E ETERNAMENTE.\n\nSOU SERVO DO SENHOR, SERVI-LO É MEU PRAZER POIS SALVO EM SUA GRAÇA POR SEU FILHO JESUS, TORNOU-ME LIVRE.\n\nEM JESUS POSSO IR DIRETO AO PAI, COM OUSADIA ENTRAR NO SANTO DOS SANTOS COMO REI E SACERDOTE O SENHOR ME COLOCOU POSSO GOVERNAR, POSSO MINISTRAR, AO SENHOR SEMPRE ADORAR, EM ESPÍRITO E VERDADE HOJE E ETERNAMENTE. (2X)', 'SOU SERVO DO SENHOR, SERVI-LO É MEU PRAZER POIS SALVO EM SUA GRAÇA POR SEU FILHO JESUS, TORNOU-ME LIVRE. EM JESUS POSSO IR DIRETO AO PAI, COM OUSADIA ENTRAR NO SANTO DOS SANTOS COMO REI E SACERDOTE O SENHOR ME COLOCOU POSSO GOVERNAR, POSSO MINISTRAR, AO SENHOR SEMPRE ADORAR, EM ESPÍRITO E VERDADE HOJE E ETERNAMENTE.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('45', '45 - JESUS RESSUSCITOU! (MEU AMIGO JESUS)', 'CORO (2X) JESUS RESSUSCITOU! CANTAREI EM MEU CORAÇÃO TODA GLÓRIA, HONRA E LOUVOR, A JESUS, POR SUA PAZ E PERDÃO.\n\nNO MEU CORAÇÃO NÃO HÁ TRISTEZAS, POIS JESUS, COM SEU SANGUE, O LAVOU. HOJE EU CANTO COM MUITA ALEGRIA, ADORANDO AO MEU REI E SENHOR.\n\nHOJE POSSO TESTEMUNHAR DE UM AMIGO QUE SÓ ME QUER BEM. DEU, NA CRUZ, SUA VIDA POR MIM, PRA QUE EU VÁ NO CÉU MORAR TAMBÉM.\n\nCORO JESUS RESSUSCITOU! CANTAREI EM MEU CORAÇÃO TODA GLÓRIA, HONRA E LOUVOR, A JESUS, POR SUA PAZ E PERDÃO. FINAL TODA GLÓRIA, HONRA E LOUVOR A JESUS, POR SUA PAZ E PERDÃO.', 'JESUS RESSUSCITOU! CANTAREI EM MEU CORAÇÃO TODA GLÓRIA, HONRA E LOUVOR, A JESUS, POR SUA PAZ E PERDÃO. NO MEU CORAÇÃO NÃO HÁ TRISTEZAS, POIS JESUS, COM SEU SANGUE, O LAVOU. HOJE EU CANTO COM MUITA ALEGRIA, ADORANDO AO MEU REI E SENHOR. HOJE POSSO TESTEMUNHAR DE UM AMIGO QUE SÓ ME QUER BEM. DEU, NA CRUZ, SUA VIDA POR MIM, PRA QUE EU VÁ NO CÉU MORAR TAMBÉM. CORO JESUS RESSUSCITOU! CANTAREI EM MEU CORAÇÃO TODA GLÓRIA, HONRA E LOUVOR, A JESUS, POR SUA PAZ E PERDÃO. FINAL TODA GLÓRIA, HONRA E LOUVOR A JESUS, POR SUA PAZ E PERDÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('46', '46 - COMO É BOM SERVIR A DEUS', 'COMO É BOM SERVIR A DEUS CONHECÊ-LO CADA VEZ MAIS E VIVENDO NO SEU AMOR SENTIR PROTEÇÃO.\n\nCORO (2X) QUE ME TRAZ SEGURANÇA, VIDA, AMOR, PAZ E ALEGRIA. COM MEU DEUS EU SEI QUE SOU MAIS QUE VENCEDOR. VOU PASSANDO AS PROVAÇÕES FIRMADO NO SEU AMOR.', 'COMO É BOM SERVIR A DEUS CONHECÊ-LO CADA VEZ MAIS E VIVENDO NO SEU AMOR SENTIR PROTEÇÃO. QUE ME TRAZ SEGURANÇA, VIDA, AMOR, PAZ E ALEGRIA. COM MEU DEUS EU SEI QUE SOU MAIS QUE VENCEDOR. VOU PASSANDO AS PROVAÇÕES FIRMADO NO SEU AMOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('47', '47 - TENHO NO MEU CORAÇÃO', 'CORO TENHO NO MEU CORAÇÃO O DESEJO DE SERVIR AO SENHOR. TENHO NO MEU CORAÇÃO O DESEJO DE LOUVAR AO SENHOR.\n\nSOU UM SERVO ALEGRE PORQUE POSSO SENTIR O AMOR DO SENHOR, PORQUE DESDE PEQUENINO POSSO OUVIR A VOZ DO SENHOR.\n\nQUANDO TRISTE EU ESTOU O SENHOR ME ESTENDE A MÃO. QUANDO OLHO PARA OS CÉUS, O SENHOR OUVE A MINHA ORAÇÃO.\n\nCORO TENHO NO MEU CORAÇÃO O DESEJO DE SERVIR AO SENHOR. TENHO NO MEU CORAÇÃO O DESEJO DE LOUVAR AO SENHOR.\n\nPARA TE LOUVAR. PARA TE LOUVAR.\n\nENTRÃO EU ABRO O MEU CORAÇÃO PARA TI, SENHOR, MEU JESUS. VEM EM MIM HABITAR, POIS UM SERVO EU SOU. POIS UM SERVO EU SOU. QUERO CRESCER NA TUA PRESENÇA Ó PAI, PARA TE LOUVAR. PARA TE LOUVAR.', 'CORO TENHO NO MEU CORAÇÃO O DESEJO DE SERVIR AO SENHOR. TENHO NO MEU CORAÇÃO O DESEJO DE LOUVAR AO SENHOR. SOU UM SERVO ALEGRE PORQUE POSSO SENTIR O AMOR DO SENHOR, PORQUE DESDE PEQUENINO POSSO OUVIR A VOZ DO SENHOR. QUANDO TRISTE EU ESTOU O SENHOR ME ESTENDE A MÃO. QUANDO OLHO PARA OS CÉUS, O SENHOR OUVE A MINHA ORAÇÃO. PARA TE LOUVAR. PARA TE LOUVAR. ENTRÃO EU ABRO O MEU CORAÇÃO PARA TI, SENHOR, MEU JESUS. VEM EM MIM HABITAR, POIS UM SERVO EU SOU. POIS UM SERVO EU SOU. QUERO CRESCER NA TUA PRESENÇA Ó PAI, PARA TE LOUVAR. PARA TE LOUVAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('48', '48 - JESUS CRISTO É O AMADO', 'JESUS CRISTO É O AMADO QUE MORREU PELO PECADOR O SEU SANGUE CLAMA AO PAI PARA REDENÇÃO, PARA OBTERMOS PERDÃO, SEGUNDO AS RIQUEZAS DE SUA GRAÇA.\n\nCORO POR ESSE MOTIVO ESCOLHEMOS FICAR CONTIGO, JESUS, E HONRAR O TEU SACRIFÍCIO FEITO NO ALTAR DA CRUZ. ESCOLHEMOS A VIDA E A BÊNÇÃO PARA NÓS E A NOSSA HERANÇA VIVERMOS SOB A TUA PROTEÇÃO.\n\nJESUS CRISTO É O AMADO QUE MORREU PELO PECADOR O SEU SANGUE CLAMA AO PAI PARA REDENÇÃO, PARA OBTERMOS PERDÃO, SEGUNDO AS RIQUEZAS DE SUA GRAÇA.\n\nCORO (2X) POR ESSE MOTIVO ESCOLHEMOS FICAR CONTIGO, JESUS, E HONRAR O TEU SACRIFÍCIO FEITO NO ALTAR DA CRUZ. ESCOLHEMOS A VIDA E A BÊNÇÃO PARA NÓS E A NOSSA HERANÇA VIVERMOS SOB A TUA PROTEÇÃO.', 'JESUS CRISTO É O AMADO QUE MORREU PELO PECADOR O SEU SANGUE CLAMA AO PAI PARA REDENÇÃO, PARA OBTERMOS PERDÃO, SEGUNDO AS RIQUEZAS DE SUA GRAÇA. CORO POR ESSE MOTIVO ESCOLHEMOS FICAR CONTIGO, JESUS, E HONRAR O TEU SACRIFÍCIO FEITO NO ALTAR DA CRUZ. ESCOLHEMOS A VIDA E A BÊNÇÃO PARA NÓS E A NOSSA HERANÇA VIVERMOS SOB A TUA PROTEÇÃO. POR ESSE MOTIVO ESCOLHEMOS FICAR CONTIGO, JESUS, E HONRAR O TEU SACRIFÍCIO FEITO NO ALTAR DA CRUZ. ESCOLHEMOS A VIDA E A BÊNÇÃO PARA NÓS E A NOSSA HERANÇA VIVERMOS SOB A TUA PROTEÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('49', '49 - A MINHA ALEGRIA É CRESCER COM O MEU SENHOR!', 'A MINHA ALEGRIA É CRESCER COM O MEU SENHOR! SENHOR DA MINHA VIDA, O MEU GRANDE AMOR! A TI EU RENDEREI TODA GLÓRIA E LOUVOR. AQUI TRABALHAREI NA TUA OBRA COM AMOR.\n\nUSA-ME SENHOR, PRA PREGAR AO PECADOR, USA-ME SENHOR, NA TUA OBRA DE AMOR, USA-ME SENHOR, QUERO TE GLORIFICAR! USA-ME SENHOR, EM TODO E QUALQUER LUGAR, USA-ME SENHOR, ATÉ MEU JESUS VOLTAR!', 'A MINHA ALEGRIA É CRESCER COM O MEU SENHOR! SENHOR DA MINHA VIDA, O MEU GRANDE AMOR! A TI EU RENDEREI TODA GLÓRIA E LOUVOR. AQUI TRABALHAREI NA TUA OBRA COM AMOR. USA-ME SENHOR, PRA PREGAR AO PECADOR, USA-ME SENHOR, NA TUA OBRA DE AMOR, USA-ME SENHOR, QUERO TE GLORIFICAR! USA-ME SENHOR, EM TODO E QUALQUER LUGAR, USA-ME SENHOR, ATÉ MEU JESUS VOLTAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('50', '50 - DO ALTO CÉU', 'DO ALTO CÉU ELE ME AMOU, DO MUNDO MAU ME RETIROU; FIZ A ESCOLHA DA SALVAÇÃO. EU ACEITEI O CORDEIRO DE DEUS.\n\nSEU SANGUE LAVOU O MEU CORAÇÃO, DE TODO PECADO ME PERDOOU; E HOJE SOU SALVO PELA GRAÇA DE DEUS. FINAL E HOJE SOU SALVO PELA GRAÇA DE DEUS.', 'DO ALTO CÉU ELE ME AMOU, DO MUNDO MAU ME RETIROU; FIZ A ESCOLHA DA SALVAÇÃO. EU ACEITEI O CORDEIRO DE DEUS. SEU SANGUE LAVOU O MEU CORAÇÃO, DE TODO PECADO ME PERDOOU; E HOJE SOU SALVO PELA GRAÇA DE DEUS. FINAL E HOJE SOU SALVO PELA GRAÇA DE DEUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('51', '51 - EIS-ME AQUI, SENHOR, EM TEU ALTAR', 'EIS-ME AQUI, SENHOR, EM TEU ALTAR, EIS-ME AQUI, SENHOR, PRONTO A TE ADORAR.\nBIS\n\nSEI QUE A PRESENÇA DO SENHOR JESUS NÃO FALHARÁ, SEI QUE TU ESTÁS AQUI, PRONTO A ABENÇOAR. INSTRUMENTOS\nBIS\n\nSANTO E GLORIOSO ÉS, CONOSCO ESTÁS, SANTO E GLORIOSO, VIMOS TE ADORAR, SANTO E GLORIOSO ÉS, Ó NOSSO AMADO, DO TEU ESPÍRITO TENS DERRAMADO, DANDO RENOVO E TRANSFORMANDO; SANTO E GLORIOSO ÉS, VEM EM NÓS TRANSBORDAR, Ó SANTO ESPÍRITO, TOMA O TEU LUGAR.\n\nSEI QUE A PRESENÇA DO SENHOR JESUS NÃO FALHARÁ, SEI QUE TU ESTÁS AQUI, PRONTO A ABENÇOAR.\nBIS', 'EIS-ME AQUI, SENHOR, EM TEU ALTAR, EIS-ME AQUI, SENHOR, PRONTO A TE ADORAR. SEI QUE A PRESENÇA DO SENHOR JESUS NÃO FALHARÁ, SEI QUE TU ESTÁS AQUI, PRONTO A ABENÇOAR. INSTRUMENTOS SANTO E GLORIOSO ÉS, CONOSCO ESTÁS, SANTO E GLORIOSO, VIMOS TE ADORAR, SANTO E GLORIOSO ÉS, Ó NOSSO AMADO, DO TEU ESPÍRITO TENS DERRAMADO, DANDO RENOVO E TRANSFORMANDO; SANTO E GLORIOSO ÉS, VEM EM NÓS TRANSBORDAR, Ó SANTO ESPÍRITO, TOMA O TEU LUGAR. SEI QUE A PRESENÇA DO SENHOR JESUS NÃO FALHARÁ, SEI QUE TU ESTÁS AQUI, PRONTO A ABENÇOAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('52', '52- SEU NOME É MARAVILHOSO', 'SEU NOME É MARAVILHOSO, (3x) PAI DA ETERNIDADE, PRÍNCIPE DA PAZ. SEU NOME É CONSELHEIRO, (3X) PAI DA ETERNIDADE, PRÍNCIPE DA PAZ\n\nSEU NOME É DEUS FORTE, (3X) PAI DA ETERNIDADE, PRÍNCIPE DA PAZ. IREI SEGUI-LO PARA SEMPRE POIS ELE DÁ FELICIDADE SEU NOME É MARAVILHOSO, PAI DA ETERNIDADE, PRÍNCIPE DA PAZ.', 'SEU NOME É MARAVILHOSO,  PAI DA ETERNIDADE, PRÍNCIPE DA PAZ. SEU NOME É CONSELHEIRO,  PAI DA ETERNIDADE, PRÍNCIPE DA PAZ SEU NOME É DEUS FORTE,  PAI DA ETERNIDADE, PRÍNCIPE DA PAZ. IREI SEGUI-LO PARA SEMPRE POIS ELE DÁ FELICIDADE SEU NOME É MARAVILHOSO, PAI DA ETERNIDADE, PRÍNCIPE DA PAZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
