INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('54', '54 - COMO É BOM SABER', 'COMO É BOM SABER QUE JESUS CRISTO FOI MENINO ASSIM COMO EU, QUE BRINCAVA E TRABALHAVA COMO UM JOVEM GALILEU.\n\nCORO QUE PRAZER CONHECER CRISTO QUE TAMBÉM FOI MENINO AQUI E SABER QUE NO CÉU LINDO, QUER TER-ME JUNTO A SI!\n\nE É MELHOR SABER QUE JESUS CRISTO JÁ SUBIU LÁ PARA O CÉU E PREPARA UM LUGARZINHO DE UM MENINO, ASSIM, COMO EU.\n\nCORO QUE PRAZER CONHECER CRISTO QUE TAMBÉM FOI MENINO AQUI E SABER QUE NO CÉU LINDO, QUER TER-ME JUNTO A SI!', 'COMO É BOM SABER QUE JESUS CRISTO FOI MENINO ASSIM COMO EU, QUE BRINCAVA E TRABALHAVA COMO UM JOVEM GALILEU. CORO QUE PRAZER CONHECER CRISTO QUE TAMBÉM FOI MENINO AQUI E SABER QUE NO CÉU LINDO, QUER TER-ME JUNTO A SI! E É MELHOR SABER QUE JESUS CRISTO JÁ SUBIU LÁ PARA O CÉU E PREPARA UM LUGARZINHO DE UM MENINO, ASSIM, COMO EU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('55', '55 - É SOMENTE TEU MEU CORAÇÃO SENHOR', 'É SOMENTE TEU MEU CORAÇÃO, SENHOR, É SOMENTE TEU MEU CORPO E MEU LOUVOR; É SOMENTE TEU, MEU PAI, O MEU AMOR, SOU SOMENTE TEU, MEU REDENTOR.\n\nQUERO TE AMAR, MEU DEUS, Ó MEU SENHOR, QUERO TE ADORAR, PRA SEMPRE, COM ARDOR, QUERO A TI ORAR COM FÉ E COM FERVOR, SOU SOMENTE TEU, MEU REDENTOR.', 'É SOMENTE TEU MEU CORAÇÃO, SENHOR, É SOMENTE TEU MEU CORPO E MEU LOUVOR; É SOMENTE TEU, MEU PAI, O MEU AMOR, SOU SOMENTE TEU, MEU REDENTOR. QUERO TE AMAR, MEU DEUS, Ó MEU SENHOR, QUERO TE ADORAR, PRA SEMPRE, COM ARDOR, QUERO A TI ORAR COM FÉ E COM FERVOR, SOU SOMENTE TEU, MEU REDENTOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
