INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('58', '58 - VIVER É TÃO LINDO', 'VIVER É TÃO LINDO QUANDO SE TEM JESUS, O MUNDO É TÃO PEQUENO PARA O QUE EU TENHO EM MIM; E O QUE TENHO É BEM MAIOR QUE TUDO NA VIDA EU TENHO É JESUS\n\nVIVO A CANTAR, VIVO A SORRIR TUDO O QUE EU TENHO JAMAIS SE ACABARÁ.', 'VIVER É TÃO LINDO QUANDO SE TEM JESUS, O MUNDO É TÃO PEQUENO PARA O QUE EU TENHO EM MIM; E O QUE TENHO É BEM MAIOR QUE TUDO NA VIDA EU TENHO É JESUS VIVO A CANTAR, VIVO A SORRIR TUDO O QUE EU TENHO JAMAIS SE ACABARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('59', '59 - O NOME DO MEU SENHOR', 'O NOME DO MEU SENHOR, O QUE VENCEU A MORTE E A CRUZ, O ÚNICO QUE É DIGNO DE LOUVOR, O SEU NOME É JESUS!\n\nCORO É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA.\n\nO MEU SENHOR DERRAMOU O SEU PRECIOSO SANGUE NA CRUZ, PERDOOU MEUS PECADOS, LAVOU-ME, O SEU NOME É JESUS!\n\nCORO É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA.\n\nAGORA VOU, FELIZ, TE ADORAR, POIS O TEU SANGUE ME LIMPOU. CAMINHANDO VOU PARA A PÁTRIA, Ó JESUS, VEM ME BUSCAR!\n\nCORO É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. MINHA ESPERANÇA É JESUS,', 'O NOME DO MEU SENHOR, O QUE VENCEU A MORTE E A CRUZ, O ÚNICO QUE É DIGNO DE LOUVOR, O SEU NOME É JESUS! CORO É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. O MEU SENHOR DERRAMOU O SEU PRECIOSO SANGUE NA CRUZ, PERDOOU MEUS PECADOS, LAVOU-ME, O SEU NOME É JESUS! AGORA VOU, FELIZ, TE ADORAR, POIS O TEU SANGUE ME LIMPOU. CAMINHANDO VOU PARA A PÁTRIA, Ó JESUS, VEM ME BUSCAR! CORO É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. É FIEL, É A PAZ, É AMOR, É O SALVADOR! É A ESPERANÇA ETERNA. MINHA ESPERANÇA É JESUS,', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
