INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', '.', '73 - DE ETERNIDADE A ETERNIDADE\n\nDE ETERNIDADE A ETERNIDADE JESUS É O NOSSO DEUS. TODAS AS COISAS POR ELE FORAM FEITAS E SEM ELE NADA DO QUE FOI FEITO SE FEZ.\n\nO HOMEM DEUS CRIOU, IGUAL A ELE O FORMOU, PODENDO ASSIM VIVER ETERNAMENTE COM JESUS.\n\nMAS O HOMEM PECOU E DE DEUS SE AFASTOU. NOVO PROJETO DEUS CRIOU POR SEU AMOR, PRA RESGATAR O HOMEM, POBRE PECADOR:\n\n“PORQUE DEUS AMOU O MUNDO DE TAL MANEIRA QUE DEU O SEU FILHO UNIGÊNITO, PARA QUE TODO AQUELE QUE NELE CRÊ NÃO PEREÇA, MAS TENHA A VIDA ETERNA.”\n\nCREIO EM JESUS, ETERNAMENTE VIVEREI; SEGUINDO A JESUS PARA O CÉU EU SUBIREI E ADORAREI O REI, ADORAREI O REI, ADORAREI O REI JESUS.', '73 - DE ETERNIDADE A ETERNIDADE DE ETERNIDADE A ETERNIDADE JESUS É O NOSSO DEUS. TODAS AS COISAS POR ELE FORAM FEITAS E SEM ELE NADA DO QUE FOI FEITO SE FEZ. O HOMEM DEUS CRIOU, IGUAL A ELE O FORMOU, PODENDO ASSIM VIVER ETERNAMENTE COM JESUS. MAS O HOMEM PECOU E DE DEUS SE AFASTOU. NOVO PROJETO DEUS CRIOU POR SEU AMOR, PRA RESGATAR O HOMEM, POBRE PECADOR: PORQUE DEUS AMOU O MUNDO DE TAL MANEIRA QUE DEU O SEU FILHO UNIGÊNITO, PARA QUE TODO AQUELE QUE NELE CRÊ NÃO PEREÇA, MAS TENHA A VIDA ETERNA. CREIO EM JESUS, ETERNAMENTE VIVEREI; SEGUINDO A JESUS PARA O CÉU EU SUBIREI E ADORAREI O REI, ADORAREI O REI, ADORAREI O REI JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('74', '74 - AQUILO QUE MELHOR EXISTE', 'AQUILO QUE MELHOR EXISTE É FALAR DE JESUS. ELE É O CAMINHO E A VIDA, A VERDADE E A LUZ.\nBIS\n\nJESUS, AO APROXIMAR-SE, JOÃO BATISTA APONTOU: “EIS O CORDEIRO DE DEUS, QUE VEIO AO MUNDO NOS DAR SALVAÇÃO”.\n\nOS DISCÍPULOS, DESANIMADOS POR PESCAREM EM VÃO, JESUS OS MANDA DE VOLTA AO MAR E MUITOS PEIXES PESCAR.\n\nALEGRES DESCOBRIRAM: “O CORDEIRO DE DEUS ESTÁ VIVO ENTRE NÓS!” REINA PRA SEMPRE O MEU SALVADOR.', 'AQUILO QUE MELHOR EXISTE É FALAR DE JESUS. ELE É O CAMINHO E A VIDA, A VERDADE E A LUZ. JESUS, AO APROXIMAR-SE, JOÃO BATISTA APONTOU: EIS O CORDEIRO DE DEUS, QUE VEIO AO MUNDO NOS DAR SALVAÇÃO. OS DISCÍPULOS, DESANIMADOS POR PESCAREM EM VÃO, JESUS OS MANDA DE VOLTA AO MAR E MUITOS PEIXES PESCAR. ALEGRES DESCOBRIRAM: O CORDEIRO DE DEUS ESTÁ VIVO ENTRE NÓS! REINA PRA SEMPRE O MEU SALVADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('75', '75 - VOCÊ SABE QUEM FOI JOÃO BATISTA?', 'VOCÊ SABE QUEM FOI JOÃO BATISTA? FOI UM PROFETA DE DEUS. ELE VEIO PARA TESTEMUNHAR DE UMA VERDADEIRA LUZ.\n\nQUEM CRÊ EM JESUS TEM O PODER DE SER FEITO FILHO DE DEUS, COM O DIREITO NA HERANÇA DA VIDA ETERNA COM JESUS.\n\nVOCÊ SABE QUEM FOI O OUTRO JOÃO? UM APÓSTOLO DE JESUS. ESCREVEU O EVANGELHO DE JOÃO, PRA MOSTRAR QUE JESUS É DEUS.\n\nESCREVEU TAMBÉM JOÃO 1, 2, 3 E O LIVRO DE APOCALIPSE, QUE CONTA A HISTÓRIA DO TEMPO DO FIM E DAS LINDAS PROMESSAS DE DEUS PARA A SUA IGREJA.', 'VOCÊ SABE QUEM FOI JOÃO BATISTA? FOI UM PROFETA DE DEUS. ELE VEIO PARA TESTEMUNHAR DE UMA VERDADEIRA LUZ. QUEM CRÊ EM JESUS TEM O PODER DE SER FEITO FILHO DE DEUS, COM O DIREITO NA HERANÇA DA VIDA ETERNA COM JESUS. VOCÊ SABE QUEM FOI O OUTRO JOÃO? UM APÓSTOLO DE JESUS. ESCREVEU O EVANGELHO DE JOÃO, PRA MOSTRAR QUE JESUS É DEUS. ESCREVEU TAMBÉM JOÃO 1, 2, 3 E O LIVRO DE APOCALIPSE, QUE CONTA A HISTÓRIA DO TEMPO DO FIM E DAS LINDAS PROMESSAS DE DEUS PARA A SUA IGREJA.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('76', '76 - JESUS FALOU QUE O CÉU É PRA MIM', 'JESUS FALOU QUE O CÉU É PRA MIM (3X) JESUS FALOU QUE O CÉU É PRA MIM, O CÉU É PRA MIM, EU VOU COM ELE SIM.\n\nCORO E SE VOCÊ QUISER O CÉU TAMBÉM PRECISA ACEITAR JESUS EM SEU CORAÇÃO, PARA PODER LHE TRANSFORMAR E O CÉU LHE DAR E LÁ VOCÊ MORAR.\n\nEU SOU FIEL E JESUS ME QUER BEM, (3X) POR ISSO EU SEI QUE O CÉU É PRA MIM, O CÉU É PRA MIM. EU VOU PRA GLÓRIA, SIM.\n\nCORO E SE VOCÊ QUISER O CÉU TAMBÉM PRECISA ACEITAR JESUS EM SEU CORAÇÃO, PARA PODER LHE TRANSFORMAR E O CÉU LHE DAR E LÁ VOCÊ MORAR.', 'JESUS FALOU QUE O CÉU É PRA MIM  JESUS FALOU QUE O CÉU É PRA MIM, O CÉU É PRA MIM, EU VOU COM ELE SIM. CORO E SE VOCÊ QUISER O CÉU TAMBÉM PRECISA ACEITAR JESUS EM SEU CORAÇÃO, PARA PODER LHE TRANSFORMAR E O CÉU LHE DAR E LÁ VOCÊ MORAR. EU SOU FIEL E JESUS ME QUER BEM,  POR ISSO EU SEI QUE O CÉU É PRA MIM, O CÉU É PRA MIM. EU VOU PRA GLÓRIA, SIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('77', '77 - VOU CONTAR-VOS DE JESUS', 'VOU CONTAR-VOS DE JESUS O QUE JOÃO NOS ENSINOU: ELE, POR AMOR, SE FEZ NOSSO SALVADOR.\n\nDE ALUNOS QUE CHAMOU: PEDRO, ANDRÉ, TIAGO E JOÃO, FILIPE, BARTOLOMEU E MATEUS, TIAGO, SIMÃO, TOMÉ, JUDAS, O BOM AMIGO, OUTRO JUDAS, O TRAIDOR.\n\nJESUS A TODOS CHAMOU: “EU SOU CAMINHO, A VERDADE E A LUZ”, A ELES ENSINOU.\n\nVOU CONTAR-VOS DE JESUS O QUE JOÃO NOS ENSINOU: ELE, POR AMOR, SE FEZ NOSSO SALVADOR.\n\nDE ALUNOS QUE CHAMOU: PEDRO, ANDRÉ, TIAGO E JOÃO, FILIPE, BARTOLOMEU E MATEUS, TIAGO, SIMÃO, TOMÉ, JUDAS, O BOM AMIGO, OUTRO JUDAS, O TRAIDOR.\n\nJESUS A TODOS CHAMOU: “EU SOU CAMINHO, A VERDADE E A LUZ”, A ELES ENSINOU.', 'VOU CONTAR-VOS DE JESUS O QUE JOÃO NOS ENSINOU: ELE, POR AMOR, SE FEZ NOSSO SALVADOR. DE ALUNOS QUE CHAMOU: PEDRO, ANDRÉ, TIAGO E JOÃO, FILIPE, BARTOLOMEU E MATEUS, TIAGO, SIMÃO, TOMÉ, JUDAS, O BOM AMIGO, OUTRO JUDAS, O TRAIDOR. JESUS A TODOS CHAMOU: EU SOU CAMINHO, A VERDADE E A LUZ, A ELES ENSINOU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('78', '78 - O SENHOR É A MINHA LUZ', 'O SENHOR É A MINHA LUZ E A MINHA SALVAÇÃO. A QUEM EU TEMEREI? DE QUEM ME RECEAREI?\nBIS\n\nBOM E RETO É O SENHOR, POIS APONTA O CAMINHO AO PECADOR. NELE HÁ O PERDÃO, NELE ESPERO TODO DIA. (3X)', 'O SENHOR É A MINHA LUZ E A MINHA SALVAÇÃO. A QUEM EU TEMEREI? DE QUEM ME RECEAREI? BOM E RETO É O SENHOR, POIS APONTA O CAMINHO AO PECADOR. NELE HÁ O PERDÃO, NELE ESPERO TODO DIA.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('79', '79 - DEUS AMA VOCÊ', 'DEUS AMA VOCÊ E ELE QUER TE SALVAR, ELE VAI OPERAR EM SEU CORAÇÃO.\n\nA VIDA QUE UM DIA VOCÊ NÃO CONHECIA, GOZO, PAZ E ALEGRIA EM JESUS ENCONTROU.\n\nELE É O CAMINHO, A VERDADE E A VIDA E OFERECE A VOCÊ SALVAÇÃO. BASTA ABRIR A PORTA DO CORAÇÃO, PRA SENTIR A SUA PAZ E PERDÃO.\n\nELE É O CAMINHO, A VERDADE E A VIDA E OFERECE A VOCÊ SALVAÇÃO. BASTA ABRIR A PORTA DO CORAÇÃO, PRA SENTIR A SUA PAZ E PERDÃO. (2X)', 'DEUS AMA VOCÊ E ELE QUER TE SALVAR, ELE VAI OPERAR EM SEU CORAÇÃO. A VIDA QUE UM DIA VOCÊ NÃO CONHECIA, GOZO, PAZ E ALEGRIA EM JESUS ENCONTROU. ELE É O CAMINHO, A VERDADE E A VIDA E OFERECE A VOCÊ SALVAÇÃO. BASTA ABRIR A PORTA DO CORAÇÃO, PRA SENTIR A SUA PAZ E PERDÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('80', '80 - EXISTE UM LUGAR', 'EXISTE UM LUGAR QUE MEU JESUS FOI PREPARAR. SE VOCÊ QUISER CHEGAR LÁ, É SÓ A ELE SE ENTREGAR.\n\nESSE LUGAR É O CÉU DE LUZ, ONDE HABITA O MEU JESUS. SE QUERES PAZ, ALEGRIA E PERDÃO ENTREGA A ELE O TEU CORAÇÃO.', 'EXISTE UM LUGAR QUE MEU JESUS FOI PREPARAR. SE VOCÊ QUISER CHEGAR LÁ, É SÓ A ELE SE ENTREGAR. ESSE LUGAR É O CÉU DE LUZ, ONDE HABITA O MEU JESUS. SE QUERES PAZ, ALEGRIA E PERDÃO ENTREGA A ELE O TEU CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('81', '81 - QUANDO OUVI JESUS ME CHAMAR', 'QUANDO OUVI JESUS ME CHAMAR PENSEI QUE ERA IRREAL, SUA VOZ DOCE E MEIGA A DIZER: “VINDE PEQUENINOS DO PAI”.\n\nENTÃO PUDE CRER QUE JESUS QUER USAR-ME TAMBÉM. AINDA PEQUENINO SOU, MAS POSSO LEVAR, DE DEUS, A MENSAGEM DE AMOR.\n\nCORO QUEM QUER SUBIR PARA O CÉU PEQUENO SE FAÇA TAMBÉM, ENTREGA TUA VIDA A JESUS, O PAI CELESTE QUE DESEJA A TI TODO BEM.\n\nSE QUERES TER DE CRISTO O PERDÃO ABRE HOJE O TEU CORAÇÃO. DEIXE CRISTO ENTRAR E REINAR E A PAZ EM TUA VIDA NUNCA IRÁ FALTAR.\n\nCORO QUEM QUER SUBIR PARA O CÉU PEQUENO SE FAÇA TAMBÉM, ENTREGA TUA VIDA A JESUS, O PAI CELESTE QUE DESEJA A TI TODO BEM.\n\nCORO QUEM QUER SUBIR PARA O CÉU PEQUENO SE FAÇA TAMBÉM, ENTREGA TUA VIDA A JESUS, O PAI CELESTE QUE DESEJA A TI TODO BEM. O PAI CELESTE QUE DESEJA A TI TODO BEM.', 'QUANDO OUVI JESUS ME CHAMAR PENSEI QUE ERA IRREAL, SUA VOZ DOCE E MEIGA A DIZER: VINDE PEQUENINOS DO PAI. ENTÃO PUDE CRER QUE JESUS QUER USAR-ME TAMBÉM. AINDA PEQUENINO SOU, MAS POSSO LEVAR, DE DEUS, A MENSAGEM DE AMOR. CORO QUEM QUER SUBIR PARA O CÉU PEQUENO SE FAÇA TAMBÉM, ENTREGA TUA VIDA A JESUS, O PAI CELESTE QUE DESEJA A TI TODO BEM. SE QUERES TER DE CRISTO O PERDÃO ABRE HOJE O TEU CORAÇÃO. DEIXE CRISTO ENTRAR E REINAR E A PAZ EM TUA VIDA NUNCA IRÁ FALTAR. CORO QUEM QUER SUBIR PARA O CÉU PEQUENO SE FAÇA TAMBÉM, ENTREGA TUA VIDA A JESUS, O PAI CELESTE QUE DESEJA A TI TODO BEM. O PAI CELESTE QUE DESEJA A TI TODO BEM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('82', '82 - JESUS VEM NOS BUSCAR', 'JESUS VEM NOS BUSCAR, PROMETEU E NÃO FALHARÁ, PORÉM ELE NOS DISSE: DOIS CAMINHOS HAVERÁ.\n\nUM CAMINHO É CERTO, É ESTREITO E APERTADO, É O CAMINHO DE JESUS, FIRMADO NELE QUERO ESTAR.\n\nOUTRO CAMINHO TAMBÉM HÁ, É LARGO, ESPAÇOSO E CONDUZ À PERDIÇÃO, POR ELE MUITOS VÃO, POIS REJEITAM DE JESUS A SALVAÇÃO.\n\nJESUS A TODOS CONVIDA: “EU SOU O CAMINHO A VERDADE E A VIDA”. VEM DEPRESSA, VEM DEPRESSA, VEM, A PORTA ABERTA UM DIA FECHARÁ.\nBIS', 'JESUS VEM NOS BUSCAR, PROMETEU E NÃO FALHARÁ, PORÉM ELE NOS DISSE: DOIS CAMINHOS HAVERÁ. UM CAMINHO É CERTO, É ESTREITO E APERTADO, É O CAMINHO DE JESUS, FIRMADO NELE QUERO ESTAR. OUTRO CAMINHO TAMBÉM HÁ, É LARGO, ESPAÇOSO E CONDUZ À PERDIÇÃO, POR ELE MUITOS VÃO, POIS REJEITAM DE JESUS A SALVAÇÃO. JESUS A TODOS CONVIDA: EU SOU O CAMINHO A VERDADE E A VIDA. VEM DEPRESSA, VEM DEPRESSA, VEM, A PORTA ABERTA UM DIA FECHARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('83', '83 - FOI NO CAMINHO PARA DAMASCO', 'FOI NO CAMINHO PARA DAMASCO QUE O GRANDE SAULO, PERSEGUIDOR, VIU GLORIOSA LUZ CELESTE, QUE POR TERRA O DERRUBOU.\n\nDESSA LUZ SURGIU A VOZ: SAULO, SAULO, QUE ME PERSEGUES. ELE, CAÍDO, RESPONDEU: QUEM ÉS, SENHOR? QUEM ÉS, SENHOR? INSTRUMENTOS\n\nEU SOU JESUS, QUE TU PERSEGUES. QUE QUERES TU, SENHOR, QUE EU FAÇA? LEVANTA-TE E OBEDECE. NOVO CAMINHO JESUS TE DÁ.\n\nQUERO TAMBÉM SER DE JESUS, TER ESTA LUZ, ME ILUMINAR. E A CADA PASSO OUVIR SUA VOZ, ATÉ O DIA QUE AO CÉU CHEGAR.', 'FOI NO CAMINHO PARA DAMASCO QUE O GRANDE SAULO, PERSEGUIDOR, VIU GLORIOSA LUZ CELESTE, QUE POR TERRA O DERRUBOU. DESSA LUZ SURGIU A VOZ: SAULO, SAULO, QUE ME PERSEGUES. ELE, CAÍDO, RESPONDEU: QUEM ÉS, SENHOR? QUEM ÉS, SENHOR? INSTRUMENTOS EU SOU JESUS, QUE TU PERSEGUES. QUE QUERES TU, SENHOR, QUE EU FAÇA? LEVANTA-TE E OBEDECE. NOVO CAMINHO JESUS TE DÁ. QUERO TAMBÉM SER DE JESUS, TER ESTA LUZ, ME ILUMINAR. E A CADA PASSO OUVIR SUA VOZ, ATÉ O DIA QUE AO CÉU CHEGAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('84', '84 - QUERES SER FELIZ?', 'QUERES SER FELIZ, CONHECER JESUS? ELE VAI TE DAR ALEGRIA, VIDA E PAZ.\n\nEU JÁ SOU FELIZ, DEI MEU CORAÇÃO E JESUS EM MIM VEIO HABITAR.\n\nQUERES SER FELIZ, CONHECER JESUS? ELE VAI TE DAR ALEGRIA, VIDA E PAZ. EU JÁ SOU FELIZ, DEI MEU CORAÇÃO E JESUS EM MIM VEIO HABITAR.', 'QUERES SER FELIZ, CONHECER JESUS? ELE VAI TE DAR ALEGRIA, VIDA E PAZ. EU JÁ SOU FELIZ, DEI MEU CORAÇÃO E JESUS EM MIM VEIO HABITAR. QUERES SER FELIZ, CONHECER JESUS? ELE VAI TE DAR ALEGRIA, VIDA E PAZ. EU JÁ SOU FELIZ, DEI MEU CORAÇÃO E JESUS EM MIM VEIO HABITAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('85', '85 - NESTE DIA ESPECIAL', 'Neste dia especial, Vou lhe apresentar Jesus, Que dissipa todo o mal e faz Raiar em nós sua luz.\n\nCORO JESUS É BOM AMIGO, ABRIGO NO TEMPORAL, QUE NOS LIVRA DO PERIGO E POR NÓS VENCEU O MAL.\n\nABRA o seu coração e venha conhecer jesus, Com amor e segurança À Vida eterna O conduz\n\nCORO JESUS É BOM AMIGO, ABRIGO NO TEMPORAL, QUE NOS LIVRA DO PERIGO E POR NÓS VENCEU O MAL. FINAL: SIM, POR NÓS VENCEU O MAL.', 'NESTE DIA ESPECIAL, VOU LHE APRESENTAR JESUS, QUE DISSIPA TODO O MAL E FAZ RAIAR EM NÓS SUA LUZ. CORO JESUS É BOM AMIGO, ABRIGO NO TEMPORAL, QUE NOS LIVRA DO PERIGO E POR NÓS VENCEU O MAL. ABRA O SEU CORAÇÃO E VENHA CONHECER JESUS, COM AMOR E SEGURANÇA À VIDA ETERNA O CONDUZ CORO JESUS É BOM AMIGO, ABRIGO NO TEMPORAL, QUE NOS LIVRA DO PERIGO E POR NÓS VENCEU O MAL.  SIM, POR NÓS VENCEU O MAL.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('86', '86 - UM DIA MORREU O MEU JESUS', 'UM DIA MORREU O MEU JESUS NAQUELA CRUZ POR MIM, O SANGUE DERRAMADO LIMPOU OS MEUS PECADOS. EU VOU CANTAR, PORQUE JESUS ESTÁ AQUI.\n\nCORO AGORA MEU JESUS VOU ADORAR, EU VOU CANTAR. EU VOU LOUVAR COMO DAVI, AO DERROTAR OS QUE QUERIAM O ATINGIR. INSTRUMENTOS\n\nJESUS, EU QUERO MINHA VIDA TE ENTREGAR E QUERO MAIS A TUA OBRA REALIZAR, SERVI-LO SEMPRE DE TODO O CORAÇÃO E SEMPRE EU CANTAREI ESTA CANÇÃO.\n\nCORO (2X) AGORA MEU JESUS VOU ADORAR, EU VOU CANTAR. EU VOU LOUVAR COMO DAVI, AO DERROTAR OS QUE QUERIAM O ATINGIR.', 'UM DIA MORREU O MEU JESUS NAQUELA CRUZ POR MIM, O SANGUE DERRAMADO LIMPOU OS MEUS PECADOS. EU VOU CANTAR, PORQUE JESUS ESTÁ AQUI. CORO AGORA MEU JESUS VOU ADORAR, EU VOU CANTAR. EU VOU LOUVAR COMO DAVI, AO DERROTAR OS QUE QUERIAM O ATINGIR. INSTRUMENTOS JESUS, EU QUERO MINHA VIDA TE ENTREGAR E QUERO MAIS A TUA OBRA REALIZAR, SERVI-LO SEMPRE DE TODO O CORAÇÃO E SEMPRE EU CANTAREI ESTA CANÇÃO. AGORA MEU JESUS VOU ADORAR, EU VOU CANTAR. EU VOU LOUVAR COMO DAVI, AO DERROTAR OS QUE QUERIAM O ATINGIR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('87', '87 - O SENHOR É O BOM PASTOR', 'O SENHOR É O BOM PASTOR, ELE É O SALVADOR, O DEUS DE PAZ E DE ALEGRIA, O DEUS DE AMOR.\n\nCORO ELE VEM ME BUSCAR E VAI ME LEVAR PARA A CIDADE CELESTIAL. VOCÊ TAMBÉM PODE IR, É SÓ ANDAR NO CAMINHO DE CRISTO JESUS.\n\nELE CURA E SALVA, FALA AOS CORAÇÕES, CUIDA DOS SEUS FILHOS COM MUITO AMOR.\n\nCORO ELE VEM ME BUSCAR E VAI ME LEVAR PARA A CIDADE CELESTIAL. VOCÊ TAMBÉM PODE IR, É SÓ ANDAR NO CAMINHO DE CRISTO JESUS.\n\nCORO ELE VEM ME BUSCAR E VAI ME LEVAR PARA A CIDADE CELESTIAL. VOCÊ TAMBÉM PODE IR, É SÓ ANDAR NO CAMINHO DE CRISTO JESUS. NO CAMINHO DE CRISTO JESUS.', 'O SENHOR É O BOM PASTOR, ELE É O SALVADOR, O DEUS DE PAZ E DE ALEGRIA, O DEUS DE AMOR. CORO ELE VEM ME BUSCAR E VAI ME LEVAR PARA A CIDADE CELESTIAL. VOCÊ TAMBÉM PODE IR, É SÓ ANDAR NO CAMINHO DE CRISTO JESUS. ELE CURA E SALVA, FALA AOS CORAÇÕES, CUIDA DOS SEUS FILHOS COM MUITO AMOR. CORO ELE VEM ME BUSCAR E VAI ME LEVAR PARA A CIDADE CELESTIAL. VOCÊ TAMBÉM PODE IR, É SÓ ANDAR NO CAMINHO DE CRISTO JESUS. NO CAMINHO DE CRISTO JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('88', '88 - VOU CONTAR PARA VOCÊ UMA HISTÓRIA ESPECIAL', 'VOU CONTAR PARA VOCÊ UMA HISTÓRIA ESPECIAL: É A HISTÓRIA DE JESUS, NOSSO AMIGO LEAL.\n\nELE VEIO COM A MISSÃO DE NOS DAR A SALVAÇÃO, PARA TANTO HOMEM SE FEZ, DEIXOU O CÉU DE ESPLENDOR, VEIO À TERRA DEMONSTRAR DE DEUS O INFINITO AMOR\n\nPADECEU EM UMA CRUZ E MORREU PARA NOS LIMPAR, NOSSO PECADO LAVAR COM SEU SANGUE REMIDOR, E VIVO ELE ESTÁ!\n\nNO TERCEIRO DIA RESSUSCITOU, À DIREITA DE DEUS PAI ROGA PELO PECADOR. BREVE, BREVE VOLTARÁ PARA A IGREJA ARREBATAR E JUNTOS VAMOS MORAR NO CELESTE LAR DE AMOR.\n\nSE VOCÊ TAMBÉM QUISER NESTE LAR DE AMOR MORAR BASTA ABRIR SEU CORAÇÃO E DEIXAR JESUS ENTRAR. FINAL: BASTA ABRIR SEU CORAÇÃO E DEIXAR JESUS ENTRAR.', 'VOU CONTAR PARA VOCÊ UMA HISTÓRIA ESPECIAL: É A HISTÓRIA DE JESUS, NOSSO AMIGO LEAL. ELE VEIO COM A MISSÃO DE NOS DAR A SALVAÇÃO, PARA TANTO HOMEM SE FEZ, DEIXOU O CÉU DE ESPLENDOR, VEIO À TERRA DEMONSTRAR DE DEUS O INFINITO AMOR PADECEU EM UMA CRUZ E MORREU PARA NOS LIMPAR, NOSSO PECADO LAVAR COM SEU SANGUE REMIDOR, E VIVO ELE ESTÁ! NO TERCEIRO DIA RESSUSCITOU, À DIREITA DE DEUS PAI ROGA PELO PECADOR. BREVE, BREVE VOLTARÁ PARA A IGREJA ARREBATAR E JUNTOS VAMOS MORAR NO CELESTE LAR DE AMOR. SE VOCÊ TAMBÉM QUISER NESTE LAR DE AMOR MORAR BASTA ABRIR SEU CORAÇÃO E DEIXAR JESUS ENTRAR.  BASTA ABRIR SEU CORAÇÃO E DEIXAR JESUS ENTRAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('89', '89 - CERTO HOMEM, PRÍNCIPE DOS JUDEUS (NICODEMOS)', 'CERTO HOMEM, PRÍNCIPE DOS JUDEUS, FOI AO MESTRE PERGUNTAR-LHE ASSIM:\n\nBEM SABEMOS QUE ÉS MESTRE VINDO DE DEUS. COMO PODE ALGUÉM NASCER DE NOVO?\n\nAQUELE QUE NÃO NASCER DA ÁGUA E DO ESPÍRITO NÃO PODE VER O REINO DE DEUS. MAS AQUELE QUE PERMANECER FIEL ATÉ O FIM, O VERÁ.', 'CERTO HOMEM, PRÍNCIPE DOS JUDEUS, FOI AO MESTRE PERGUNTAR-LHE ASSIM: BEM SABEMOS QUE ÉS MESTRE VINDO DE DEUS. COMO PODE ALGUÉM NASCER DE NOVO? AQUELE QUE NÃO NASCER DA ÁGUA E DO ESPÍRITO NÃO PODE VER O REINO DE DEUS. MAS AQUELE QUE PERMANECER FIEL ATÉ O FIM, O VERÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('90', '90 - ELE MORREU MAS RESSURGIU', 'ELE MORREU, MAS RESSURGIU; PLENA PAZ ENCHEU O MEU VIVER; JESUS É TUDO O QUE SEMPRE QUIS, JESUS ME TROUXE O SEU PERDÃO.\n\nQUERO FALAR DESTA SALVAÇÃO, POR ONDE QUER QUE EU POSSA ANDAR, JESUS É TUDO, ELE É A PAZ. QUE VOCÊ PODE ENCONTRAR (2X) INSTRUMENTOS\n\nCRISTO, O SENHOR, É QUEM ME CONDUZ, LIVRA-ME DO MAL E DA TENTAÇÃO. E O SEU AMOR SEMPRE TRADUZ VIDA REAL E O CÉU PRA MIM!\n\nCRISTO JESUS É O MEU SENHOR SEMPRE PRESENTE NO MEU CORAÇÃO. BREVE VIRÁ DO CÉU ME BUSCAR. QUE ALEGRIA SERÁ! (2X)', 'ELE MORREU, MAS RESSURGIU; PLENA PAZ ENCHEU O MEU VIVER; JESUS É TUDO O QUE SEMPRE QUIS, JESUS ME TROUXE O SEU PERDÃO. QUERO FALAR DESTA SALVAÇÃO, POR ONDE QUER QUE EU POSSA ANDAR, JESUS É TUDO, ELE É A PAZ. QUE VOCÊ PODE ENCONTRAR  INSTRUMENTOS CRISTO, O SENHOR, É QUEM ME CONDUZ, LIVRA-ME DO MAL E DA TENTAÇÃO. E O SEU AMOR SEMPRE TRADUZ VIDA REAL E O CÉU PRA MIM! CRISTO JESUS É O MEU SENHOR SEMPRE PRESENTE NO MEU CORAÇÃO. BREVE VIRÁ DO CÉU ME BUSCAR. QUE ALEGRIA SERÁ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('91', '91 - TENHO ALGO PRA VOCÊ', 'TENHO ALGO PRA VOCÊ NESTA CANÇÃO: TEM ALGUÉM QUE MORA EM MEU CORAÇÃO. ESTE ALGUÉM DESEJA DAR A VOCÊ O PERDÃO.\n\nESTE ALGUÉM É MUITO IMPORTANTE! ESTÁ BEM PERTO DE VOCÊ A TODO INSTANTE. ELE SÓ POR NÓS MORREU NUMA CRUZ. ESTE ALGUÉM, LHE DIGO, É: JESUS', 'TENHO ALGO PRA VOCÊ NESTA CANÇÃO: TEM ALGUÉM QUE MORA EM MEU CORAÇÃO. ESTE ALGUÉM DESEJA DAR A VOCÊ O PERDÃO. ESTE ALGUÉM É MUITO IMPORTANTE! ESTÁ BEM PERTO DE VOCÊ A TODO INSTANTE. ELE SÓ POR NÓS MORREU NUMA CRUZ. ESTE ALGUÉM, LHE DIGO, É: JESUS', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('92', '92 - JESUS CURA E SALVA', 'JESUS CURA E SALVA, JESUS É O MEU SALVADOR; JESUS É MEU AMIGO, E ELE É O MEU REDENTOR.\n\nQUANDO ESTOU DOENTE, CURA ELE DÁ, QUANDO ESTOU TRISTE, A PAZ VEM REINAR!\n\nJESUS É FORÇA E ESPERANÇA! JESUS É O PRÍNCIPE DA PAZ!', 'JESUS CURA E SALVA, JESUS É O MEU SALVADOR; JESUS É MEU AMIGO, E ELE É O MEU REDENTOR. QUANDO ESTOU DOENTE, CURA ELE DÁ, QUANDO ESTOU TRISTE, A PAZ VEM REINAR! JESUS É FORÇA E ESPERANÇA! JESUS É O PRÍNCIPE DA PAZ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('93', '93 - JESUS TE AMA', 'JESUS TE AMA, ELE QUER TE SALVAR, DEIXA A LUZ ENTRAR EM TEU CORAÇÃO.\n\nELE QUER TRANSFORMAR TEU CHORO EM ALEGRIA, ELE QUER TE DAR PAZ, GOZO E ESPERANÇA.\n\nJESUS TE AMA (JESUS TE AMA), ELE QUER TE SALVAR (ELE QUER TE SALVAR), DEIXA A LUZ (DEIXA A LUZ) ENTRAR EM TEU CORAÇÃO.\n\nELE QUER TRANSFORMAR TEU CHORO EM ALEGRIA, ELE QUER TE DAR PAZ, GOZO E ESPERANÇA.\n\nSE TU QUISERES (SE TU QUISERES) ACEITAR A CRISTO (ACEITAR A CRISTO), ABRE HOJE MESMO (ABRE HOJE MESMO) O SEU CORAÇÃO!\n\nJESUS TE CHAMA! JESUS TE AMA! JESUS TE AMA!', 'JESUS TE AMA, ELE QUER TE SALVAR, DEIXA A LUZ ENTRAR EM TEU CORAÇÃO. ELE QUER TRANSFORMAR TEU CHORO EM ALEGRIA, ELE QUER TE DAR PAZ, GOZO E ESPERANÇA. JESUS TE AMA (JESUS TE AMA), ELE QUER TE SALVAR (ELE QUER TE SALVAR), DEIXA A LUZ (DEIXA A LUZ) ENTRAR EM TEU CORAÇÃO. SE TU QUISERES (SE TU QUISERES) ACEITAR A CRISTO (ACEITAR A CRISTO), ABRE HOJE MESMO (ABRE HOJE MESMO) O SEU CORAÇÃO! JESUS TE CHAMA! JESUS TE AMA! JESUS TE AMA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('94', '94 - AQUELE QUE TEM SEDE', 'AQUELE QUE TEM SEDE VÁ À FONTE E BEBA, SUAS ÁGUAS DESSEDENTAM E TRAZEM VIDA.\n\nRIOS DE ÁGUAS CORRERÃO NO SEU INTERIOR, E A ALEGRIA QUE SALTA PARA A VIDA ETERNA.\n\nJESUS É ESSA FONTE DE VIDA, QUE SACIA A SEDE DA ALMA. VENHA A ELE E BEBA, E UMA VIDA ETERNA LHE DARÁ.\n\nAQUELE QUE TEM SEDE VÁ À FONTE E BEBA, SUAS ÁGUAS DESSEDENTAM E TRAZEM VIDA.\n\nRIOS DE ÁGUAS CORRERÃO NO SEU INTERIOR, E A ALEGRIA QUE SALTA PARA A VIDA ETERNA.', 'AQUELE QUE TEM SEDE VÁ À FONTE E BEBA, SUAS ÁGUAS DESSEDENTAM E TRAZEM VIDA. RIOS DE ÁGUAS CORRERÃO NO SEU INTERIOR, E A ALEGRIA QUE SALTA PARA A VIDA ETERNA. JESUS É ESSA FONTE DE VIDA, QUE SACIA A SEDE DA ALMA. VENHA A ELE E BEBA, E UMA VIDA ETERNA LHE DARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('95', '95 - DO PÃO DO CÉU', 'DO PÃO DO CÉU ME NUTRIREI, DA FONTE ETERNA BEBEREI; “E QUEM BEBER”, NOS DIZ JESUS, “JAMAIS DE SEDE SOFRERÁ.”\n\nNÃO MAIS SEDE? QUE DIZEIS? NÃO MAIS SEDE ASSIM SERÁS!\nBIS\n\n“E QUEM BEBER,” NOS DIZ JESUS “JAMAIS DE SEDE SOFRERÁ.” SEDE NÃO TERÁ!', 'DO PÃO DO CÉU ME NUTRIREI, DA FONTE ETERNA BEBEREI; E QUEM BEBER, NOS DIZ JESUS, JAMAIS DE SEDE SOFRERÁ. NÃO MAIS SEDE? QUE DIZEIS? NÃO MAIS SEDE ASSIM SERÁS! E QUEM BEBER, NOS DIZ JESUS JAMAIS DE SEDE SOFRERÁ. SEDE NÃO TERÁ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('96', '96 - SONHEI COM JESUS ESTA NOITE', 'SONHEI COM JESUS ESTA NOITE UM CONSELHO ELE ME DEU: “NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU” “NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU”\n\nEU IMPLOREI A JESUS: “SENHOR TEM DE MIM COMPAIXÃO EU VIVO VAGANDO NO MUNDO SEM PAZ SEM CONSOLAÇÃO” “EU VIVO VAGANDO NO MUNDO SEM PAZ SEM CONSOLAÇÃO”\n\nSONHEI COM JESUS ESTA NOITE UM CONSELHO ELE ME DEU: “NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU” “NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU”\n\nUMA VOZ ME FALOU BAIXINHO NO FUNDO DO CORAÇÃO “OH PECADOR VEM DE PRESSA CRISTO TE DÁ SALVAÇÃO” “OH PECADOR VEM DE PRESSA CRISTO TE DÁ SALVAÇÃO”\n\nSONHEI COM JESUS ESTA NOITE UM CONSELHO ELE ME DEU: “NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU” “NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU”', 'SONHEI COM JESUS ESTA NOITE UM CONSELHO ELE ME DEU: NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU NÃO SIGAS POR OUTRO CAMINHO QUE A PORTA DO CÉU SOU EU EU IMPLOREI A JESUS: SENHOR TEM DE MIM COMPAIXÃO EU VIVO VAGANDO NO MUNDO SEM PAZ SEM CONSOLAÇÃO EU VIVO VAGANDO NO MUNDO SEM PAZ SEM CONSOLAÇÃO UMA VOZ ME FALOU BAIXINHO NO FUNDO DO CORAÇÃO OH PECADOR VEM DE PRESSA CRISTO TE DÁ SALVAÇÃO OH PECADOR VEM DE PRESSA CRISTO TE DÁ SALVAÇÃO', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
