INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('102', '102 - DAS MARAVILHAS QUE A BÍBLIA CONTÉM', 'DAS MARAVILHAS QUE A BÍBLIA CONTÉM, EIS A MAIS LINDA: JESUS ME QUER BEM;\n\nCHEGO-ME A ELE, NINGUÉM ME DETÉM, QUANDO ME LEMBRO DE QUE ME QUER BEM.\n\nOH! QUE PRAZER! JESUS ME QUER BEM, SIM, ME QUER BEM, A TI TAMBÉM; OH! QUE PRAZER! JESUS ME QUER BEM, SIM, ME QUER BEM, JESUS.', 'DAS MARAVILHAS QUE A BÍBLIA CONTÉM, EIS A MAIS LINDA: JESUS ME QUER BEM; CHEGO-ME A ELE, NINGUÉM ME DETÉM, QUANDO ME LEMBRO DE QUE ME QUER BEM. OH! QUE PRAZER! JESUS ME QUER BEM, SIM, ME QUER BEM, A TI TAMBÉM; OH! QUE PRAZER! JESUS ME QUER BEM, SIM, ME QUER BEM, JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('103', '103 - VINDE, MENINOS', 'VINDE, MENINOS, VINDE A JESUS! ELE GANHOU-VOS BÊNÇÃOS NA CRUZ, OS PEQUENINOS ELE CONDUZ, Ó VINDE AO SALVADOR!\n\nCORO QUE ALEGRIA! SEM PECADO OU MAL REUNIR-NOS TODOS, AFINAL, NA SANTA PÁTRIA CELESTIAL COM NOSSO SALVADOR.\n\nJÁ SEM DEMORA, HOJE CONVÉM IR CAMINHANDO À GLÓRIA ALÉM; JESUS VOS CHAMA, QUER VOSSO BEM, Ó VINDE AO SALVADOR!\n\nCORO QUE ALEGRIA! SEM PECADO OU MAL REUNIR-NOS TODOS, AFINAL, NA SANTA PÁTRIA CELESTIAL COM NOSSO SALVADOR.\n\nCRISTO VOS AMA, BEM ELE O DIZ, QUER RECEBER-VOS NO BOM PAÍS; QUER CONCEDER-VOS VIDA FELIZ, Ó VINDE AO SALVADOR!\n\nCORO QUE ALEGRIA! SEM PECADO OU MAL REUNIR-NOS TODOS, AFINAL, NA SANTA PÁTRIA CELESTIAL COM NOSSO SALVADOR.', 'VINDE, MENINOS, VINDE A JESUS! ELE GANHOU-VOS BÊNÇÃOS NA CRUZ, OS PEQUENINOS ELE CONDUZ, Ó VINDE AO SALVADOR! CORO QUE ALEGRIA! SEM PECADO OU MAL REUNIR-NOS TODOS, AFINAL, NA SANTA PÁTRIA CELESTIAL COM NOSSO SALVADOR. JÁ SEM DEMORA, HOJE CONVÉM IR CAMINHANDO À GLÓRIA ALÉM; JESUS VOS CHAMA, QUER VOSSO BEM, Ó VINDE AO SALVADOR! CRISTO VOS AMA, BEM ELE O DIZ, QUER RECEBER-VOS NO BOM PAÍS; QUER CONCEDER-VOS VIDA FELIZ, Ó VINDE AO SALVADOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
