INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('108', '108 - HOJE É UM GRANDE DIA', 'HOJE É UM GRANDE DIA, É DIA DE LOUVAR; LOUVEMOS A JESUS QUE ESTÁ NESTE LUGAR; HOJE É DIA DE VITÓRIA E VITÓRIA ELE NOS DÁ; RECEBA JESUS, ELE QUER TE ABENÇOAR.\n\nCORO VENHAM TODOS JÁ, A JESUS VAMOS LOUVAR! ACEITEM O SALVADOR QUE VIDA ETERNA QUER LHES DAR!\n\nHOJE AQUI ESTOU PARA TE LOUVAR POR TANTAS MARAVILHAS, POR SEMPRE ME AMAR. TUA OBRA EM MINHA VIDA, VOU ANUNCIAR, OBRA DE ALEGRIA E ALEGRIA QUE É SEM PAR!\n\nCORO VENHAM TODOS JÁ, A JESUS VAMOS LOUVAR! ACEITEM O SALVADOR QUE VIDA ETERNA QUER LHES DAR!', 'HOJE É UM GRANDE DIA, É DIA DE LOUVAR; LOUVEMOS A JESUS QUE ESTÁ NESTE LUGAR; HOJE É DIA DE VITÓRIA E VITÓRIA ELE NOS DÁ; RECEBA JESUS, ELE QUER TE ABENÇOAR. CORO VENHAM TODOS JÁ, A JESUS VAMOS LOUVAR! ACEITEM O SALVADOR QUE VIDA ETERNA QUER LHES DAR! HOJE AQUI ESTOU PARA TE LOUVAR POR TANTAS MARAVILHAS, POR SEMPRE ME AMAR. TUA OBRA EM MINHA VIDA, VOU ANUNCIAR, OBRA DE ALEGRIA E ALEGRIA QUE É SEM PAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('109', '109 - CANTA ALEGREMENTE, Ó FILHA DE SIÃO', 'CANTA ALEGREMENTE, Ó FILHA DE SIÃO; REJUBILA, Ó ISRAEL: REGOZIJA-TE E EXULTA DE TODO O CORAÇÃO, Ó FILHA DE JERUSALÉM.\n\nO SENHOR AFASTOU OS TEUS JUÍZOS, EXTERMINOU O TEU INIMIGO; O SENHOR, O REI DE ISRAEL, ESTÁ NO MEIO DE TI; TU NÃO VERÁS MAIS MAL ALGUM.\n\nNAQUELE DIA SE DIRÁ A JERUSALÉM: NÃO TEMAS, Ó SIÃO, NÃO ENFRAQUEÇAM AS TUAS MÃOS.\n\nO SENHOR TEU DEUS ESTÁ NO MEIO DE TI, PODEROSO PARA TE SALVAR; SE DELEITARÁ EM TI COM ALEGRIA;\n\nCALAR-SE-Á POR SEU AMOR, REGOZIJAR-SE-Á EM TI COM JÚBILO, Ó FILHA DE JERUSALÉM (2X)', 'CANTA ALEGREMENTE, Ó FILHA DE SIÃO; REJUBILA, Ó ISRAEL: REGOZIJA-TE E EXULTA DE TODO O CORAÇÃO, Ó FILHA DE JERUSALÉM. O SENHOR AFASTOU OS TEUS JUÍZOS, EXTERMINOU O TEU INIMIGO; O SENHOR, O REI DE ISRAEL, ESTÁ NO MEIO DE TI; TU NÃO VERÁS MAIS MAL ALGUM. NAQUELE DIA SE DIRÁ A JERUSALÉM: NÃO TEMAS, Ó SIÃO, NÃO ENFRAQUEÇAM AS TUAS MÃOS. O SENHOR TEU DEUS ESTÁ NO MEIO DE TI, PODEROSO PARA TE SALVAR; SE DELEITARÁ EM TI COM ALEGRIA; CALAR-SE-Á POR SEU AMOR, REGOZIJAR-SE-Á EM TI COM JÚBILO, Ó FILHA DE JERUSALÉM', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
