INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('114', '114 - O SENHOR É QUEM CUIDA DE MIM', 'O SENHOR É QUEM CUIDA DE MIM, O SENHOR É QUEM CUIDA DE MIM,\n\nNADA HEI DE TEMER, COM JESUS VOU VENCER, POIS EU SEI QUE ELE CUIDA DE MIM.\n\nA JESUS SERVIREI COM AMOR, POIS EU SEI QUE ELE É MEU SALVADOR. A MEU LADO ESTÁ, NUNCA VAI ME DEIXAR, POIS EU SEI QUE ELE CUIDA DE MIM.', 'O SENHOR É QUEM CUIDA DE MIM, O SENHOR É QUEM CUIDA DE MIM, NADA HEI DE TEMER, COM JESUS VOU VENCER, POIS EU SEI QUE ELE CUIDA DE MIM. A JESUS SERVIREI COM AMOR, POIS EU SEI QUE ELE É MEU SALVADOR. A MEU LADO ESTÁ, NUNCA VAI ME DEIXAR, POIS EU SEI QUE ELE CUIDA DE MIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('115', '115 - JESUS, O BOM PASTOR', 'JESUS, O BOM PASTOR, CUIDA DE MIM COM MUITO AMOR. A PASTOS VERDEJANTES SEMPRE LEVA-ME O SENHOR\n\nE A ÁGUAS TÃO TRANQUILAS MANSAMENTE ME CONDUZ. NAS VEREDAS DA JUSTIÇA, VIVEREI NA LUZ.\n\nSE MESMO O PERIGO TIVER QUE ENFRENTAR, SEI QUE MEU JESUS COMIGO SEMPRE ESTARÁ. MEDO NÃO TEREI, POIS SINTO SUA MÃO EM MIM. COM O CAJADO DO SENHOR CONSOLO TENHO SIM!\n\nMESA ABUNDANTE HEI DE TER; ÓLEO DO ESPÍRITO EM MEU VIVER; CÁLICE TRANSBORDANTE, VIDA ABUNDANTE, COM A MISERICÓRDIA DO SENHOR.\n\nE POR LONGOS DIAS VIVEREI EM SUA CASA ETERNA HABITAREI. EM SUA CASA ETERNA HABITAREI.', 'JESUS, O BOM PASTOR, CUIDA DE MIM COM MUITO AMOR. A PASTOS VERDEJANTES SEMPRE LEVA-ME O SENHOR E A ÁGUAS TÃO TRANQUILAS MANSAMENTE ME CONDUZ. NAS VEREDAS DA JUSTIÇA, VIVEREI NA LUZ. SE MESMO O PERIGO TIVER QUE ENFRENTAR, SEI QUE MEU JESUS COMIGO SEMPRE ESTARÁ. MEDO NÃO TEREI, POIS SINTO SUA MÃO EM MIM. COM O CAJADO DO SENHOR CONSOLO TENHO SIM! MESA ABUNDANTE HEI DE TER; ÓLEO DO ESPÍRITO EM MEU VIVER; CÁLICE TRANSBORDANTE, VIDA ABUNDANTE, COM A MISERICÓRDIA DO SENHOR. E POR LONGOS DIAS VIVEREI EM SUA CASA ETERNA HABITAREI. EM SUA CASA ETERNA HABITAREI.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('116', '116 - SOBRE AS ONDAS', 'SOBRE AS ONDAS PODEREI ANDAR. JESUS ESTÁ COMIGO, NENHUM MAL ME ATINGIRÁ. SOPRA O VENTO, DEIXA SOPRAR JESUS ACALMA AS ONDAS E A PAZ VOLTA A REINAR.\n\nAS ONDAS VÃO SUBINDO, FORMANDO UM MONTÃO, ESTE MUNDO MAU QUER NAUFRAGAR MEU CORAÇÃO. MAS NÃO TEMO, OLHO PARA JESUS. AO SOM DA SUA VOZ ATÉ AS TREVAS VIRAM LUZ.', 'SOBRE AS ONDAS PODEREI ANDAR. JESUS ESTÁ COMIGO, NENHUM MAL ME ATINGIRÁ. SOPRA O VENTO, DEIXA SOPRAR JESUS ACALMA AS ONDAS E A PAZ VOLTA A REINAR. AS ONDAS VÃO SUBINDO, FORMANDO UM MONTÃO, ESTE MUNDO MAU QUER NAUFRAGAR MEU CORAÇÃO. MAS NÃO TEMO, OLHO PARA JESUS. AO SOM DA SUA VOZ ATÉ AS TREVAS VIRAM LUZ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('117', '117 - DEITADA NA RELVA', 'DEITADA NA RELVA UMA FELIZ OVELHA JUNTO AO PASTOR ESTÁ A DESCANSAR,\n\nNAS ÁGUAS TRANQUILAS SACIA SUA SEDE, DA RELVA BEM VERDE PODE SE ALIMENTAR.\n\nCORO OVELHA, JAMAIS DEIXE ESTE APRISCO. TENS O BOM PASTOR PARA DE TI CUIDAR. NO MUNDO LÁ FORA HÁ TRISTEZA E GUERRA, JESUS É A ALEGRIA, VERDADE E PAZ.\n\nE QUANDO AS TREVAS CAEM SOBRE O MUNDO O TENTADOR VEM DISPOSTO A TRAGAR. JESUS EM SEUS BRAÇOS, OVELHA, TE ACOLHE, PARA QUE TU POSSAS A VIDA HERDAR.\n\nCORO OVELHA, JAMAIS DEIXE ESTE APRISCO. TENS O BOM PASTOR PARA DE TI CUIDAR. NO MUNDO LÁ FORA HÁ TRISTEZA E GUERRA, JESUS É A ALEGRIA, VERDADE E PAZ.', 'DEITADA NA RELVA UMA FELIZ OVELHA JUNTO AO PASTOR ESTÁ A DESCANSAR, NAS ÁGUAS TRANQUILAS SACIA SUA SEDE, DA RELVA BEM VERDE PODE SE ALIMENTAR. CORO OVELHA, JAMAIS DEIXE ESTE APRISCO. TENS O BOM PASTOR PARA DE TI CUIDAR. NO MUNDO LÁ FORA HÁ TRISTEZA E GUERRA, JESUS É A ALEGRIA, VERDADE E PAZ. E QUANDO AS TREVAS CAEM SOBRE O MUNDO O TENTADOR VEM DISPOSTO A TRAGAR. JESUS EM SEUS BRAÇOS, OVELHA, TE ACOLHE, PARA QUE TU POSSAS A VIDA HERDAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('118', '118 - ÂNCORA SEGURA', 'UMA ÂNCORA SEGURA TENHO NO SENHOR, QUEM NELE CONFIOU JAMAIS PERDIDO SE ACHOU.\n\nEU CONFIO NO SENHOR, SEGURO NELE ESTOU, SEI DE FATOS GRANDIOSOS, RESPOSTAS DO SENHOR:\n\nELIAS PEDIU A DEUS, FOGO DO CÉU DESCEU, CONSUMINDO O HOLOCAUSTO, O POVO SE ALEGROU: SÓ O SENHOR É DEUS! SÓ O SENHOR É DEUS!\n\nPEDRO SUBIU AO TEMPLO, VIU UM COXO A ESMOLAR. MELHOR QUE PRATA E OURO É O NOME DE JESUS. GRAÇA SE DERRAMOU, O COXO SE LEVANTOU. O POVO SE ALEGROU: SÓ O SENHOR É DEUS!\n\nPAULO VIU A TORMENTA DESTRUIR A NAU SEM RUMO. TODOS DESANIMADOS, PAULO ANUNCIOU: O ANJO DE DEUS FALOU: NINGUÉM SE PERDERÁ. TODOS CHEGARAM VIVOS, SÓ O SENHOR É DEUS!', 'UMA ÂNCORA SEGURA TENHO NO SENHOR, QUEM NELE CONFIOU JAMAIS PERDIDO SE ACHOU. EU CONFIO NO SENHOR, SEGURO NELE ESTOU, SEI DE FATOS GRANDIOSOS, RESPOSTAS DO SENHOR: ELIAS PEDIU A DEUS, FOGO DO CÉU DESCEU, CONSUMINDO O HOLOCAUSTO, O POVO SE ALEGROU: SÓ O SENHOR É DEUS! SÓ O SENHOR É DEUS! PEDRO SUBIU AO TEMPLO, VIU UM COXO A ESMOLAR. MELHOR QUE PRATA E OURO É O NOME DE JESUS. GRAÇA SE DERRAMOU, O COXO SE LEVANTOU. O POVO SE ALEGROU: SÓ O SENHOR É DEUS! PAULO VIU A TORMENTA DESTRUIR A NAU SEM RUMO. TODOS DESANIMADOS, PAULO ANUNCIOU: O ANJO DE DEUS FALOU: NINGUÉM SE PERDERÁ. TODOS CHEGARAM VIVOS, SÓ O SENHOR É DEUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('119', '119 - JESUS, O MELHOR AMIGO', 'JESUS, O MELHOR AMIGO, QUE SEMPRE ESTÁ PRESENTE. NELE HÁ PODER PARA MUDAR O CRENTE.\n\nCORO (2X) MESMO EM LUTAS E AFLIÇÕES ELE RESPONDE ÀS NOSSAS ORAÇÕES.\n\nJESUS TEM PODER. MESMO EM GUERRAS E BATALHAS VOCÊ VAI VENCER, PORQUE JESUS NÃO FALHA.\n\nSE VOCÊ QUISER UMA BÊNÇÃO, BASTA SOMENTE CRER, É SÓ PEDIR A ELE, QUE VOCÊ VAI RECEBER.\n\nCORO (2X) MESMO EM LUTAS E AFLIÇÕES ELE RESPONDE ÀS NOSSAS ORAÇÕES.', 'JESUS, O MELHOR AMIGO, QUE SEMPRE ESTÁ PRESENTE. NELE HÁ PODER PARA MUDAR O CRENTE. MESMO EM LUTAS E AFLIÇÕES ELE RESPONDE ÀS NOSSAS ORAÇÕES. JESUS TEM PODER. MESMO EM GUERRAS E BATALHAS VOCÊ VAI VENCER, PORQUE JESUS NÃO FALHA. SE VOCÊ QUISER UMA BÊNÇÃO, BASTA SOMENTE CRER, É SÓ PEDIR A ELE, QUE VOCÊ VAI RECEBER.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('120', '120 - PODERÁ CANTAR', 'CORO PODERÁ CANTAR QUEM NA ROCHA ESTIVER, PODERÁ CANTAR, POIS COM JESUS FIRMADO ESTARÁ.\n\nESPEREI PELO SENHOR COM PACIÊNCIA E ME FIRMOU OS PASSOS. DE ESCORREGAR LIVROU-ME, DEU-ME UM NOVO CANTO.\n\nCORO PODERÁ CANTAR QUEM NA ROCHA ESTIVER, PODERÁ CANTAR, POIS COM JESUS FIRMADO ESTARÁ.\n\nO CORAÇÃO FIRME EM CRISTO POR VENDAVAIS NÃO VACILARÁ. NÃO EM AREIA, MAS NA ROCHA, OUVI SUA VOZ, QUE SEGURANÇA!\n\nCORO PODERÁ CANTAR QUEM NA ROCHA ESTIVER, PODERÁ CANTAR, POIS COM JESUS FIRMADO ESTARÁ.', 'CORO PODERÁ CANTAR QUEM NA ROCHA ESTIVER, PODERÁ CANTAR, POIS COM JESUS FIRMADO ESTARÁ. ESPEREI PELO SENHOR COM PACIÊNCIA E ME FIRMOU OS PASSOS. DE ESCORREGAR LIVROU-ME, DEU-ME UM NOVO CANTO. O CORAÇÃO FIRME EM CRISTO POR VENDAVAIS NÃO VACILARÁ. NÃO EM AREIA, MAS NA ROCHA, OUVI SUA VOZ, QUE SEGURANÇA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('121', '121 - MEU BARQUINHO', 'MEU BARQUINHO NAVEGA NO MAR, MUI FELIZ VIVO SEMPRE A CANTAR,\n\nGRANDES ONDAS VÊM PARA O NAUFRAGAR, NÃO TENHO MEDO, VOU A ÂNCORA LANÇAR.\n\nCORO VOU A JESUS, VOU A ÂNCORA LANÇAR. VOU A JESUS, POR SEU SANGUE VOU CLAMAR E FELIZ VOU AO PORTO CHEGAR. ALELUIA! ALELUIA É O MEU CANTAR!\n\nMEU JESUS É AMIGO FIEL, POR SUA MÃO ME CONDUZ ATÉ AO CÉU E SE UM DIA VIER A AFLIÇÃO, NÃO TENHO MEDO, POIS É ELE O CAPITÃO.\n\nCORO VOU A JESUS, VOU A ÂNCORA LANÇAR. VOU A JESUS, POR SEU SANGUE VOU CLAMAR E FELIZ VOU AO PORTO CHEGAR. ALELUIA! ALELUIA É O MEU CANTAR!', 'MEU BARQUINHO NAVEGA NO MAR, MUI FELIZ VIVO SEMPRE A CANTAR, GRANDES ONDAS VÊM PARA O NAUFRAGAR, NÃO TENHO MEDO, VOU A ÂNCORA LANÇAR. CORO VOU A JESUS, VOU A ÂNCORA LANÇAR. VOU A JESUS, POR SEU SANGUE VOU CLAMAR E FELIZ VOU AO PORTO CHEGAR. ALELUIA! ALELUIA É O MEU CANTAR! MEU JESUS É AMIGO FIEL, POR SUA MÃO ME CONDUZ ATÉ AO CÉU E SE UM DIA VIER A AFLIÇÃO, NÃO TENHO MEDO, POIS É ELE O CAPITÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
