INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('124', '124 - O CÉU E A TERRA PASSARÃO', 'O CÉU E A TERRA PASSARÃO MAS AS MINHAS PALAVRAS NÃO HÃO DE PASSAR.\n\nEU SOU DEUS, SOU FIEL, NÃO TEMAIS, NEM VOS ASSUSTEIS! PROCLAMAI A VITÓRIA, RENOVAI A VOSSA FÉ, E O VOSSO ÂNIMO PORQUE A TERRA PROMETIDA VOS DAREI. (BIS)\n\n125 - COM JESUS VENCEDOR SEREI\n\nCOM JESUS VENCEDOR SEREI! COM JESUS VENCEDOR SEREI! VENCEDOR, VENCEDOR, VENCEDOR SEREI! COM JESUS VENCEDOR SEREI!\n\nNAS BATALHAS ELE ME ACOMPANHA. NAS BATALHAS ELE ME ACOMPANHA. MEU JESUS, MEU JESUS, SEMPRE ME ACOMPANHA. COM JESUS VENCEDOR SEREI! COM JESUS VENCEDOR SEREI!\n\nELE É O MEU SALVADOR. ELE É O MEU SALVADOR. SALVADOR, SALVADOR É O MEU JESUS! COM JESUS VENCEDOR SEREI!', 'O CÉU E A TERRA PASSARÃO MAS AS MINHAS PALAVRAS NÃO HÃO DE PASSAR. EU SOU DEUS, SOU FIEL, NÃO TEMAIS, NEM VOS ASSUSTEIS! PROCLAMAI A VITÓRIA, RENOVAI A VOSSA FÉ, E O VOSSO ÂNIMO PORQUE A TERRA PROMETIDA VOS DAREI. 125 - COM JESUS VENCEDOR SEREI COM JESUS VENCEDOR SEREI! COM JESUS VENCEDOR SEREI! VENCEDOR, VENCEDOR, VENCEDOR SEREI! COM JESUS VENCEDOR SEREI! NAS BATALHAS ELE ME ACOMPANHA. NAS BATALHAS ELE ME ACOMPANHA. MEU JESUS, MEU JESUS, SEMPRE ME ACOMPANHA. COM JESUS VENCEDOR SEREI! COM JESUS VENCEDOR SEREI! ELE É O MEU SALVADOR. ELE É O MEU SALVADOR. SALVADOR, SALVADOR É O MEU JESUS! COM JESUS VENCEDOR SEREI!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
