INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('143', '143 - TENHO UMA CANDEIA (CANDEIA ACESA)', 'CORO TENHO UMA CANDEIA, DE ÓLEO ESTÁ CHEIA, ELA ANUNCIA QUE JESUS VOLTARÁ;\n\nLUZ BRILHANTE, REFULGENTE, É O GRITO DA IGREJA: JESUS VAI VOLTAR!\n\nACENDE TUA CANDEIA, NÃO A DEIXES APAGAR, ILUMINANDO O TEU ROSTO JESUS TE VERÁ.\n\nCORO TENHO UMA CANDEIA, DE ÓLEO ESTÁ CHEIA, ELA ANUNCIA QUE JESUS VOLTARÁ; LUZ BRILHANTE, REFULGENTE, É O GRITO DA IGREJA: JESUS VAI VOLTAR!\n\nCANDEIA VAZIA EU NÃO QUERO, NÃO, CANDEIA CHEIA, EU QUERO, SIM, CANDEIA COM FOGO PRONTA PARA SUBIR.\n\nCANDEIA VAZIA EU NÃO QUERO, NÃO, CANDEIA CHEIA, EU QUERO, SIM, CANDEIA COM FOGO PRONTA PARA SUBIR.\n\nCORO TENHO UMA CANDEIA, DE ÓLEO ESTÁ CHEIA, ELA ANUNCIA QUE JESUS VOLTARÁ; LUZ BRILHANTE, REFULGENTE, É O GRITO DA IGREJA: JESUS VAI VOLTAR!', 'CORO TENHO UMA CANDEIA, DE ÓLEO ESTÁ CHEIA, ELA ANUNCIA QUE JESUS VOLTARÁ; LUZ BRILHANTE, REFULGENTE, É O GRITO DA IGREJA: JESUS VAI VOLTAR! ACENDE TUA CANDEIA, NÃO A DEIXES APAGAR, ILUMINANDO O TEU ROSTO JESUS TE VERÁ. CORO TENHO UMA CANDEIA, DE ÓLEO ESTÁ CHEIA, ELA ANUNCIA QUE JESUS VOLTARÁ; LUZ BRILHANTE, REFULGENTE, É O GRITO DA IGREJA: JESUS VAI VOLTAR! CANDEIA VAZIA EU NÃO QUERO, NÃO, CANDEIA CHEIA, EU QUERO, SIM, CANDEIA COM FOGO PRONTA PARA SUBIR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('144', '144 - NOVA ALIANÇA', 'NOVA ALIANÇA FEZ O MEU SENHOR COM O SEU POVO NUM ATO DE AMOR; “EIS QUE FAÇO NOVAS TODAS AS COISAS”, ASSIM FALOU O SENHOR.\n\nPREPARO O MEU CORAÇÃO PARA O ARREBATAMENTO, NÃO SERÁ FÁCIL, LUTAS VIRÃO, MAS NA BUSCA AO SENHOR ELE DARÁ A SOLUÇÃO;\n\nCOM O ESPÍRITO SANTO QUER BATIZAR E EXPERIÊNCIAS COM OS DONS QUER DAR,\n\nEU NÃO TEMEREI, COM CRISTO ANDAREI NA DIREÇÃO DO ESPÍRITO SANTO.\n\nQUANDO EU CAMINHAR COM MEU JESUS VAI RAIAR A LUZ E VIDAS SE ENTREGARÃO, RECEBENDO A SALVAÇÃO;\n\nE JUNTOS NUM CORPO UNIDO VAMOS RUMO À TÃO ESPERADA SIÃO. TÃO ESPERADA SIÃO.', 'NOVA ALIANÇA FEZ O MEU SENHOR COM O SEU POVO NUM ATO DE AMOR; EIS QUE FAÇO NOVAS TODAS AS COISAS, ASSIM FALOU O SENHOR. PREPARO O MEU CORAÇÃO PARA O ARREBATAMENTO, NÃO SERÁ FÁCIL, LUTAS VIRÃO, MAS NA BUSCA AO SENHOR ELE DARÁ A SOLUÇÃO; COM O ESPÍRITO SANTO QUER BATIZAR E EXPERIÊNCIAS COM OS DONS QUER DAR, EU NÃO TEMEREI, COM CRISTO ANDAREI NA DIREÇÃO DO ESPÍRITO SANTO. QUANDO EU CAMINHAR COM MEU JESUS VAI RAIAR A LUZ E VIDAS SE ENTREGARÃO, RECEBENDO A SALVAÇÃO; E JUNTOS NUM CORPO UNIDO VAMOS RUMO À TÃO ESPERADA SIÃO. TÃO ESPERADA SIÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
