INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('146', '146 - A IGREJA QUE ORA É VITORIOSA', 'A IGREJA QUE ORA É VITORIOSA, SEMPRE ESTÁ NA DEPENDÊNCIA DO SENHOR;\n\nA IGREJA QUE ORA É VITORIOSA, MUITOS MILAGRES ACONTECEM. PORQUE É UM CORPO VIVO ONDE O CABEÇA É JESUS. PORQUE É UM CORPO VIVO ONDE O CABEÇA É JESUS.\n\nSE UMA BÊNÇÃO QUER DOBRA OS TEUS JOELHOS, CLAMA, ORA, BUSCA SUA FACE; ORANDO, CANTANDO, VERÁS O FRUTO DO ESPÍRITO SANTO.', 'A IGREJA QUE ORA É VITORIOSA, SEMPRE ESTÁ NA DEPENDÊNCIA DO SENHOR; A IGREJA QUE ORA É VITORIOSA, MUITOS MILAGRES ACONTECEM. PORQUE É UM CORPO VIVO ONDE O CABEÇA É JESUS. PORQUE É UM CORPO VIVO ONDE O CABEÇA É JESUS. SE UMA BÊNÇÃO QUER DOBRA OS TEUS JOELHOS, CLAMA, ORA, BUSCA SUA FACE; ORANDO, CANTANDO, VERÁS O FRUTO DO ESPÍRITO SANTO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('147', '147 - EM TUAS PISADAS', 'EM TUAS PISADAS COLOCO OS MEUS PÉS, ASSIM, BEM FIRMADO, NÃO VACILAREI;\n\nOS TEUS PRECEITOS, SENHOR, OS GUARDEI, AS TUAS PALAVRAS SÃO MEU ALIMENTO, VIVER NA TUA OBRA É O MEU SUSTENTO.\nBIS\n\nEM TUAS PISADAS COLOCO OS MEUS PÉS, ASSIM, BEM FIRMADO, NÃO VACILAREI;\n\nOS TEUS PRECEITOS, SENHOR, OS GUARDEI, AS TUAS PALAVRAS SÃO MEU ALIMENTO, VIVER NA TUA OBRA É O MEU SUSTENTO.\nBIS', 'EM TUAS PISADAS COLOCO OS MEUS PÉS, ASSIM, BEM FIRMADO, NÃO VACILAREI; OS TEUS PRECEITOS, SENHOR, OS GUARDEI, AS TUAS PALAVRAS SÃO MEU ALIMENTO, VIVER NA TUA OBRA É O MEU SUSTENTO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('148', '148 - ANDANDO EM ESPÍRITO', 'ANDANDO EM ESPÍRITO VENCEREI A TENTAÇÃO, AGRADAREI A DEUS EM PERFEITA COMUNHÃO,\n\n“POIS O FRUTO DO ESPÍRITO É: AMOR, GOZO, PAZ, LONGANIMIDADE, BENIGNIDADE, BONDADE, FÉ, MANSIDÃO, TEMPERANÇA”.\n\nVIVENDO EM ESPÍRITO, ANDANDO EM ESPÍRITO, ASSIM SOMOS SERVOS FIÉIS DE CRISTO.\nBIS', 'ANDANDO EM ESPÍRITO VENCEREI A TENTAÇÃO, AGRADAREI A DEUS EM PERFEITA COMUNHÃO, POIS O FRUTO DO ESPÍRITO É: AMOR, GOZO, PAZ, LONGANIMIDADE, BENIGNIDADE, BONDADE, FÉ, MANSIDÃO, TEMPERANÇA. VIVENDO EM ESPÍRITO, ANDANDO EM ESPÍRITO, ASSIM SOMOS SERVOS FIÉIS DE CRISTO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('149', '149 - NÓS SOMOS UMA VOZ', 'NÓS SOMOS UMA VOZ QUE CLAMA NO DESERTO, NESTE MUNDO DE ILUSÕES PROCLAMAMOS A MENSAGEM DE SALVAÇÃO.\n\nDEUS AMA O QUE SE APRESSA, ELE AMA O EVANGELISTA, AMA AQUELE QUE SE APRESSA PRA ANUNCIAR A SALVAÇÃO EM NOME DE JESUS.\n\nNÓS SOMOS UMA VOZ QUE CLAMA NO DESERTO, NESTE MUNDO DE ILUSÕES PROCLAMAMOS A MENSAGEM DE SALVAÇÃO.\n\nDEUS AMA O QUE SE APRESSA, ELE AMA O EVANGELISTA, AMA AQUELE QUE SE APRESSA PRA ANUNCIAR A SALVAÇÃO EM NOME DE JESUS.', 'NÓS SOMOS UMA VOZ QUE CLAMA NO DESERTO, NESTE MUNDO DE ILUSÕES PROCLAMAMOS A MENSAGEM DE SALVAÇÃO. DEUS AMA O QUE SE APRESSA, ELE AMA O EVANGELISTA, AMA AQUELE QUE SE APRESSA PRA ANUNCIAR A SALVAÇÃO EM NOME DE JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('150', '150 - CINCO SEIXOS', 'CINCO SEIXOS DO RIBEIRO DAVI COLOCOU EM SEU ALFORJE DE PASTOR QUANDO GOLIAS FOI A ENFRENTAR. SEM MEDO DAVI FOI LUTAR.\n\nDAVI OROU, CONFIOU EM DEUS, PARA ASSIM VENCER O FILISTEU.\n\nEM SEU CORAÇÃO HAVIA O LOUVOR, A PALAVRA, O JEJUM E A ORAÇÃO E NA MADRUGADA, SEMPRE A DEUS BUSCAVA DEIXANDO O SENHOR SUA VIDA DIRIGIR.\n\nE QUANDO GOLIAS FOI ENCONTRAR SÓ UMA PEDRA EM SUA FUNDA COLOCOU. E MIROU, GIROU, LANÇOU NA TESTA DO GIGANTE A PEDRA CRAVOU (TRUMMMM)\n\nO GIGANTE CAIU, DAVI FOI VENCEDOR. NO NOME DO SENHOR, NO NOME DO SENHOR.', 'CINCO SEIXOS DO RIBEIRO DAVI COLOCOU EM SEU ALFORJE DE PASTOR QUANDO GOLIAS FOI A ENFRENTAR. SEM MEDO DAVI FOI LUTAR. DAVI OROU, CONFIOU EM DEUS, PARA ASSIM VENCER O FILISTEU. EM SEU CORAÇÃO HAVIA O LOUVOR, A PALAVRA, O JEJUM E A ORAÇÃO E NA MADRUGADA, SEMPRE A DEUS BUSCAVA DEIXANDO O SENHOR SUA VIDA DIRIGIR. E QUANDO GOLIAS FOI ENCONTRAR SÓ UMA PEDRA EM SUA FUNDA COLOCOU. E MIROU, GIROU, LANÇOU NA TESTA DO GIGANTE A PEDRA CRAVOU (TRUMMMM) O GIGANTE CAIU, DAVI FOI VENCEDOR. NO NOME DO SENHOR, NO NOME DO SENHOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('151', '151 - NO PRINCÍPIO ERA O VERBO', '“NO PRINCÍPIO ERA O VERBO E O VERBO ESTAVA COM DEUS E O VERBO ERA DEUS”.\n\n“TODAS AS COISAS FORAM FEITAS POR ELE E SEM ELE NADA DO QUE FOI FEITO SE FEZ”.\n\nVEIO TOMAR AS NOSSAS DORES, CONHECER AS NOSSAS FRAQUEZAS E, DIANTE DO PAI, O NOSSO PECADO JUSTIFICAR.\n\nESTE VERBO É A PALAVRA VIVA, PROCLAMADA NA ETERNIDADE POR DEUS, QUANDO PROMETEU O SEU FILHO PRA MORRER EM NOSSO LUGAR.\n\nPOR ISSO TE LOUVAMOS, TE ADORAMOS, Ó DEUS! PELO SACRIFÍCIO DE JESUS TE RENDEMOS GLÓRIA,\n\nTODOS OS DIAS VALORIZAMOS ESSA IDENTIDADE: A MARCA DO SANGUE, O ESPÍRITO SANTO, QUE NOS DÁ PLENA COMUNHÃO COM DEUS.\n\nPOR ISSO TE LOUVAMOS, TE ADORAMOS, Ó DEUS! PELO SACRIFÍCIO DE JESUS TE RENDEMOS GLÓRIA,\n\nTODOS OS DIAS VALORIZAMOS ESSA IDENTIDADE: A MARCA DO SANGUE, O ESPÍRITO SANTO, QUE NOS DÁ PLENA COMUNHÃO COM DEUS.', 'NO PRINCÍPIO ERA O VERBO E O VERBO ESTAVA COM DEUS E O VERBO ERA DEUS. TODAS AS COISAS FORAM FEITAS POR ELE E SEM ELE NADA DO QUE FOI FEITO SE FEZ. VEIO TOMAR AS NOSSAS DORES, CONHECER AS NOSSAS FRAQUEZAS E, DIANTE DO PAI, O NOSSO PECADO JUSTIFICAR. ESTE VERBO É A PALAVRA VIVA, PROCLAMADA NA ETERNIDADE POR DEUS, QUANDO PROMETEU O SEU FILHO PRA MORRER EM NOSSO LUGAR. POR ISSO TE LOUVAMOS, TE ADORAMOS, Ó DEUS! PELO SACRIFÍCIO DE JESUS TE RENDEMOS GLÓRIA, TODOS OS DIAS VALORIZAMOS ESSA IDENTIDADE: A MARCA DO SANGUE, O ESPÍRITO SANTO, QUE NOS DÁ PLENA COMUNHÃO COM DEUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('152', '152 - NAS BATALHAS SOU UM VENCEDOR', 'NAS BATALHAS SOU UM VENCEDOR, USO AS ARMAS ESPIRITUAIS. REVESTIDO DE TODA A ARMADURA DE DEUS, SEREI FORTALECIDO NO SENHOR.\n\nTENHO CINGIDOS OS LOMBOS COM A VERDADE, E VESTIDA A COURAÇA DA JUSTIÇA, E CALÇADOS OS PÉS NA PREPARAÇÃO DO EVANGELHO DA PAZ.\n\nTENHO, SOBRETUDO O ESCUDO DA FÉ, E TAMBÉM O CAPACETE DA SALVAÇÃO, E A ESPADA DO ESPÍRITO, QUE É A PALAVRA DE DEUS.\n\nNAS BATALHAS SOU UM VENCEDOR, USO AS ARMAS ESPIRITUAIS. REVESTIDO DE TODA A ARMADURA DE DEUS, SEREI FORTALECIDO NO SENHOR, E NA FORÇA DO SEU PODER.', 'NAS BATALHAS SOU UM VENCEDOR, USO AS ARMAS ESPIRITUAIS. REVESTIDO DE TODA A ARMADURA DE DEUS, SEREI FORTALECIDO NO SENHOR. TENHO CINGIDOS OS LOMBOS COM A VERDADE, E VESTIDA A COURAÇA DA JUSTIÇA, E CALÇADOS OS PÉS NA PREPARAÇÃO DO EVANGELHO DA PAZ. TENHO, SOBRETUDO O ESCUDO DA FÉ, E TAMBÉM O CAPACETE DA SALVAÇÃO, E A ESPADA DO ESPÍRITO, QUE É A PALAVRA DE DEUS. NAS BATALHAS SOU UM VENCEDOR, USO AS ARMAS ESPIRITUAIS. REVESTIDO DE TODA A ARMADURA DE DEUS, SEREI FORTALECIDO NO SENHOR, E NA FORÇA DO SEU PODER.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('153', '153 - COMO NOÉ', 'COMO NOÉ, QUERO OBEDECER À DOCE VOZ DE DEUS, A ARCA VOU FAZER; COMO NOÉ NA ARCA VOU ENTRAR E SALVAÇÃO VOU ALCANÇAR.\n\nPELA FÉ NOÉ EM TUDO OBEDECEU, SEGUINDO O PROJETO, NADA ELE TEMEU. HERDEIRO COM JESUS DEUS ASSIM O FEZ, POIS A SALVAÇÃO É PRA TODO O QUE CRÊ.\n\nHERDEIRO COM JESUS DEUS ASSIM O FEZ, POIS A SALVAÇÃO É PRA TODO O QUE CRÊ.\n\nCOMO NOÉ, QUERO OBEDECER À DOCE VOZ DE DEUS, A ARCA VOU FAZER. COMO NOÉ NA ARCA VOU ENTRAR E SALVAÇÃO VOU ALCANÇAR.', 'COMO NOÉ, QUERO OBEDECER À DOCE VOZ DE DEUS, A ARCA VOU FAZER; COMO NOÉ NA ARCA VOU ENTRAR E SALVAÇÃO VOU ALCANÇAR. PELA FÉ NOÉ EM TUDO OBEDECEU, SEGUINDO O PROJETO, NADA ELE TEMEU. HERDEIRO COM JESUS DEUS ASSIM O FEZ, POIS A SALVAÇÃO É PRA TODO O QUE CRÊ. HERDEIRO COM JESUS DEUS ASSIM O FEZ, POIS A SALVAÇÃO É PRA TODO O QUE CRÊ. COMO NOÉ, QUERO OBEDECER À DOCE VOZ DE DEUS, A ARCA VOU FAZER. COMO NOÉ NA ARCA VOU ENTRAR E SALVAÇÃO VOU ALCANÇAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('154', '154 - COM O ESPÍRITO DE DEUS', 'CORO COM O ESPÍRITO DE DEUS EM MINHA VIDA, SOU FELIZ, SOU FELIZ! TENHO PAZ, CONSOLO, GRAÇA E ALEGRIA. SOU FELIZ, SIM, SOU FELIZ!\n\nCRISTO PROMETEU DEIXAR-NOS O CONSOLADOR PARA ESTAR CONOSCO SEMPRE ONDE QUER QUE FOR,\n\nO ESPÍRITO DE VERDADE, QUE ACONSELHARÁ MINHA VIDA, ENSINANDO COMO DEVO ANDAR.\n\nCORO COM O ESPÍRITO DE DEUS EM MINHA VIDA, SOU FELIZ, SOU FELIZ! TENHO PAZ, CONSOLO, GRAÇA E ALEGRIA. SOU FELIZ, SIM, SOU FELIZ!\n\nQUERO SER OBEDIENTE AO ESPÍRITO DE DEUS, SEGUIR SUA PALAVRA E OS ENSINOS SEUS, TETEMUNHAR QUE TENHO DEUS EM MEU VIVER, MINHA VIDA É TRANSFORMADA, VOU DIZER PORQUÊ:\n\nCORO COM O ESPÍRITO DE DEUS EM MINHA VIDA, SOU FELIZ, SOU FELIZ! TENHO PAZ, CONSOLO, GRAÇA E ALEGRIA. SOU FELIZ, SIM, SOU FELIZ!', 'CORO COM O ESPÍRITO DE DEUS EM MINHA VIDA, SOU FELIZ, SOU FELIZ! TENHO PAZ, CONSOLO, GRAÇA E ALEGRIA. SOU FELIZ, SIM, SOU FELIZ! CRISTO PROMETEU DEIXAR-NOS O CONSOLADOR PARA ESTAR CONOSCO SEMPRE ONDE QUER QUE FOR, O ESPÍRITO DE VERDADE, QUE ACONSELHARÁ MINHA VIDA, ENSINANDO COMO DEVO ANDAR. QUERO SER OBEDIENTE AO ESPÍRITO DE DEUS, SEGUIR SUA PALAVRA E OS ENSINOS SEUS, TETEMUNHAR QUE TENHO DEUS EM MEU VIVER, MINHA VIDA É TRANSFORMADA, VOU DIZER PORQUÊ:', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('155', '155 - QUERO ESTAR EM TUA PRESENÇA, Ó DEUS', 'QUERO ESTAR EM TUA PRESENÇA, Ó DEUS, EM TUA OBRA PROSSEGUIR ATÉ O FIM, SEM TEMER E SEMPRE CONFIAR QUE JESUS AO MEU LADO ESTÁ.\n\nCORO FAZE DE MIM UM VENCEDOR, NÃO IMPORTA QUAL SEJA A MINHA DOR.\n\nFAZE DE MIM UM ENVIADO TEU, PARA A TUA OBRA REALIZAR E DO TEU PODER TESTEMUNHAR E POR FIM JERUSALÉM ALCANÇAR.\n\nQUERO SER UM SERVO DO SENHOR E ANDAR SOB A REVELAÇÃO, PARTILHAR DA SUA COMUNHÃO, DANDO A ELE SEMPRE O MEU CORAÇÃO.\n\nCORO FAZE DE MIM UM VENCEDOR, NÃO IMPORTA QUAL SEJA A MINHA DOR.\n\nFAZE DE MIM UM ENVIADO TEU, PARA A TUA OBRA REALIZAR E DO TEU PODER TESTEMUNHAR E POR FIM JERUSALÉM ALCANÇAR.', 'QUERO ESTAR EM TUA PRESENÇA, Ó DEUS, EM TUA OBRA PROSSEGUIR ATÉ O FIM, SEM TEMER E SEMPRE CONFIAR QUE JESUS AO MEU LADO ESTÁ. CORO FAZE DE MIM UM VENCEDOR, NÃO IMPORTA QUAL SEJA A MINHA DOR. FAZE DE MIM UM ENVIADO TEU, PARA A TUA OBRA REALIZAR E DO TEU PODER TESTEMUNHAR E POR FIM JERUSALÉM ALCANÇAR. QUERO SER UM SERVO DO SENHOR E ANDAR SOB A REVELAÇÃO, PARTILHAR DA SUA COMUNHÃO, DANDO A ELE SEMPRE O MEU CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('156', '156 - SOMOS CIDADÃOS DO CÉU', 'SOMOS CIDADÃOS, CIDADÃOS DO CÉU. SOMOS CIDADÃOS, TEMOS SALVAÇÃO.\n\nIDENTIDADE TEMOS, VEIO LÁ DA CRUZ, COM O SACRIFÍCIO DO SENHOR JESUS. FALAMOS A VERDADE, SEGUIMOS A JUSTIÇA. SOMOS CIDADÃOS, CIDADÃOS DO CÉU.\n\nTEMOS AS MÃOS LIMPAS, UM PURO CORAÇÃO. SOMOS CIDADÃOS, CIDADÃOS DO CÉU. SOMOS CIDADÃOS, CIDADÃOS DO CÉU. SOMOS CIDADÃOS, TEMOS SALVAÇÃO.\nBIS', 'SOMOS CIDADÃOS, CIDADÃOS DO CÉU. SOMOS CIDADÃOS, TEMOS SALVAÇÃO. IDENTIDADE TEMOS, VEIO LÁ DA CRUZ, COM O SACRIFÍCIO DO SENHOR JESUS. FALAMOS A VERDADE, SEGUIMOS A JUSTIÇA. SOMOS CIDADÃOS, CIDADÃOS DO CÉU. TEMOS AS MÃOS LIMPAS, UM PURO CORAÇÃO. SOMOS CIDADÃOS, CIDADÃOS DO CÉU. SOMOS CIDADÃOS, CIDADÃOS DO CÉU. SOMOS CIDADÃOS, TEMOS SALVAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('157', '157 - ESTE MUNDO NÃO É MEU LUGAR', 'ESTE MUNDO NÃO É MEU LUGAR, PEREGRINO, ESTOU A CAMINHAR.\n\nMAS PARA O CÉU IREI MORAR COM MEU BOM REI, JESUS JÁ PREPAROU MEU NOVO LAR.\n\nTORNOU-ME CIDADÃO DO CÉU POR SEU AMOR, COM O SEU SANGUE ME COMPROU, ETERNAMENTE AO SENHOR DAREI LOUVOR, TODA GLÓRIA, HONRA, GRAÇAS E AMOR.\n\nSOU CIDADÃO DO CÉU, A PÁTRIA CELESTIAL, O GRANDE AMOR DE DEUS, SUA GRAÇA ETERNAL, ME CONCEDEU PERDÃO, ME DEU A SALVAÇÃO,\n\nPOR ISSO SOU CIDADÃO DO CÉU, ALELUIA! SOU CIDADÃO DO CÉU, ALELUIA! SOU CIDADÃO DO CÉU!', 'ESTE MUNDO NÃO É MEU LUGAR, PEREGRINO, ESTOU A CAMINHAR. MAS PARA O CÉU IREI MORAR COM MEU BOM REI, JESUS JÁ PREPAROU MEU NOVO LAR. TORNOU-ME CIDADÃO DO CÉU POR SEU AMOR, COM O SEU SANGUE ME COMPROU, ETERNAMENTE AO SENHOR DAREI LOUVOR, TODA GLÓRIA, HONRA, GRAÇAS E AMOR. SOU CIDADÃO DO CÉU, A PÁTRIA CELESTIAL, O GRANDE AMOR DE DEUS, SUA GRAÇA ETERNAL, ME CONCEDEU PERDÃO, ME DEU A SALVAÇÃO, POR ISSO SOU CIDADÃO DO CÉU, ALELUIA! SOU CIDADÃO DO CÉU, ALELUIA! SOU CIDADÃO DO CÉU!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('158', '158 - NO CAMINHO EU QUERO ESTAR', 'NO CAMINHO EU QUERO ESTAR, AO SENHOR VOU OBEDECER. AONDE QUER QUE DEUS MANDAR ANUNCIAR O SEU AMOR. ESTOU PRONTO, SENHOR, EU VOU!\n\nTANTAS VIDAS NÃO CONHECEM O SENHOR, VIVEM TRISTES, TÃO VAZIAS, SEM AMOR, PRECISANDO RECEBER O SEU PERDÃO, PARA ASSIM ALCANÇAREM A SALVAÇÃO,\n\nQUE JESUS LÁ NO CALVÁRIO REVELOU, QUANDO SEU PRECIOSO SANGUE DERRAMOU, CONCEDENDO VIDA ETERNA AO QUE CRER, TRANSFORMANDO SUAS VIDAS, QUE PRAZER!\n\nQUE ALEGRIA PODER SERVIR A JESUS, NOSSO SALVADOR! EM SUA OBRA TRABALHAR, ATENDENDO AO SEU CHAMAR, ESTOU PRONTO, SENHOR, EU VOU!', 'NO CAMINHO EU QUERO ESTAR, AO SENHOR VOU OBEDECER. AONDE QUER QUE DEUS MANDAR ANUNCIAR O SEU AMOR. ESTOU PRONTO, SENHOR, EU VOU! TANTAS VIDAS NÃO CONHECEM O SENHOR, VIVEM TRISTES, TÃO VAZIAS, SEM AMOR, PRECISANDO RECEBER O SEU PERDÃO, PARA ASSIM ALCANÇAREM A SALVAÇÃO, QUE JESUS LÁ NO CALVÁRIO REVELOU, QUANDO SEU PRECIOSO SANGUE DERRAMOU, CONCEDENDO VIDA ETERNA AO QUE CRER, TRANSFORMANDO SUAS VIDAS, QUE PRAZER! QUE ALEGRIA PODER SERVIR A JESUS, NOSSO SALVADOR! EM SUA OBRA TRABALHAR, ATENDENDO AO SEU CHAMAR, ESTOU PRONTO, SENHOR, EU VOU!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('159', '159 - SERVO DO SENHOR EU SOU', 'SERVO DO SENHOR EU SOU E POSSO OUVIR SUA VOZ. SERVO DO SENHOR EU SOU E QUERO SERVIR A JESUS.\n\nUMA SÓ VOZ FALA AO MEU CORAÇÃO, A VOZ DE JESUS, MEU SENHOR. OUÇO A VOZ DO PERFEITO AMOR, A VOZ DO MEU BOM SALVADOR.\n\nSE VOCÊ TAMBÉM QUISER, PODE OUVIR A JESUS. BASTA ABRIR SEU CORAÇÃO E DEIXAR ENTRAR SUA LUZ.\n\nE UMA VOZ FALARÁ AO SEU CORAÇÃO, A VOZ DE JESUS, O SENHOR. SÓ A DOCE VOZ ENCHERÁ SEU CORAÇÃO DE PAZ, ALEGRIA E AMOR. DE PAZ, ALEGRIA E AMOR.', 'SERVO DO SENHOR EU SOU E POSSO OUVIR SUA VOZ. SERVO DO SENHOR EU SOU E QUERO SERVIR A JESUS. UMA SÓ VOZ FALA AO MEU CORAÇÃO, A VOZ DE JESUS, MEU SENHOR. OUÇO A VOZ DO PERFEITO AMOR, A VOZ DO MEU BOM SALVADOR. SE VOCÊ TAMBÉM QUISER, PODE OUVIR A JESUS. BASTA ABRIR SEU CORAÇÃO E DEIXAR ENTRAR SUA LUZ. E UMA VOZ FALARÁ AO SEU CORAÇÃO, A VOZ DE JESUS, O SENHOR. SÓ A DOCE VOZ ENCHERÁ SEU CORAÇÃO DE PAZ, ALEGRIA E AMOR. DE PAZ, ALEGRIA E AMOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('160', '160 - EXISTEM SEGREDOS NA TUA PALAVRA', 'EXISTEM SEGREDOS NA TUA PALAVRA QUE SÓ TEU ESPÍRITO PODE REVELAR.\n\nENSINOS DE VIDA, DE FELICIDADE, CAMINHO DE LUZ QUE JESUS VEIO NOS DAR.\n\nÉ LIVRO SELADO, MISTÉRIO GUARDADO, QUE O HOMEM SEM DEUS NÃO PODE ALCANÇAR.\n\nÉ LIVRO ABERTO, CLARO, DESCOBERTO, PARA OS SERVOS FIÉIS QUE COM JESUS VÃO REINAR. PARA OS SERVOS FIÉIS QUE COM JESUS VÃO REINAR.', 'EXISTEM SEGREDOS NA TUA PALAVRA QUE SÓ TEU ESPÍRITO PODE REVELAR. ENSINOS DE VIDA, DE FELICIDADE, CAMINHO DE LUZ QUE JESUS VEIO NOS DAR. É LIVRO SELADO, MISTÉRIO GUARDADO, QUE O HOMEM SEM DEUS NÃO PODE ALCANÇAR. É LIVRO ABERTO, CLARO, DESCOBERTO, PARA OS SERVOS FIÉIS QUE COM JESUS VÃO REINAR. PARA OS SERVOS FIÉIS QUE COM JESUS VÃO REINAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('161', '161 - TU ÉS PRA MIM UM VASO ESCOLHIDO', 'TU ÉS PRA MIM UM VASO ESCOLHIDO, TU ÉS PRA MIM UM VASO DE VALOR. EU TE CHAMEI PARA SALVAR TUA VIDA, EU SOU JESUS, O TEU SENHOR.\n\nTU ÉS PRA MIM UM VASO ESCOLHIDO, TU ÉS PRA MIM UM VASO DE VALOR. PARA LEVAR MEU NOME A TANTAS GENTES E TESTEMUNHAR DO MEU AMOR.\n\nCORO VAI, FALA E ANUNCIA JESUS QUER O HOMEM TRANSFORMAR. OS DIAS PASSAM, O TEMPO FINDA, JESUS TEM PRESSA E VAI VOLTAR!\n\nCORO VAI, FALA E ANUNCIA JESUS QUER O HOMEM TRANSFORMAR. OS DIAS PASSAM, O TEMPO FINDA, JESUS TEM PRESSA E VAI VOLTAR! (2X)', 'TU ÉS PRA MIM UM VASO ESCOLHIDO, TU ÉS PRA MIM UM VASO DE VALOR. EU TE CHAMEI PARA SALVAR TUA VIDA, EU SOU JESUS, O TEU SENHOR. TU ÉS PRA MIM UM VASO ESCOLHIDO, TU ÉS PRA MIM UM VASO DE VALOR. PARA LEVAR MEU NOME A TANTAS GENTES E TESTEMUNHAR DO MEU AMOR. CORO VAI, FALA E ANUNCIA JESUS QUER O HOMEM TRANSFORMAR. OS DIAS PASSAM, O TEMPO FINDA, JESUS TEM PRESSA E VAI VOLTAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('162', '162 - ANDAI NA MINHA PRESENÇA', 'CORO (2X) ANDAI NA MINHA PRESENÇA, ANDAI NA MINHA PRESENÇA E VOS GUIAREI.\n\nE VOS LEVAREI A UM LUGAR CHEIO DE GOZO E DE PAZ. FOME E SEDE JAMAIS TEREIS, POIS COMEREIS DO PÃO DA VIDA.\n\nCORO (2X) ANDAI NA MINHA PRESENÇA, ANDAI NA MINHA PRESENÇA E VOS GUIAREI.\n\nE O MEU ESPÍRITO VOS CONDUZIRÁ À PÁTRIA CELESTIAL. LUTAS E DORES NUNCA MAIS TEREIS, POIS VIVEREIS COM O VOSSO REI.\n\nCORO (2X) ANDAI NA MINHA PRESENÇA, ANDAI NA MINHA PRESENÇA E VOS GUIAREI.', 'ANDAI NA MINHA PRESENÇA, ANDAI NA MINHA PRESENÇA E VOS GUIAREI. E VOS LEVAREI A UM LUGAR CHEIO DE GOZO E DE PAZ. FOME E SEDE JAMAIS TEREIS, POIS COMEREIS DO PÃO DA VIDA. E O MEU ESPÍRITO VOS CONDUZIRÁ À PÁTRIA CELESTIAL. LUTAS E DORES NUNCA MAIS TEREIS, POIS VIVEREIS COM O VOSSO REI.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('163', '163 - DÁ-ME UM CORAÇÃO CHEIO DO TEU AMOR', 'DÁ-ME UM CORAÇÃO CHEIO DO TEU AMOR, QUE SEMPRE RECONHEÇA TUA BONDADE, Ó MEU SENHOR.\n\nUM CORAÇÃO QUE ESTREMEÇA AO OUVIR TUA VOZ ASSIM E QUE NUNCA SE ESQUEÇA O QUE TU FAZES POR MIM.\n\nUM CORAÇÃO OBEDIENTE À TUA REVELAÇÃO, PURO E RENOVADO PELA TUA SALVAÇÃO. Ó SENHOR, AMÉM!', 'DÁ-ME UM CORAÇÃO CHEIO DO TEU AMOR, QUE SEMPRE RECONHEÇA TUA BONDADE, Ó MEU SENHOR. UM CORAÇÃO QUE ESTREMEÇA AO OUVIR TUA VOZ ASSIM E QUE NUNCA SE ESQUEÇA O QUE TU FAZES POR MIM. UM CORAÇÃO OBEDIENTE À TUA REVELAÇÃO, PURO E RENOVADO PELA TUA SALVAÇÃO. Ó SENHOR, AMÉM!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('164', '164 - DÁ-ME UM CORAÇÃO HUMILDE E RETO', 'DÁ-ME UM CORAÇÃO HUMILDE E RETO, QUE ATENTE PARA TUA PALAVRA E SABEDORIA,PRA QUE EU POSSA ANDAR EM TUA PRESENÇA.\n\nCORO QUERO SER OBEDIENTE À TUA VOZ, TRABALHAR NA TUA OBRA COM AMOR; QUERO IR MORAR PRA SEMPRE NO PALÁCIO DO REI DOS REIS, NAS MANSÕES CELESTIAIS.\n\nQUE MINHA CASA ESTEJA FIRMADA EM JESUS, ROCHA ETERNA, ELE É MEU ABRIGO SEGURO, MINHA FORTALEZA, MEU AMIGO FIEL, O MEU SALVADOR.\n\nCORO QUERO SER OBEDIENTE À TUA VOZ, TRABALHAR NA TUA OBRA COM AMOR; QUERO IR MORAR PRA SEMPRE NO PALÁCIO DO REI DOS REIS, NAS MANSÕES CELESTIAIS.\n\nCORO QUERO SER OBEDIENTE À TUA VOZ, TRABALHAR NA TUA OBRA COM AMOR; QUERO IR MORAR PRA SEMPRE NO PALÁCIO DO REI DOS REIS, NAS MANSÕES CELESTIAIS. (BIS)', 'DÁ-ME UM CORAÇÃO HUMILDE E RETO, QUE ATENTE PARA TUA PALAVRA E SABEDORIA,PRA QUE EU POSSA ANDAR EM TUA PRESENÇA. CORO QUERO SER OBEDIENTE À TUA VOZ, TRABALHAR NA TUA OBRA COM AMOR; QUERO IR MORAR PRA SEMPRE NO PALÁCIO DO REI DOS REIS, NAS MANSÕES CELESTIAIS. QUE MINHA CASA ESTEJA FIRMADA EM JESUS, ROCHA ETERNA, ELE É MEU ABRIGO SEGURO, MINHA FORTALEZA, MEU AMIGO FIEL, O MEU SALVADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('165', '165 - QUERO SER SANTO PARA O SENHOR', 'QUERO SER SANTO PARA O SENHOR, QUERO SER PURO EM SEU AMOR, EM TODO O TEMPO TESTEMUNHAR DE SEU AMOR E GRAÇA SEM PAR.\n\nQUERO LEVAR A MENSAGEM DE SALVAÇÃO PARA ESTE MUNDO QUE ESTÁ NA ESCURIDÃO E QUANDO CRISTO VIER NOS BUSCAR POSSAMOS TODOS NO CÉU IR MORAR.\n\nQUERO SER SANTO PARA O SENHOR, QUERO SER PURO EM SEU AMOR, EM TODO O TEMPO TESTEMUNHAR DE SEU AMOR E GRAÇA SEM PAR.\n\nQUERO LEVAR A MENSAGEM DE SALVAÇÃO PARA ESTE MUNDO QUE ESTÁ NA ESCURIDÃO E QUANDO CRISTO VIER NOS BUSCAR POSSAMOS TODOS NO CÉU IR MORAR.\n\nQUERO SER SANTO, QUERO SER PURO PARA O SENHOR!', 'QUERO SER SANTO PARA O SENHOR, QUERO SER PURO EM SEU AMOR, EM TODO O TEMPO TESTEMUNHAR DE SEU AMOR E GRAÇA SEM PAR. QUERO LEVAR A MENSAGEM DE SALVAÇÃO PARA ESTE MUNDO QUE ESTÁ NA ESCURIDÃO E QUANDO CRISTO VIER NOS BUSCAR POSSAMOS TODOS NO CÉU IR MORAR. QUERO SER SANTO, QUERO SER PURO PARA O SENHOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('166', '166 - BRILHANDO, BRILHANDO', 'BRILHANDO, BRILHANDO, MINHA CANDEIA SEMPRE ESTÁ BRILHANDO. BRILHANDO, BRILHANDO, BRILHANDO ATÉ JESUS VOLTAR.\n\nNÃO FALTARÁ O ÓLEO DO ESPÍRITO, MINHA CANDEIA SEMPRE CHEIA FICARÁ, PORQUE JESUS NOS DEU SUA PROMESSA QUE SEU ESPÍRITO CONOSCO ESTARÁ.\n\nE SE VOCÊ QUISER TAMBÉM BRILHAR, É SÓ ABRIR SEU CORAÇÃO E DEIXAR CRISTO NELE ENTRAR E ASSIM VOCÊ SEMPRE ESTARÁ.\n\nBRILHANDO, BRILHANDO, MINHA CANDEIA SEMPRE ESTÁ BRILHANDO. BRILHANDO, BRILHANDO, BRILHANDO ATÉ JESUS VOLTAR.', 'BRILHANDO, BRILHANDO, MINHA CANDEIA SEMPRE ESTÁ BRILHANDO. BRILHANDO, BRILHANDO, BRILHANDO ATÉ JESUS VOLTAR. NÃO FALTARÁ O ÓLEO DO ESPÍRITO, MINHA CANDEIA SEMPRE CHEIA FICARÁ, PORQUE JESUS NOS DEU SUA PROMESSA QUE SEU ESPÍRITO CONOSCO ESTARÁ. E SE VOCÊ QUISER TAMBÉM BRILHAR, É SÓ ABRIR SEU CORAÇÃO E DEIXAR CRISTO NELE ENTRAR E ASSIM VOCÊ SEMPRE ESTARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('167', '167- DANIEL NA COVA DOS LEÕES', 'DANIEL TRÊS VEZES AO DIA BUSCAVA AO SENHOR, DE JOELHOS ELE ORAVA E GLORIFICAVA A DEUS.\n\nCERTO DIA O ENCONTRARAM ORANDO PROSTRADO AO SEU DEUS, SEUS INIMIGOS CONTARAM AO REI E PEDIRAM PARA QUE CUMPRISSE A LEI.\n\nCORO DANIEL NA COVA DOS LEÕES, UM ANJO APARECEU. DANIEL NA COVA DOS LEÕES, O SEU DEUS O PROTEGEU. INSTRUMENTOS\n\nDANIEL FOI SALVO POR SEU DEUS E O REI COMPREENDEU. SEUS INIMIGOS FORAM PUNIDOS E O REI LOGO DECRETOU QUE O DEUS DE DANIEL É DEUS VIVO E SALVADOR.\n\nCORO DANIEL NA COVA DOS LEÕES, UM ANJO APARECEU. DANIEL NA COVA DOS LEÕES, O SEU DEUS O PROTEGEU.', 'DANIEL TRÊS VEZES AO DIA BUSCAVA AO SENHOR, DE JOELHOS ELE ORAVA E GLORIFICAVA A DEUS. CERTO DIA O ENCONTRARAM ORANDO PROSTRADO AO SEU DEUS, SEUS INIMIGOS CONTARAM AO REI E PEDIRAM PARA QUE CUMPRISSE A LEI. CORO DANIEL NA COVA DOS LEÕES, UM ANJO APARECEU. DANIEL NA COVA DOS LEÕES, O SEU DEUS O PROTEGEU. INSTRUMENTOS DANIEL FOI SALVO POR SEU DEUS E O REI COMPREENDEU. SEUS INIMIGOS FORAM PUNIDOS E O REI LOGO DECRETOU QUE O DEUS DE DANIEL É DEUS VIVO E SALVADOR. CORO DANIEL NA COVA DOS LEÕES, UM ANJO APARECEU. DANIEL NA COVA DOS LEÕES, O SEU DEUS O PROTEGEU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('168', '168 - JESUS CHAMA OS SEUS MINISTROS', 'JESUS CHAMA OS SEUS MINISTROS PARA UMA GRANDE MISSÃO: GUIAR O SEU POVO RUMO À BELA SIÃO.\n\nO APÓSTOLO É AQUELE, QUE LEVA A PALAVRA DO SENHOR. TESTEMUNHA PRA MUITOS POVOS, APRESENTA A OBRA COM AMOR.\n\nO PROFETA APONTA O CAMINHO, É UM PROCLAMADOR. FALA DO QUE ESTÁ POR VIR, É O INTÉRPRETE DO SENHOR.\n\nPARA O EVANGELISTA NÃO HÁ TEMPO RUIM, PREGA A PALAVRA EM TODOS OS LUGARES COM A AUTORIDADE DE JESUS.\n\nO PASTOR CUIDA DA IGREJA GUIANDO O REBANHO À SALVAÇÃO. APASCENTA COM CUIDADO, LEVANDO O POVO À ADORAÇÃO!\n\nE O MESTRE É AQUELE, O MENOR DOS IRMÃOS, ENSINA A SÃ DOUTRINA, EDUCA COM A REVELAÇÃO.\n\nESTES SÃO OS CINCO MINISTÉRIOS, FORMA SIMPLES E PATERNA DE JESUS ORIENTAR A SUA IGREJA, LEVANDO-A À VIDA ETERNA! LEVANDO-A À VIDA ETERNA!', 'JESUS CHAMA OS SEUS MINISTROS PARA UMA GRANDE MISSÃO: GUIAR O SEU POVO RUMO À BELA SIÃO. O APÓSTOLO É AQUELE, QUE LEVA A PALAVRA DO SENHOR. TESTEMUNHA PRA MUITOS POVOS, APRESENTA A OBRA COM AMOR. O PROFETA APONTA O CAMINHO, É UM PROCLAMADOR. FALA DO QUE ESTÁ POR VIR, É O INTÉRPRETE DO SENHOR. PARA O EVANGELISTA NÃO HÁ TEMPO RUIM, PREGA A PALAVRA EM TODOS OS LUGARES COM A AUTORIDADE DE JESUS. O PASTOR CUIDA DA IGREJA GUIANDO O REBANHO À SALVAÇÃO. APASCENTA COM CUIDADO, LEVANDO O POVO À ADORAÇÃO! E O MESTRE É AQUELE, O MENOR DOS IRMÃOS, ENSINA A SÃ DOUTRINA, EDUCA COM A REVELAÇÃO. ESTES SÃO OS CINCO MINISTÉRIOS, FORMA SIMPLES E PATERNA DE JESUS ORIENTAR A SUA IGREJA, LEVANDO-A À VIDA ETERNA! LEVANDO-A À VIDA ETERNA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('169', '169 - SETE CARTAS', 'SETE CARTAS JOÃO ESCREVEU, AO ANJO DAS IGREJAS DEUS MANDOU, “CONHEÇO AS TUAS OBRAS”, ASSIM DIZ O SENHOR, “QUEM TEM OUVIDOS OUÇA A VOZ DO MEU ESPÍRITO”.\n\nÉFESO, ESMIRNA, PÉRGAMO, TIATIRA, SARDES, FILADÉLFIA E LAODICÉIA.\n\nSÃO SETE CARTAS, SETE IGREJAS, SETE ESPÍRITOS DE DEUS A FALAR NO MEIO DAS IGREJAS.\n\nSÃO SETE CARTAS, SETE IGREJAS, SETE ESPÍRITOS DE DEUS A FALAR NO MEIO DAS IGREJAS. INSTRUMENTOS\n\nSETE CARTAS JOÃO ESCREVEU, AO ANJO DAS IGREJAS DEUS MANDOU, “CONHEÇO AS TUAS OBRAS”, ASSIM DIZ O SENHOR, “QUEM TEM OUVIDOS OUÇA A VOZ DO MEU ESPÍRITO”.\n\nÉFESO, ESMIRNA, PÉRGAMO, TIATIRA, SARDES, FILADÉLFIA E LAODICÉIA.\n\nSÃO SETE CARTAS, SETE IGREJAS, SETE ESPÍRITOS DE DEUS A FALAR NO MEIO DAS IGREJAS.', 'SETE CARTAS JOÃO ESCREVEU, AO ANJO DAS IGREJAS DEUS MANDOU, CONHEÇO AS TUAS OBRAS, ASSIM DIZ O SENHOR, QUEM TEM OUVIDOS OUÇA A VOZ DO MEU ESPÍRITO. ÉFESO, ESMIRNA, PÉRGAMO, TIATIRA, SARDES, FILADÉLFIA E LAODICÉIA. SÃO SETE CARTAS, SETE IGREJAS, SETE ESPÍRITOS DE DEUS A FALAR NO MEIO DAS IGREJAS. SÃO SETE CARTAS, SETE IGREJAS, SETE ESPÍRITOS DE DEUS A FALAR NO MEIO DAS IGREJAS. INSTRUMENTOS', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('170', '170 - NÓS SOMOS A CARTA DE CRISTO', 'NÓS SOMOS A CARTA DE CRISTO, A CARTA VIVA DE DEUS, PARA SER LIDA POR TODOS, REVELAR OS PROPÓSITOS DE DEUS.\n\nO QUE COMUNICA ESSA CARTA? O AMOR, O PERDÃO DE JESUS, AS PROMESSAS DE VIDA ETERNA NO CAMINHO QUE ELE NOS CONDUZ.\n\nNÃO FOI ESCRITA NA PEDRA, MAS NAS TÁBUAS DO CORAÇÃO. E QUEM ESCREVEU ESSA CARTA FOI O ESPÍRITO SANTO DE DEUS.\n\nNÓS SOMOS A CARTA DE CRISTO, A CARTA VIVA DE DEUS TESTEMUNHANDO A TODOS PRA SALVAR-NOS JESUS MORREU.', 'NÓS SOMOS A CARTA DE CRISTO, A CARTA VIVA DE DEUS, PARA SER LIDA POR TODOS, REVELAR OS PROPÓSITOS DE DEUS. O QUE COMUNICA ESSA CARTA? O AMOR, O PERDÃO DE JESUS, AS PROMESSAS DE VIDA ETERNA NO CAMINHO QUE ELE NOS CONDUZ. NÃO FOI ESCRITA NA PEDRA, MAS NAS TÁBUAS DO CORAÇÃO. E QUEM ESCREVEU ESSA CARTA FOI O ESPÍRITO SANTO DE DEUS. NÓS SOMOS A CARTA DE CRISTO, A CARTA VIVA DE DEUS TESTEMUNHANDO A TODOS PRA SALVAR-NOS JESUS MORREU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('171', '171 - SOU O SANTO', 'SOU O SANTO O VERDADEIRO, TENHO A CHAVE DE DAVI; A PORTA ABERTA ESTÁ, PRA QUE POSSAS ME ADORAR!\n\nGUARDASTE AS MINHAS PALAVRAS, NÃO NEGASTE O MEU NOME; TAMBÉM EU TE GUARDAREI, EIS QUE VENHO SEM DEMORA.\n\nENTÃO, GUARDA O QUE TENS, PARA QUE NINGUÉM TOME A TUA COROA.', 'SOU O SANTO O VERDADEIRO, TENHO A CHAVE DE DAVI; A PORTA ABERTA ESTÁ, PRA QUE POSSAS ME ADORAR! GUARDASTE AS MINHAS PALAVRAS, NÃO NEGASTE O MEU NOME; TAMBÉM EU TE GUARDAREI, EIS QUE VENHO SEM DEMORA. ENTÃO, GUARDA O QUE TENS, PARA QUE NINGUÉM TOME A TUA COROA.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('172', '172 - QUATRO COISAS TÃO PEQUENINAS', 'QUATRO COISAS TÃO PEQUENINAS UMA GRANDE LIÇÃO NOS DÃO: EMBORA TÃO FRÁGEIS, MUITO SÁBIAS SÃO.\n\nAS FORMIGAS SÃO TRABALHADORAS, CADA UMA EXERCE A SUA FUNÇÃO; É COMO A IGREJA DE CRISTO, TRABALHANDO PRA MANTER A SALVAÇÃO.\n\nEM SEGURANÇA O COELHO CONSTRÓI SUA CASA SOBRE A ROCHA; E, PARA ESTARMOS BEM SEGUROS, CONSTRUÍMOS NA ROCHA QUE É JESUS.\n\nOS GAFANHOTOS TODOS SAEM, E EM GRUPOS SE REPARTEM, SAÍMOS PROCLAMANDO A PALAVRA, SEMPRE UNIDOS, EM UM SÓ CORAÇÃO.\n\nE, NO FINAL DA CAMINHADA, HERDEIROS DAS PROMESSAS DE DEUS, TAL QUAL A ARANHA MORA NO PALÁCIO, MORAREMOS NAS MANSÕES COM JESUS!', 'QUATRO COISAS TÃO PEQUENINAS UMA GRANDE LIÇÃO NOS DÃO: EMBORA TÃO FRÁGEIS, MUITO SÁBIAS SÃO. AS FORMIGAS SÃO TRABALHADORAS, CADA UMA EXERCE A SUA FUNÇÃO; É COMO A IGREJA DE CRISTO, TRABALHANDO PRA MANTER A SALVAÇÃO. EM SEGURANÇA O COELHO CONSTRÓI SUA CASA SOBRE A ROCHA; E, PARA ESTARMOS BEM SEGUROS, CONSTRUÍMOS NA ROCHA QUE É JESUS. OS GAFANHOTOS TODOS SAEM, E EM GRUPOS SE REPARTEM, SAÍMOS PROCLAMANDO A PALAVRA, SEMPRE UNIDOS, EM UM SÓ CORAÇÃO. E, NO FINAL DA CAMINHADA, HERDEIROS DAS PROMESSAS DE DEUS, TAL QUAL A ARANHA MORA NO PALÁCIO, MORAREMOS NAS MANSÕES COM JESUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('173', '173 - OUÇO O CLAMOR DO BOM PASTOR', 'OUÇO O CLAMOR DO BOM PASTOR PELO DESERTO ASSOLADOR, SEUS CORDEIRINHOS A CHAMAR, MUI DESEJOSO DE OS SALVAR.\n\nCORO VAI BUSCAR, VAI BUSCAR! VAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR! TRAZE-OS PARA O MEU APRISCO.\n\nQUEM NÃO DESEJA TRABALHAR E OS CORDEIRINHOS SEUS BUSCAR E ENCAMINHÁ-LOS A JESUS, FONTE DE VIDA, AMOR E LUZ?\n\nCORO VAI BUSCAR, VAI BUSCAR! VAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR! TRAZE-OS PARA O MEU APRISCO.\n\nPELOS DESERTOS A SOFRER, OUVE OS SEUS GRITOS AO MORRER E O MESTRE CLAMA: “VAI BUSCAR!” PRA QUE OS POSSA, ENTÃO, SALVAR.\n\nCORO VAI BUSCAR, VAI BUSCAR! VAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR! TRAZE-OS PARA O MEU APRISCO.', 'OUÇO O CLAMOR DO BOM PASTOR PELO DESERTO ASSOLADOR, SEUS CORDEIRINHOS A CHAMAR, MUI DESEJOSO DE OS SALVAR. CORO VAI BUSCAR, VAI BUSCAR! VAI BUSCAR MEUS CORDEIRINHOS, VAI! VAI BUSCAR, VAI BUSCAR! TRAZE-OS PARA O MEU APRISCO. QUEM NÃO DESEJA TRABALHAR E OS CORDEIRINHOS SEUS BUSCAR E ENCAMINHÁ-LOS A JESUS, FONTE DE VIDA, AMOR E LUZ? PELOS DESERTOS A SOFRER, OUVE OS SEUS GRITOS AO MORRER E O MESTRE CLAMA: VAI BUSCAR! PRA QUE OS POSSA, ENTÃO, SALVAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('174', '174 - A TUA PALAVRA É SEMENTE', 'A TUA PALAVRA É SEMENTE E TU ÉS O SEMEADOR, O MEU CORAÇÃO É A TERRA QUE TU SEMEASTE, SENHOR.\n\nCORO A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA, SENHOR; A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA É AMOR.\n\nMEDITANDO EU CERTO DIA NA TUA PALAVRA, SENHOR, SENTI QUE DO ALTO DESCIA A GRAÇA DO CONSOLADOR.\n\nCORO A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA, SENHOR; A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA É AMOR.', 'A TUA PALAVRA É SEMENTE E TU ÉS O SEMEADOR, O MEU CORAÇÃO É A TERRA QUE TU SEMEASTE, SENHOR. CORO A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA, SENHOR; A TUA PALAVRA, A TUA PALAVRA, A TUA PALAVRA É AMOR. MEDITANDO EU CERTO DIA NA TUA PALAVRA, SENHOR, SENTI QUE DO ALTO DESCIA A GRAÇA DO CONSOLADOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('175', '175 - UM CERTO PASTORZINHO', 'UM CERTO PASTORZINHO, QUE SE CHAMOU DAVI, ERA MUI PEQUENO, NÃO CONFIAVA EM SI; ELE, COM OUSADIA, ORANDO SEMPRE A DEUS, FOI ENFRENTAR, UM DIA, O HERÓI DOS FILISTEUS.\n\nNA FUNDA UMA PEDRA COLOCOU, E GIROU, GIROU, GIROU; NA FUNDA UMA PEDRA COLOCOU, E GIROU, GIROU, GIROU;\n\nMAIS UMA VEZ, MAIS OUTRA VEZ, MAIS UMA VEZ GIROU, E A PEDRA ATIROU, O ALVO ACERTOU, E O GIGANTE AO CHÃO TOMBOU!', 'UM CERTO PASTORZINHO, QUE SE CHAMOU DAVI, ERA MUI PEQUENO, NÃO CONFIAVA EM SI; ELE, COM OUSADIA, ORANDO SEMPRE A DEUS, FOI ENFRENTAR, UM DIA, O HERÓI DOS FILISTEUS. NA FUNDA UMA PEDRA COLOCOU, E GIROU, GIROU, GIROU; NA FUNDA UMA PEDRA COLOCOU, E GIROU, GIROU, GIROU; MAIS UMA VEZ, MAIS OUTRA VEZ, MAIS UMA VEZ GIROU, E A PEDRA ATIROU, O ALVO ACERTOU, E O GIGANTE AO CHÃO TOMBOU!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
