INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('177', '177 - FALA AO MEU CORAÇÃO, JESUS', 'FALA AO MEU CORAÇÃO, JESUS! FALA AO MEU CORAÇÃO, JESUS! FALA, FALA, FALA, FALA AO MEU CORAÇÃO.\n\nVIVE EM MEU CORAÇÃO, JESUS! VIVE EM MEU CORAÇÃO, JESUS! VIVE, VIVE, VIVE, VIVE EM MEU CORAÇÃO.\n\nREINA EM MEU CORAÇÃO, JESUS! REINA EM MEU CORAÇÃO, JESUS! FALA, VIVE, REINA, REINA EM MEU CORAÇÃO.', 'FALA AO MEU CORAÇÃO, JESUS! FALA AO MEU CORAÇÃO, JESUS! FALA, FALA, FALA, FALA AO MEU CORAÇÃO. VIVE EM MEU CORAÇÃO, JESUS! VIVE EM MEU CORAÇÃO, JESUS! VIVE, VIVE, VIVE, VIVE EM MEU CORAÇÃO. REINA EM MEU CORAÇÃO, JESUS! REINA EM MEU CORAÇÃO, JESUS! FALA, VIVE, REINA, REINA EM MEU CORAÇÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('178', '178 - DISSE JESUS AOS JUDEUS (EU TRABALHO TAMBÉM)', 'DISSE JESUS AOS JUDEUS, ASSIM JESUS LHES RESPONDEU: “O MEU PAI TRABALHA ATÉ AGORA, EU TRABALHO TAMBÉM!\n\nEU TRABALHO TAMBÉM, EU TRABALHO TAMBÉM, O MEU PAI TRABALHA ATÉ AGORA, E EU TRABALHO TAMBÉM!”\n\nCONVÉM QUE EU FAÇA AS MESMAS OBRAS DAQUELE QUE ME ENVIOU, ENQUANTO É DIA, A NOITE VEM QUANDO NÃO SE PODE TRABALHAR. O MEU PAI TRABALHA ATÉ AGORA, EU TRABALHO TAMBÉM!\n\nQUERO TRABALHAR TAMBÉM, QUERO TRABALHAR TAMBÉM, COM O ESPÍRITO SANTO EM MINHA VIDA, QUERO TRABALHAR TAMBÉM!', 'DISSE JESUS AOS JUDEUS, ASSIM JESUS LHES RESPONDEU: O MEU PAI TRABALHA ATÉ AGORA, EU TRABALHO TAMBÉM! EU TRABALHO TAMBÉM, EU TRABALHO TAMBÉM, O MEU PAI TRABALHA ATÉ AGORA, E EU TRABALHO TAMBÉM! CONVÉM QUE EU FAÇA AS MESMAS OBRAS DAQUELE QUE ME ENVIOU, ENQUANTO É DIA, A NOITE VEM QUANDO NÃO SE PODE TRABALHAR. O MEU PAI TRABALHA ATÉ AGORA, EU TRABALHO TAMBÉM! QUERO TRABALHAR TAMBÉM, QUERO TRABALHAR TAMBÉM, COM O ESPÍRITO SANTO EM MINHA VIDA, QUERO TRABALHAR TAMBÉM!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('179', '179 - ORAR FAZ BEM', 'ORAR FAZ BEM! ORAR FAZ BEM! FAZ BEM À ALMA, FAZ BEM AO CORAÇÃO, ALEGRA A ALMA, ALEGRA O CORAÇÃO!\n\nORAR FAZ BEM! ORAR FAZ BEM! O NOSSO DEUS RESPONDE, RETIRA A AFLIÇÃO! O NOSSO DEUS RESPONDE, CONFORTA O CORAÇÃO!', 'ORAR FAZ BEM! ORAR FAZ BEM! FAZ BEM À ALMA, FAZ BEM AO CORAÇÃO, ALEGRA A ALMA, ALEGRA O CORAÇÃO! ORAR FAZ BEM! ORAR FAZ BEM! O NOSSO DEUS RESPONDE, RETIRA A AFLIÇÃO! O NOSSO DEUS RESPONDE, CONFORTA O CORAÇÃO!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('180', '180 - QUERO SER FIEL (JESUS ESTÁ VIVO)', 'QUERO SER FIEL A TI, JESUS. FAZER SEMPRE A TUA VONTADE. GUARDAR A TUA PALAVRA, QUE É LUZ E VIDA PARA O MEU CAMINHO.\n\nCORO SER FIEL, SER FIEL, SER FIEL ATÉ O FIM. SER FIEL, SER FIEL ATÉ MEU JESUS VOLTAR!\n\nQUERO ANDAR NA TUA LUZ, SENHOR, SÓ ASSIM ESTOU SEGURO; QUERO PROCLAMAR EM ALTA VOZ: “JESUS RESSUSCITOU AO TERCEIRO DIA, ESTÁ VIVO E REINA EM MEU VIVER”.\n\nCORO SER FIEL, SER FIEL, SER FIEL ATÉ O FIM. SER FIEL, SER FIEL ATÉ MEU JESUS VOLTAR! JESUS ESTÁ VIVO, REINA EM MEU VIVER, E EM BREVE VOLTARÁ!', 'QUERO SER FIEL A TI, JESUS. FAZER SEMPRE A TUA VONTADE. GUARDAR A TUA PALAVRA, QUE É LUZ E VIDA PARA O MEU CAMINHO. CORO SER FIEL, SER FIEL, SER FIEL ATÉ O FIM. SER FIEL, SER FIEL ATÉ MEU JESUS VOLTAR! QUERO ANDAR NA TUA LUZ, SENHOR, SÓ ASSIM ESTOU SEGURO; QUERO PROCLAMAR EM ALTA VOZ: JESUS RESSUSCITOU AO TERCEIRO DIA, ESTÁ VIVO E REINA EM MEU VIVER. CORO SER FIEL, SER FIEL, SER FIEL ATÉ O FIM. SER FIEL, SER FIEL ATÉ MEU JESUS VOLTAR! JESUS ESTÁ VIVO, REINA EM MEU VIVER, E EM BREVE VOLTARÁ!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('181', '181 - JERUSALÉM, CIDADE DO MEU REI', 'JERUSALÉM, CIDADE DO MEU REI, A CADA DIA ANSEIO ESTAR ALI; CONTEMPLAREI A FACE DO SENHOR, AQUELE QUE ENTREGOU SUA VIDA POR MIM.\n\nJAMAIS A MORTE VEREI POIS VIDA ETERNA ALCANCEI QUANDO ENTREGUEI MEU CORAÇÃO PARA JESUS.', 'JERUSALÉM, CIDADE DO MEU REI, A CADA DIA ANSEIO ESTAR ALI; CONTEMPLAREI A FACE DO SENHOR, AQUELE QUE ENTREGOU SUA VIDA POR MIM. JAMAIS A MORTE VEREI POIS VIDA ETERNA ALCANCEI QUANDO ENTREGUEI MEU CORAÇÃO PARA JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('182', '182 - QUERO VIVER PARA SEMPRE COM JESUS', 'CORO QUERO VIVER PARA SEMPRE COM JESUS, QUERO BEBER DA ÁGUA DA VIDA,\n\nQUERO COMER DA ÁRVORE DA VIDA, COM JESUS ETERNAMENTE VOU VIVER!\n\nVOU VIVER, SIM, VOU VIVER, PASSEAR NO JARDIM DO MEU SENHOR; A ÁRVORE DA VIDA NO MEIO DO JARDIM É CERTEZA QUE JESUS ME PROMETEU.\n\nCORO QUERO VIVER PARA SEMPRE COM JESUS, QUERO BEBER DA ÁGUA DA VIDA, QUERO COMER DA ÁRVORE DA VIDA, COM JESUS ETERNAMENTE VOU VIVER!\n\nPARA TRÁS O PECADO FICARÁ, SEU DOMÍNIO NÃO ME ATINGIRÁ; ESCOLHI MUITO BEM: VIVER ETERNAMENTE COM JESUS!\n\nCORO QUERO VIVER PARA SEMPRE COM JESUS, QUERO BEBER DA ÁGUA DA VIDA, QUERO COMER DA ÁRVORE DA VIDA, COM JESUS ETERNAMENTE VOU VIVER!', 'CORO QUERO VIVER PARA SEMPRE COM JESUS, QUERO BEBER DA ÁGUA DA VIDA, QUERO COMER DA ÁRVORE DA VIDA, COM JESUS ETERNAMENTE VOU VIVER! VOU VIVER, SIM, VOU VIVER, PASSEAR NO JARDIM DO MEU SENHOR; A ÁRVORE DA VIDA NO MEIO DO JARDIM É CERTEZA QUE JESUS ME PROMETEU. CORO QUERO VIVER PARA SEMPRE COM JESUS, QUERO BEBER DA ÁGUA DA VIDA, QUERO COMER DA ÁRVORE DA VIDA, COM JESUS ETERNAMENTE VOU VIVER! PARA TRÁS O PECADO FICARÁ, SEU DOMÍNIO NÃO ME ATINGIRÁ; ESCOLHI MUITO BEM: VIVER ETERNAMENTE COM JESUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('183', '183 - UM NOVO CÉU, UMA NOVA TERRA SE FARÁ', 'UM NOVO CÉU, UMA NOVA TERRA SE FARÁ, NOVO NOME NUMA PEDRA BRANCA RECEBEREI,\n\nNA CIDADE DE LUZ ETERNA IREI MORAR PARA SEMPRE COM O SALVADOR; RUAS DE OURO ANDAREI, RIO DA VIDA CONTEMPLAREI\n\nE MUITO LOUVOR ALI ENTOAREI, NÃO HAVERÁ DOR, PRANTO OU CLAMOR, TODA LÁGRIMA JESUS VAI ENXUGAR.\n\nNÓS SEREMOS O SEU POVO ETERNAMENTE E DEUS NOSSO TABERNÁCULO PARA SEMPRE;\n\nSE VOCÊ QUISER VIR A CRISTO, ABRA AGORA O SEU CORAÇÃO E DEIXA JESUS FAZER DA SUA VIDA MORADA ETERNA DE DEUS.', 'UM NOVO CÉU, UMA NOVA TERRA SE FARÁ, NOVO NOME NUMA PEDRA BRANCA RECEBEREI, NA CIDADE DE LUZ ETERNA IREI MORAR PARA SEMPRE COM O SALVADOR; RUAS DE OURO ANDAREI, RIO DA VIDA CONTEMPLAREI E MUITO LOUVOR ALI ENTOAREI, NÃO HAVERÁ DOR, PRANTO OU CLAMOR, TODA LÁGRIMA JESUS VAI ENXUGAR. NÓS SEREMOS O SEU POVO ETERNAMENTE E DEUS NOSSO TABERNÁCULO PARA SEMPRE; SE VOCÊ QUISER VIR A CRISTO, ABRA AGORA O SEU CORAÇÃO E DEIXA JESUS FAZER DA SUA VIDA MORADA ETERNA DE DEUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('184', '184 - NO EXPRESSO VIAJAMOS', 'NO EXPRESSO VIAJAMOS PARA O BOM PAÍS DA LUZ, É O NOSSO PASSAPORTE O NOME DE JESUS;\n\nÉ HORA DA PARTIDA E ENSEJO AINDA TÊM AQUELES QUE QUISEREM TER PASSAGEM NESSE TREM.\n\nBEM-VINDOS SEJAM TODOS PARA O TREM QUE PARTE JÁ, ENTRADA PARA A PÁTRIA JESUS A TODOS DÁ; EIS O SINAL DE MARCHA, SE QUERES IR TAMBÉM, RECEBE O TEU BILHETE DE PASSAGEM NESSE TREM.', 'NO EXPRESSO VIAJAMOS PARA O BOM PAÍS DA LUZ, É O NOSSO PASSAPORTE O NOME DE JESUS; É HORA DA PARTIDA E ENSEJO AINDA TÊM AQUELES QUE QUISEREM TER PASSAGEM NESSE TREM. BEM-VINDOS SEJAM TODOS PARA O TREM QUE PARTE JÁ, ENTRADA PARA A PÁTRIA JESUS A TODOS DÁ; EIS O SINAL DE MARCHA, SE QUERES IR TAMBÉM, RECEBE O TEU BILHETE DE PASSAGEM NESSE TREM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('185', '185 - HÁ UMA LINDA MORADA', 'HÁ UMA LINDA MORADA PRA MIM NO CÉU QUE JESUS FOI ALI PREPARAR; UM LUGAR DE DELÍCIAS E AMOR SEM FIM, COM JESUS VOU PRA SEMPRE MORAR.\n\nNA CIDADE DE DEUS, NOVA JERUSALÉM, ONDE O SOL NÃO PRECISA BRILHAR; O CORDEIRO É A LUZ QUE ILUMINA A CIDADE ONDE VOU PARA SEMPRE MORAR.', 'HÁ UMA LINDA MORADA PRA MIM NO CÉU QUE JESUS FOI ALI PREPARAR; UM LUGAR DE DELÍCIAS E AMOR SEM FIM, COM JESUS VOU PRA SEMPRE MORAR. NA CIDADE DE DEUS, NOVA JERUSALÉM, ONDE O SOL NÃO PRECISA BRILHAR; O CORDEIRO É A LUZ QUE ILUMINA A CIDADE ONDE VOU PARA SEMPRE MORAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('186', '186 - NOSSAS VIDAS SÃO MUDADAS', 'NOSSAS VIDAS SÃO MUDADAS PARA ANDARMOS MUI FELIZES, QUANDO OUVIMOS A PALAVRA, DO NOSSO AMIGO E JUIZ.\n\nELE JULGA A NOSSA CAUSA E APAGA A TRANSGRESSÃO, DE TODO MAL SOMOS GUARDADOS, CAMINHAMOS EM SUA LUZ.\n\nLUZ QUE BRILHA A CADA INSTANTE PARA SERMOS IDENTIFICADOS, NAQUELE GRANDE DIA, E POR JESUS ARREBATADOS.\n\nMARANATA! É O NOSSO GRITO, MARANATA! VEM, JESUS! EM BREVE A TROMBETA SOARÁ, NO CÉU COM ELE QUERO ESTAR.\n\nMARANATA! É O NOSSO GRITO, MARANATA! VEM, JESUS! EM BREVE A TROMBETA SOARÁ, NO CÉU COM ELE QUERO ESTAR. NO CÉU COM ELE QUERO ESTAR.', 'NOSSAS VIDAS SÃO MUDADAS PARA ANDARMOS MUI FELIZES, QUANDO OUVIMOS A PALAVRA, DO NOSSO AMIGO E JUIZ. ELE JULGA A NOSSA CAUSA E APAGA A TRANSGRESSÃO, DE TODO MAL SOMOS GUARDADOS, CAMINHAMOS EM SUA LUZ. LUZ QUE BRILHA A CADA INSTANTE PARA SERMOS IDENTIFICADOS, NAQUELE GRANDE DIA, E POR JESUS ARREBATADOS. MARANATA! É O NOSSO GRITO, MARANATA! VEM, JESUS! EM BREVE A TROMBETA SOARÁ, NO CÉU COM ELE QUERO ESTAR. MARANATA! É O NOSSO GRITO, MARANATA! VEM, JESUS! EM BREVE A TROMBETA SOARÁ, NO CÉU COM ELE QUERO ESTAR. NO CÉU COM ELE QUERO ESTAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
