INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('191', '191 - A NOIVA AMADA DO CORDEIRO', 'A NOIVA AMADA DO CORDEIRO ESTÁ SENDO PREPARADA PARA O ENCONTRO COM SEU NOIVO NA LINDA FESTA NO CÉU.\n\nCORO DIANTE DO TRONO DO CORDEIRO EXPRESSARÁ TODO AMOR; ALELUIA! HONRA E GLÓRIA AO NOSSO SALVADOR!\n\nSEGUE O CORDEIRO PARA ONDE ELE VAI, LÁ ESTÁ ELA, TÃO FORMOSA! PREPARADA JÁ ESTÁ.\n\nCORO DIANTE DO TRONO DO CORDEIRO EXPRESSARÁ TODO AMOR; ALELUIA! HONRA E GLÓRIA AO NOSSO SALVADOR!\n\nCANTARÁ UM CANTO NOVO E SÓ ELA SABERÁ POIS SOMENTE OS REMIDOS ESTE CANTO ENTOARÃO.\n\nCORO DIANTE DO TRONO DO CORDEIRO EXPRESSARÁ TODO AMOR; ALELUIA! HONRA E GLÓRIA AO NOSSO SALVADOR!', 'A NOIVA AMADA DO CORDEIRO ESTÁ SENDO PREPARADA PARA O ENCONTRO COM SEU NOIVO NA LINDA FESTA NO CÉU. CORO DIANTE DO TRONO DO CORDEIRO EXPRESSARÁ TODO AMOR; ALELUIA! HONRA E GLÓRIA AO NOSSO SALVADOR! SEGUE O CORDEIRO PARA ONDE ELE VAI, LÁ ESTÁ ELA, TÃO FORMOSA! PREPARADA JÁ ESTÁ. CANTARÁ UM CANTO NOVO E SÓ ELA SABERÁ POIS SOMENTE OS REMIDOS ESTE CANTO ENTOARÃO.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('192', '192 - VOU PRA JERUSALÉM', 'VOU PRA JERUSALÉM, A CIDADE DO MEU REI. ALI VEREI JESUS, MEU SALVADOR, O FILHO DE DEUS.\n\nVOU PRA JERUSALÉM. EM RUAS DE OURO SEI QUE ANDAREI, DA FONTE CRISTALINA, LÁ DESFRUTAREI E A GLÓRIA ETERNA DE DEUS ME ILUMINARÁ.\n\nPRA SEMPRE ESTAREI NA ETERNA MANSÃO, COM OS ANJOS, ADORANDO O SALVADOR QUE EM MEU LUGAR MORREU, MAS VIVO ELE É,\n\nSENTADO ESTÁ NO TRONO COM PODER E GLÓRIA, HONRA E LOUVOR, POR ISSO VOU PRA JERUSALÉM. VOU PRA JERUSALÉM. VOU PRA JERUSALÉM.', 'VOU PRA JERUSALÉM, A CIDADE DO MEU REI. ALI VEREI JESUS, MEU SALVADOR, O FILHO DE DEUS. VOU PRA JERUSALÉM. EM RUAS DE OURO SEI QUE ANDAREI, DA FONTE CRISTALINA, LÁ DESFRUTAREI E A GLÓRIA ETERNA DE DEUS ME ILUMINARÁ. PRA SEMPRE ESTAREI NA ETERNA MANSÃO, COM OS ANJOS, ADORANDO O SALVADOR QUE EM MEU LUGAR MORREU, MAS VIVO ELE É, SENTADO ESTÁ NO TRONO COM PODER E GLÓRIA, HONRA E LOUVOR, POR ISSO VOU PRA JERUSALÉM. VOU PRA JERUSALÉM. VOU PRA JERUSALÉM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
