INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('198', '198 - O DIA ESTÁ CHEGANDO', 'O DIA ESTÁ CHEGANDO, AS HORAS ESTÃO PASSANDO, O DIA JÁ VAI CHEGAR E JESUS VAI VOLTAR!\n\nO TEMPO SE APRESSA, O AMOR SE ESFRIANDO, PESSOAS NÃO SE FALANDO. MAS JESUS VAI VOLTAR!\n\nPOIS ASSIM SERÁ QUANDO JESUS VOLTAR. ASSIM SERÁ QUANDO JESUS VOLTAR. VAI VOLTAR, VAI VOLTAR! INSTRUMENTOS\n\nMENSAGENS REVELADAS, ESCRITAS POR JESUS, PARA NOS PREPARAR, PORQUE JESUS VAI VOLTAR. PREPARANDO OS CORAÇÕES PARA JESUS ENCONTRAR, JESUS, MEU BOM PASTOR, QUE ME FAZ VENCEDOR.\n\nESTAMOS PREPARANDO, ESTAMOS PREPARANDO OS CORAÇÕES, ESTAMOS PREPARANDO, PREPARANDO OS CORAÇÕES. PORQUE JESUS VAI VOLTAR, VAI VOLTAR, VAI VOLTAR! VAI VOLTAR!', 'O DIA ESTÁ CHEGANDO, AS HORAS ESTÃO PASSANDO, O DIA JÁ VAI CHEGAR E JESUS VAI VOLTAR! O TEMPO SE APRESSA, O AMOR SE ESFRIANDO, PESSOAS NÃO SE FALANDO. MAS JESUS VAI VOLTAR! POIS ASSIM SERÁ QUANDO JESUS VOLTAR. ASSIM SERÁ QUANDO JESUS VOLTAR. VAI VOLTAR, VAI VOLTAR! INSTRUMENTOS MENSAGENS REVELADAS, ESCRITAS POR JESUS, PARA NOS PREPARAR, PORQUE JESUS VAI VOLTAR. PREPARANDO OS CORAÇÕES PARA JESUS ENCONTRAR, JESUS, MEU BOM PASTOR, QUE ME FAZ VENCEDOR. ESTAMOS PREPARANDO, ESTAMOS PREPARANDO OS CORAÇÕES, ESTAMOS PREPARANDO, PREPARANDO OS CORAÇÕES. PORQUE JESUS VAI VOLTAR, VAI VOLTAR, VAI VOLTAR! VAI VOLTAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('199', '199 - AGUARDANDO A ORIENTAÇÃO DO SALVADOR', 'AGUARDANDO A ORIENTAÇÃO DO SALVADOR, COM JESUS MARCHAMOS, SEGUINDO SEM PARAR.\n\nA BANDEIRA DA VITÓRIA CONOSCO ESTÁ. OH! GLÓRIA, HOSANA AO REI! JESUS VENCENDO VEM!\n\nPELA FÉ AVISTAMOS NOSSO LAR, ANSIOSOS PARA O ENCONTRO COM JESUS. TODA LÁGRIMA ELE ENXUGARÁ. OH! GLÓRIA, HOSANA AO REI! JESUS VENCENDO VEM! INSTRUMENTOS\n\nCOM OS ANJOS CANTAREMOS LÁ NO CÉU LOUVORES AO CORDEIRO QUE VENCEU. A COROA ELE NOS DARÁ.\n\nOH! GLÓRIA! (GLÓRIA, GLÓRIA, GLÓRIA) JESUS VENCENDO VEM! GLÓRIA, JESUS VENCENDO VEM!', 'AGUARDANDO A ORIENTAÇÃO DO SALVADOR, COM JESUS MARCHAMOS, SEGUINDO SEM PARAR. A BANDEIRA DA VITÓRIA CONOSCO ESTÁ. OH! GLÓRIA, HOSANA AO REI! JESUS VENCENDO VEM! PELA FÉ AVISTAMOS NOSSO LAR, ANSIOSOS PARA O ENCONTRO COM JESUS. TODA LÁGRIMA ELE ENXUGARÁ. OH! GLÓRIA, HOSANA AO REI! JESUS VENCENDO VEM! INSTRUMENTOS COM OS ANJOS CANTAREMOS LÁ NO CÉU LOUVORES AO CORDEIRO QUE VENCEU. A COROA ELE NOS DARÁ. OH! GLÓRIA! (GLÓRIA, GLÓRIA, GLÓRIA) JESUS VENCENDO VEM! GLÓRIA, JESUS VENCENDO VEM!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('200', '200 - VOU PARA O MEU LAR', 'CORO VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR. VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR.\n\nESTÁ CHEGANDO A HORA DA PARTIDA, JESUS JÁ VEM ME BUSCAR. A TROMBETA VAI ECOAR E VOU PARA O MEU LAR.\n\nCORO VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR. VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR.\n\nMINHA CANDEIA ESTÁ BEM ACESA E ÓLEO NA BOTIJA TAMBÉM HÁ. A TROMBETA VAI ECOAR E VOU PARA O MEU LAR.\n\nCORO VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR. VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR.', 'CORO VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR. VOU PARA O MEU LAR, JESUS JÁ PREPAROU O MEU LUGAR. ESTÁ CHEGANDO A HORA DA PARTIDA, JESUS JÁ VEM ME BUSCAR. A TROMBETA VAI ECOAR E VOU PARA O MEU LAR. MINHA CANDEIA ESTÁ BEM ACESA E ÓLEO NA BOTIJA TAMBÉM HÁ. A TROMBETA VAI ECOAR E VOU PARA O MEU LAR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('201', '201 - SOBRE AS NUVENS JESUS VEM', 'SOBRE AS NUVENS JESUS VEM COMO REI E VENCEDOR, SUA IGREJA VEM BUSCAR CUMPRINDO SUA PROMESSA AO SEU POVO.\n\nQUE ALEGRIA VER NOSSO REI. MARANATA, CANTAREMOS COM FERVOR, POIS SEU BRADO É DE VITÓRIA, ALELUIA, HONRA E GLÓRIA LHE DAREMOS COM AMOR.\n\nCOMO IGREJA FIEL, TODOS UNIDOS A UMA VOZ, CANTAREMOS JESUS VEM VENCEDOR! JESUS VEM VENCEDOR! JESUS VEM VENCEDOR!', 'SOBRE AS NUVENS JESUS VEM COMO REI E VENCEDOR, SUA IGREJA VEM BUSCAR CUMPRINDO SUA PROMESSA AO SEU POVO. QUE ALEGRIA VER NOSSO REI. MARANATA, CANTAREMOS COM FERVOR, POIS SEU BRADO É DE VITÓRIA, ALELUIA, HONRA E GLÓRIA LHE DAREMOS COM AMOR. COMO IGREJA FIEL, TODOS UNIDOS A UMA VOZ, CANTAREMOS JESUS VEM VENCEDOR! JESUS VEM VENCEDOR! JESUS VEM VENCEDOR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('202', '202 - A RAABE VEIO A ORDEM', 'A RAABE VEIO A ORDEM: NA JANELA DE SUA CASA COLOCAR, UM CORDÃO DE FIO DE ESCARLATA PRA DA MORTE SE LIVRAR.\n\nELA CREU E OBEDECEU. VIU MUROS, CASAS ROLAREM, MAS SUA CASA NÃO CAIU FIO DE ESCARLATA A DEFENDEU.\n\nESTE MUNDO UM DIA PASSARÁ, A TERRA TREMERÁ, CASAS, PEDRAS VÃO ROLAR, MAS COM JESUS FIRME ESTAREI.\n\nSEU SANGUE É ESTE FIO E NELE VOU CONFIAR. ESTAMOS PRONTOS, MARANATA! ORA VEM, SENHOR JESUS! ORA VEM, SENHOR JESUS!\n\nESTAMOS PRONTOS, MARANATA! ORA VEM, SENHOR JESUS!', 'A RAABE VEIO A ORDEM: NA JANELA DE SUA CASA COLOCAR, UM CORDÃO DE FIO DE ESCARLATA PRA DA MORTE SE LIVRAR. ELA CREU E OBEDECEU. VIU MUROS, CASAS ROLAREM, MAS SUA CASA NÃO CAIU FIO DE ESCARLATA A DEFENDEU. ESTE MUNDO UM DIA PASSARÁ, A TERRA TREMERÁ, CASAS, PEDRAS VÃO ROLAR, MAS COM JESUS FIRME ESTAREI. SEU SANGUE É ESTE FIO E NELE VOU CONFIAR. ESTAMOS PRONTOS, MARANATA! ORA VEM, SENHOR JESUS! ORA VEM, SENHOR JESUS! ESTAMOS PRONTOS, MARANATA! ORA VEM, SENHOR JESUS!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('203', '203 - O FIO DE ESCARLATA O MISTÉRIO', 'O FIO DE ESCARLATA É O MISTÉRIO, DA FÉ E DA GRAÇA DO SENHOR. FOI REVELADO A NÓS E NOS LIGA À PRESENÇA DE DEUS, O SENHOR.\n\nCORO SEGUINDO O FIO DE ESCARLATA VEREMOS MARANATA. ORA VEM, SENHOR JESUS!\n\nA IGREJA TRIUNFANTE, ENTOANDO UM NOVO CANTO, SUBIRÁ PRA MORAR COM JESUS.\n\nESTE FIO DE ESCARLATA REPRESENTA O SANGUE DE JESUS: NOVA ALIANÇA, VÉU RASGADO, NOVO CAMINHO PARA O CÉU.\n\nCORO SEGUINDO O FIO DE ESCARLATA VEREMOS MARANATA. ORA VEM, SENHOR JESUS!\n\nFINAL: A IGREJA TRIUNFANTE, ENTOANDO UM NOVO CANTO, SUBIRÁ PRA MORAR COM JESUS.', 'O FIO DE ESCARLATA É O MISTÉRIO, DA FÉ E DA GRAÇA DO SENHOR. FOI REVELADO A NÓS E NOS LIGA À PRESENÇA DE DEUS, O SENHOR. CORO SEGUINDO O FIO DE ESCARLATA VEREMOS MARANATA. ORA VEM, SENHOR JESUS! A IGREJA TRIUNFANTE, ENTOANDO UM NOVO CANTO, SUBIRÁ PRA MORAR COM JESUS. ESTE FIO DE ESCARLATA REPRESENTA O SANGUE DE JESUS: NOVA ALIANÇA, VÉU RASGADO, NOVO CAMINHO PARA O CÉU.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('204', '204 - “MARCHAI! MARCHAI!”', 'CORO “MARCHAI! MARCHAI!” FIRME, SEGURA, CONFIANTE VAI, A IGREJA ADORNADA, PREPARADA, AO ENCONTRO DE SEU NOIVO VAI.\n\nEIS O MOMENTO! ESTAMOS ATENTOS! VIGILANTES, EM CONSTANTE ADORAÇÃO. JESUS NAS NUVENS DESCERÁ, SUA IGREJA LEVARÁ PARA A NOVA JERUSALÉM, ETERNO LAR.\n\nCORO “MARCHAI! MARCHAI!” FIRME, SEGURA, CONFIANTE VAI, A IGREJA ADORNADA, PREPARADA, AO ENCONTRO DE SEU NOIVO VAI.\n\nO FIO DE ESCARLATA NOS GARANTE AS MORADAS PREPARADAS POR JESUS. TUDO ESTÁ PRONTO! CANDEIA ACESA! SOMOS O POVO QUE O ESPÍRITO CONDUZ. INSTRUMENTOS\n\nCORO “MARCHAI! MARCHAI!” FIRME, SEGURA, CONFIANTE VAI, A IGREJA ADORNADA, PREPARADA, AO ENCONTRO DE SEU NOIVO VAI.', 'CORO MARCHAI! MARCHAI! FIRME, SEGURA, CONFIANTE VAI, A IGREJA ADORNADA, PREPARADA, AO ENCONTRO DE SEU NOIVO VAI. EIS O MOMENTO! ESTAMOS ATENTOS! VIGILANTES, EM CONSTANTE ADORAÇÃO. JESUS NAS NUVENS DESCERÁ, SUA IGREJA LEVARÁ PARA A NOVA JERUSALÉM, ETERNO LAR. O FIO DE ESCARLATA NOS GARANTE AS MORADAS PREPARADAS POR JESUS. TUDO ESTÁ PRONTO! CANDEIA ACESA! SOMOS O POVO QUE O ESPÍRITO CONDUZ. INSTRUMENTOS', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
