INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('207', '207 - JESUS PROMETEU NOS BUSCAR', 'JESUS PROMETEU NOS BUSCAR ARREBATAR SUA IGREJA FIEL COM ELE IREMOS MORAR HABITAR LÁ NO CÉU NAS RUAS DE OURO IREMOS ANDAR.\n\nCORO OH! QUANTO ANSEIO ESTAR ALI VER FACE A FACE O MEU REI ENFIM, VOU ADORAR JESUS.\n\nNÃO HÁ ALI TRISTEZA OU DOR, NEM MORTE, NEM PRANTO OU CLAMOR SÓ HAVERÁ SEU AMOR POIS CONOSCO ESTARÁ COM O CORDEIRO IREMOS AO LAR.\n\nCORO OH! QUANTO ANSEIO ESTAR ALI VER FACE A FACE O MEU REI ENFIM, VOU ADORAR JESUS.\n\nESSE DIA ESTÁ PRA CHEGAR A TROMBETA ESTÁ PRA SOAR QUEM VENCER TUDO HERDARÁ COM JESUS REINARÁ AO CORDEIRO IREMOS LOUVAR.\n\nCORO OH! QUANTO ANSEIO ESTAR ALI VER FACE A FACE O MEU REI ENFIM, VOU ADORAR JESUS. ENFIM, VOU CONTEMPLAR JESUS. ENFIM, VOU ABRAÇAR JESUS.', 'JESUS PROMETEU NOS BUSCAR ARREBATAR SUA IGREJA FIEL COM ELE IREMOS MORAR HABITAR LÁ NO CÉU NAS RUAS DE OURO IREMOS ANDAR. CORO OH! QUANTO ANSEIO ESTAR ALI VER FACE A FACE O MEU REI ENFIM, VOU ADORAR JESUS. NÃO HÁ ALI TRISTEZA OU DOR, NEM MORTE, NEM PRANTO OU CLAMOR SÓ HAVERÁ SEU AMOR POIS CONOSCO ESTARÁ COM O CORDEIRO IREMOS AO LAR. ESSE DIA ESTÁ PRA CHEGAR A TROMBETA ESTÁ PRA SOAR QUEM VENCER TUDO HERDARÁ COM JESUS REINARÁ AO CORDEIRO IREMOS LOUVAR. CORO OH! QUANTO ANSEIO ESTAR ALI VER FACE A FACE O MEU REI ENFIM, VOU ADORAR JESUS. ENFIM, VOU CONTEMPLAR JESUS. ENFIM, VOU ABRAÇAR JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('208', '208 - EU QUERO IR PARA O CÉU', 'EU QUERO IR PARA O CÉU VER O MEU SALVADOR. QUE ME RESGATOU, SEU SANGUE ME COMPROU\n\nEU QUERO IR PARA O CÉU VIVER COM MEU JESUS ANDAR NA SUA LUZ, PASSEAR NO SEU JARDIM.\n\nEU QUERO IR PARA O CÉU NAS RUAS DE OURO ANDAR VER AS BELAS MANSÕES, SUA GLÓRIA CONTEMPLAR INSTRUMENTOS\n\nEU QUERO IR PARA O CÉU MORAR COM MEU SENHOR DA ÁRVORE DA VIDA EU QUERO DESFRUTAR,\n\nADORAR O MEU JESUS, COM OS ANJOS VOU CANTAR ADORAR O MEU JESUS, COM OS ANJOS VOU CANTAR', 'EU QUERO IR PARA O CÉU VER O MEU SALVADOR. QUE ME RESGATOU, SEU SANGUE ME COMPROU EU QUERO IR PARA O CÉU VIVER COM MEU JESUS ANDAR NA SUA LUZ, PASSEAR NO SEU JARDIM. EU QUERO IR PARA O CÉU NAS RUAS DE OURO ANDAR VER AS BELAS MANSÕES, SUA GLÓRIA CONTEMPLAR INSTRUMENTOS EU QUERO IR PARA O CÉU MORAR COM MEU SENHOR DA ÁRVORE DA VIDA EU QUERO DESFRUTAR, ADORAR O MEU JESUS, COM OS ANJOS VOU CANTAR ADORAR O MEU JESUS, COM OS ANJOS VOU CANTAR', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('209', '209 - SE EU FALO', 'SE EU FALO DO GRANDE AMOR DO PAI GRANDES BÊNÇÃOS PODEREI CONTAR. SE EU CANTO AS MARAVILHAS QUE ELE FAZ, O LOUVAREI ETERNAMENTE.\n\nGRANDE É O AMOR DO PAI, MARAVILHAS ELE FAZ QUANDO A IGREJA CANTA E ORA SEM CESSAR. SANTO, JUSTO E FIEL, SUA PROMESSA CUMPRIRÁ BREVE VEM NOS BUSCAR!\n\nMARANATA, JESUS VEM! MARANATA, JESUS VEM! BREVE VEM NOS BUSCAR! MARANATA, JESUS VEM! MARANATA, JESUS VEM! BREVE VEM NOS BUSCAR! BREVE VEM NOS BUSCAR! BREVE VEM NOS BUSCAR!', 'SE EU FALO DO GRANDE AMOR DO PAI GRANDES BÊNÇÃOS PODEREI CONTAR. SE EU CANTO AS MARAVILHAS QUE ELE FAZ, O LOUVAREI ETERNAMENTE. GRANDE É O AMOR DO PAI, MARAVILHAS ELE FAZ QUANDO A IGREJA CANTA E ORA SEM CESSAR. SANTO, JUSTO E FIEL, SUA PROMESSA CUMPRIRÁ BREVE VEM NOS BUSCAR! MARANATA, JESUS VEM! MARANATA, JESUS VEM! BREVE VEM NOS BUSCAR! MARANATA, JESUS VEM! MARANATA, JESUS VEM! BREVE VEM NOS BUSCAR! BREVE VEM NOS BUSCAR! BREVE VEM NOS BUSCAR!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('210', '210 - JESUS VOLTARÁ PRA ME BUSCAR! (MARANATA)', 'JESUS VOLTARÁ PRA ME BUSCAR! O MEU CORAÇÃO ANSEIA POR ESSSE ENCONTRO TÃO ESPECIAL, ONDE TODA LÁGRIMA ELE VAI ENXUGAR.\n\nA MINHA CANDEIA ESTÁ ACESA E O ÓLEO NA BOTIJA TAMBÉM HÁ. POR ISSO EU CLAMO. POR ISSO EU CLAMO.\n\nMARANATA! VEM, SENHOR JESUS! MARANATA! VEM, SENHOR JESUS! A TUA IGREJA TE ESPERA, A TUA IGREJA PROCLAMA: VEM, SIM, VEM JESUS.\n\nCORO MARANATA É O GRITO DA IGREJA, MARANATA É UMA FORMA DE VIDA, MARANATA É O DESPERTAR DO SONO, É A ESPERANÇA DA ALMA REMIDA. INSTRUMENTOS POR ISSO EU CLAMO. POR ISSO EU CLAMO.\nBIS\n\nCORO MARANATA É O GRITO DA IGREJA, MARANATA É UMA FORMA DE VIDA, MARANATA É O DESPERTAR DO SONO, É A ESPERANÇA DA ALMA REMIDA.\n\nELE VEM! MARANATA! JESUS VEM! MARANATA! ELE VEM! MARANATA! JESUS VEM! MARANATA!', 'JESUS VOLTARÁ PRA ME BUSCAR! O MEU CORAÇÃO ANSEIA POR ESSSE ENCONTRO TÃO ESPECIAL, ONDE TODA LÁGRIMA ELE VAI ENXUGAR. A MINHA CANDEIA ESTÁ ACESA E O ÓLEO NA BOTIJA TAMBÉM HÁ. POR ISSO EU CLAMO. POR ISSO EU CLAMO. MARANATA! VEM, SENHOR JESUS! MARANATA! VEM, SENHOR JESUS! A TUA IGREJA TE ESPERA, A TUA IGREJA PROCLAMA: VEM, SIM, VEM JESUS. CORO MARANATA É O GRITO DA IGREJA, MARANATA É UMA FORMA DE VIDA, MARANATA É O DESPERTAR DO SONO, É A ESPERANÇA DA ALMA REMIDA. INSTRUMENTOS POR ISSO EU CLAMO. POR ISSO EU CLAMO. CORO MARANATA É O GRITO DA IGREJA, MARANATA É UMA FORMA DE VIDA, MARANATA É O DESPERTAR DO SONO, É A ESPERANÇA DA ALMA REMIDA. ELE VEM! MARANATA! JESUS VEM! MARANATA! ELE VEM! MARANATA! JESUS VEM! MARANATA!', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('211', '211 - OS DIAS ESTÃO PASSANDO', 'OS DIAS ESTÃO PASSANDO, O MOMENTO SE APROXIMA DO ENCONTRO TRIUNFAL. A TROMBETA VAI TOCAR.\n\nA PALAVRA ME DIRIGE, APONTA O CAMINHO, OLHO PARA O ALTO E VEJO A REDENÇÃO.\n\nCORO EU IREI CONTEMPLAR OS CÉUS ABERTOS, OUVIREI JESUS CHAMAR PELO MEU NOME, EU TEREI O MEU CORPO TRANSFORMADO, PARA ENCONTRAR O SALVADOR.\n\nEU IREI ANDAR NAS RUAS DE OURO, EU IREI CANTAR NO LINDO CORAL, EU IREI VER A ÁRVORE DA VIDA, ESTAREI EM MEU NOVO LAR (ESTAREI EM JERUSALÉM). INSTRUMENTOS\n\nQUERO ESTAR CONTIGO, MEU SENHOR, (VEM, MEU SENHOR) VEM ME BUSCAR! TIRA TODA DOR E O MEU SOFRER, TRANSFORMA O MEU CHORO EM ALEGRIA.\n\nCORO EU IREI CONTEMPLAR OS CÉUS ABERTOS, OUVIREI JESUS CHAMAR PELO MEU NOME, EU TEREI O MEU CORPO TRANSFORMADO, PARA ENCONTRAR O SALVADOR.\n\nEU IREI ANDAR NAS RUAS DE OURO, EU IREI CANTAR NO LINDO CORAL, EU IREI VER A ÁRVORE DA VIDA, ESTAREI EM MEU NOVO LAR (ESTAREI EM JERUSALÉM).\n\nCORO EU IREI CONTEMPLAR OS CÉUS ABERTOS, OUVIREI JESUS CHAMAR PELO MEU NOME, EU TEREI O MEU CORPO TRANSFORMADO, PARA ENCONTRAR O SALVADOR.\n\nEU IREI ANDAR NAS RUAS DE OURO, EU IREI CANTAR NO LINDO CORAL, EU IREI VER A ÁRVORE DA VIDA, ESTAREI EM MEU NOVO LAR (ESTAREI EM JERUSALÉM).', 'OS DIAS ESTÃO PASSANDO, O MOMENTO SE APROXIMA DO ENCONTRO TRIUNFAL. A TROMBETA VAI TOCAR. A PALAVRA ME DIRIGE, APONTA O CAMINHO, OLHO PARA O ALTO E VEJO A REDENÇÃO. CORO EU IREI CONTEMPLAR OS CÉUS ABERTOS, OUVIREI JESUS CHAMAR PELO MEU NOME, EU TEREI O MEU CORPO TRANSFORMADO, PARA ENCONTRAR O SALVADOR. EU IREI ANDAR NAS RUAS DE OURO, EU IREI CANTAR NO LINDO CORAL, EU IREI VER A ÁRVORE DA VIDA, ESTAREI EM MEU NOVO LAR (ESTAREI EM JERUSALÉM). INSTRUMENTOS QUERO ESTAR CONTIGO, MEU SENHOR, (VEM, MEU SENHOR) VEM ME BUSCAR! TIRA TODA DOR E O MEU SOFRER, TRANSFORMA O MEU CHORO EM ALEGRIA. EU IREI ANDAR NAS RUAS DE OURO, EU IREI CANTAR NO LINDO CORAL, EU IREI VER A ÁRVORE DA VIDA, ES