INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('214', '214 - NÓS RECEBEREMOS LÁ NO CÉU', 'NÓS RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU.\n\nUM NOME NOVO, UM NOME NOVO, NÓS TEREMOS JÁ; UM NOME NOVO, UM NOME NOVO, QUANDO ENTRARMOS LÁ.\n\nCORO SIM, RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU.\n\nNA PEDRINHA BRANCA, EU SÓ LEREI ESSE NOME NOVO DE JESUS; BRANCA MAIS QUE A NEVE A GUARDAREI NO REINO DE LUZ.\n\nPEDRINHA BRANCA, PEDRINHA BRANCA NÓS TEREMOS JÁ; PEDRINHA BRANCA, PEDRINHA BRANCA QUANDO ENTRARMOS LÁ.\n\nCORO SIM, RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU.\n\nLARGA O MUNDO, CRÊ EM CRISTO E VEM! O MANÁ ESCONDIDO É PARA TI; SERVE, POIS, A DEUS, TENS TODO O BEM! CRISTO É TUDO ALI.\n\nMANÁ ESCONDIDO, MANÁ ESCONDIDO NÓS TEREMOS JÁ; MANÁ ESCONDIDO, MANÁ ESCONDIDO, QUANDO ENTRARMOS LÁ.\n\nCORO SIM, RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU.', 'NÓS RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU. UM NOME NOVO, UM NOME NOVO, NÓS TEREMOS JÁ; UM NOME NOVO, UM NOME NOVO, QUANDO ENTRARMOS LÁ. CORO SIM, RECEBEREMOS LÁ NO CÉU, LÁ NO CÉU, O LINDO, LINDO CÉU, OUTRO NOME NOVO ALÉM DO VÉU, NESSE LINDO CÉU. NA PEDRINHA BRANCA, EU SÓ LEREI ESSE NOME NOVO DE JESUS; BRANCA MAIS QUE A NEVE A GUARDAREI NO REINO DE LUZ. PEDRINHA BRANCA, PEDRINHA BRANCA NÓS TEREMOS JÁ; PEDRINHA BRANCA, PEDRINHA BRANCA QUANDO ENTRARMOS LÁ. LARGA O MUNDO, CRÊ EM CRISTO E VEM! O MANÁ ESCONDIDO É PARA TI; SERVE, POIS, A DEUS, TENS TODO O BEM! CRISTO É TUDO ALI. MANÁ ESCONDIDO, MANÁ ESCONDIDO NÓS TEREMOS JÁ; MANÁ ESCONDIDO, MANÁ ESCONDIDO, QUANDO ENTRARMOS LÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('215', '215 - EIS QUE JESUS VEM NAS NUVENS', 'EIS QUE JESUS VEM NAS NUVENS BUSCAR SUAS JOIAS, SUAS JOIAS PRECIOSAS DE MUITO VALOR.\n\nCORO COMO ESTRELAS DA AURORA BRILHANDO NA FRONTE DE JESUS, LÁ NA GLÓRIA, ADORNO SERÃO.\n\nVEM BUSCÁ-LAS, VAI LEVÁ-LAS AO REINO CELESTE, SUAS JOIAS RESPLENDENTES DE MUITO VALOR.\n\nCORO COMO ESTRELAS DA AURORA BRILHANDO NA FRONTE DE JESUS, LÁ NA GLÓRIA, ADORNO SERÃO.\n\nOS MENINOS E AS MENINAS QUE SERVEM A JESUS, SÃO-LHES JOIAS, RICAS JOIAS DE MUITO VALOR.\n\nCORO COMO ESTRELAS DA AURORA BRILHANDO NA FRONTE DE JESUS, LÁ NA GLÓRIA, ADORNO SERÃO.', 'EIS QUE JESUS VEM NAS NUVENS BUSCAR SUAS JOIAS, SUAS JOIAS PRECIOSAS DE MUITO VALOR. CORO COMO ESTRELAS DA AURORA BRILHANDO NA FRONTE DE JESUS, LÁ NA GLÓRIA, ADORNO SERÃO. VEM BUSCÁ-LAS, VAI LEVÁ-LAS AO REINO CELESTE, SUAS JOIAS RESPLENDENTES DE MUITO VALOR. OS MENINOS E AS MENINAS QUE SERVEM A JESUS, SÃO-LHES JOIAS, RICAS JOIAS DE MUITO VALOR.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('216', '216 - SOBRE AS NUVENS DESCERÁ (ELE VIRÁ)', 'SOBRE AS NUVENS DESCERÁ NOSSO REI E SALVADOR; COM PODER E GRANDE GLÓRIA AO SOAR DA TROMBETA ELE VIRÁ.\n\nCORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL.\n\nTUDO AQUI HÁ DE FINDAR, TUDO NOVO ELE FARÁ! QUEM VENCER TUDO HERDARÁ E NO CÉU COM JESUS VAMOS MORAR.\n\nCORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL.\n\nA NOVA JERUSALÉM O CORDEIRO ILUMINARÁ, RIO PURO HAVERÁ E A ÁRVORE DA VIDA ALI ESTARÁ.\n\nCORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL.\n\nCORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL. ELE VIRÁ PARA BUSCAR A IGREJA FIEL.', 'SOBRE AS NUVENS DESCERÁ NOSSO REI E SALVADOR; COM PODER E GRANDE GLÓRIA AO SOAR DA TROMBETA ELE VIRÁ. CORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL. TUDO AQUI HÁ DE FINDAR, TUDO NOVO ELE FARÁ! QUEM VENCER TUDO HERDARÁ E NO CÉU COM JESUS VAMOS MORAR. A NOVA JERUSALÉM O CORDEIRO ILUMINARÁ, RIO PURO HAVERÁ E A ÁRVORE DA VIDA ALI ESTARÁ. CORO ELE VIRÁ (ARREBATAR) ELE VIRÁ (NÃO TARDARÁ) ELE VIRÁ PARA BUSCAR A IGREJA FIEL. ELE VIRÁ PARA BUSCAR A IGREJA FIEL.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('217', '217 - VEREI MEU GRANDE REI', 'VEREI MEU GRANDE REI, UM VENCEDOR SEREI, VEREI O MEU JESUS! NOVA JERUSALÉM, CIDADE DO MEU REI, ESTAREI COM MEU JESUS!\n\nCORO OH! QUE GLÓRIA! SERÁ O ENCONTRO TRIUNFAL! ALELUIA! VEREI OS CÉUS SE ABRIREM. E COM OS ANJOS A CANTAR, NA CIDADE VOU ENTRAR. “ORA VEM, VEM SENHOR JESUS!”\n\nELE MORREU NA CRUZ, PORÉM RESSUSCITOU E VIVO HOJE ESTÁ. QUANDO ENCONTRAR JESUS, VEREI A SUA LUZ, OH! QUE GLÓRIA ETERNAL!\n\nCORO OH! QUE GLÓRIA! SERÁ O ENCONTRO TRIUNFAL! ALELUIA! VEREI OS CÉUS SE ABRIREM. E COM OS ANJOS A CANTAR, NA CIDADE VOU ENTRAR. “ORA VEM, VEM SENHOR JESUS!” VEM SENHOR JESUS.', 'VEREI MEU GRANDE REI, UM VENCEDOR SEREI, VEREI O MEU JESUS! NOVA JERUSALÉM, CIDADE DO MEU REI, ESTAREI COM MEU JESUS! CORO OH! QUE GLÓRIA! SERÁ O ENCONTRO TRIUNFAL! ALELUIA! VEREI OS CÉUS SE ABRIREM. E COM OS ANJOS A CANTAR, NA CIDADE VOU ENTRAR. ORA VEM, VEM SENHOR JESUS! ELE MORREU NA CRUZ, PORÉM RESSUSCITOU E VIVO HOJE ESTÁ. QUANDO ENCONTRAR JESUS, VEREI A SUA LUZ, OH! QUE GLÓRIA ETERNAL! CORO OH! QUE GLÓRIA! SERÁ O ENCONTRO TRIUNFAL! ALELUIA! VEREI OS CÉUS SE ABRIREM. E COM OS ANJOS A CANTAR, NA CIDADE VOU ENTRAR. ORA VEM, VEM SENHOR JESUS! VEM SENHOR JESUS.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('218', '218 - MARANATA', 'CORO MARANATA, MARANATA, JESUS BREVE VIRÁ!\n\nEU VOU SUBIR, POIS A TROMBETA ESCUTAREI; E LÁ NAS NUVENS COM JESUS ME ENCONTRAREI. DESPERTA IGREJA, VAMOS SUBIR! O TEU LUGAR NÃO É AQUI, PORQUE EM BREVE JESUS VIRÁ.\n\nCORO MARANATA, MARANATA, JESUS BREVE VIRÁ!\n\nEU VOU SUBIR, POIS A TROMBETA ESCUTAREI; E LÁ NAS NUVENS COM JESUS ME ENCONTRAREI. DESPERTA IGREJA, VAMOS SUBIR! O TEU LUGAR NÃO É AQUI, PORQUE EM BREVE JESUS VIRÁ.', 'CORO MARANATA, MARANATA, JESUS BREVE VIRÁ! EU VOU SUBIR, POIS A TROMBETA ESCUTAREI; E LÁ NAS NUVENS COM JESUS ME ENCONTRAREI. DESPERTA IGREJA, VAMOS SUBIR! O TEU LUGAR NÃO É AQUI, PORQUE EM BREVE JESUS VIRÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('219', '219 - ELE VIRÁ NO MEIO DA NOITE', 'ELE VIRÁ NO MEIO DA NOITE, O NOIVO VEM BUSCAR A NOIVA AMADA, ELE VEM! PRA IR À NOVA JERUSALÉM, RESGATARÁ O POVO QUE ANDA NA LUZ.\n\nOS DIAS SÃO DE TREVAS, EU SEI, MAS PARA A IGREJA BRILHA A GLÓRIA DE CRISTO JESUS! ENCHE TUA CANDEIA, DEIXA BRILHAR, DEIXA O ESPÍRITO SANTO BRILHAR!\n\nCORO BRILHA EM NÓS O ESPÍRITO SANTO DE DEUS, NOS LEVANDO A ENCONTRAR COM JESUS!\n\nNAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! INSTRUMENTOS\n\nELE VIRÁ NO MEIO DA NOITE, O NOIVO VEM BUSCAR A NOIVA AMADA, ELE VEM! PRA IR À NOVA JERUSALÉM, RESGATARÁ O POVO QUE ANDA NA LUZ.\n\nOS DIAS SÃO DE TREVAS, EU SEI, MAS PARA A IGREJA BRILHA A GLÓRIA DE CRISTO JESUS! ENCHE TUA CANDEIA, DEIXA BRILHAR, DEIXA O ESPÍRITO SANTO BRILHAR!\n\nCORO BRILHA EM NÓS O ESPÍRITO SANTO DE DEUS, NOS LEVANDO A ENCONTRAR COM JESUS!\n\nNAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR!\n\nCORO BRILHA EM NÓS O ESPÍRITO SANTO DE DEUS, NOS LEVANDO A ENCONTRAR COM JESUS!\n\nNAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR!', 'ELE VIRÁ NO MEIO DA NOITE, O NOIVO VEM BUSCAR A NOIVA AMADA, ELE VEM! PRA IR À NOVA JERUSALÉM, RESGATARÁ O POVO QUE ANDA NA LUZ. OS DIAS SÃO DE TREVAS, EU SEI, MAS PARA A IGREJA BRILHA A GLÓRIA DE CRISTO JESUS! ENCHE TUA CANDEIA, DEIXA BRILHAR, DEIXA O ESPÍRITO SANTO BRILHAR! CORO BRILHA EM NÓS O ESPÍRITO SANTO DE DEUS, NOS LEVANDO A ENCONTRAR COM JESUS! NAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! INSTRUMENTOS NAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! NAQUELE GRANDE DIA SUBIRÁ QUEM ESTIVER NA LUZ, ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR! ENCHE A CANDEIA E BRILHA O FOGO DO SENHOR!', 2, CURRENT_TIMESTAMP, 