INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('220', '220 - ONDE VIVE O REI DA GLÓRIA?', 'ONDE VIVE O REI DA GLÓRIA? VIVE EM MEU CORAÇÃO. ONDE REINA O REI DA GLÓRIA? REINA EM MEU CORAÇÃO.\n\nELE FALA, ELE GUIA NOSSOS PASSOS A JERUSALÉM, PARA JUNTOS HABITARMOS COM JESUS, NOSSO REI.\n\nO NOSSO REI DA GLÓRIA OPERA MARAVILHAS NO MEIO DA IGREJA, LIBERTA CORAÇÕES;\n\nE HOJE QUER TRANSFORMAR A SUA VIDA, VENHA AGORA E SINTA O PODER DO SALVADOR, ALELUIA! OH, GLÓRIA! LOUVEMOS AO SENHOR.\n\nONDE VIVE E REINA O REI DA GLÓRIA? VIVE, REINA EM NOSSOS CORAÇÕES.', 'ONDE VIVE O REI DA GLÓRIA? VIVE EM MEU CORAÇÃO. ONDE REINA O REI DA GLÓRIA? REINA EM MEU CORAÇÃO. ELE FALA, ELE GUIA NOSSOS PASSOS A JERUSALÉM, PARA JUNTOS HABITARMOS COM JESUS, NOSSO REI. O NOSSO REI DA GLÓRIA OPERA MARAVILHAS NO MEIO DA IGREJA, LIBERTA CORAÇÕES; E HOJE QUER TRANSFORMAR A SUA VIDA, VENHA AGORA E SINTA O PODER DO SALVADOR, ALELUIA! OH, GLÓRIA! LOUVEMOS AO SENHOR. ONDE VIVE E REINA O REI DA GLÓRIA? VIVE, REINA EM NOSSOS CORAÇÕES.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
