INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('223', '223 - PAI, TUA BELEZA ME ADMIRA', 'PAI, TUA BELEZA ME ADMIRA, NO UNIVERSO QUE CRIASTE EU POSSO VER COMO TU ÉS COMO TU ÉS;\n\nPAI, ATÉ NAS MAIS PEQUENAS COISAS EXISTE TANTA HARMONIA, PERFEITA É A OBRA DAS TUAS MÃOS.\n\nCORO COM TEU PODER FUNDASTE A TERRA, LEVANTASTE MONTES, SEPARASTE MARES; NO CÉU PUSESTE O SOL E A LUA, SOMENTE AO TEU MANDAR A VIDA SE FORMOU.\n\nE DO BARRO TU FIZESTE O HOMEM À TUA IMAGEM E SEMELHANÇA PARA ESTAR PERANTE TI E TE LOUVAR;\n\nPAI, OUVE ESTA MINHA ADORAÇÃO, É A MAIS SINCERA EXPRESSÃO DO AMOR QUE HÁ DENTRO DE MIM. INSTRUMENTOS\n\nCORO COM TEU PODER FUNDASTE A TERRA, LEVANTASTE MONTES, SEPARASTE MARES; NO CÉU PUSESTE O SOL E A LUA, SOMENTE AO TEU MANDAR A VIDA SE FORMOU.\n\nE DO BARRO TU FIZESTE O HOMEM À TUA IMAGEM E SEMELHANÇA PARA ESTAR PERANTE TI E TE LOUVAR;\n\nPAI, OUVE ESTA MINHA ADORAÇÃO, É A MAIS SINCERA EXPRESSÃO DO AMOR QUE HÁ DENTRO DE MIM.', 'PAI, TUA BELEZA ME ADMIRA, NO UNIVERSO QUE CRIASTE EU POSSO VER COMO TU ÉS COMO TU ÉS; PAI, ATÉ NAS MAIS PEQUENAS COISAS EXISTE TANTA HARMONIA, PERFEITA É A OBRA DAS TUAS MÃOS. CORO COM TEU PODER FUNDASTE A TERRA, LEVANTASTE MONTES, SEPARASTE MARES; NO CÉU PUSESTE O SOL E A LUA, SOMENTE AO TEU MANDAR A VIDA SE FORMOU. E DO BARRO TU FIZESTE O HOMEM À TUA IMAGEM E SEMELHANÇA PARA ESTAR PERANTE TI E TE LOUVAR; PAI, OUVE ESTA MINHA ADORAÇÃO, É A MAIS SINCERA EXPRESSÃO DO AMOR QUE HÁ DENTRO DE MIM. INSTRUMENTOS PAI, OUVE ESTA MINHA ADORAÇÃO, É A MAIS SINCERA EXPRESSÃO DO AMOR QUE HÁ DENTRO DE MIM.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('224', '224 - DEUS CRIOU OS CÉUS E A TERRA', 'DEUS CRIOU OS CÉUS E A TERRA, O UNIVERSO, TUDO ELE FEZ; A TERRA ERA VAZIA, COBERTA DE TREVAS, SEMELHANTE AO CORAÇÃO DO HOMEM SEM DEUS.\n\nELE TROUXE A LUZ, SEPAROU AS ÁGUAS, CRIOU A TERRA FIRME, AS ERVAS FEZ BROTAR, ILUMINOU OS CÉUS COM O BRILHO DO SOL, LUA E ESTRELAS,\n\nENCHEU O MAR DE VIDA, CRIOU OS ANIMAIS, PREPAROU TUDO TÃO BELO PARA DEPOIS O HOMEM CRIAR.\n\nCORO DO NADA ELE FEZ TUDO, COM SUA PALAVRA OS CRIOU, ELE É O DEUS DA VIDA E TUDO ESTÁ EM SUAS MÃOS;\n\nÉ ELE QUEM MOVE OS CORAÇÕES PARA CONHECERMOS O SEU AMOR, NOS REVELANDO JESUS, NOSSO SALVADOR.\n\nESTA TERRA UM DIA PASSARÁ, NÃO MAIS EXISTIRÁ, O NOVO CÉU E A NOVA TERRA JESUS NOS DARÁ.\n\nCORO DO NADA ELE FEZ TUDO, COM SUA PALAVRA OS CRIOU, ELE É O DEUS DA VIDA E TUDO ESTÁ EM SUAS MÃOS;\n\nÉ ELE QUEM MOVE OS CORAÇÕES PARA CONHECERMOS O SEU AMOR, NOS REVELANDO JESUS, NOSSO SALVADOR.', 'DEUS CRIOU OS CÉUS E A TERRA, O UNIVERSO, TUDO ELE FEZ; A TERRA ERA VAZIA, COBERTA DE TREVAS, SEMELHANTE AO CORAÇÃO DO HOMEM SEM DEUS. ELE TROUXE A LUZ, SEPAROU AS ÁGUAS, CRIOU A TERRA FIRME, AS ERVAS FEZ BROTAR, ILUMINOU OS CÉUS COM O BRILHO DO SOL, LUA E ESTRELAS, ENCHEU O MAR DE VIDA, CRIOU OS ANIMAIS, PREPAROU TUDO TÃO BELO PARA DEPOIS O HOMEM CRIAR. CORO DO NADA ELE FEZ TUDO, COM SUA PALAVRA OS CRIOU, ELE É O DEUS DA VIDA E TUDO ESTÁ EM SUAS MÃOS; É ELE QUEM MOVE OS CORAÇÕES PARA CONHECERMOS O SEU AMOR, NOS REVELANDO JESUS, NOSSO SALVADOR. ESTA TERRA UM DIA PASSARÁ, NÃO MAIS EXISTIRÁ, O NOVO CÉU E A NOVA TERRA JESUS NOS DARÁ.', 2, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
