INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('02', '02AV - PREPARAI-VOS, Ó IGREJA AMADA', 'PREPARAI-VOS, Ó IGREJA AMADA, POIS O NOIVO EM BREVE VIRÁ! Ó NOIVA AMADA, ATAVIADA, PREPARADA PARA O NOIVO ESTÁ!\n\nCOM ALEGRIA, A PROCLAMAR: OH! JESUS, MEU SENHOR, VEM NOS BUSCAR! A NOIVA PRONTA E PREPARADA ANSEIA ESTAR LOGO EM SEU NOVO LAR!\n\nCANDEIA ACESA E REVESTIDA, AGUARDA PRONTA O NOIVO A VIR BUSCAR! COM FÉ SABEMOS E VIGIAMOS, ANSIANDO ESTE DIA CHEGAR!\n\nCOM ALEGRIA A PROCLAMAR: OH! JESUS, MEU SENHOR, VEM NOS BUSCAR! A NOIVA PRONTA E PREPARADA ANSEIA ESTAR LOGO EM SEU NOVO LAR!', 'PREPARAI-VOS, Ó IGREJA AMADA, POIS O NOIVO EM BREVE VIRÁ! Ó NOIVA AMADA, ATAVIADA, PREPARADA PARA O NOIVO ESTÁ! COM ALEGRIA, A PROCLAMAR: OH! JESUS, MEU SENHOR, VEM NOS BUSCAR! A NOIVA PRONTA E PREPARADA ANSEIA ESTAR LOGO EM SEU NOVO LAR! CANDEIA ACESA E REVESTIDA, AGUARDA PRONTA O NOIVO A VIR BUSCAR! COM FÉ SABEMOS E VIGIAMOS, ANSIANDO ESTE DIA CHEGAR! COM ALEGRIA A PROCLAMAR: OH! JESUS, MEU SENHOR, VEM NOS BUSCAR! A NOIVA PRONTA E PREPARADA ANSEIA ESTAR LOGO EM SEU NOVO LAR!',  4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('03', '03AV- JESUS VOLTARÁ', 'JESUS VOLTARÁ, VOU ME PREPARAR, QUE DIA GLORIOSO QUANDO ELE VOLTAR!\n\nJESUS VOLTARÁ, NA GLÓRIA ESTAREI, COM OS ANJOS CANTANDO A JESUS LOUVAREI!\n\nJESUS REUNIDO COM SUA IGREJA FIEL, SERÁ UMA FESTA COM TODOS NO CÉU!\n\nJESUS VOLTARÁ, NA GLÓRIA ESTAREI, COM OS ANJOS CANTANDO A JESUS LOUVAREI! (3x)', 'JESUS VOLTARÁ, VOU ME PREPARAR, QUE DIA GLORIOSO QUANDO ELE VOLTAR! JESUS VOLTARÁ, NA GLÓRIA ESTAREI, COM OS ANJOS CANTANDO A JESUS LOUVAREI! JESUS REUNIDO COM SUA IGREJA FIEL, SERÁ UMA FESTA COM TODOS NO CÉU!',  4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('04', '04AV- AS PARÁBOLAS DO REINO', 'SAINDO DE CASA NAQUELE DIA, ASSENTADO JUNTO AO MAR, JESUS FOI EXPLICAR\n\nÀQUELA MULTIDĀO E A TODOS QUE O SEGUIAM, SOBRE AS PARÁBOLAS DE UM REINO QUE VIRIA.\n\nSETE SÃO AS PARÁBOLAS DO REINO: SEMEADOR, TRIGO E JOIO,\n\nGRÃO DE MOSTARDA, FERMENTO,\n\nTESOURO ESCONDIDO, PÉROLA E REDE.\n\nVAMOS VIGIAR E ORAR, JESUS BREVE VIRÁ; IREMOS TODOS COM ELE MORAR. MARANATA, JESUS VEM!', 'SAINDO DE CASA NAQUELE DIA, ASSENTADO JUNTO AO MAR, JESUS FOI EXPLICAR ÀQUELA MULTIDĀO E A TODOS QUE O SEGUIAM, SOBRE AS PARÁBOLAS DE UM REINO QUE VIRIA. SETE SÃO AS PARÁBOLAS DO REINO: SEMEADOR, TRIGO E JOIO, GRÃO DE MOSTARDA, FERMENTO, TESOURO ESCONDIDO, PÉROLA E REDE. VAMOS VIGIAR E ORAR, JESUS BREVE VIRÁ; IREMOS TODOS COM ELE MORAR. MARANATA, JESUS VEM!',  4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('05', '05AV- EU CLAMO, EU ORO', 'EU CLAMO, EU ORO PELO SANGUE LÁ DA CRUZ, EU CLAMO, EU ORO PELO ESPÍRITO QUE RELUZ.\n\nQUANDO EU OLHO PARA A CRUZ, SEI QUE A VITÓRIA CHEGARÁ, POIS POR MIM MORREU JESUS QUE NÃO ME ABANDONA NEM ME ABANDONARÁ.\n\nEU CLAMO, EU ORO PELO SANGUE LÁ DA CRUZ, EU CLAMO, EU ORO PELO ESPÍRITO QUE RELUZ.', 'EU CLAMO, EU ORO PELO SANGUE LÁ DA CRUZ, EU CLAMO, EU ORO PELO ESPÍRITO QUE RELUZ. QUANDO EU OLHO PARA A CRUZ, SEI QUE A VITÓRIA CHEGARÁ, POIS POR MIM MORREU JESUS QUE NÃO ME ABANDONA NEM ME ABANDONARÁ.',  4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
