INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('07', '07AV- ESTÁ CHEGANDO A HORA', 'ESTÁ CHEGANDO A HORA, O TREM JÁ VAI PARTIR LEVANDO OS CORDEIRINHOS QUE VÃO AO CÉU SUBIR.\n\nJESUS ESTÁ CHAMANDO, A TODOS CONVIDANDO: “VENHAM SEM DEMORA PRA NOVA JERUSALÉM”.\n\nVOCÊ, Ó PEQUENINO, POR QUE NÃO VEM TAMBÉM? RECEBA O SEU BILHETE E ENTRE NESSE TREM.\n\nE ALI JUNTOS ESTAREMOS NA CELESTIAL CIDADE, COM CRISTO ANDAREMOS, SERÁ SÓ FELICIDADE.', 'ESTÁ CHEGANDO A HORA, O TREM JÁ VAI PARTIR LEVANDO OS CORDEIRINHOS QUE VÃO AO CÉU SUBIR. JESUS ESTÁ CHAMANDO, A TODOS CONVIDANDO: VENHAM SEM DEMORA PRA NOVA JERUSALÉM. VOCÊ, Ó PEQUENINO, POR QUE NÃO VEM TAMBÉM? RECEBA O SEU BILHETE E ENTRE NESSE TREM. E ALI JUNTOS ESTAREMOS NA CELESTIAL CIDADE, COM CRISTO ANDAREMOS, SERÁ SÓ FELICIDADE.',  4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
