INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SHEKINAH', 'CORO\nÓ SENHOR, A TI CLAMO\nQUE NOS MOSTRES A TUA SHEKINAH;\nCOM TUA GLÓRIA ENCHE ESTE LUGAR,\nCOM TUA PRESENÇA, A TUA SHEKINAH.\n\nSENHOR, SEI QUE ME CONHECES,\nQUERO ANDAR EM TEU CAMINHO\nE ENCONTRAR O TEU FAVOR.\n\nSENHOR, SÓ TUA PRESENÇA \nPODE ME FAZER DESCANSAR, \nSE NÃO FORES COMIGO EU NÃO PROSSEGUIREI,\nSE ANDARES COMIGO O MUNDO SABERÁ QUE \nTE ENCONTREI, POIS DESTE MUNDO EU ME SEPAREI.\n\nCORO\nÓ SENHOR, A TI CLAMO\nQUE NOS MOSTRES A TUA SHEKINAH;\nCOM TUA GLÓRIA ENCHE ESTE LUGAR,\nCOM TUA PRESENÇA, A TUA SHEKINAH. \n\nINSTRUMENTOS\nBIS\n\nCORO\nÓ SENHOR, A TI CLAMO\nQUE NOS MOSTRES A TUA SHEKINAH;\nCOM TUA GLÓRIA ENCHE ESTE LUGAR,\nCOM TUA PRESENÇA, A TUA SHEKINAH.\n\nFINAL: SHEKINAH, SHEKINAH!', 'CORO Ó SENHOR, A TI CLAMO QUE NOS MOSTRES A TUA SHEKINAH; COM TUA GLÓRIA ENCHE ESTE LUGAR, COM TUA PRESENÇA, A TUA SHEKINAH. SENHOR, SEI QUE ME CONHECES, QUERO ANDAR EM TEU CAMINHO E ENCONTRAR O TEU FAVOR. SENHOR, SÓ TUA PRESENÇA PODE ME FAZER DESCANSAR, SE NÃO FORES COMIGO EU NÃO PROSSEGUIREI, SE ANDARES COMIGO O MUNDO SABERÁ QUE TE ENCONTREI, POIS DESTE MUNDO EU ME SEPAREI. CORO Ó SENHOR, A TI CLAMO QUE NOS MOSTRES A TUA SHEKINAH; COM TUA GLÓRIA ENCHE ESTE LUGAR, COM TUA PRESENÇA, A TUA SHEKINAH. INSTRUMENTOS CORO Ó SENHOR, A TI CLAMO QUE NOS MOSTRES A TUA SHEKINAH; COM TUA GLÓRIA ENCHE ESTE LUGAR, COM TUA PRESENÇA, A TUA SHEKINAH.  SHEKINAH, SHEKINAH!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'TORRE FORTE', 'ESSAS LÁGRIMAS QUE ESCORREM DOS TEUS OLHOS\nSÓ JESUS É QUE PODE ENXUGÁ-LAS,\nSE TU VIVES TÃO SOZINHO, SEM PAZ, SEM AMOR,\nCRISTO QUER SER SEU AMIGO;\n\nELE JÁ LEVOU SOBRE SI \nNOSSAS DORES E OS NOSSOS PECADOS,\nFOI COM SANGUE INOCENTE \nQUE NÓS FOMOS COMPRADOS,\nHOJE SOMOS PARTE DO REINO DE DEUS.\n\nCORO\nTORRE FORTE (TORRE FORTE),\nDEUS CONOSCO (DEUS CONOSCO),\nPODEROSO EM OBRAS, PRÍNCIPE DOS EXÉRCITOS;\nO SENHOR SEMPRE VENCEU,\nNELE NÃO HÁ DERROTAS, \nELE É REI SOBRE TODOS OS REIS\n\nINSTRUMENTOS\n\nVEJO CRISTO SOBRE AS NUVENS TRIUNFANTE,\nEIS O BRADO DE VITÓRIA, NÓS VENCEMOS!\nOUÇO O TOQUE DA TROMBETA, \nA IGREJA VAI SUBINDO,\nANJOS E ARCANJOS NOS ESPERAM NO CÉU;\n\nALELUIA, NÓS CHEGAMOS AO FIM, \nOS REMIDOS QUE EM DEUS CONFIARAM,\nHÁ NO CÉU ALEGRIA, ALTO SOM DE LOUVOR,\nTODOS BENDIZENDO AO FILHO DE DEUS.\n\nCORO\nTORRE FORTE (TORRE FORTE),\nDEUS CONOSCO (DEUS CONOSCO),\nPODEROSO EM OBRAS, PRÍNCIPE DOS EXÉRCITOS;\nO SENHOR SEMPRE VENCEU,\nNELE NÃO HÁ DERROTAS, \nELE É REI SOBRE TODOS OS REIS\n\nFINAL:\nO SENHOR SEMPRE VENCEU, \nNELE NÃO HÁ DERROTAS, \nELE É REI SOBRE TODOS OS REIS,\nREI SOBRE TODOS OS REIS.', 'ESSAS LÁGRIMAS QUE ESCORREM DOS TEUS OLHOS SÓ JESUS É QUE PODE ENXUGÁ-LAS, SE TU VIVES TÃO SOZINHO, SEM PAZ, SEM AMOR, CRISTO QUER SER SEU AMIGO; ELE JÁ LEVOU SOBRE SI NOSSAS DORES E OS NOSSOS PECADOS, FOI COM SANGUE INOCENTE QUE NÓS FOMOS COMPRADOS, HOJE SOMOS PARTE DO REINO DE DEUS. CORO TORRE FORTE (TORRE FORTE), DEUS CONOSCO (DEUS CONOSCO), PODEROSO EM OBRAS, PRÍNCIPE DOS EXÉRCITOS; O SENHOR SEMPRE VENCEU, NELE NÃO HÁ DERROTAS, ELE É REI SOBRE TODOS OS REIS INSTRUMENTOS VEJO CRISTO SOBRE AS NUVENS TRIUNFANTE, EIS O BRADO DE VITÓRIA, NÓS VENCEMOS! OUÇO O TOQUE DA TROMBETA, A IGREJA VAI SUBINDO, ANJOS E ARCANJOS NOS ESPERAM NO CÉU; ALELUIA, NÓS CHEGAMOS AO FIM, OS REMIDOS QUE EM DEUS CONFIARAM, HÁ NO CÉU ALEGRIA, ALTO SOM DE LOUVOR, TODOS BENDIZENDO AO FILHO DE DEUS. CORO TORRE FORTE (TORRE FORTE), DEUS CONOSCO (DEUS CONOSCO), PODEROSO EM OBRAS, PRÍNCIPE DOS EXÉRCITOS; O SENHOR SEMPRE VENCEU, NELE NÃO HÁ DERROTAS, ELE É REI SOBRE TODOS OS REIS O SENHOR SEMPRE VENCEU, NELE NÃO HÁ DERROTAS, ELE É REI SOBRE TODOS OS REIS, REI SOBRE TODOS OS REIS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'VARÃO DE GUERRA É O SENHOR', 'VARÃO DE GUERRA É O SENHOR \nQUE VAI À FRENTE DO SEU POVO\nNAS PELEJAS DA ALMA, NAS PROVAÇÕES\nNAS LUTAS CONTRA OS NOSSOS ADVERSÁRIOS.\n\nVARÃO DE GUERRA É O SENHOR \nQUE ERGUE A SUA ESPADA \nE COM LABAREDAS DE FOGO\nTOCA NOS SEUS MINISTROS \nE OS TORNA CAPAZES PARA AS PELEJAS.\n\nESTE É O MEU DEUS, \nSOBERANO EM FORÇA E PODER, \nO ÚNICO QUE SE ASSENTA \nNO TRONO DA GLÓRIA E QUE REINARÁ\nETERNAMENTE;\n\nESTE É O MEU DEUS, \nSOBERANO EM FORÇA E PODER, \nJUSTO E CUMPRIDOR DA SUA PALAVRA, \nQUE NOS PROMETEU UM REI, JESUS,\nPARA REINAR SOBRE NÓS NA CIDADE SANTA\n\nFINAL: ETERNAMENTE.', 'VARÃO DE GUERRA É O SENHOR QUE VAI À FRENTE DO SEU POVO NAS PELEJAS DA ALMA, NAS PROVAÇÕES NAS LUTAS CONTRA OS NOSSOS ADVERSÁRIOS. VARÃO DE GUERRA É O SENHOR QUE ERGUE A SUA ESPADA E COM LABAREDAS DE FOGO TOCA NOS SEUS MINISTROS E OS TORNA CAPAZES PARA AS PELEJAS. ESTE É O MEU DEUS, SOBERANO EM FORÇA E PODER, O ÚNICO QUE SE ASSENTA NO TRONO DA GLÓRIA E QUE REINARÁ ETERNAMENTE; ESTE É O MEU DEUS, SOBERANO EM FORÇA E PODER, JUSTO E CUMPRIDOR DA SUA PALAVRA, QUE NOS PROMETEU UM REI, JESUS, PARA REINAR SOBRE NÓS NA CIDADE SANTA  ETERNAMENTE.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'AINDA HÁ TEMPO', 'COMO É FORTE OUVIR COMO SERÁ O FIM; \nO MOMENTO CHEGARÁ, \nO CLAMOR DA MEIA-NOITE SE OUVIRÁ.\n\nA IGREJA FIEL PREPARADA ESTÁ \nE A IGREJA INFIEL, \nSUA OPORTUNIDADE ACABARÁ.\n\nAINDA HÁ TEMPO, \nJESUS CRISTO NÃO VOLTOU, \nAINDA HÁ TEMPO \nDO SEU SONO DESPERTAR. \n\nAINDA HÁ TEMPO, \nTEMPO DE SE ARREPENDER, \nTEMPO DE BUSCAR A DEUS, \nDE BATER À PORTA E ELA SE ABRIRÁ\n\nAINDA HÁ TEMPO DO AZEITE IR COMPRAR, \nAINDA HÁ TEMPO, TEMPO DE SANTIFICAR; AINDA HÁ TEMPO DA CANDEIA SE ENCHER, DO ESPOSO ENCONTRAR E COM ELE, \nPARA AS BODAS DO CORDEIRO, ENTRAR.\n\nAINDA HÁ TEMPO, \nJESUS CRISTO NÃO VOLTOU, \nAINDA HÁ TEMPO \nDO SEU SONO DESPERTAR. \n\nAINDA HÁ TEMPO, \nTEMPO DE SE ARREPENDER, \nTEMPO DE BUSCAR A DEUS, \nDE BATER À PORTA E ELA SE ABRIRÁ\n\nAINDA HÁ TEMPO DO AZEITE IR COMPRAR, \nAINDA HÁ TEMPO, TEMPO DE SANTIFICAR; AINDA HÁ TEMPO DA CANDEIA SE ENCHER, DO ESPOSO ENCONTRAR E COM ELE, \nPARA AS BODAS DO CORDEIRO, ENTRAR.\n\nFINAL: AINDA HÁ TEMPO', 'COMO É FORTE OUVIR COMO SERÁ O FIM; O MOMENTO CHEGARÁ, O CLAMOR DA MEIA-NOITE SE OUVIRÁ. A IGREJA FIEL PREPARADA ESTÁ E A IGREJA INFIEL, SUA OPORTUNIDADE ACABARÁ. AINDA HÁ TEMPO, JESUS CRISTO NÃO VOLTOU, AINDA HÁ TEMPO DO SEU SONO DESPERTAR. AINDA HÁ TEMPO, TEMPO DE SE ARREPENDER, TEMPO DE BUSCAR A DEUS, DE BATER À PORTA E ELA SE ABRIRÁ AINDA HÁ TEMPO DO AZEITE IR COMPRAR, AINDA HÁ TEMPO, TEMPO DE SANTIFICAR; AINDA HÁ TEMPO DA CANDEIA SE ENCHER, DO ESPOSO ENCONTRAR E COM ELE, PARA AS BODAS DO CORDEIRO, ENTRAR. AINDA HÁ TEMPO DO AZEITE IR COMPRAR, AINDA HÁ TEMPO, TEMPO DE SANTIFICAR; AINDA HÁ TEMPO DA CANDEIA SE ENCHER, DO ESPOSO ENCONTRAR E COM ELE, PARA AS BODAS DO CORDEIRO, ENTRAR.  AINDA HÁ TEMPO', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'UMA VOZ MANSA E DELICADA SINTA AGORA', 'UMA VOZ MANSA E DELICADA\nSINTA AGORA A MINHA PAZ EM TEU CORAÇÃO \nSEI PORQUE ESTÁS AQUI\nPENSAS QUE EU NÃO TE VI\nSAI DA ESCURIDÃO EU VOU TIRAR \nO MEDO DO TEU CORAÇÃO\n\nPENSAS ESTAR SÓ E OLHAS AO TEU REDOR\nSEI QUE HÁ MUITOS QUE ME DEIXARAM \nDESTRUIRAM MEUS ALTARES\nDESPREZARAM A PROFECIA DO TEU DEUS\n\nEU QUERO TE DIZER QUE EU LIVREI SALVEI\nTODOS QUE NÃO ME NEGARAM\nJOELHOS QUE NÃO SE DOBRARAM\nBIS\n\nSEU FIM NÃO É AQUI\nVOCÊ VAI PROSSEGUIR\nPOIS GRANDES COISAS SIM \nTU VIVERÁS POR MIM\n\nUMA VOZ MANSA E DELICADA\nSINTA AGORA A MINHA PAZ EM TEU CORAÇÃO \nSEI PORQUE ESTÁS AQUI\nPENSAS QUE EU NÃO TE VI\nSAI DA ESCURIDÃO EU VOU TIRAR \nO MEDO DO TEU CORAÇÃO\n\nPENSAS ESTAR SÓ E OLHAS AO TEU REDOR\nSEI QUE HÁ MUITOS QUE ME DEIXARAM \nDESTRUIRAM MEUS ALTARES\nDESPREZARAM A PROFECIA DO TEU DEUS\n\nEU QUERO TE DIZER QUE EU LIVREI SALVEI\nTODOS QUE NÃO ME NEGARAM\nJOELHOS QUE NÃO SE DOBRARAM\nBIS\n\nSEU FIM NÃO É AQUI\nVOCÊ VAI PROSSEGUIR\nPOIS GRANDES COISAS SIM \nTU VIVERÁS POR MIM\nBIS', 'UMA VOZ MANSA E DELICADA SINTA AGORA A MINHA PAZ EM TEU CORAÇÃO SEI PORQUE ESTÁS AQUI PENSAS QUE EU NÃO TE VI SAI DA ESCURIDÃO EU VOU TIRAR O MEDO DO TEU CORAÇÃO PENSAS ESTAR SÓ E OLHAS AO TEU REDOR SEI QUE HÁ MUITOS QUE ME DEIXARAM DESTRUIRAM MEUS ALTARES DESPREZARAM A PROFECIA DO TEU DEUS EU QUERO TE DIZER QUE EU LIVREI SALVEI TODOS QUE NÃO ME NEGARAM JOELHOS QUE NÃO SE DOBRARAM SEU FIM NÃO É AQUI VOCÊ VAI PROSSEGUIR POIS GRANDES COISAS SIM TU VIVERÁS POR MIM', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'EM CAMINHOS DE DOR', 'EM CAMINHOS DE DOR EU ANDAVA, \nEM DESOBEDIÊNCIA AO SENHOR; \nEU ME ASSENTAVA E CHORAVA, \nME LEMBRANDO COMO EU ERA FELIZ. \nQUEM JÁ FOI ESCRAVO UM DIA, \nSABE QUE NÃO HÁ ALEGRIA \nEM CANTAR LOUVORES DISTANTE, \nDISTANTE DA CASA DO PAI.\n\nCOMO EU PODERIA CANTAR ALI?\nPOIS TU ÉS MINHA MAIOR ALEGRIA;\nSE NÃO FOR PRA TI, PRA NINGUÉM MAIS SERÁ.\nTUA PRESENÇA, Ó DEUS, QUERO TODOS OS DIAS!\nMEU LOUVOR É SÓ PRA TI, \nSÓ PRA TI!\n\nHAVERÁ O DIA DA VITÓRIA, A VITÓRIA FINAL,\nQUANDO SERÁ DESTRUÍDO E ESQUECIDO O MAL,\nO INIMIGO DO SENHOR, AQUELE QUE AOS HOMENS TROUXE TANTA DOR, TANTA DOR.\nHOJE EU CANTO AO SENHOR, \nVOLTEI, NÃO ME ESQUECEREI \nQUE É, SENHOR, NA TUA PRESENÇA, \nO LUGAR DE ONDE EU NÃO MAIS SAIREI!\n\nCOMO EU PODERIA CANTAR ALI?\nPOIS TU ÉS MINHA MAIOR ALEGRIA;\nSE NÃO FOR PRA TI, PRA NINGUÉM MAIS SERÁ.\nTUA PRESENÇA, Ó DEUS, QUERO TODOS OS DIAS!\nMEU LOUVOR É SÓ PRA TI, \nSÓ PRA TI!\n\nHAVERÁ O DIA DA VITÓRIA, A VITÓRIA FINAL,\nQUANDO SERÁ DESTRUÍDO E ESQUECIDO O MAL,\nO INIMIGO DO SENHOR, AQUELE QUE AOS HOMENS TROUXE TANTA DOR, TANTA DOR.\nHOJE EU CANTO AO SENHOR, \nVOLTEI, NÃO ME ESQUECEREI \nQUE É, SENHOR, NA TUA PRESENÇA, \nO LUGAR DE ONDE EU NÃO MAIS SAIREI!\n\nFINAL: \nQUE É, SENHOR, NA TUA PRESENÇA, \nO LUGAR DE ONDE EU NÃO MAIS SAIREI!', 'EM CAMINHOS DE DOR EU ANDAVA, EM DESOBEDIÊNCIA AO SENHOR; EU ME ASSENTAVA E CHORAVA, ME LEMBRANDO COMO EU ERA FELIZ. QUEM JÁ FOI ESCRAVO UM DIA, SABE QUE NÃO HÁ ALEGRIA EM CANTAR LOUVORES DISTANTE, DISTANTE DA CASA DO PAI. COMO EU PODERIA CANTAR ALI? POIS TU ÉS MINHA MAIOR ALEGRIA; SE NÃO FOR PRA TI, PRA NINGUÉM MAIS SERÁ. TUA PRESENÇA, Ó DEUS, QUERO TODOS OS DIAS! MEU LOUVOR É SÓ PRA TI, SÓ PRA TI! HAVERÁ O DIA DA VITÓRIA, A VITÓRIA FINAL, QUANDO SERÁ DESTRUÍDO E ESQUECIDO O MAL, O INIMIGO DO SENHOR, AQUELE QUE AOS HOMENS TROUXE TANTA DOR, TANTA DOR. HOJE EU CANTO AO SENHOR, VOLTEI, NÃO ME ESQUECEREI QUE É, SENHOR, NA TUA PRESENÇA, O LUGAR DE ONDE EU NÃO MAIS SAIREI! QUE É, SENHOR, NA TUA PRESENÇA, O LUGAR DE ONDE EU NÃO MAIS SAIREI!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'A MELHOR COISA', 'DE TANTAS COISAS \nQUE ANDEI FAZENDO,\nDE QUASE TODAS ME ARREPENDI.\nMAS HOUVE UMA ESPECIAL,\nFOI A MAIS CERTA QUE ESCOLHI.\n\nCORO\nA MELHOR COISA QUE EU JÁ FIZ\nEM TODA A MINHA VIDA, \nPOR ISSO SOU FELIZ EM \nACEITAR JESUS, SINCERAMENTE FOI\nA MELHOR COISA QUE EU JÁ FIZ.\n\nCOISAS ERRADAS ANDEI FAZENDO\nNA CONDIÇÃO DE PECADOR,\nMAS QUANDO ACHEI-ME DESFALECENDO,\nTOMEI A DECISÃO QUE ME SALVOU.\n\nCORO (2X)\nA MELHOR COISA QUE EU JÁ FIZ\nEM TODA A MINHA VIDA, \nPOR ISSO SOU FELIZ EM \nACEITAR JESUS, SINCERAMENTE FOI\nA MELHOR COISA QUE EU JÁ FIZ.', 'DE TANTAS COISAS QUE ANDEI FAZENDO, DE QUASE TODAS ME ARREPENDI. MAS HOUVE UMA ESPECIAL, FOI A MAIS CERTA QUE ESCOLHI. CORO A MELHOR COISA QUE EU JÁ FIZ EM TODA A MINHA VIDA, POR ISSO SOU FELIZ EM ACEITAR JESUS, SINCERAMENTE FOI A MELHOR COISA QUE EU JÁ FIZ. COISAS ERRADAS ANDEI FAZENDO NA CONDIÇÃO DE PECADOR, MAS QUANDO ACHEI-ME DESFALECENDO, TOMEI A DECISÃO QUE ME SALVOU. A MELHOR COISA QUE EU JÁ FIZ EM TODA A MINHA VIDA, POR ISSO SOU FELIZ EM ACEITAR JESUS, SINCERAMENTE FOI A MELHOR COISA QUE EU JÁ FIZ.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'BARNABÉ, HOMEM DE DEUS', 'NÃO FICA BEM A GENTE PASSAR BEM E O OUTRO CARESTIA, AINDA MAIS QUANDO SE SABE O QUE FAZER E NÃO SE FAZ. COMO FRUTO DO AMOR DE CRISTO, FRUTO DO SEU COMPROMISSO, VENDEU UM HOMEM O QUE TINHA E REPARTIU.\n\nCORO\nERA SEU NOME BARNABÉ, NATURAL DE CHIPRE,\nTAMBÉM CHAMADO DE JOSÉ DA CONSOLAÇÃO,\nHOMEM BOM E PIEDOSO, CHEIO DE TEMOR E FÉ,\nHOMEM DE DEUS.\n\nE QUANDO SAULO CONVERTEU-SE A CRISTO LHE FALTOU AMIGO,ALGUÉM QUE FOSSE COMPANHEIRO,\nFONTE DE CONSOLO E ABRIGO. COMO FRUTO DO AMOR DE CRISTO, FRUTO DO SEU COMPROMISSO, FOI UM HOMEM PROCURÁ-LO, DANDO-LHE A MÃO.\n\nCORO\nERA SEU NOME BARNABÉ, NATURAL DE CHIPRE,\nTAMBÉM CHAMADO DE JOSÉ DA CONSOLAÇÃO,\nHOMEM BOM E PIEDOSO, CHEIO DE TEMOR E FÉ,\nHOMEM DE DEUS.\n\nE QUANDO A IGREJA SE ESPALHOU POR TODO CANTO QUE HAVIA, POR PROVIDÊNCIA, SIM,\nPOR MÃO DE DEUS CHEGOU A ANTIOQUIA.\nPRECISANDO DE UM PASTOR DE ALMAS, \nMESMO DE UM PASTOR DE HOMENS, FORAM PROCURAR AQUELE QUE DEUS PREPAROU.\n\nCORO\nERA SEU NOME BARNABÉ, NATURAL DE CHIPRE,\nTAMBÉM CHAMADO DE JOSÉ DA CONSOLAÇÃO,\nHOMEM BOM E PIEDOSO, CHEIO DE TEMOR E FÉ,\nHOMEM DE DEUS.', 'NÃO FICA BEM A GENTE PASSAR BEM E O OUTRO CARESTIA, AINDA MAIS QUANDO SE SABE O QUE FAZER E NÃO SE FAZ. COMO FRUTO DO AMOR DE CRISTO, FRUTO DO SEU COMPROMISSO, VENDEU UM HOMEM O QUE TINHA E REPARTIU. CORO ERA SEU NOME BARNABÉ, NATURAL DE CHIPRE, TAMBÉM CHAMADO DE JOSÉ DA CONSOLAÇÃO, HOMEM BOM E PIEDOSO, CHEIO DE TEMOR E FÉ, HOMEM DE DEUS. E QUANDO SAULO CONVERTEU-SE A CRISTO LHE FALTOU AMIGO,ALGUÉM QUE FOSSE COMPANHEIRO, FONTE DE CONSOLO E ABRIGO. COMO FRUTO DO AMOR DE CRISTO, FRUTO DO SEU COMPROMISSO, FOI UM HOMEM PROCURÁ-LO, DANDO-LHE A MÃO. E QUANDO A IGREJA SE ESPALHOU POR TODO CANTO QUE HAVIA, POR PROVIDÊNCIA, SIM, POR MÃO DE DEUS CHEGOU A ANTIOQUIA. PRECISANDO DE UM PASTOR DE ALMAS, MESMO DE UM PASTOR DE HOMENS, FORAM PROCURAR AQUELE QUE DEUS PREPAROU.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'COMO AGRADECER (MEU TRIBUTO)', 'COMO AGRADECER, PELO BEM \nQUE TENS FEITO A MIM? \nQUE VEM DEMONSTRAR QUANTO \nAMOR TU TENS, Ó DEUS, POR MIM;\n\nAS VOZES DE MILHÕES DE ANJOS, \nNÃO PODERIAM EXPRESSAR, \nA GRATIDÃO DO MEU PEQUENO\nSER, QUE SÓ PERTENCE A TI.\n\nA DEUS DEMOS GLÓRIA (3X)\nQUE POR NÓS TANTO FEZ;\nFOI NA CRUZ QUE SALVOU-ME,\nSEU PODER RESTAUROU-ME,\nA DEUS DEMOS GLÓRIA, \nQUE POR NÓS TANTO FEZ.\n\nFOI NA CRUZ QUE SALVOU-ME,\nSEU PODER RESTAUROU-ME,\nA DEUS DEMOS GLÓRIA, \nQUE POR NÓS TANTO FEZ.\n\nQUERO VIVER AQUI \nPARA ADORAR-TE, MEU SENHOR,\nE SE SURGIR UM LOUVOR\nAO CALVÁRIO SEJA, SIM, SEJA, SIM.\n\nFOI NA CRUZ QUE SALVOU-ME,\nSEU PODER RESTAUROU-ME,\nA DEUS DEMOS GLÓRIA, \nQUE POR NÓS TANTO FEZ.', 'COMO AGRADECER, PELO BEM QUE TENS FEITO A MIM? QUE VEM DEMONSTRAR QUANTO AMOR TU TENS, Ó DEUS, POR MIM; AS VOZES DE MILHÕES DE ANJOS, NÃO PODERIAM EXPRESSAR, A GRATIDÃO DO MEU PEQUENO SER, QUE SÓ PERTENCE A TI. A DEUS DEMOS GLÓRIA  QUE POR NÓS TANTO FEZ; FOI NA CRUZ QUE SALVOU-ME, SEU PODER RESTAUROU-ME, A DEUS DEMOS GLÓRIA, QUE POR NÓS TANTO FEZ. FOI NA CRUZ QUE SALVOU-ME, SEU PODER RESTAUROU-ME, A DEUS DEMOS GLÓRIA, QUE POR NÓS TANTO FEZ. QUERO VIVER AQUI PARA ADORAR-TE, MEU SENHOR, E SE SURGIR UM LOUVOR AO CALVÁRIO SEJA, SIM, SEJA, SIM.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'CORDEIRO DE DEUS', 'TEU SANTO FILHO, SEM PECAR\nFOI ENVIADO PRA LEVAR\nUM PESO QUE ERA TODO MEU\nE SE TORNAR CORDEIRO DE DEUS.\n\nO SEU PRESENTE DE AMOR\nFOI REJEITADO PELOS SEUS,\nHUMILDE REI, NADA FALOU\nE ASSIM SE FEZ CORDEIRO DE DEUS.\n\nCORO\nCORDEIRO DE DEUS, TÃO PURO ÉS\nVOU TE ADORAR E TE EXALTAR\nCOM SANGUE VEM LAVAR MEU SER\nMEU CRISTO ÉS CORDEIRO DE DEUS.\n\nPERDIDO ESTAVA SEM SABER,\nMAS ME ATRAÍSTE A TEU SER\nE HOJE SOU UM SERVO TEU\nPRA TEU LOUVOR, \nCORDEIRO DE DEUS.\n\nCORO\nCORDEIRO DE DEUS, TÃO PURO ÉS\nVOU TE ADORAR E TE EXALTAR\nCOM SANGUE VEM LAVAR MEU SER\nMEU CRISTO ÉS CORDEIRO DE DEUS.\nBIS', 'TEU SANTO FILHO, SEM PECAR FOI ENVIADO PRA LEVAR UM PESO QUE ERA TODO MEU E SE TORNAR CORDEIRO DE DEUS. O SEU PRESENTE DE AMOR FOI REJEITADO PELOS SEUS, HUMILDE REI, NADA FALOU E ASSIM SE FEZ CORDEIRO DE DEUS. CORO CORDEIRO DE DEUS, TÃO PURO ÉS VOU TE ADORAR E TE EXALTAR COM SANGUE VEM LAVAR MEU SER MEU CRISTO ÉS CORDEIRO DE DEUS. PERDIDO ESTAVA SEM SABER, MAS ME ATRAÍSTE A TEU SER E HOJE SOU UM SERVO TEU PRA TEU LOUVOR, CORDEIRO DE DEUS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'DESCANSA, Ó ALMA', 'DESCANSA, Ó ALMA, \nEIS O SENHOR AO LADO; \nPACIENTE LEVA, \nE SEM QUEIXAR-TE, A CRUZ;\n\nDEIXA O SENHOR TOMAR \nDE TI CUIDADO,\nÉ IMUTÁVEL TEU FIEL JESUS!\nPROSSEGUE, Ó ALMA, \nO AMIGO CELESTIAL PROTEGERÁ \nTEUS PASSOS NO ESPINHAL!\n\nPROSSEGUE, Ó ALMA, \nO TRILHO É ESTREITO E ESCURO,\nMAS, NO PASSADO DEUS \nGUIOU-TE ASSIM!\nCONFIA AGORA A DEUS O TEU FUTURO,\nE O QUE É MISTÉRIO HÁ DE \nACLARAR-SE ENFIM,\nCONFIA, Ó ALMA, A SUA MANSA \nVOZ AINDA ACALMA O VENTO \nE O MAR FEROZ!\n\nCONFIA, Ó ALMA, \nA HORA VEM CHEGANDO!\nIRÁS COM CRISTO, O TEU SENHOR, \nMORAR, SEM DOR, NEM MÁGOAS,\nGOZARÁS, CANTANDO \nAS ALEGRIAS DO CELESTE LAR!\nDESCANSA, Ó ALMA, \nAGORA HÁ PRANTO E DOR, \nDEPOIS O GOZO, A PAZ, \nO CÉU DE AMOR!', 'DESCANSA, Ó ALMA, EIS O SENHOR AO LADO; PACIENTE LEVA, E SEM QUEIXAR-TE, A CRUZ; DEIXA O SENHOR TOMAR DE TI CUIDADO, É IMUTÁVEL TEU FIEL JESUS! PROSSEGUE, Ó ALMA, O AMIGO CELESTIAL PROTEGERÁ TEUS PASSOS NO ESPINHAL! PROSSEGUE, Ó ALMA, O TRILHO É ESTREITO E ESCURO, MAS, NO PASSADO DEUS GUIOU-TE ASSIM! CONFIA AGORA A DEUS O TEU FUTURO, E O QUE É MISTÉRIO HÁ DE ACLARAR-SE ENFIM, CONFIA, Ó ALMA, A SUA MANSA VOZ AINDA ACALMA O VENTO E O MAR FEROZ! CONFIA, Ó ALMA, A HORA VEM CHEGANDO! IRÁS COM CRISTO, O TEU SENHOR, MORAR, SEM DOR, NEM MÁGOAS, GOZARÁS, CANTANDO AS ALEGRIAS DO CELESTE LAR! DESCANSA, Ó ALMA, AGORA HÁ PRANTO E DOR, DEPOIS O GOZO, A PAZ, O CÉU DE AMOR!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'DEUS, DEUS, DEUS', 'DEUS, DEUS, DEUS\nSANTO É TEU NOME, SENHOR!\nÀ TUA IMAGEM FEITOS,\nA TI LOUVORES ENTOEMOS\nPOIS FILHOS NOS FIZESTE.\n\nO TEU PODER IMENSO, Ó DEUS,\nMOSTRAR-NOS VEM\nCOM GRANDE AMOR.\n\n\nGLÓRIA AO SENHOR CANTEMOS\nE SEU AMOR LOUVEMOS.\nGLÓRIA, GLÓRIA AO SENHOR!\nBIS\nBIS\n\nQUAL VIAJANTE \nEM NOITE ESCURA,\nVAI PROCURANDO A LUZ,\nASSIM MINH’ALMA\nA DEUS PROCURA,\nPOIS ELE ME CONDUZ.\n\nÉ CERTO QUE PROVADO SOU,\nPELO CAMINHO A PROSSEGUIR,\nPORÉM EU SEI QUE TU BEM PERTO ESTÁS.\nQUANDO HEI DE IR, SENHOR, NÃO SEI,\nPARA O TEU SANTO LAR,\nSINTO PORÉM TUA VOZ CHAMAR,\nEM BREVE PARTIREI.\n\nTUDO O QUE DEUS FIZER É BOM,\nA NOITE CLARA FICARÁ\nNO ETERNO LAR DE DEUS.\nAMÉM (5X)', 'DEUS, DEUS, DEUS SANTO É TEU NOME, SENHOR! À TUA IMAGEM FEITOS, A TI LOUVORES ENTOEMOS POIS FILHOS NOS FIZESTE. O TEU PODER IMENSO, Ó DEUS, MOSTRAR-NOS VEM COM GRANDE AMOR. GLÓRIA AO SENHOR CANTEMOS E SEU AMOR LOUVEMOS. GLÓRIA, GLÓRIA AO SENHOR! QUAL VIAJANTE EM NOITE ESCURA, VAI PROCURANDO A LUZ, ASSIM MINH’ALMA A DEUS PROCURA, POIS ELE ME CONDUZ. É CERTO QUE PROVADO SOU, PELO CAMINHO A PROSSEGUIR, PORÉM EU SEI QUE TU BEM PERTO ESTÁS. QUANDO HEI DE IR, SENHOR, NÃO SEI, PARA O TEU SANTO LAR, SINTO PORÉM TUA VOZ CHAMAR, EM BREVE PARTIREI. TUDO O QUE DEUS FIZER É BOM, A NOITE CLARA FICARÁ NO ETERNO LAR DE DEUS. AMÉM (5X)', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'DEUS PREPAROU', 'DEUS PREPAROU UMA CIDADE \nPARA SUA IGREJA FIEL.\nNESTA CIDADE NÃO HAVERÁ DOR,\nPOIS ELE NÃO PERMITIRÁ.\nSEREMOS TIRADOS DESTE \nMUNDO CRUEL, PARA \nMORARMOS COM ELE NO CÉU.\n\nCORO\nEU QUERO ENTRAR NO CÉU COM JESUS,\nE CONTEMPLAR AS MANSÕES \nQUE DEUS PREPAROU.\nQUERO ANDAR NAS RUAS DE OURO\nE OUVIR A VOZ DE DEUS \nO MEU NOME CHAMAR.\n\nOUVE A VOZ DO REDENTOR, \nELE QUER HOJE SALVAR, \nQUER LIBERTAR E PERDOAR, \nTUAS FERIDAS CURAR E NÃO TARDARÁ.\nCRISTO VEM NOS BUSCAR, \nENTREGA TUA VIDA E ELE TE LEVARÁ.\n\nCORO\nEU QUERO ENTRAR NO CÉU COM JESUS,\nE CONTEMPLAR AS MANSÕES \nQUE DEUS PREPAROU.\nQUERO ANDAR NAS RUAS DE OURO\nE OUVIR A VOZ DE DEUS \nO MEU NOME CHAMAR.', 'DEUS PREPAROU UMA CIDADE PARA SUA IGREJA FIEL. NESTA CIDADE NÃO HAVERÁ DOR, POIS ELE NÃO PERMITIRÁ. SEREMOS TIRADOS DESTE MUNDO CRUEL, PARA MORARMOS COM ELE NO CÉU. CORO EU QUERO ENTRAR NO CÉU COM JESUS, E CONTEMPLAR AS MANSÕES QUE DEUS PREPAROU. QUERO ANDAR NAS RUAS DE OURO E OUVIR A VOZ DE DEUS O MEU NOME CHAMAR. OUVE A VOZ DO REDENTOR, ELE QUER HOJE SALVAR, QUER LIBERTAR E PERDOAR, TUAS FERIDAS CURAR E NÃO TARDARÁ. CRISTO VEM NOS BUSCAR, ENTREGA TUA VIDA E ELE TE LEVARÁ.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'EU NÃO POSSO FUGIR DO TEU ESPÍRITO', 'EU NÃO POSSO FUGIR DO TEU ESPÍRITO, (SERVAS) \nNEM FICAR DISTANTE DO TEU AMOR; (VARÕES)\nSE EU SUBIR ATÉ OS CÉUS, \nSE AO ABISMO EU DESCER, \nSEI QUE ALI TU ESTÁS.\n\nMESMO SE EU TOMAR \nAS ASAS DA ALVORADA, \nTUA  MÃO AINDA ASSIM MEGUIARÁ, \nMESMO VINDO A NOITE ESCURA\nTUA LUZ BRILHARÁ, \nE COMIGO ESTARÁS.\n\nEU NÃO POSSO FUGIR DO TEU ESPÍRITO, (SERVAS) \nNEM FICAR DISTANTE DO TEU AMOR; (VARÕES)\nSE EU SUBIR ATÉ OS CÉUS, \nSE AO ABISMO EU DESCER, \nSEI QUE ALI TU ESTÁS.\n\nMESMO SE EU TOMAR \nAS ASAS DA ALVORADA, \nTUA  MÃO AINDA ASSIM MEGUIARÁ, \nMESMO VINDO A NOITE ESCURA\nTUA LUZ BRILHARÁ, \nE COMIGO ESTARÁS.', 'EU NÃO POSSO FUGIR DO TEU ESPÍRITO,  NEM FICAR DISTANTE DO TEU AMOR;  SE EU SUBIR ATÉ OS CÉUS, SE AO ABISMO EU DESCER, SEI QUE ALI TU ESTÁS. MESMO SE EU TOMAR AS ASAS DA ALVORADA, TUA MÃO AINDA ASSIM MEGUIARÁ, MESMO VINDO A NOITE ESCURA TUA LUZ BRILHARÁ, E COMIGO ESTARÁS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'EXPIROU', 'EXPIROU! NÃO MAIS PADECE\nDE DEUS PAI O FILHO AMADO,\nMAS O SOLO SE ESTREMECE \nCOM SEU SANGUE DERRAMADO.\n\nFOI ENTÃO QUE A DEUS BENDITO\nIMPLOROU O SALVADOR.\nEIS O AMOR VASTO, INFINITO, \nPELO POVO TRANSGRESSOR.\nENTREGOU A SUA VIDA! \nGRANDE PROVA DE AMOR!\nMESMO ALI CRUCIFICADO \nAMA A QUEM O CONDENOU!\n\nAOS SEUS PÉS EU VOU, ENQUANTO\nSOFRE ASSIM O CRISTO AMADO,\nPARA AO MENOS DAR-LHE O PRANTO,\nPOIS POR MIM DEU O SEU SANGUE.\nPARA AO MENOS DAR-LHE O PRANTO,\nPOIS POR MIM DEU O SEU SANGUE.', 'EXPIROU! NÃO MAIS PADECE DE DEUS PAI O FILHO AMADO, MAS O SOLO SE ESTREMECE COM SEU SANGUE DERRAMADO. FOI ENTÃO QUE A DEUS BENDITO IMPLOROU O SALVADOR. EIS O AMOR VASTO, INFINITO, PELO POVO TRANSGRESSOR. ENTREGOU A SUA VIDA! GRANDE PROVA DE AMOR! MESMO ALI CRUCIFICADO AMA A QUEM O CONDENOU! AOS SEUS PÉS EU VOU, ENQUANTO SOFRE ASSIM O CRISTO AMADO, PARA AO MENOS DAR-LHE O PRANTO, POIS POR MIM DEU O SEU SANGUE. PARA AO MENOS DAR-LHE O PRANTO, POIS POR MIM DEU O SEU SANGUE.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'ÉS UM REMIDO DE DEUS', 'ÉS UM REMIDO DE DEUS.\nTU ÉS UM DOS MEUS,\nÉS AQUELE A QUEM ESCOLHI.\nUM DIA OUVIU MINHA VOZ,\nA VOZ DO FILHO DE DEUS,\nA FALAR: VEM AQUI ME SERVIR.\n\nSENHOR, NADA TENHO PRA TE OFERECER.\nSABES O QUE SOU, \nSABES QUEM SOU EU E O TEU \nQUERER EM MIM SERÁ O MEU PRAZER. \nTEUS MISTÉRIOS CONHECER, TUA OBRA\nREALIZAR, MINHA VIDA ENTREGAR.\n\nAS LUTAS VÊM, \nMAS AS VITÓRIAS TAMBÉM VÊM.\nE O TEU AMOR É MAIS FORTE,\nE SEM ELE NÃO ESTARIA AQUI.\nCOM TEU PODER, TUA GRAÇA, TUA LUZ\nSOU UM VENCEDOR, SOU UM SERVO,\nSOU VASO A SERVIÇO DO SENHOR.\nBIS\n\nFINAL: \nSOU VASO A SERVIÇO DO SENHOR.', 'ÉS UM REMIDO DE DEUS. TU ÉS UM DOS MEUS, ÉS AQUELE A QUEM ESCOLHI. UM DIA OUVIU MINHA VOZ, A VOZ DO FILHO DE DEUS, A FALAR: VEM AQUI ME SERVIR. SENHOR, NADA TENHO PRA TE OFERECER. SABES O QUE SOU, SABES QUEM SOU EU E O TEU QUERER EM MIM SERÁ O MEU PRAZER. TEUS MISTÉRIOS CONHECER, TUA OBRA REALIZAR, MINHA VIDA ENTREGAR. AS LUTAS VÊM, MAS AS VITÓRIAS TAMBÉM VÊM. E O TEU AMOR É MAIS FORTE, E SEM ELE NÃO ESTARIA AQUI. COM TEU PODER, TUA GRAÇA, TUA LUZ SOU UM VENCEDOR, SOU UM SERVO, SOU VASO A SERVIÇO DO SENHOR. SOU VASO A SERVIÇO DO SENHOR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'FINDA-SE ESTE DIA', 'FINDA-SE ESTE DIA, QUE MEU PAI ME DEU,\nSOMBRA VESPERTINAS COBREM JÁ OS CÉUS,\nÓ JESUS BENDITO, SE COMIGO ESTÁS,\nEU NÃO TEMO A NOITE, VOU DORMIR EM PAZ.\n\nCOM MEUS PECADOS EU TE ENTRISTECI,\nMAS PERDÃO TE PEÇO, POR AMOR DE TI.\nSOU HUMANO E FRACO, LIVRA-ME DO MAL\nE EM SOSSEGO TENHO SONO E PAZ REAL.\n\nGUARDA O MARINHEIRO NO VIOLENTO MAR\nE AO QUE SOFRE DORES, \nQUEIRAS CONFORTAR.\nAO TENTADO ESTENDE TUA MÃO SENHOR,\nMANDA AO TRISTE E AFLITO TEU CONSOLADOR.\n\nPELOS PAIS E AMIGOS, PELA SANTA LEI,\nPELO AMOR DIVINO GRAÇAS TE DAREI.\nÓ JESUS ACEITA MINHA PETIÇÃO,\nE SEGURO DURMO SEM PERTUBAÇÃO.', 'FINDA-SE ESTE DIA, QUE MEU PAI ME DEU, SOMBRA VESPERTINAS COBREM JÁ OS CÉUS, Ó JESUS BENDITO, SE COMIGO ESTÁS, EU NÃO TEMO A NOITE, VOU DORMIR EM PAZ. COM MEUS PECADOS EU TE ENTRISTECI, MAS PERDÃO TE PEÇO, POR AMOR DE TI. SOU HUMANO E FRACO, LIVRA-ME DO MAL E EM SOSSEGO TENHO SONO E PAZ REAL. GUARDA O MARINHEIRO NO VIOLENTO MAR E AO QUE SOFRE DORES, QUEIRAS CONFORTAR. AO TENTADO ESTENDE TUA MÃO SENHOR, MANDA AO TRISTE E AFLITO TEU CONSOLADOR. PELOS PAIS E AMIGOS, PELA SANTA LEI, PELO AMOR DIVINO GRAÇAS TE DAREI. Ó JESUS ACEITA MINHA PETIÇÃO, E SEGURO DURMO SEM PERTUBAÇÃO.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'GUIA-ME', 'GUIA-ME PELAS DUNAS DO DESERTO\nGUIA-ME SINTO O TEU AMOR BEM PERTO\nGUIA-ME PELAS RUAS DESTA VIDA\nGUIA-ME ATÉ A TERRA PROMETIDA\n\nCORO\nGUIA-ME, DÁ-ME FORÇAS \nDE TI PRECISO TANTO ASSIM, \nUSA-ME, COMO QUISERES \nPORQUE TU ÉS PARTE DE MIM.\n\nINSTRUMENTOS\n\nÓ SENHOR, TEU CAJADO ME CONSOLA\nONDE FOR O MEU CÁLICE TRANSBORDA.\nFAZ-ME SER UM MANANCIAL DE ÁGUAS\nFAZ CORRER ÁGUAS VIVAS DE MINH’ALMA.\n\nCORO\nGUIA-ME, DÁ-ME FORÇAS \nDE TI PRECISO TANTO ASSIM, \nUSA-ME, COMO QUISERES \nPORQUE TU ÉS PARTE DE MIM.\n\nUSA-ME, COMO QUISERES,\nPORQUE TU ÉS PARTE DE MIM\n\nFINAL: PARTE DE MIM.', 'GUIA-ME PELAS DUNAS DO DESERTO GUIA-ME SINTO O TEU AMOR BEM PERTO GUIA-ME PELAS RUAS DESTA VIDA GUIA-ME ATÉ A TERRA PROMETIDA CORO GUIA-ME, DÁ-ME FORÇAS DE TI PRECISO TANTO ASSIM, USA-ME, COMO QUISERES PORQUE TU ÉS PARTE DE MIM. INSTRUMENTOS Ó SENHOR, TEU CAJADO ME CONSOLA ONDE FOR O MEU CÁLICE TRANSBORDA. FAZ-ME SER UM MANANCIAL DE ÁGUAS FAZ CORRER ÁGUAS VIVAS DE MINH’ALMA. CORO GUIA-ME, DÁ-ME FORÇAS DE TI PRECISO TANTO ASSIM, USA-ME, COMO QUISERES PORQUE TU ÉS PARTE DE MIM. USA-ME, COMO QUISERES, PORQUE TU ÉS PARTE DE MIM  PARTE DE MIM.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'GRANDE É O SENHOR, INCLINAI-VOS', 'GRANDE É O SENHOR,\nINCLINAI-VOS TERRA HUMILDEMENTE.\nGRANDE É O SENHOR\nADORAÍ-O SEMPRE ALEGREMENTE.\nREGOZIJAI! DE CORAÇÃO!\nELE É O CRIADOR E NÓS A CRIAÇÃO.\n\nEIS QUE VEM DE SUA MÃO:\nO SOL, A LUA, ESTRELAS E PLANETAS\nJUSTO ÉS EM SUAS LEIS;\nTUFÕES ACALMAM, ONDAS OBEDECEM-NO\nSOSSEGA O MAR AO SEU MANDAR,\nMONTANHAS SE DESFARÃO,\nDESERTOS BROTARÃO BOAS COLHEITAS,\nA CRIAÇÃO LHE APRAZ, O SOL DIRIGE A\nLUA, ESCONDE A NOITE EM DIA FAZ.\n\nGRANDE É O SENHOR,\nO SOBERANO LÁ NOS CÉUS,\nE REI DOS REIS ONIPOTENTE E SANTO \nDEUS, GRANDE EM SEU PODER,\nE MASJESTOSO EM SEU SABER.\nSUA BONDADE É IMENSURAVÉL COMO \nO MAR, SEU AMPLO AMOR É \nINDISPENSÁVEL COMO O AR, \nSANTO É O SENHOR, \nVINDE ADORÁ-LO COM LOUVOR.\n\nHOSANA AO SALVADOR, \n(UNIDOS TODOS NÓS) VARÕES\nCANTEMOS EM LOUVOR,\n(CANTEMOS NUMA VOZ) VARÕES\nHOSANA AO NOSSO DEUS,\nHOSANA AO SALVADOR,\nHOSANA AO SENHOR,\nHOSANA AO SENHOR, AO SENHOR.', 'GRANDE É O SENHOR, INCLINAI-VOS TERRA HUMILDEMENTE. GRANDE É O SENHOR ADORAÍ-O SEMPRE ALEGREMENTE. REGOZIJAI! DE CORAÇÃO! ELE É O CRIADOR E NÓS A CRIAÇÃO. EIS QUE VEM DE SUA MÃO: O SOL, A LUA, ESTRELAS E PLANETAS JUSTO ÉS EM SUAS LEIS; TUFÕES ACALMAM, ONDAS OBEDECEM-NO SOSSEGA O MAR AO SEU MANDAR, MONTANHAS SE DESFARÃO, DESERTOS BROTARÃO BOAS COLHEITAS, A CRIAÇÃO LHE APRAZ, O SOL DIRIGE A LUA, ESCONDE A NOITE EM DIA FAZ. GRANDE É O SENHOR, O SOBERANO LÁ NOS CÉUS, E REI DOS REIS ONIPOTENTE E SANTO DEUS, GRANDE EM SEU PODER, E MASJESTOSO EM SEU SABER. SUA BONDADE É IMENSURAVÉL COMO O MAR, SEU AMPLO AMOR É INDISPENSÁVEL COMO O AR, SANTO É O SENHOR, VINDE ADORÁ-LO COM LOUVOR. HOSANA AO SALVADOR, (UNIDOS TODOS NÓS) VARÕES CANTEMOS EM LOUVOR, (CANTEMOS NUMA VOZ) VARÕES HOSANA AO NOSSO DEUS, HOSANA AO SALVADOR, HOSANA AO SENHOR, HOSANA AO SENHOR, AO SENHOR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'HOJE EU DESCOBRI', 'HOJE EU DESCOBRI A FORMA DE VIVER FELIZ, \nA CRISTO EU ENTREGUEI A MINHA VIDA E ESPEREI,\nA SOLUÇÃO DOS MEUS PROBLEMAS.\nE A RESPOSTA EM CRISTO EU ACHEI.\n\nHOJE EU VIVO BEM E A SUA PAZ EU ENCONTREI, \nO FARDO TÃO PESADO \nNO CAMINHO EU DEIXEI.\nELE O TOMOU EM SUA CRUZ E DISSE-ME: \n“FILHO MEU, TE ALIVIAREI”.\n\nCORO\nFICA SENHOR, EM MEU CORAÇÃO,\nMORA SENHOR, EM MEU CORAÇÃO.\n\nINSTRUMENTOS\n\nHOJE EU VIVO BEM E A SUA PAZ EU ENCONTREI, \nO FARDO TÃO PESADO \nNO CAMINHO EU DEIXEI.\nELE O TOMOU EM SUA CRUZ E DISSE-ME: \n“FILHO MEU, TE ALIVIAREI”.\n\nCORO\nFICA SENHOR, EM MEU CORAÇÃO,\nMORA SENHOR, EM MEU CORAÇÃO.', 'HOJE EU DESCOBRI A FORMA DE VIVER FELIZ, A CRISTO EU ENTREGUEI A MINHA VIDA E ESPEREI, A SOLUÇÃO DOS MEUS PROBLEMAS. E A RESPOSTA EM CRISTO EU ACHEI. HOJE EU VIVO BEM E A SUA PAZ EU ENCONTREI, O FARDO TÃO PESADO NO CAMINHO EU DEIXEI. ELE O TOMOU EM SUA CRUZ E DISSE-ME: FILHO MEU, TE ALIVIAREI. CORO FICA SENHOR, EM MEU CORAÇÃO, MORA SENHOR, EM MEU CORAÇÃO. INSTRUMENTOS CORO FICA SENHOR, EM MEU CORAÇÃO, MORA SENHOR, EM MEU CORAÇÃO.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'JERUSALÉM DE OURO', 'JERUSALÉM, EM TUAS RUAS \nEU OUÇO A ECOAR\nO SOM DA HARPA DE DAVI,\nMINHA ALMA A ACALENTAR\nPOR ENTRE AS TUAS OLIVEIRAS, \nO VENTO VEM CONTAR\nHISTÓRIAS DA VELHA CIDADE \nQUE OS MUROS VÃO GUARDAR.\n\nCORO\nJERUSALÉM, JERUSALÉM,\nBELEZA E COR DE ISRAEL,\nTU ÉS O ORGULHO DO TEU DEUS,\nEMANUEL.\nBIS\n\nAINDA OUÇO EM TUAS RUAS \nO SOM DA MULTIDÃO,\nDE PEREGRINOS E AMANTES \nEM BUSCA DE OBLAÇÃO,\nNO SANTO TEMPLO E EM TUAS FONTES\nSE ENCONTRAVA A PAZ,\nJERUSALÉM, QUEM TE CONHECE \nNÃO TE ESQUECE MAIS.\n\nCORO\nJERUSALÉM, JERUSALÉM,\nBELEZA E COR DE ISRAEL,\nTU ÉS O ORGULHO DO TEU DEUS,\nEMANUEL.\nBIS\n\nJERUSALÉM, EM TUAS PRECES \nLEMBRA DOS FILHOS TEUS\nQUE AINDA NÃO TE CONHECERAM \nMAS AMAM O TEU DEUS,\nSÃO VELHOS, JOVENS E MENINOS \nDISTANTES DO SEU LAR,\nQUE NÃO PISARAM TUAS RUAS \nMAS VIVEM POR TE AMAR.\n\nCORO\nJERUSALÉM, JERUSALÉM,\nBELEZA E COR DE ISRAEL,\nTU ÉS O ORGULHO DO TEU DEUS,\nEMANUEL.\nBIS', 'JERUSALÉM, EM TUAS RUAS EU OUÇO A ECOAR O SOM DA HARPA DE DAVI, MINHA ALMA A ACALENTAR POR ENTRE AS TUAS OLIVEIRAS, O VENTO VEM CONTAR HISTÓRIAS DA VELHA CIDADE QUE OS MUROS VÃO GUARDAR. CORO JERUSALÉM, JERUSALÉM, BELEZA E COR DE ISRAEL, TU ÉS O ORGULHO DO TEU DEUS, EMANUEL. AINDA OUÇO EM TUAS RUAS O SOM DA MULTIDÃO, DE PEREGRINOS E AMANTES EM BUSCA DE OBLAÇÃO, NO SANTO TEMPLO E EM TUAS FONTES SE ENCONTRAVA A PAZ, JERUSALÉM, QUEM TE CONHECE NÃO TE ESQUECE MAIS. JERUSALÉM, EM TUAS PRECES LEMBRA DOS FILHOS TEUS QUE AINDA NÃO TE CONHECERAM MAS AMAM O TEU DEUS, SÃO VELHOS, JOVENS E MENINOS DISTANTES DO SEU LAR, QUE NÃO PISARAM TUAS RUAS MAS VIVEM POR TE AMAR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'JESUS, SEMPRE TE AMO', 'JESUS, SEMPRE TE AMO \nPORQUE SEI QUE ÉS MEU;\nFELIZ EU TE RENDO LOUVOR \nQUE É SÓ TEU.\n\nMEU MESTRE DIVINO, \nSENHOR E MEU DEUS, \nPRA SEMPRE HEI DE AMAR-TE \nMEU CRISTO, MEU REI.\n\nSERVAS\n\nVARÕES\n\nEU TE AMO PORQUE TU MORRESTE POR MIM; \nEU TE AMO PORQUE TEU AMOR NÃO TEM FIM.\nEM TI CONFIANÇA PRA SEMPRE TEREI; \nPRA SEMPRE HEI DE AMAR-TE MEU REI.\n\nEU TE AMO NA VIDA, NA MORTE TAMBÉM;\nSEMPRE HEI DE LOUVAR-TE NA GLÓRIA DE ALÉM;\nAGORA E PRA SEMPRE POR TI VIVEREI;\nSEMPRE HEI DE AMAR-TE, \nMEU CRISTO, MEU REI.\nSEMPRE HEI DE AMAR-TE, \nMEU CRISTO, MEU REI.', 'JESUS, SEMPRE TE AMO PORQUE SEI QUE ÉS MEU; FELIZ EU TE RENDO LOUVOR QUE É SÓ TEU. MEU MESTRE DIVINO, SENHOR E MEU DEUS, PRA SEMPRE HEI DE AMAR-TE MEU CRISTO, MEU REI. EU TE AMO PORQUE TU MORRESTE POR MIM; EU TE AMO PORQUE TEU AMOR NÃO TEM FIM. EM TI CONFIANÇA PRA SEMPRE TEREI; PRA SEMPRE HEI DE AMAR-TE MEU REI. EU TE AMO NA VIDA, NA MORTE TAMBÉM; SEMPRE HEI DE LOUVAR-TE NA GLÓRIA DE ALÉM; AGORA E PRA SEMPRE POR TI VIVEREI; SEMPRE HEI DE AMAR-TE, MEU CRISTO, MEU REI. SEMPRE HEI DE AMAR-TE, MEU CRISTO, MEU REI.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'JESUS, SOMENTE TU', 'JESUS, SOMENTE TU, \nA MINHA SEDE PODE SACIAR,\nNADA TEM MAIS VALOR, \nDO QUE BUSCAR TUA FACE E TE ADORAR.\n\nCORO\nSÓ TU ÉS A LUZ DA MINHA VIDA,\nSÓ TU DÁS SENTIDO AO MEU VIVER,\nSÓ TU VÊS A MINHA ALMA FERIDA,\nSÓ TU TENS PODER PRA EU VENCER.\n\nJESUS, EU TE AMO,\nVEM MINHA SEDE AGORA SACIAR;\nENCHE MINH’ALMA COM TEU AMOR \nATÉ EU TRANSBORDAR.\n\nCORO\nSÓ TU ÉS A LUZ DA MINHA VIDA,\nSÓ TU DÁS SENTIDO AO MEU VIVER,\nSÓ TU VÊS A MINHA ALMA FERIDA,\nSÓ TU TENS PODER PRA EU VENCER.\nBIS', 'JESUS, SOMENTE TU, A MINHA SEDE PODE SACIAR, NADA TEM MAIS VALOR, DO QUE BUSCAR TUA FACE E TE ADORAR. CORO SÓ TU ÉS A LUZ DA MINHA VIDA, SÓ TU DÁS SENTIDO AO MEU VIVER, SÓ TU VÊS A MINHA ALMA FERIDA, SÓ TU TENS PODER PRA EU VENCER. JESUS, EU TE AMO, VEM MINHA SEDE AGORA SACIAR; ENCHE MINH’ALMA COM TEU AMOR ATÉ EU TRANSBORDAR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'JUNTO A TI', 'JUNTO A TI, QUERO ESTAR, \nEM TUA CASA HABITAR, \nTEU AMOR EM MIM SENTIR,\nTUA VOZ SUAVE OUVIR, JESUS!\n\nCORO\nTU ÉS TUDO QUE EU \nALMEJO MINHA VIDA E LUZ, \nCADA DIA TE DESEJO MAIS \nE MAIS, JESUS.\n\nPELO ALTAR JÁ PASSEI\nE NO SANGUE ME LAVEI,\nPOSSO AGORA TE ADORAR\nMINHA SEDE SACIAR, JESUS!\n\nCORO (2X)\nTU ÉS TUDO QUE EU \nALMEJO MINHA VIDA E LUZ, \nCADA DIA TE DESEJO MAIS \nE MAIS, JESUS.\n\nINSTRUMENTOS\n\nCORO\nTU ÉS TUDO QUE EU \nALMEJO MINHA VIDA E LUZ, \nCADA DIA TE DESEJO MAIS \nE MAIS, JESUS.', 'JUNTO A TI, QUERO ESTAR, EM TUA CASA HABITAR, TEU AMOR EM MIM SENTIR, TUA VOZ SUAVE OUVIR, JESUS! CORO TU ÉS TUDO QUE EU ALMEJO MINHA VIDA E LUZ, CADA DIA TE DESEJO MAIS E MAIS, JESUS. PELO ALTAR JÁ PASSEI E NO SANGUE ME LAVEI, POSSO AGORA TE ADORAR MINHA SEDE SACIAR, JESUS! TU ÉS TUDO QUE EU ALMEJO MINHA VIDA E LUZ, CADA DIA TE DESEJO MAIS E MAIS, JESUS. INSTRUMENTOS', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
