INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'MAIS QUE MARAVILHOSO', 'JESUS NOS PROMETEU UM\nCONSELHEIRO SER,\nO FORTE DEUS, PRÍNCIPE DA\nPAZ, E COMO UM PAI SERIA\nPARA DAR-NOS AMOR, TÃO\nGRANDE QUE NÃO CESSARÁ\nJAMAIS.\n\nSERVAS\n\nDESCOBRI QUE JESUS CRISTO\nÉ UM DEUS FIEL.\nO QUE NOS PROMETEU\nCUMPRIU ENFIM.\nENTÃO, A MEU JESUS DESEJO\nCONFESSAR O QUE ELE\nREPRESENTA PARA MIM.\n\nVARÕES\n\nCORO\nMARAVILHOSO DEUS,\nMUITO MAIS QUE MEU SER \nPOSSA IMAGINAR, MUITO MAIS DO QUE SEI.\nPOIS ELE É MAIS, BEM MUITO MAIS DO QUE SONHEI.\nNELE ENCONTREI TUDO QUE PROCURAVA\nO QUE PRECISAVA SÓ NELE ACHEI:\nMARAVILHOSO, MUITO MAIS PRA MIM;\nDEUS MILAGROSO É, SEMPRE SERÁ\nMARAVILHOSO DEUS,\nÉ O QUE CRISTO É PRA MIM.\n\nAO RELEMBRAR QUE UM DIA,\nO REI DA GLÓRIA EM FORMA\nDE UM HOMEM SE TORNOU,\nFELIZ ME FAZ POIS SEI\nQUANTO ME AMAS, AO\nPENSAR QUEM ELE É\nE QUEM EU SOU.\n\nVARÕES\n\nCORO\nMARAVILHOSO DEUS,\nMUITO MAIS QUE MEU SER POSSA\nIMAGINAR, MUITO MAIS DO QUE SEI.\nPOIS ELE É MAIS, BEM MUITO MAIS DO QUE SONHEI.\nNELE ENCONTREI TUDO QUE PROCURAVA \nO QUE PRECISAVA SÓ NELE ACHEI:\nMARAVILHOSO, MUITO MAIS PRA MIM;\nDEUS MILAGROSO É, SEMPRE SERÁ \nMARAVILHOSO DEUS,\nÉ O QUE CRISTO É PRA MIM.\n\nFINAL:\nNELE ENCONTREI TUDO QUE\nPROCURAVA, O QUE PRECISAVA SÓ\nNELE ACHEI: MARAVILOSO, MUITO MAIS, PRA MIM;\nDEUS MILAGROSO É, SEMPRE SERÁ\nMARAVILHOSO DEUS,\nPODEROSO DEUS É, SEMPRE SERÁ\nMARAVILHOSO DEUS, PODEROSO DEUS, \nÉ O QUE CRISTO É PRA MIM.\nMARAVILHOSO É PRA MIM, PRA MIM.', 'JESUS NOS PROMETEU UM CONSELHEIRO SER, O FORTE DEUS, PRÍNCIPE DA PAZ, E COMO UM PAI SERIA PARA DAR-NOS AMOR, TÃO GRANDE QUE NÃO CESSARÁ JAMAIS. DESCOBRI QUE JESUS CRISTO É UM DEUS FIEL. O QUE NOS PROMETEU CUMPRIU ENFIM. ENTÃO, A MEU JESUS DESEJO CONFESSAR O QUE ELE REPRESENTA PARA MIM. CORO MARAVILHOSO DEUS, MUITO MAIS QUE MEU SER POSSA IMAGINAR, MUITO MAIS DO QUE SEI. POIS ELE É MAIS, BEM MUITO MAIS DO QUE SONHEI. NELE ENCONTREI TUDO QUE PROCURAVA O QUE PRECISAVA SÓ NELE ACHEI: MARAVILHOSO, MUITO MAIS PRA MIM; DEUS MILAGROSO É, SEMPRE SERÁ MARAVILHOSO DEUS, É O QUE CRISTO É PRA MIM. AO RELEMBRAR QUE UM DIA, O REI DA GLÓRIA EM FORMA DE UM HOMEM SE TORNOU, FELIZ ME FAZ POIS SEI QUANTO ME AMAS, AO PENSAR QUEM ELE É E QUEM EU SOU. NELE ENCONTREI TUDO QUE PROCURAVA, O QUE PRECISAVA SÓ NELE ACHEI: MARAVILOSO, MUITO MAIS, PRA MIM; DEUS MILAGROSO É, SEMPRE SERÁ MARAVILHOSO DEUS, PODEROSO DEUS É, SEMPRE SERÁ MARAVILHOSO DEUS, PODEROSO DEUS, É O QUE CRISTO É PRA MIM. MARAVILHOSO É PRA MIM, PRA MIM.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'MIL GLÓRIAS', 'SENHOR JESUS EXALTADO SEJA\nSEMPRE O TEU SANTO NOME.\nNOME SANTO E BOM. (ÉS TU SENHOR)\nNOME SANTO E BOM. (É SALVADOR)\nEXALTADO SEJA SEMPRE \nO NOME DE JESUS.\n\nEXALTADO SEJA SEMPRE \nO TEU NOME, Ó JESUS.\nTU QUE HABITAS LÁ NA GLÓRIA,\nÓ AMADO JESUS.\n\nGLÓRIA A JESUS, GLÓRIA A JESUS\nJESUS CRISTO ESTÁ NA GLÓRIA\nREVESTIDO DE PODER.\nBIS\nBIS\n\nGLÓRIA A JESUS CRISTO, ELE É O SALVADOR.\nFOI QUEM POR NÓS COM PODE NOS RESGAROU.\nGLÓRIA AO PAI CELESTE, \nGLÓRIA A JESUS CRISTO, O FILHO,\nQUE SALVOU AS NOSSAS ALMAS,\nCOM PODER NOS RESGATOU.\nELE ESTÁ SENTADO, REVESTIDO \nDE PODER ESPERANDO PELO PAI \nPRA USA IGREJA RECEBER.\n\nE ESTA JORNADA VAMOS ENCERRAR.\nE NOSSA COROA LÁ NO CÉU VAMOS GANHAR.\nTE PREPARA, Ó CRENTE PRA ENCONTRAR O SALVADOR,\nPOIS EM BREVE ESTA IGREJA VAI SUBIR COM O SENHOR.\nELE VEM NAS NUVENS COM PODER E \nGRANDE GLÓRIA PRA BUSCAR A IGREJA\nSANTA QUE RECEBEU A VITÓRIA.\n\nGLÓRIA, GLÓRIA, JESUS,\nCRISTO ESTÁ NA GLÓRIA \nREVESTIDO DE PODER\n\nAO SENHOR DAÍ GLÓRIA,\nDAÍ GLÓRIA.\nPRA SEMPRE, AMÉM.\nBIS\n\nABENÇOA-NOS SENHOR,\nDERRAMA SOBRE NÓS TUA PAZ\nABENÇOA-NOS SENHOR,\nDERRAMA SOBRE NÓS TEU AMOR.', 'SENHOR JESUS EXALTADO SEJA SEMPRE O TEU SANTO NOME. NOME SANTO E BOM. (ÉS TU SENHOR) NOME SANTO E BOM. (É SALVADOR) EXALTADO SEJA SEMPRE O NOME DE JESUS. EXALTADO SEJA SEMPRE O TEU NOME, Ó JESUS. TU QUE HABITAS LÁ NA GLÓRIA, Ó AMADO JESUS. GLÓRIA A JESUS, GLÓRIA A JESUS JESUS CRISTO ESTÁ NA GLÓRIA REVESTIDO DE PODER. GLÓRIA A JESUS CRISTO, ELE É O SALVADOR. FOI QUEM POR NÓS COM PODE NOS RESGAROU. GLÓRIA AO PAI CELESTE, GLÓRIA A JESUS CRISTO, O FILHO, QUE SALVOU AS NOSSAS ALMAS, COM PODER NOS RESGATOU. ELE ESTÁ SENTADO, REVESTIDO DE PODER ESPERANDO PELO PAI PRA USA IGREJA RECEBER. E ESTA JORNADA VAMOS ENCERRAR. E NOSSA COROA LÁ NO CÉU VAMOS GANHAR. TE PREPARA, Ó CRENTE PRA ENCONTRAR O SALVADOR, POIS EM BREVE ESTA IGREJA VAI SUBIR COM O SENHOR. ELE VEM NAS NUVENS COM PODER E GRANDE GLÓRIA PRA BUSCAR A IGREJA SANTA QUE RECEBEU A VITÓRIA. GLÓRIA, GLÓRIA, JESUS, CRISTO ESTÁ NA GLÓRIA REVESTIDO DE PODER AO SENHOR DAÍ GLÓRIA, DAÍ GLÓRIA. PRA SEMPRE, AMÉM. ABENÇOA-NOS SENHOR, DERRAMA SOBRE NÓS TUA PAZ ABENÇOA-NOS SENHOR, DERRAMA SOBRE NÓS TEU AMOR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'NO EXÍLIO', 'NO EXÍLIO BEM DISTANTE,\nMINHA TERRA VOU CANTAR,\nÉ DE TODAS A MAIS BELA,\nCOM ELA VIVO A SONHAR,\nÉ MAIS SALUBRE O SEU CLIMA\nDO QUE DESTA SOLIDÃO.\n\nCORO\nEU TENHO SAUDADES DELA\nOH! QUANDO IREI PRA SIÃO?\nOH! BELA SIÃO, MINHA TERRA É SIÃO;\nEU TENHO SAUDADES DELA,\nOH! QUANDO IREI PRA SIÃO?\n\nLÁ NÃO HÁ CHORO E NEM GUERRA,\nE NEM MESMO REVOLUÇÃO,\nPASTA O LOBO COM O CORDEIRO\nE O NOVILHO COM O LEÃO,\nE O MENINO APASCENTA\nCOM O CAJADO NA MÃO.\n\nCORO\nEU TENHO SAUDADES DELA\nOH! QUANDO IREI PRA SIÃO?\nOH! BELA SIÃO, MINHA TERRA É SIÃO;\nEU TENHO SAUDADES DELA,\nOH! QUANDO IREI PRA SIÃO?\n\nCORO\nOH! BELA SIÃO, MINHA TERRA É SIÃO;\nEU TENHO SAUDADES DELA,\nOH! QUANDO IREI PRA SIÃO?\n\nFINAL: \nOH! QUANDO IREI PRA SIÃO?', 'NO EXÍLIO BEM DISTANTE, MINHA TERRA VOU CANTAR, É DE TODAS A MAIS BELA, COM ELA VIVO A SONHAR, É MAIS SALUBRE O SEU CLIMA DO QUE DESTA SOLIDÃO. CORO EU TENHO SAUDADES DELA OH! QUANDO IREI PRA SIÃO? OH! BELA SIÃO, MINHA TERRA É SIÃO; EU TENHO SAUDADES DELA, OH! QUANDO IREI PRA SIÃO? LÁ NÃO HÁ CHORO E NEM GUERRA, E NEM MESMO REVOLUÇÃO, PASTA O LOBO COM O CORDEIRO E O NOVILHO COM O LEÃO, E O MENINO APASCENTA COM O CAJADO NA MÃO. CORO OH! BELA SIÃO, MINHA TERRA É SIÃO; EU TENHO SAUDADES DELA, OH! QUANDO IREI PRA SIÃO?  OH! QUANDO IREI PRA SIÃO?', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'O NOVO CÉU', 'DEUS MESMO REVELOU O NOVO CÉU;\nDEUS MESMO REVELOU O NOVO CÉU\nE A NOVA TERRA,\nESTA TERRA NÃO MAIS EXISTIRÁ;\nESTA TERRA NÃO MAIS EXISTIRÁ,\n\nNOVO É ESTE CÉU E O MAR TAMBÉM SE DESFEZ,\nO EVANGELISTA VIU A CIDADE DO SENHOR,\nCIDADE DO SENHOR, CIDADE DO SENHOR,\nQUE APRESENTAVA A GLÓRIA DO SENHOR.\n\nNO BRILHO DE DEUS E NOSSO ETERNO PAI;\nNO BRILHO DE DEUS E NOSSO ETERNO PAI;\nNO BRILHO DE NOSSO ETERNO \nPAI E REDENTOR DOS CÉUS,\nNOSSO PAI DOS CÉUS.\n\nE SOOU UMA VOZ MUI FORTE, \nDIZENDO: EIS DE DEUS A HABITAÇÃO \nENTRE OS HOMENS COM OS SEUS SERVOS,\nPOIS DEUS VAI HABITAR, VAI MORAR COM OS SEUS.\nENTÃO VEREIS, ENTÃO VEREIS, ENTÃO VEREIS, \nENTÃO VEREIS A GLÓRIA DIVINA,\nE DEUS VOS GUIARÁ E SERÁ \nO VOSSO PAI.\n\nTODA LÁGRIMA ELE LIMPARÁ;\nTODA LÁGRIMA ELE LIMPARÁ;\nTODA LÁGRIMA ELE LIMPARÁ;\nTODA LÁGRIMA ELE LIMPARÁ.\n\nSIM, DOS VOSSOS OLHOS TODA \nA LÁGRIMA LIMPARÁ, LIMPARÁ, \nLIMPARÁ, LIMPARÁ.\n\nLOUVAI A DEUS, LOUVAI A DEUS, \nLOUVAI A DEUS, LOUVAI A DEUS, \nALELUIA.\nBIS', 'DEUS MESMO REVELOU O NOVO CÉU; DEUS MESMO REVELOU O NOVO CÉU E A NOVA TERRA, ESTA TERRA NÃO MAIS EXISTIRÁ; ESTA TERRA NÃO MAIS EXISTIRÁ, NOVO É ESTE CÉU E O MAR TAMBÉM SE DESFEZ, O EVANGELISTA VIU A CIDADE DO SENHOR, CIDADE DO SENHOR, CIDADE DO SENHOR, QUE APRESENTAVA A GLÓRIA DO SENHOR. NO BRILHO DE DEUS E NOSSO ETERNO PAI; NO BRILHO DE DEUS E NOSSO ETERNO PAI; NO BRILHO DE NOSSO ETERNO PAI E REDENTOR DOS CÉUS, NOSSO PAI DOS CÉUS. E SOOU UMA VOZ MUI FORTE, DIZENDO: EIS DE DEUS A HABITAÇÃO ENTRE OS HOMENS COM OS SEUS SERVOS, POIS DEUS VAI HABITAR, VAI MORAR COM OS SEUS. ENTÃO VEREIS, ENTÃO VEREIS, ENTÃO VEREIS, ENTÃO VEREIS A GLÓRIA DIVINA, E DEUS VOS GUIARÁ E SERÁ O VOSSO PAI. TODA LÁGRIMA ELE LIMPARÁ; TODA LÁGRIMA ELE LIMPARÁ; TODA LÁGRIMA ELE LIMPARÁ; TODA LÁGRIMA ELE LIMPARÁ. SIM, DOS VOSSOS OLHOS TODA A LÁGRIMA LIMPARÁ, LIMPARÁ, LIMPARÁ, LIMPARÁ. LOUVAI A DEUS, LOUVAI A DEUS, LOUVAI A DEUS, LOUVAI A DEUS, ALELUIA.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'O SENHOR É MINHA ROCHA', 'QUANDO PARO PRA PENSAR\nEM TEU GRANDE AMOR\nPENSO: “PODE DEUS AMAR A MIM PECADOR?”\nAO SENTIR O TEU PODER MANIFESTO EM MIM, \nEU NÃO CONSIGO COMPREENDER\nPORQUE ME AMAS TANTO ASSIM.\n\nPOIS O SENHOR É MINHA ROCHA\nMEU REFÚGIO É O SENHOR.\nNÃO TEMEREI, MAS FORTE SEREI\nQUANDO O MAL SOBRE MIM VIER\nPOIS O SENHOR É MINHA FORTALEZA \nE MEU PROTETOR SE EM PROVAÇÃO, \nME AJUDAS GRANDE É O AMOR \nQUE TENS POR MIM.\n\nQUANDO OLHO PARA OS CÉUS, \nOBRA DE TUAS MÃOS,\nVEJO O QUANTO ÉS GRANDE, \nÓ DEUS POR TUA CRIAÇÃO\nÓ MEU PAI, SENHOR DA LUZ, \nSOU UM PECADOR MAS SEI QUE \nO SANGUE DE JESUS \nFAZ DE MIM UM VENCEDOR!\n\nPOIS O SENHOR É MINHA ROCHA\nMEU REFÚGIO É O SENHOR.\nNÃO TEMEREI, MAS FORTE SEREI\nQUANDO O MAL SOBRE MIM VIER\nPOIS O SENHOR É MINHA FORTALEZA \nE MEU PROTETOR, SE EM PROVAÇÃO ME\nAJUDAS, GRANDE É O AMOR QUE \nTENS POR MIM.\n\nPOR ISSO PEÇO, Ó PAI, \nCOMIGO SÊ NESTA LUTA AQUI.\nLOUVOR, ENTÃO, HEI DE RENDER\nEM GRATIDÃO A TI. \nA TI, Ó PAI DE AMOR!\nPAI DE AMOR!\n\nPOIS O SENHOR É MINHA ROCHA.\nMEU REFÚGIO É O SENHOR\nNÃO TEMEREI, MAS FORTE SEREI\nQUANDO O MAL SOBRE MIM VIER\nPOIS O SENHOR É MINHA FORTALEZA \nE MEU PROTETOR SE EM PROVAÇÃO \nME AJUDAS GRANDE É O AMOR \nQUE TENS POR MIM!\nFINAL: SENHOR, TU ÉS A MINHA ROCHA.', 'QUANDO PARO PRA PENSAR EM TEU GRANDE AMOR PENSO: PODE DEUS AMAR A MIM PECADOR? AO SENTIR O TEU PODER MANIFESTO EM MIM, EU NÃO CONSIGO COMPREENDER PORQUE ME AMAS TANTO ASSIM. POIS O SENHOR É MINHA ROCHA MEU REFÚGIO É O SENHOR. NÃO TEMEREI, MAS FORTE SEREI QUANDO O MAL SOBRE MIM VIER POIS O SENHOR É MINHA FORTALEZA E MEU PROTETOR SE EM PROVAÇÃO, ME AJUDAS GRANDE É O AMOR QUE TENS POR MIM. QUANDO OLHO PARA OS CÉUS, OBRA DE TUAS MÃOS, VEJO O QUANTO ÉS GRANDE, Ó DEUS POR TUA CRIAÇÃO Ó MEU PAI, SENHOR DA LUZ, SOU UM PECADOR MAS SEI QUE O SANGUE DE JESUS FAZ DE MIM UM VENCEDOR! POIS O SENHOR É MINHA ROCHA MEU REFÚGIO É O SENHOR. NÃO TEMEREI, MAS FORTE SEREI QUANDO O MAL SOBRE MIM VIER POIS O SENHOR É MINHA FORTALEZA E MEU PROTETOR, SE EM PROVAÇÃO ME AJUDAS, GRANDE É O AMOR QUE TENS POR MIM. POR ISSO PEÇO, Ó PAI, COMIGO SÊ NESTA LUTA AQUI. LOUVOR, ENTÃO, HEI DE RENDER EM GRATIDÃO A TI. A TI, Ó PAI DE AMOR! PAI DE AMOR! POIS O SENHOR É MINHA ROCHA. MEU REFÚGIO É O SENHOR NÃO TEMEREI, MAS FORTE SEREI QUANDO O MAL SOBRE MIM VIER POIS O SENHOR É MINHA FORTALEZA E MEU PROTETOR SE EM PROVAÇÃO ME AJUDAS GRANDE É O AMOR QUE TENS POR MIM!  SENHOR, TU ÉS A MINHA ROCHA.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'ORAÇÃO PELA PÁTRIA', 'POR NOSSA PÁTRIA ORAMOS A TI, \nÓ SUPREMO DEUS!\nPOR NOSSO LAR CLAMAMOS A TI, \nÓ REI DO CÉUS.\nGOVERNA A PÁTRIA COM AMOR,\nSÊ NOSSO AMPARO, Ó SALVADOR. \nDERRAMA GRAÇA COM FAVOR,\nDEUS SALVE A PÁTRIA!\n\nÀ PÁTRIA QUE NOS DESTE,\nESTENDE TUA MÃO.\nCOM BÊNÇÃOS NOS REVESTE,\nDÁ-NOS PAZ,CONSOLAÇÃO.\nSUSTENTA A ORDEM NACIONAL, \nO BOM GOVERNO IMPARCIAL; \nE DÁ-NOS GRAÇA DIVINAL,\nDEUS SALVE A PÁTRIA!\n\nDÁ-NOS REAL CIVISMO,\nFIEL, CONSTANTE, AUDAZ;\nPROMOVE O CRISTIANISMO \nDO PRÍNCIPE DA PAZ;\nA PÁTRIA LIVRA DO MAL,\nSÊ NOSSO AUXÍLIO DIVINAL,\nDIRIGE COM PODER REAL\nDEUS SALVE A PÁTRIA!\n\nA TUA IGREJA INFLAMA COM \nZELO, E TERNO AMOR,\nE SEJA O SEU PROGRAMA, \nCUMPRIDO COM VIGOR.\nENTÃO, OS SALVOS DE JESUS,\nLUTANDO FIRMES PELA CRUZ,\nDIFUNDIRÃO DE CRISTO,\nA LUZ POR TODA A PÁTRIA! \nAMÉM.', 'POR NOSSA PÁTRIA ORAMOS A TI, Ó SUPREMO DEUS! POR NOSSO LAR CLAMAMOS A TI, Ó REI DO CÉUS. GOVERNA A PÁTRIA COM AMOR, SÊ NOSSO AMPARO, Ó SALVADOR. DERRAMA GRAÇA COM FAVOR, DEUS SALVE A PÁTRIA! À PÁTRIA QUE NOS DESTE, ESTENDE TUA MÃO. COM BÊNÇÃOS NOS REVESTE, DÁ-NOS PAZ,CONSOLAÇÃO. SUSTENTA A ORDEM NACIONAL, O BOM GOVERNO IMPARCIAL; E DÁ-NOS GRAÇA DIVINAL, DEUS SALVE A PÁTRIA! DÁ-NOS REAL CIVISMO, FIEL, CONSTANTE, AUDAZ; PROMOVE O CRISTIANISMO DO PRÍNCIPE DA PAZ; A PÁTRIA LIVRA DO MAL, SÊ NOSSO AUXÍLIO DIVINAL, DIRIGE COM PODER REAL DEUS SALVE A PÁTRIA! A TUA IGREJA INFLAMA COM ZELO, E TERNO AMOR, E SEJA O SEU PROGRAMA, CUMPRIDO COM VIGOR. ENTÃO, OS SALVOS DE JESUS, LUTANDO FIRMES PELA CRUZ, DIFUNDIRÃO DE CRISTO, A LUZ POR TODA A PÁTRIA! AMÉM.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'OS CÉUS DECLARAM A GLÓRIA DE DEUS', 'OS CÉUS DECLARAM A GLÓRIA DE DEUS,\nPODER ETERNO DO SENHOR.\nOS CÉUS, A TERRA, O MAR, \nTODO O SER CANTA O TEU LOUVOR,\nO TEU LOUVOR! Ó DEUS! Ó DEUS!  	\nÉS GRANDE REI DO UNIVERSO, \nDA VIDA O GRANDE AUTOR, SENHOR!\n\nOS CÉUS DECLARAM A GLÓRIA DE DEUS,\nÓ DEUS, MEU REI E SALVADOR,\nÓ DEUS, MEU PAI E SALVADOR!\n\nINSTRUMENTOS\n\nOS CÉUS DECLARAM A GLÓRIA DE DEUS,\nPODER ETERNO DO SENHOR.\nOS CÉUS, A TERRA, O MAR, \nTODO O SER CANTA O TEU LOUVOR,\nO TEU LOUVOR! Ó DEUS! Ó DEUS!  	\nÉS GRANDE REI DO UNIVERSO, \nDA VIDA O GRANDE AUTOR, SENHOR!\n\nOS CÉUS DECLARAM A GLÓRIA DE DEUS,\nÓ DEUS, MEU REI E SALVADOR,\nÓ DEUS, MEU PAI E SALVADOR!', 'OS CÉUS DECLARAM A GLÓRIA DE DEUS, PODER ETERNO DO SENHOR. OS CÉUS, A TERRA, O MAR, TODO O SER CANTA O TEU LOUVOR, O TEU LOUVOR! Ó DEUS! Ó DEUS! ÉS GRANDE REI DO UNIVERSO, DA VIDA O GRANDE AUTOR, SENHOR! OS CÉUS DECLARAM A GLÓRIA DE DEUS, Ó DEUS, MEU REI E SALVADOR, Ó DEUS, MEU PAI E SALVADOR! INSTRUMENTOS OS CÉUS DECLARAM A GLÓRIA DE DEUS, Ó DEUS, MEU REI E SALVADOR, Ó DEUS, MEU PAI E SALVADOR!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PAI NOSSO', 'PAI NOSSO QUE ESTÁS NO CÉUS,\nSANTIFICADO SEJA O TEU NOME\nVENHA O TEU REINO,\nSEJA FEITA A TUA VONTADE ASSIM \nNA TERRA COMO NOS CÉUS.\nO PÃO NOSSO DE CADA DIA NOS DÁ \nHOJE E PERDOA AS NOSSAS DÍVIDAS\nASSIM COMO NÓS PERDOAMOS\nAOS NOSSOS DEVEDORES.\n\nE NÃO NOS DEIXES CAIR EM TENTAÇÃO\nMAS LIVRA NOS DO MAL\nPORQUE TEU É O REINO E O PODER, \nE A GLÓRIA, PARA SEMPRE.\nAMÉM.', 'PAI NOSSO QUE ESTÁS NO CÉUS, SANTIFICADO SEJA O TEU NOME VENHA O TEU REINO, SEJA FEITA A TUA VONTADE ASSIM NA TERRA COMO NOS CÉUS. O PÃO NOSSO DE CADA DIA NOS DÁ HOJE E PERDOA AS NOSSAS DÍVIDAS ASSIM COMO NÓS PERDOAMOS AOS NOSSOS DEVEDORES. E NÃO NOS DEIXES CAIR EM TENTAÇÃO MAS LIVRA NOS DO MAL PORQUE TEU É O REINO E O PODER, E A GLÓRIA, PARA SEMPRE. AMÉM.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PEGA A TUA PARTE', 'CORO\nPEGA A TUA PARTE, IRMÃO, A MESA ESTÁ BEM FARTA,\nPEGUE EM ABUNDÂNCIA POIS A TUA ESPERANÇA \nCRISTO NÃO DEIXOU MORRER;\nENCHA O TEU CELEIRO DE BÊNÇÃOS ESPIRITUAIS,\nDEIXA QUE TRANSBORDE, QUE DERRAME \nATÉ QUE SOBRE, BENÇÃOS \nNUNCA SÃO DE MAIS.\n\nA MESA PREPARADA EM NOSSA FRENTE, \nO ANJO COM A BANDEJA EM SUAS MÃOS, \nDOS CRENTES A CABEÇA É UNGIDA, \nBRASAS VIVAS QUEIMAM O CORAÇÃO;\nCRISTO A CADA UM VAI PERGUNTANDO: \n“QUE QUERES QUE TE FAÇA, MEU IRMÃO”? \nHOJE AS BÊNÇÃOS VÊM EM ABUNDÂNCIA,\nESTENDE A JESUS CRISTO A TUA MÃO.\n\nCORO\nPEGA A TUA PARTE, IRMÃO, A MESA ESTÁ BEM FARTA,\nPEGUE EM ABUNDÂNCIA POIS A TUA ESPERANÇA \nCRISTO NÃO DEIXOU MORRER;\nENCHA O TEU CELEIRO DE BÊNÇÃOS ESPIRITUAIS,\nDEIXA QUE TRANSBORDE, QUE DERRAME \nATÉ QUE SOBRE, BENÇÃOS \nNUNCA SÃO DE MAIS.\n\nO ALIMENTO DESTA FESTA É MUITO PURO, \nAS BÊNÇÃOS VÃO DE CURA À SALVAÇÃO, CEGOS, MUDOS, SURDOS E OPRIMIDOS RECEBEM NESTA FESTA PERFEIÇÃO; \nCRISTO A CADA UM VAI PERGUNTANDO:\n“QUE QUERES QUE TE FAÇA, MEU IRMÃO”? \nHOJE AS BÊNÇÃOS VÊM EM ABUNDÂNCIA, ESTENDE A JESUS CRISTO A TUA MÃO.\n\nCORO\nPEGA A TUA PARTE, IRMÃO, A MESA ESTÁ BEM FARTA,\nPEGUE EM ABUNDÂNCIA POIS A TUA ESPERANÇA \nCRISTO NÃO DEIXOU MORRER;\nENCHA O TEU CELEIRO DE BÊNÇÃOS ESPIRITUAIS,\nDEIXA QUE TRANSBORDE, QUE DERRAME \nATÉ QUE SOBRE, BENÇÃOS NUNCA SÃO DE MAIS.\nBIS', 'CORO PEGA A TUA PARTE, IRMÃO, A MESA ESTÁ BEM FARTA, PEGUE EM ABUNDÂNCIA POIS A TUA ESPERANÇA CRISTO NÃO DEIXOU MORRER; ENCHA O TEU CELEIRO DE BÊNÇÃOS ESPIRITUAIS, DEIXA QUE TRANSBORDE, QUE DERRAME ATÉ QUE SOBRE, BENÇÃOS NUNCA SÃO DE MAIS. A MESA PREPARADA EM NOSSA FRENTE, O ANJO COM A BANDEJA EM SUAS MÃOS, DOS CRENTES A CABEÇA É UNGIDA, BRASAS VIVAS QUEIMAM O CORAÇÃO; CRISTO A CADA UM VAI PERGUNTANDO: QUE QUERES QUE TE FAÇA, MEU IRMÃO? HOJE AS BÊNÇÃOS VÊM EM ABUNDÂNCIA, ESTENDE A JESUS CRISTO A TUA MÃO. O ALIMENTO DESTA FESTA É MUITO PURO, AS BÊNÇÃOS VÃO DE CURA À SALVAÇÃO, CEGOS, MUDOS, SURDOS E OPRIMIDOS RECEBEM NESTA FESTA PERFEIÇÃO; CRISTO A CADA UM VAI PERGUNTANDO: QUE QUERES QUE TE FAÇA, MEU IRMÃO? HOJE AS BÊNÇÃOS VÊM EM ABUNDÂNCIA, ESTENDE A JESUS CRISTO A TUA MÃO.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PELO VALE ESCURO', 'PELO VALE ESCURO, SEGUREI JESUS\nMAS POR TI SEGURO, VENDO A SUA LUZ.\nO MEU PASSO INCERTO, TU DIRIGIRÁS \nE AO SENTIR-TE PERTO, NUNCA PERCO A PAZ.\n\nOS ESPINHOS TANTOS, QUE NOS VEM SANGRAR\nSÃO REMÉDIOS SANTOS, PARA NOS CURAR\nONDE EXISTE A GRAÇA DO BONDOSO DEUS\nTUDO O QUE SE PASSA,NOS CONDUZ AO CÉU.\n\nNÃO HÁ DOR QUE SEJA, SEM DIVINO FIM\nFAZE Ó DEUS QUE A IGREJA, COMPREENDA ASSIM.\nE APESAR DAS TREVAS, POSSO VER SENHOR\nQUE TU MESMO A LEVAS COM IMENSO AMOR.\n\nBREVE A NOITE DESCE, NOITE DE EMAÚS\nE MEU SER CARECE DE TE VER JESUS\nCOMPANHEIRO AMIGO, AO MEU ALDO VEM\nFICA Ó DEUS COMIGO, INFINITO BEM.', 'PELO VALE ESCURO, SEGUREI JESUS MAS POR TI SEGURO, VENDO A SUA LUZ. O MEU PASSO INCERTO, TU DIRIGIRÁS E AO SENTIR-TE PERTO, NUNCA PERCO A PAZ. OS ESPINHOS TANTOS, QUE NOS VEM SANGRAR SÃO REMÉDIOS SANTOS, PARA NOS CURAR ONDE EXISTE A GRAÇA DO BONDOSO DEUS TUDO O QUE SE PASSA,NOS CONDUZ AO CÉU. NÃO HÁ DOR QUE SEJA, SEM DIVINO FIM FAZE Ó DEUS QUE A IGREJA, COMPREENDA ASSIM. E APESAR DAS TREVAS, POSSO VER SENHOR QUE TU MESMO A LEVAS COM IMENSO AMOR. BREVE A NOITE DESCE, NOITE DE EMAÚS E MEU SER CARECE DE TE VER JESUS COMPANHEIRO AMIGO, AO MEU ALDO VEM FICA Ó DEUS COMIGO, INFINITO BEM.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'PRIMEIRO QUERO VER MEU SALVADOR', 'NO CÉU HÁ MUITAS, MUITAS COISAS\nQUE EU ANSEIO VER,\nSUAS MIL BELEZAS, FAUSTO EXPLENDOR,\nMAS AO FRUIR DAS CELESTIAIS,\nMORADAS O PRAZER,\nPRIMEIRO QUERO VER MEU SALVADOR.\n\nCORO\nPRIMEIRO QUERO VER MEU SALVADOR,\nSIM, ANTES DOS REMIDOS AO REDOR,\nENTÃO POR LONGOS DIAS! \nQUE DOCES ALEGRIAS!\nEU QUERO VER PRIMEIRO O SALVADOR.\n\nAS RUAS DE OURO EU QUERO VER, E OS\nSEUS NÓVEIS PORTAIS, EU QUERO VER OS\nPASSOS CELESTIAIS, A ÁRVORE DA VIDA E \nO LINDO RIO DO SENHOR,\nPRIMEIRO QUERO VER MEU SALVADOR.\n\nCORO\nPRIMEIRO QUERO VER MEU SALVADOR,\nSIM, ANTES DOS REMIDOS AO REDOR,\nENTÃO POR LONGOS DIAS! \nQUE DOCES ALEGRIAS!\nEU QUERO VER PRIMEIRO O SALVADOR.\n\nEU QUERO VER OS IRMÃOS E ESTAR COM\nELES NOS JARDINS, E OS DIAS IDOS\nLEMBRAREI COM AMOR, QUERIDOS QUERO\nVER OS QUAIS PARTIRAM ANTES SIM,\nMAS QUERO VER PRIMEIRO O SALVADOR.\n\nCORO\nPRIMEIRO QUERO VER MEU SALVADOR,\nSIM, ANTES DOS REMIDOS AO REDOR,\nENTÃO POR LONGOS DIAS! \nQUE DOCES ALEGRIAS!\nEU QUERO VER PRIMEIRO O SALVADOR.', 'NO CÉU HÁ MUITAS, MUITAS COISAS QUE EU ANSEIO VER, SUAS MIL BELEZAS, FAUSTO EXPLENDOR, MAS AO FRUIR DAS CELESTIAIS, MORADAS O PRAZER, PRIMEIRO QUERO VER MEU SALVADOR. CORO PRIMEIRO QUERO VER MEU SALVADOR, SIM, ANTES DOS REMIDOS AO REDOR, ENTÃO POR LONGOS DIAS! QUE DOCES ALEGRIAS! EU QUERO VER PRIMEIRO O SALVADOR. AS RUAS DE OURO EU QUERO VER, E OS SEUS NÓVEIS PORTAIS, EU QUERO VER OS PASSOS CELESTIAIS, A ÁRVORE DA VIDA E O LINDO RIO DO SENHOR, PRIMEIRO QUERO VER MEU SALVADOR. EU QUERO VER OS IRMÃOS E ESTAR COM ELES NOS JARDINS, E OS DIAS IDOS LEMBRAREI COM AMOR, QUERIDOS QUERO VER OS QUAIS PARTIRAM ANTES SIM, MAS QUERO VER PRIMEIRO O SALVADOR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'QUANDO CHEGO EM TUA CASA', 'QUANDO CHEGO EM TUA CASA\nDOBRO OS MEUS JOELHOS, \nCOMEÇO A ORAR, SINTO ALGO \nTÃO DIVINO, TÃO MARAVILHOSO, \nCOMEÇO A CHORAR, SINTO UM \nCALOR TÃO FORTE QUE VAI \nAQUECENDO O MEU CORAÇÃO, \nSEI QUE É O MEU JESUS E \nJÁ OUVIU MINHA ORAÇÃO.\n\nCORO \nJESUS, VEM TOCAR EM MINHA VIDA\nTRAZENDO UM POUCO DE ALEGRIA\nPARA ESTE POBRE, POBRE CORAÇÃO;\nJESUS, PELA FÉ AGORA EU VEJO\nO ANJO TRAZENDO A VITÓRIA E \nELE ENTREGA EM MINHAS MÃOS.\n\nSE ESTÁ AMARGURADO,\nSE ESTÁ CANSADO DE TANTO SOFRER,\nSUAS FORÇAS JÁ SE FORAM,\nVOCÊ ATÉ PENSA QUE NÃO VAI VENCER,\nLEVANTE SEUS OLHOS AGORA,\nVEJA A VITÓRIA PERTO DE VOCÊ, \nJESUS CRISTO ESTÁ AQUI E ESTÁ \nDIZENDO: VOCÊ VAI VENCER.\n\nCORO \nJESUS, VEM TOCAR EM MINHA VIDA\nTRAZENDO UM POUCO DE ALEGRIA\nPARA ESTE POBRE, POBRE CORAÇÃO;\nJESUS, PELA FÉ AGORA EU VEJO\nO ANJO TRAZENDO A VITÓRIA E \nELE ENTREGA EM MINHAS MÃOS.\nO ANJO TRAZENDO A VITÓRIA E \nELE ENTREGA EM MINHAS MÃOS.', 'QUANDO CHEGO EM TUA CASA DOBRO OS MEUS JOELHOS, COMEÇO A ORAR, SINTO ALGO TÃO DIVINO, TÃO MARAVILHOSO, COMEÇO A CHORAR, SINTO UM CALOR TÃO FORTE QUE VAI AQUECENDO O MEU CORAÇÃO, SEI QUE É O MEU JESUS E JÁ OUVIU MINHA ORAÇÃO. CORO JESUS, VEM TOCAR EM MINHA VIDA TRAZENDO UM POUCO DE ALEGRIA PARA ESTE POBRE, POBRE CORAÇÃO; JESUS, PELA FÉ AGORA EU VEJO O ANJO TRAZENDO A VITÓRIA E ELE ENTREGA EM MINHAS MÃOS. SE ESTÁ AMARGURADO, SE ESTÁ CANSADO DE TANTO SOFRER, SUAS FORÇAS JÁ SE FORAM, VOCÊ ATÉ PENSA QUE NÃO VAI VENCER, LEVANTE SEUS OLHOS AGORA, VEJA A VITÓRIA PERTO DE VOCÊ, JESUS CRISTO ESTÁ AQUI E ESTÁ DIZENDO: VOCÊ VAI VENCER. CORO JESUS, VEM TOCAR EM MINHA VIDA TRAZENDO UM POUCO DE ALEGRIA PARA ESTE POBRE, POBRE CORAÇÃO; JESUS, PELA FÉ AGORA EU VEJO O ANJO TRAZENDO A VITÓRIA E ELE ENTREGA EM MINHAS MÃOS. O ANJO TRAZENDO A VITÓRIA E ELE ENTREGA EM MINHAS MÃOS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'QUANDO ENTREI NESTE LUGAR', 'QUANDO ENTREI NESTE LUGAR, \nSENTI SUA PRESENÇA\nEU SABIA AQUI É O LUGAR, \nONDE HABITA O AMOR.\n\nPOIS ESTE É O TEMPLO DE DEUS, \nJEOVÁ AMADO.\nNÓS ESTAMOS EM SUA PRESENÇA \nEM SANTO LUGAR.\n\nNÓS ESTAMOS EM SANTO LUGAR,\nE POSSO SENTIR AQUI HÁ ANJOS AO REDOR.\nVAMOS ADORAR A JESUS AGORA,\nESTAMOS REUNIDOS EM SUA\nPRESENÇA EM SANTO LUGAR.\n\nNA PRESENÇA DO SENHOR HÁ GOZO IMENSO, \nE A SEUS PÉS A PAZ DA ALMA ENCONTRARÁS.\n\nSE ESTÁS EM DÚVIDA, EU SEI \nSÓ ELE É A RESPOSTA.\nNÓS ESTAMOS EM SUA PRESENÇA \nEM SANTO LUGAR.\n\nNÓS ESTAMOS EM SANTO LUGAR\nE POSSO SENTIR AQUI HÁ ANJOS AO REDOR,\nVAMOS ADORAR A JESUS AGORA, \nESTAMOS REUNIDOS EM SUA PRESENÇA, \nEM SANTO LUGAR.\n\nNÓS ESTAMOS EM SANTO LUGAR\nE POSSO SENTIR AQUI HÁ ANJOS AO REDOR\nVAMOS ADORAR A JESUS AGORA.\nESTAMOS REUNIDOS EM SUA\nPRESENÇA, EM SANTO LUGAR.\n\n3X', 'QUANDO ENTREI NESTE LUGAR, SENTI SUA PRESENÇA EU SABIA AQUI É O LUGAR, ONDE HABITA O AMOR. POIS ESTE É O TEMPLO DE DEUS, JEOVÁ AMADO. NÓS ESTAMOS EM SUA PRESENÇA EM SANTO LUGAR. NÓS ESTAMOS EM SANTO LUGAR, E POSSO SENTIR AQUI HÁ ANJOS AO REDOR. VAMOS ADORAR A JESUS AGORA, ESTAMOS REUNIDOS EM SUA PRESENÇA EM SANTO LUGAR. NA PRESENÇA DO SENHOR HÁ GOZO IMENSO, E A SEUS PÉS A PAZ DA ALMA ENCONTRARÁS. SE ESTÁS EM DÚVIDA, EU SEI SÓ ELE É A RESPOSTA. NÓS ESTAMOS EM SUA PRESENÇA EM SANTO LUGAR. NÓS ESTAMOS EM SANTO LUGAR E POSSO SENTIR AQUI HÁ ANJOS AO REDOR, VAMOS ADORAR A JESUS AGORA, ESTAMOS REUNIDOS EM SUA PRESENÇA, EM SANTO LUGAR. NÓS ESTAMOS EM SANTO LUGAR E POSSO SENTIR AQUI HÁ ANJOS AO REDOR VAMOS ADORAR A JESUS AGORA. ESTAMOS REUNIDOS EM SUA PRESENÇA, EM SANTO LUGAR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'QUEM SÃO ESTES DE BRANCOS VESTIDOS?', 'QUEM SÃO ESTES \nDE BRANCOS VESTIDOS?  \nESTES SÃO OS QUE LAVARAM \nNO SANGUE DE JESUS\nSEUS VESTIDOS, SEUS VESTIDOS.\nBIS\nBIS\n\nPOR ISSO ESTÃO PERANTE \nO TRONO DIZENDO:\nSEJAM DADOS GLÓRIA,\nFORÇA E PODER AO NOSSO\nDEUS PARA TODO O SEMPRE.\nBIS\n\nFOME E SEDE JAMAIS SUAS ALMAS TERÃO,\nA SOMBRA DO CORDEIRO OS COBRIRÁ;\nA MANSÃO DOS FIÉIS VOSSA CERTA SERÁ\nE TODA A LÁGRIMA DEUS LIMPARÁ,\nTODA A LÁGRIMA DEUS LIMPARÁ,\nTODA A LÁGRIMA DEUS LIMPARÁ.', 'QUEM SÃO ESTES DE BRANCOS VESTIDOS? ESTES SÃO OS QUE LAVARAM NO SANGUE DE JESUS SEUS VESTIDOS, SEUS VESTIDOS. POR ISSO ESTÃO PERANTE O TRONO DIZENDO: SEJAM DADOS GLÓRIA, FORÇA E PODER AO NOSSO DEUS PARA TODO O SEMPRE. FOME E SEDE JAMAIS SUAS ALMAS TERÃO, A SOMBRA DO CORDEIRO OS COBRIRÁ; A MANSÃO DOS FIÉIS VOSSA CERTA SERÁ E TODA A LÁGRIMA DEUS LIMPARÁ, TODA A LÁGRIMA DEUS LIMPARÁ, TODA A LÁGRIMA DEUS LIMPARÁ.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'REFÚGIO VERDADEIRO', 'SEGURO ESTOU, \nNÃO TENHO TEMOR DO MAL, \nSIM GUARDADO PELA FÉ.\nEM MEU JESUS NÃO POSSO DUVIDAR\nDESSE AMOR LEAL, ELE EM SEU CAMINHO\nSEMPRE ME CONDUZ.\n\nNÃO ME DEIXARÁ, MAS ME ABRIGARÁ.\nDO PECADO VIL ME VEM LIVRAR.\nA SUA GRAÇA NÃO ME RECUSARÁ.\nSIM, JESUS É QUEM ME PODE SUSTENTAR\n\nNO PODER DE CRISTO, O MESTRE,\nMINHA VIDA SALVA ESTÁ!\nDO PERIGO QUE CERCÁ-LA, \nELE PODERÁ LIVRÁ-LA, SEU PODER ETERNO\nSEMPRE A SUSTERÁ.\n\nABRIGO ETERNO TENHO NO SALVADOR\nELE ESCONDE A MINHA VIDA \nEM SEU PODER EU RECEAR \nNÃO POSSO DO MALFEITOR \nQUE PROCURA PERTINAZ \nME ENFRAQUECER CONFIADO \nENTÃO NESSA PROTEÇÃO SIGO \nA CRISTO E QUERO SER FIEL \nNA MINHA VIDA CHEIO \nDE GRATIDÃO SIM, \nA MEU SENHOR E REI EMANUEL.\n\nNO PODER DE CRISTO, O MESTRE,\nMINHA VIDA SALVA ESTÁ!\nDO PERIGO QUE CERCÁ-LA, \nELE PODERÁ LIVRÁ-LA, SEU PODER ETERNO\nSEMPRE A SUSTERÁ.\n\nPERIGO ALGUM ME PODE CAUSAR TEMOR, \nPOIS MEU SALVADOR NÃO ME ABANDONARÁ \nCOM SUA PROTEÇÃO E COM SEU AMOR, \nDIRIGINDO MINHA VIDA ELE ESTARÁ \nNUNCA O DEIXAREI, MAS  FIEL SEREI, \nSEMPRE FIRME CHEIO DE FERVOR\nA CRISTO REDENTOR, MEU SENHOR E REI, \nEU ME ENTREGAREI, FIRMADO EM SEU AMOR.\n\nNO PODER DE CRISTO O MESTRE\nMINHA VIDA SALVA ESTÁ! \nDO PERIGO QUE CERCÁ-LA\nELE PODERÁ LIVRÁ-LA\nSEU PODER ETERNO\nSEMPRE A SUSTERÁ.', 'SEGURO ESTOU, NÃO TENHO TEMOR DO MAL, SIM GUARDADO PELA FÉ. EM MEU JESUS NÃO POSSO DUVIDAR DESSE AMOR LEAL, ELE EM SEU CAMINHO SEMPRE ME CONDUZ. NÃO ME DEIXARÁ, MAS ME ABRIGARÁ. DO PECADO VIL ME VEM LIVRAR. A SUA GRAÇA NÃO ME RECUSARÁ. SIM, JESUS É QUEM ME PODE SUSTENTAR NO PODER DE CRISTO, O MESTRE, MINHA VIDA SALVA ESTÁ! DO PERIGO QUE CERCÁ-LA, ELE PODERÁ LIVRÁ-LA, SEU PODER ETERNO SEMPRE A SUSTERÁ. ABRIGO ETERNO TENHO NO SALVADOR ELE ESCONDE A MINHA VIDA EM SEU PODER EU RECEAR NÃO POSSO DO MALFEITOR QUE PROCURA PERTINAZ ME ENFRAQUECER CONFIADO ENTÃO NESSA PROTEÇÃO SIGO A CRISTO E QUERO SER FIEL NA MINHA VIDA CHEIO DE GRATIDÃO SIM, A MEU SENHOR E REI EMANUEL. PERIGO ALGUM ME PODE CAUSAR TEMOR, POIS MEU SALVADOR NÃO ME ABANDONARÁ COM SUA PROTEÇÃO E COM SEU AMOR, DIRIGINDO MINHA VIDA ELE ESTARÁ NUNCA O DEIXAREI, MAS FIEL SEREI, SEMPRE FIRME CHEIO DE FERVOR A CRISTO REDENTOR, MEU SENHOR E REI, EU ME ENTREGAREI, FIRMADO EM SEU AMOR. NO PODER DE CRISTO O MESTRE MINHA VIDA SALVA ESTÁ! DO PERIGO QUE CERCÁ-LA ELE PODERÁ LIVRÁ-LA SEU PODER ETERNO SEMPRE A SUSTERÁ.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SABE FILHO', 'SABE, FILHO, QUERO FALAR COM VOCÊ,\nTENHO VISTO TEU LAMENTO, TUA DOR,\nVOCÊ DIZ: SENHOR NÃO VOU SUPORTAR,\nPOIS A LUTA É TÃO GRANDE,\nESTOU QUASE A DESANIMAR.\n\nOLHA, FILHO, TU JÁ LESTE MINHA HISTÓRIA,\nQUE EU DEIXEI UM TRONO DE GLÓRIA,\nPRA MORRER POR TI,\nLÁ NA RUA CENTRAL DE JERUSALÉM,\nCADA PASSO QUE EU DAVA,\nA MARCA DO MEU SANGUE FICAVA TAMBÉM.\n\nUMA COROA DE ESPINHOS ME COLOCARAM,\nE COM CRAVOS PONTIAGUDOS\nNAQUELA CRUZ ME PREGARAM,\nUMA LANÇA AFIADA ME TRANSPASSOU,\nNESSA HORA DE AGONIA,\nATÉ O MEU PAI ME DESAMPAROU.\nBIS\n\nNESSA HORA, MEUS AMIGOS ME DESPREZARAM,\nATÉ PEDRO, POR TRÊS VEZES ME NEGOU,\nLÁZARO, O AMIGO QUE RESSUSCITEI,\nFUI POR ELE DESPREZADO E POR TODOS QUE AMEI;\nTUDO ISSO EU PASSEI POR TE AMAR,\nPRA QUE HOJE EU PUDESSE TE ENTENDER.\n\nTEUS PROBLEMAS TODOS POSSO RESOLVER,\nBASTA SOMENTE CRER,\nQUE A SOLUÇÃO JÁ CHEGOU PRA VOCÊ,\nSE ESTÁS DOENTE, EU SOU O TEU REMÉDIO,\nSE ESTÁS CANSADO, EU POSSO TE ALIVIAR.\n\nEU VENCI, VOCÊ TAMBÉM VENCERÁ,\nSOU JESUS CRISTO, \nO ÚNICO QUE PODE TE AJUDAR;\nEU VENCI, VOCÊ TAMBÉM VENCERÁ,\nSOU JESUS CRISTO, \nO VERDADEIRO FILHO DE DEUS.', 'SABE, FILHO, QUERO FALAR COM VOCÊ, TENHO VISTO TEU LAMENTO, TUA DOR, VOCÊ DIZ: SENHOR NÃO VOU SUPORTAR, POIS A LUTA É TÃO GRANDE, ESTOU QUASE A DESANIMAR. OLHA, FILHO, TU JÁ LESTE MINHA HISTÓRIA, QUE EU DEIXEI UM TRONO DE GLÓRIA, PRA MORRER POR TI, LÁ NA RUA CENTRAL DE JERUSALÉM, CADA PASSO QUE EU DAVA, A MARCA DO MEU SANGUE FICAVA TAMBÉM. UMA COROA DE ESPINHOS ME COLOCARAM, E COM CRAVOS PONTIAGUDOS NAQUELA CRUZ ME PREGARAM, UMA LANÇA AFIADA ME TRANSPASSOU, NESSA HORA DE AGONIA, ATÉ O MEU PAI ME DESAMPAROU. NESSA HORA, MEUS AMIGOS ME DESPREZARAM, ATÉ PEDRO, POR TRÊS VEZES ME NEGOU, LÁZARO, O AMIGO QUE RESSUSCITEI, FUI POR ELE DESPREZADO E POR TODOS QUE AMEI; TUDO ISSO EU PASSEI POR TE AMAR, PRA QUE HOJE EU PUDESSE TE ENTENDER. TEUS PROBLEMAS TODOS POSSO RESOLVER, BASTA SOMENTE CRER, QUE A SOLUÇÃO JÁ CHEGOU PRA VOCÊ, SE ESTÁS DOENTE, EU SOU O TEU REMÉDIO, SE ESTÁS CANSADO, EU POSSO TE ALIVIAR. EU VENCI, VOCÊ TAMBÉM VENCERÁ, SOU JESUS CRISTO, O ÚNICO QUE PODE TE AJUDAR; EU VENCI, VOCÊ TAMBÉM VENCERÁ, SOU JESUS CRISTO, O VERDADEIRO FILHO DE DEUS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SANSÃO', 'SANSÃO ERA UM MENINO NAZIREU\nPARA LIVRAR SEUS PAIS DAS MÃOS DOS FILISTEUS.\nO MENINO FICOU GRANDE E O SENHOR \nO ABENÇOOU.\nE VIVIA IMPELIDO PELO ESPÍRITO DO SENHOR.\n\nSANSÃO MATOU UM LEÃO À MÃO DESARMADA.\nE MATOU TAMBÉM MIL HOMENS COM\nUMA QUEIXADA.\nE COM TREZENTAS RAPOSAS FEZ UMA \nGRANDE QUEIMADA,\nMAS FICOU UMA RAPOSINHA NO SEU\nPEITO ENCERRADA.\n\nO NOME DA RAPOSINHA CHAMA-SE PAIXÃO.\nPOIS ELA TEM DERROTADO MUITOS IRMÃOS.\nSANSÃO ENXERGOU DALILA E LOGO\nFOI DERROTADO.\nBRINCOU DE SER AMARRADO E FICOU\nMESMO AMARRADO.\n\nSERVAS\n\nO SEGREDO DO PODER ESTAVA NO CABELO.\nE POR UMA PAIXÃO CARNAL ELE \nPERDEUO ZELO. E ACABOU \nREVELANDO O SEGREDO DO SENHOR. \nFOI LEVADO AO CATIVEIRO E \nOS OLHOS LHE CUSTOU.\n\nVARÕES\n\nSEM OS OLHOS E CABELOS E AINDA\nAMARRADO, TRABALHAVA COMO ESCRAVO\nE ERA TORTURADO.\nMAS UM DIA ELE SENTIU O CABELO A CRESCER, \nPERCEBEU QUE SUA FÉ ESTAVA A REVIVER.\n\nFOI LEVADO PRÁ BRINCAR DIANTE DOS FILISTEUS.\nMAS JÁ TINHA O CORAÇÃO VOLTADO PARA DEUS.\nLEVANTOU A SUA VOZ E DISSE: Ó GRANDE\nDEUS, ESFORÇA-TE SÓ ESTA VEZ, \nMORRA EU COM OS FILISTEUS.', 'SANSÃO ERA UM MENINO NAZIREU PARA LIVRAR SEUS PAIS DAS MÃOS DOS FILISTEUS. O MENINO FICOU GRANDE E O SENHOR O ABENÇOOU. E VIVIA IMPELIDO PELO ESPÍRITO DO SENHOR. SANSÃO MATOU UM LEÃO À MÃO DESARMADA. E MATOU TAMBÉM MIL HOMENS COM UMA QUEIXADA. E COM TREZENTAS RAPOSAS FEZ UMA GRANDE QUEIMADA, MAS FICOU UMA RAPOSINHA NO SEU PEITO ENCERRADA. O NOME DA RAPOSINHA CHAMA-SE PAIXÃO. POIS ELA TEM DERROTADO MUITOS IRMÃOS. SANSÃO ENXERGOU DALILA E LOGO FOI DERROTADO. BRINCOU DE SER AMARRADO E FICOU MESMO AMARRADO. O SEGREDO DO PODER ESTAVA NO CABELO. E POR UMA PAIXÃO CARNAL ELE PERDEUO ZELO. E ACABOU REVELANDO O SEGREDO DO SENHOR. FOI LEVADO AO CATIVEIRO E OS OLHOS LHE CUSTOU. SEM OS OLHOS E CABELOS E AINDA AMARRADO, TRABALHAVA COMO ESCRAVO E ERA TORTURADO. MAS UM DIA ELE SENTIU O CABELO A CRESCER, PERCEBEU QUE SUA FÉ ESTAVA A REVIVER. FOI LEVADO PRÁ BRINCAR DIANTE DOS FILISTEUS. MAS JÁ TINHA O CORAÇÃO VOLTADO PARA DEUS. LEVANTOU A SUA VOZ E DISSE: Ó GRANDE DEUS, ESFORÇA-TE SÓ ESTA VEZ, MORRA EU COM OS FILISTEUS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SANTO, DEUS DOS EXÉRCITOS', 'SANTO, SANTO, SANTO, \nDEUS DOS EXÉRCITOS\nOS CÉUS E A TERRA ESTÃO CHEIOS\nDA TUA GLÓRIA.\nBIS\n\nHOSANA, HOSANA, HOSANA \nNAS ALTURAS,\nBENDITO O QUE VEM EM NOME \nDO SENHOR BENDITO, \nBENDITO SENHOR.\n\nSERVAS\n\nHOSANA (VARÕES)\nHOSANA, HOSANA, NAS ALTURAS.', 'SANTO, SANTO, SANTO, DEUS DOS EXÉRCITOS OS CÉUS E A TERRA ESTÃO CHEIOS DA TUA GLÓRIA. HOSANA, HOSANA, HOSANA NAS ALTURAS, BENDITO O QUE VEM EM NOME DO SENHOR BENDITO, BENDITO SENHOR. HOSANA  HOSANA, HOSANA, NAS ALTURAS.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SEI QUE ÉS FIEL, JESUS', 'NUM ATO TEU SENHOR, \nVEJO A GRANDEZA DESTE TEU\nAMOR, QUE É PURO E SUAVE, \nME TORNANDO MAIS FELIZ.\nNUMA PALAVRA TUA, \nSINTO TUDO RESOLVER, \nTU FAZES O IMPOSSÍVEL PARA \nME FAZER VENCER.\n\nEU SEI QUE NÃO MEREÇO\nMAS SABES QUE CAREÇO\nDESTE TEU AMOR,\nQUE ME FAZ UM VENCEDOR!\nSEI QUE ÉS FIEL, JESUS\n\nINSTRUMENTOS\n\nVARÕES\n\nNUM ATO TEU SENHOR, \nVEJO A GRANDEZA DESTE TEU\nAMOR, QUE É PURO E SUAVE, \nME TORNANDO MAIS FELIZ.\nNUMA PALAVRA TUA, \nSINTO TUDO RESOLVER, \nTU FAZES O IMPOSSÍVEL PARA \nME FAZER VENCER.\n\nEU SEI QUE NÃO MEREÇO\nMAS SABES QUE CAREÇO\nDESTE TEU AMOR,\nQUE ME FAZ UM VENCEDOR!\nSEI QUE ÉS FIEL, JESUS\n\nVARÕES\n\nCORO (2X)\nSEI QUE ÉS FIEL, JESUS\n(E É GRANDE O TEU AMOR) VARÕES\nSEI QUE ÉS FIEL, JESUS\nSEI QUE ÉS FIEL!\n\nINSTRUMENTOS\n\nTODAS TUAS PROMESSAS FEITAS PARA MIM, \nEM BREVE TODAS ELAS EU VEREI CUMPRIR, POIS, \nO FUTURO É UM SEGREDO QUE \nTU GUARDAS E PERTENCE SÓ A TI.\n\nELEVO TOTALMENTE  MINHA GRATIDÃO, POIS, \nMEU LOUVOR É FRUTO TAMBÉM DESTE AMOR, \nE SEI QUE ESTOU GUARDADO \nEM TEU CORAÇÃO, \nNA TUA MEMÓRIA SENHOR.\n\nCORO (2X)\nSEI QUE ÉS FIEL, JESUS\n(E É GRANDE O TEU AMOR) VARÕES\nSEI QUE ÉS FIEL, JESUS\nSEI QUE ÉS FIEL!', 'NUM ATO TEU SENHOR, VEJO A GRANDEZA DESTE TEU AMOR, QUE É PURO E SUAVE, ME TORNANDO MAIS FELIZ. NUMA PALAVRA TUA, SINTO TUDO RESOLVER, TU FAZES O IMPOSSÍVEL PARA ME FAZER VENCER. EU SEI QUE NÃO MEREÇO MAS SABES QUE CAREÇO DESTE TEU AMOR, QUE ME FAZ UM VENCEDOR! SEI QUE ÉS FIEL, JESUS INSTRUMENTOS EU SEI QUE NÃO MEREÇO MAS SABES QUE CAREÇO DESTE TEU AMOR, QUE ME FAZ UM VENCEDOR! SEI QUE ÉS FIEL, JESUS SEI QUE ÉS FIEL, JESUS (E É GRANDE O TEU AMOR) VARÕES SEI QUE ÉS FIEL, JESUS SEI QUE ÉS FIEL! INSTRUMENTOS TODAS TUAS PROMESSAS FEITAS PARA MIM, EM BREVE TODAS ELAS EU VEREI CUMPRIR, POIS, O FUTURO É UM SEGREDO QUE TU GUARDAS E PERTENCE SÓ A TI. ELEVO TOTALMENTE MINHA GRATIDÃO, POIS, MEU LOUVOR É FRUTO TAMBÉM DESTE AMOR, E SEI QUE ESTOU GUARDADO EM TEU CORAÇÃO, NA TUA MEMÓRIA SENHOR. SEI QUE ÉS FIEL, JESUS (E É GRANDE O TEU AMOR) VARÕES SEI QUE ÉS FIEL, JESUS SEI QUE ÉS FIEL!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'VOZES LINDAS NUNCA OUVIDAS', 'VOZES LINDAS NUNCA OUVIDAS,\nVEM DESCENDO DAS ALTURAS,\nONDE OS ANJOS CANTAM GLÓRIA, \nAO NOSSO CRIADOR.\n\nHARMONIAS SANTAS VIBRAM NOS RENCANTOS,\nDAS CELESTIAIS MANSÕES,\nE NA TERRA ECÔA, COM DULÇOR IMENSO,\nESSE CORO CELESTIAL.\n\nVOZES LINDAS NUNCA OUVIDAS,\nVEM DESCENDO DAS ALTURAS,\nONDE OS ANJOS CANTAM GLÓRIA, \nAO NOSSO CRIADOR.\n\nHARMONIAS SANTAS VIBRAM NOS RENCANTOS,\nDAS CELESTIAIS MANSÕES,\nE NA TERRA ECÔA, COM DULÇOR IMENSO,\nESSE CORO CELESTIAL.\n\nDEUS OFERECE AO PECADOR,\nPOR MEIO DO SEU FILHO,\nSALVAÇÃO DE GRAÇA, NUNCA MERECIDA,\nCRISTO PELA CRUZ VIDA ETERNA TRAZ,\nGLÓRIA A DEUS PELA REDENÇÃO DO PECADOR,\nGLÓRIA A DEUS PORQUE REUNIU \nOS FILHOS SEUS.\n\nGRANDES FORAM AS TRANSGRESSÕES,\nCOMETIDAS PELO PECADOR,\nSÓ O AMOR DO BONDOSO DEUS,\nPODE AS CULPAS NOS RELEVAR,\nPERDOADOS E ARREPENDIDOS,\nLIBERTADOS, POIS, DE TODO MAL,\nPERTO DE JESUS, NOSSO SALVADOR,\nGOZAREMOS DOCE PAZ.\n\nPORTANTO A DEUS SEJAMOS GRATOS,\nO SEU AMADO FILHO HONREMOS,\nDANDO AO SANTO ESPÍRITO LOUVOR,\nETERNAMENTE AMÉM!\nGLÓRIA, GLÓRIA A DEUS,\nDANDO AO SANTO ESPÍRITO LOUVOR,\nETERNAMENTE AMÉM! GLÓRIA, \nGLÓRIA A DEUS! ETERNAMENTE AMÉM!\nGLÓRIA A DEUS! \nAMÉM! (4X)', 'VOZES LINDAS NUNCA OUVIDAS, VEM DESCENDO DAS ALTURAS, ONDE OS ANJOS CANTAM GLÓRIA, AO NOSSO CRIADOR. HARMONIAS SANTAS VIBRAM NOS RENCANTOS, DAS CELESTIAIS MANSÕES, E NA TERRA ECÔA, COM DULÇOR IMENSO, ESSE CORO CELESTIAL. DEUS OFERECE AO PECADOR, POR MEIO DO SEU FILHO, SALVAÇÃO DE GRAÇA, NUNCA MERECIDA, CRISTO PELA CRUZ VIDA ETERNA TRAZ, GLÓRIA A DEUS PELA REDENÇÃO DO PECADOR, GLÓRIA A DEUS PORQUE REUNIU OS FILHOS SEUS. GRANDES FORAM AS TRANSGRESSÕES, COMETIDAS PELO PECADOR, SÓ O AMOR DO BONDOSO DEUS, PODE AS CULPAS NOS RELEVAR, PERDOADOS E ARREPENDIDOS, LIBERTADOS, POIS, DE TODO MAL, PERTO DE JESUS, NOSSO SALVADOR, GOZAREMOS DOCE PAZ. PORTANTO A DEUS SEJAMOS GRATOS, O SEU AMADO FILHO HONREMOS, DANDO AO SANTO ESPÍRITO LOUVOR, ETERNAMENTE AMÉM! GLÓRIA, GLÓRIA A DEUS, DANDO AO SANTO ESPÍRITO LOUVOR, ETERNAMENTE AMÉM! GLÓRIA, GLÓRIA A DEUS! ETERNAMENTE AMÉM! GLÓRIA A DEUS! AMÉM!', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'SALVO DE GRAÇA', 'SALVOS DE GRAÇA POR JESUS, \nVIVO FELIZ  NA SUA LUZ.\nSEI QUE POR MINHA SALVAÇÃO, \nELE PAGOU A REMISSÃO.\n\nCORO\nQUERO COM CRISTO SEMPRE ANDAR, \nGRATO LOUVOR A DEUS CANTAR.\nCRISTO É O AMIGO SEM IGUAL, \nGUIA-ME AO LAR CELESTIAL.\n\nQUANTO ME AGRADA A DEUS SERVIR,\nHOJE NA LUTA OU NO PORVIR.\nSEU EVANGELHO VIDA TRAZ, \nGOZO PERFEITO E SANTA PAZ.\n\nCORO\nQUERO COM CRISTO SEMPRE ANDAR, \nGRATO LOUVOR A DEUS CANTAR.\nCRISTO É O AMIGO SEM IGUAL, \nGUIA-ME AO LAR CELESTIAL.\n\nQUERO SEGUIR MEU SALVADOR,\nSEMPRE LOUVAR O SEU AMOR.\nQUANDO O LABOR AQUI FINDAR,\nVOU COM JESUS NO CÉU MORAR.\n\nCORO\nQUERO COM CRISTO SEMPRE ANDAR, \nGRATO LOUVOR A DEUS CANTAR.\nCRISTO É O AMIGO SEM IGUAL, \nGUIA-ME AO LAR CELESTIAL.', 'SALVOS DE GRAÇA POR JESUS, VIVO FELIZ NA SUA LUZ. SEI QUE POR MINHA SALVAÇÃO, ELE PAGOU A REMISSÃO. CORO QUERO COM CRISTO SEMPRE ANDAR, GRATO LOUVOR A DEUS CANTAR. CRISTO É O AMIGO SEM IGUAL, GUIA-ME AO LAR CELESTIAL. QUANTO ME AGRADA A DEUS SERVIR, HOJE NA LUTA OU NO PORVIR. SEU EVANGELHO VIDA TRAZ, GOZO PERFEITO E SANTA PAZ. QUERO SEGUIR MEU SALVADOR, SEMPRE LOUVAR O SEU AMOR. QUANDO O LABOR AQUI FINDAR, VOU COM JESUS NO CÉU MORAR.', 4, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
